import{r,o as t,c as n,a as e,b as a,F as o,d as s,e as c}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const _={},i=s('<h3 id="\u533B\u751F\u8BF4\u6CD5\u4E0D\u4E00\u6837-\u8BE5\u542C\u8BF4\u7684" tabindex="-1"><a class="header-anchor" href="#\u533B\u751F\u8BF4\u6CD5\u4E0D\u4E00\u6837-\u8BE5\u542C\u8BF4\u7684" aria-hidden="true">#</a> \u533B\u751F\u8BF4\u6CD5\u4E0D\u4E00\u6837\uFF0C\u8BE5\u542C\u8BF4\u7684\uFF1F</h3><p>\u65E0\u8BBA\u662F\u53BB\u533B\u9662\u770B\u75C5\uFF0C\u8FD8\u662F\u5728\u7F51\u4E0A\u770B\u79D1\u666E\uFF0C\u90FD\u53EF\u80FD\u78B0\u5230\u4E0D\u540C\u7684\u533B\u751F\u5BF9\u540C\u4E00\u4E2A\u95EE\u9898\u6709\u4E0D\u540C\u7684\u770B\u6CD5\uFF0C\u4ECA\u5929\u51FA\u4E2A\u80B2\u513F\u7537\u795E\u8BF4\u8FD9\u6837\u597D\uFF0C\u660E\u5929\u51FA\u4E2A\u5976\u5976\u8BF4\u90A3\u6837\u597D\uFF0C\u90FD\u8BF4\u81EA\u5DF1\u79D1\u5B66\uFF0C\u90FD\u8BF4\u81EA\u5DF1\u6709\u9053\u7406\uFF0C\u533B\u751F\u8BF4\u6CD5\u4E0D\u4E00\u6837\uFF0C\u5230\u5E95\u8BE5\u542C\u8BF4\u7684\uFF1F</p><p>\u5BF9\u4E00\u4E2A\u5B69\u5B50\u5177\u4F53\u75BE\u75C5\u7684\u8BCA\u65AD\uFF0C\u533B\u751F\u5B58\u5728\u5206\u6B67\u662F\u5F88\u5E38\u89C1\u7684\uFF0C\u6709\u65F6\u5019\u8BCA\u65AD\u660E\u786E\u4E86\uFF0C\u533B\u751F\u7684\u6CBB\u7597\u65B9\u6848\u4E5F\u53EF\u80FD\u4F1A\u6709\u533A\u522B\uFF0C\u8981\u60A3\u8005\u53BB\u5224\u65AD\u8C01\u5BF9\u8C01\u9519\u662F\u4E0D\u592A\u53EF\u80FD\uFF0C\u4F46\u5982\u679C\u8BA9\u533B\u751F\u81EA\u5DF1\u6765\u8FA9\u8BBA\uFF0C\u8BC4\u5224\u7684\u6807\u51C6\u5C31\u662F\u770B\u8BCA\u65AD\u4F9D\u636E\u548C\u7528\u836F\u4F9D\u636E\uFF0C\u8BF4\u767D\u4E86\u5C31\u662F<strong>\u770B\u8BC1\u636E</strong>\u3002</p><p>\u9488\u5BF9\u4E2A\u4F53\u7684\u8BCA\u6CBB\uFF0C\u533B\u751F\u5BF9\u8BC1\u636E\u7684\u5224\u65AD\u4F1A\u53D7\u5230\u7ECF\u9A8C\u548C\u72B6\u6001\u7684\u5F71\u54CD\uFF0C\u6BD4\u5982\u4E00\u4E2A\u533B\u751F\u6478\u4E86\u75C5\u4EBA\u809A\u5B50\u8BF4\u91CC\u9762\u6709\u4E2A\u80BF\u7624\uFF0C\u53E6\u5916\u4E00\u4E2A\u533B\u751F\u6478\u4E86\u5374\u8BF4\u6CA1\u6709\uFF0C\u8FD9\u5176\u5B9E\u662F\u96BE\u4EE5\u7EDD\u5BF9\u907F\u514D\u7684\u95EE\u9898\uFF0C\u56E0\u4E3A\u89E6\u8BCA\u7684\u5224\u65AD\u6709\u4E00\u5B9A\u7684\u4E3B\u89C2\u6027\uFF0C\u6CA1\u8FDB\u4E00\u6B65\u505A\u68C0\u67E5\uFF0C\u7B2C\u4E09\u4E2A\u533B\u751F\u6709\u65F6\u4E5F\u5224\u65AD\u4E0D\u4E86\u8C01\u5BF9\u8C01\u9519\uFF0C\u66F4\u522B\u8BF4\u60A3\u8005\u81EA\u5DF1\u4E86\u3002</p><p>\u4F46\u8FD9\u4E9B\u533B\u751F\u8BCA\u65AD\u548C\u6CBB\u7597\u7684\u65F6\u5019\u90FD\u9075\u5FAA\u4E86\u4E00\u4E2A\u5171\u540C\u7684\u6807\u51C6\uFF0C\u90A3\u5C31\u662F\u8BA4\u53EF\u809A\u5B50\u6478\u5230\u80BF\u7624\u662F\u6709\u95EE\u9898\u7684\u3002\u5982\u679C\u4E00\u4E2A\u533B\u751F\u8BF4\uFF1A\u6211\u6478\u5230\u4E86\u80BF\u7624\uFF0C\u6240\u4EE5\u4F60\u6CA1\u6709\u95EE\u9898\u3002\u90A3\u522B\u8BF4\u5176\u4ED6\u533B\u751F\u77E5\u9053\u4ED6\u9519\u4E86\uFF0C\u5C31\u8FDE\u60A3\u8005\u4E5F\u80FD\u77E5\u9053\u8FD9\u533B\u751F\u5728\u80E1\u8BF4\u516B\u9053\u3002\u56E0\u4E3A\u957F\u80BF\u7624\u662F\u751F\u75C5\u4E86\uFF0C\u8FD9\u662F\u5927\u5BB6\u90FD\u8BA4\u53EF\u7684\u6807\u51C6\u7B54\u6848\u3002</p><p>\u6709\u4EBA\u4EBA\u90FD\u77E5\u9053\u7684\u6807\u51C6\u7B54\u6848\uFF0C\u5224\u65AD\u8D77\u6765\u5C31\u5F88\u5BB9\u6613\uFF0C\u4F46\u73B0\u5B9E\u533B\u5B66\u662F\u4E13\u4E1A\u6027\u5F88\u5F3A\u7684\u5B66\u79D1\uFF0C\u5927\u90E8\u5206\u95EE\u9898\u7684\u7B54\u6848\u5927\u5BB6\u5E76\u4E0D\u77E5\u9053\uFF0C\u800C\u4E14\u4EE5\u5F80\u7684\u90A3\u4E9B\u201C\u6807\u51C6\u7B54\u6848\u201D\uFF0C\u968F\u7740\u7814\u7A76\u7684\u6DF1\u5165\u6709\u4E9B\u786E\u5B9E\u88AB\u63A8\u7FFB\u4E86\uFF0C\u4E0D\u4F46\u60A3\u8005\u4E0D\u77E5\u9053\uFF0C\u77E5\u8BC6\u6CA1\u6709\u66F4\u65B0\u7684\u533B\u751F\u4E5F\u4E0D\u77E5\u9053\u4E86\uFF0C\u7136\u540E\u60A3\u8005\u5C31\u542C\u5230\u4E86\u4E0D\u540C\u533B\u751F\u7684\u4E0D\u540C\u8BF4\u6CD5\uFF0C\u4E0D\u77E5\u9053\u542C\u8C01\u7684\u3002</p><p>\u90A3\u8FD9\u79CD\u60C5\u51B5\u5230\u5E95\u8BE5\u542C\u8C01\u7684\uFF1F\u770B\u533B\u751F\u5E74\u9F84\uFF1F\u8FD8\u662F\u770B\u533B\u751F\u804C\u79F0\uFF1F\u8FD8\u662F\u770B\u533B\u751F\u4EBA\u6C14\uFF1F\u5176\u5B9E\u90FD\u4E0D\u662F\uFF0C\u800C\u662F\u770B\u8C01\u7684\u8BF4\u6CD5\u66F4\u6709\u4F9D\u636E\uFF0C\u4E5F\u5C31\u662F<strong>\u770B\u8BC1\u636E</strong>\u3002</p><p>\u8BC1\u636E\u6709\u5F88\u591A\u79CD\uFF0C\u52A8\u7269\u8BD5\u9A8C\uFF0C\u533B\u751F\u81EA\u5DF1\u7684\u4E34\u5E8A\u7ECF\u9A8C\uFF0C\u4E34\u5E8A\u62A5\u9053\uFF0C\u75C5\u4F8B\u5BF9\u7167\u7814\u7A76\uFF0C\u961F\u5217\u7814\u7A76\uFF0C\u968F\u673A\u5BF9\u7167\u7814\u7A76\uFF0C\u7CFB\u7EDF\u6027\u7EFC\u8FF0\u8FD9\u4E9B\u90FD\u662F\u8BC1\u636E\uFF0C\u4E0D\u540C\u7EA7\u522B\u7684\u8BC1\u636E\uFF0C\u53EF\u4FE1\u5EA6\u4E0D\u4E00\u6837\uFF0C\u4E34\u5E8A\u6307\u5357\u7ED9\u51FA\u63A8\u8350\u610F\u89C1\u7684\u4F9D\u636E\u5C31\u662F\u8FD9\u4E9B\u8BC1\u636E\uFF0C\u8BC1\u636E\u7EA7\u522B\u4E0D\u540C\uFF0C\u7ED9\u51FA\u7684\u63A8\u8350\u7EA7\u522B\u4E5F\u4E0D\u4E00\u6837\u3002</p><p>\u8BC1\u636E\u7EA7\u522B\u9AD8\u7684\uFF0C\u6BD4\u5982\u4E00\u4E2A\u836F\u7ED9\u80BF\u7624\u75C5\u4EBA\u7528\uFF0C\u88AB\u5F88\u591A\u4E2A\u968F\u673A\u53CC\u76F2\u5BF9\u7167\u7814\u7A76\u90FD\u8BC1\u5B9E\u53EF\u4EE5\u6D88\u706D\u80BF\u7624\uFF0C\u800C\u4E14\u6CA1\u6709\u526F\u4F5C\u7528\uFF0C\u90A3\u7ED3\u8BBA\u53EF\u4FE1\u5EA6\u5F88\u5927\u7684\uFF0C\u533B\u5B66\u6307\u5357\u53EF\u80FD\u5C31\u7ED9\u51FAA\u7EA7\u63A8\u8350\uFF0C\u8FD9\u4E9B\u7ED3\u8BBA\u4E5F\u53EF\u80FD\u4F1A\u88AB\u90A3\u4E9B\u6743\u5A01\u7684\u533B\u5B66\u673A\u6784\u91C7\u7EB3\u544A\u8BC9\u516C\u4F17\u3002</p><p>\u8BC1\u636E\u7EA7\u522B\u4F4E\u7684\uFF0C\u6BD4\u5982\u5355\u5976\u5976\u8BF4\u53D1\u73B0\u81EA\u5DF1\u505A\u8FC7\u88AB\u52A8\u64CD\u7684\u5B69\u5B50\u5927\u8111\u53D1\u80B2\u597D\uFF0C\u8FD9\u80FD\u5F53\u8BC1\u636E\u5417\uFF1F\u5F53\u7136\u53EF\u4EE5\uFF0C\u53EA\u4E0D\u8FC7\u62C9\u62C9\u6643\u6643\u80FD\u957F\u8111\u5B50\u8FD9\u79CD\u8BF4\u6CD5\u6709\u8FDD\u5E38\u7406\uFF0C\u5979\u53C8\u62FF\u4E0D\u51FA\u5177\u4F53\u6570\u636E\uFF0C\u81EA\u7136\u4E0D\u53EF\u4FE1\u3002\u7528\u6765\u9A97\u9A97\u4E0D\u61C2\u53C8\u6025\u529F\u8FD1\u5229\u7684\u5BB6\u957F\u662F\u53EF\u4EE5\u7684\uFF0C\u4F46\u5F88\u96BE\u9A97\u5230\u5176\u5B83\u533B\u751F\uFF0C\u6240\u4EE5\u4E0D\u4F1A\u6709\u54EA\u5BB6\u6743\u5A01\u533B\u5B66\u673A\u4F1A\u6784\u63A8\u8350\u7ED9\u6B63\u5E38\u5B69\u5B50\u505A\u88AB\u52A8\u64CD\u6765\u957F\u667A\u529B\u3002</p><p>\u5F88\u591A\u4EBA\u53EF\u80FD\u8981\u8BF4\u533B\u5B66\u6CA1\u6709\u7EDD\u5BF9\uFF0C\u6743\u5A01\u673A\u6784\u7684\u610F\u89C1\u5C31\u662F\u5BF9\u7684\u5417\uFF1F\u4ECA\u5929\u88AB\u8BA4\u4E3A\u6B63\u786E\u7684\u77E5\u8BC6\uFF0C\u4F1A\u4E0D\u4F1A\u90A3\u5929\u88AB\u65B0\u7684\u7814\u7A76\u63A8\u7FFB\u5462\uFF1F\u4F1A\u4E0D\u4F1A\u6709\u4E00\u5929\u6709\u65B0\u7684\u7814\u7A76\u8BC1\u5B9E\u5176\u5B9E\u8FD9\u4E9B\u6743\u5A01\u533B\u5B66\u673A\u6784\u90FD\u9519\u4E86\uFF0C\u800C\u5355\u5976\u5976\u5BF9\u4E86\uFF1F</p><p>\u8FD9\u79CD\u53EF\u80FD\u6027\u5F53\u7136\u5B58\u5728\uFF0C\u6BD4\u5982\u4EE5\u524D\u6CA1\u6709\u63A8\u8350\u5A74\u513F\u4EF0\u5367\uFF0C\u540E\u6765\u7814\u7A76\u53D1\u73B0\u4EF0\u5367\u53EF\u4EE5\u51CF\u5C11\u5A74\u513F\u731D\u6B7B\u7684\u98CE\u9669\uFF0C\u7F8E\u56FD\u513F\u79D1\u5B66\u4F1A\uFF08AAP\uFF09\u624D\u5F00\u59CB\u63A8\u52A8\u5A74\u513F\u4EF0\u5367\uFF0C\u5982\u679C\u4EE5\u524D\u6709\u4E2A\u533B\u751F\u4E3B\u5F20\u8981\u4EF0\u5367\uFF0C\u9A6C\u540E\u70AE\u7684\u6765\u770B\uFF0CAAP\u6CA1\u6709\u66F4\u65E9\u7684\u63A8\u8350\uFF0C\u90A3\u5C31\u662F\u9519\u4E86\u3002</p><p>\u4F46\u65E0\u8BBA\u662F\u63A8\u8350\u6216\u53CD\u5BF9\u4E00\u79CD\u505A\u6CD5\uFF0C\u6211\u4EEC\u90FD\u9700\u8981\u4E00\u5B9A\u7684\u7406\u636E\uFF0C\u9075\u5FAA\u7684\u7406\u636E\u7B49\u7EA7\u8D8A\u9AD8\uFF0C\u51FA\u9519\u7684\u51E0\u7387\u4E5F\u5C31\u8D8A\u4F4E\u3002\u6CA1\u6709\u53EF\u9760\u7684\u8BC1\u636E\u5C31\u4E0D\u63A8\u8350\u4E00\u79CD\u505A\u6CD5\uFF0C\u662F\u53EF\u80FD\u4F1A\u72AF\u9519\u7684\uFF0C\u4F46\u76F8\u6BD4\u4E4B\u4E0B\u5982\u679C\u6CA1\u6709\u8BC1\u636E\u5C31\u63A8\u8350\u4E00\u79CD\u505A\u6CD5\uFF0C\u72AF\u9519\u7684\u6982\u7387\u53EA\u4F1A\u66F4\u5927\u3002\u76F8\u4FE1\u6709\u8BC1\u636E\u7684\u63A8\u8350\u5B58\u5728\u88AB\u8BEF\u5BFC\u7684\u53EF\u80FD\u6027\uFF0C\u4F46\u98CE\u9669\u4F1A\u8FDC\u8FDC\u4F4E\u4E8E\u76F8\u4FE1\u6CA1\u6709\u8BC1\u636E\u7684\u63A8\u8350\u3002</p><p>\u6240\u4EE5\uFF0C\u70ED\u6027\u60CA\u53A5\u7684\u5B69\u5B50\u5634\u91CC\u8981\u4E0D\u8981\u585E\u4E1C\u897F\uFF0C\u4F60\u662F\u60C5\u613F\u76F8\u4FE1\u7F8E\u56FD\u56FD\u7ACB\u795E\u7ECF\u75BE\u75C5\u4E0E\u5352\u4E2D\u7814\u7A76\u9662\u7684\u610F\u89C1\uFF0C\u8FD8\u662F\u76F8\u4FE1\u5D14\u533B\u751F\u81EA\u5DF1\u7684\u4E2A\u4EBA\u7ECF\u9A8C\u5462\uFF1F\u5728\u5A74\u513F\u88AB\u52A8\u64CD\u7684\u95EE\u9898\u4E0A\uFF0C\u4F60\u662F\u76F8\u4FE1AAP\u7684\u610F\u89C1\u5462\u8FD8\u662F\u76F8\u4FE1\u5355\u5976\u5976\u7684\u8BF4\u6CD5\u5462\uFF1F</p><p>\u533B\u5B66\u786E\u5B9E\u662F\u5728\u4E0D\u505C\u7684\u8FDB\u6B65\u7684\uFF0C\u533B\u751F\u548C\u79D1\u7814\u4EBA\u5458\u4E0D\u505C\u7684\u505A\u7814\u7A76\uFF0C\u6709\u4E9B\u5C31\u662F\u4E3A\u4E86\u9A8C\u8BC1\u5F53\u524D\u7684\u610F\u89C1\u662F\u4E0D\u662F\u5BF9\u7684\uFF0C\u6709\u4E9B\u662F\u4E3A\u4E86\u9A8C\u8BC1\u6709\u6CA1\u6709\u66F4\u597D\u7684\u65B9\u6CD5\uFF0C\u4E5F\u6B63\u662F\u8FD9\u4E9B\u7814\u7A76\u4E3A\u65B0\u7684\u6307\u5357\u63D0\u4F9B\u65B0\u7684\u652F\u6301\u6216\u8005\u5426\u5B9A\u7684\u8BC1\u636E\uFF0C\u7136\u540E\u6307\u5357\u548C\u6559\u6750\u6839\u636E\u8FD9\u4E9B\u8BC1\u636E\u8FDB\u884C\u4FEE\u8BA2\u3002</p><p>AAP\u7684\u6307\u5357\u51E0\u4E4E\u662F\u6BCF\u96945\u5E74\u5C31\u4FEE\u8BA2\u4E00\u6B21\uFF0C\u5C3C\u5C14\u68EE\u513F\u79D1\u5B66\u4E5F\u51E0\u4E4E\u662F\u6BCF\u9694\u51E0\u5E74\u91CD\u7248\u4E00\u6B21\uFF0C\u539F\u56E0\u5C31\u662F\u6709\u4E9B\u65B0\u7684\u7814\u7A76\u63D0\u4F9B\u4E86\u65B0\u8FDB\u5C55\uFF0C\u6709\u4E9B\u77E5\u8BC6\u9700\u8981\u8FDB\u884C\u66F4\u65B0\u4E86\uFF0C\u66F4\u65B0\u7684\u4F9D\u636E\u4E0D\u662F\u770B\u54EA\u4E2A\u533B\u751F\u7684\u7ECF\u9A8C\uFF0C\u4E0D\u662F\u54EA\u4E2A\u6743\u5A01\u4E13\u5BB6\u6709\u4E86\u65B0\u7684\u89C2\u70B9\uFF0C\u800C\u662F\u770B\u662F\u4E0D\u662F\u6709\u53EF\u9760\u7684\u65B0\u8BC1\u636E\uFF0C\u5176\u5B9E\u8FD8\u662F<strong>\u9760\u8BC1\u636E\u6765\u8BF4\u8BDD</strong>\u3002</p><p>\u5982\u679C\u5355\u5976\u5976\u771F\u80FD\u505A\u4E2A\u968F\u673A\u5BF9\u7167\u8BD5\u9A8C\u8BC1\u5B9E\u81EA\u5DF1\u7684\u64CD\u80FD\u4FC3\u8FDB\u5927\u8111\u53D1\u80B2\uFF0C\u4E0D\u4F1A\u4F24\u5BB3\u5B69\u5B50\uFF0C\u800C\u4E14\u8BD5\u9A8C\u53EF\u4EE5\u88AB\u522B\u4EBA\u91CD\u590D\u9A8C\u8BC1\uFF0C\u90A3\u6307\u5357\u548C\u6559\u79D1\u4E66\u771F\u53EF\u80FD\u6309\u5979\u7684\u8BF4\u6CD5\u6765\u5199\u3002\u53EA\u4E0D\u8FC7\u4E00\u4E2A\u8BD5\u9A8C\u88AB\u6279\u51C6\u4E4B\u524D\u81F3\u5C11\u8981\u6709\u70B9\u7406\u8BBA\u4F9D\u636E\uFF0C\u4E5F\u81F3\u5C11\u8981\u7ECF\u8FC7\u4F26\u7406\u5BA1\u67E5\uFF0C\u5426\u5219\u4F60\u4ECA\u5929\u8BF4\u8FD9\u6837\u6643\u5B69\u5B50\u80FD\u957F\u8111\u5B50\uFF0C\u660E\u5929\u6709\u4EBA\u8BF4\u6253\u5B69\u5B50\u80FD\u957F\u8111\u5B50\uFF0C\u90A3\u4E0D\u77E5\u9053\u591A\u5C11\u5B69\u5B50\u8981\u906D\u6B83\u3002</p><p>\u5F53\u6211\u6307\u51FA\u4E00\u4E9B\u533B\u5B66\u9519\u8BEF\u7684\u65F6\u5019\uFF0C\u8FD8\u4F1A\u542C\u5230\u53E6\u5916\u4E00\u79CD\u58F0\u97F3\uFF1A\u6BCF\u4E2A\u4EBA\u90FD\u6709\u81EA\u5DF1\u7684\u80B2\u513F\u89C2\uFF0C\u9002\u5408\u81EA\u5DF1\u7684\u5C31\u662F\u6700\u597D\u7684\u3002\u8BF4\u5D14\u533B\u751F\u7684\u90A3\u4E9B\u533B\u5B66\u9519\u8BEF\u7684\u65F6\u5019\u6709\u4EBA\u8FD9\u6837\u8BB2\uFF0C\u8BF4\u5355\u5976\u5976\u7684\u88AB\u52A8\u64CD\u8FD8\u662F\u6709\u4EBA\u8FD9\u6837\u8BB2\uFF0C\u8FD9\u79CD\u9E21\u6C64\u8BF4\u6CD5\u5F88\u5BB9\u6613\u5F97\u4EBA\u5FC3\u7684\uFF0C\u5F88\u591A\u72AF\u8FC7\u8FD9\u79CD\u7C7B\u4F3C\u9519\u8BEF\u7684\u4EBA\u53EF\u4EE5\u636E\u6B64\u627E\u5230\u4E00\u4E2A\u5B89\u6170\u81EA\u5DF1\u7684\u7406\u7531\u3002</p><p>\u80B2\u513F\u89C2\u867D\u7136\u662F\u4E00\u4E2A\u5927\u7684\u6846\uFF0C\u4F46\u4E0D\u662F\u4EC0\u4E48\u95EE\u9898\u90FD\u53EF\u4EE5\u5F80\u91CC\u9762\u88C5\uFF0C\u533B\u5B66\u5EFA\u8BAE\u662F\u6839\u636E\u8BC1\u636E\u6743\u8861\u6536\u76CA\u548C\u98CE\u9669\u540E\u4F5C\u51FA\u7684\uFF0C\u6536\u76CA\u98CE\u9669\u6BD4\u6700\u5927\u7684\u90A3\u4E2A\uFF0C\u5C31\u662F\u6700\u9002\u5408\u5B69\u5B50\u7684\u3002\u4E0D\u4F1A\u8BF4\u522B\u4EBA\u5BB6\u5B69\u5B50\u60CA\u53A5\u4E0D\u8981\u585E\u52FA\u67C4\uFF0C\u4F60\u5BB6\u5B69\u5B50\u5374\u53EF\u4EE5\u585E\u52FA\u67C4\uFF0C\u522B\u4EBA\u5BB6\u5B69\u5B50\u88AB\u6447\u6643\u6709\u98CE\u9669\uFF0C\u4F60\u5BB6\u5B69\u5B50\u88AB\u6447\u6643\u5C31\u6CA1\u98CE\u9669\uFF0C\u522B\u4EBA\u5BB6\u5B69\u5B50\u751F\u75C5\u627E\u533B\u751F\uFF0C\u4F60\u5BB6\u5B69\u5B50\u751F\u75C5\u53EF\u4EE5\u8BF7\u4E2A\u5927\u4ED9\u3002</p><p>\u5BF9\u4E8E\u4E00\u4E2A\u5177\u4F53\u7684\u75C5\u4EBA\uFF0C\u533B\u751F\u53EF\u4EE5\u6839\u636E\u5F53\u524D\u7684\u6700\u4F73\u8BC1\u636E\uFF0C\u7ED3\u5408\u81EA\u5DF1\u7684\u7ECF\u9A8C\u548C\u75C5\u4EBA\u7684\u610F\u613F\u63D0\u4F9B\u8BCA\u6CBB\u65B9\u6848\uFF0C\u4F46\u533B\u5B66\u79D1\u666E\u662F\u9762\u5BF9\u7FA4\u4F53\uFF0C\u533B\u751F\u53EA\u80FD\u6839\u636E\u533B\u5B66\u539F\u5219\u63D0\u4F9B\u6700\u53EF\u9760\u7684\u4FE1\u606F\uFF0C\u63A5\u4E0D\u63A5\u53D7\uFF0C\u63A5\u53D7\u54EA\u4E2A\u8BF4\u6CD5\u786E\u5B9E\u662F\u8BFB\u8005\u81EA\u5DF1\u7684\u9009\u62E9\uFF0C\u4F46\u5EFA\u7ACB\u4E86\u5FAA\u8BC1\u89C2\u5FF5\u7684\u8BFB\u8005\uFF0C\u9762\u5BF9\u533B\u751F\u4E0D\u4E00\u6837\u7684\u8BF4\u6CD5\uFF0C\u81EA\u7136\u4E5F\u77E5\u9053\u8BE5\u542C\u8C01\u7684\u3002</p><p>\u7F51\u4E0A\u5404\u5BB6\u4F17\u8BF4\u7EB7\u7EAD\uFF0C\u81EA\u5DF1\u53C8\u6CA1\u6709\u8FA8\u8BC6\u80FD\u529B\uFF0C\u5982\u4F55\u627E\u5230\u9760\u8C31\u7684\u80B2\u513F\u4FE1\u606F\uFF1F\u957F\u6309\u4E0B\u9762\u56FE\u7247\u5173\u6CE8\u540E\uFF0C\u56DE\u590D\u201C<strong>126</strong>\u201D\u67E5\u770B\u6587\u7AE0\u3002</p>',21),l={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=403048225&idx=1&sn=c196b5200d043ca69111c0e7baf3da1f&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},h=c("\u67E5\u770B\u539F\u6587");function m(f,g){const p=r("ExternalLinkIcon");return t(),n(o,null,[i,e("p",null,[e("a",l,[h,a(p)])])],64)}var u=d(_,[["render",m]]);export{u as default};
