import{r,o as t,c as a,a as e,b as n,F as o,d as s,e as c}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const i={},l=s('<h3 id="\u5B69\u5B50\u4E3A\u4EC0\u4E48\u78E8\u7259-\u9700\u8981\u6253\u866B\u6216\u8865\u9499\u5417" tabindex="-1"><a class="header-anchor" href="#\u5B69\u5B50\u4E3A\u4EC0\u4E48\u78E8\u7259-\u9700\u8981\u6253\u866B\u6216\u8865\u9499\u5417" aria-hidden="true">#</a> \u5B69\u5B50\u4E3A\u4EC0\u4E48\u78E8\u7259\uFF0C\u9700\u8981\u6253\u866B\u6216\u8865\u9499\u5417\uFF1F</h3><p>\u6211\u4EEC\u53EF\u80FD\u90FD\u6709\u8FD9\u6837\u7684\u7ECF\u5386\uFF0C\u534A\u591C\u9192\u6765\uFF0C\u542C\u5230\u5B69\u5B50\u54AF\u6ECB\u54AF\u6ECB\u7684\u78E8\u7259\u58F0\uFF0C\u4E00\u65B9\u9762\u81EA\u5DF1\u542C\u8D77\u6765\u96BE\u53D7\uFF0C\u53E6\u4E00\u65B9\u9762\uFF0C\u4E5F\u4F1A\u62C5\u5FC3\u5B69\u5B50\u662F\u4E0D\u662F\u8EAB\u4F53\u51FA\u4E86\u95EE\u9898\uFF0C\u8FD9\u6837\u78E8\u7259\u4F1A\u4E0D\u4F1A\u78E8\u574F\u7259\u9F7F\u3002\u8FD8\u6709\u4E9B\u4EBA\u8BF4\u78E8\u7259\u662F\u56E0\u4E3A\u809A\u5B50\u91CC\u6709\u86D4\u866B\uFF0C\u6216\u8005\u662F\u56E0\u4E3A\u7F3A\u9499\uFF0C\u662F\u8FD9\u6837\u7684\u5417\uFF1F</p><p><strong>\u78E8\u7259\u5230\u5E95\u662F\u600E\u4E48\u56DE\u4E8B\uFF1F</strong></p><p>\u78E8\u7259\u75C7\u662F\u5480\u56BC\u808C\u6536\u7F29\u5BFC\u81F4\u7684\u4E00\u79CD\u65E0\u610F\u8BC6\uFF0C\u975E\u81EA\u4E3B\u6027\u7684\u8282\u5F8B\u6D3B\u52A8\uFF0C\u5BFC\u81F4\u4E0A\u4E0B\u7259\u7D27\u54AC\u6216\u76F8\u4E92\u7814\u78E8\uFF0C\u5927\u4EBA\u548C\u5C0F\u5B69\u90FD\u53EF\u80FD\u53D1\u751F\uFF0C\u65E2\u53EF\u4EE5\u53D1\u751F\u4E8E\u7761\u7720\u65F6\uFF0C\u4E5F\u53EF\u4EE5\u53D1\u751F\u4E8E\u6E05\u9192\u65F6\uFF0C\u665A\u4E0A\u7761\u89C9\u65F6\u7684\u78E8\u7259\u5C31\u79F0\u4E3A\u201C\u591C\u78E8\u7259\u201D\u3002\u5176\u4E2D\uFF0C\u7236\u6BCD\u4EB2\u4EEC\u6700\u6709\u5207\u8EAB\u4F53\u4F1A\u7684\u6050\u6015\u5C31\u662F\u5B69\u5B50\u7684\u591C\u78E8\u7259\u4E86\u3002</p><p><strong>\u6211\u4EEC\u4E3A\u4EC0\u4E48\u4F1A\u78E8\u7259\uFF1F</strong></p><p>\u5F88\u9057\u61BE\uFF0C\u76EE\u524D\u8FD9\u4E2A\u95EE\u9898\u8FD8\u6CA1\u6709\u786E\u5207\u7684\u7B54\u6848\u3002\u4F46\u76EE\u524D\u5927\u591A\u6570\u5B66\u8005\u8BA4\u4E3A\u78E8\u7259\u662F\u7531\u591A\u56E0\u7D20\u5171\u540C\u5BFC\u81F4\u7684\uFF0C\u5BF9\u5927\u4EBA\u6765\u8BF4\uFF0C\u9157\u9152\u3001\u5927\u58F0\u6253\u9F3E\u3001\u5496\u5561\u56E0\u6444\u5165\u3001\u62BD\u70DF\u3001\u7126\u8651\u3001\u9AD8\u538B\u529B\u7684\u751F\u6D3B\u53CA\u5DE5\u4F5C\u72B6\u6001\u7B49\uFF0C\u51FA\u73B0\u591C\u78E8\u7259\u7684\u98CE\u9669\u4F1A\u589E\u5927\u3002</p><p>\u5BF9\u513F\u7AE5\u6765\u8BF4\uFF0C\u5728\u51FA\u7259\u671F\uFF0C\u5305\u62EC\u4E73\u7259\u548C\u6052\u7259\u840C\u51FA\u7684\u65F6\u671F\uFF0C\u662F\u5B69\u5B50\u51FA\u73B0\u591C\u78E8\u7259\u7684\u4E24\u4E2A\u9AD8\u5CF0\u65F6\u671F\uFF0C\u6240\u4EE5\u5B69\u5B50\u591C\u78E8\u7259\u53EF\u80FD\u53EA\u662F\u7279\u6B8A\u65F6\u671F\u7684\u4E00\u4E2A\u751F\u7406\u73B0\u8C61\u3002\u6839\u636E\u6700\u8FD1\u7684\u4E00\u9879\u7814\u7A76\uFF0C\u6027\u522B\uFF08\u7537\u5B69\u6BD4\u5973\u5B69\u66F4\u5BB9\u6613\u53D1\u751F\u591C\u78E8\u7259\uFF09\u3001\u67D0\u4E9B\u7279\u5B9A\u57FA\u56E0\u3001\u767D\u5929\u8FD0\u52A8\u91CF\u592A\u5927\u3001\u7126\u8651\u3001\u7D27\u5F20\u3001\u4E8C\u624B\u70DF\u3001\u7761\u7720\u4E0D\u5B89\u53CA\u6253\u9F3E\u3001\u623F\u95F4\u566A\u97F3\u3001\u7761\u7720\u65F6\u95F4\u4E0D\u8DB3\uFF08\u22648\u5C0F\u65F6\uFF09\u3001\u5934\u75DB\u3001\u559C\u7231\u54AC\u4E1C\u897F\u3001\u67D0\u4E9B\u884C\u4E3A\u95EE\u9898\u3001\u5FC3\u7406\u95EE\u9898\u4F1A\u589E\u52A0\u513F\u7AE5\u591C\u78E8\u7259\u7684\u98CE\u9669\u3002</p><p>\u9700\u8981\u7279\u522B\u63D0\u9192\u7684\u662F\uFF0C\u6709\u7761\u7720\u547C\u5438\u963B\u788D\u7684\u5B69\u5B50\uFF0C\u591C\u78E8\u7259\u7684\u98CE\u9669\u4F1A\u589E\u5927\u3002\u5BF9\u817A\u6837\u4F53\u3001\u6241\u6843\u4F53\u80A5\u5927\u5BFC\u81F4\u7684\u7761\u7720\u547C\u5438\u969C\u788D\u7684\u5B69\u5B50\uFF0C\u624B\u672F\u540E\u591C\u78E8\u7259\u4E5F\u4F1A\u51CF\u8F7B\u3002</p><p><strong>\u78E8\u7259\u548C\u5BC4\u751F\u866B\u3001\u7F3A\u9499\u6709\u5173\u5417\uFF1F</strong></p><p>\u786E\u5B9E\u6709\u4EBA\u8BA4\u4E3A\u5BC4\u751F\u866B\u7684\u6D3B\u52A8\u53CA\u5176\u5206\u6CCC\u7684\u4EE3\u8C22\u4EA7\u7269\u8FDB\u5165\u4EBA\u4F53\u5185\uFF0C\u523A\u6FC0\u5927\u8111\u7684\u76F8\u5E94\u90E8\u4F4D\uFF0C\u4F1A\u5F15\u8D77\u591C\u78E8\u7259\uFF0C\u4F46\u76EE\u524D\u5E76\u6CA1\u6709\u8DB3\u591F\u7684\u8BC1\u636E\u8BC1\u5B9E\u8FD9\u4E00\u70B9\u3002\u6709\u4E9B\u4EBA\u89C9\u5F97\u7F3A\u9499\u4F1A\u5F15\u8D77\u5480\u56BC\u808C\u8089\u75C9\u631B\u5BFC\u81F4\u78E8\u7259\uFF0C\u4F46\u540C\u6837\u662F\u6CA1\u6709\u8BC1\u636E\u8BC1\u5B9E\u4E24\u8005\u7684\u5173\u8054\u3002</p><p>\u636E\u7EDF\u8BA1\u7EA630%\u7684\u5B69\u5B50\u5728\u6210\u957F\u8FC7\u7A0B\u4E2D\u4F1A\u51FA\u73B0\u78E8\u7259\uFF0C\u8FD9\u5176\u4E2D\u5C31\u5305\u62EC\u90A3\u4E9B\u5B8C\u5168\u5065\u5EB7\u7684\u5B69\u5B50\uFF0C\u6240\u4EE5\u6211\u4EEC\u4E0D\u80FD\u5355\u7EAF\u6839\u636E\u78E8\u7259\u5C31\u5224\u65AD\u5B69\u5B50\u809A\u5B50\u91CC\u6709\u5BC4\u751F\u866B\u6216\u8005\u7F3A\u9499\uFF0C\u6240\u4EE5\u4E0D\u9700\u8981\u56E0\u4E3A\u5B69\u5B50\u6709\u78E8\u7259\u5C31\u53BB\u201C\u6253\u866B\u201D\u6216\u989D\u5916\u8865\u9499\u3002</p><p><strong>\u78E8\u7259\u4F1A\u6709\u4EC0\u4E48\u5371\u5BB3\uFF1F</strong></p><p>\u5728\u5927\u591A\u6570\u60C5\u51B5\u4E0B\uFF0C\u78E8\u7259\u75C7\u4E0D\u4F1A\u5BF9\u8EAB\u4F53\u4EA7\u751F\u4E25\u91CD\u7684\u5371\u5BB3\uFF0C\u4F46\u4E25\u91CD\u7684\u78E8\u7259\u75C7\u5374\u53EF\u80FD\u5BFC\u81F4\u4E0B\u9762\u8FD9\u4E9B\u95EE\u9898\uFF1A</p><ol><li><p>\u7259\u91C9\u8D28\u8FC7\u5EA6\u78E8\u8017\uFF0C\u5BFC\u81F4\u7259\u5C16\u4F4E\u5E73\uFF0C\u4E34\u5E8A\u7259\u51A0\u53D8\u77ED\uFF0C\u65E2\u5F71\u54CD\u4E86\u7259\u9F7F\u7684\u7F8E\u89C2\uFF0C\u53C8\u4F7F\u7259\u672C\u8D28\u66B4\u9732\uFF0C\u8FDB\u800C\u4F7F\u7259\u9F7F\u5BF9\u98DF\u7269\u53CA\u6E29\u5EA6\u523A\u6FC0\u654F\u611F\u6027\u589E\u52A0\u3002\u5BF9\u4E8E\u5B69\u5B50\u6765\u8BF4\uFF0C\u56E0\u4E3A\u4ED6\u4EEC\u7684\u54AC\u5408\u529B\u91CF\u6709\u9650\uFF0C\u6240\u4EE5\u77ED\u6682\u800C\u95F4\u65AD\u7684\u78E8\u7259\u901A\u5E38\u4E0D\u81F3\u4E8E\u5F71\u54CD\u7259\u9F7F\u7684\u6574\u4F53\u7ED3\u6784\uFF0C\u53EA\u6709\u5728\u78E8\u7259\u75C7\u72B6\u5341\u5206\u4E25\u91CD\u7684\u65F6\u5019\uFF0C\u624D\u53EF\u80FD\u51FA\u73B0\u7259\u9F7F\u78E8\u8017\u4E25\u91CD\u7B49\u95EE\u9898\u3002</p></li><li><p>\u957F\u671F\u78E8\u7259\u53EF\u80FD\u4F1A\u9020\u6210\u5480\u56BC\u808C\u7684\u75B2\u52B3\uFF0C\u989E\u4E0B\u988C\u5173\u8282\u53D7\u7D2F\uFF0C\u751A\u81F3\u4E0B\u988C\u9AA8\u7684\u53D8\u5F62\uFF0C\u7EE7\u800C\u5F71\u54CD\u5480\u56BC\u7CFB\u7EDF\u7684\u529F\u80FD\u3002</p></li><li><p>\u591C\u78E8\u7259\u4EA7\u751F\u5404\u79CD\u7259\u9F7F\u78B0\u649E\u7684\u58F0\u97F3\u4F1A\u5F71\u54CD\u81EA\u5DF1\u4EE5\u53CA\u4EB2\u4EBA\u7684\u7761\u7720\u8D28\u91CF\uFF0C\u4E5F\u53EF\u80FD\u56E0\u6B64\u800C\u4EA7\u751F\u7CBE\u795E\u7D27\u5F20\u3001\u75B2\u52B3\u3001\u8BB0\u5FC6\u529B\u51CF\u9000\u3001\u5B66\u4E60\u548C\u5DE5\u4F5C\u6548\u7387\u4F4E\u4E0B\u7B49\u95EE\u9898\u3002</p></li></ol><p><strong>\u6709\u78E8\u7259\u600E\u4E48\u529E\uFF1F</strong></p><p>\u4E8B\u5B9E\u4E0A\uFF0C\u513F\u7AE5\u78E8\u7259\u5F88\u5E38\u89C1\uFF0C\u591C\u78E8\u7259\u901A\u5E38\u5E76\u4E0D\u4F1A\u5F15\u8D77\u4EC0\u4E48\u5F71\u54CD\uFF0C\u800C\u4E14\u5177\u6709\u4E00\u5B9A\u7684\u201C\u81EA\u6108\u6027\u201D\uFF0C\u53EF\u4EE5\u89C6\u4F5C\u5065\u5EB7\u5B69\u5B50\u6210\u957F\u53D1\u80B2\u8FC7\u7A0B\u4E2D\u7684\u4E00\u90E8\u5206\uFF0C\u5BB6\u957F\u65E0\u6CD5\u5F7B\u5E95\u9884\u9632\u6216\u8005\u5236\u6B62\uFF0C\u53EA\u8981\u5B69\u5B50\u6CA1\u6709\u5176\u5B83\u5065\u5EB7\u95EE\u9898\uFF0C\u5C31\u4E0D\u5FC5\u56E0\u4E3A\u5076\u5C14\u77ED\u6682\u7684\u78E8\u7259\u800C\u62C5\u5FC3\uFF0C\u4E5F\u4E0D\u9700\u8981\u7279\u522B\u7684\u5E72\u9884\u6027\u6CBB\u7597\uFF0C\u53EA\u9700\u5B9A\u671F\u590D\u8BCA\u89C2\u5BDF\u5B83\u7684\u8FDB\u5C55\u3002</p><p>\u540C\u65F6\uFF0C\u6211\u4EEC\u4E5F\u53EF\u4EE5\u5BF9\u4E0A\u8FF0\u78E8\u7259\u75C7\u7684\u5371\u9669\u56E0\u7D20\u8FDB\u884C\u9010\u4E00\u6392\u9664\uFF0C\u6BD4\u5982\u770B\u770B\u5B69\u5B50\u662F\u5426\u540C\u65F6\u5408\u5E76\u6253\u9F3E\uFF0C\u5982\u679C\u6709\u7684\u8BDD\u5C31\u9700\u8981\u627E\u8033\u9F3B\u5589\u79D1\u533B\u751F\u68C0\u67E5\uFF0C\u4E5F\u4E86\u89E3\u6709\u4EC0\u4E48\u4E8B\u60C5\u4F7F\u5B69\u5B50\u4EA7\u751F\u4E86\u7126\u8651\u548C\u7D27\u5F20\uFF0C\u907F\u514D\u4E8C\u624B\u70DF\u3001\u5496\u5561\u56E0\u3001\u4FDD\u6301\u5367\u5BA4\u5B89\u9759\u3001\u9002\u5F53\u51CF\u5C11\u8FC7\u5927\u7684\u8FD0\u52A8\u91CF\u4EE5\u53CA\u589E\u52A0\u5B69\u5B50\u7761\u7720\u65F6\u95F4\u7B49\u3002</p><p>\u5982\u679C\u591C\u78E8\u7259\u6301\u7EED\u5230\u4E86\u9752\u5C11\u5E74\u671F\u53CA\u6210\u5E74\u65E9\u671F\uFF0C\u5219\u53EF\u80FD\u5BF9\u5480\u56BC\u7CFB\u7EDF\u4EA7\u751F\u521B\u4F24\uFF0C\u5E94\u8BE5\u53CA\u65F6\u53BB\u770B\u533B\u751F\u67E5\u627E\u539F\u56E0\uFF0C\u5FC5\u8981\u65F6\u505A\u4E00\u4E9B\u5E72\u9884\u6CBB\u7597\uFF0C\u6216\u9700\u8981\u4F69\u6234\u4E00\u4E9B\u53E3\u8154\u88C5\u7F6E\u5982\u54AC\u5408\u677F\u7B49\u4FDD\u62A4\u7259\u9F7F\uFF0C\u8C03\u5408\u5480\u56BC\u808C\u677E\u5F1B\u6CBB\u7597\u751A\u81F3\u5FC3\u7406\u65B9\u9762\u7684\u6CBB\u7597\u7B49\u3002</p><hr><p>\u70B9\u9605\u8BFB\u539F\u6587\uFF0C\u53EF\u5411\u4F5C\u8005\u970D\u533B\u751F\u54A8\u8BE2</p><p>\u957F\u6309\u4E8C\u7EF4\u7801\uFF0C\u53EF\u5411\u66F4\u591A\u6021\u79BE\u533B\u751F\u54A8\u8BE2</p>',21),_={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651627615&idx=1&sn=ea50ddd8d716e4b09ba1c4333e48406c&chksm=8b5c89a5bc2b00b3bea69f00a518d1be99e8a36e243c415ad0ff8ef366dfc8c477ddfb503af8&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},f=c("\u67E5\u770B\u539F\u6587");function h(m,b){const p=r("ExternalLinkIcon");return t(),a(o,null,[l,e("p",null,[e("a",_,[f,n(p)])])],64)}var u=d(i,[["render",h]]);export{u as default};
