import{r as t,o as n,c as o,a as p,b as s,F as e,d as g,e as a}from"./app.9aaa2256.js";import{_ as c}from"./plugin-vue_export-helper.21dcd24c.js";const d={},_=g('<h3 id="\u5B69\u5B50\u600E\u6837\u7761\u66F4\u5B89\u5168-\u7F8E\u56FD\u513F\u79D1\u5B66\u4F1A\u6709\u65B0\u6307\u5357\u4E86" tabindex="-1"><a class="header-anchor" href="#\u5B69\u5B50\u600E\u6837\u7761\u66F4\u5B89\u5168-\u7F8E\u56FD\u513F\u79D1\u5B66\u4F1A\u6709\u65B0\u6307\u5357\u4E86" aria-hidden="true">#</a> \u5B69\u5B50\u600E\u6837\u7761\u66F4\u5B89\u5168\uFF1F\u7F8E\u56FD\u513F\u79D1\u5B66\u4F1A\u6709\u65B0\u6307\u5357\u4E86</h3><p>\u56E0\u4E3A\u62C5\u5FC3\u5B69\u5B50\u6241\u5934\u800C\u7528\u5B9A\u578B\u6795\uFF1B\u56E0\u4E3A\u62C5\u5FC3\u5410\u5976\u545B\u5230\u800C\u8BA9\u5B69\u5B50\u4FA7\u7761\uFF1B\u56E0\u4E3A\u62C5\u5FC3\u5B9D\u5B9D\u7FFB\u8EAB\u649E\u5230\u800C\u4F7F\u7528\u9632\u649E\u5E8A\u56F4\uFF0C\u56E0\u4E3A\u62C5\u5FC3\u5B69\u5B50\u4E00\u4E2A\u4EBA\u7761\u4E0D\u5B89\u5168\uFF0C\u800C\u62B1\u5B69\u5B50\u5230\u81EA\u5DF1\u5E8A\u4E0A\u3002\u8FD9\u4E9B\u4E8B\u4F60\u505A\u8FC7\u5417\uFF1F</p><p>\u5F88\u591A\u770B\u4F3C\u4E3A\u5B69\u5B50\u597D\u7684\u505A\u6CD5\uFF0C\u5176\u5B9E\u53CD\u800C\u53EF\u80FD\u589E\u52A0\u5B69\u5B50\u7684\u5371\u9669\uFF0C\u6BCF\u5E74\u90FD\u6709\u5B69\u5B50\u56E0\u4E3A\u5BB6\u957F\u7684\u201C\u597D\u5FC3\u201D\u800C\u5931\u53BB\u4E86\u751F\u547D\u3002\u533B\u52A1\u4EBA\u5458\u5982\u679C\u4E0D\u4E86\u89E3\uFF0C\u540C\u6837\u4F1A\u589E\u52A0\u5B69\u5B50\u4E0D\u5FC5\u8981\u7684\u98CE\u9669\uFF0C\u6BD4\u5982\u4E0B\u9762\u8FD9\u6BB5\u89C6\u9891\u5C31\u8BA9\u5F88\u591A\u5BB6\u957F\u5FC3\u75DB\u4E0D\u5DF2\u3002</p><p>\u5173\u4E8E\u5982\u4F55\u8BA9\u5B69\u5B50\u7761\u5F97\u66F4\u5B89\u5168\uFF0C\u5176\u5B9E\u662F\u4E00\u4E2A\u8001\u751F\u5E38\u8C08\u7684\u8BDD\u9898\u3002\u4F46\u7F8E\u56FD\u513F\u79D1\u5B66\u4F1A\u8FD1\u65E5\u53C8\u53D1\u5E03\u4E00\u4EFD\u65B0\u7684\u4E34\u5E8A\u6307\u5357\uFF0C\u91CC\u9762\u6709\u4E0D\u5C11\u548C\u65E5\u5E38\u80B2\u513F\u64CD\u4F5C\u76F8\u5173\u7684\u8981\u70B9\uFF0C\u548C\u5927\u5BB6\u5206\u4EAB\u4E00\u4E0B\u3002</p><p><strong>\u4E00\u5C81\u5185\u8981\u4EF0\u5367</strong></p><p>\u4EF0\u5367\u662F\u5A74\u513F\u7761\u7720\u5B89\u5168\u53CD\u590D\u5F3A\u8C03\u7684\u4E00\u4E2A\u95EE\u9898\uFF0C\u65B0\u7684\u6307\u5357\u91CC\u6CA1\u6709\u53D8\u5316\uFF0C\u4F9D\u7136\u5F3A\u8C03\uFF1A<strong>\u4E0D\u8BBA\u662F\u767D\u5929\u8FD8\u662F\u665A\u4E0A\uFF0C\u4E0D\u8BBA\u662F\u65E9\u4EA7\u513F\u8FD8\u662F\u8DB3\u6708\u513F\uFF0C\u5728\u5468\u5C81\u5185\u90FD\u5E94\u8BE5\u4EF0\u5367</strong>\u3002</p><p>\u5BF9\u4E8E\u7ECF\u5E38\u5410\u5976\uFF0C\u6BD4\u5982\u6709\u80C3\u98DF\u7BA1\u53CD\u6D41\u7684\u5B69\u5B50\uFF0C\u5BB6\u957F\u5E38\u5E38\u62C5\u5FC3\u8FD4\u6D41\u7684\u6DB2\u4F53\u6216\u98DF\u7269\u5438\u5230\u6C14\u7BA1\u5BFC\u81F4\u7A92\u606F\u3002\u4F46\u5176\u5B9E\uFF0C\u4E86\u89E3\u4E86\u98DF\u7BA1\u548C\u6C14\u7BA1\u7684\u89E3\u5256\u5173\u7CFB\uFF0C\u5C31\u4F1A\u660E\u767D\uFF0C\u8FD9\u79CD\u62C5\u5FC3\u662F\u591A\u4F59\uFF0C\u539F\u7406\u6211\u5DF2\u7ECF\u5728\u4E4B\u524D\u7684\u79D1\u666E\u6587\u7AE0\u4E2D\u4ECB\u7ECD\u8FC7\u4E86\u3002\uFF08\u516C\u4F17\u53F7\uFF1A<strong>DrHAN_AU</strong>\u5386\u53F2\u6D88\u606F\u91CC\u67E5\u770B\u300A\u80C3\u98DF\u7BA1\u53CD\u6D41\u75C5\u7684\u5B9D\u5B9D\u5982\u4F55\u79D1\u5B66\u7684\u5582\u548C\u7761\uFF1F\u300B\u4E00\u6587\uFF09</p><p>\u56E0\u6B64\uFF0C\u5065\u5EB7\u7684\u5B69\u5B50\u5E94\u8BE5\u4EF0\u5367\uFF0C\u4E00\u5C81\u5185\u6709\u80C3\u98DF\u7BA1\u53CD\u6D41\u75C5\u7684\u5B69\u5B50\u540C\u6837\u5EFA\u8BAE\u4EF0\u5367\u4F4D\uFF0C\u4F0F\u8DB4\u7684\u59FF\u52BF\u53EA\u53EF\u4EE5\u5728\u6E05\u9192\u65F6\u4E14\u5728\u5927\u4EBA\u76D1\u62A4\u4E0B\u5C1D\u8BD5\u3002\u4F46\u5BF9\u4E8E\u4E00\u5C0F\u90E8\u5206\u80C3\u98DF\u7BA1\u53CD\u6D41\u5408\u5E76\u4E0A\u547C\u5438\u9053\u7578\u5F62\u95EE\u9898\u7684\u5E7C\u513F\uFF0C\u5219\u9700\u8981\u533B\u751F\u505A\u4E2A\u4F53\u5316\u8BC4\u4F30\u3002</p><p>\u5728\u8BA8\u8BBA\u7761\u59FF\u7684\u65F6\u5019\uFF0C\u5988\u5988\u4EEC\u6700\u7ECF\u5E38\u95EE\u5230\u7684\u662F\uFF1A\u5B69\u5B50\u81EA\u5DF1\u7FFB\u8EAB\u4E86\u600E\u4E48\u529E\uFF1F\u6700\u65B0\u7684\u6307\u5357\u91CC\u63D0\u5230\uFF0C\u5982\u679C\u5B69\u5B50\u80FD\u591F\u6765\u56DE\u81EA\u5982\u7684\u7FFB\u8EAB\u800C\u51FA\u73B0\u4FA7\u7761\u6216\u8005\u8DB4\u7761\uFF0C\u5BB6\u957F\u53EF\u4EE5\u4E0D\u7528\u7279\u610F\u628A\u5B69\u5B50\u7FFB\u56DE\u6765\uFF0C\u4F46\u9700\u8981\u4FDD\u8BC1\u5B69\u5B50\u8EAB\u8FB9\u6CA1\u6709\u6BEF\u5B50\u3001\u6795\u5934\u3001\u6BDB\u7ED2\u73A9\u5177\u3001\u4FDD\u62A4\u57AB\u4E4B\u7C7B\u4F1A\u589E\u52A0\u5B69\u5B50\u7A92\u606F\u98CE\u9669\u7684\u7269\u54C1\u3002</p><p><strong>\u79FB\u9664\u67D4\u8F6F\u548C\u6563\u5728\u7684\u5E8A\u4E0A\u7528\u54C1</strong></p><p>\u5176\u5B9E\uFF0C\u4E0D\u8BBA\u5B69\u5B50\u662F\u5426\u80FD\u7FFB\u8EAB\uFF0C\u90FD\u4E0D\u8981\u8BA9\u5A74\u513F\u5E8A\u4E0A\u6709\u4EFB\u4F55\u53EF\u80FD\u906E\u76D6\u5B69\u5B50\u9762\u90E8\u7684\u67D4\u8F6F\u7269\u54C1\uFF0C\u56E0\u4E3A\u8FD9\u4E9B\u7269\u54C1\u4F1A\u589E\u52A0\u4E09\u6708\u9F84\u540E\u5A74\u513F\u7684\u731D\u6B7B\u98CE\u9669\u3002\u5728\u4E4B\u524D\u7684\u5B89\u5168\u7761\u7720\u6307\u5357\u91CC\uFF0C\u63D0\u51FA\u67D4\u8F6F\u7269\u54C1\u57284\u6708\u9F84\u540E\u53EF\u4EE5\u4F7F\u7528\uFF0C\u4F46\u662F\u5728\u8FD9\u6B21\u65B0\u7684\u6307\u5357\u91CC\uFF0C\u63D0\u51FA\u4E86<strong>12\u6708\u9F84\u524D\uFF0C\u90FD\u5E94\u8BE5\u907F\u514D\u5A74\u513F\u5E8A\u4E0A\u6709\u67D4\u8F6F\u7684\u7269\u54C1\u3002</strong></p><p><strong>\u751F\u540E\u5E94\u5C3D\u5FEB\u6BCD\u5A74\u808C\u80A4\u63A5\u89E6</strong></p><p>\u7F8E\u56FD\u513F\u79D1\u5B66\u4F1A\u5728\u8FD9\u6B21\u6307\u5357\u91CC\u7B2C\u4E00\u6B21\u660E\u786E\u63D0\u51FA\uFF1A\u4E0D\u8BBA\u54EA\u79CD\u5582\u517B\u65B9\u5F0F\u548C\u5206\u5A29\u65B9\u5F0F\uFF0C\u53EA\u8981\u5988\u5988\u4EA7\u540E\u610F\u8BC6\u6E05\u9192\uFF0C\u72B6\u51B5\u7A33\u5B9A\uFF0C\u80FD\u5BF9\u81EA\u5DF1\u7684\u5B9D\u5B9D\u6709\u54CD\u5E94\uFF0C\u90A3\u5C31<strong>\u5E94\u8BE5\u5C3D\u5FEB\u548C\u5B69\u5B50\u808C\u80A4\u63A5\u89E6\u81F3\u5C11\u4E00\u4E2A\u5C0F\u65F6</strong>\u3002\u6839\u636E\u73B0\u6709\u7684\u8BC1\u636E\uFF0C\u65B0\u751F\u513F\u5728\u51FA\u751F\u540E\u5934\u51E0\u4E2A\u5C0F\u65F6\u4FA7\u5367\u4F4D\u5E76\u4E0D\u80FD\u5E2E\u52A9\u6E05\u9664\u7F8A\u6C34\u51CF\u5C11\u5438\u5165\u98CE\u9669\u3002\u56E0\u6B64\uFF0C\u808C\u80A4\u63A5\u89E6\u5B8C\u6BD5\u4E4B\u540E\uFF0C\u6216\u8005\u4EA7\u5987\u9700\u8981\u5176\u4ED6\u5904\u7406\u7684\u65F6\u5019\uFF0C\u5C31\u5E94\u8BE5\u5C06\u65B0\u751F\u513F\u4EF0\u5367\u4F4D\u653E\u5728\u6447\u7BEE\u3002</p><p><strong>\u4F7F\u7528\u575A\u5B9E\u7684\u5E8A\u57AB</strong></p><p>\u548C\u4E4B\u524D\u7684\u63A8\u8350\u4E00\u6837\uFF0C\u65B0\u7684\u6307\u5357<strong>\u4F9D\u7136\u5F3A\u8C03\u5A74\u513F\u5E94\u8BE5\u5728\u575A\u5B9E\u7684\u5E8A\u57AB\u4E0A\u7761\u89C9</strong>\uFF0C\u5E8A\u4E0A\u6CA1\u6709\u5176\u4ED6\u7684\u5E8A\u4E0A\u7528\u54C1\u6216\u8005\u67D4\u8F6F\u7684\u7269\u4EF6\u3002\u592A\u67D4\u8F6F\u7684\u5E8A\u57AB\u6216\u8005\u8BB0\u5FC6\u6D77\u7EF5\uFF0C\u4F1A\u56E0\u4E3A\u5A74\u513F\u7684\u5934\u90E8\u91CD\u91CF\u800C\u538B\u51FA\u51F9\u9677\uFF0C\u4ECE\u800C\u5BFC\u81F4\u5728\u4E0D\u5C0F\u5FC3\u7FFB\u8EAB\u4E4B\u540E\u589E\u52A0\u7A92\u606F\u7684\u98CE\u9669\u3002</p><p>\u5BB6\u957F\u54C4\u7761\u65F6\u53EF\u80FD\u4F1A\u7528\u5230\u8F66\u5EA7\u6905\uFF0C\u5A74\u513F\u63A8\u8F66\uFF0C\u6447\u6447\u6905\uFF0C\u513F\u7AE5\u80CC\u5E26\u80CC\u5DFE\u7B49\uFF0C\u4F46\u662F\u8FD9\u4E9B\u90FD\u4E0D\u63A8\u8350\u4F5C\u4E3A\u5C0F\u5E7C\u513F\u7684\u5E38\u89C4\u7761\u7720\u73AF\u5883\uFF0C\u5C24\u5176\u5BF94\u6708\u4EE5\u5185\u7684\u5B69\u5B50\u3002\u53E6\u5916\uFF0C\u5728\u4F7F\u7528\u80CC\u5DFE\u6216\u8005\u80CC\u5E26\u65F6\uFF0C\u8BF7\u4E00\u5B9A\u786E\u8BA4\u5B69\u5B50\u7684\u5934\u90E8\uFF0C\u5634\u5DF4\u548C\u9F3B\u5B50\u6CA1\u6709\u4EFB\u4F55\u906E\u76D6\u3002</p><p><strong>\u6BCD\u4E73\u5582\u517B</strong></p><p><strong>\u6BCD\u4E73\u5582\u517B\u53EF\u4EE5\u964D\u4F4E\u5A74\u513F\u731D\u6B7B\u7EFC\u5408\u5F81\u7684\u98CE\u9669</strong>\uFF0C\u53E6\u5916\u8FD8\u6709\u7814\u7A76\u8868\u660E\u7EAF\u6BCD\u4E73\u5582\u517B\u7684\u4FDD\u62A4\u6027\u9AD8\u4E8E\u6DF7\u5408\u5582\u517B\uFF0C\u800C\u7EAF\u6BCD\u4E73\u5582\u517B\u6216\u8005\u6DF7\u5408\u5582\u517B\u7684\u4FDD\u62A4\u6027\u90FD\u9AD8\u4E8E\u5176\u4ED6\u5582\u517B\u65B9\u5F0F\u3002</p><p><strong>\u5A74\u513F\u4E0E\u7236\u6BCD\u540C\u623F\u4E0D\u540C\u5E8A</strong></p><p>\u6700\u65B0\u6307\u5357\u5EFA\u8BAE\uFF0C\u5B69\u5B50\u548C\u5927\u4EBA<strong>\u540C\u623F\u4E0D\u540C\u5E8A\u7684\u7761\u7720\u65B9\u5F0F\uFF0C\u5E94\u8BE5\u6301\u7EED\u5230\u751F\u540E\u81F3\u5C116\u4E2A\u6708\uFF0C\u5982\u679C\u80FD\u5230\u4E00\u5E74\u5C31\u66F4\u597D</strong>\u3002\u53EA\u6709\u5728\u5582\u5976\u6216\u8005\u5B89\u629A\u65F6\u624D\u80FD\u62B1\u5B69\u5B50\u5230\u5927\u4EBA\u5E8A\u4E0A\u3002</p><p>\u4E00\u4E9B\u5988\u5988\u53EF\u80FD\u4F1A\u62C5\u5FC3\u53E6\u5916\u4E00\u4E2A\u95EE\u9898\uFF0C\u534A\u591C\u628A\u5B9D\u5B9D\u62B1\u5230\u5927\u4EBA\u5E8A\u4E0A\u5582\u591C\u5976\uFF0C\u5173\u4E8E\u4E07\u4E00\u7761\u7740\u4E86\u600E\u4E48\u529E\uFF1F\u5176\u5B9E\u8FD9\u79CD\u72B6\u51B5\u786E\u5B9E\u662F\u5F88\u5371\u9669\uFF0C\u65B0\u7684\u6307\u5357\u4E2D\u4E5F\u63D0\u5230\u4E86\uFF0C\u4E3A\u4E86\u964D\u4F4E\u98CE\u9669\uFF0C\u5582\u591C\u5976\u65F6\u5927\u4EBA\u5E8A\u4E0A\u4E5F\u5E94\u907F\u514D\u6709\u6BEF\u5B50\uFF0C\u6795\u5934\u6216\u8005\u5176\u4ED6\u7684\u67D4\u8F6F\u7269\u4EF6\uFF0C\u4E00\u65E6\u9192\u6765\uFF0C\u8981\u7B2C\u4E00\u65F6\u95F4\u5C06\u5B9D\u5B9D\u653E\u56DE\u5230\u81EA\u5DF1\u7684\u5C0F\u5E8A\u6216\u8005\u6447\u7BEE\u3002</p><p>\u53E6\u5916\uFF0C\u6307\u5357\u4E5F\u9996\u6B21\u63D0\u51FA\uFF0C\u5F53\u5BB6\u957F\u4E0E\u5A74\u513F\u4E00\u8D77\u5728\u6C99\u53D1\uFF0C\u6276\u624B\u6905\u6216\u8005\u8EBA\u6905\u7B49\u5730\u65B9\u4E00\u8D77\u8EBA\u7740\u4F11\u606F\uFF0C\u6216\u8005\u5582\u5976\u65F6\uFF0C\u4E00\u5B9A\u6CE8\u610F\u6CE8\u610F\u4FDD\u6301\u6E05\u9192\uFF0C\u6C38\u8FDC\u4E0D\u80FD\u8BA9\u5A74\u513F\u5728\u8FD9\u4E9B\u73AF\u5883\u91CC\u7761\u7740\uFF0C\u56E0\u4E3A\u8FD9\u4E9B\u5730\u65B9\u6709\u6781\u9AD8\u7684\u7761\u7720\u610F\u5916\u6B7B\u4EA1\u98CE\u9669\u3002</p><p><strong>\u767D\u5929\u5C0F\u7761\u6216\u8005\u591C\u7761\u53EF\u4EE5\u8BD5\u7528\u5976\u5634</strong></p><p>\u7814\u7A76\u53D1\u73B0\u4F7F\u7528\u5976\u5634\u53EF\u4EE5\u964D\u4F4E\u5A74\u513F\u731D\u6B7B\u7EFC\u5408\u5F81\u98CE\u9669\uFF0C\u4F46\u662F\u5176\u4E2D\u539F\u7406\u8FD8\u6CA1\u5B8C\u5168\u660E\u786E\u3002\u66F4\u4EE4\u4EBA\u610F\u5916\u7684\u662F\uFF0C\u8FD9\u6B21\u7684\u6307\u5357\u4E2D\u63D0\u5230\uFF0C\u5373\u4F7F\u5976\u5634\u5728\u7761\u89C9\u8FC7\u7A0B\u4E2D\u6389\u51FA\u6765\u4E86\uFF0C\u8FD9\u4E2A\u4FDD\u62A4\u6027\u4F5C\u7528\u4ECD\u7136\u5B58\u5728\u3002</p><p><strong>\u907F\u514D\u5A74\u5E7C\u513F\u8FC7\u70ED\u53CA\u906E\u76D6\u5934\u9762\u90E8</strong></p><p><strong>\u8FC7\u70ED\u53EF\u4EE5\u589E\u52A0\u5A74\u5E7C\u513F\u731D\u6B7B\u7EFC\u5408\u5F81\u7684\u98CE\u9669</strong>\uFF0C\u4F46\u5728\u4E0D\u540C\u7684\u7814\u7A76\u4E2D\uFF0C\u5BF9\u4E8E\u201C\u8FC7\u70ED\u201D\u7684\u5B9A\u4E49\u5B58\u5728\u5DEE\u5F02\uFF0C\u6240\u4EE5\u6307\u5357\u91CC\u9762\u5E76\u6CA1\u6709\u76F4\u63A5\u544A\u8BC9\u5230\u5E95\u51E0\u5EA6\u624D\u662F\u8FC7\u70ED\u3002\u901A\u5E38\u6765\u8BB2\uFF0C\u53EF\u4EE5\u53C2\u7167\u7236\u6BCD\u7740\u8863\u7684\u8212\u9002\u5EA6\uFF0C\u5A74\u5E7C\u513F\u6700\u591A\u53EA\u6BD4\u7236\u6BCD\u591A\u4E00\u5C42\u3002\u540C\u65F6\uFF0C\u5BB6\u957F\u5E94\u8BE5\u6CE8\u610F\u53CA\u65F6\u7684\u89C2\u5BDF\u5A74\u5E7C\u513F\u8FC7\u70ED\u6216\u8005\u8FC7\u51B7\uFF0C\u53CA\u65F6\u589E\u51CF\u8863\u7269\u3002\u5982\u679C\u5B69\u5B50\u7684\u624B\u6E29\u70ED\u6F6E\u6E7F\u4E86\uFF0C\u8138\u4E0A\u7EA2\u7EA2\u70EB\u70EB\u7684\uFF0C\u901A\u5E38\u8BF4\u660E\u5DF2\u7ECF\u8FC7\u70ED\u4E86\u3002\uFF08\u56DE\u590D\u201C\u7A7F\u8863\u201D\u53EF\u8BE6\u7EC6\u4E86\u89E3\u600E\u6837\u7ED9\u5B69\u5B50\u7A7F\u8863\uFF09</p><p><strong>\u6309\u5E38\u89C4\u63A5\u79CD\u75AB\u82D7</strong></p><p>\u76EE\u524D\u6CA1\u6709\u75AB\u82D7\u63A5\u79CD\u4F1A\u5BFC\u81F4\u5A74\u513F\u731D\u6B7B\u7EFC\u5408\u5F81\u7684\u8BC1\u636E; \u4E8B\u5B9E\u4E0A\uFF0C\u6700\u65B0\u7684\u8BC1\u636E\u8868\u660E\u75AB\u82D7\u63A5\u79CD\u8FD8\u53EF\u80FD\u53EF\u4EE5\u964D\u4F4E\u5A74\u513F\u731D\u6B7B\u7EFC\u5408\u5F81\u7684\u98CE\u9669\uFF0C\u7ED9\u5B69\u5B50\u63D0\u4F9B\u4FDD\u62A4\u3002</p><p><strong>\u5B55\u671F\u53CA\u4EA7\u540E\u5438\u70DF\uFF0C\u996E\u9152\u4EE5\u53CA\u4F7F\u7528\u8FDD\u7981\u836F\u54C1\uFF0C\u4F1A\u589E\u52A0\u5A74\u513F\u7761\u7720\u610F\u5916\u98CE\u9669</strong></p><p>\u5B55\u671F\u5438\u70DF\uFF0C\u4EE5\u53CA\u5B55\u5988\u5988\u6216\u8005\u5A74\u513F\u66B4\u9732\u4E8E\u5438\u70DF\u73AF\u5883\u90FD\u662F\u5A74\u513F\u731D\u6B7B\u7EFC\u5408\u5F81\u7684\u91CD\u8981\u5371\u9669\u56E0\u7D20\uFF0C\u5747\u9700\u907F\u514D\u3002\u53E6\u5916\uFF0C\u5982\u679C\u5A74\u513F\u4E0E\u5438\u70DF\u7684\u5BB6\u957F\u540C\u5E8A\uFF0C\u5373\u4F7F\u5BB6\u957F\u6CA1\u5728\u5E8A\u4E0A\u5438\u70DF\uFF0C\u7761\u7720\u610F\u5916\u7684\u98CE\u9669\u4E5F\u4F1A\u589E\u9AD8\u3002</p><p>\u4E0E\u5438\u70DF\u7C7B\u4F3C\u7684\u53E6\u5916\u4E00\u4E2A\u91CD\u8981\u5371\u9669\u56E0\u7D20\u5C31\u662F\u996E\u9152\u6216\u8005\u670D\u7528\u6709\u9547\u9759\u4F5C\u7528\u7684\u836F\u7269\u3002\u6307\u5357\u91CC\u63D0\u51FA\uFF0C\u5BB6\u957F\u559D\u9152\u6216\u4F7F\u7528\u8FDD\u7981\u836F\u54C1\uFF0C\u4E0E\u5A74\u513F\u540C\u5E8A\u7761\u89C9\u65F6\uFF0C\u4F1A\u4F7F\u5B69\u5B50\u9762\u5BF9\u6781\u9AD8\u7684\u7761\u7720\u610F\u5916\u98CE\u9669\u3002</p><p><strong>\u4E0E\u5B89\u5168\u7761\u7720\u73AF\u5883\u6307\u5357\u76F8\u8FDD\u80CC\u7684\u4EA7\u54C1\uFF0C\u4E0D\u7BA1\u5BA3\u79F0\u4E3A\u4EC0\u4E48\u529F\u6548\u90FD\u5E94\u8BE5\u62D2\u7EDD\u4F7F\u7528</strong></p><p>\u5E02\u9762\u4E0A\u5404\u7C7B\u5A74\u5E7C\u513F\u5546\u54C1\u5C42\u51FA\u4E0D\u7A77\uFF0C\u800C\u4E14\u591A\u559C\u6B22\u5BA3\u79F0\u4E00\u4E9B\u7279\u6B8A\u4F5C\u7528\u548C\u529F\u6548\u3002\u7F8E\u56FD\u513F\u79D1\u5B66\u4F1A\u6700\u65B0\u6307\u5357\u91CC\u6307\u51FA\uFF0C<strong>\u9700\u8981\u907F\u514D\u4F7F\u7528\u5404\u7C7B\u5B9A\u578B\u6795\uFF0C\u9694\u79BB\u57AB\uFF0C\u6216\u8005\u5176\u4ED6\u7684\u5BA3\u79F0\u53EF\u4EE5\u5E2E\u52A9\u56FA\u5B9A\u59FF\u52BF\uFF08\u6BD4\u5982\u7761\u59FF\u6216\u8005\u5934\u4F4D\uFF09\uFF0C\u80FD\u5E2E\u52A9\u5B69\u5B50\u548C\u5927\u4EBA\u540C\u5E8A\u65F6\u4E92\u76F8\u9694\u79BB\u7684\u4EA7\u54C1\u3002</strong></p><p>\u6211\u4EEC\u9762\u5BF9\u773C\u82B1\u7F2D\u4E71\u7684\u4EA7\u54C1\u65E0\u4ECE\u9009\u62E9\u65F6\uFF0C\u6700\u597D\u7684\u505A\u6CD5\u662F\u56DE\u987E\u4E00\u4E0B\u5B89\u5168\u7761\u7720\u7684\u57FA\u672C\u8981\u70B9\uFF0C\u53EA\u8981\u4E0E\u8FD9\u4E9B\u539F\u5219\u76F8\u8FDD\u80CC\uFF0C\u90A3\u4E0D\u7BA1\u4EA7\u54C1\u529F\u80FD\u4ECB\u7ECD\u6709\u591A\u795E\u5947\uFF0C\u4E5F\u5E94\u8BE5\u62D2\u7EDD\u4F7F\u7528\u3002</p><p>\u5728\u5A74\u5E7C\u513F\u6E05\u9192\u65F6\uFF0C\u5EFA\u8BAE\u5728\u5BB6\u957F\u76D1\u62A4\u4E0B\u4F0F\u8DB4\u6D3B\u52A8\u6765\uFF0C\u4EE5\u4FC3\u8FDB\u53D1\u80B2\uFF0C\u51CF\u5C11\u5934\u578B\u5F02\u5E38\u98CE\u9669\u3002</p><p>\u6BCF\u6B21\u8BF4\u5230\u4EF0\u5367\u4F4D\u7761\u7720\u7684\u65F6\u5019\uFF0C\u5F88\u591A\u5BB6\u957F\u90FD\u62C5\u5FC3\u6241\u5934\u7684\u95EE\u9898\u3002\u8FD9\u6B21\u6307\u5357\u91CC\u4E5F\u7684\u786E\u517C\u987E\u5230\u4E86\u8FD9\u4E2A\u62C5\u5FE7\uFF0C\u4F9D\u7136<strong>\u5EFA\u8BAE\u5728\u5B69\u5B50\u6E05\u9192\u591A\u4F0F\u8DB4</strong>\uFF0C\u4E0D\u4EC5\u53EF\u4EE5\u5E2E\u52A9\u7EC3\u4E60\u62AC\u5934\uFF0C\u589E\u52A0\u9888\u80A9\u90E8\u4F4D\u7684\u808C\u8089\u529B\u91CF\uFF0C\u540C\u65F6\u4E5F\u51CF\u5C11\u6241\u5934\u4E4B\u7C7B\u4F53\u4F4D\u6027\u5934\u578B\u5F02\u5E38\u7684\u62C5\u5FC3\u3002\u81F3\u4E8E\u5230\u5E95\u4E00\u5929\u51E0\u6B21\uFF0C\u6BCF\u6B21\u591A\u4E45\u7684\u4F0F\u8DB4\uFF0C\u6307\u5357\u91CC\u5E76\u6CA1\u6709\u660E\u786E\u7684\u6570\u503C\u548C\u63A8\u8350\uFF0C\u4F46\u662F\u901A\u5E38\u6765\u8BB2\u662F\u4EE5\u5B9D\u5B9D\u8212\u9002\u4E3A\u524D\u63D0\uFF0C\u5982\u679C\u5B9D\u5B9D\u54ED\u95F9\u62D2\u7EDD\uFF0C\u90A3\u81EA\u7136\u5E94\u8BE5\u53CA\u65F6\u505C\u6B62\u3002</p><p><strong>\u6CA1\u6709\u8BC1\u636E\u652F\u6301\u4F7F\u7528\u8941\u8913\u6216\u8005\u5305\u88F9\u53EF\u4EE5\u51CF\u5C11\u5A74\u513F\u731D\u6B7B\u7EFC\u5408\u5F81</strong></p><p>\u5BF9\u4E8E\u5C0F\u5E7C\u513F\u5408\u7406\u7684\u5305\u88F9\u6216\u8005\u8941\u8913\u662F\u4E00\u79CD\u5E38\u7528\u7684\u5B89\u629A\u65B9\u5F0F\uFF0C\u4F46\u662F\u5FC5\u987B\u8981\u505A\u5230\u4EF0\u5367\u4F4D\uFF0C\u56E0\u4E3A\u5305\u88F9\u7740\u7684\u5B9D\u5B9D\u5982\u679C\u5728\u4FEF\u5367\u6216\u8005\u4E0D\u5C0F\u5FC3\u7FFB\u8EAB\u4FEF\u5367\u6709\u5F88\u9AD8\u7684\u7A92\u606F\u6B7B\u4EA1\u98CE\u9669\u3002\u56E0\u6B64\uFF0C\u6700\u65B0\u7684\u6307\u5357\u91CC\u63D0\u51FA\uFF0C<strong>\u4E00\u65E6\u5A74\u513F\u4F53\u73B0\u7FFB\u8EAB\u7684\u610F\u613F\uFF0C\u5C31\u5E94\u8BE5\u505C\u6B62\u5305\u88F9\u6216\u8005\u4F7F\u7528\u8941\u8913\u3002</strong></p><p>\u4EE5\u4E0A\u662F\u8FD9\u6B21\u7F8E\u56FD\u513F\u79D1\u5B66\u4F1A\u6700\u65B0\u53D1\u5E03\u7684\u5B89\u5168\u7761\u7720\u6307\u5357\u7684\u4E00\u4E9B\u8981\u70B9\uFF0C\u5E0C\u671B\u8FD9\u4E9B\u91CD\u8981\u800C\u53C8\u6709\u7528\u7684\u4FE1\u606F\uFF0C\u80FD\u8BA9\u66F4\u591A\u4EBA\u770B\u5230\uFF0C\u8BA9\u89C6\u9891\u91CC\u90A3\u6837\u7684\u60B2\u5267\u66F4\u5C11\u4E00\u70B9\u3002 \uFF08\u9898\u56FE\u6765\u81EAPixabay\uFF09</p><hr><p>\u957F\u6309\u4E0B\u9762\u4E8C\u7EF4\u7801\u52A0\u5173\u6CE8\uFF0C\u53EF\u5411\u513F\u79D1\u533B\u751F\u54A8\u8BE2\u513F\u7AE5\u7761\u7720\u95EE\u9898</p>',41),i={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651626449&idx=1&sn=cc4a888ea5e23c7e6203b58081cafb88&chksm=8b5cb22bbc2b3b3d9411fc2864d702761b50e4469314161bf8b3cf0f69f72e1e36738c87019b&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},b=a("\u67E5\u770B\u539F\u6587");function h(l,f){const r=t("ExternalLinkIcon");return n(),o(e,null,[_,p("p",null,[p("a",i,[b,s(r)])])],64)}var u=c(d,[["render",h]]);export{u as default};
