import{r,o as t,c as n,a as p,b as o,F as s,d as a,e as c}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const _={},i=a('<h3 id="\u4E0D\u8BBA\u5256\u4E0D\u5256-\u4E0D\u610F\u5473\u7740\u8981\u786C\u625B" tabindex="-1"><a class="header-anchor" href="#\u4E0D\u8BBA\u5256\u4E0D\u5256-\u4E0D\u610F\u5473\u7740\u8981\u786C\u625B" aria-hidden="true">#</a> \u4E0D\u8BBA\u5256\u4E0D\u5256\uFF0C\u4E0D\u610F\u5473\u7740\u8981\u786C\u625B</h3><p>\u4ECA\u5929\uFF0C\u9655\u897F\u6986\u6797\u4E00\u4EA7\u5987\u75BC\u75DB\u96BE\u5FCD\uFF0C\u6700\u540E\u8DF3\u697C\u8EAB\u4EA1\u7684\u65B0\u95FB\u5728\u7F51\u4E0A\u88AB\u70ED\u8BAE\u3002</p><p>\u867D\u7136\u533B\u60A3\u53CC\u65B9\u5BF9\u4E8E\u8C01\u4E0D\u80AF\u7ED9\u4EA7\u5987\u5256\u8179\u4EA7\u4ECD\u5404\u6267\u4E00\u8BCD\uFF0C\u4F46\u6709\u4E00\u4E2A\u4E8B\u5B9E\u662F\uFF0C\u56FD\u5185\u533B\u9662\u5BF9\u4E8E\u75C5\u4EBA\u75BC\u75DB\u7BA1\u7406\u7684\u610F\u8BC6\u786E\u5B9E\u666E\u904D\u4E0D\u8DB3\uFF0C\u54EA\u6015\u5C31\u662F\u987A\u4EA7\uFF0C\u5E76\u4E0D\u610F\u5473\u7740\u53EA\u80FD\u54AC\u7740\u7259\u786C\u625B\u3002\u8BA9\u75C5\u4EBA\u75BC\u5230\u65E0\u6CD5\u5FCD\u53D7\u6700\u540E\u8DF3\u697C\u8EAB\u4EA1\uFF0C\u662F\u4E0D\u5E94\u8BE5\u53D1\u751F\u8FD9\u4E2A\u65F6\u4EE3\u7684\u4E8B\u60C5\uFF0C\u56E0\u4E3A\u73B0\u5728\u6709\u5206\u5A29\u9547\u75DB\u6280\u672F\u3002</p><p>\u6021\u79BE\u4E0A\u66FE\u7ECF\u63A8\u9001\u8FC7\u4E00\u7BC7\u6765\u81EA\u6B66\u6C49\u534F\u548C\u533B\u9662\u6C6A\u5E73\u533B\u751F\u5199\u7684\u76F8\u5173\u79D1\u666E\uFF0C\u8F6C\u53D1\u7ED9\u5927\u5BB6\u3002\u867D\u7136\u66F4\u9700\u8981\u4E86\u89E3\u8FD9\u4E9B\u77E5\u8BC6\u7684\u662F\u533B\u62A4\u4EBA\u5458\uFF0C\u4F46\u4F5C\u4E3A\u4E00\u4E2A\u51C6\u5988\u5988\u6216\u8005\u5C06\u6765\u7684\u51C6\u5988\u5988\uFF0C\u4E86\u89E3\u8FD9\u4E9B\uFF0C\u53EF\u4EE5\u4F5C\u4E3A\u9009\u62E9\u5206\u5A29\u533B\u9662\u6216\u8005\u9EBB\u9189\u533B\u751F\u7684\u4E00\u4E2A\u53C2\u8003\u3002</p><p>\u5728\u533B\u5B66\u4E0D\u53D1\u8FBE\u7684\u5E74\u4EE3\uFF0C\u751F\u5B69\u5B50\u5BF9\u5F88\u591A\u5988\u5988\u6765\u8BF4\u771F\u7684\u662F\u4E00\u9053\u52AB\uFF0C\u968F\u7740\u73B0\u4EE3\u533B\u5B66\u7684\u53D1\u5C55\uFF0C\u5206\u5A29\u4E0D\u4F46\u66F4\u5B89\u5168\uFF0C\u4E5F\u771F\u7684\u53EF\u4EE5\u505A\u5230\u4E0D\u90A3\u4E48\u75BC\u75DB\u4E86\u3002\u4E3A\u4E86\u8BA9\u5206\u5A29\u8FC7\u7A0B\u4E2D\u7684\u75BC\u75DB\u51CF\u8F7B\u751A\u81F3\u6D88\u5931\u7684\u6280\u672F\uFF0C\u4E5F\u5C31\u662F\u5927\u5BB6\u4FD7\u79F0\u7684\u201C\u65E0\u75DB\u5206\u5A29\u201D\uFF0C\u533B\u5B66\u4E0A\u7EDF\u79F0\u4E3A\u5206\u5A29\u9547\u75DB\u3002</p><p>\u7406\u60F3\u7684\u5206\u5A29\u9547\u75DB\u662F\u4EC0\u4E48\u6837\u7684\u5462\uFF1F\u5B83\u6700\u597D\u662F\uFF1A</p><p>\u25CF <strong>\u5BF9\u6BCD\u4EB2\u548C\u5B69\u5B50\u53CC\u65B9\u7684\u5F71\u54CD\u5C0F</strong></p><p><strong>\u25CF \u5E94\u7528\u7B80\u4FBF\uFF0C\u8D77\u6548\u5FEB\uFF0C\u4F5C\u7528\u53EF\u9760\u3002</strong></p><p><strong>\u25CF \u4E0D\u5F71\u54CD\u5B50\u5BAB\u6536\u7F29\u548C\u4EA7\u5987\u7684\u6D3B\u52A8</strong></p><p><strong>\u25CF \u4E0D\u5F71\u54CD\u4EA7\u5987\u610F\u8BC6\uFF0C\u65B9\u4FBF\u914D\u5408\u6574\u4E2A\u5206\u5A29\u8FC7\u7A0B</strong></p><p>\u5B9E\u73B0\u8FD9\u4E9B\u76EE\u6807\uFF0C\u4E3B\u8981\u4F9D\u9760\u6211\u4EEC\u9EBB\u9189\u533B\u5E08\uFF0C\u5F53\u7136\u4E5F\u9700\u8981\u4EA7\u5987\u548C\u53C2\u4E0E\u5206\u5A29\u8FC7\u7A0B\u7684\u533B\u751F\u548C\u62A4\u58EB\u7684\u5171\u540C\u53C2\u4E0E\u3002\u8981\u7F13\u89E3\u548C\u6D88\u9664\u75BC\u75DB\uFF0C\u6211\u4EEC\u9996\u5148\u8981\u77E5\u9053\u75BC\u75DB\u7684\u6765\u6E90\u3002</p><p>\u5206\u5A29\u75BC\u75DB\u4E0E\u5FC3\u7406\u548C\u751F\u7406\u53CC\u91CD\u56E0\u7D20\u6709\u5173\u3002\u5FC3\u7406\u65B9\u9762\uFF0C\u4EA7\u5987\u7684\u7D27\u5F20\u3001\u5BB3\u6015\u3001\u7126\u8651\u7B49\u60C5\u7EEA\u8D8A\u91CD\uFF0C\u5206\u5A29\u75BC\u75DB\u4E5F\u4F1A\u8D8A\u91CD\u3002\u751F\u7406\u65B9\u9762\uFF0C\u968F\u7740\u5206\u5A29\u7684\u8FDB\u5C55\uFF0C\u75BC\u75DB\u7684\u4EA7\u751F\u539F\u7406\u3001\u90E8\u4F4D\u3001\u6027\u8D28\u7B49\u4E5F\u4F1A\u6709\u6240\u53D8\u5316\uFF1A\u5206\u5A29\u524D\u671F\u75BC\u75DB\u4E3B\u8981\u6765\u81EA\u5B50\u5BAB\u6536\u7F29\uFF0C\u75BC\u75DB\u5728\u4E0B\u8179\u90E8\u548C\u8170\u80CC\u90E8\uFF0C\u6BD4\u8F83\u949D\uFF1B\u540E\u671F\u4E3B\u8981\u6765\u81EA\u80CE\u513F\u5BF9\u4EA7\u9053\u7684\u6324\u538B\uFF0C\u75BC\u75DB\u5728\u4F1A\u9634\u90E8\uFF0C\u6BD4\u8F83\u5C16\u9510\u3002</p><p>\u77E5\u9053\u4E86\u75BC\u75DB\u7684\u6765\u6E90\uFF0C\u90A3\u6211\u4EEC\u5E94\u8BE5\u600E\u4E48\u505A\u6765\u7F13\u89E3\u5462\uFF1F</p><p><strong>\u5206\u5A29\u524D</strong></p><p>\u5206\u5A29\u65F6\u7684\u75BC\u75DB\u8DDF\u5FC3\u7406\u56E0\u7D20\u6709\u5173\uFF0C\u63D0\u524D\u505A\u529F\u8BFE\u5C3D\u91CF\u7F13\u89E3\u7D27\u5F20\u7126\u8651\u7684\u60C5\u7EEA\u80FD\u7F13\u89E3\u5206\u5A29\u65F6\u7684\u75BC\u75DB\u3002\u5B55\u5988\u5988\u53EF\u4EE5\u63D0\u524D\u4E86\u89E3\u5206\u5A29\u8FC7\u7A0B\u662F\u600E\u4E48\u6837\u7684\uFF0C\u5982\u4F55\u914D\u5408\uFF0C\u8FD9\u6837\u5BF9\u4E8E\u53EF\u80FD\u53D1\u751F\u7684\u4E8B\u60C5\u6709\u4E2A\u9884\u671F\uFF0C\u6700\u5927\u9650\u5EA6\u51CF\u5C11\u56E0\u4E3A\u672A\u77E5\u800C\u611F\u53D7\u5230\u7684\u5BB3\u6015\u3002\u53E6\u5916\uFF0C\u4EA7\u524D\u4E5F\u53EF\u4EE5\u5B66\u4E60\u5982\u4F55\u653E\u677E\u5FC3\u60C5\uFF0C\u4FDD\u6301\u60C5\u7EEA\u5E73\u548C\uFF0C\u5982\u4F55\u63A7\u5236\u6DF1\u547C\u5438\u7B49\u3002</p><p><strong>\u5206\u5A29\u4E2D</strong></p><p>\u5728\u5206\u5A29\u8FC7\u7A0B\u4E2D\uFF0C\u53EF\u4EE5\u8BD5\u8BD5\u6301\u7EED\u6D3B\u52A8\u6216\u8005\u6539\u53D8\u4F53\u4F4D\u6765\u7F13\u89E3\u75BC\u75DB\uFF1A\u53EF\u4EE5\u8BD5\u8BD5\u4FA7\u8EBA\uFF0C\u4E0B\u8E72\uFF0C\u7AD9\u8D77\u6765\uFF0C\u6216\u8005\u6765\u56DE\u8D70\u52A8\uFF0C\u8FD9\u6837\u6BD4\u8D77\u4E00\u76F4\u4EF0\u5367\u8EBA\u5728\u4EA7\u5E8A\u4E0A\u4F1A\u597D\u4E00\u4E9B\u3002\u4E5F\u53EF\u4EE5\u501F\u52A9\u7B80\u5355\u7684\u5DE5\u5177\u6BD4\u5982\u5206\u5A29\u7403\uFF1A\u5750\u3001\u62B1\u3001\u80CC\u9760\u5728\u5F69\u8272\u7684\u5206\u5A29\u7403\u4E0A\uFF0C\u5206\u6563\u6CE8\u610F\u529B\uFF0C\u589E\u52A0\u8212\u9002\u5EA6\uFF0C\u7F13\u89E3\u7D27\u5F20\u60C5\u7EEA\u3002\u5206\u5A29\u7403\u7684\u6309\u6469\u4F5C\u7528\u4E5F\u53EF\u4EE5\u7F13\u89E3\u8170\u80CC\u90E8\u3001\u81C0\u90E8\u3001\u4F1A\u9634\u90E8\u808C\u8089\u7684\u9178\u80C0\u4E0D\u9002\uFF1B\u53E6\u5916\uFF0C\u8FD8\u53EF\u4EE5\u8BD5\u8BD5\u6E29\u6C34\u6D74\uFF1A\u6CE1\u6FA1\u6216\u6DCB\u6D74\u53EF\u4EE5\u6539\u5584\u8840\u6DB2\u5FAA\u73AF\uFF0C\u677E\u5F1B\u808C\u8089\uFF0C\u7F13\u89E3\u75BC\u75DB\uFF0C\u6CE8\u610F\u6E29\u5EA6\u9002\u5B9C\uFF0C\u63A7\u5236\u5728\u7EA637\u5EA6\u3002</p><p>\u4E0A\u9762\u8FD9\u4E9B\u65B9\u5F0F\u4E0D\u9700\u8981\u7279\u6B8A\u836F\u7269\u6216\u8005\u5668\u68B0\uFF0C\u64CD\u4F5C\u65B9\u4FBF\uFF0C\u5BF9\u6BCD\u4EB2\u548C\u80CE\u513F\u6CA1\u6709\u526F\u4F5C\u7528\u3002\u4E0D\u8DB3\u4E4B\u5904\u662F\u75BC\u75DB\u7F13\u89E3\u7A0B\u5EA6\u6BD4\u8F83\u6709\u9650\u3002\u5982\u679C\u505A\u8FC7\u8FD9\u4E9B\u4EE5\u540E\u75BC\u75DB\u6539\u5584\u6548\u679C\u4E0D\u592A\u6EE1\u610F\uFF0C\u533B\u751F\u901A\u5E38\u4F1A\u5C1D\u8BD5\u66F4\u4E13\u4E1A\u7684\u9547\u75DB\u65B9\u5F0F\uFF1A</p><p><strong>1\u3001\u5438\u5165\u9547\u75DB</strong></p><p>\u5438\u5165\u9547\u75DB\u662F\u901A\u8FC7\u5438\u5165\u6709\u9547\u75DB\u4F5C\u7528\u7684\u6C14\u4F53\u6765\u8FBE\u5230\u7F13\u89E3\u75DB\u7684\u76EE\u7684\uFF0C\u76EE\u524D\u4F7F\u7528\u6700\u591A\u7684\u662F\u7B11\u6C14\u3002\u7B11\u6C14\u901A\u8FC7\u547C\u5438\u5230\u80BA\u91CC\u9762\u4EE5\u540E\uFF0C\u8FDB\u5165\u8840\u6DB2\uFF0C\u5728\u5927\u8111\u91CC\u8D77\u4F5C\u7528\uFF0C\u4EA7\u751F\u9547\u75DB\u6548\u679C\u3002</p><p>\u7B11\u6C14\u7684\u9547\u75DB\u6548\u679C\u662F\u786E\u5207\u7684\uFF0C\u800C\u4E14\u65B9\u4FBF\u64CD\u4F5C\u3002\u76EE\u524D\u9547\u75DB\u6D53\u5EA6\u7684\u7B11\u6C14\u8FD8\u6CA1\u6709\u53D1\u73B0\u5BF9\u6BCD\u4EB2\u6216\u80CE\u513F\u6709\u4E0D\u5229\u5F71\u54CD\u3002\u5927\u91CF\u7814\u7A76\u6307\u51FA\uFF0C\u5438\u5165\u9547\u75DB\u4E0D\u4F46\u53EF\u4EE5\u5728\u4E00\u5B9A\u7A0B\u5EA6\u4E0A\u7F13\u89E3\u5206\u5A29\u75BC\u75DB\uFF0C\u800C\u4E14\u4E0D\u589E\u52A0\u5256\u5BAB\u4EA7\u548C\u4F7F\u7528\u5668\u68B0\u52A9\u4EA7\u7684\u6982\u7387\uFF0C\u4E0D\u5F71\u54CD\u65B0\u751F\u513F\u7684\u5065\u5EB7\u3002</p><p>\u4E0D\u8FC7\u7B11\u6C14\u4F7F\u7528\u8FC7\u7A0B\u4E2D\u51C6\u5988\u5988\u53EF\u80FD\u51FA\u73B0\u4E00\u4E9B\u4E0D\u9002\uFF0C\u6BD4\u5982\u5934\u6655\u3001\u6076\u5FC3\u6216\u8005\u55DC\u7761\u7684\u611F\u89C9\u3002\u5982\u679C\u89C9\u5F97\u8FD9\u4E9B\u53CD\u5E94\u96BE\u4EE5\u5FCD\u53D7\uFF0C\u53EF\u4EE5\u6682\u65F6\u505C\u6B62\u4F7F\u7528\uFF0C\u4E00\u822C\u51E0\u5206\u949F\u5185\u4E0D\u9002\u53CD\u5E94\u5C31\u4F1A\u7F13\u89E3\u3002</p><p><strong>2\u3001\u786C\u819C\u5916\u9EBB\u9189</strong></p><p>\u786C\u819C\u5916\u9EBB\u9189\u662F\u4E00\u79CD\u7279\u6B8A\u7684\u5C40\u90E8\u9EBB\u9189\u65B9\u5F0F\u3002\u4EBA\u4F53\u80CC\u540E\u7684\u810A\u67F1\u4E2D\u95F4\u6709\u4E00\u4E2A\u7A7A\u95F4\u53EB\u786C\u819C\u5916\u8154\uFF0C\u91CC\u9762\u6709\u5F88\u591A\u795E\u7ECF\u8D1F\u8D23\u4EBA\u4F53\u7684\u611F\u89C9\u548C\u8FD0\u52A8\u7684\u529F\u80FD\u3002\u5982\u679C\u5728\u8FD9\u91CC\u7528\u836F\uFF0C\u836F\u7269\u4F5C\u7528\u4E8E\u8D1F\u8D23\u75DB\u89C9\u7684\u795E\u7ECF\u540E\uFF0C\u75DB\u89C9\u5C31\u4F1A\u88AB\u201C\u9EBB\u75F9\u201D\uFF0C\u4F46\u836F\u7269\u5BF9\u8D1F\u8D23\u8FD0\u52A8\u7684\u795E\u7ECF\u4E0D\u8D77\u4F5C\u7528\uFF0C\u6240\u4EE5\u8FD0\u52A8\u529F\u80FD\u4E0D\u4F1A\u88AB\u201C\u9EBB\u75F9\u201D\u3002\u6211\u4EEC\u5229\u7528\u8FD9\u4E2A\u7279\u70B9\uFF0C\u8FBE\u5230\u9547\u75DB\u4F46\u4E0D\u5F71\u54CD\u5206\u5A29\u8FC7\u7A0B\u7684\u76EE\u7684\u3002</p><p>\u786C\u819C\u5916\u9EBB\u9189\u662F\u53EF\u4EE5\u8FBE\u5230\u5B8C\u5168\u65E0\u75DB\u7684\u9547\u75DB\u65B9\u5F0F\u3002\u505A\u4E86\u786C\u819C\u5916\u9EBB\u9189\u540C\u6837\u5E76\u4E0D\u589E\u52A0\u5256\u5BAB\u4EA7\u6982\u7387\uFF0C\u4F46\u662F\u53EF\u589E\u52A0\u5668\u68B0\u52A9\u4EA7\u548C\u4F7F\u7528\u50AC\u4EA7\u7D20\u7684\u6982\u7387\u4EE5\u53CA\u5BFC\u81F4\u7B2C\u4E8C\u4EA7\u7A0B\u5EF6\u957F\u3002\u867D\u7136\u5B83\u662F\u5728\u8170\u80CC\u90E8\u64CD\u4F5C\uFF0C\u4F46\u786C\u819C\u5916\u9EBB\u9189\u4E0E\u4EA7\u540E\u8170\u75DB\u5E76\u65E0\u5173\u8054\u3002</p><p>\u5F88\u591A\u5988\u5988\u62C5\u5FC3\u7528\u9EBB\u836F\u4F1A\u4E0D\u4F1A\u5BF9\u80CE\u513F\u5F71\u54CD\uFF0C\u56E0\u4E3A\u786C\u819C\u5916\u9EBB\u9189\u7684\u7528\u836F\u91CF\u5F88\u5C0F\uFF0C\u8FDB\u5165\u8840\u6DB2\u8FDB\u800C\u901A\u8FC7\u80CE\u76D8\u8FDB\u5165\u80CE\u513F\u7684\u91CF\u5FAE\u4E4E\u5176\u5FAE\u3002\u76EE\u524D\u7684\u8BC1\u636E\u4E5F\u8868\u660E\uFF0C\u51C6\u5988\u5988\u9009\u62E9\u4F7F\u7528\u786C\u819C\u5916\u9547\u75DB\uFF0C\u751F\u4E0B\u6765\u7684\u5B9D\u5B9D\uFF0C\u51FA\u751F\u65F6\u7684\u65B0\u751F\u513F\u8BC4\u5206\u548C\u4E0D\u4F7F\u7528\u7684\u60C5\u51B5\u4E0B\u76F8\u6BD4\uFF08\u4F7F\u7528\u5176\u4ED6\u9547\u75DB\u65B9\u6CD5\u6216\u8005\u4E0D\u7528\u9547\u75DB\uFF09\u5E76\u6CA1\u6709\u5DEE\u522B\u3002\u6240\u4EE5\u6211\u4EEC\u4E0D\u9700\u8981\u62C5\u5FC3\u5B83\u5BF9\u5B9D\u5B9D\u7684\u5F71\u54CD\u3002</p><p>\u65E0\u8BBA\u662F\u5BF9\u51C6\u5988\u5988\u8FD8\u662F\u5BF9\u80CE\u513F\uFF0C\u786C\u819C\u5916\u9EBB\u9189\u90FD\u662F\u7406\u60F3\u7684\u9547\u75DB\u65B9\u5F0F\uFF0C\u4F46\u9700\u8981\u6CE8\u610F\u7684\u662F\uFF0C\u662F\u5426\u9002\u5408\u4F7F\u7528\u8FD9\u4E2A\u65B9\u6CD5\u9700\u8981\u5148\u505A\u8BC4\u4F30\uFF0C\u5982\u679C\u80CC\u540E\u7684\u7A7F\u523A\u70B9\u5468\u56F4\u5B58\u5728\u611F\u67D3\uFF0C\u6216\u8005\u4EA7\u5987\u6709\u51DD\u8840\u529F\u80FD\u969C\u788D\u3001\u810A\u67F1\u7578\u5F62\uFF0C\u6216\u5408\u5E76\u6709\u4E2D\u67A2\u795E\u7ECF\u7CFB\u7EDF\u75BE\u75C5\u7B49\u5C31\u4E0D\u80FD\u7528\u3002</p><p>\u786C\u819C\u5916\u9EBB\u9189\u4E5F\u4F1A\u6709\u70B9\u70B9\u98CE\u9669\uFF0C\u6BD4\u5982\u7A7F\u523A\u53EF\u80FD\u5931\u8D25\uFF0C\u6216\u8005\u53D1\u751F\u786C\u819C\u88AB\u523A\u7834\u800C\u5BFC\u81F4\u8111\u810A\u6DB2\u6F0F\u51FA\u6765\uFF0C\u8FD9\u6837\u672F\u540E\u4F1A\u5934\u75BC\u3002\u4E5F\u53EF\u80FD\u4F1A\u6709\u4E00\u4E9B\u4E0D\u826F\u53CD\u5E94\uFF1A\u6BD4\u5982\u8840\u538B\u964D\u4F4E\uFF0C\u53D1\u70E7\uFF0C\u6682\u65F6\u6027\u53CC\u817F\u65E0\u529B\u3002\u8FD9\u4E9B\u4E0D\u826F\u53CD\u5E94\u5982\u679C\u53D1\u751F\u4E86\uFF0C\u4E5F\u4E0D\u9700\u8981\u8FC7\u5206\u62C5\u5FC3\uFF0C\u5728\u533B\u62A4\u4EBA\u5458\u53CA\u65F6\u5BF9\u75C7\u5904\u7406\u540E\u4F1A\u6062\u590D\u6B63\u5E38\u3002\u786C\u819C\u5916\u9EBB\u9189\u53D1\u751F\u610F\u5916\u53EF\u5BFC\u81F4\u6C38\u4E45\u6027\u7684\u795E\u7ECF\u635F\u4F24\uFF0C\u4ECE\u800C\u51FA\u73B0\u611F\u89C9\u4E27\u5931\uFF0C\u808C\u65E0\u529B\u7B49\uFF0C\u4F46\u662F\u8FD9\u4E2A\u6982\u7387\u662F\u5341\u5206\u4F4E\u7684\uFF0C\u6709\u4E34\u5E8A\u7EDF\u8BA1\u663E\u793A\u5206\u5A29\u65F6\u4F7F\u7528\u786C\u819C\u5916\u9EBB\u9189\u5BFC\u81F4\u6C38\u4E45\u6027\u795E\u7ECF\u635F\u4F24\u7684\u6982\u7387\u662F10\u4E07\u5206\u4E4B0.6\u3002</p><p>\u4E86\u89E3\u4E86\u4E0A\u9762\u8FD9\u4E9B\u8F83\u4E3A\u5E38\u89C1\u7684\u5206\u5A29\u9547\u75DB\u65B9\u5F0F\u4EE5\u540E\uFF0C\u51C6\u5988\u5988\u4EEC\u53EF\u4EE5\u6839\u636E\u81EA\u5DF1\u7684\u9700\u6C42\u548C\u533B\u751F\u5546\u91CF\u9009\u62E9\u9547\u75DB\u65B9\u5F0F\uFF0C\u6BD4\u5982\u5148\u9009\u62E9\u7B80\u5355\u6613\u884C\u7684\uFF0C\u5982\u679C\u75BC\u75DB\u52A0\u91CD\u96BE\u4EE5\u5FCD\u53D7\u518D\u52A0\u7528\u4E13\u4E1A\u7684\u65B9\u6CD5\u3002</p><p>\u5206\u5A29\u662F\u4E00\u4E2A\u81EA\u7136\u7684\u751F\u7406\u8FC7\u7A0B\uFF0C\u4F46\u75BC\u75DB\u5E76\u4E0D\u662F\u5FC5\u987B\u5FCD\u53D7\u7684\uFF0C\u5E0C\u671B\u8FD9\u7BC7\u6587\u7AE0\u80FD\u591F\u7ED9\u60A8\u5E26\u6765\u5E2E\u52A9\uFF0C\u8BA9\u4F60\u5C06\u6765\u80FD\u6709\u66F4\u591A\u9009\u62E9\u3002</p><hr><p>\u672C\u6587\u4F5C\u8005\u6C6A\u5E73\uFF0C\u6B66\u6C49\u534F\u548C\u533B\u9662\u4E3B\u6CBB\u533B\u5E08\u3002\u5982\u679C\u60A8\u6709\u9EBB\u9189\u3001\u672F\u540E\u9547\u75DB\u7B49\u95EE\u9898\uFF0C\u53EF\u70B9\u51FB<strong>\u9605\u8BFB\u539F\u6587</strong>\u5411\u5979\u54A8\u8BE2\u3002\u957F\u6309\u4E0B\u9762\u4E8C\u7EF4\u7801\uFF0C\u4E5F\u53EF\u5411\u66F4\u591A\u513F\u79D1\u3001\u5987\u79D1\u3001\u76AE\u80A4\u79D1\u533B\u751F\u54A8\u8BE2\u3002</p>',32),g={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651626928&idx=1&sn=4e459f7064ca6680c572aef060394cb4&chksm=8b5cb44abc2b3d5c5213eda5786fe35b0c18ceed0f56d53ff5a3c28be05282501b1c69660125&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},h=c("\u67E5\u770B\u539F\u6587");function l(f,m){const e=r("ExternalLinkIcon");return t(),n(s,null,[i,p("p",null,[p("a",g,[h,o(e)])])],64)}var u=d(_,[["render",l]]);export{u as default};
