import{r as a,o as r,c as t,a as e,b as n,F as o,d as c,e as s}from"./app.9aaa2256.js";import{_}from"./plugin-vue_export-helper.21dcd24c.js";const d={},i=c('<h3 id="\u7259\u7EBF\u5230\u5E95\u8FD8\u8981\u4E0D\u8981\u7528" tabindex="-1"><a class="header-anchor" href="#\u7259\u7EBF\u5230\u5E95\u8FD8\u8981\u4E0D\u8981\u7528" aria-hidden="true">#</a> \u7259\u7EBF\u5230\u5E95\u8FD8\u8981\u4E0D\u8981\u7528\uFF1F</h3><p>\u6211\u81EA\u5DF1\u662F\u4E00\u4E2A\u88AB\u7259\u75BE\u56F0\u6270\u7684\u4EBA\uFF0C\u51E0\u4E2A\u7259\u9F7F\u51FA\u4E86\u95EE\u9898\u505A\u8FC7\u6CBB\u7597\uFF0C\u6CBB\u7597\u8FC7\u7684\u7259\u9F7F\u7EC8\u7A76\u6CA1\u6709\u539F\u751F\u7684\u597D\uFF0C\u603B\u662F\u585E\u7259\u3002\u4EE5\u524D\u7528\u7259\u7B7E\uFF0C\u4F46\u7259\u7B7E\u7684\u6E05\u7406\u6548\u679C\u6709\u9650\uFF0C\u6709\u65F6\u8FDE\u7528\u51E0\u6839\uFF0C\u5D4C\u585E\u611F\u8FD8\u662F\u89E3\u51B3\u4E0D\u4E86\uFF0C\u4E00\u6574\u5929\u7259\u95F4\u80C0\u75DB\uFF0C\u5F88\u662F\u82E6\u607C\u3002</p><p>\u540E\u6765\u5728\u5FAE\u535A\u770B\u4E86\u4E00\u4E9B\u7259\u533B\u7684\u79D1\u666E\uFF0C\u8BF4\u7259\u7EBF\u80FD\u6539\u5584\u7259\u9F7F\u5065\u5EB7\uFF0C\u80FD_\u51CF\u5C1160%\u7684\u90BB\u9762\u9F8B\u9F7F\uFF0C\u80FD\u51CF\u5C1170%\u7684\u7259\u5468\u75C5\u673A\u4F1A\uFF0C\u5B83\u53EF\u4EE5\u8BA9\u4F60\u572880\u5C81\u7684\u65F6\u5019\u4ECD\u62E5\u6709\u4E00\u53E3\u4F60\u81EA\u5DF1\u7684\u5065\u5EB7\u7259\u9F7F_\u7B49\u7B49\uFF0C\u6211\u4E86\u89E3\u4E86\u8FD9\u4E9B\u540E\uFF0C\u5F00\u59CB\u4F7F\u7528\u7259\u7EBF\u3002</p><p>\u521A\u5F00\u59CB\u4F7F\u7528\u4E0D\u719F\u7EC3\uFF0C\u4F46\u7528\u719F\u540E\uFF0C\u611F\u89C9\u6E05\u7406\u6548\u679C\u6BD4\u7259\u7B7E\u597D\u592A\u591A\u3002\u7259\u7EBF\u538B\u5165\u7259\u7F1D\uFF0C\u62C9\u4E00\u62C9\u4EC0\u4E48\u90FD\u51FA\u6765\uFF0C\u5D4C\u585E\u611F\u987F\u65F6\u70DF\u6D88\u4E91\u6563\uFF0C\u8FD9\u79CD\u8212\u7545\u611F\u8BA9\u6211\u5F88\u5FEB\u5C31\u7231\u4E0A\u4E86\u7259\u7EBF\u3002\u73B0\u5728\u51E0\u4E4E\u662F\u996D\u540E\u5FC5\u7528\uFF0C\u51FA\u95E8\u8981\u662F\u5FD8\u4E86\u5E26\u7259\u7EBF\uFF0C\u996D\u90FD\u4E0D\u592A\u6562\u5403\uFF0C\u73B0\u5728\u56DE\u60F3\u4EE5\u524D\u6CA1\u6709\u7259\u7EBF\u7684\u65E5\u5B50\uFF0C\u90FD\u4E0D\u77E5\u9053\u662F\u600E\u4E48\u8FC7\u6765\u7684\u3002</p><p>\u56E0\u4E3A\u81EA\u5DF1\u7528\u7684\u4F53\u9A8C\u5F88\u597D\uFF0C\u800C\u7259\u533B\u4EEC\u4E5F\u5728\u53CD\u590D\u79D1\u666E\u7259\u7EBF\u5BF9\u4E8E\u7259\u9F7F\u7684\u597D\u5904\uFF0C\u4E3A\u4E86\u4E0D\u8BA9\u5B69\u5B50\u4E0D\u518D\u7259\u9F7F\u95EE\u9898\u4E0A\u91CD\u6ED4\u81EA\u5DF1\u7684\u8986\u8F99\uFF0C\u6211\u9664\u4E86\u5E2E\u5973\u513F\u5237\u7259\uFF0C\u5F88\u65E9\u5C31\u5F00\u59CB\u7ED9\u5979\u7528\u7259\u7EBF\u4E86\uFF0C\u73B0\u5728\u59796\u5C81\u591A\uFF0C\u5DF2\u7ECF\u80FD\u6BD4\u8F83\u719F\u7EC3\u7684\u81EA\u5DF1\u7528\u3002</p><p>\u4F46\u5979\u6709\u65F6\u5019\u4F1A\u5077\u61D2\uFF0C\u5149\u5237\u7259\u4E0D\u7528\u7259\u7EBF\uFF0C\u6709\u65F6\u5019\u770B\u5979\u5F88\u62B5\u89E6\u6211\u4E5F\u5C31\u6CA1\u575A\u6301\uFF0C\u4F46\u56E0\u4E3A\u7259\u533B\u4EEC\u53CD\u590D\u5F3A\u8C03\u7259\u7EBF\u6709\u90A3\u4E48\u91CD\u8981\uFF0C\u6211\u5FC3\u91CC\u8FD8\u662F\u9690\u9690\u62C5\u5FC3\uFF1A\u5979\u4F1A\u4E0D\u4F1A\u56E0\u6B64\u8FD9\u6B21\u6CA1\u7528\u7259\u7EBF\u800C\u7259\u9F7F\u51FA\u95EE\u9898\uFF1F</p><p>\u7136\u800C\uFF0C\u6628\u5929\u770B\u5230@\u52FF\u602A\u5E78 \u7684\u4E00\u6761\u5FAE\u535A\uFF0C\u56E0\u4E3A\u76EE\u524D\u5173\u4E8E\u7259\u7EBF\u53BB\u9664\u7259\u83CC\u6591\u3001\u51CF\u5C11\u7259\u9F88\u708E\u7684\u6548\u679C\u8BC1\u636E\u5F88\u5F31\u3001\u4E0D\u53EF\u9760\uFF0C\u6240\u4EE5\u7F8E\u56FD\u536B\u751F\u7F72\u5220\u9664\u4E86\u7528\u7259\u7EBF\u7684\u5EFA\u8BAE\u3002</p><p>\u6211\u7684\u611F\u89C9\u662F\u88AB\u98A0\u8986\u4E86\u3002</p><p>\u4E00\u76F4\u88AB\u7259\u533B\u4EEC\u704C\u8F93\u8BF4\u7259\u7EBF\u5BF9\u7259\u9F7F\u5F88\u597D\uFF0C\u6709\u90A3\u4E48\u591A\u597D\u5904\uFF0C\u4F46\u770B\u4E86\u90A3\u7BC7\u62A5\u9053\uFF08\u70B9\u9605\u8BFB\u539F\u6587\u53EF\u67E5\u770B\uFF09\uFF0C\u5B9E\u9645\u4E0A\uFF0C\u56DE\u987E\u8FC7\u53BB10\u5E74\u5185\u5BF9\u6BD4\u5237\u7259\u548C\u5237\u7259+\u7259\u7EBF\u6548\u679C\u768425\u9879\u6700\u53EF\u9760\u7814\u7A76\u5374\u53D1\u73B0\uFF0C\u603B\u4F53\u800C\u8A00\uFF0C\u7259\u7EBF\u6709\u6548\u7684\u8BC1\u636E\u662F\u201C\u5F31\u800C\u4E0D\u53EF\u9760\u201D\u7684\u3002\u8FD9\u4E2A\u4E8B\u5B9E\u5BF9\u4E8E\u6211\u8FD9\u6837\u5728\u7259\u533B\u4EEC\u79D1\u666E\u4E0B\u5F62\u6210\u7684\u201C\u5E38\u8BC6\u201D\u53CD\u5DEE\u592A\u5927\u4E86\uFF0C\u6240\u4EE5\u6211\u5728\u5FAE\u535A\u4E0A\u8BF4\u4E86\u53E5\uFF1A\u5F88\u591A\u201C\u5E38\u8BC6\u201D\u662F\u7ECF\u4E0D\u8D77\u8BC1\u636E\u7684\u68C0\u9A8C\u3002</p><p>\u4F46\u6211\u4F1A\u56E0\u4E3A\u8FD9\u6B21\u7F8E\u56FD\u536B\u751F\u7F72\u7684\u610F\u89C1\u800C\u653E\u5F03\u7528\u7259\u7EBF\u5417\uFF1F\u4E0D\u4F1A\u7684\u3002\u7259\u7EBF\u5DF2\u7ECF\u6210\u4E3A\u6211\u751F\u6D3B\u91CC\u4E0D\u53EF\u6216\u7F3A\u7684\u4E00\u90E8\u5206\uFF0C\u6211\u575A\u6301\u7528\u5B83\uFF0C\u662F\u56E0\u4E3A\u6211\u4F9D\u8D56\u4E8E\u5B83\u80FD\u6D88\u9664\u98DF\u7269\u5D4C\u585E\u5F15\u8D77\u7684\u4E0D\u9002\u3002\u6211\u4E5F\u6CE8\u610F\u5230\u7F8E\u56FD\u7259\u79D1\u534F\u4F1A\u4ECD\u7136\u63A8\u8350\u7528\u7259\u7EBF\uFF0C\u62A5\u9053\u4E2D\u53D7\u8BBF\u7684\u7259\u533B\u4E13\u5BB6\u4E5F\u7EE7\u7EED\u63A8\u8350\u4F7F\u7528\u7259\u7EBF\u3002</p><p>\u4F46\u8FD9\u6B21\u7F8E\u56FD\u536B\u751F\u7F72\u7684\u4FE1\u606F\u8FD8\u662F\u6539\u53D8\u4E86\u6211\u5BF9\u7259\u7EBF\u7684\u8BA4\u8BC6\uFF0C\u5728\u6709\u66F4\u591A\u6709\u529B\u7684\u8BC1\u636E\u51FA\u73B0\u4E4B\u524D\uFF0C\u6211\u4E0D\u518D\u76F8\u4FE1\u7259\u533B\u4EEC\u8BF4\u7684\u7259\u7EBF\u80FD\u660E\u663E\u964D\u4F4E\u9F8B\u9F7F\u548C\u7259\u5468\u75BE\u75C5\u7684\u98CE\u9669\uFF0C\u6211\u4E5F\u4E0D\u518D\u4F1A\u56E0\u4E3A\u5973\u513F\u5237\u4E86\u7259\u800C\u5076\u5C14\u6CA1\u7528\u7259\u7EBF\u800C\u62C5\u5FC3\u7126\u8651\u4E86\u3002</p><p>\u540C\u65F6\uFF0C\u8FD9\u6761\u65B0\u95FB\u51FA\u6765\u540E\uFF0C\u6211\u4E5F\u6CE8\u610F\u5230\u4E00\u4E9B\u7259\u533B\u5728\u5FAE\u535A\u4E0A\u53EB\u5927\u5BB6\u5FFD\u7565\u7F8E\u56FD\u536B\u751F\u7F72\u7684\u610F\u89C1\uFF0C\u8981\u542C\u4E13\u4E1A\u53E3\u8154\u673A\u6784\u7684\u610F\u89C1\uFF0C\u7EE7\u7EED\u5F3A\u529B\u63A8\u8350\u7259\u7EBF\u3002\u6211\u4E0D\u4F1A\u4ECE\u52A8\u673A\u4E0A\u505A\u4EC0\u4E48\u63A8\u65AD\uFF0C\u53EA\u662F\u89C9\u5F97\u4ED6\u4EEC\u6709\u4E9B\u8BF4\u6CD5\u7AD9\u4E0D\u4F4F\u811A\u3002</p><p>\u6BD4\u5982\u7259\u533B\u5728\u63A8\u8350\u7259\u7EBF\u7684\u65F6\u5019\u53CD\u590D\u5F3A\u8C03\u6E05\u7406\u7259\u7F1D\u7684\u91CD\u8981\uFF0C\u5F3A\u8C03\u8FD9\u662F\u9760\u7259\u5237\u505A\u4E0D\u5230\u7684\u4E8B\u3002\u4F46\u73B0\u5728\u95EE\u9898\u4E0D\u662F\u6709\u4EBA\u8981\u5426\u8BA4\u6E05\u7406\u7259\u7F1D\u7684\u91CD\u8981\u6027\uFF0C\u800C\u662F\u6E05\u7406\u7259\u7F1D\u7684\u65B9\u5F0F\u662F\u4E0D\u662F\u53EA\u6709\u7259\u7EBF\uFF1F\u7259\u7EBF\u662F\u4E0D\u662F\u6700\u597D\u7684\u65B9\u5F0F\uFF1F\u6211\u89C9\u5F97\u4E0D\u80FD\u6DF7\u6DC6\u4E86\u6E05\u7406\u7259\u7F1D\u7684\u5FC5\u8981\u6027\u548C\u4F7F\u7528\u7259\u7EBF\u7684\u5FC5\u8981\u6027\uFF0C\u7F8E\u56FD\u536B\u751F\u7F72\u4E5F\u4E0D\u662F\u5EFA\u8BAE\u4E0D\u8981\u7BA1\u7259\u7F1D\u4E86\uFF0C\u800C\u662F\u63A8\u8350\u4E86\u9F7F\u95F4\u5237\u3002</p><p>\u6709\u7259\u533B\u8BF4\u7259\u7EBF\u6BD4\u9F7F\u95F4\u5237\u6210\u672C\u5C0F\uFF0C\u4F7F\u7528\u66F4\u65B9\u4FBF\uFF0C\u90A3\u7259\u7B7E\u6BD4\u7259\u7EBF\u6210\u672C\u8FD8\u66F4\u5C0F\u5462\uFF0C\u4F7F\u7528\u8FD8\u66F4\u65B9\u4FBF\u5462\uFF0C\u6309\u8FD9\u79CD\u903B\u8F91\u6211\u4EEC\u5E94\u8BE5\u63A8\u8350\u7684\u662F\u7259\u7B7E\u800C\u4E0D\u662F\u7259\u7EBF\u4E86\u3002</p><p>\u4E5F\u6709\u4EBA\u8981\u8BF4\u7259\u7EBF\u7684\u597D\u5904\u5E76\u4E0D\u662F\u6CA1\u6709\u8BC1\u636E\uFF0C\u53EA\u4E0D\u8FC7\u8BC1\u636E\u6BD4\u8F83\u5F31\uFF0C\u6240\u4EE5\u53EF\u4EE5\u7EE7\u7EED\u63A8\u8350\u3002\u4F46\u5176\u5B9E\u8BC1\u636E\u6709\u5F88\u591A\u79CD\uFF0C\u5F88\u591A\u4E2D\u6210\u836F\u3001\u4FDD\u5065\u54C1\u5076\u5C14\u4E5F\u80FD\u8E66\u51FA\u706B\u661F\u5B50\u4E00\u822C\u7684\u8BC1\u636E\uFF0C\u4E0D\u662F\u6293\u5230\u4E00\u70B9\u8BC1\u636E\u5C31\u53EF\u4EE5\u5927\u529B\u63A8\u8350\u7684\u3002</p><p>\u5C31\u6211\u81EA\u5DF1\u7684\u4F7F\u7528\u4F53\u9A8C\u6765\u8BF4\uFF0C\u6211\u89C9\u5F97\u7528\u4E86\u7259\u7EBF\u540E\u7259\u9F88\u80BF\u75DB\u7684\u75C7\u72B6\u4E5F\u662F\u660E\u663E\u7F13\u89E3\u4E86\u3002\u60A3\u8005\u7ECF\u9A8C\uFF0C\u4E13\u5BB6\u610F\u89C1\uFF0C\u5C0F\u6837\u672C\u7684\u5BF9\u6BD4\u8BD5\u9A8C\uFF0C\u8FD9\u4E9B\u90FD\u662F\u8BC1\u636E\uFF0C\u4F46\u8FD9\u4E9B\u90FD\u662F\u4F4E\u7EA7\u522B\u7684\u3001\u5F31\u7684\u8BC1\u636E\u3002\u6211\u4EEC\u505A\u63A8\u8350\u7684\u7EA7\u522B\u662F\u5E94\u8BE5\u662F\u548C\u8BC1\u636E\u5F3A\u5EA6\u76F8\u5339\u914D\u7684\uFF0C\u6709\u591A\u5927\u8BC1\u636E\u505A\u591A\u5927\u63A8\u8350\u3002</p><p>\u4E00\u4E2A\u6CA1\u6709\u8BC1\u636E\u8BC1\u5B9E\u6709\u7528\u7684\u4E1C\u897F\uFF0C\u6211\u4EEC\u9ED8\u8BA4\u5B83\u662F\u6CA1\u7528\u7684\uFF0C\u6240\u4EE5\u4E0D\u505A\u63A8\u8350\uFF1B\u4E00\u4E2A\u6709\u5F31\u8BC1\u636E\u8BC1\u5B9E\u6709\u7528\u7684\u4E1C\u897F\uFF0C\u5982\u679C\u4E13\u4E1A\u673A\u6784\u6839\u636E\u8BC1\u636E\u8BA4\u4E3A\u5B83\u7684\u98CE\u9669\u548C\u6210\u672C\u5F88\u5C0F\uFF0C\u6536\u76CA\u5927\u4E8E\u6210\u672C\uFF08\u98CE\u9669\uFF09\uFF0C\u800C\u4E14\u5B83\u662F\u6240\u6709\u65B9\u6848\u91CC\u6536\u76CA/\u6210\u672C\u6BD4\u6700\u5927\u7684\uFF0C\u63A8\u8350\u662F\u6CA1\u6709\u95EE\u9898\u7684\u3002\u4F46\u5C06\u6CA1\u6709\u6709\u529B\u8BC1\u636E\u8BC1\u5B9E\u6709\u7528\u7684\u4E1C\u897F\uFF0C\u5F3A\u529B\u63A8\u8350\u7ED9\u60A3\u8005\u7528\uFF0C\u90A3\u5C31\u548C\u5356\u4FDD\u5065\u54C1\u7684\u6CA1\u4EC0\u4E48\u533A\u522B\u4E86\u3002</p><p>\u63A8\u8350\u4F7F\u7528\u7259\u7EBF\u7684\u4F9D\u636E\u662F\u5B83\u80FD\u53BB\u9664\u7259\u83CC\u6591\uFF0C\u964D\u4F4E\u9F8B\u9F7F\u548C\u7259\u5468\u708E\u7684\u98CE\u9669\uFF0C\u800C\u652F\u51FA\u548C\u98CE\u9669\u662F\u4E70\u7259\u7EBF\u7684\u94B1\u548C\u5B66\u4E60\u3001\u4F7F\u7528\u7259\u7EBF\u7684\u65F6\u95F4\u548C\u7CBE\u529B\u6210\u672C\uFF0C\u4EE5\u53CA\u5076\u5C14\u7684\u635F\u4F24\u7259\u9F88\u3001\u7259\u9F7F\u98CE\u9669\u3002\u536B\u751F\u7F72\u662F\u56E0\u4E3A\u4EE5\u524D\u9AD8\u4F30\u4E86\u7259\u7EBF\u7684\u6536\u76CA\uFF0C\u91CD\u65B0\u8BC4\u4F30\u4E86\u7814\u7A76\u8BC1\u636E\u540E\uFF0C\u8FD9\u6B21\u8C03\u6574\u4E86\u63A8\u8350\uFF0C\u6211\u89C9\u5F97\u662F\u5408\u7406\u7684\u3002</p><p>\u7259\u533B\u5982\u5B9E\u544A\u77E5\u5411\u60A3\u8005\u76EE\u524D\u7259\u7EBF\u4F7F\u7528\u7684\u5229\u5F0A\uFF0C\u540C\u65F6\u7ED3\u5408\u60A3\u8005\u7684\u5177\u4F53\u60C5\u51B5\uFF0C\u63D0\u51FA\u4E2A\u4F53\u5316\u5EFA\u8BAE\uFF0C\u90A3\u662F\u6CA1\u6709\u95EE\u9898\u7684\u3002\u4F46\u73B0\u5728\u95EE\u9898\u662F\u53E3\u8154\u754C\u5728\u7259\u7EBF\u95EE\u9898\u6709\u6CA1\u6709\u505A\u4E00\u4E9B\u6CA1\u6709\u6839\u636E\u7684\u8FC7\u5EA6\u5BA3\u4F20\uFF1F\u8FD9\u6B21\u6211\u4E4B\u6240\u4EE5\u6709\u88AB\u98A0\u8986\u7684\u611F\u89C9\uFF0C\u5C31\u662F\u56E0\u4E3A\u5728\u5FAE\u535A\u4E0A\u770B\u5230\u5F88\u591A\u7259\u533B\u628A\u7259\u7EBF\u5439\u5F97\u795E\u4E4E\u5176\u795E\u3002\u7259\u533B\u4EEC\u8981\u7EE7\u7EED\u5927\u529B\u63A8\u8350\u7259\u7EBF\uFF0C\u6700\u597D\u5148\u62FF\u51FA\u70B9\u8FC7\u786C\u7684\u8BC1\u636E\u6765\u8BC1\u5B9E\u5B83\u6709\u7528\uFF0C\u800C\u4E0D\u662F\u8BF4\u6211\u4EEC\u89C9\u5F97\u6709\u7528\uFF0C\u5426\u5219\u548C\u4E2D\u533B\u6CA1\u4EC0\u4E48\u533A\u522B\u3002</p><p>\u90A3\u7259\u7EBF\u5230\u5E95\u8FD8\u8981\u4E0D\u8981\u7528\uFF1F\u6BCF\u4E2A\u4EBA\u7684\u4EF7\u503C\u53D6\u5411\u4F1A\u6709\u5DEE\u5F02\uFF0C\u5BF9\u6536\u76CA/\u6210\u672C\u7684\u4EF7\u503C\u5224\u65AD\u4E5F\u4F1A\u6709\u5DEE\u5F02\u3002\u8BC1\u636E\u662F\u6211\u4EEC\u505A\u51B3\u7B56\u7684\u91CD\u8981\u4F9D\u636E\uFF0C\u4F46\u4E0D\u662F\u552F\u4E00\u7684\u4F9D\u636E\u3002\u559D\u9152\u4E0D\u5229\u4E8E\u5065\u5EB7\uFF0C\u4F46\u4E0D\u7B49\u4E8E\u8981\u5F3A\u5236\u6240\u6709\u7684\u4EBA\u4E0D\u559D\u9152\uFF0C\u6743\u8861\u4E86\u53E3\u6B32\u548C\u5065\u5EB7\u98CE\u9669\uFF0C\u613F\u610F\u4E3A\u53E3\u6B32\u5192\u4E00\u70B9\u5065\u5EB7\u98CE\u9669\u672A\u5C1D\u4E0D\u53EF\uFF0C\u81EA\u5DF1\u4E3A\u81EA\u5DF1\u7684\u9009\u62E9\u8D1F\u540E\u679C\u5C31\u597D\u4E86\u3002</p><p>\u76EE\u524D\u6765\u770B\uFF0C\u7259\u7EBF\u6709\u4E00\u5B9A\u7684\u6E05\u7406\u7259\u7F1D\u7684\u6548\u679C\uFF0C\u6709\u5F31\u800C\u4E0D\u53EF\u9760\u7684\u8BC1\u636E\u8BC1\u5B9E\u80FD\u53BB\u9664\u7259\u83CC\u6591\uFF0C\u80FD\u51CF\u5C11\u7259\u9F88\u51FA\u8840\u7B49\u95EE\u9898\uFF0C\u4E5F\u5F88\u5C11\u6709\u5371\u5BB3\uFF0C\u5982\u679C\u4E0D\u662F\u7ECF\u6D4E\u56F0\u96BE\u5230\u4E70\u4E0D\u8D77\u7259\u7EBF\uFF0C\u6211\u89C9\u5F97\u53EF\u4EE5\u7EE7\u7EED\u7528\uFF0C\u4F46\u4E5F\u6CA1\u5FC5\u8981\u56E0\u4E3A\u5076\u5C14\u6CA1\u7528\u7259\u7EBF\u800C\u62C5\u5FC3\u4EC0\u4E48\uFF0C\u66F4\u4E0D\u7528\u6839\u636E\u7528\u4E0D\u7528\u7259\u7EBF\u53BB\u5224\u65AD\u4E00\u4E2A\u4EBA\u7684\u751F\u6D3B\u65B9\u5F0F\u662F\u5426\u6587\u660E\u3002</p><p>\u5C31\u6211\u4E2A\u4EBA\u800C\u8A00\uFF0C\u6211\u80FD\u5F88\u719F\u7EC3\u7684\u7528\u7259\u7EBF\uFF0C\u800C\u4E14\u6211\u5DF2\u7ECF\u559C\u6B22\u4E0A\u996D\u540E\u7528\u7259\u7EBF\u6E05\u7406\u540E\u7684\u7545\u5FEB\u611F\uFF0C\u6E05\u7406\u98DF\u7269\u5D4C\u585E\u7684\u6548\u679C\u592A\u597D\u4E86\uFF0C\u6CA1\u6709\u5B83\u6211\u90FD\u4E0D\u6562\u5403\u996D\uFF0C\u4E70\u7259\u7EBF\u7684\u94B1\u6211\u4E5F\u4E0D\u7F3A\uFF0C\u6240\u4EE5\u54EA\u6015\u7528\u7259\u7EBF\u6709\u8F7B\u5FAE\u7684\u5371\u5BB3\uFF0C\u6211\u90FD\u4F1A\u7EE7\u7EED\u7528\uFF0C\u66F4\u4F55\u51B5\u8FD9\u4E9B\u8F7B\u5FAE\u5371\u5BB3\u90FD\u662F\u5076\u53D1\u7684\uFF0C\u6240\u4EE5\u6211\u80AF\u5B9A\u4F1A\u7EE7\u7EED\u7528\uFF0C\u6211\u4E5F\u4F1A\u575A\u6301\u8BA9\u6211\u5973\u513F\u7528\uFF0C\u4F46\u4E5F\u8BB8\u4E5F\u4F1A\u5C1D\u8BD5\u4E00\u4E0B\u9F7F\u95F4\u5237\uFF0C\u8BF4\u4E0D\u5B9A\u5C31\u6362\u4E86\u3002</p><hr><p>\u56DE\u590D\u201C<strong>\u7259\u9F7F</strong>\u201D\u4E86\u89E3\u5982\u4F55\u8BA9\u5B69\u5B50\u7259\u9F7F\u66F4\u597D</p>',24),l={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651626257&idx=1&sn=189f892480e595a4646d94bc8b6af9db&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},h=s("\u67E5\u770B\u539F\u6587");function m(f,x){const p=a("ExternalLinkIcon");return r(),t(o,null,[i,e("p",null,[e("a",l,[h,n(p)])])],64)}var g=_(d,[["render",m]]);export{g as default};
