import{r as a,o,c as r,a as e,b as n,F as t,d as c,e as s}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const l={},i=c('<h3 id="\u6D77\u6DD8\u9000\u70E7\u836F\u6709\u5FC5\u8981\u5417" tabindex="-1"><a class="header-anchor" href="#\u6D77\u6DD8\u9000\u70E7\u836F\u6709\u5FC5\u8981\u5417" aria-hidden="true">#</a> \u6D77\u6DD8\u9000\u70E7\u836F\u6709\u5FC5\u8981\u5417</h3><p>\u968F\u7740\u7F51\u7EDC\u7684\u53D1\u8FBE\uFF0C\u5F88\u591A\u4EBA\u9009\u62E9\u4E86\u7F51\u7EDC\u8D2D\u7269\uFF0C\u5728\u5BB6\u8F7B\u70B9\u9F20\u6807\u5C31\u53EF\u4EE5\u4E70\u5230\u5730\u7403\u4E0A\u5927\u90E8\u5206\u4E1C\u897F\uFF0C\u8863\u670D\u5305\u5305\u5976\u7C89\u5316\u5986\u54C1\u4EC0\u4E48\u7684\u6211\u77E5\u9053\u5F88\u591A\u4EBA\u90FD\u662F\u6D77\u6DD8\uFF0C\u8FDE\u9000\u70E7\u836F\u4E5F\u5F00\u59CB\u6D77\u6DD8\u8FD8\u662F\u51FA\u4E4E\u6211\u7684\u610F\u6599\u3002</p><p>\u90A3\u5929\u5FAE\u535A\u4E0A\u6709\u4E2A\u5BB6\u957F\u95EE:\u201C\u5B9D\u5B9D\u53D1\u70E738.8\u5EA6\uFF0C\u540C\u65F6\u7ED9\u7528\u4E86\u9000\u70E7\u836F\u548C\u9000\u70E7\u6813\u600E\u4E48\u529E\uFF1F\u8FC7\u91CF\u7684\u7528\u836F\u4F1A\u6709\u5371\u9669\u5417\uFF1F\u201D\u56E0\u4E3A\u79C1\u4FE1\u54A8\u8BE2\u592A\u591A\uFF0C\u6211\u57FA\u672C\u4E0D\u56DE\u7B54\uFF0C\u4F46\u770B\u5230\u8FD9\u6761\u89C9\u5F97\u5BB6\u957F\u80AF\u5B9A\u4F1A\u7740\u6025\u5C31\u6CA1\u5FCD\u4F4F\u56DE\u4E86\u4E00\u53E5\uFF1A\u4E0D\u662F\u540C\u4E00\u79CD\u836F\u7684\u8BDD\u4E00\u822C\u4E5F\u6CA1\u4E8B\u3002\u5BB6\u957F\u8FDE\u8BF4\u8C22\u8C22\uFF0C\u7136\u540E\u544A\u8BC9\u6211\u7528\u4E86\u53E3\u670D\u6CF0\u8BFA\u6797\u548C\u5FB7\u56FDHEXAL\u9000\u70ED\u6813\uFF0C\u63A5\u7740\u95EE\u9000\u70ED\u8FC7\u5FEB\u662F\u5426\u4F1A\u5BF9\u5B9D\u5B9D\u8EAB\u4F53\u6709\u6240\u4F24\u5BB3\uFF1F\u5927\u6982\u5979\u4EE5\u4E3A\u8FD9\u4E24\u79CD\u836F\u662F\u4E0D\u540C\u7684\u836F\uFF0C\u6240\u4EE5\u677E\u4E86\u4E00\u53E3\u6C14\u3002</p><p>\u6211\u77E5\u9053\u5B69\u5B50\u9000\u70E7\u57FA\u672C\u5C31\u662F\u5BF9\u4E59\u9170\u6C28\u57FA\u915A\u548C\u5E03\u6D1B\u82AC\u8FD9\u4E24\u79CD\u836F\uFF0C\u6CF0\u8BFA\u6797\u6211\u77E5\u9053\u662F\u5BF9\u4E59\u9170\u6C28\u57FA\u915A\uFF0C\u4F46HEXAL\u9000\u70ED\u6813\u662F\u4EC0\u4E48\u5176\u5B9E\u6211\u4E5F\u4E0D\u77E5\u9053\uFF0C\u51FA\u4E8E\u597D\u5947\u4E0A\u7F51\u67E5\u4E86\u4E00\u4E0B\uFF0C\u662FParacetamol\uFF0C\u4E2D\u6587\u7FFB\u8BD1\u662F\u6251\u70ED\u606F\u75DB\uFF0C\u5176\u5B9E\u4E5F\u5C31\u662F\u5BF9\u4E59\u9170\u6C28\u57FA\u915A\u3002\u8FD9\u4E2A\u5BB6\u957F\u4EE5\u4E3A\u662F\u4E24\u79CD\u4E0D\u540C\u7684\u836F\uFF0C\u800C\u4E8B\u5B9E\u4E0A\u7ED9\u5B69\u5B50\u7528\u7684\u90FD\u662F\u5BF9\u4E59\u9170\u6C28\u57FA\u915A\uFF0C\u7B49\u4E8E\u7ED9\u5B69\u5B50\u7528\u4E86\u53CC\u500D\u7684\u5242\u91CF\uFF0C\u800C\u5BF9\u4E59\u9170\u6C28\u57FA\u915A\u8FC7\u91CF\u4F7F\u7528\u662F\u53EF\u80FD\u9020\u6210\u809D\u635F\u5BB3\u3002</p><p>\u5C31\u8FD9\u4E2A\u4E8B\u6211\u53D1\u4E86\u6761\u5FAE\u535A\uFF0C\u770B\u8BC4\u8BBA\u624D\u77E5\u9053\u6D77\u6DD8\u836F\u54C1\u5DF2\u7ECF\u5F88\u5E38\u89C1\u4E86\uFF0C\u4E0A\u67D0\u5B9D\u641C\u5916\u56FD\u7684\u9000\u70E7\u836F\u679C\u7136\u4E0D\u5C11\uFF0C\u8BF8\u5982\uFF1APanadol\u3001Pamol\u3001Nurofen\uFF0C\u90FD\u662F\u6CA1\u542C\u8FC7\u7684\uFF0C\u6709\u7684\u8FD8\u88AB\u51A0\u4EE5\u201CX\u56FD\u795E\u836F\u201D\uFF0C\u4EF7\u683C\u4E5F\u4E0D\u4FBF\u5B9C\u3002</p><p>\u5BF9\u4E8EPamol\u6709\u4E9B\u4EE3\u8D2D\u662F\u8FD9\u6837\u5BA3\u4F20\u7684\uFF0C\u4E0B\u9762\u7684\u6587\u5B57\u622A\u5C4F\u81EA\u67D0\u5B9D\u4E0A\u7684\u5B9D\u8D1D\u4ECB\u7ECD\uFF1A</p><p>\u53E6\u4E00\u4E2APanadol\u4E5F\u88AB\u8BF4\u5F97\u540C\u6837\u7384\u4E4E\uFF0C\u6709\u5174\u8DA3\u53EF\u4EE5\u53BB\u641C\u6765\u770B\u770B\uFF0C\u53BB\u67E5\u4E86\u4E00\u4E0B\u8FD9\u4E24\u4E2A\u836F\u7684\u5177\u4F53\u6210\u4EFD\uFF0C\u7ED3\u679C\u90FD\u662F\uFF1A\u5BF9\u4E59\u9170\u6C28\u57FA\u915A\uFF01\u4E5F\u5C31\u662F\u56FD\u5185\u5927\u5BB6\u719F\u6089\u7684\u6CF0\u8BFA\u6797\u548C\u767E\u670D\u549B\u3002</p><p>\u518D\u6765\u770B\u770B\u53E6\u5916\u4E00\u4E2ANurofen\u662F\u600E\u4E48\u5199\u7684\uFF1A</p><p>\u518D\u4E86\u89E3\u4E00\u4E0BNurofen\u7684\u6210\u5206\uFF0C\u7ED3\u679C\u662F\uFF1A\u5E03\u6D1B\u82AC\uFF01\u4E5F\u5C31\u662F\u56FD\u5185\u5927\u5BB6\u719F\u6089\u7684\u7F8E\u6797\u3002</p><p>\u8FD9\u662F\u600E\u4E48\u56DE\u4E8B\uFF1F\u5356Pamol\u7684\u8BF4\u5B83\u662F\u6FB3\u6D32\u533B\u751F\u552F\u4E00\u63A8\u8350\u7684\u9000\u70E7\u836F\uFF0C\u8FD8\u8BF4\u5E03\u6D1B\u82AC\u5728\u65B0\u897F\u5170\u548C\u6FB3\u6D32\u662F\u7981\u7528\u7684\uFF0C\u600E\u4E48\u5356Nurofen\u7684\u53C8\u8BF4\u5B83\u662F\u6FB3\u6D32\u6BCF\u5BB6\u6BCF\u6237\u5FC5\u5907\u5462\uFF0C\u800C\u4E14\u5BF9\u5B69\u5B50\u6CA1\u6709\u4EFB\u4F55\u7684\u4F24\u5BB3\u3002\u6211\u771F\u60F3\u8BA9\u8FD9\u4E24\u5BB6\u7684\u4EE3\u8D2D\u6765\u573A\u201C\u9000\u70E7\u795E\u836F\u201D\u7684\u6495\u903C\u5927\u4F1A\u3002</p><p>\u4E3A\u4EC0\u4E48\u56FD\u5916\u7684\u9000\u70E7\u795E\u836F\u4F1A\u548C\u56FD\u5185\u5E38\u89C1\u7684\u666E\u901A\u9000\u70E7\u836F\u6210\u4EFD\u4E00\u6837\uFF1F\u56E0\u4E3A\u65E0\u8BBA\u56FD\u5185\u8FD8\u662F\u56FD\u5916\uFF0C\u76EE\u524D\u516C\u8BA4\u6BD4\u8F83\u5B89\u5168\u53EF\u9760\u7684\u513F\u7AE5\u9000\u70E7\u836F\u5C31\u662F\u5E03\u6D1B\u82AC\u548C\u5BF9\u4E59\u9170\u6C28\u57FA\u915A\u8FD9\u4E24\u79CD\uFF0C\u5982\u679C\u4F60\u542C\u8BF4\u54EA\u56FD\u6709\u4EC0\u4E48\u6CA1\u542C\u8FC7\u7684\u795E\u836F\u80FD\u7ED9\u5B69\u5B50\u9000\u70E7\u5374\u4E0D\u662F\u8FD9\u4E24\u79CD\u6210\u4EFD\uFF0C\u4F60\u53CD\u800C\u8981\u5C0F\u5FC3\u4E86\u3002</p><p>\u4E3A\u4EC0\u4E48\u8FD9\u4E9B\u4EE3\u8D2D\u4F1A\u6709\u5982\u6B64\u4E0D\u540C\u7684\u8BF4\u6CD5\uFF1F\u539F\u56E0\u5C31\u662F\u8FD9\u4E9B\u4EE3\u8D2D\u53EA\u662F\u4E3A\u81EA\u5DF1\u7684\u4EA7\u54C1\u4EE3\u8A00\uFF0C\u800C\u4E0D\u4F1A\u4E3A\u4E8B\u5B9E\u548C\u771F\u76F8\u4EE3\u8A00\uFF0C\u4E3A\u4E86\u81EA\u5DF1\u7684\u5229\u76CA\u751A\u81F3\u4E0D\u60DC\u62B9\u9ED1\u540C\u7C7B\u836F\u54C1\uFF0C\u4F20\u64AD\u8C23\u8A00\uFF0C\u5236\u9020\u6050\u614C\uFF0C\u7C7B\u4F3C\u7684\u8C23\u8A00\u4E5F\u5728\u5FAE\u4FE1\u670B\u53CB\u5708\u91CC\u5E7F\u6CDB\u7684\u4F20\u64AD\u7740\u3002</p><p>\u800C\u4E8B\u5B9E\u4E0A\uFF0C\u7F8E\u56FD\u513F\u79D1\u5B66\u4F1A\u5173\u4E8E\u513F\u7AE5\u9000\u70E7\u836F\u7684\u6307\u5357\u91CC\u8BF4\u5F97\u5F88\u660E\u786E\uFF1A\u6CBB\u7597\u53D1\u70E7\uFF0C\u5982\u679C\u5B69\u5B50\u603B\u4F53\u5065\u5EB7\uFF0C\u76EE\u524D\u7684\u8BC1\u636E\u8868\u660E\u5BF9\u4E59\u9170\u6C28\u57FA\u915A\u548C\u5E03\u6D1B\u82AC\u5B89\u5168\u6027\u548C\u6709\u6548\u6027\u6CA1\u6709\u5B9E\u8D28\u6027\u533A\u522B\u3002</p><p>\u751A\u81F3\u6709\u4E9B\u7814\u7A76\u6570\u636E\u8BA4\u4E3A\u5E03\u6D1B\u82AC\u7684\u836F\u6548\u65F6\u95F4\u66F4\u957F\uFF0C\u5F53\u7136\u5E03\u6D1B\u82AC\u4E0D\u9002\u7528\u4E8E6\u6708\u4EE5\u4E0B\u7684\u5B69\u5B50\uFF08\u9664\u975E\u533B\u751F\u5EFA\u8BAE\uFF09\uFF0C\u4E5F\u4E0D\u9002\u7528\u4E8E\u8131\u6C34\u7684\u5B69\u5B50\uFF0C\u56E0\u4E3A\u6709\u80BE\u6BD2\u6027\u65B9\u9762\u7684\u62C5\u5FC3\uFF0C\u5E76\u4E0D\u50CF\u4E00\u4E9B\u4EE3\u8D2D\u6240\u8BF4\u5BF9\u5B69\u5B50\u6CA1\u6709\u4EFB\u4F55\u4F24\u5BB3\u3002\u540C\u6837\uFF0C\u5BF9\u4E59\u9170\u6C28\u57FA\u915A\u4E5F\u6709\u809D\u6BD2\u6027\u7684\u62C5\u5FC3\uFF0C\u5BF9\u4E8E\u54EE\u5598\u7684\u5B69\u5B50\u4E5F\u6709\u52A0\u91CD\u75C7\u72B6\u7684\u62C5\u5FC3\uFF0C\u4F46\u5356Pamol\u7684\u4E0D\u4F1A\u544A\u8BC9\u4F60\u8FD9\u4E9B\u3002</p><p>\u98DF\u7269\u5403\u591A\u4E86\u4E5F\u53EF\u80FD\u4F1A\u5410\uFF0C\u66F4\u4F55\u51B5\u836F\u7269\uFF0C\u4ECE\u67D0\u79CD\u610F\u4E49\u4E0A\u8BB2\uFF0C\u4E16\u754C\u4E0A\u6CA1\u6709\u7EDD\u5BF9\u5B89\u5168\u7684\u795E\u836F\uFF0C\u548C\u6240\u6709\u7684\u836F\u7269\u4E00\u6837\uFF0C\u9000\u70E7\u836F\u7684\u4F7F\u7528\u539F\u5219\u4E5F\u662F\u80FD\u4E0D\u7528\u5C3D\u91CF\u4E0D\u7528\uFF0C\u4F46\u5982\u679C\u70E7\u5F97\u592A\u9AD8\u4E86\u8BA9\u5B69\u5B50\u592A\u96BE\u53D7\u4E5F\u4E0D\u4EBA\u9053\uFF0C\u8BE5\u7528\u7684\u65F6\u5019\u4E5F\u8981\u7528\uFF0C\u5426\u5219\u8FD9\u4E9B\u836F\u4E5F\u6CA1\u6709\u4E0A\u5E02\u7684\u5FC5\u8981\uFF0C\u5982\u679C\u5408\u7406\u4F7F\u7528\uFF0C\u8FD9\u4E24\u4E2A\u836F\u90FD\u662F\u5B89\u5168\u7684\u3002</p><p>\u540C\u65F6\u4E5F\u8981\u77E5\u9053\uFF0C\u9664\u975E\u8D85\u9AD8\u70ED\uFF0C\u5065\u5EB7\u7684\u5B69\u5B50\u53D1\u70E7\u5E76\u6CA1\u6709\u4EC0\u4E48\u574F\u5904\u53CD\u800C\u5BF9\u5B69\u5B50\u75C5\u60C5\u6709\u5E2E\u52A9\uFF0C\u4F7F\u7528\u9000\u70E7\u836F\u7684\u76EE\u7684\u4E0D\u662F\u7ED9\u5B69\u5B50\u964D\u6E29\uFF0C\u800C\u662F\u6539\u5584\u5B69\u5B50\u7684\u8212\u9002\u5EA6\uFF0C\u53EA\u6709\u5F53\u5B69\u5B50\u70E7\u5F97\u5F88\u96BE\u53D7\u4E86\u624D\u9700\u8981\u5403\u9000\u70E7\u836F\uFF0C\u5177\u4F53\u7528\u54EA\u4E00\u79CD\u836F\u53EF\u4EE5\u53C2\u7167\u4E24\u4E2A\u836F\u54C1\u7684\u7279\u70B9\u518D\u7ED3\u5408\u5B69\u5B50\u7684\u60C5\u51B5\u9009\u7528\u3002\uFF08\u5982\u4F55\u5E94\u5BF9\u5B69\u5B50\u53D1\u70E7\u6211\u66FE\u7ECF\u5199\u8FC7\u6587\u7AE0\uFF0C\u5173\u6CE8\u8FD9\u4E2A\u8D26\u53F7\u540E\u56DE\u590D48\u53EF\u67E5\u770B\uFF0C\u4E5F\u53EF\u70B9\u9605\u8BFB\u539F\u6587\u67E5\u770B\u76EE\u5F55\uFF09</p><p>\u4E5F\u8BB8\u6709\u4EBA\u4F1A\u8BF4\uFF0C\u5373\u4FBF\u9000\u70ED\u836F\u836F\u54C1\u6210\u4EFD\u4E00\u6837\uFF0C\u56FD\u5916\u751F\u4EA7\u7684\u548C\u56FD\u5185\u80AF\u5B9A\u4F1A\u6709\u4E0D\u4E00\u6837\uFF0C\u5C31\u50CF\u540C\u6837\u662F\u5976\u7C89\uFF0C\u56FD\u5185\u7684\u5C31\u51FA\u73B0\u8FC7\u4E09\u805A\u6C30\u80FA\u800C\u56FD\u5916\u6CA1\u6709\u3002\u786E\u5B9E\uFF0C\u6B64\u523B\u6211\u5C31\u5728\u56FD\u5916\u5B66\u4E60\uFF0C\u6211\u6240\u770B\u5230\u7684\u4E1C\u897F\u7EDD\u5927\u90E8\u5206\u90FD\u6BD4\u56FD\u5185\u8981\u597D\uFF0C\u6B63\u662F\u56E0\u4E3A\u522B\u4EBA\u6BD4\u6211\u4EEC\u505A\u5F97\u597D\u6240\u4EE5\u662F\u6211\u4EEC\u51FA\u6765\u5B66\u4E60\u800C\u4E0D\u662F\u522B\u4EBA\u6765\u6211\u4EEC\u8FD9\u91CC\u5B66\u4E60\u3002\u4F46\u56FD\u5916\u7684\u4EA7\u54C1\u80AF\u5B9A\u4E5F\u4F1A\u6709\u4E0D\u5408\u7406\u7684\u5730\u65B9\uFF0C\u6BD4\u5982\u65BD\u4FDD\u5229\u901A\u8FD9\u6837\u7684\u6CA1\u4EC0\u4E48\u7528\u7684\u6D0B\u8349\u836F\u4E5F\u662F\u5FB7\u56FD\u751F\u4EA7\u7684\u3002</p><p>\u6211\u4E5F\u4E0D\u5426\u8BA4\u9000\u70ED\u836F\u522B\u4EBA\u7684\u751F\u4EA7\u5DE5\u827A\u53EF\u80FD\u6BD4\u56FD\u4EA7\u7684\u66F4\u597D\uFF0C\u5305\u88C5\u66F4\u79D1\u5B66\uFF0C\u53E3\u5473\u66F4\u5408\u7406\uFF0C\u4F46\u8FD9\u4E24\u79CD\u513F\u7AE5\u5E38\u7528\u9000\u70E7\u836F\u90FD\u662F\u53E3\u670D\u6216\u8005\u6813\u5242\uFF0C\u4E5F\u4E0D\u9700\u8981\u591A\u9AD8\u7EA7\u7684\u751F\u4EA7\u5DE5\u827A\uFF0C\u76EE\u524D\u4E3A\u6B62\u4E5F\u6CA1\u542C\u8BF4\u56FD\u5185\u9000\u70E7\u836F\u5B58\u5728\u4EC0\u4E48\u836F\u54C1\u8D28\u91CF\u95EE\u9898\u3002</p><p>\u5C31\u7B97\u4F60\u4E0D\u4FE1\u4EFB\u56FD\u8D27\uFF0C\u56FD\u5185\u4E5F\u6709\u5F88\u591A\u5927\u578B\u8DE8\u56FD\u836F\u4F01\u7684\u4EA7\u54C1\uFF0C\u8FD9\u4E9B\u4F01\u4E1A\u56FD\u5185\u5916\u9500\u552E\u7684\u4EA7\u54C1\u5F88\u591A\u53EA\u662F\u5305\u88C5\u4E0A\u7684\u5DEE\u522B\uFF0C\u6211\u81EA\u5DF1\u5B69\u5B50\u4E5F\u7528\u8FC7\uFF0C\u8349\u8393\u5473\uFF0C\u5E26\u523B\u5EA6\u7684\u5438\u7BA1\uFF0C\u4F7F\u7528\u4E5F\u5F88\u65B9\u4FBF\uFF0C\u836F\u54C1\u53E3\u5473\u548C\u5305\u88C5\u4E5F\u633A\u4EBA\u6027\u5316\u3002\u8FD9\u4E9B\u8DE8\u56FD\u836F\u4F01\u7684\u89C4\u6A21\u8FDC\u5927\u4E8E\u90A3\u4E9B\u4E0D\u77E5\u540D\u7684\u56FD\u5916\u5C0F\u836F\u5382\uFF0C\u8D28\u91CF\u548C\u8BBE\u8BA1\u8C01\u66F4\u597D\u8FD8\u4E0D\u4E00\u5B9A\u5462\uFF0C\u800C\u4E14\u4E5F\u66F4\u4FBF\u5B9C\uFF0C\u540C\u6837100ml\u7684\u5E03\u6D1B\u82AC\u6DF7\u60AC\u6DB2\uFF0C\u56FD\u5185\u5F3A\u751F\u7684\u7F8E\u6797\u4E0D\u523020\u5757\uFF0C\u540C\u6837\u5BB9\u91CF\u7684\u6D77\u6DD8\u4EA7\u54C1\u898160\uFF0C\u6709\u7684\u8FD8\u66F4\u8D35\u3002</p><p>\u836F\u54C1\u672C\u6765\u4E0D\u662F\u751F\u6D3B\u5E38\u7528\u54C1\uFF0C\u4E00\u5E74\u7528\u4E0D\u4E0A\u51E0\u6B21\uFF0C\u56FD\u5185\u6CA1\u53D1\u73B0\u8FC7\u8D28\u91CF\u95EE\u9898\u7684\u9000\u70E7\u836F\u90FD\u9700\u8981\u4ECE\u56FD\u5916\u4E70\uFF0C\u90A3\u65E5\u5E38\u98DF\u7269\uFF0C\u6BCF\u5929\u90FD\u8981\u559D\u7684\u6C34\uFF0C\u6BCF\u5929\u90FD\u8981\u547C\u5438\u7684\u7A7A\u6C14\u600E\u4E48\u529E\uFF1F\u6574\u5929\u5438\u7740\u96FE\u973E\uFF0C\u968F\u65F6\u53EF\u80FD\u5403\u4E0A\u5730\u6C9F\u6CB9\u4E5F\u6CA1\u591A\u5C11\u529E\u6CD5\uFF0C\u5076\u5C14\u5403\u6B21\u9000\u70E7\u836F\u5374\u975E\u8981\u7528\u6D77\u6DD8\u7684\u65E0\u975E\u662F\u7ED9\u81EA\u5DF1\u4E00\u70B9\u5FC3\u7406\u5B89\u6170\u3002</p><p>\u66F4\u4E3B\u8981\u7684\uFF0C\u6D77\u6DD8\u7684\u836F\u54C1\u8FDE\u4E2D\u6587\u8BF4\u660E\u4E66\u90FD\u53EF\u80FD\u6CA1\u6709\uFF0C\u836F\u54C1\u6210\u4EFD\uFF0C\u4F7F\u7528\u65B9\u6CD5\uFF0C\u4F7F\u7528\u5242\u91CF\u90FD\u4E0D\u6E05\u695A\uFF0C\u5916\u8BED\u4E0D\u597D\u7684\u53EA\u80FD\u542C\u4EE3\u8D2D\u4EEC\u7684\u4ECB\u7ECD\uFF0C\u56FD\u4EBA\u6709\u672C\u4E8B\u628A\u9999\u7070\u3001\u8349\u836F\u90FD\u5439\u6210\u795E\u836F\uFF0C\u66F4\u4F55\u51B5\u662F\u771F\u6709\u6548\u679C\u7684\u9000\u70E7\u836F\u3002\u4EE3\u8D2D\u4EEC\u628A\u5E38\u89C1\u7684\u9000\u70E7\u836F\u5439\u6210\u4E86\u968F\u4FBF\u5403\u4E5F\u4E0D\u4F1A\u7ED9\u5B69\u5B50\u5E26\u6765\u4F24\u5BB3\u7684\u795E\u836F\uFF0C\u7ED3\u679C\u5C31\u50CF\u6587\u7AE0\u5F00\u5934\u63D0\u5230\u7684\u90A3\u4E2A\u5BB6\u957F\u4E00\u6837\u7ED9\u5B69\u5B50\u7528\u4E86\u53CC\u500D\u7684\u5BF9\u4E59\u9170\u6C28\u57FA\u915A\uFF0C\u6D77\u6DD8\u8FC7\u6765\u7684\u201C\u9AD8\u7EA7\u836F\u201D\uFF0C\u7ED3\u679C\u6210\u4E3A\u4F24\u5BB3\u5B69\u5B50\u7684\u6BD2\u836F\u3002</p><p>\u4E8B\u5B9E\u4E0A\u5F88\u591A\u5BB6\u957F\u672C\u6765\u5C31\u6CA1\u6709\u4EC0\u4E48\u533B\u5B66\u5E38\u8BC6\uFF0C\u6D77\u6DD8\u836F\u54C1\u53EA\u662F\u8111\u888B\u53D1\u70ED\u65E0\u610F\u8BC6\u7684\u8DDF\u98CE\uFF0C\u770B\u5230\u522B\u4EBA\u5BB6\u5B69\u5B50\u559D\u8FDB\u53E3\u5976\u7C89\uFF0C\u81EA\u5DF1\u4E5F\u4E70\u8FDB\u53E3\u5976\u7C89\uFF0C\u770B\u5230\u522B\u5BB6\u559D\u8FDB\u53E3\u9000\u70E7\u836F\uFF0C\u81EA\u5DF1\u4E5F\u4E70\u8FDB\u53E3\u836F\uFF0C\u5C31\u89C9\u5F97\u81EA\u5DF1\u5B69\u5B50\u4E0D\u80FD\u843D\u540E\uFF0C\u4E0D\u80FD\u8F93\u5728\u8D77\u8DD1\u7EBF\u4E0A\u3002</p><p>\u5728\u56FD\u5185\u4FE1\u4EFB\u5371\u673A\u7684\u5927\u73AF\u5883\u4E0B\uFF0C\u4F60\u6000\u7591\u4E00\u5207\u4E5F\u7F62\uFF0C\u8DDF\u98CE\u4E5F\u7F62\uFF0C\u5982\u679C\u4F60\u4E0D\u5728\u4E4E\u591A\u82B1\u90A3\u70B9\u94B1\uFF0C\u4E5F\u6709\u53EF\u9760\u7684\u4E70\u836F\u6E20\u9053\uFF0C\u81EA\u5DF1\u4E5F\u6709\u5F88\u597D\u7684\u5916\u8BED\u6C34\u5E73\u548C\u7406\u89E3\u80FD\u529B\uFF0C\u4E5F\u613F\u610F\u4E3A\u53EF\u80FD\u7684\u90A3\u4E48\u4E00\u70B9\u6216\u8005\u6839\u672C\u4E0D\u5B58\u5728\u7684\u6536\u76CA\u53BB\u6D77\u6DD8\u9000\u70E7\u836F\uFF0C\u4E5F\u65E0\u53EF\u539A\u975E\u3002\u5C31\u6015\u5565\u4E5F\u4E0D\u61C2\uFF0C\u8DDF\u98CE\u6D77\u6DD8\u5F53\u795E\u836F\u80E1\u5403\u4E71\u5403\uFF0C\u591A\u82B1\u94B1\u628A\u5B69\u5B50\u5BB3\u4E86\uFF0C\u90A3\u8FD8\u4E0D\u5982\u5148\u7ED9\u81EA\u5DF1\u8111\u888B\u9000\u9000\u70E7\uFF0C\u5B66\u70B9\u5065\u5EB7\u5E38\u8BC6\u5BF9\u5B69\u5B50\u66F4\u5B9E\u5728\uFF0C\u94B1\u6CA1\u5904\u82B1\u4E5F\u53EF\u4EE5\u6253\u8D4F\u7ED9\u6211\u3002\uFF08\u539F\u521B\u6587\u7AE0\u672A\u7ECF\u8BB8\u53EF\u8C22\u7EDD\u8F6C\u8F7D\uFF09</p><p><strong>\u6743\u5A01\u7684\u80B2\u513F\u79D1\u666E\uFF0C\u6DF1\u5EA6\u7684\u533B\u5B66\u4EBA\u6587</strong></p><p><strong>\u4EC5\u63A8\u9001\u539F\u521B\u6587\u7AE0\uFF0C\u6B22\u8FCE\u5173\u6CE8drpeihg</strong></p>',25),_={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=202793791&idx=1&sn=1c8b328d1e938205ddd48660ef0e75c1&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},m=s("\u67E5\u770B\u539F\u6587");function h(f,u){const p=a("ExternalLinkIcon");return o(),r(t,null,[i,e("p",null,[e("a",_,[m,n(p)])])],64)}var g=d(l,[["render",h]]);export{g as default};
