import{r as a,o as r,c as t,a as e,b as n,F as c,d as o,e as s}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const i={},_=o('<h3 id="\u5B9D\u5B9D\u6253\u4EBA-\u5982\u4F55\u5E94\u5BF9" tabindex="-1"><a class="header-anchor" href="#\u5B9D\u5B9D\u6253\u4EBA-\u5982\u4F55\u5E94\u5BF9" aria-hidden="true">#</a> \u5B9D\u5B9D\u6253\u4EBA\uFF0C\u5982\u4F55\u5E94\u5BF9\uFF1F</h3><p>\u201C\u5B9D\u5B9D11\u4E2A\u6708\uFF0C\u5F88\u559C\u6B22\u6253\u5927\u4EBA\u7684\u8138\uFF0C\u6BCF\u6B21\u62CD\u4E86\u4E4B\u540E\u5C31\u7B11\u4E2A\u4E0D\u505C\uFF0C\u4E0D\u8BA9\u6253\u5C31\u53D1\u813E\u6C14\uFF0C\u8BF7\u95EE\u8BE5\u600E\u4E48\u529E\uFF1F\u201D</p><p>\u5728\u6021\u79BE\u4E0A\u7C7B\u4F3C\u7684\u54A8\u8BE2\u8FD8\u6709\u5F88\u591A\uFF0C\u513F\u7AE5\u6253\u4EBA\u7684\u884C\u4E3A\u4E00\u76F4\u662F\u8BE2\u95EE\u9891\u7387\u8F83\u9AD8\u7684\u95EE\u9898\u4E4B\u4E00\uFF0C\u5F53\u7B2C\u4E00\u6B21\u53D1\u73B0\u5B9D\u5B9D\u201C\u6253\u4EBA\u201D\u65F6\uFF0C\u5927\u591A\u6570\u5988\u5988\u90FD\u662F\u9707\u60CA\u5E76\u624B\u8DB3\u65E0\u63AA\u7684\u3002</p><p>\u4E8B\u5B9E\u4E0A\uFF0C\u5927\u90E8\u5206\u5B9D\u5B9D\u7684 \u201C\u6253\u4EBA\u201D\u884C\u4E3A\u5E76\u4E0D\u5177\u6709\u653B\u51FB\u76EE\u7684\uFF0C\u53EA\u8981\u5BB6\u957F\u4EEC\u6B63\u786E\u5F15\u5BFC\uFF0C\u5B69\u5B50\u201C\u6253\u4EBA\u201D\u7684\u884C\u4E3A\u5C31\u4F1A\u5F97\u5230\u4E00\u5B9A\u7684\u6539\u5584\u3002</p><p><strong>\u5B9D\u5B9D\u4E3A\u4EC0\u4E48\u4F1A\u201C\u6253\u4EBA\u201D\uFF1F</strong></p><p>\u5B9D\u5B9D\u51FA\u73B0\u201C\u6253\u4EBA\u201D\u7684\u884C\u4E3A\uFF0C\u901A\u5E38\u6709\u4EE5\u4E0B\u51E0\u79CD\u539F\u56E0\uFF1A</p><p>\u4E00\u3001\u5B9D\u5B9D\u5728\u63A2\u7D22\u81EA\u5DF1\u80A2\u4F53\u7684\u6D3B\u52A8\u8303\u56F4\u548C\u80FD\u529B\uFF0C\u540C\u65F6\u4F53\u9A8C\u5176\u4E2D\u7684\u56E0\u679C\u5173\u7CFB\u3002</p><p>\u4F8B\u5982\u5B9D\u5B9D\u65E0\u610F\u4E2D\u62CD\u6253\u5230\u5927\u4EBA\u7684\u8138\u4E0A\u6216\u8005\u8EAB\u4E0A\uFF0C\u53D1\u73B0\u8FD9\u79CD\u884C\u4E3A\u4F1A\u5F15\u8D77\u7236\u6BCD\u7684\u60C5\u7EEA\u53D8\u5316\uFF0C\u6BD4\u5982\u60CA\u8BB6\u6216\u8005\u5927\u7B11\uFF0C\u8FD9\u65F6\u5728\u5B69\u5B50\u7684\u610F\u8BC6\u4E2D\u4F1A\u8BA4\u4E3A\u8FD9\u662F\u4E00\u4EF6\u5F88\u6709\u8DA3\u7684\u4E8B\u60C5\uFF0C\u6BD4\u62CD\u6253\u73A9\u5177\u66F4\u5BB9\u6613\u4EA7\u751F\u4E92\u52A8\uFF0C\u4ECE\u800C\u4F7F\u4ED6\u4EEC\u52A0\u6DF1\u4E86\u8BB0\u5FC6\uFF0C\u540C\u65F6\u4E5F\u5F3A\u5316\u4E86\u201C\u6253\u4EBA\u201D\u201D\u7684\u884C\u4E3A\u3002</p><p>\u4E8C\u3001\u5B9D\u5B9D\u201C\u6253\u4EBA\u201D\u662F\u8868\u8FBE\u60C5\u7EEA\u7684\u4E00\u79CD\u65B9\u5F0F\u3002</p><p>\u5F53\u5B69\u5B50\u4EEC\u8FD8\u4E0D\u4F1A\u7528\u8BED\u8A00\u6216\u8005\u5176\u4ED6\u65B9\u5F0F\u6765\u8868\u8FBE\u60C5\u7EEA\u65F6\uFF0C\u4ED6\u4EEC\u6700\u76F4\u63A5\u7684\u65B9\u5F0F\u5C31\u662F\u4F7F\u7528\u80A2\u4F53\u8BED\u8A00\uFF0C\u5C24\u5176\u662F\u5728\u4ED6\u4EEC\u7126\u8651\u6216\u8005\u5174\u594B\u7684\u72B6\u6001\u4E0B\uFF0C\u8FD9\u79CD\u201C\u62CD\u6253\u201D\u7684\u884C\u4E3A\u4F1A\u66F4\u52A0\u660E\u663E\u3002</p><p>\u4E09\u3001\u5B9D\u5B9D\u201C\u6253\u4EBA\u201D\u662F\u5B69\u5B50\u7F3A\u4E4F\u4EBA\u9645\u4EA4\u5F80\u6280\u5DE7\u7684\u8868\u73B0\u3002</p><p>\u5F88\u591A\u65F6\u5019\uFF0C\u5B9D\u5B9D\u4F1A\u901A\u8FC7\u201C\u6253\u4EBA\u201D\u7684\u65B9\u5F0F\u6765\u83B7\u5F97\u5173\u6CE8\uFF0C\u8BA9\u540C\u4F34\u548C\u4ED6\u8FDB\u884C\u4E92\u52A8\u3002\u8FD9\u79CD\u60C5\u51B5\u4E3B\u8981\u662F\u56E0\u4E3A\u5B9D\u5B9D\u8FD8\u6CA1\u6709\u5B66\u4F1A\u5982\u4F55\u548C\u540C\u4F34\u5F00\u542F\u5BF9\u8BDD\u548C\u4E92\u52A8\u3002</p><p>\u56DB\u3001\u5B9D\u5B9D\u201C\u6253\u4EBA\u201D\u7684\u884C\u4E3A\u662F\u6E90\u4E8E\u5BF9\u67D0\u4E2A\u5361\u901A\u4EBA\u7269\u7684\u6A21\u4EFF\u3002</p><p>\u52A8\u753B\u7247\u4EE5\u5176\u58F0\u753B\u5E76\u8302\u3001\u5938\u5F20\u5E7D\u9ED8\u7B49\u7279\u70B9\uFF0C\u5BF9\u513F\u7AE5\u6709\u7740\u5DE8\u5927\u7684\u5438\u5F15\u529B\uFF0C\u5982\u4ECA\u770B\u52A8\u753B\u7247\u5DF2\u7ECF\u6210\u4E3A\u5927\u90E8\u5206\u5C0F\u670B\u53CB\u751F\u6D3B\u4E2D\u4E0D\u53EF\u7F3A\u5C11\u7684\u4E00\u90E8\u5206\u3002\u800C\u5B69\u5B50\u5728\u6CA1\u6709\u8FA8\u522B\u662F\u975E\u80FD\u529B\u4E4B\u524D\uFF0C\u4F1A\u5728\u73B0\u5B9E\u751F\u6D3B\u4E2D\u76F2\u76EE\u6A21\u4EFF\u52A8\u753B\u7247\u4E2D\u4EBA\u7269\u7684\u8BED\u8A00\u548C\u884C\u4E3A\uFF0C\u90A3\u4E48\u52A8\u753B\u7247\u4E2D\u51FA\u73B0\u7684\u4E00\u4E9B\u4E0D\u5F53\u7684\u884C\u4E3A\u548C\u8BED\u8A00\uFF0C\u5C31\u4F1A\u5728\u4E00\u5B9A\u7A0B\u5EA6\u4E0A\u6210\u4E3A\u5B69\u5B50\u201C\u6253\u4EBA\u201D\u7684\u4E3B\u8981\u539F\u56E0\u3002</p><p>\u4E94\u3001\u5B69\u5B50\u201C\u6253\u4EBA\u201D\u662F\u4E3A\u4E86\u5438\u5F15\u7236\u6BCD\u7684\u5173\u6CE8\u3002</p><p>\u5F53\u7236\u6BCD\u56E0\u4E3A\u67D0\u79CD\u539F\u56E0\u758F\u4E8E\u548C\u5B69\u5B50\u8FDB\u884C\u6709\u6548\u7684\u6C9F\u901A\u548C\u4E92\u52A8\u65F6\uFF0C\u5B69\u5B50\u4F1A\u611F\u53D7\u5230\u81EA\u5DF1\u88AB\u7236\u6BCD\u6240\u5FFD\u7565\uFF0C\u4ECE\u800C\u4EA7\u751F\u7126\u8651\u548C\u60F6\u6050\u7684\u60C5\u7EEA\uFF0C\u8FD9\u65F6\u4ED6\u4EEC\u4F1A\u8868\u73B0\u51FA\u5404\u79CD\u884C\u4E3A\u6765\u5438\u5F15\u7236\u6BCD\u7684\u5173\u6CE8\uFF0C\u5176\u4E2D\u4E5F\u5305\u62EC\u201C\u6253\u4EBA\u201D\u3002</p><p><strong>\u6211\u4EEC\u8BE5\u600E\u4E48\u529E\u5462\uFF1F</strong></p><p>\u4E8B\u5B9E\u4E0A\uFF0C\u5F53\u5B9D\u5B9D\u51FA\u73B0\u201C\u6253\u4EBA\u201D\u7684\u884C\u4E3A\u65F6\uFF0C\u7236\u6BCD\u9700\u8981\u53CA\u65F6\u7684\u5E72\u9884\u548C\u5F15\u5BFC\uFF0C\u8BA9\u4ED6\u4EEC\u660E\u767D\u600E\u6837\u7684\u52A8\u4F5C\u548C\u884C\u4E3A\u624D\u662F\u6B63\u786E\u7684\uFF0C\u56E0\u4E3A\u7236\u6BCD\u7684\u5E72\u9884\u5728\u4E00\u5B9A\u7A0B\u5EA6\u4E0A\u5C31\u662F\u544A\u77E5\u5B69\u5B50\uFF1A \u201C\u6253\u4EBA\u201D\u7684\u884C\u4E3A\u662F\u4E0D\u88AB\u5141\u8BB8\u7684\u3002</p><p>\u4E00\u3001\u5F53\u5B9D\u5B9D\u51FA\u73B0\u201C\u6253\u4EBA\u201D\u7684\u884C\u4E3A\u65F6\uFF0C\u628A\u4ED6\u62C9\u5411\u4E00\u8FB9\uFF0C\u7528\u5E73\u9759\u4F46\u575A\u5B9A\u7684\u8BED\u6C14\u544A\u8BC9\u4ED6\u201C\u4E0D\u53EF\u4EE5\u201D\uFF0C\u5728\u8BF4\u5B8C\u4E4B\u540E\u628A\u5934\u626D\u5230\u4E00\u8FB9\uFF0C\u505C\u987F5-10\u79D2\u540E\u518D\u7EE7\u7EED\u548C\u5B9D\u5B9D\u8FDB\u884C\u6B63\u5E38\u4E92\u52A8\u3002\u6B64\u65F6\u5BB6\u4E2D\u7684\u5176\u4ED6\u4EBA\u4E5F\u9700\u8981\u914D\u5408\uFF0C\u4E0D\u505A\u51FA\u4EFB\u4F55\u8868\u60C5\u56DE\u5E94\u3002</p><p>\u4E8C\u3001\u9F13\u52B1\u5B9D\u5B9D\u901A\u8FC7\u62CD\u6253\u73A9\u5177\u6765\u63A2\u7D22\u6BCF\u6B21\u52A8\u4F5C\u548C\u7ED3\u679C\uFF08\u53D1\u58F0\u3001\u6389\u843D\u7B49\uFF09\u4E4B\u95F4\u7684\u5373\u65F6\u56E0\u679C\u5173\u7CFB\u3002\u8FD9\u6837\u53EF\u4EE5\u5E2E\u52A9\u5B69\u5B50\u4E86\u89E3\u81EA\u5DF1\u7684\u8EAB\u4F53\uFF0C\u953B\u70BC\u808C\u8089\u529B\u91CF\u548C\u8EAB\u4F53\u7684\u534F\u8C03\u80FD\u529B\uFF0C\u540C\u65F6\u5B69\u5B50\u4F1A\u56E0\u4E3A\u5BF9\u80A2\u4F53\u6108\u52A0\u719F\u7EC3\u7684\u638C\u63A7\u800C\u9010\u6E10\u5EFA\u7ACB\u8D77\u81EA\u4FE1\u3002</p><p>\u4E09\u3001\u4E0D\u65AD\u5411\u5B69\u5B50\u793A\u8303\u6B63\u786E\u7684\u60C5\u7EEA\u548C\u8868\u8FBE\u60C5\u7EEA\u7684\u65B9\u5F0F\u3002\u4F8B\u5982\u5728\u5F00\u5FC3\u7684\u65F6\u5019\u4E00\u8FB9\u8BF4\u201C\u592A\u597D\u4E86\u201D\u4E00\u8FB9\u9F13\u638C\u3002\u540C\u65F6\u627E\u4E00\u4E9B\u8868\u60C5\u8BC6\u522B\u5361\u548C\u5B69\u5B50\u4E00\u8D77\u5B66\u4E60\uFF0C\u6559\u4F1A\u5B69\u5B50\u4E0D\u540C\u7684\u8868\u60C5\u4EE3\u8868\u600E\u6837\u7684\u60C5\u7EEA\u3002\u6B64\u5916\uFF0C\u4E5F\u53EF\u4EE5\u627E\u4E00\u4E9B\u5173\u4E8E\u201C\u751F\u6C14\u201D\u548C\u5C0F\u670B\u53CB\u6253\u67B6\u7684\u7ED8\u672C\u6216\u8005\u89C6\u9891\uFF0C\u548C\u5B69\u5B50\u4E00\u8D77\u770B\uFF0C\u544A\u8BC9\u4ED6\u8FD9\u6837\u7684\u65B9\u5F0F\u5927\u5BB6\u90FD\u4E0D\u559C\u6B22\uFF0C\u5E76\u5411\u5B69\u5B50\u793A\u8303\u5982\u4F55\u6B63\u786E\u5E76\u53CB\u597D\u5730\u9080\u8BF7\u540C\u4F34\u548C\u81EA\u5DF1\u4E00\u8D77\u73A9\u3002</p><p>\u56DB\u3001\u7B5B\u9009\u52A8\u753B\u7247\u5E76\u548C\u5B69\u5B50\u4E00\u8D77\u89C2\u770B\uFF0C\u5728\u89C2\u770B\u8FC7\u7A0B\u4E2D\u53D1\u73B0\u4E0D\u826F\u884C\u4E3A\u8981\u53CA\u65F6\u544A\u77E5\u5B69\u5B50\u3002\u6CE8\u610F\u4E0D\u8981\u786C\u6027\u5265\u593A\u5B69\u5B50\u770B\u52A8\u753B\u7247\u7684\u6743\u5229\uFF0C\u4F46\u53EF\u4EE5\u9009\u62E9\u79EF\u6781\u5411\u4E0A\u3001\u9002\u5408\u5B69\u5B50\u5FC3\u7406\u53D1\u5C55\u7279\u70B9\u7684\u52A8\u753B\u7247\u8FDB\u884C\u89C2\u770B\u3002</p><p>\u4E94\u3001\u5E73\u65E5\u91CC\u7ED9\u4E88\u5B69\u5B50\u9AD8\u8D28\u91CF\u7684\u4EB2\u5B50\u966A\u4F34\uFF0C\u5F53\u5B69\u5B50\u7126\u8651\u60F6\u6050\u65F6\uFF0C\u53CA\u65F6\u62E5\u62B1\u5B69\u5B50\uFF0C\u7ED9\u4ED6\u5B89\u5168\u611F\u3002\u5728\u5B69\u5B50\u51FA\u73B0\u201C\u6253\u4EBA\u201D\u884C\u4E3A\u65F6\uFF0C\u5219\u65E0\u9700\u7ED9\u4E88\u7279\u522B\u7684\u53CD\u9988\uFF0C\u5C24\u5176\u662F\u907F\u514D\u8BB2\u9053\u7406\u4E4B\u540E\u518D\u62E5\u62B1\u7684\u65B9\u6CD5\uFF0C\u53EA\u9700\u8F7B\u63CF\u6DE1\u5199\u8BF4\u4E00\u53E5\u201C\u6253\u4EBA\u662F\u4E0D\u5BF9\u7684\u201D\u3002\u8FD9\u6837\u575A\u6301\u4E0B\u6765\uFF0C\u5E73\u65E5\u91CC\u7236\u6BCD\u5DF2\u7ECF\u7ED9\u4E88\u5B69\u5B50\u8DB3\u591F\u7684\u5173\u6CE8\u4E86\uFF0C\u5B69\u5B50\u5219\u4E0D\u9700\u8981\u901A\u8FC7\u95EE\u9898\u884C\u4E3A\u6765\u83B7\u5F97\u3002\u5B69\u5B50\u4E5F\u4F1A\u53D1\u73B0\uFF0C\u5373\u4F7F\u4ED6\u51FA\u73B0\u4E86\u95EE\u9898\u884C\u4E3A\uFF0C\u4E5F\u4E0D\u4F1A\u83B7\u5F97\u7236\u6BCD\u66F4\u591A\u7684\u5173\u6CE8\u3002\u4E8E\u662F\uFF0C\u201C\u6253\u4EBA\u201D\u884C\u4E3A\u5C31\u4F1A\u5931\u53BB\u5B83\u6240\u5177\u5907\u7684\u5438\u5F15\u7236\u6BCD\u6CE8\u610F\u529B\u7684\u529F\u80FD\uFF0C\u81EA\u7136\u4F1A\u9010\u6E10\u51CF\u5C11\uFF0C\u76F4\u81F3\u6D88\u5931\u3002</p><p>\u516D\u3001\u7236\u6BCD\u8981\u8D77\u8868\u7387\u4F5C\u7528\u3002\u6C38\u8FDC\u4E0D\u80FD\u4F4E\u4F30\u8A00\u4F20\u8EAB\u6559\u7684\u5F71\u54CD\u529B\uFF0C\u5728\u6559\u80B2\u5B69\u5B50\u5982\u4F55\u63A7\u5236\u60C5\u7EEA\u4E4B\u524D\uFF0C\u5927\u4EBA\u9996\u5148\u5E94\u8BE5\u63A7\u5236\u597D\u81EA\u5DF1\u7684\u60C5\u7EEA\uFF0C\u505A\u597D\u60C5\u7EEA\u7BA1\u7406\u3002</p><p>\u9700\u8981\u63D0\u9192\u7684\u662F\uFF0C\u5982\u679C\u53D1\u73B0\u5B69\u5B50\u5BF9\u4EBA\u7269\u8868\u60C5\u548C\u60C5\u7EEA\u7684\u7406\u89E3\u5B58\u5728\u95EE\u9898\uFF0C\u53C8\u6392\u9664\u6559\u517B\u73AF\u5883\u548C\u65B9\u5F0F\u7684\u5F71\u54CD\uFF0C\u5219\u8981\u6CE8\u610F\u5E26\u5B69\u5B50\u53BB\u505A\u4E00\u4E2A\u8BA4\u77E5\u80FD\u529B\u7684\u8BC4\u4F30\uFF0C\u6392\u9664\u5176\u4ED6\u95EE\u9898\u3002</p><hr><p>\u9898\u56FE\u6765\u81EAPixabay\u3002\u672C\u6587\u4F5C\u8005\u5F90\u6842\u51E4\uFF0C\u4E3A\u7F8E\u56FD\u7231\u8377\u534E\u513F\u7AE5\u533B\u9662\u53D1\u80B2\u4E0E\u969C\u788D\u4E2D\u5FC3\u513F\u7AE5\u5FC3\u7406\u533B\u751F\uFF0C\u5979\u7684\u516C\u4F17\u53F7\u4E3Axuguifeng82\u3002\u5173\u6CE8\u6021\u79BE\u5065\u5EB7\uFF0C\u53EF\u5411\u5F90\u533B\u751F\u548C\u5176\u5B83\u513F\u79D1\u3001\u5987\u79D1\u3001\u76AE\u80A4\u79D1\u533B\u751F\u54A8\u8BE2\u3002</p>',27),f={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651626533&idx=1&sn=814484bc0f8a12c231b4825e0aa3f7f2&chksm=8b5cb5dfbc2b3cc9a90dc3c9bf1fea663b871548a8fd065e4de819aae4bfe7e76ef5a037b67e&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},h=s("\u67E5\u770B\u539F\u6587");function l(b,m){const p=a("ExternalLinkIcon");return r(),t(c,null,[_,e("p",null,[e("a",f,[h,n(p)])])],64)}var g=d(i,[["render",l]]);export{g as default};
