import{r as p,o as c,c as s,a as e,b as a,w as r,F as d,e as t,d as _}from"./app.9aaa2256.js";import{_ as i}from"./plugin-vue_export-helper.21dcd24c.js";const h={},l=e("h3",{id:"\u533B\u9662\u81EA\u5236\u836F-\u662F\u5B9D\u8D1D\u836F\u8FD8\u662F\u5783\u573E\u836F",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#\u533B\u9662\u81EA\u5236\u836F-\u662F\u5B9D\u8D1D\u836F\u8FD8\u662F\u5783\u573E\u836F","aria-hidden":"true"},"#"),t(" \u533B\u9662\u81EA\u5236\u836F\uFF0C\u662F\u5B9D\u8D1D\u836F\u8FD8\u662F\u5783\u573E\u836F\uFF1F")],-1),f=t("\u7F51\u4E0A\u65F6\u4E0D\u65F6\u4F1A\u770B\u5230\u4E00\u4E9B\u70ED\u6367\u9662\u5185\u81EA\u5236\u836F\u7684\u6587\u7AE0\uFF0C\u6BD4\u5982\u524D\u4E0D\u4E45\u5357\u4EAC\u5E02\u513F\u7AE5\u533B\u9662\u7684\u5B98\u65B9\u5FAE\u4FE1\u5C31\u63A8\u9001\u8FC7\u4E00\u7BC7\u300A"),m=t("\u513F\u533B\u201C\u660E\u661F\u201D\u81EA\u5236\u5242\u5F3A\u52BF\u56DE\u5F52\uFF0C\u8FD9\u6B21\u4E0D\u7528\u6392\u961F\u4E5F\u80FD\u5F00\uFF01"),b=t("\u300B\u7684\u6587\u7AE0\uFF0C\u70ED\u6367\u81EA\u5DF1\u533B\u9662\u7684\u81EA\u5236\u836F\u3002"),x=_("<p>\u5A92\u4F53\u4E5F\u7ECF\u5E38\u505A\u7C7B\u4F3C\u7684\u62A5\u9053\uFF0C\u628A\u533B\u9662\u7684\u81EA\u5236\u836F\u6367\u4E3A\u826F\u5FC3\u836F\uFF0C\u5B9D\u8D1D\u836F\uFF0C\u6BD4\u5982\u5357\u4EAC\u513F\u7AE5\u533B\u9662\u7684\u6297601\u5408\u5242\uFF0C\u53C8\u6BD4\u5982\u9996\u90FD\u513F\u7814\u6240\u9F0E\u9F0E\u5927\u540D\u7684\u80A4\u4E50\u971C\uFF0C\u4E0D\u4F46\u5728\u5317\u4EAC\u88AB\u70ED\u6367\uFF0C\u8FD8\u6709\u5F88\u591A\u5916\u5730\u4EBA\u627E\u4EE3\u8D2D\u8D2D\u4E70\u3002</p><p>\u81EA\u5236\u836F\u771F\u7684\u6709\u90A3\u4E48\u597D\u5417\uFF1F\u5B83\u4EEC\u5230\u5E95\u662F\u5B9D\u8D1D\u836F\u8FD8\u662F\u5783\u573E\u836F\uFF1F</p><p>\u5927\u5BB6\u90FD\u77E5\u9053\u533B\u9662\u662F\u770B\u75C5\u7684\u5730\u65B9\uFF0C\u836F\u54C1\u662F\u7531\u836F\u5382\u751F\u4EA7\u7684\uFF0C\u533B\u9662\u91CC\u7684\u836F\uFF0C\u901A\u5E38\u4E5F\u662F\u836F\u5382\u751F\u4EA7\u533B\u9662\u91C7\u8D2D\u518D\u5F00\u7ED9\u5927\u5BB6\u3002</p><p>\u5728\u7F3A\u533B\u5C11\u836F\u7684\u5E74\u4EE3\uFF0C\u6709\u4E9B\u4E34\u5E8A\u4E0A\u9700\u8981\u7528\u7684\u836F\u836F\u5382\u6CA1\u751F\u4EA7\uFF0C\u4E3A\u89E3\u51B3\u7528\u836F\u9700\u6C42\uFF0C\u4E00\u4E9B\u533B\u9662\u5C31\u81EA\u5DF1\u5EFA\u8BBE\u5236\u5242\u5BA4\u81EA\u5DF1\u751F\u4EA7\u836F\u54C1\u5728\u81EA\u5DF1\u533B\u9662\u91CC\u9500\u552E\uFF0C\u7136\u540E\u4EA7\u751F\u4E86\u9662\u5185\u81EA\u5236\u836F\u3002\u9662\u5185\u81EA\u5236\u836F\u5176\u5B9E\u662F\u7F3A\u533B\u5C11\u836F\u5E74\u4EE3\u7684\u4E00\u4E2A\u9057\u7559\u4EA7\u7269\u3002</p><p>\u6700\u65E9\u81EA\u5236\u836F\u751F\u4EA7\u6CA1\u4EC0\u4E48\u7BA1\u7406\uFF0C\u751F\u4EA7\u4E5F\u5F88\u968F\u610F\uFF0C\u53EF\u80FD\u67D0\u4E2A\u533B\u751F\u81EA\u5DF1\u89C9\u5F97\u67D0\u5F20\u65B9\u5B50\u4E0D\u9519\uFF0C\u7136\u540E\u548C\u836F\u5242\u79D1\u5236\u5242\u5BA4\u7684\u4EBA\u4E00\u5408\u8BA1\uFF0C\u5C31\u4EA7\u751F\u4E86\u4E00\u4E2A\u65B0\u836F\uFF0C\u6240\u4EE5\u81EA\u5236\u836F\u66FE\u7ECF\u5F88\u76DB\u884C\uFF0C\u5C24\u5176\u662F\u5728\u4E00\u4E9B\u4E2D\u533B\u9662\u91CC\uFF0C\u5728\u9876\u5CF0\u65F6\u671F\uFF0C\u5355\u5BB6\u533B\u9662\u53EF\u4EE5\u6709600\u591A\u79CD\u9662\u5185\u5236\u5242\u3002</p><p>\u8FD9\u79CD\u4E0D\u89C4\u8303\u663E\u7136\u4F1A\u5E26\u6765\u5B89\u5168\u95EE\u9898\uFF0C\u5230\u4E862005\u5E74\uFF0C\u836F\u76D1\u90E8\u95E8\u51FA\u53F0\u4E86\u300A\u533B\u7597\u673A\u6784\u5236\u5242\u6CE8\u518C\u7BA1\u7406\u529E\u6CD5\u300B\uFF0C\u5BF9\u4E00\u4E9B\u81EA\u5236\u836F\u505A\u4E86\u6E05\u7406\uFF0C\u4E5F\u5BF9\u5BA1\u6279\u548C\u751F\u4EA7\u505A\u4E86\u4E00\u4E9B\u89C4\u8303\uFF0C\u4F46\u5BA1\u6279\u7684\u6743\u9650\u4F9D\u7136\u662F\u653E\u5728\u7701\u7EA7\u836F\u76D1\u90E8\u95E8\uFF0C\u800C\u4E14\uFF0C\u6309\u539F\u5F53\u65F6\u53D1\u5E03\u7684\u7533\u62A5\u8981\u6C42\uFF0C\u53EA\u8981\u7533\u62A5\u7684\u5236\u5242\u6709\u7701\u3001\u81EA\u6CBB\u533A\u3001\u76F4\u8F96\u5E02\u533B\u7597\u673A\u6784\u5236\u5242\u6807\u51C6\u7684\uFF0C\u5C31\u4E0D\u9700\u8981\u6709\u4E34\u5E8A\u7814\u7A76\u8D44\u6599\uFF0C\u8FD9\u4E5F\u662F\u4E3A\u4EC0\u4E48\u6709\u81EA\u5236\u836F\u7684\u57FA\u672C\u90FD\u662F\u90A3\u4E9B\u5927\u578B\u7684\u7701\u3001\u90E8\u7EA7\u533B\u9662\u3002</p><p>\u56E0\u4E3A\u81EA\u5236\u836F\u53EF\u4EE5\u4E0D\u7528\u5148\u7ECF\u8FC7\u4E34\u5E8A\u8BD5\u9A8C\u5148\u9A8C\u8BC1\u6709\u6548\u6027\u548C\u5B89\u5168\u6027\u5C31\u83B7\u5F97\u6279\u53F7\uFF0C\u8FD9\u4E5F\u5C31\u51B3\u5B9A\u4E86\u8FD9\u7C7B\u836F\u7684\u54C1\u8D28\u3002\u5982\u679C\u4F60\u7559\u5FC3\u7684\u8BDD\u5C31\u4F1A\u6CE8\u610F\u5230\uFF0C\u9662\u5185\u81EA\u5236\u836F\u7EDD\u5927\u90E8\u5206\u90FD\u662F\u4E2D\u6210\u836F\uFF0C\u6BD4\u5982\u4E0B\u9762\u8FD9\u4E2A\u5357\u4EAC\u5E02\u513F\u7AE5\u533B\u9662\u7684\u81EA\u5236\u836F\u5408\u96C6\u3002</p><p>\u56FE\u7247\u6E90\u81EA\u5357\u4EAC\u5E02\u513F\u7AE5\u533B\u9662\u5B98\u65B9\u5FAE\u4FE1</p><p>\u53C8\u6BD4\u5982\u8457\u540D\u7684\u9996\u90FD\u513F\u79D1\u7814\u7A76\u6240\u91CC\u768460\u591A\u79CD\u81EA\u5236\u836F\u91CC\uFF0C\u5C31\u5305\u62EC\u4E86\u80A4\u4E50\u971C\u3001\u53CC\u9EC4\u795B\u6E7F\u6D17\u5242\uFF0C\u8F9B\u82CD\u9F3B\u8212\u53E3\u670D\u6DB2\u3001\u674F\u8D1D\u6B62\u54B3\u6D17\u75F0\u53E3\u670D\u6DB2\u3001\u6E05\u80BA\u5316\u75F0\u9897\u7C92\u7B49\u4E00\u5927\u5806\u7684\u4E2D\u6210\u836F\u3002</p><p>\u6211\u5728\u300A\u4E3A\u4EC0\u4E48\u4E0D\u8981\u7528\u4E2D\u836F\u300B\u8FD9\u7BC7\u6587\u7AE0\u91CC\u8BF4\u8FC7\uFF0C\u56E0\u4E3A\u4E2D\u836F\u7684\u529F\u6548\u662F\u65E0\u6CD5\u8FDB\u884C\u9A8C\u8BC1\u7684\uFF0C\u6240\u4EE5\u8FD9\u4E9B\u836F\u7684\u6548\u679C\u90FD\u662F\u672A\u7ECF\u4E25\u683C\u9A8C\u8BC1\u7684\uFF0C\u6BD4\u5982\u6211\u4E4B\u524D\u4E13\u95E8\u5199\u6587\u7AE0\u5206\u6790\u8FC7\u7684\u201C\u80A4\u4E50\u971C\u201D\uFF0C\u6548\u679C\u5176\u5B9E\u662F\u6765\u81EA\u91CC\u9762\u6DFB\u52A0\u7684\u6FC0\u7D20\uFF0C\u540E\u671F\u4E0D\u8BA9\u6DFB\u52A0\u6FC0\u7D20\u4E86\uFF0C\u5C31\u8981\u548C\u6FC0\u7D20\u642D\u914D\u7740\u7528\u624D\u6709\u6548\u3002\uFF08\u56DE\u590D\u300C<strong>\u80A4\u4E50\u971C</strong>\u300D\u53EF\u4EE5\u770B\u8BE6\u7EC6\u89E3\u91CA\uFF09</p><p>\u518D\u6BD4\u5982\u5357\u4EAC\u513F\u7AE5\u533B\u9662\u7684\u625B\u628A\u5B50\u6297601\u5408\u5242\uFF0C\u662F\u7531\u91D1\u94F6\u82B1\u3001\u9EC4\u82A9\u3001\u5927\u9EC4\u3001\u9EC4\u67CF\u3001\u677F\u84DD\u6839\u7EC4\u6210\uFF0C\u5374\u6807\u79F0\u80FD\u6E05\u70ED\u89E3\u6BD2\u3001\u6297\u75C5\u6BD2\uFF0C\u88AB\u7528\u4E8E\u6CBB\u7597\u75C5\u6BD2\u6027\u611F\u5192\u3001\u6025\u6027\u652F\u6C14\u7BA1\u708E\u3001\u6241\u6843\u4F53\u708E\u3001\u816E\u817A\u708E\u7B49\uFF0C\u5F00\u8FD9\u4E9B\u836F\u7684\u533B\u751F\u4F1A\u4E0D\u6E05\u695A\u76EE\u524D\u6CA1\u6709\u9488\u5BF9\u611F\u5192\u3001\u513F\u7AE5\u6025\u6027\u652F\u6C14\u7BA1\u708E\u7684\u6297\u75C5\u6BD2\u836F\u5417\uFF1F</p><p>\u5728\u6709\u6B63\u89C4\u7684\u836F\u54C1\u6279\u53F7\u7684\u57FA\u7840\u4E0A\uFF0C\u836F\u76D1\u90E8\u95E8\u4FDD\u7559\u9662\u5185\u81EA\u5236\u836F\u7684\u6279\u53F7\uFF0C\u521D\u8877\u53EF\u80FD\u662F\u4E3A\u4E86\u6EE1\u8DB3\u533B\u9662\u7684\u4E34\u5E8A\u7528\u836F\u9700\u6C42\uFF0C\u6240\u4EE5\u8981\u6C42\u81EA\u5236\u836F\u4E0D\u80FD\u662F\u5E02\u9762\u4E0A\u6709\u6B63\u89C4\u836F\u54C1\u7684\u54C1\u79CD\uFF0C\u4F46\u56E0\u4E3A\u533B\u9662\u81EA\u5236\u836F\u53EF\u4EE5\u4EA7\u9500\u4E00\u6761\u9F99\u5229\u6DA6\u66F4\u5927\uFF0C\u6240\u4EE5\u54EA\u6015\u6709\u6B63\u89C4\u836F\u54C1\uFF0C\u6709\u81EA\u5236\u836F\u7684\u533B\u9662\u4E5F\u70ED\u8877\u4E8E\u4F7F\u7528\u81EA\u5236\u836F\u3002</p><p>\u5E7F\u5DDE\u67D0\u533B\u9662\u7684\u5185\u90E8\u901A\u77E5</p><p>\u6BD4\u5982\u5E02\u9762\u4E0A\u65E9\u5C31\u6709\u6B63\u89C4\u836F\u5382\u751F\u4EA7\u7684\u9000\u70ED\u836F\uFF0C\u800C\u6709\u7684\u513F\u7AE5\u533B\u9662\u5C45\u7136\u8FD8\u5728\u81EA\u5236\u201C\u7EA2\u836F\u6C34\u201D\u9000\u70E7\u836F\uFF0C\u800C\u8FD9\u79CD\u81EA\u5236\u7684\u201C\u7EA2\u836F\u6C34\u201D\u6210\u4EFD\u5176\u5B9E\u5C31\u662F\u5BF9\u4E59\u9170\u6C28\u57FA\u915A\uFF0C\u540C\u6837\u7684\u836F\u54C1\uFF0C\u4F60\u662F\u66F4\u76F8\u4FE1\u6B63\u89C4\u836F\u5382\u7684\u8D28\u91CF\u63A7\u5236\uFF0C\u8FD8\u662F\u76F8\u4FE1\u533B\u9662\u5185\u90E8\u4F5C\u574A\u5F0F\u5236\u5242\u5BA4\u7684\u751F\u4EA7\u5DE5\u827A\u5462\uFF1F</p><p>\u65E2\u7136\u662F\u6CA1\u7528\u6216\u54C1\u8D28\u5DEE\u7684\u836F\uFF0C\u4E3A\u4EC0\u4E48\u8FD8\u4F1A\u5E7F\u53D7\u6B22\u8FCE\u5462\uFF1F\u8FD9\u662F\u56E0\u4E3A\u7528\u8FD9\u4E9B\u81EA\u5236\u836F\u7684\u533B\u9662\u672C\u8EAB\u90FD\u662F\u8001\u724C\u7684\u5927\u578B\u516C\u7ACB\u533B\u9662\uFF0C\u5BB9\u6613\u88AB\u4EBA\u4FE1\u4EFB\uFF0C\u5C31\u50CF\u5927\u4E09\u7532\u533B\u9662\u7684\u4E13\u5BB6\u95E8\u8BCA\u91CC\u5750\u4E2A\u6C5F\u6E56\u90CE\u4E2D\uFF0C\u4E5F\u4E00\u6837\u4F1A\u6709\u4EBA\u6392\u961F\u6302\u53F7\u4E00\u6837\u3002\u800C\u81EA\u5236\u836F\u8FD8\u53EA\u80FD\u5728\u9662\u5185\u9500\u552E\uFF0C\u6240\u4EE5\u5BB9\u6613\u8BA9\u4EBA\u4EA7\u751F\u662F\u5927\u533B\u9662\u72EC\u5BB6\u79D8\u65B9\u7684\u611F\u89C9\u3002</p><p>\u81EA\u5236\u836F\u56E0\u4E3A\u533B\u9662\u81EA\u5DF1\u4EA7\u9500\u4E00\u6761\u9F99\uFF0C\u5F88\u591A\u5229\u6DA6\u6BD4\u8F83\u9AD8\uFF0C\u6240\u4EE5\u6709\u7684\u533B\u9662\u4E5F\u4F1A\u8BA9\u533B\u751F\u591A\u5F00\uFF0C\u800C\u5F88\u591A\u75BE\u75C5\u672C\u6765\u5C31\u662F\u81EA\u6108\u6027\u75BE\u75C5\uFF0C\u6BD4\u5982\u6297601\u9488\u5BF9\u7684\u611F\u5192\u3001\u513F\u7AE5\u652F\u6C14\u7BA1\u708E\u3001\u75C5\u6BD2\u6027\u816E\u817A\u708E\uFF0C\u7528\u5F97\u591A\u4E86\u78B0\u5230\u201C\u6709\u6548\u201D\u7684\u6B21\u6570\u4E5F\u5C31\u8D8A\u591A\uFF0C\u7136\u540E\u5C31\u88AB\u75C5\u4EBA\u5439\u6367\u6210\u795E\u836F\u3002</p><p>\u6309\u7167\u300A\u533B\u7597\u673A\u6784\u5236\u5242\u6CE8\u518C\u7BA1\u7406\u529E\u6CD5\u300B\uFF0C\u81EA\u5236\u836F\u53EA\u80FD\u5728\u533B\u7597\u673A\u6784\u5185\u51ED\u533B\u751F\u5904\u65B9\u4F7F\u7528\uFF0C\u81EA\u5236\u836F\u5982\u679C\u771F\u7684\u662F\u5B9D\u8D1D\u836F\uFF0C\u56FD\u5BB6\u4E3A\u4EC0\u4E48\u8981\u505A\u8FD9\u6837\u7684\u9650\u5236\uFF1F\u4E3A\u4EC0\u4E48\u4E0D\u7ED9\u6B63\u89C4\u7684\u836F\u54C1\u6279\u53F7\u8BA9\u6B63\u89C4\u836F\u5382\u53BB\u751F\u4EA7\u7136\u540E\u5168\u56FD\u9500\u552E\u8BA9\u66F4\u591A\u60A3\u8005\u53D7\u76CA\uFF1F\u5982\u679C\u8FD9\u4E9B\u836F\u771F\u7684\u6709\u90A3\u4E48\u597D\uFF0C\u836F\u5382\u4E3A\u4EC0\u4E48\u4E0D\u4E70\u4E13\u5229\u53BB\u5927\u89C4\u6A21\u751F\u4EA7\u9500\u552E\uFF1F\u5982\u679C\u4E00\u4E2A\u533B\u9662\u6CBB\u75C5\u9760\u7684\u662F\u81EA\u5236\u836F\u6765\u625B\u628A\u5B50\uFF0C\u4F60\u8BA9\u90A3\u4E9B\u6CA1\u6709\u8FD9\u4E2A\u81EA\u5236\u836F\u7684\u533B\u9662\u62FF\u4EC0\u4E48\u53BB\u6CBB\u75C5\u5462\uFF1F</p><p>\u5728\u7433\u7405\u6EE1\u76EE\u7684\u81EA\u5236\u836F\u91CC\uFF0C\u6216\u8BB8\u6709\u4E00\u4E9B\u6210\u5206\u6E05\u6670\u6548\u679C\u4E0D\u9519\u4EF7\u683C\u4FBF\u5B9C\u7684\u836F\uFF0C\u4F46\u6709\u7684\u8BDD\u4E5F\u662F\u51E4\u6BDB\u9E9F\u89D2\uFF0C\u8FD9\u662F\u7531\u81EA\u5236\u836F\u7684\u5BA1\u6279\u6807\u51C6\u548C\u751F\u4EA7\u6761\u4EF6\u51B3\u5B9A\u7684\u3002\u8FD9\u4E9B\u5728\u5927\u533B\u9662\u91CC\u5356\u5F97\u706B\u70ED\u7684\u660E\u661F\u836F\uFF0C\u5176\u5B9E\u5927\u90E8\u5206\u90FD\u662F\u6210\u5206\u4E0D\u660E\uFF0C\u529F\u6548\u4E0D\u660E\uFF0C\u5B89\u5168\u6027\u4E0D\u660E\u7684\u5783\u573E\u836F\u3002</p>",18),u={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651627517&idx=1&sn=297a9f36657daa141cb62eaf78925ccd&chksm=8b5cb607bc2b3f11de43ba790ccc5d2d47a8998255ddf28438221dbdd45670c058459e972225&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},k=t("\u67E5\u770B\u539F\u6587");function z(g,N){const o=p("RouterLink"),n=p("ExternalLinkIcon");return c(),s(d,null,[l,e("p",null,[f,a(o,{to:"/s/__biz=MzA5MzQ3ODAzMQ==&mid=2649687846&idx=1&sn=957a65ab9dec75bde56090aab63f8314/"},{default:r(()=>[m]),_:1}),b]),x,e("p",null,[e("a",u,[k,a(n)])])],64)}var L=i(h,[["render",z]]);export{L as default};
