import{r as a,o as r,c as t,a as e,b as n,F as o,d as c,e as s}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const _={},i=c('<h3 id="\u7978\u4ECE\u53E3\u5165\u2014\u2014\u513F\u7AE5\u6D88\u5316\u9053\u5F02\u7269" tabindex="-1"><a class="header-anchor" href="#\u7978\u4ECE\u53E3\u5165\u2014\u2014\u513F\u7AE5\u6D88\u5316\u9053\u5F02\u7269" aria-hidden="true">#</a> \u7978\u4ECE\u53E3\u5165\u2014\u2014\u513F\u7AE5\u6D88\u5316\u9053\u5F02\u7269</h3><p>\u524D\u51E0\u5929\u6709\u4E2A2\u5C81\u7684\u5B69\u5B50\uFF0C\u53D1\u70E7\u5455\u5410\u809A\u5B50\u80C0\uFF0C\u62CD\u7247\u5B50\u53D1\u73B0\u809A\u5B50\u91CC\u6709\u4E2A\u5C0F\u5F02\u7269\u3002</p><p>\u6309\u8BF4\u8FD9\u4E48\u5C0F\u7684\u5F02\u7269\u57FA\u672C\u53EF\u4EE5\u81EA\u5DF1\u6392\u51FA\u6765\uFF0C\u4E5F\u4E0D\u592A\u53EF\u80FD\u5F15\u8D77\u80A0\u6897\u963B\uFF0C\u4F46\u8FD9\u4E2A\u5B69\u5B50\u5374\u6709\u660E\u663E\u7684\u80A0\u6897\u963B\uFF0C\u4E5F\u6709\u8179\u819C\u708E\u3002\u7136\u540E\u505A\u4E86\u624B\u672F\uFF0C\u624B\u672F\u4E2D\u53D1\u73B0\u4E24\u5904\u80A0\u5B50\u7A7F\u5B54\u3002</p><p>\u540E\u6765\u5728\u809A\u5B50\u91CC\u627E\u5230\u4E86\u90A3\u4E2A\u5C0F\u5F02\u7269\uFF0C\u62FF\u51FA\u6765\u4E00\u770B\uFF0C\u539F\u6765\u662F\u4E00\u4E2A\u5C0F\u78C1\u94C1\u548C\u4E00\u4E2A\u5C0F\u94C1\u6761\u7D27\u7D27\u7684\u5438\u5728\u4E00\u8D77\u3002</p><p>\u539F\u6765\u5B69\u5B50\u8D81\u5BB6\u957F\u4E0D\u6CE8\u610F\uFF0C\u5148\u540E\u5403\u8FDB\u4E86\u4E00\u4E2A\u5C0F\u78C1\u94C1\u548C\u4E00\u6839\u5C0F\u94C1\u6761\uFF0C\u8FD9\u4E24\u4E2A\u4E1C\u897F\u8FDB\u80A0\u5B50\u540E\u9694\u7740\u80A0\u58C1\u5438\u5728\u4E00\u8D77\uFF0C\u6162\u6162\u7684\u628A\u80A0\u5B50\u538B\u70C2\u4E86\uFF0C\u5BFC\u81F4\u80A0\u7A7F\u5B54\u3002</p><p>\u4EE5\u524D\u8FD8\u89C1\u8FC7\u597D\u51E0\u4F8B\u5403\u4E86\u51E0\u4E2A\u5C0F\u78C1\u94C1\u5BFC\u81F4\u7A7F\u5B54\u7684\uFF0C\u5370\u8C61\u5F88\u6DF1\u523B\u7684\u662F\u51E0\u5E74\u524D\u6709\u4E2A\u5F02\u7269\u7684\u5B69\u5B50\uFF0C\u533B\u751F\u770B\u7247\u5B50\u4EE5\u4E3A\u662F\u4E2A\u94A2\u6761\u5BFC\u81F4\u7684\u7A7F\u5B54\uFF0C\u5F00\u8FDB\u53BB\u53D1\u73B0\u90A3\u4E2A\u94A2\u6761\u662F5\u9897\u5C0F\u78C1\u94C1\u5438\u6210\u4E00\u4E32\uFF0C\u5BFC\u81F4\u80C3\u548C\u80A0\u5B50\u591A\u5904\u7A7F\u5B54\uFF0C\u60C5\u5F62\u60E8\u4E0D\u5FCD\u7779\u3002</p><p>\u5176\u5B9E\u6D88\u5316\u9053\u5F02\u7269\u5728\u513F\u7AE5\u5F88\u5E38\u89C1\uFF0C\u5C24\u5176\u662F6\u4E2A\u6708\u52303\u5C81\u7684\u5B69\u5B50\uFF0C\u8FD9\u4E2A\u5E74\u9F84\u9636\u6BB5\u7684\u5B69\u5B50\u5BF9\u4EC0\u4E48\u90FD\u5F88\u597D\u5947\uFF0C\u53C8\u6CA1\u4EC0\u4E48\u5B89\u5168\u610F\u8BC6\uFF0C\u5E38\u5E38\u662F\u62FF\u5230\u4E1C\u897F\u5C31\u5F80\u5634\u91CC\u653E\uFF0C\u597D\u5728\u7684\u662F\u5927\u90E8\u5206\u5F02\u7269\u90FD\u662F\u786C\u5E01\u3001\u7EBD\u6263\u3001\u53D1\u5939\u3001\u7535\u6C60\u4E4B\u7C7B\uFF0C\u6CA1\u6709\u4E0A\u9762\u8BF4\u7684\u78C1\u94C1\u90A3\u4E48\u5371\u9669\u3002</p><p>\u8FD9\u4E9B\u5F02\u7269\u4E00\u65E6\u541E\u4E0B\uFF0C\u4F1A\u8DDF\u98DF\u7269\u4E00\u6837\u6CBF\u7740\u6D88\u5316\u9053\u5F80\u4E0B\u8D70\uFF0C\u4F46\u5F02\u7269\u786C\u5EA6\u5F88\u5927\u5F62\u72B6\u5404\u5F02\uFF0C\u5F80\u5F80\u4E0D\u80FD\u50CF\u98DF\u7269\u4E00\u6837\u6B63\u5E38\u901A\u884C\uFF0C\u5F88\u591A\u5173\u5361\u4F1A\u8BA9\u5B83\u505C\u7559\u5728\u6D88\u5316\u9053\u91CC\u3002\u9996\u5148\u7B2C\u4E00\u5173\u5C31\u662F\u98DF\u9053\uFF0C\u98DF\u9053\u6709\u4E09\u4E2A\u6BD4\u8F83\u72ED\u7A84\u7684\u90E8\u4F4D\uFF0C\u5165\u53E3\u3001\u4E0E\u6C14\u7BA1\u4EA4\u53C9\u7684\u5730\u65B9\u3001\u8FD8\u6709\u98DF\u9053\u7A7F\u8FC7\u8188\u808C\u8FDB\u5165\u8179\u8154\u4F4D\u7F6E\uFF0C\u5F88\u591A\u5F02\u7269\u88AB\u5361\u5728\u8FD9\u4E09\u4E2A\u4F4D\u7F6E\u3002</p><p>\u56E0\u4E3A\u5F02\u7269\u5343\u5947\u767E\u602A\uFF0C\u6240\u4EE5\u6709\u98DF\u9053\u5F02\u7269\u7684\u5B69\u5B50\u75C7\u72B6\u4E5F\u5404\u4E0D\u76F8\u540C\uFF0C\u6709\u4E9B\u5B69\u5B50\u6709\u6076\u5FC3\u3001\u7A92\u606F\u3001\u54B3\u55FD\u3001\u6D41\u53E3\u6C34\u3001\u62D2\u98DF\u7B49\u75C7\u72B6\uFF0C\u4F46\u4E5F\u6709\u7EA61\uFF0F3\u7684\u5B69\u5B50\u5B8C\u5168\u6CA1\u6709\u4EFB\u4F55\u75C7\u72B6\uFF0C\u6240\u4EE5\u4E0D\u7BA1\u6709\u6CA1\u6709\u75C7\u72B6\uFF0C\u533B\u751F\u5BF9\u90A3\u4E9B\u6709\u53EF\u7591\u5F02\u7269\u53F2\u7684\u5B69\u5B50\u90FD\u4F1A\u5F88\u91CD\u89C6\uFF0C\u5B81\u53EF\u4FE1\u5176\u6709\u53BB\u4ED4\u7EC6\u68C0\u67E5\uFF0C\uFF08\u56DE\u590D104\u53EF\u8BFB\u5230\u4E00\u4E2A\u533B\u7597\u6545\u4E8B\uFF09\u3002</p><p>\u56E0\u4E3A\u786E\u5B9E\u6709\u4E9B\u5F02\u7269\u4F1A\u5BFC\u81F4\u5F88\u4E25\u91CD\u7684\u540E\u679C\uFF0C\u6709\u4E9B\u5F02\u7269\u5982\u7535\u6C60\u53EF\u4EE5\u5F15\u8D77\u7535\u6D41\u70E7\u4F24\uFF0C\u65F6\u95F4\u4E45\u4E86\u7535\u6C60\u91CC\u7684\u7535\u89E3\u6DB2\u6D41\u51FA\u6765\u8FD8\u4F1A\u8150\u8680\u98DF\u9053\u5F15\u8D77\u7A7F\u5B54\u548C\u4E2D\u6BD2\uFF0C\u66F4\u522B\u8BF4\u90A3\u4E9B\u5C16\u9510\u7684\u5F02\u7269\u523A\u7834\u98DF\u9053\uFF0C\u5BFC\u81F4\u51FA\u8840\u4EC0\u4E48\u7684\uFF0C\u4EE5\u524D\u8FD8\u89C1\u8FC7\u4E00\u4E2A\u5B69\u5B50\u5403\u8FDB\u53BB\u4E00\u4E2A\u9E21\u9AA8\u5934\uFF0C\u7136\u540E\u4ECE\u98DF\u9053\u624E\u5230\u4E86\u4E3B\u52A8\u8109\u3002</p><p>\u4E00\u65E6\u5F02\u7269\u8FC7\u4E86\u98DF\u9053\u8FDB\u4E86\u80C3\uFF0C95%\u53EF\u4EE5\u81EA\u884C\u6392\u51FA\uFF0C\u5927\u90E8\u5206\u57284-6\u5929\u5185\uFF0C\u65F6\u95F4\u957F\u7684\u89813\uFF0D4\u5468\uFF0C\u5BFC\u81F4\u6D88\u5316\u9053\u7A7F\u5B54\u7684\u4E0D\u52301%\u3002</p><p>\u8BEF\u541E\u786C\u5E01\u3001\u7EBD\u6263\u4E4B\u7C7B\u7684\u666E\u901A\u7684\u5F02\u7269\uFF0C\u9884\u4F30\u4E0D\u80FD\u81EA\u884C\u6392\u51FA\uFF0C\u6216\u8005\u6709\u6BD2\u6027\u3001\u8150\u8680\u6027\u7684\u53EF\u4EE5\u505A\u80C3\u955C\u53D6\u51FA\uFF0C\u5426\u5219\u53EF\u4EE5\u7B49\u5F85\u5B83\u81EA\u884C\u6392\u51FA\uFF0C\u5728\u7B49\u5F85\u8FC7\u7A0B\u4E2D\u5B69\u5B50\u53EF\u4EE5\u6B63\u5E38\u8FDB\u98DF\uFF0C\u4E5F\u4E0D\u9700\u8981\u5403\u4EC0\u4E48\u6CFB\u836F\uFF0C\u5BB6\u957F\u9700\u8981\u89C2\u5BDF\u5B69\u5B50\u6709\u6CA1\u6709\u4EC0\u4E48\u5F02\u5E38\u75C7\u72B6\uFF0C\u6BD4\u5982\u809A\u5B50\u75DB\uFF0C\u5455\u5410\uFF0C\u53D1\u70E7\uFF0C\u6CE8\u610F\u5927\u4FBF\u91CC\u6709\u6CA1\u6709\u8840\uFF0C\u540C\u65F6\u4ED4\u7EC6\u68C0\u67E5\u5927\u4FBF\u770B\u5F02\u7269\u6709\u6CA1\u6709\u6392\u51FA\u3002\u5982\u679C3-4\u5468\u90FD\u6CA1\u6709\u6392\u51FA\uFF0C\u5BFC\u81F4\u7A7F\u5B54\u7684\u53EF\u80FD\u6027\u4E5F\u4E0D\u5927\uFF0C\u4F46\u8981\u8B66\u60D5\u5B69\u5B50\u662F\u5426\u5B58\u5728\u6D88\u5316\u9053\u7578\u5F62\uFF0C\u6BD4\u5982\u80A0\u61A9\u5BA4\u7B49\uFF0C\u5F02\u7269\u6709\u65F6\u521A\u597D\u6389\u8FDB\u61A9\u5BA4\u91CC\u51FA\u4E0D\u6765\u3002</p><p>\u4F46\u6709\u4E9B\u5C16\u9510\u7684\u5F02\u7269\u6BD4\u8F83\u5BB9\u6613\u635F\u4F24\u80C3\u80A0\u9053\uFF0C\u5C31\u76F8\u5BF9\u66F4\u5371\u9669\uFF0C\u6BD4\u5982\u94C1\u9489\uFF0C\u87BA\u4E1D\u9489\uFF0C\u522B\u9488\uFF0C\u5982\u679C\u8FD8\u5728\u80C3\u91CC\uFF0C\u6709\u6761\u4EF6\u8FD8\u662F\u5E94\u8BE5\u7528\u80C3\u955C\u53D6\u51FA\u6765\uFF0C\u8FD9\u4E9B\u4E1C\u897F\u8FDB\u4E86\u5C0F\u80A0\u518D\u60F3\u53D6\u5C31\u53EA\u6709\u5F00\u5200\u4E86\u3002\u4F46\u5E76\u4E0D\u662F\u5C16\u9510\u7684\u4E1C\u897F\u8FDB\u4E86\u80A0\u5B50\u5C31\u4E00\u5B9A\u4F1A\u4F24\u5230\u80A0\u5B50\uFF0C\u4EBA\u7C7B\u7684\u6D88\u5316\u9053\u8FDB\u5316\u5F97\u6BD4\u8F83\u795E\u5947\uFF0C\u597D\u51E0\u6B21\u78B0\u5230\u541E\u98DF\u4E864-5\u5398\u7C73\u957F\u87BA\u4E1D\u9489\u7684\u5B69\u5B50\uFF0C\u751A\u81F3\u8FD8\u6709\u5F20\u5F00\u7684\u522B\u9488\uFF0C\u4EE5\u4E3A\u6392\u4E0D\u51FA\uFF0C\u7ED3\u679C\u5B69\u5B50\u6BEB\u53D1\u65E0\u4F24\u7684\u6392\u51FA\u6765\u4E86\u3002</p><p>\u9664\u975E\u9884\u4F30\u4E0D\u592A\u53EF\u80FD\u6392\u51FA\uFF0C\u8FD9\u7C7B\u5F02\u7269\u4E00\u65E6\u8FDB\u4E86\u5C0F\u80A0\u4E5F\u53EF\u4EE5\u9009\u62E9\u7B49\u5F85\u81EA\u884C\u6392\u51FA\uFF0C\u4F46\u7B49\u5F85\u8FC7\u7A0B\u4E2D\u4E00\u5B9A\u8981\u5BC6\u5207\u89C2\u5BDF\u5B69\u5B50\uFF0C\u786E\u5B9E\u6709\u4E9B\u9020\u6210\u80C3\u80A0\u7A7F\u5B54\u7684\uFF0C\u6BD4\u5982\u4E00\u4E9B\u6BD4\u8F83\u957F\u7684\u9489\u5B50\uFF0C\u5728\u901A\u8FC7\u5E7D\u95E8\u3001\u5341\u4E8C\u6307\u80A0\u3001\u56DE\u76F2\u90E8\u7684\u65F6\u5019\u5C31\u53EF\u80FD\u523A\u7834\u80C3\u80A0\u58C1\uFF0C\u4E00\u65E6\u6709\u7A7F\u5B54\u7684\u8FF9\u8C61\u5C31\u8981\u7D27\u6025\u624B\u672F\u3002</p><p>\u76F8\u5BF9\u4E0A\u9762\u4E24\u7C7B\uFF0C\u6700\u5371\u9669\u7684\u8FD8\u662F\u5F00\u59CB\u63D0\u5230\u7684\u78C1\u94C1\uFF0C\u66FE\u7ECF\u56E0\u4E3A\u73A9\u5177\u91CC\u7684\u5C0F\u78C1\u94C1\u5BB9\u6613\u6389\u51FA\u6765\uFF0C\u6015\u5B69\u5B50\u8BEF\u541E\u53D1\u751F\u5371\u9669\uFF0C\u7F8E\u6CF0\u516C\u53F8\u8FD8\u5927\u89C4\u6A21\u53EC\u56DE\u8FC7\u3002\u5403\u4E00\u4E2A\u78C1\u94C1\u518D\u5403\u8FDB\u53EF\u4EE5\u88AB\u5438\u4F4F\u7684\u91D1\u5C5E\u5F02\u7269\u6216\u8005\u540C\u65F6\u5403\u4E24\u4E2A\u4EE5\u4E0A\u7684\u78C1\u94C1\u5F88\u5371\u9669\uFF0C\u4F46\u5E76\u4E0D\u662F\u8BF4\u5355\u72EC\u5403\u4E00\u4E2A\u78C1\u94C1\u5C31\u6CA1\u6709\u98CE\u9669\uFF0C\u5F88\u591A\u4F4E\u5EC9\u7684\u78C1\u94C1\u73A9\u5177\u94C5\u542B\u91CF\u8D85\u6807\uFF0C\u5403\u8FDB\u53BB\u4E5F\u53EF\u80FD\u5BFC\u81F4\u94C5\u4E2D\u6BD2\uFF0C\u6240\u4EE5\u4E00\u5B9A\u8981\u8BA9\u5B69\u5B50\u8FDC\u79BB\u8FD9\u4E9B\u4E1C\u897F\u3002</p><p>\u80C3\u80A0\u7A7F\u5B54\u4F1A\u7ED9\u5B69\u5B50\u9020\u6210\u5F88\u5927\u4F24\u5BB3\uFF0C\u9664\u4E86\u809A\u5B50\u4E0A\u7684\u75A4\u75D5\u5916\uFF0C\u4E25\u91CD\u7684\u611F\u67D3\u4E5F\u53EF\u80FD\u5371\u53CA\u5B69\u5B50\u7684\u751F\u547D\uFF0C\u5373\u4FBF\u6CA1\u6709\u53D1\u751F\u7A7F\u5B54\uFF0C\u5BB6\u957F\u62C5\u5FC3\u53D7\u6015\u7684\u4E0D\u8BF4\uFF0C\u5B69\u5B50\u4E5F\u8981\u62CD\u7247\u5B50\u505A\u80C3\u955C\uFF0C\u627F\u53D7\u8FD9\u4E9B\u672C\u53EF\u4EE5\u4E0D\u7ECF\u5386\u7684\u4F24\u5BB3\u3002\u5982\u679C\u76D1\u62A4\u5230\u4F4D\uFF0C\u8BA9\u5B69\u5B50\u8FDC\u79BB\u8FD9\u4E9B\u53EF\u4EE5\u88AB\u541E\u4E0B\u7684\u5F02\u7269\uFF0C\u7EDD\u5927\u90E8\u5206\u6D88\u5316\u9053\u5F02\u7269\u90FD\u53EF\u4EE5\u907F\u514D\uFF0C\u5728\u5B69\u5B50\u8FD8\u6CA1\u6709\u5B89\u5168\u610F\u8BC6\u7684\u65F6\u5019\uFF0C\u5BB6\u957F\u7684\u76D1\u62A4\u548C\u6559\u80B2\u81F3\u5173\u91CD\u8981\u3002</p><p>\u9664\u4E86\u628A\u5F02\u7269\u5403\u8FDB\u809A\u5B50\uFF0C\u66F4\u5371\u9669\u7684\u662F\u8FD9\u4E9B\u653E\u8FDB\u5634\u91CC\u7684\u5F02\u7269\u76F4\u63A5\u6389\u8FDB\u6C14\u7BA1\uFF0C\u5BFC\u81F4\u7A92\u606F\uFF0C\u6BCF\u5E74\u90FD\u6709\u5F88\u591A\u5B69\u5B50\u56E0\u6C14\u7BA1\u5F02\u7269\u6B7B\u4EA1\uFF0C\u4F46\u6CA1\u6709\u89C1\u8BC6\u8FC7\u8FD9\u4E9B\u60E8\u75DB\u7684\u75C5\u4F8B\uFF0C\u5F88\u591A\u5BB6\u957F\u5BF9\u6B64\u5E76\u4E0D\u4EE5\u4E3A\u7136\uFF0C\u671F\u671B\u8FD9\u7BC7\u6587\u7AE0\u80FD\u63D0\u9AD8\u5927\u5BB6\u7684\u8BA4\u8BC6\uFF0C\u4E5F\u671F\u671B\u5927\u5BB6\u591A\u591A\u8F6C\u53D1\uFF0C\u8BA9\u66F4\u591A\u5BB6\u957F\u770B\u5230\uFF0C\u907F\u514D\u66F4\u591A\u7684\u60B2\u5267\u3002</p><p>\u63D0\u793A\uFF1A\u9898\u56FE\u6765\u6E90\u4E8E\u7F51\u7EDC\uFF0C\u5185\u5BB9\u90E8\u5206\u56FE\u7247\u7531\u5F20\u7FC5\u4E3B\u6CBB\u533B\u751F\u63D0\u4F9B\u3002</p>',18),l={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=200212958&idx=1&sn=63163de78b0073b959cda66936c30fd1&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},h=s("\u67E5\u770B\u539F\u6587");function m(f,x){const p=a("ExternalLinkIcon");return r(),t(o,null,[i,e("p",null,[e("a",l,[h,n(p)])])],64)}var k=d(_,[["render",m]]);export{k as default};
