import{r as a,o as r,c as t,a as e,b as n,F as c,d as o,e as s}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const i={},_=o('<h3 id="\u5C0F\u65F6\u5019\u6CA1\u90A3\u4E48\u591A\u8BB2\u7A76-\u4F60\u4E3A\u4EC0\u4E48\u597D\u597D\u7684" tabindex="-1"><a class="header-anchor" href="#\u5C0F\u65F6\u5019\u6CA1\u90A3\u4E48\u591A\u8BB2\u7A76-\u4F60\u4E3A\u4EC0\u4E48\u597D\u597D\u7684" aria-hidden="true">#</a> \u5C0F\u65F6\u5019\u6CA1\u90A3\u4E48\u591A\u8BB2\u7A76\uFF0C\u4F60\u4E3A\u4EC0\u4E48\u597D\u597D\u7684\uFF1F</h3><p>\u201C\u4F60\u5C0F\u65F6\u5019\u4E5F\u6CA1\u6709\u90A3\u4E48\u591A\u8BB2\u7A76\uFF0C\u4F60\u8FD8\u4E0D\u662F\u6D3B\u7684\u597D\u597D\u7684\uFF1F\u201D\u8FD9\u53E5\u8BDD\u53EF\u80FD\u5F88\u591A\u4EBA\u90FD\u5F88\u719F\u6089\u3002</p><p>\u5F53\u8001\u4EBA\u7ED9\u521A\u6EE1\u6708\u7684\u5B69\u5B50\u5403\u7C73\u7CCA\u7684\u65F6\u5019\uFF0C\u5F53\u8001\u4EBA\u7ED9\u5B69\u5B50\u5C1D\u9152\u7684\u65F6\u5019\uFF0C\u5F53\u8001\u4EBA\u7ED9\u5B69\u5B50\u5403\u4E2D\u836F\u7684\u65F6\u5019\uFF0C\u4F60\u53BB\u529D\u963B\uFF0C\u8001\u4EBA\u653E\u51FA\u8FD9\u53E5\u91D1\u53E5\uFF0C\u4F60\u53EF\u80FD\u5C31\u54D1\u53E3\u65E0\u8A00\u4E86\u3002</p><p>\u4E8B\u5B9E\u4E0A\uFF0C\u5F88\u591A\u4EBA\u5728\u9762\u5BF9\u4E00\u4E9B\u79D1\u5B66\u89C2\u5FF5\u7684\u65F6\u5019\uFF0C\u81EA\u5DF1\u4E5F\u4F1A\u6709\u8FD9\u6837\u7684\u5FF5\u5934\uFF0C\u81EA\u5DF1\u4EE5\u5C0F\u65F6\u5019\u6CA1\u8FD9\u4E48\u591A\u8BB2\u7A76\uFF0C\u73B0\u5728\u4E5F\u4E0D\u8FC7\u5F97\u597D\u597D\u7684\u5417\uFF1F\u4E8E\u662F\u5C31\u653E\u5F03\u4E86\u4E13\u4E1A\u610F\u89C1\u800C\u9009\u62E9\u4E86\u81EA\u5DF1\u7684\u76F4\u89C9\uFF0C\u4E00\u6B21\u6B21\u7684\u5C1D\u8BD5\u4E0B\u6765\uFF0C\u597D\u50CF\u4E5F\u6CA1\u53D1\u73B0\u4EC0\u4E48\u4E0D\u597D\u7684\u5730\u65B9\u3002\u90A3\u662F\u600E\u4E48\u56DE\u4E8B\u5462\uFF1F</p><p><strong>\u9996\u5148\uFF0C\u4E2A\u4F53\u5B58\u5728\u5DEE\u522B\uFF0C\u4E2A\u4F8B\u4EE3\u8868\u4E0D\u4E86\u6574\u4F53\u3002</strong></p><p>\u8BC4\u4EF7\u4E00\u79CD\u4E1C\u897F\u6216\u505A\u6CD5\u662F\u5426\u5B89\u5168\uFF0C\u4E0D\u662F\u9760\u4F8B\u5B50\uFF0C\u800C\u662F\u8981\u505A\u6574\u4F53\u7684\u5BF9\u6BD4\u3002\u6BD4\u5982\u4E00\u767E\u4E2A\u5B69\u5B50\u540C\u65F6\u5403\u67D0\u79CD\u53D8\u8D28\u98DF\u7269\uFF0C\u6709\u7684\u5B69\u5B50\u597D\u597D\u7684\uFF0C\u6709\u7684\u5B69\u5B50\u8F7B\u5FAE\u80A0\u708E\uFF0C\u6709\u7684\u5B69\u5B50\u4F11\u514B\u751A\u81F3\u6B7B\u4EA1\u3002\u53D1\u751F\u95EE\u9898\u7684\u5F80\u5F80\u662F\u4E00\u5C0F\u90E8\u5206\u4EBA\uFF0C\u4F46\u4ED6\u4EEC\u5DF2\u7ECF\u6CA1\u6709\u540E\u6094\u7684\u673A\u4F1A\u4E86\uFF0C\u4E5F\u6CA1\u673A\u4F1A\u7AD9\u51FA\u6765\u8BC1\u660E\u81EA\u5DF1\u662F\u597D\u597D\u7684\u3002\u6211\u4EEC\u4E0D\u80FD\u56E0\u4E3A\u81EA\u5DF1\u5B69\u5B50\u5403\u4E86\u597D\u597D\u7684\uFF0C\u5C31\u8BA4\u4E3A\u53D8\u8D28\u98DF\u7269\u548C\u65B0\u9C9C\u98DF\u7269\u4E00\u6837\u5B89\u5168\u3002</p><p><strong>\u6B64\u5916\uFF0C\u4F60\u89C9\u5F97\u597D\u597D\u7684\u672A\u5FC5\u771F\u5C31\u662F\u597D\u597D\u7684\u3002</strong></p><p>\u6709\u7684\u4E0D\u826F\u540E\u679C\u4F1A\u7ACB\u9A6C\u663E\u73B0\uFF0C\u6BD4\u5982\u5403\u4E86\u4E1C\u897F\u4E2D\u6BD2\uFF0C\u4F46\u6709\u7684\u540E\u679C\u662F\u7F13\u6162\u800C\u6301\u7EED\u7684\uFF0C\u53EF\u80FD\u5728\u6570\u6708\uFF0C\u6570\u5E74\u751A\u81F3\u6570\u5341\u5E74\u540E\u624D\u80FD\u770B\u5230\u3002</p><p>\u6BD4\u5982\u5927\u4EBA\u5C06\u81EA\u5DF1\u56BC\u8FC7\u7684\u4E1C\u897F\u5582\u7ED9\u5B69\u5B50\u5403\uFF0C\u53EF\u80FD\u5F53\u65F6\u5E76\u6CA1\u6709\u4EA7\u751F\u4EC0\u4E48\u4E0D\u597D\u7684\u53CD\u5E94\uFF0C\u4F60\u4EE5\u4E3A\u5C31\u662F\u597D\u597D\u7684\uFF0C\u7B49\u8FC7\u4E86\u534A\u5E74\u4E00\u5E74\uFF0C\u5B69\u5B50\u53D1\u751F\u4E86\u9F8B\u9F7F\uFF0C\u5982\u679C\u4F60\u6CA1\u6709\u4E86\u89E3\u76F8\u5173\u77E5\u8BC6\uFF0C\u4F60\u53EF\u80FD\u60F3\u4E0D\u5230\u548C\u4F60\u628A\u81EA\u5DF1\u53E3\u8154\u75C5\u83CC\u4F20\u7ED9\u4E86\u5B69\u5B50\u6709\u5173\u3002</p><p>\u5148\u540E\u53D1\u751F\u7684\u95EE\u9898\u4E0D\u7B49\u4E8E\u6709\u56E0\u679C\u5173\u7CFB\uFF0C\u4E24\u4EF6\u770B\u4F3C\u4E0D\u76F8\u5E72\u7684\u4E8B\u4EF6\uFF0C\u672A\u5FC5\u6CA1\u6709\u5173\u7CFB\u3002\u76F8\u5173\u6027\u9700\u8981\u6709\u4E13\u4E1A\u77E5\u8BC6\u7684\u4EBA\u6309\u79D1\u5B66\u7684\u65B9\u6CD5\u8FDB\u884C\u5BF9\u6BD4\u8BC4\u4F30\uFF0C\u4EC5\u9760\u76F4\u89C9\u662F\u65E0\u6CD5\u4F5C\u51FA\u51C6\u786E\u5224\u65AD\u7684\u3002</p><p><strong>\u518D\u8005\uFF0C\u5373\u4FBF\u771F\u662F\u597D\u597D\u7684\uFF0C\u4E5F\u4E0D\u4EE3\u8868\u8FD9\u79CD\u505A\u6CD5\u6709\u5FC5\u8981\u3002</strong></p><p>\u6BD4\u5982\u7ED9\u5B69\u5B50\u7528\u5B66\u6B65\u8F66\u4F1A\u589E\u52A0\u5B69\u5B50\u53D1\u751F\u610F\u5916\u4F24\u5BB3\u7684\u98CE\u9669\uFF0C\u4F46\u6709\u4EBA\u53EF\u80FD\u4F1A\u8BF4\uFF0C\u8C01\u5BB6\u7684\u5B69\u5B50\u7528\u4E86\u5B66\u6B65\u8F66\uFF0C\u4E0D\u4E5F\u6CA1\u6709\u4EC0\u4E48\u4E8B\u5417\uFF1F\u786E\u5B9E\u5927\u90E8\u5206\u4F7F\u7528\u5B66\u6B65\u8F66\u7684\u5B69\u5B50\u4E5F\u6CA1\u51FA\u4EC0\u4E48\u4E8B\uFF0C\u4F46\u4F7F\u7528\u5B66\u6B65\u8F66\u9664\u4E86\u53EF\u4EE5\u8BA9\u7236\u6BCD\u4EEC\u81EA\u5DF1\u5077\u5077\u61D2\uFF0C\u89E3\u653E\u4E00\u4E0B\u81EA\u5DF1\u7684\u53CC\u624B\uFF0C\u5E76\u4E0D\u80FD\u771F\u7684\u8BA9\u5B69\u5B50\u5B66\u4F1A\u8D70\u8DEF\uFF0C\u751A\u8005\u8FD8\u53EF\u80FD\u5F71\u54CD\u5B69\u5B50\u7684\u8FD0\u52A8\u53D1\u80B2\u3002</p><p>\u4E00\u4E2A\u505A\u6CD5\u5230\u5E95\u5408\u4E0D\u5408\u9002\uFF0C\u6070\u4E0D\u6070\u5F53\uFF0C\u4E0D\u662F\u9760\u81EA\u5DF1\u7684\u76F4\u89C9\uFF0C\u4E5F\u4E0D\u662F\u542C\u522B\u4EBA\u7684\u4F20\u8A00\uFF0C\u800C\u662F\u8981\u6839\u636E\u8BC1\u636E\u53BB\u6743\u8861\u5229\u5F0A\u3002\u5982\u679C\u81EA\u5DF1\u8FA8\u522B\u4E0D\u4E86\u8BC1\u636E\uFF0C\u7701\u4E8B\u7684\u65B9\u6CD5\u662F\u542C\u6743\u5A01\u533B\u5B66\u673A\u6784\u7684\u610F\u89C1\u3002</p><p>\u4EC5\u6839\u636E\u76F4\u89C9\u6216\u4F20\u8A00\u53BB\u5224\u65AD\uFF0C\u4F1A\u5BFC\u81F4\u8BE5\u8BB2\u7A76\u7684\u4E0D\u8BB2\u7A76\uFF0C\u4E0D\u8BE5\u8BB2\u7A76\u7684\u778E\u8BB2\u7A76\u3002\u4E8B\u5B9E\u4E0A\u4E5F\u662F\uFF0C\u559C\u6B22\u8BF4\u201C\u5C0F\u65F6\u5019\u6CA1\u90A3\u4E48\u591A\u8BB2\u7A76\uFF0C\u4F60\u4E3A\u4EC0\u4E48\u8FD8\u662F\u597D\u597D\u7684\uFF1F\u201D\u8FD9\u53E5\u8BDD\u7684\uFF0C\u540C\u6837\u4E5F\u559C\u6B22\u8BF4\uFF1A\u201D\u73B0\u5728\u4E0D\u6CE8\u610F\uFF0C\u7B49\u4F60\u8001\u4E86\u5C31\u77E5\u9053\u4E86\u3002\u201D</p><hr><p>\u9898\u56FE\u6765\u81EAPixabay\uFF0C\u672C\u6587\u9996\u53D1\u4E8E2016\u5E744\u6708</p><p>\u56DE\u590D**\u300C\u4FE1\u606F\u300D**\uFF0C\u9605\u8BFB\uFF1A\u5982\u4F55\u627E\u5230\u9760\u8C31\u7684\u80B2\u513F\u4FE1\u606F\uFF1F</p><p>\u56DE\u590D**\u300C\u8BF4\u6CD5\u300D**\uFF0C\u9605\u8BFB\uFF1A\u533B\u751F\u8BF4\u6CD5\u4E0D\u4E00\u6837\uFF0C\u8BE5\u542C\u8C01\u7684\uFF1F</p><p>\u70B9\u9605\u8BFB\u539F\u6587\uFF0C\u5411\u6211\u548C\u5176\u5B83\u533B\u751F\u54A8\u8BE2</p>',19),f={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651626689&idx=1&sn=6e0bf05f0414f55d4c369cfb8ef53a0e&chksm=8b5cb53bbc2b3c2de520ace000fdc0ededfee4a8aaff556a23cd473dd863cd4d0211d61b8b90&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},h=s("\u67E5\u770B\u539F\u6587");function l(b,m){const p=a("ExternalLinkIcon");return r(),t(c,null,[_,e("p",null,[e("a",f,[h,n(p)])])],64)}var u=d(i,[["render",l]]);export{u as default};
