import{r as a,o as r,c as t,a as p,b as n,F as o,d as s,e as c}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const i={},C=s('<h3 id="\u5B69\u5B50\u53D1\u70E7\u9700\u8981\u67E5\u8840\u5E38\u89C4\u5417" tabindex="-1"><a class="header-anchor" href="#\u5B69\u5B50\u53D1\u70E7\u9700\u8981\u67E5\u8840\u5E38\u89C4\u5417" aria-hidden="true">#</a> \u5B69\u5B50\u53D1\u70E7\u9700\u8981\u67E5\u8840\u5E38\u89C4\u5417\uFF1F</h3><p>\u5F88\u591A\u4EBA\u53EF\u80FD\u6709\u8FD9\u6837\u7684\u7ECF\u9A8C\uFF0C\u5B69\u5B50\u53D1\u70E7\u53BB\u533B\u9662\uFF0C\u533B\u751F\u95EE\u5B8C\u75C5\u60C5\u68C0\u67E5\u4E00\u4E0B\u8EAB\u4F53\uFF0C\u7136\u540E\u5C31\u53EB\u53BB\u67E5\u8840\u5E38\u89C4\uFF0C\u67E5\u5B8C\u8840\u540E\u518D\u53BB\u770B\u533B\u751F\uFF0C\u66F4\u6709\u751A\u8005\uFF0C\u6709\u7684\u533B\u9662\u53D1\u70E7\u770B\u6025\u8BCA\u7684\u75C5\u4EBA\u4E00\u5F8B\u7531\u62A4\u58EB\u5148\u5F00\u597D\u8840\u5E38\u89C4\uFF0C\u505A\u5B8C\u8840\u5E38\u89C4\u518D\u53BB\u770B\u533B\u751F\uFF0C\u533B\u751F\u770B\u4E86\u67E5\u8840\u7684\u62A5\u544A\u540E\u8BF4\u6000\u7591\u7EC6\u83CC\u611F\u67D3\u6216\u75C5\u6BD2\u611F\u67D3\u3002</p><p>\u8840\u5E38\u89C4\u6210\u4E3A\u4E86\u5B69\u5B50\u53D1\u70E7\u6700\u5E38\u505A\u7684\u68C0\u67E5\uFF0C\u4E5F\u662F\u5F88\u591A\u533B\u751F\u5224\u65AD\u662F\u5426\u662F\u7EC6\u83CC\u611F\u67D3\uFF0C\u662F\u5426\u7528\u6297\u83CC\u7D20\u7684\u4F9D\u636E\uFF0C\u8840\u5E38\u89C4\u80FD\u533A\u5206\u7EC6\u83CC\u6216\u75C5\u6BD2\u611F\u67D3\u5417\uFF1F\u53D1\u70E7\u9700\u8981\u67E5\u8840\u5E38\u89C4\u5417\uFF1F</p><p>\u5982\u679C\u8981\u7B80\u5355\u7684\u56DE\u7B54\u7B2C\u4E00\u4E2A\u95EE\u9898\uFF0C\u90A3\u5C31\u662F\u4E0D\u80FD\u3002\u5176\u5B9E\u4E0D\u5149\u8840\u5E38\u89C4\u533A\u5206\u4E0D\u4E86\uFF0C\u76EE\u524D\u4E5F\u6CA1\u6709\u4EFB\u4F55\u4E00\u4E2A\u68C0\u67E5\u80FD\u7EDD\u5BF9\u533A\u5206\u7EC6\u83CC\u6216\u75C5\u6BD2\u611F\u67D3\uFF0C\u5305\u62EC\u8840\u57F9\u517B\u3002</p><p>\u6709\u4EBA\u53EF\u80FD\u4E0D\u8BA4\u540C\uFF0C\u96BE\u9053\u62BD\u8840\u57F9\u517B\u51FA\u4E86\u7EC6\u83CC\u8FD8\u4E0D\u80FD\u8BC1\u660E\u6709\u7EC6\u83CC\u611F\u67D3\u5417\uFF1F\u662F\u7684\uFF0C\u867D\u7136\u673A\u4F1A\u6BD4\u8F83\u5C0F\uFF0C\u4F46\u62BD\u8840\u64CD\u4F5C\u3001\u5B58\u50A8\u65F6\u8840\u6837\u4E5F\u53EF\u80FD\u88AB\u4F53\u8868\u6216\u4F53\u5916\u7684\u7EC6\u83CC\u6C61\u67D3\uFF0C\u5373\u4FBF\u6CA1\u6709\u6C61\u67D3\uFF0C\u8840\u91CC\u57F9\u517B\u51FA\u7EC6\u83CC\u4E5F\u5E76\u4E0D\u610F\u5473\u7740\u4E00\u5B9A\u4F1A\u53D1\u751F\u4E25\u91CD\u7684\u611F\u67D3\uFF0C\u6BD4\u5982\u62D4\u7259\u540E\u77ED\u671F\u5185\u8840\u57F9\u517B\u9633\u6027\u5F88\u5E38\u89C1\uFF3B1\uFF3D\uFF0C\u4F46\u51FA\u73B0\u5168\u8EAB\u611F\u67D3\u75C7\u72B6\u7684\u5E76\u4E0D\u591A\u3002</p><p>\u540C\u6837\uFF0C\u62BD\u8840\u53BB\u57F9\u517B\uFF0C\u7ED3\u679C\u6CA1\u6709\u957F\u51FA\u7EC6\u83CC\uFF0C\u540C\u6837\u4E0D\u80FD\u5C31\u8BF4\u6CA1\u6709\u7EC6\u83CC\u611F\u67D3\uFF0C\u56E0\u4E3A\u62BD\u8840\u7684\u65F6\u673A\uFF0C\u8840\u6837\u7684\u5B58\u653E\u548C\u57F9\u517B\u65B9\u6CD5\u7B49\u90FD\u53EF\u80FD\u5F71\u54CD\u7ED3\u679C\u3002</p><p>\u6240\u4EE5\uFF0C\u5BF9\u4E8E\u5224\u65AD\u662F\u5426\u5B58\u5728\u7EC6\u83CC\u611F\u67D3\uFF0C\u5373\u4FBF\u662F\u8840\u57F9\u517B\u8FD9\u6837\u7684\u6240\u8C13\u201C\u91D1\u6807\u51C6\u201D\uFF0C\u4F9D\u7136\u5B58\u5728\u5047\u9633\u6027\u548C\u5047\u9634\u6027\u7684\u95EE\u9898\uFF0C\u4F46\u4E0D\u80FD\u636E\u6B64\u5426\u8BA4\u8840\u57F9\u517B\u5728\u5224\u65AD\u7EC6\u83CC\u611F\u67D3\u4E0A\u7684\u610F\u4E49\u3002</p><p>\u5224\u65AD\u4E00\u9879\u68C0\u67E5\u662F\u5426\u6709\u610F\u4E49\uFF0C\u5E94\u8BE5\u4ECE\u8FD9\u9879\u68C0\u67E5\u7684\u654F\u611F\u6027\u548C\u7279\u5F02\u6027\u6765\u5206\u6790\uFF0C\u5224\u65AD\u7ED3\u679C\u66F4\u591A\u7684\u662F\u6982\u7387\u5927\u5C0F\u7684\u95EE\u9898\uFF0C\u800C\u4E0D\u662F\u6709\u6216\u8005\u65E0\u7684\u95EE\u9898\u3002\u90A3\u8840\u5E38\u89C4\u7528\u4E8E\u5224\u65AD\u53D1\u70E7\u75C5\u4EBA\u7EC6\u83CC\u611F\u67D3\uFF0C\u7279\u5F02\u6027\u548C\u654F\u611F\u6027\u5982\u4F55\uFF1F</p><p>\u5BF9\u4E8E\u6709\u5C40\u9650\u6027\u7684\u611F\u67D3\u7076\uFF0C\u6BD4\u5982\u76AE\u80A4\u4E0A\u957F\u4E86\u5927\u7684\u7596\u80BF\uFF0C\u6216\u8005\u660E\u663E\u7EA2\u80BF\u6E17\u6DB2\u4F24\u53E3\u7684\uFF0C\u8FD9\u79CD\u660E\u663E\u662F\u7EC6\u83CC\u611F\u67D3\u624D\u6709\u7684\u75C7\u72B6\uFF0C\u81EA\u7136\u4E0D\u9700\u8981\u518D\u9760\u8840\u5E38\u89C4\u6765\u5224\u65AD\u662F\u7EC6\u83CC\u8FD8\u662F\u75C5\u6BD2\u611F\u67D3\u3002\u540C\u6837\uFF0C\u5982\u679C\u4F60\u6839\u636E\u4E34\u5E8A\u75C7\u72B6\u5DF2\u7ECF\u8BCA\u65AD\u4E00\u4E2A\u5B69\u5B50\u4E3A\u666E\u901A\u611F\u5192\uFF0C\u660E\u786E\u4E3A\u75C5\u6BD2\u611F\u67D3\uFF0C\u81EA\u7136\u4E5F\u4E0D\u9700\u8981\u5E38\u89C4\u505A\u8840\u5E38\u89C4\u6765\u9274\u522B\u3002</p><p>\u5BF9\u90A3\u4E9B\u53EA\u6709\u53D1\u70E7\u800C\u6CA1\u6709\u5176\u5B83\u75C7\u72B6\u7684\u5B69\u5B50\uFF0C\u5224\u65AD\u662F\u75C5\u6BD2\u611F\u67D3\u8FD8\u662F\u4E00\u4E9B\u4E25\u91CD\u7684\u7EC6\u83CC\u611F\u67D3\u5F15\u8D77\uFF0C\u624D\u66F4\u6709\u610F\u4E49\u3002\u8840\u5E38\u89C4\u7528\u4E8E\u5224\u65AD\u611F\u67D3\u6027\u8D28\uFF0C\u4E3B\u8981\u662F\u770B\u767D\u7EC6\u80DE\u8BA1\u6570\uFF08WBC)\u548C\u4E2D\u6027\u7C92\u7EC6\u80DE\u8BA1\u6570\uFF0C\u5C24\u5176\u662F\u524D\u8005\u3002</p><p>\u80FD\u5F15\u8D77\u8FD9\u4E24\u9879\u6307\u6807\u5347\u9AD8\u7684\u539F\u56E0\u6709\u5F88\u591A\uFF0C\u9664\u4E86\u4E25\u91CD\u7684\u7EC6\u83CC\u611F\u67D3\u4EE5\u5916\uFF0C\u75C5\u6BD2\u611F\u67D3\uFF0C\u975E\u611F\u67D3\u6027\u7684\u708E\u75C7\u6BD4\u5982\u7C7B\u98CE\u6E7F\u6027\u5173\u8282\u708E\u3001\u8840\u7BA1\u708E\uFF0C\u8EAB\u4F53\u6216\u7CBE\u795E\u521B\u4F24\uFF0C\u4E00\u4E9B\u80BF\u7624\u3001\u836F\u7269\uFF0C\u751A\u8005\u8FD0\u52A8\u90FD\u53EF\u80FD\u5BFC\u81F4\u8FD9\u4E24\u9879\u6307\u6807\u5347\u9AD8\u3002</p><p>\u767D\u7EC6\u80DE\u5347\u9AD8\u5BF9\u4E8E\u4E25\u91CD\u7684\u7EC6\u83CC\u611F\u67D3\u5224\u65AD\u7684\u610F\u4E49\uFF0C\u7ED3\u8BBA\u4E5F\u662F\u4E94\u82B1\u516B\u95E8\uFF0C\u6709\u7684\u8BA4\u4E3A\u548CC\u53CD\u5E94\u86CB\u767D\uFF08CRP\uFF09\u548C\u964D\u9499\u7D20\u539F\uFF08PCT\uFF09\u5DEE\u4E0D\u591A\uFF0C\u800C\u4E14\u8FD9\u4E9B\u6307\u6807\u90FD\u6BD4\u5355\u7EAF\u7684\u4E34\u5E8A\u8BC4\u4F30\u66F4\u4F18\uFF3B2\uFF3D\u3002\u4E5F\u6709\u7814\u7A76\u8BA4\u4E3A\u7528WBC\u6765\u8BC6\u522B\u51FA\u4E25\u91CD\u7EC6\u83CC\u611F\u67D3\u6709\u4E9B\u4EF7\u503C\uFF0C\u4F46\u4E0D\u5982CRP\u548CPCT\u654F\u611F\uFF0C\u800C\u7528WBC\u6765\u6392\u9664\u4E25\u91CD\u7EC6\u83CC\u611F\u67D3\u6CA1\u6709\u4EF7\u503C\uFF3B3\uFF3D\u3002\u4E5F\u6709\u7814\u7A76\u8BA4\u4E3A\u4E2D\u6027\u7C92\u7EC6\u80DE\u8BA1\u6570\u5BF9\u4E8E\u83CC\u8840\u75C7\u7684\u9884\u6D4B\u610F\u4E49\u6700\u5927\uFF3B4\uFF3D\u3002\u4E5F\u6709\u8BA4\u4E3AWBC\u4E0D\u51C6\u786E\uFF3B5\uFF3D\u3002</p><p>\u8FD9\u4E9B\u7814\u7A76\u9488\u5BF9\u7684\u5E74\u9F84\u4E0D\u4E00\u6837\uFF0C\u7814\u7A76\u7684\u65F6\u95F4\u3001\u6837\u672C\u6765\u6E90\u3001\u6570\u91CF\u4E5F\u4E0D\u4E00\u6837\uFF0C\u603B\u7684\u8BF4\u6765\uFF0C\u65E9\u671F\u7684\u7814\u7A76\u8BA4\u4E3AWBC\u7528\u4E8E\u5224\u65AD\u7EC6\u83CC\u611F\u67D3\u6BD4\u5355\u7EAF\u7684\u4E34\u5E8A\u8BC4\u4F30\u66F4\u6709\u4F18\uFF0C\u4F46\u7279\u5F02\u6027\u5DEE\uFF0C\u654F\u611F\u6027\u4E0D\u5982CRP\u548CPCT\u3002\u5728\u513F\u7AE5\u666E\u904D\u63A5\u79CD\u4E86\u6D41\u611F\u55DC\u8840\u6746\u83CC\u548C\u80BA\u708E\u7403\u83CC\u75AB\u82D7\u7684\u56FD\u5BB6\uFF0C\u75AB\u82D7\u7684\u63A5\u79CD\u8BA9\u513F\u7AE5\u53D1\u70ED\u7684\u75BE\u75C5\u8C31\u53D1\u751F\u4E86\u660E\u663E\u7684\u6539\u53D8\uFF0C\u7EC6\u83CC\u611F\u67D3\u8D8A\u6765\u8D8A\u5C11\uFF0C\u75C5\u6BD2\u611F\u67D3\u8D8A\u6765\u8D8A\u591A\uFF0CWBC\u8FD9\u79CD\u672C\u6765\u654F\u611F\u6027\u5C31\u66F4\u4F4E\u7684\u6307\u6807\u53C2\u8003\u610F\u4E49\u5C31\u66F4\u5C0F\u4E86\u3002\u53BB\u5E74\u6FB3\u5927\u5229\u4E9A\u7684\u4E00\u9879\u5927\u6837\u672C\u7814\u7A76\u8BA4\u4E3AWBC\u7528\u4E8E\u7B5B\u67E55\u5C81\u4EE5\u4E0B\u513F\u7AE5\u53D1\u70ED\u4E25\u91CD\u7EC6\u83CC\u611F\u67D3\u7684\u51C6\u786E\u6027\u4E0D\u8DB3\uFF3B5\uFF3D\u3002\u4F46\u8FD9\u4E2A\u7ED3\u8BBA\u8FD8\u6709\u5F85\u8FDB\u4E00\u6B65\u7684\u7814\u7A76\u9A8C\u8BC1\uFF0C\u76EE\u524D\u8FD8\u6CA1\u6709\u54EA\u4EFD\u6743\u5A01\u6307\u5357\u5B8C\u5168\u5426\u8BA4WBC\u5BF9\u4E25\u91CD\u7EC6\u83CC\u611F\u67D3\u7B5B\u67E5\u7684\u610F\u4E49\u3002</p><p>\u5BF9\u4E8E3\u4E2A\u6708\u4EE5\u4E0B\u7684\u5B69\u5B50\uFF0C\u5C24\u5176\u662F0-28\u5929\u7684\u65B0\u751F\u513F\uFF0C\u7EC6\u83CC\u5BFC\u81F4\u8FD9\u4E2A\u5E74\u9F84\u6BB5\u7684\u5B69\u5B50\u53D1\u70E7\u7684\u6BD4\u4F8B\u66F4\u9AD8\uFF0CWBC\u7528\u4E8E\u8FD9\u4E2A\u5E74\u9F84\u6BB5\u7684\u5B69\u5B50\u7684\u53D1\u70E7\u5224\u65AD\u7684\u53C2\u8003\u610F\u4E49\u4E5F\u5C31\u66F4\u5927\uFF0C\u6240\u4EE5\uFF0C\u5305\u62EC\u6CE2\u58EB\u987F\u6807\u51C6\u3001\u7F57\u5207\u65AF\u7279\u6807\u51C6\u3001\u5339\u5179\u5821\u6307\u5357\u3001\u8D39\u57CE\u534F\u8BAE\u7B49\u56FD\u5916\u7684\u6307\u5357\uFF0C\u76EE\u524D\u4ECD\u7136\u628AWBC\u8BA1\u6570\u5F53\u4F5C\u5224\u65AD1-3\u6708\u53D1\u70ED\u5B69\u5B50\u4E25\u91CD\u7EC6\u83CC\u611F\u67D3\u98CE\u9669\u7684\u4E00\u4E2A\u6307\u6807\u3002</p><p>\uFF08\u6765\u6E90\uFF1ANelson Textbook of Peadiatrics edition 20\uFF09</p><p>\u4E0B\u56FE\u662F\u7F8E\u56FD\u5BB6\u5EAD\u533B\u751F\u6742\u5FD72013\u5E74\u4E00\u7BC7\u7EFC\u8FF0\u91CC3\u5C81\u5185\u513F\u7AE5\u53D1\u70ED\u7684\u5904\u7F6E\u7684\u6D41\u7A0B\u56FE\uFF0C\u4ECE\u4E2D\u4E5F\u53EF\u4EE5\u770B\u51FA\uFF1A\u4E0D\u523029\u5929\u7684\u53D1\u70ED\u65B0\u751F\u513F\u662F\u6536\u5165\u4F4F\u9662\uFF0C\u5E76\u505A\u8840\u5E38\u89C4\u57F9\u517B\u4E4B\u7C7B\u7684\u68C0\u67E5\uFF0C\u975E\u65B0\u751F\u513F\u6709\u4E25\u91CD\u75BE\u75C5\u8868\u73B0\u7684\uFF0C\u540C\u6837\u662F\u6536\u5165\u4F4F\u9662\uFF0C\u540C\u6837\u4E5F\u8981\u505A\u8840\u5E38\u89C4\u3002\u6CA1\u6709\u4E25\u91CD\u75BE\u75C5\u8868\u73B0\u7684\uFF0C1-3\u4E2A\u6708\u7684\u8FD8\u662F\u8981\u505A\u8840\u5E38\u89C4\u7B5B\u67E5\uFF0C\u4F463\u4E2A\u6708\u4EE5\u4E0A\u7684\u4E0D\u5E38\u89C4\u63A8\u8350\u3002</p><p>\uFF08\u6765\u6E90\uFF1AAm Fam Physician. 2013 Feb 15;87(4):254-60\uFF09</p><p>\u800C\u56FD\u5185\u7684\u60C5\u51B5\u8FD8\u548C\u7F8E\u56FD\u8FD9\u4E9B\u53D1\u8FBE\u56FD\u5BB6\u8FD8\u4E0D\u4E00\u6837\uFF0C\u56FD\u5185\u6D41\u611F\u55DC\u8840\u6746\u83CC\u548C\u80BA\u708E\u7403\u83CC\u75AB\u82D7\u90FD\u662F\u81EA\u8D39\u75AB\u82D7\uFF0C\u63A5\u79CD\u5E76\u4E0D\u666E\u904D\uFF0C\u8FD9\u4E24\u79CD\u7EC6\u83CC\u4ECD\u7136\u662F\u513F\u7AE5\u53D1\u70ED\u7684\u91CD\u8981\u75C5\u539F\u4F53\uFF0C\u7EC6\u83CC\u5BFC\u81F4\u7684\u513F\u7AE5\u53D1\u70ED\u6BD4\u4F8B\u4ECD\u9AD8\u4E8E\u53D1\u8FBE\u56FD\u5BB6\uFF0C\u8840\u5E38\u89C4\u9274\u522B\u7EC6\u83CC\u611F\u67D3\u7684\u53C2\u8003\u610F\u4E49\u80AF\u5B9A\u4E5F\u66F4\u5927\u3002</p><p>\u867D\u7136\u654F\u611F\u6027\u548C\u7279\u5F02\u6027\u4F4E\u4E8EPCT\u548CCRP\uFF0C\u4F46\u8840\u5E38\u89C4\u5FEB\u901F\u6613\u884C\uFF0C\u4EF7\u683C\u4F4E\u5EC9\uFF0C\u51E0\u4E4E\u6240\u6709\u7684\u533B\u7597\u673A\u6784\u90FD\u53EF\u4EE5\u5F00\u5C55\u3002\u800C\u4E14\u68C0\u67E5\u7684\u7279\u5F02\u6027\u548C\u654F\u611F\u6027\u4E5F\u53EF\u4EE5\u968F\u5224\u5B9A\u6807\u51C6\u7684\u6539\u53D8\u800C\u6539\u53D8\uFF0C\u6BD4\u5982\u6709\u7814\u7A76\u53D1\u73B03-36\u6708\u5B69\u5B50\uFF0CWBC&gt;25,000/mm3 \uFF0C\u670939%\u7684\u98CE\u9669\u4E3A\u4E25\u91CD\u7EC6\u83CC\u611F\u67D3\uFF3B6\uFF3D\u3002</p><p>\u53D1\u70ED\u662F\u513F\u7AE5\u6700\u5E38\u89C1\u7684\u75C7\u72B6\uFF0C\u4F46\u8981\u786E\u5B9A\u53D1\u70ED\u7684\u5177\u4F53\u539F\u56E0\u5E76\u4E0D\u5BB9\u6613\uFF0C\u5C24\u5176\u662F\u5728\u65E9\u671F\u53BB\u533A\u522B\u662F\u7EC6\u83CC\u8FD8\u662F\u75C5\u6BD2\u611F\u67D3\uFF0C\u9700\u8981\u7ED3\u5408\u5B69\u5B50\u7684\u5E74\u9F84\u3001\u63A5\u79CD\u53F2\u3001\u75C5\u53F2\u3001\u67E5\u4F53\u4EE5\u53CA\u68C0\u67E5\u5316\u9A8C\u6765\u7EFC\u5408\u5224\u65AD\u5206\u6790\uFF0C\u5F88\u591A\u65F6\u5019\u8FD8\u662F\u9760\u7ECF\u9A8C\u6765\u5224\u65AD\uFF0C\u5355\u7EAF\u4F9D\u9760\u4E00\u4E2A\u8840\u5E38\u89C4\u6765\u660E\u786E\u533A\u5206\u662F\u4E0D\u53EF\u80FD\u7684\uFF0C\u4F46\u4E0D\u80FD\u5C31\u6B64\u5B8C\u5168\u5426\u8BA4\u8840\u5E38\u89C4\u8BCA\u65AD\u610F\u4E49\u3002\u5BF9\u4E8E\u4E00\u4E9B\u6709\u53D1\u70ED\u75C7\u72B6\u7684\u7279\u5B9A\u7684\u75BE\u75C5\uFF0C\u6BD4\u5982\u80BA\u708E\uFF0CWBC\u4E5F\u662F\u533A\u522B\u7EC6\u83CC\u6027\u80BA\u708E\u548C\u75C5\u6BD2\u6027\u80BA\u708E\u7684\u4E00\u4E2A\u6709\u7528\u7684\u6307\u6807\u3002</p><p>\u53D1\u70E7\u662F\u5426\u9700\u8981\u67E5\u8840\u5E38\u89C4\uFF0C\u6216\u8FDB\u4E00\u6B65\u505A\u5176\u5B83\u68C0\u67E5\uFF0C\u9700\u8981\u6839\u636E\u5B69\u5B50\u7684\u5177\u4F53\u60C5\u51B5\u6765\u7EFC\u5408\u6743\u8861\uFF0C\u800C\u4E0D\u662F\u7B80\u5355\u7684\u5426\u8BA4\uFF0C\u7ED3\u679C\u5206\u6790\u4E5F\u8981\u7ED3\u5408\u5176\u5B83\u60C5\u51B5\u6765\u7EFC\u5408\u5224\u65AD\u3002</p><p>\u4F46\u53E6\u4E00\u4E2A\u73B0\u5B9E\u662F\uFF0C\u56FD\u5185\u8840\u5E38\u89C4\u786E\u5B9E\u505A\u5F97\u5F88\u6CDB\u6EE5\uFF0C\u6BD4\u5982\u524D\u6587\u8BF4\u7684\u6240\u6709\u6025\u8BCA\u53D1\u70ED\u75C5\u4EBA\u5148\u67E5\u8840\u5E38\u89C4\u518D\u770B\u75C5\uFF0C\u4E5F\u786E\u5B9E\u6709\u5F88\u591A\u533B\u751F\u5C31\u662F\u5355\u7EAF\u7684\u770B\u767D\u7EC6\u80DE\u548C\u4E2D\u5FC3\u7C92\u7EC6\u80DE\u8BA1\u6570\u6765\u51B3\u5B9A\u662F\u5426\u7528\u6297\u83CC\u7D20\uFF0C\u54EA\u6015\u5F88\u660E\u786E\u7684\u4E00\u4E2A\u75B1\u75B9\u6027\u54BD\u5CE1\u708E\uFF0C\u4E5F\u53BB\u5316\u9A8C\u4E2A\u8840\u5E38\u89C4\uFF0C\u4E00\u770B\u5230\u767D\u7EC6\u80DE\u9AD8\uFF0C\u6216\u8005\u4E2D\u5FC3\u7C92\u7EC6\u80DE\u6BD4\u4F8B\u9AD8\uFF0C\u4E5F\u7ED9\u5F00\u4E0A\u4E86\u6297\u83CC\u7D20\u3002\u6CA1\u6709\u53D1\u70E7\u7B49\u5176\u5B83\u75C7\u72B6\uFF0C\u75C5\u4EBA\u5355\u7EAF\u767D\u7EC6\u80DE\u6570\u504F\u9AD8\u5C31\u88AB\u4E00\u76F4\u7528\u7740\u6297\u83CC\u7D20\u5F88\u5E38\u89C1\u3002</p><p>\u5BF9\u53D1\u70E7\u7684\u5B69\u5B50\u6EE5\u505A\u8840\u5E38\u89C4\uFF0C\u9519\u8BEF\u7684\u89E3\u8BFB\u8840\u5E38\u89C4\u6210\u4E3A\u6EE5\u7528\u6297\u751F\u7D20\u3001\u8FC7\u5EA6\u6CBB\u7597\u7684\u4E00\u4E2A\u91CD\u8981\u539F\u56E0\uFF0C\u89E3\u51B3\u8FD9\u4E2A\u95EE\u9898\u9760\u7684\u662F\u63D0\u9AD8\u533B\u751F\u5BF9\u8FD9\u4E2A\u68C0\u67E5\u7684\u8BA4\u8BC6\uFF0C\u800C\u4E0D\u662F\u7B80\u5355\u7684\u5426\u8BA4\u8FD9\u4E2A\u68C0\u67E5\u7684\u610F\u4E49\u3002</p><p><strong>\u53C2\u8003\u8D44\u6599\uFF1A</strong></p><p>\uFF3B1\uFF3DOral Dis. 2007 Jan;13(1):56-62</p><p>\uFF3B2\uFF3DArch Dis Child. 2011 May;96(5):440-6</p><p>\uFF3B3\uFF3DBMJ. 2011 Jun 8;342</p><p>\uFF3B4\uFF3DEmerg Med Australas. 2007 Aug;19(4):353-8</p><p>\uFF3B5\uFF3DArch Dis Child 2014;99:493-499</p><p>\uFF3B6\uFF3DArch Dis Child. 2010 Mar;95(3):209-12</p><p>\u76F8\u5173\u9605\u8BFB\uFF0C\u5173\u6CE8\u8FD9\u4E2A\u8D26\u53F7\u540E\u56DE\u590D\u6570\u5B57\u5173\u952E\u8BCD\u53EF\u67E5\u770B\u6587\u7AE0</p><p><strong>308</strong> \u6297\u751F\u7D20\u548C\u4E0A\u611F\uFF0C\u5BB6\u957F\u5E94\u8BE5\u77E5\u9053\u4EC0\u4E48\uFF1F</p><p><strong>402</strong> \u513F\u7AE5\u4E0A\u611F\u5408\u7406\u4F7F\u7528\u6297\u751F\u7D20\u539F\u5219</p><p><strong>48</strong> \u5982\u4F55\u5E94\u5BF9\u5B69\u5B50\u53D1\u70E7\uFF1F</p>',34),h={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=400785333&idx=1&sn=3f941fbb1c2619dcd16f1a318e7381a1&chksm=02b3564f35c4df59cfcf98f0f706293a8adf2adaca31541a11ec7ad9996ba198cfdc51709444&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},l=c("\u67E5\u770B\u539F\u6587");function _(f,m){const e=a("ExternalLinkIcon");return r(),t(o,null,[C,p("p",null,[p("a",h,[l,n(e)])])],64)}var x=d(i,[["render",_]]);export{x as default};
