import{r,o as t,c as n,a as p,b as o,F as s,d as a,e as c}from"./app.9aaa2256.js";import{_ as i}from"./plugin-vue_export-helper.21dcd24c.js";const d={},g=a('<h3 id="\u5B69\u5B50\u5F00\u5B66\u4E86-\u6CA1\u6709\u5206\u79BB\u7126\u8651\u6B63\u5E38\u5417" tabindex="-1"><a class="header-anchor" href="#\u5B69\u5B50\u5F00\u5B66\u4E86-\u6CA1\u6709\u5206\u79BB\u7126\u8651\u6B63\u5E38\u5417" aria-hidden="true">#</a> \u5B69\u5B50\u5F00\u5B66\u4E86\uFF0C\u6CA1\u6709\u5206\u79BB\u7126\u8651\u6B63\u5E38\u5417\uFF1F</h3><p>\u5E7C\u513F\u56ED\u5F00\u5B66\u4E86\uFF0C\u4E0D\u5C11\u5B69\u5B50\u7B2C\u4E00\u6B21\u79BB\u5F00\u7236\u6BCD\u8E0F\u5165\u65B0\u7684\u751F\u6D3B\uFF0C\u5BB6\u957F\u662F\u4E0D\u662F\u65E2\u671F\u5F85\u53C8\u6EE1\u6000\u7126\u8651\u5462\uFF1F</p><p>\u8BB0\u5F97\u5F53\u5E74\u628A\u6211\u5BB6\u201C\u795E\u517D\u201D\u9001\u8FDB\u5C0F\u73ED\u65F6\uFF0C\u90A3\u53EF\u8C13\u662F\u4E00\u573A\u201C\u6DF7\u6218\u201D\uFF1A\u4E00\u4E2A\u73ED20\u591A\u4E2A\u5C0F\u670B\u53CB\uFF0C\u51E0\u4E4E\u90FD\u54C7\u54C7\u5927\u54ED\u3001\u4E0D\u80FD\u81EA\u5DF2\u3002</p><p>\u8001\u5E08\u4EEC\u4E00\u4EBA\u5E26\u51E0\u4E2A\uFF0C\u987E\u5F97\u4E0A\u8FD9\u4E2A\u987E\u4E0D\u4E0A\u90A3\u4E2A\uFF1B\u5BB6\u957F\u4EEC\u90FD\u4EE5\u5404\u79CD\u59FF\u52BF\u201C\u9690\u853D\u201D\u5728\u7A97\u53F0\u548C\u95E8\u8FB9\uFF0C\u5077\u5077\u64E6\u7740\u773C\u6CEA\u600E\u4E48\u4E5F\u632A\u4E0D\u52A8\u817F\u8D70\u5F00\uFF1B\u5C11\u6570\u51E0\u5BF9\u53EF\u4EE5\u5B8C\u7F8E\u907F\u5F00\u4EE5\u4E0A\u573A\u666F\u7684\uFF0C\u5219\u662F\u90A3\u4E9B\u4E4B\u524D\u5DF2\u7ECF\u4E0A\u8FC7\u5C0F\u5C0F\u73ED\u7684\u5B69\u5B50\u548C\u7238\u5988\u4EEC\u3002</p><p>\u5176\u5B9E\uFF0C\u5BF9\u4E8E\u5B69\u5B50\u6765\u8BF4\uFF0C\u5165\u56ED\u4E0D\u4EC5\u4EC5\u662F\u5E74\u9F84\u4E0A\u8981\u6EE1\u8DB3\u8981\u6C42\uFF0C\u8FD8\u9700\u8981\u4ED6\u4EEC\u7684\u793E\u4EA4\u80FD\u529B\u3001\u9047\u5230\u56F0\u96BE\u7684\u8C03\u63A7\u80FD\u529B\u3001\u4E00\u5B9A\u751F\u6D3B\u81EA\u7406\u80FD\u529B\u7B49\u90FD\u51C6\u5907\u597D\u3002\u7AD9\u5728\u5B69\u5B50\u7684\u89D2\u5EA6\uFF0C\u5165\u56ED\u662F\u4E00\u4E2A\u6311\u6218\uFF0C\u800C\u8FD9\u4E2A\u6311\u6218\u7684\u7B2C\u4E00\u5173\u5C31\u662F\u8981\u9002\u5E94\u4E0E\u7236\u6BCD\u7684\u5206\u5F00\u3002</p><p><strong>01</strong></p><p><strong>\u5728\u5B69\u5B50\u773C\u4E2D\u201C\u5206\u5F00\u201D\u4EE3\u8868\u4EC0\u4E48\uFF1F</strong></p><p>\u6709\u7684\u5B69\u5B50\u5728\u4E0E\u4E3B\u8981\u517B\u80B2\u4EBA\uFF0C\u6BD4\u5982\u5988\u5988\u5206\u5F00\u65F6\u4F1A\u611F\u5230\u7126\u8651\u3001\u4E0D\u5B89\u3001\u6216\u8005\u54ED\u95F9\u7B49\uFF0C\u8FD9\u4E9B\u8868\u73B0\u79F0\u4E3A\u201C\u5206\u79BB\u7126\u8651\u201D\u3002</p><p>\u8FD9\u662F\u56E0\u4E3A\u5B69\u5B50\u548C\u517B\u80B2\u4EBA\u4E4B\u95F4\u5EFA\u7ACB\u4E86\u4EB2\u5BC6\u7684\u60C5\u611F\u8054\u7ED3\uFF0C\u5F53\u7ECF\u5386\u5206\u79BB\u65F6\uFF0C\u5C31\u4F1A\u4EA7\u751F\u4F24\u5FC3\u3001\u96BE\u8FC7\u548C\u75DB\u82E6\u7684\u611F\u53D7\u3002</p><p>\u5206\u79BB\u7126\u8651\u901A\u5E38\u6700\u65E9\u5728\u5B69\u5B508-10\u4E2A\u6708\u5DE6\u53F3\u5F00\u59CB\u51FA\u73B0\uFF0C12-24\u4E2A\u6708\u662F\u5206\u79BB\u7126\u8651\u7684\u9AD8\u5CF0\u9636\u6BB5\uFF1B\u5728\u4E0D\u540C\u7684\u5E74\u9F84\u6BB5\u5B69\u5B50\u7684\u5206\u79BB\u7126\u8651\u4F1A\u6709\u4E0D\u540C\u7684\u8868\u73B0\uFF1A8-14\u4E2A\u6708\u65F6\uFF0C\u4E0D\u613F\u8BA9\u5988\u5988\u79BB\u5F00\u81EA\u5DF1\u7684\u89C6\u7EBF\uFF0C\u5F53\u5988\u5988\u4E0A\u5395\u6240\u65F6\u5B69\u5B50\u53EF\u80FD\u4F1A\u5728\u95E8\u5916\u5927\u54ED\u3001\u62CD\u95E8\uFF0C\u4E0A\u73ED\u65F6\u62B1\u5927\u817F\u4E0D\u8BA9\u8D70\uFF1B\u4E0A\u5E7C\u513F\u56ED\u5728\u4E0E\u7238\u5988\u5206\u79BB\u65F6\u54ED\u95F9\u3001\u4E0D\u5B89\u7B49\u3002</p><p><strong>02</strong></p><p><strong>\u8FD9\u4E9B\u60C5\u7EEA\u4F1A\u5F71\u54CD\u5B69\u5B50\u5417\uFF1F</strong></p><p>\u76EE\u524D\u5927\u90E8\u5206\u89C2\u70B9\u8BA4\u4E3A\uFF0C\u5B69\u5B50\u51FA\u73B0\u9002\u5EA6\u7684\u5206\u79BB\u7126\u8651\u662F\u6B63\u5E38\u7684\uFF0C\u662F\u5B69\u5B50\u6210\u957F\u8FC7\u7A0B\u4E2D\u4E00\u4E2A\u6B63\u5E38\u7684\u53D1\u5C55\u9636\u6BB5\uFF0C\u4E5F\u662F\u513F\u7AE5\u7684\u4E00\u79CD\u81EA\u6211\u4FDD\u62A4\u65B9\u5F0F\u3002</p><p>\u9002\u5EA6\u7684\u5206\u79BB\u7126\u8651\u5E76\u4E0D\u4F1A\u7ED9\u5B69\u5B50\u5E26\u6765\u592A\u591A\u7684\u56F0\u6270\u548C\u538B\u529B\uFF0C\u7238\u7238\u5988\u5988\u9700\u8981\u7406\u89E3\u5B69\u5B50\uFF0C\u4E0D\u8981\u8FC7\u4E8E\u62C5\u5FC3\uFF0C\u800C\u662F\u8010\u5FC3\u7684\u966A\u4F34\u548C\u5F15\u5BFC\u5B69\u5B50\u9002\u5E94\u548C\u8FC7\u6E21\u3002</p><p>\u4F46\u4E5F\u6709\u4E9B\u60C5\u51B5\uFF0C\u8FC7\u5EA6\u7684\u5206\u79BB\u7126\u8651\u53EF\u80FD\u7ED9\u5B69\u5B50\u5E26\u6765\u56F0\u6270\uFF0C\u6BD4\u5982\u5F3A\u70C8\u5730\u62D2\u7EDD\u548C\u7238\u7238\u5988\u5988\u5206\u5F00\uFF0C\u4E00\u65E6\u5FC5\u987B\u8981\u5206\u5F00\u5219\u53CD\u590D\u51FA\u73B0\u7126\u8651\u3001\u5BB3\u6015\u3001\u54ED\u95F9\uFF0C\u8FD8\u4F1A\u4F34\u6709\u6076\u5FC3\u3001\u5455\u5410\u7B49\u8EAB\u4F53\u4E0D\u9002\uFF0C\u4E25\u91CD\u8005\u53EF\u80FD\u51FA\u73B0\u62D2\u7EDD\u4E0A\u5E7C\u513F\u56ED\u7684\u8868\u73B0\u3002\u8FD9\u5219\u9700\u8981\u5F15\u8D77\u91CD\u89C6\uFF0C\u53CA\u65F6\u8C03\u6574\u3002</p><p><strong>03</strong></p><p><strong>\u6CA1\u6709\u5206\u79BB\u7126\u8651\u7684\u5B69\u5B50\u6B63\u5E38\u5417\uFF1F</strong></p><p>\u9996\u5148\u8981\u77E5\u9053\uFF0C\u6211\u4EEC\u6BCF\u4E2A\u5B69\u5B50\u90FD\u4E0D\u4E00\u6837\uFF0C\u800C\u4E14\u6BCF\u4F4D\u517B\u80B2\u4EBA\u7684\u6027\u683C\u3001\u5B69\u5B50\u4E0E\u7236\u6BCD\u7684\u76F8\u5904\u6A21\u5F0F\u3001\u7236\u6BCD\u4E0E\u5B69\u5B50\u5206\u79BB\u65F6\u7684\u5904\u7406\u65B9\u6CD5\u7B49\u7B49\u4E5F\u5404\u6709\u4E0D\u540C\uFF0C\u56E0\u6B64\u6BCF\u4E2A\u5B69\u5B50\u6240\u4EA7\u751F\u7684\u5206\u79BB\u7126\u8651\u4E5F\u662F\u6709\u5DEE\u5F02\u7684\u3002</p><p><strong>\u4F46\u6211\u4EEC\u66F4\u9700\u8981\u6CE8\u610F\u7684\u662F\uFF0C\u5B69\u5B50\u7A76\u7ADF\u662F\u5206\u79BB\u7126\u8651\u7684\u7A0B\u5EA6\u6BD4\u8F83\u4F4E\uFF0C\u8FD8\u662F\u6709\u5206\u79BB\u7126\u8651\u4F46\u6CA1\u6709\u8868\u8FBE\u51FA\u6765\u3002</strong></p><p>\u5982\u679C\u5B69\u5B50\u672C\u8EAB\u6BD4\u8F83\u5BB9\u6613\u9002\u5E94\u65B0\u73AF\u5883\u3001\u60C5\u7EEA\u4F53\u9A8C\u8F83\u4E3A\u5E73\u548C\uFF0C\u4E00\u76F4\u4EE5\u6765\u5728\u7ECF\u5386\u548C\u7236\u6BCD\u5206\u79BB\u65F6\u90FD\u6BD4\u8F83\u987A\u5229\uFF0C\u6CA1\u6709\u592A\u8FC7\u4E8E\u660E\u663E\u7684\u7126\u8651\u60C5\u7EEA\uFF0C\u90A3\u6211\u4EEC\u53EF\u4EE5\u8BA4\u4E3A\u5B69\u5B50\u7684\u5206\u79BB\u7126\u8651\u7A0B\u5EA6\u6BD4\u8F83\u4F4E\uFF0C\u5219\u65E0\u9700\u62C5\u5FC3\u3002</p><p>\u4F46\u5982\u679C\u5B69\u5B50\u672C\u8EAB\u5B58\u5728\u5206\u79BB\u7126\u8651\uFF0C\u53EA\u662F\u4E0D\u4EE5\u54ED\u95F9\u3001\u7EA0\u7F20\u6765\u8868\u8FBE\uFF0C\u8FD9\u4E5F\u4F1A\u8BEF\u4EE5\u4E3A\u5B69\u5B50\u4E0D\u5B58\u5728\u5206\u79BB\u7126\u8651\u60C5\u7EEA\u3002</p><p>\u800C\u8FD9\u4E9B\u5B69\u5B50\u5F80\u5F80\u4F1A\u51FA\u73B0\u4E00\u4E9B\u4EE5\u524D\u6CA1\u6709\u8FC7\u7684\u884C\u4E3A\uFF1A\u5403\u624B\u6307\u3001\u5543\u54AC\u6307\u7532\u3001\u628A\u5934\u53D1\u3001\u5BB9\u6613\u53D1\u813E\u6C14\u3001\u80C6\u5C0F\u9000\u7F29\u3001\u4E0D\u613F\u53C2\u52A0\u96C6\u4F53\u751F\u6D3B\u7B49\uFF0C\u751A\u81F3\u5F3A\u70C8\u62B5\u6297\u5165\u56ED\uFF0C\u90A3\u5B9E\u9645\u662F\u5B69\u5B50\u4EE5\u5176\u4ED6\u65B9\u5F0F\u6765\u8868\u8FBE\u81EA\u5DF1\u7684\u5206\u79BB\u7126\u8651\u60C5\u7EEA\u7F62\u4E86\u3002</p><p>\u8FD9\u65F6\u5019\u6211\u4EEC\u9700\u8981\u8003\u8651\u4EE5\u4E0B\u539F\u56E0\uFF1A</p><ul><li><p>\u5B69\u5B50\u662F\u5426\u65E2\u5F80\u5728\u5BB6\u4E2D\u4E5F\u662F\u4E0D\u5584\u4E8E\u3001\u4E0D\u6562\u6216\u4E0D\u88AB\u5141\u8BB8\u8868\u8FBE\u81EA\u5DF1\u7684\u60C5\u7EEA\uFF1B</p></li><li><p>\u5B69\u5B50\u56E0\u4E3A\u548C\u7236\u6BCD\u5206\u79BB\u65F6\u54ED\u95F9\u800C\u53D7\u5230\u8FC7\u6279\u8BC4\u3001\u6307\u8D23\u6216\u8005\u6050\u5413\u4EE5\u81F3\u4E8E\u4E0D\u6562\u8868\u8FBE\uFF1B</p></li><li><p>\u5B69\u5B50\u7684\u60C5\u7EEA\u603B\u662F\u4E0D\u88AB\u7406\u89E3\u548C\u56DE\u5E94\uFF0C\u6240\u4EE5\u4F7F\u7528\u66F4\u5F3A\u70C8\u7684\u8868\u73B0\u6765\u5F15\u8D77\u5927\u4EBA\u91CD\u89C6\u3002</p></li></ul><p>\u53E6\u5916\uFF0C\u5E94\u5BF9\u4E0A\u8FF0\u95EE\u9898\u65F6\uFF0C\u4E0D\u8981\u53EA\u5173\u6CE8\u4E8E\u53BB\u7EA0\u6B63\u201C\u4E0D\u826F\u884C\u4E3A\u201D\uFF0C\u66F4\u8981\u53BB\u5C1D\u8BD5\u7406\u89E3\u5B69\u5B50\u7684\u60C5\u7EEA\uFF0C\u5728\u65E5\u5E38\u4E2D\u591A\u5BF9\u5B69\u5B50\u7684\u60C5\u7EEA\u8FDB\u884C\u76F4\u63A5\u7684\u53CD\u9988\uFF0C\u9F13\u52B1\u4ED6\u8FDB\u884C\u8868\u8FBE\u3002</p><p>\u4F8B\u5982\uFF0C\u5F53\u5B69\u5B50\u51FA\u73B0\u5543\u54AC\u6307\u7532\u7684\u73B0\u8C61\uFF0C\u5BB6\u957F\u5C3D\u91CF\u4E0D\u8981\u8001\u5BF9\u7740\u5B69\u5B50\u8BF4\u201C\u4E0D\u8981\u54AC\u6307\u7532\u201D\uFF0C\u800C\u662F\u5C1D\u8BD5\u95EE\u95EE\u5B69\u5B50\u201C\u662F\u4E0D\u662F\u56E0\u4E3A\u4EC0\u4E48\u4E8B\u60C5\u8BA9\u4F60\u7D27\u5F20\u4E86\uFF1F\u7D27\u5F20\u65F6\u5019\u53EF\u4EE5\u8DDF\u5988\u5988\u8BB2\uFF0C\u6211\u4EEC\u4E00\u8D77\u6765\u770B\u770B\u600E\u4E48\u89E3\u51B3\u95EE\u9898\u201D\u3002</p><p><strong>04</strong></p><p><strong>\u5982\u4F55\u4E0E\u5B69\u5B50\u201C\u9053\u522B\u201D\uFF1F</strong></p><p>\u77E5\u9053\u5B69\u5B50\u773C\u4E2D\u7684\u5206\u5F00\u540E\uFF0C\u521A\u5165\u56ED\u65F6\uFF0C\u7236\u6BCD\u8BE5\u5982\u4F55\u4E0E\u5B69\u5B50\u201C\u9053\u522B\u201D\u5462\uFF1F</p><p>\u5176\u5B9E\u201C\u9053\u522B\u201D\u4E5F\u662F\u4E00\u4E2A\u201C\u6280\u672F\u6D3B\u201D\uFF0C\u76F8\u5F53\u4E8E\u662F\u5206\u79BB\u65F6\u7684\u4E00\u4E2A\u4EEA\u5F0F\uFF0C\u6B63\u5F0F\u7684\u9053\u522B\u6709\u52A9\u5B69\u5B50\u5BF9\u5206\u79BB\u5EFA\u7ACB\u4E00\u4E2A\u5408\u9002\u7684\u9884\u671F\uFF0C\u4FD7\u8BDD\u8BF4\u201C\u5FC3\u91CC\u6709\u5E95\u201D\u3002</p><p>\u9996\u5148\u9700\u8981\u4E0D\u540C\u7684\u63A5\u9001\u4EBA\u5728\u9053\u522B\u65F6\u5C3D\u53EF\u80FD\u4FDD\u6301\u4E00\u81F4\u7684\u65B9\u6CD5\uFF0C\u53EF\u4EE5\u548C\u5B69\u5B50\u7EA6\u5B9A\u4E00\u4E2A\u201C\u9053\u522B\u4EEA\u5F0F\u201D\uFF0C\u6BD4\u5982\u4EB2\u4EB2\u8111\u95E8\u3001\u53CC\u624B\u51FB\u638C\u3001\u505A\u4E00\u5957\u201C\u9B54\u6CD5\u64CD\u201D\uFF0C\u4F8B\u5982\u51FB\u638C\u3001\u78B0\u62F3\u7B49\u7B80\u5355\u7EC4\u5408\u5F62\u6210\u7684\u4E00\u5957\u5C0F\u6E38\u620F\uFF0C\u7ED3\u5408\u5B69\u5B50\u7684\u559C\u597D\u8FDB\u884C\u4E0D\u540C\u7684\u547D\u540D\u7B49\u7B49\u3002</p><p>\u4E0E\u5B69\u5B50\u8FDB\u884C\u201C\u9053\u522B\u4EEA\u5F0F\u201D\u540E\uFF0C\u63A5\u9001\u4EBA\u4EE5\u8F7B\u677E\u6109\u60A6\u7684\u65B9\u5F0F\u5FEB\u901F\u79BB\u5F00\uFF0C\u8FD9\u65F6\u8981\u907F\u514D\u8FC7\u591A\u5730\u5411\u5B69\u5B50\u4EA4\u4EE3\u5728\u56ED\u7684\u6CE8\u610F\u4E8B\u9879\uFF0C\u4E5F\u907F\u514D\u8FC7\u5EA6\u7684\u7EA0\u7F20\u6216\u8005\u96BE\u5206\u96BE\u820D\u3002</p><p>\u56E0\u4E3A\u5982\u679C\u7238\u7238\u5988\u5988\u5728\u9762\u5BF9\u5206\u79BB\u65F6\u8FC7\u5EA6\u7684\u7126\u8651\u3001\u53CD\u590D\u7684\u4EA4\u5F85\uFF0C\u5B69\u5B50\u662F\u4F1A\u8BFB\u5230\u6211\u4EEC\u60C5\u7EEA\u7684\uFF0C\u4ED6\u4EEC\u4F1A\u611F\u53D7\u5230\u5373\u5C06\u8981\u53BB\u7684\u5E7C\u513F\u56ED\u662F\u8BA9\u7238\u7238\u5988\u5988\u5145\u6EE1\u7740\u7126\u8651\u548C\u4E0D\u5B89\u7684\uFF0C\u5B69\u5B50\u81EA\u7136\u4E5F\u5C31\u4E0D\u4F1A\u653E\u5FC3\u3002</p><p>\u6240\u4EE5\u6B64\u65F6\u5C31\u7B97\u6211\u4EEC\u5185\u5FC3\u5145\u6EE1\u7740\u62C5\u5FC3\uFF0C\u4E5F\u8981\u5C3D\u91CF\u4EE5\u8F7B\u677E\u6109\u60A6\u7684\u65B9\u5F0F\u9053\u522B\uFF0C\u8BA9\u5B69\u5B50\u611F\u53D7\u5230\u6211\u4EEC\u7684\u76F8\u4FE1\u548C\u653E\u5FC3\u3002</p><p>\u6709\u65F6\u5B69\u5B50\u5728\u9053\u522B\u65F6\u4F1A\u8981\u6C42\u7238\u7238\u5988\u5988\u65E9\u70B9\u6765\u63A5\u4ED6\uFF0C\u90A3\u6211\u4EEC\u53EF\u4EE5\u544A\u77E5\u4E00\u4E2A\u5177\u4F53\u7684\u6D3B\u52A8\u800C\u4E0D\u662F\u544A\u8BC9\u5B69\u5B50\u51E0\u70B9\u949F\uFF0C\u6BD4\u5982\u53EF\u4EE5\u8DDF\u5B69\u5B50\u8BF4\uFF1A\u5988\u5988\u4F1A\u5728\u4F60\u5403\u5B8C\u5348\u70B9\u4EE5\u540E\u5C31\u6765\u63A5\u4F60\u3002</p><p>\u4F46\u8981\u6CE8\u610F\u7684\u662F\u4F46\u51E1\u548C\u5B69\u5B50\u6709\u7EA6\u5B9A\u5C31\u4E00\u5B9A\u8981\u9075\u5B88\uFF0C\u4E0D\u8981\u968F\u610F\u66F4\u6539\u7EA6\u5B9A\uFF0C\u5BF9\u5B69\u5B50\u6765\u8BF4\u4ED6\u4F1A\u975E\u5E38\u91CD\u89C6\u548C\u6211\u4EEC\u7684\u7EA6\u5B9A\uFF0C\u5982\u679C\u6211\u4EEC\u7ECF\u5E38\u7834\u574F\u7EA6\u5B9A\uFF0C\u90A3\u4E45\u800C\u4E45\u4E4B\u8FD9\u79CD\u7EA6\u5B9A\u53EF\u80FD\u5C31\u4E0D\u592A\u80FD\u8D77\u6548\u4E86\u3002</p><p><strong>05</strong></p><p><strong>\u5B69\u5B50\u5165\u56ED\uFF0C\u4E3A\u4EC0\u4E48\u53CD\u800C\u662F\u81EA\u5DF1\u5728\u7126\u8651\uFF1F</strong></p><p>\u5176\u5B9E\u5728\u517B\u80B2\u8FC7\u7A0B\u4E2D\u6240\u5F62\u6210\u7684\u4F9D\u604B\u5173\u7CFB\u4E5F\u662F\u53CC\u5411\u7684\uFF0C\u800C\u76F8\u5E94\u7684\u5206\u79BB\u7126\u8651\u4E5F\u4E0D\u4EC5\u4EC5\u51FA\u73B0\u5728\u5B69\u5B50\u8EAB\u4E0A\uFF0C\u6211\u4EEC\u81EA\u5DF1\u4E5F\u4F1A\uFF0C\u5C24\u5176\u662F\u957F\u671F\u5E26\u517B\u5B69\u5B50\u7684\u4EBA\u3002</p><p>\u5728\u517B\u80B2\u5B69\u5B50\u7684\u8FC7\u7A0B\u4E2D\uFF0C\u6211\u4EEC\u662F\u65F6\u523B\u5728\u88AB\u5B69\u5B50\u6240\u9700\u8981\u7684\uFF0C\u90A3\u79CD\u611F\u89C9\u662F\u4E00\u79CD\u4EF7\u503C\u611F\u7684\u4F53\u9A8C\u3002</p><p>\u5B69\u5B50\u5165\u56ED\u4E86\uFF0C\u610F\u5473\u7740\u4ED6\u8981\u5F00\u59CB\u81EA\u5DF1\u5403\u996D\u3001\u81EA\u5DF1\u7761\u89C9\u3001\u81EA\u5DF1\u4E0A\u5395\u6240\uFF0C\u9047\u5230\u56F0\u96BE\u4E5F\u8981\u81EA\u5DF1\u9762\u5BF9\uFF0C\u8FD9\u79CD\u77ED\u6682\u201C\u4E0D\u88AB\u9700\u8981\u201D\u7684\u843D\u5DEE\u611F\u4E5F\u662F\u81EA\u7136\u800C\u7136\u5730\u4EA7\u751F\u3002\u56E0\u6B64\uFF0C\u5165\u56ED\u5206\u79BB\uFF0C\u66F4\u662F\u4E00\u4E2A\u6211\u4EEC\u4E0E\u5B69\u5B50\u5171\u540C\u9002\u5E94\u548C\u8C03\u6574\u7684\u8FC7\u7A0B\u3002</p><p>\u4E0E\u5B69\u5B50\u7684\u201C\u9053\u522B\u201D\u4E5F\u662F\u5FC3\u7406\u8054\u7ED3\u4E0A\u7684\u4E00\u79CD\u9053\u522B\u3002\u4E8E\u5BB6\u957F\u800C\u8A00\uFF0C\u5B69\u5B50\u5373\u5C06\u8FDB\u5165\u65B0\u7684\u9636\u6BB5\uFF0C\u8981\u76F8\u4FE1\u4ED6\u4EEC\u80FD\u591F\u9002\u5E94\u5E7C\u513F\u56ED\u751F\u6D3B\uFF1B\u4E8E\u5B69\u5B50\u800C\u8A00\uFF0C\u4ED6\u4EEC\u5728\u4E0E\u5355\u4E00\u7684\u5C45\u5BB6\u751F\u6D3B\u201C\u9053\u522B\u201D\uFF0C\u8D70\u5411\u66F4\u793E\u4F1A\u5316\u7684\u96C6\u4F53\u751F\u6D3B\uFF0C\u662F\u72EC\u7ACB\u548C\u6210\u957F\u7684\u4E00\u79CD\u6807\u5FD7\u3002</p><p>\u5B69\u5B50\u5165\u56ED\u4E86\uFF0C\u4F60\u505A\u597D\u201C\u9053\u522B\u201D\u7684\u51C6\u5907\u5417\uFF1F</p><hr><p>\u9664\u4E86 \u201C\u9053\u522B\u201D\uFF0C\u964C\u751F\u7684\u5E7C\u513F\u56ED\u73AF\u5883\u5BF9\u5B69\u5B50\u6765\u8BF4\u4E5F\u662F\u4E00\u4E2A\u6311\u6218\uFF0C\u5728\u9002\u5E94\u65B0\u751F\u6D3B\u7684\u671F\u95F4\uFF0C\u95EE\u9898\u4E5F\u4E0D\u5C11\uFF0C\u5982\u4F55\u8BA9\u5B69\u5B50\u66F4\u597D\u878D\u5165\u5E7C\u513F\u56ED\u751F\u6D3B\uFF0C\u5F90\u6842\u51E4\u533B\u751F\u5728\u6021\u79BE\u4E0A\u5F00\u8BBE\u7684\u5165\u56ED\u653B\u7565\u53EF\u80FD\u5BF9\u4F60\u6709\u7528\u3002</p><p>\u6709\u9700\u8981\u7684\u53EF\u901A\u8FC7\u4E0B\u9762\u4E8C\u7EF4\u7801\u8D2D\u4E70\uFF0C\u6021\u79BE\u4F1A\u54585\u6298\u542C\u3002</p>',46),l={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651634346&idx=1&sn=b18baa56d7c662550c9016aebfc12614&chksm=8b5c9350bc2b1a46690ee9f014f4cfb5991f7f5d5364281470827e6bd62f67374cd4ca27abf0&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},_=c("\u67E5\u770B\u539F\u6587");function f(h,m){const e=r("ExternalLinkIcon");return t(),n(s,null,[g,p("p",null,[p("a",l,[_,o(e)])])],64)}var u=i(d,[["render",f]]);export{u as default};
