import{r,o as t,c as a,a as e,b as n,F as o,d as c,e as s}from"./app.9aaa2256.js";import{_ as i}from"./plugin-vue_export-helper.21dcd24c.js";const d={},_=c('<h3 id="\u9762\u5BF9\u95EE\u9898\u75AB\u82D7-\u6211\u4EEC\u600E\u4E48\u529E" tabindex="-1"><a class="header-anchor" href="#\u9762\u5BF9\u95EE\u9898\u75AB\u82D7-\u6211\u4EEC\u600E\u4E48\u529E" aria-hidden="true">#</a> \u9762\u5BF9\u95EE\u9898\u75AB\u82D7\uFF0C\u6211\u4EEC\u600E\u4E48\u529E\uFF1F</h3><p>\u53EF\u80FD\u5927\u5BB6\u4E5F\u77E5\u9053\uFF0C\u6211\u4E00\u76F4\u5728\u63A8\u8350\u5927\u5BB6\u63A5\u79CD\u75AB\u82D7\uFF0C\u56E0\u4E3A\u75AB\u82D7\u662F\u73B0\u4EE3\u533B\u5B66\u6700\u4F1F\u5927\u7684\u53D1\u660E\u3002</p><p>\u4E00\u4E2A\u533B\u751F\u80FD\u6551\u6CBB\u51E0\u767E\u4E0A\u5343\u7684\u75C5\u4EBA\uFF0C\u4E00\u79CD\u75AB\u82D7\u5374\u80FD\u633D\u6551\u6570\u4EE5\u4E07\u8BA1\u7684\u75C5\u4EBA\uFF0C\u6B63\u662F\u5F97\u76CA\u4E8E\u75AB\u82D7\uFF0C\u5929\u82B1\u8FD9\u6837\u70C8\u6027\u4F20\u67D3\u75C5\u5F97\u4EE5\u6D88\u706D\uFF0C\u4E5F\u6B63\u662F\u56E0\u4E3A\u75AB\u82D7\uFF0C\u5F88\u591A\u5E38\u89C1\u75C5\u7684\u53D1\u75C5\u7387\u5927\u5E45\u4E0B\u964D\u3002</p><p>\u5168\u7403\u6BCF\u5E74\u6570\u4EE5\u767E\u4E07\u8BA1\u7684\u5B69\u5B50\u6B7B\u4E8E\u80BA\u708E\u548C\u8179\u6CFB\uFF0C\u4E3B\u8981\u96C6\u4E2D\u5728\u8D2B\u7A77\u843D\u540E\u7684\u53D1\u5C55\u4E2D\u56FD\u5BB6\uFF0C\u53D1\u8FBE\u56FD\u5BB6\u7684\u5B69\u5B50\u6B7B\u4EA1\u7387\u8FD9\u4E48\u4F4E\uFF0C\u5F88\u5927\u7A0B\u5EA6\u5F52\u529F\u4E8E\u75AB\u82D7\u3002\u5728\u7F8E\u56FD\uFF0C\u8F6E\u72B6\u75C5\u6BD2\u75AB\u82D7\u63A5\u79CD\u4F7F\u80A0\u80C3\u708E\u7684\u53D1\u751F\u7387\u4E0B\u964D\u4E8686%\uFF0CHib\u75AB\u82D7\u4F7FHib\u611F\u67D3\u4E0B\u964D\u4E8699%\u3002</p><p>\u75AB\u82D7\u4E0D\u4F46\u633D\u6551\u4E86\u5F88\u591A\u5B69\u5B50\u7684\u751F\u547D\uFF0C\u4E5F\u8BA9\u5F88\u591A\u5B69\u5B50\u514D\u53D7\u75BE\u75C5\u7684\u56F0\u6270\uFF0C\u4E5F\u8282\u7EA6\u4E86\u5927\u91CF\u7684\u533B\u7597\u6210\u672C\u3002\u75AB\u82D7\u63A5\u79CD\u9884\u9632\u75BE\u75C5\u62EF\u6551\u751F\u547D\uFF0C\u8FD9\u4E5F\u662F\u53BB\u5E74\u7F8E\u56FD\u513F\u79D1\u5B66\u4F1A\u8BC4\u51FA\u768440\u5E74\u513F\u79D1\u7814\u7A76\u4E03\u5927\u6210\u5C31\u4E4B\u9996\u3002</p><p>\u56FD\u5185\u75AB\u82D7\u6709\u4E00\u7C7B\u4E8C\u7C7B\u4E4B\u5206\uFF0C\u4E00\u7C7B\u662F\u56FD\u5BB6\u514D\u8D39\u5F3A\u5236\u63A5\u79CD\u7684\uFF0C\u4E8C\u7C7B\u662F\u81EA\u8D39\u75AB\u82D7\u81EA\u613F\u63A5\u79CD\u3002\u5F88\u591A\u4EBA\u8BEF\u4EE5\u4E3A\u65E2\u7136\u4E8C\u7C7B\u75AB\u82D7\u4E0D\u662F\u5F3A\u5236\u63A5\u79CD\u7684\uFF0C\u5C31\u662F\u4F5C\u7528\u4E0D\u5927\u7684\u75AB\u82D7\u3002\u4F46\u4E8B\u5B9E\u4E0A\uFF0C\u6B63\u89C4\u75AB\u82D7\u4E0A\u5E02\u4E4B\u524D\u90FD\u9700\u8981\u7ECF\u8FC7\u4E34\u5E8A\u8BD5\u9A8C\uFF0C\u53EA\u6709\u6536\u76CA\u5927\u4E8E\u98CE\u9669\u624D\u53EF\u80FD\u88AB\u6279\u51C6\u4E0A\u5E02\u3002Hib\u4E4B\u7C7B\u7684\u4E8C\u7C7B\u75AB\u82D7\u5728\u5F88\u591A\u53D1\u8FBE\u56FD\u5BB6\u4E5F\u662F\u53EF\u4EE5\u514D\u8D39\u63A5\u79CD\u7684\uFF0C\u53EA\u4E0D\u8FC7\u6211\u4EEC\u56FD\u5BB6\u7684\u7ECF\u6D4E\u6761\u4EF6\u6709\u9650\uFF0C\u8FD8\u9700\u8981\u81EA\u8D39\u3002</p><p>\u53EA\u8981\u662F\u6B63\u89C4\u75AB\u82D7\uFF0C\u5982\u679C\u7ECF\u6D4E\u6761\u4EF6\u5141\u8BB8\uFF0C\u65E0\u8BBA\u662F\u4E00\u7C7B\u8FD8\u662F\u4E8C\u7C7B\uFF0C\u63A5\u79CD\u4E0A\u5BF9\u5B69\u5B50\u90FD\u662F\u5229\u5927\u4E8E\u5F0A\uFF0C\u8FD9\u4E5F\u662F\u6211\u4E00\u76F4\u63A8\u8350\u5927\u5BB6\u7ED9\u5B69\u5B50\u63A5\u79CD\u75AB\u82D7\u7684\u539F\u56E0\u3002</p><p>\u4F46\u56E0\u4E3A\u884C\u653F\u6CD5\u5236\u7B49\u539F\u56E0\uFF0C\u4E00\u4E9B\u88AB\u9A8C\u8BC1\u6709\u6548\u7684\u75AB\u82D7\u6CA1\u80FD\u5728\u56FD\u5185\u4E0A\u5E02\uFF0C\u5BFC\u81F4\u5F88\u591A\u4EBA\u8DD1\u53BB\u9999\u6E2F\u63A5\u79CDHPV\u75AB\u82D7\u548C13\u4EF7\u80BA\u708E\u75AB\u82D7\uFF0C\u540C\u65F6\uFF0C\u56FD\u5185\u7684\u75AB\u82D7\u7BA1\u7406\u51FA\u8FC7\u5F88\u591A\u95EE\u9898\u3002</p><p>\u5170\u83CC\u51C0\u8FD9\u6837\u7684\u5047\u75AB\u82D7\uFF0C\u56E0\u4E3A\u5229\u76CA\u95EE\u9898\uFF0C\u66FE\u88AB\u6211\u56FD\u7684\u75BE\u63A7\u4E2D\u5FC3\u7EB3\u5165\u513F\u7AE5\u9884\u9632\u63A5\u79CD\u4FE1\u606F\u7BA1\u7406\u7CFB\u7EDF\uFF0C\u5217\u4E3A\u201C\u4E8C\u7C7B\u75AB\u82D7\u201D\u63A8\u5E7F\u9500\u552E\u3002\u56FD\u5185\u7684\u4E00\u4E9B\u4E0D\u6CD5\u5382\u5546\uFF0C\u53D1\u660E\u4E86\u4E00\u79CD\u53EB\u201C\u624B\u8DB3\u53E3\u75C5\u75C5\u6BD2\u6297\u4F53\u55B7\u5242\u201D\u7684\u4E1C\u897F\uFF0C\u58F0\u79F0\u53EF\u4EE5\u9632\u6CBB\u624B\u8DB3\u53E3\u75C5\uFF0C\u4E8B\u5B9E\u4E0A\u5B83\u53EA\u662F\u505A\u4E86\u4F53\u5916\u8BD5\u9A8C\u7684\u201C\u6D88\u5B57\u53F7\u201D\u4EA7\u54C1\uFF0C\u4E5F\u88AB\u4E00\u4E9B\u5730\u65B9\u75BE\u63A7\u90E8\u95E8\u7EB3\u5165\u4E8C\u7C7B\u75AB\u82D7\u91C7\u8D2D\uFF0C\u8BA9\u5F88\u591A\u5BB6\u957F\u82B1\u4E86\u5F88\u591A\u51A4\u6789\u94B1\u3002\u6211\u5728\u300A\u6CA1\u7528\u8FD8\u53EF\u80FD\u6709\u5BB3\u7684\u513F\u79D1\u836F\u7269\u300B\u91CC\u63ED\u9732\u8FC7\uFF0C\u4E5F\u6709\u5A92\u4F53\u8DDF\u8FDB\u62A5\u9053\u8FC7\u3002</p><p>\u524D\u51E0\u5929\u6DF1\u5733\u4E00\u4E2A\u533B\u751F\u88AB\u533B\u95F9\u903C\u8FEB\u4E0B\u8DEA\uFF0C\u8D77\u56E0\u662F\u4E00\u4E2A\u5B69\u5B50\u6B7B\u4E8E\u91CD\u75C7\u80BA\u708E\uFF0C\u9488\u5BF9\u8FD9\u4EF6\u4E8B\u524D\u4E24\u5929\u6211\u8FD8\u53D1\u4E86\u5FAE\u535A\uFF0C\u544A\u8BC9\u5927\u5BB6Hib\u75AB\u82D7\u3001\u80BA\u708E\u7403\u83CC\u75AB\u82D7\u3001\u6D41\u611F\u75AB\u82D7\u3001\u9EBB\u75B9\u3001\u6C34\u75D8\u3001\u767E\u65E5\u54B3\u75AB\u82D7\u53EF\u4EE5\u964D\u4F4E\u5B69\u5B50\u80BA\u708E\u7684\u98CE\u9669\u3002</p><p>\u5177\u6709\u8BBD\u523A\u610F\u5473\u7684\u662F\uFF0C\u4ECA\u5929\u5374\u770B\u5230\u8FD9\u6761\u65B0\u95FB\uFF0C\u5C71\u4E1C\u4E24\u4E2A\u75AB\u82D7\u8D29\u5B50\uFF0C\u57285\u5E74\u95F4\u8D29\u5356\u4E86\u903E200\u4E07\u652F\u4E8C\u7C7B\u75AB\u82D7\uFF0C\u672A\u7ECF\u4E25\u683C\u51B7\u94FE\u5B58\u50A8\u8FD0\u8F93\u9500\u5F80\u6E56\u5317\u3001\u5B89\u5FBD\u3001\u5E7F\u4E1C\u3001\u6CB3\u5357\u3001\u56DB\u5DDD\u7B4918\u4E2A\u7701\u5E02\uFF0C\u6D89\u6848\u91D1\u989D\u8FBE5.7\u4EBF\u5143\u3002</p><p>\u672A\u6309\u8981\u6C42\u5B58\u50A8\u8FD0\u8F93\u7684\u75AB\u82D7\uFF0C\u4F1A\u589E\u52A0\u75AB\u82D7\u5931\u6548\u7684\u98CE\u9669\uFF0C\u5BFC\u81F4\u7684\u7ED3\u679C\u662F\u90E8\u5206\u5BB6\u957F\u82B1\u4E86\u9AD8\u4EF7\u94B1\u7ED9\u5B69\u5B50\u6253\u4E86\u81EA\u8D39\u75AB\u82D7\uFF0C\u5374\u8D77\u4E0D\u5230\u4FDD\u62A4\u5B69\u5B50\u7684\u4F5C\u7528\uFF0C\u5BF9\u4E8E\u72C2\u72AC\u75C5\u75AB\u82D7\u8FD9\u6837\u7684\u6551\u547D\u75AB\u82D7\u6765\u8BF4\uFF0C\u5931\u6548\u5C31\u610F\u5473\u7740\u8C0B\u8D22\u5BB3\u547D\u3002\u66F4\u5177\u8BBD\u523A\u610F\u5473\u7684\u662F\uFF0C\u8FD9\u4E24\u4E2A\u72AF\u7F6A\u5ACC\u7591\u4EBA\uFF0C\u662F\u5728\u56E0\u975E\u6CD5\u7ECF\u8425\u75AB\u82D7\u88AB\u5224\u5211\u540E\uFF0C\u8FD8\u5728\u7F13\u5211\u671F\u95F4\u7EE7\u7EED\u4F5C\u7684\u6848\u3002</p><p>\u770B\u5230\u8FD9\u79CD\u65B0\u95FB\uFF0C\u6211\u4EEC\u5DF2\u7ECF\u65E0\u6CD5\u7528\u6124\u6012\u6765\u5F62\u5BB9\u5FC3\u60C5\u3002\u672C\u6765\u5F88\u591A\u6C11\u4F17\u5C31\u56E0\u4E3A\u5BF9\u75AB\u82D7\u4E0D\u826F\u53CD\u5E94\u7684\u8FC7\u5EA6\u8BEF\u8BFB\uFF0C\u5BF9\u75AB\u82D7\u6709\u62B5\u89E6\u60C5\u7EEA\uFF0C\u62D2\u7EDD\u7ED9\u81EA\u5DF1\u5B69\u5B50\u63A5\u79CD\uFF0C\u8BA9\u5B69\u5B50\u5904\u4E8E\u5371\u9669\u4E4B\u5883\u3002\u8FD9\u7C7B\u9891\u53D1\u7684\u75AB\u82D7\u95EE\u9898\u53EA\u4F1A\u8BA9\u66F4\u591A\u7684\u5BB6\u957F\u5BF9\u56FD\u5185\u75AB\u82D7\u5931\u53BB\u4FE1\u5FC3\uFF0C\u8BA9\u66F4\u591A\u7684\u5B69\u5B50\u5F97\u4E0D\u5230\u63A5\u79CD\u3002\u8FD9\u79CD\u4F24\u5929\u5BB3\u7406\u7684\u72AF\u7F6A\u6301\u7EED\u4E86\u4E94\u5E74\u4E4B\u4E45\u624D\u88AB\u53D1\u73B0\uFF0C\u4E5F\u4E0D\u77E5\u9053\u6211\u4EEC\u7684\u76D1\u7BA1\u90E8\u95E8\u5728\u5E72\u4EC0\u4E48\u3002</p><p>\u6124\u6012\u60B2\u54C0\u4E4B\u4F59\uFF0C\u6211\u4EEC\u5E94\u8BE5\u600E\u4E48\u529E\u5462\uFF1F\u6211\u8FD8\u662F\u8981\u544A\u8BC9\u5927\u5BB6\uFF0C\u5C3D\u7BA1\u51FA\u4E86\u8FD9\u591A\u95EE\u9898\uFF0C\u5728\u5F53\u524D\u7684\u6761\u4EF6\u4E0B\uFF0C<strong>\u75AB\u82D7\u80FD\u6253\u7684\u8FD8\u662F\u5C3D\u91CF\u6253</strong>\u3002</p><p>\u8FD9\u6B21\u53D7\u5F71\u54CD\u7684\u662F\u81EA\u8D39\u7684\u4E8C\u7C7B\u75AB\u82D7\uFF0C\u4E00\u7C7B\u75AB\u82D7\u4E0D\u5728\u5F71\u54CD\u4E4B\u5217\uFF0C\u53D7\u5F71\u54CD\u7684\u4E24\u767E\u591A\u4E07\u652F\u4E8C\u7C7B\u75AB\u82D7\uFF0C\u53605\u5E74\u95F4\u603B\u63A5\u79CD\u75AB\u82D7\u7684\u6BD4\u4F8B\u8FD8\u662F\u5F88\u4F4E\uFF0C\u6CA1\u5728\u51B7\u94FE\u6761\u4EF6\u4E0B\u50A8\u5B58\u8FD0\u8F93\uFF0C\u75AB\u82D7\u8D28\u91CF\u4F1A\u53D7\u5F71\u54CD\uFF0C\u5931\u6548\u7684\u98CE\u9669\u4E5F\u4F1A\u589E\u5927\uFF0C\u4F46\u4E0D\u610F\u5473\u7740\u8FD9\u4E9B\u75AB\u82D7\u90FD\u662F\u65E0\u6548\u3002</p><p>\u6211\u4EEC\u4E0D\u60F3\u770B\u5230\u8FD9\u6837\u7684\u65B0\u95FB\uFF0C\u4E5F\u4E0D\u60F3\u9762\u5BF9\u8FD9\u6837\u7684\u73B0\u5B9E\uFF0C\u4F46\u6211\u4EEC\u66F4\u5E94\u8BE5\u51B7\u9759\uFF0C\u5982\u679C\u56E0\u4E3A\u8FD9\u6B21\u7206\u51FA\u7684\u75AB\u82D7\u95EE\u9898\u800C\u62D2\u7EDD\u7ED9\u5B69\u5B50\u63A5\u79CD\u75AB\u82D7\uFF0C\u53EA\u4F1A\u589E\u52A0\u66F4\u591A\u6D41\u884C\u75C5\u7206\u53D1\u7684\u98CE\u9669\uFF0C\u8BA9\u6211\u4EEC\u81EA\u5DF1\u7684\u5B69\u5B50\u548C\u522B\u4EBA\u7684\u5B69\u5B50\u5904\u4E8E\u66F4\u5371\u9669\u7684\u73AF\u5883\u3002</p><p>\u6211\u4EEC\u51B7\u9759\u9009\u62E9\uFF0C\u4E0D\u610F\u5473\u7740\u6211\u4EEC\u8981\u5BBD\u5BB9\u72AF\u7F6A\uFF0C\u75AB\u82D7\u7275\u6D89\u5230\u5B69\u5B50\u7684\u751F\u6B7B\uFF0C\u4E5F\u7275\u6D89\u5230\u6574\u4E2A\u793E\u4F1A\u7684\u516C\u5171\u5B89\u5168\u3002\u6B63\u662F\u56E0\u4E3A\u60E9\u7F5A\u529B\u5EA6\u5C0F\uFF0C\u624D\u6709\u4EBA\u4E3A\u4E86\u5229\u76CA\uFF0C\u7F54\u987E\u522B\u4EBA\u751F\u6B7B\uFF0C\u5C06\u201C\u5170\u83CC\u51C0\u201D\u3001\u201C\u624B\u8DB3\u53E3\u75C5\u75C5\u6BD2\u6297\u4F53\u55B7\u5242\u201D\u4E4B\u7C7B\u7684\u5047\u75AB\u82D7\u5047\u836F\u7EB3\u5165\u75AB\u82D7\u9500\u552E\uFF0C\u4E5F\u6B63\u662F\u56E0\u4E3A\u72AF\u7F6A\u6210\u672C\u4F4E\uFF0C\u624D\u6709\u4EBA\u5728\u7F13\u5211\u671F\u95F4\u518D\u6B21\u94E4\u800C\u8D70\u9669\u3002</p><p>\u56E0\u4E3A\u4E09\u9E7F\u5976\u7C89\uFF0C\u73B0\u5728\u5927\u5BB6\u90FD\u4E70\u56FD\u5916\u5976\u7C89\uFF0C\u518D\u4E0D\u52A0\u5F3A\u75AB\u82D7\u7BA1\u7406\uFF0C\u52A0\u5927\u60E9\u5904\u529B\u5EA6\uFF0C\u4F1A\u6709\u5927\u5BB6\u88AB\u8FEB\u5E26\u5B69\u5B50\u5230\u5883\u5916\u6253\u75AB\u82D7\u7684\u4E00\u5929\u3002</p><p>\u624B\u8DB3\u53E3\u75C5\u75C5\u6BD2\u6297\u4F53\u55B7\u5242\u4E3A\u4EC0\u4E48\u6CA1\u7528\uFF1F\u6CA1\u7528\u8FD8\u53EF\u80FD\u6709\u5BB3\u7684\u513F\u79D1\u7528\u836F\u8FD8\u6709\u54EA\u4E9B\uFF1F\u957F\u6309\u4E0B\u9762\u56FE\u7247\u5173\u6CE8\u540E\uFF0C\u56DE\u590D\u201C<strong>92</strong>\u201D\u67E5\u770B\u7B54\u6848\u3002</p>',19),l={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=403308367&idx=1&sn=664e0a04a4fc84f5c4432e3d78484e89&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},h=s("\u67E5\u770B\u539F\u6587");function m(f,x){const p=r("ExternalLinkIcon");return t(),a(o,null,[_,e("p",null,[e("a",l,[h,n(p)])])],64)}var g=i(d,[["render",m]]);export{g as default};
