import{r as e,o as c,c as o,a as p,b as t,w as s,F as d,d as i,e as a}from"./app.9aaa2256.js";import{_}from"./plugin-vue_export-helper.21dcd24c.js";const l={},h=i('<h3 id="\u8110\u8D34-\u5751\u5A03\u4E4B\u8D34" tabindex="-1"><a class="header-anchor" href="#\u8110\u8D34-\u5751\u5A03\u4E4B\u8D34" aria-hidden="true">#</a> \u8110\u8D34\uFF0C\u5751\u5A03\u4E4B\u8D34</h3><p>\u5728\u666E\u5916\u79D1\uFF0C\u8179\u8154\u955C\u624B\u672F\u5F88\u666E\u53CA\u4E86\uFF0C\u8FD9\u4E2A\u624B\u672F\u901A\u5E38\u4F1A\u5728\u809A\u8110\u6216\u5468\u56F4\u5207\u4E2A\u5C0F\u53E3\uFF0C\u7136\u540E\u628A\u955C\u5B50\u4F38\u8FDB\u53BB\u3002\u5207\u53E3\u5207\u5728\u809A\u8110\uFF0C\u662F\u56E0\u4E3A\u8110\u7A9D\u91CC\u5F88\u9690\u853D\uFF0C\u5207\u53E3\u957F\u597D\u540E\u4E5F\u770B\u4E0D\u51FA\u505A\u8FC7\u624B\u672F\u3002</p><p>\u6709\u4E00\u6B21\u533B\u751F\u5728\u624B\u672F\u524D\u8DDF\u5BB6\u957F\u8BB2\u89E3\u624B\u672F\uFF0C\u5B69\u5B50\u7238\u7238\u5F88\u7591\u8651\uFF0C\u95EE\u4E86\u53E5\uFF1A\u533B\u751F\uFF0C\u8FD9\u6837\u6253\u6D1E\u4F1A\u4E0D\u4F1A\u628A\u809A\u8110\u7684\u7A74\u4F4D\u6253\u574F\uFF1F</p><p>\u5916\u79D1\u533B\u751F\u78B0\u5230\u8FD9\u79CD\u95EE\u9898\u901A\u5E38\u90FD\u4F1A\u53D1\u61F5\uFF0C\u56E0\u4E3A\u6211\u4EEC\u4E0D\u76F8\u4FE1\u7A74\u4F4D\u7684\u8BF4\u6CD5\uFF0C\u5426\u5219\u624B\u672F\u5C31\u6CA1\u6CD5\u505A\u4E86\u3002\u770B\u5F97\u89C1\u7684\u795E\u7ECF\u8840\u7BA1\u8FD8\u53EF\u4EE5\u5C0F\u5FC3\u5730\u907F\u5F00\uFF0C\u770B\u4E0D\u89C1\u7684\u7ECF\u7EDC\u7A74\u4F4D\u600E\u4E48\u53BB\u907F\uFF1F</p><p>\u4F46\u6211\u4EEC\u4E5F\u77E5\u9053\uFF0C\u809A\u8110\u5728\u4E2D\u533B\u91CC\u662F\u4E00\u4E2A\u91CD\u8981\u7684\u7A74\u4F4D\uFF0C\u6709\u5F88\u591A\u548C\u5B83\u6709\u5173\u7684\u5FCC\u8BB3\u3002\u6BD4\u5982\u809A\u8110\u4E0D\u80FD\u78B0\uFF0C\u4E0D\u80FD\u6D17\uFF0C\u4E0D\u80FD\u7740\u51C9\uFF0C\u6240\u4EE5\u6709\u4E9B\u4EBA\u5E38\u5E74\u4E0D\u6562\u6D17\u809A\u8110\uFF0C\u809A\u8110\u91CC\u90FD\u662F\u9ED1\u4E4E\u4E4E\u7684\u6C61\u57A2\u3002</p><p>\u540C\u65F6\u4E5F\u6709\u4E00\u4E9B\u5728\u809A\u8110\u4E0A\u505A\u6587\u7AE0\u7684\u4E2D\u533B\u6CBB\u7597\uFF0C\u6BD4\u5982\u8110\u8D34\u3002\u5927\u5BB6\u90FD\u53EF\u80FD\u542C\u8FC7\u8D34\u809A\u8110\u6CBB\u8179\u6CFB\u3001\u8D34\u8110\u8D34\u6CBB\u611F\u5192\u3001\u54B3\u55FD\u3001\u4E0D\u5403\u996D\uFF0C\u8FD8\u6709\u8BF4\u8D34\u8110\u8D34\u6CBB\u75D4\u75AE\uFF0C\u66F4\u6709\u751A\u8005\u8BF4\u8D34\u8110\u8D34\u53EF\u4EE5\u51CF\u80A5\u3002</p><p>\u809A\u8110\u6709\u4EC0\u4E48\u795E\u5947\u4E4B\u5904\u5417\uFF1F\u6211\u4EEC\u770B\u5230\u81EA\u5DF1\u6DF1\u6DF1\u7684\u8110\u7A9D\u6709\u65F6\u5019\u53EF\u80FD\u4E5F\u4F1A\u60F3\uFF0C\u8FD9\u4E2A\u7A9D\u7A9D\u901A\u5411\u54EA\u91CC\u5462\uFF1F\u4F1A\u4E0D\u4F1A\u548C\u809A\u5B50\u91CC\u76F8\u901A\uFF1F</p><p>\u5728\u80CE\u513F\u671F\uFF0C\u8110\u5E26\u662F\u80CE\u513F\u8FDE\u63A5\u5988\u5988\u7684\u7EBD\u5E26\uFF0C\u80CE\u513F\u901A\u8FC7\u8110\u5E26\u63A5\u53D7\u6765\u81EA\u5988\u5988\u7684\u8425\u517B\u548C\u6C27\u6C14\u3002\u4F46\u5B69\u5B50\u51FA\u751F\u4E4B\u540E\uFF0C\u8110\u9759\u8109\u548C\u8110\u52A8\u8109\u6162\u6162\u95ED\u5408\u6210\u4E3A\u7D22\u5E26\uFF0C\u8110\u6B8B\u7AEF\u8131\u843D\u4E4B\u540E\uFF0C\u809A\u8110\u5C31\u548C\u54EA\u91CC\u90FD\u4E0D\u901A\u4E86\uFF0C\u5426\u5219\u7684\u8BDD\uFF0C\u6211\u4EEC\u6D17\u6FA1\u7684\u65F6\u5019\u5C31\u8981\u62C5\u5FC3\u6C34\u6F0F\u8FDB\u809A\u5B50\u91CC\u4E86\u3002</p><p>\u8110\u7A9D\u6D45\u7684\u4EBA\u53EF\u4EE5\u76F4\u63A5\u770B\u5230\u5B8C\u6574\u5C01\u95ED\u7684\u809A\u8110\uFF0C\u8110\u7A9D\u6DF1\u7684\u5973\u6027\uFF0C\u6000\u5B55\u540E\u671F\u53EF\u80FD\u4E5F\u4F1A\u770B\u5230\u8110\u7A9D\u9732\u51FA\u6765\uFF0C\u4E5F\u53EF\u4EE5\u770B\u770B\u662F\u4E0D\u662F\u5C01\u95ED\u7684\u3002</p><p>\u809A\u8110\u4E0D\u4F46\u5916\u9762\u662F\u5C01\u95ED\u7684\uFF0C\u91CC\u9762\u4E5F\u4E0D\u548C\u4EC0\u4E48\u4E1C\u897F\u76F8\u8FDE\uFF0C\u65E2\u4E0D\u8FDE\u7740\u80A0\u5B50\uFF0C\u4E5F\u4E0D\u8FDE\u63A5\u8180\u80F1\u3002\u5982\u679C\u8FDE\u7740\u80A0\u5B50\u90A3\u662F\u4E00\u79CD\u5148\u5929\u6027\u75BE\u75C5\uFF0C\u53EB\u505A\u5375\u9EC4\u7BA1\u7578\u5F62\uFF0C\u5982\u679C\u548C\u80A0\u5B50\u76F8\u901A\uFF0C\u90A3\u53EB\u8110\u80A0\u7618\uFF0C\u90A3\u662F\u9700\u8981\u505A\u624B\u672F\u7684\u3002\u5982\u679C\u548C\u8180\u80F1\u76F8\u901A\uFF0C\u90A3\u53EB\u8110\u5C3F\u7BA1\u7618\uFF0C\u540C\u6837\u4E5F\u662F\u4E00\u79CD\u9700\u8981\u505A\u624B\u672F\u7684\u5148\u5929\u7578\u5F62\u3002</p><p>\u8110\u90E8\u901A\u8FC7\u809D\u5706\u97E7\u5E26\u548C\u809D\u810F\u76F8\u8FDE\uFF0C\u809D\u5706\u97E7\u5E26\u53EA\u662F\u8110\u9759\u8109\u95ED\u5408\u540E\u6B8B\u7559\u7684\u7D22\u5E26\uFF0C\u5E76\u6CA1\u6709\u529F\u7528\uFF0C\u8179\u90E8\u624B\u672F\u505A\u6A2A\u5207\u53E3\u7684\u65F6\u5019\u7ECF\u5E38\u9700\u8981\u628A\u5B83\u5207\u65AD\uFF0C\u4E0D\u5BF9\u8EAB\u4F53\u4EA7\u751F\u4EFB\u4F55\u5F71\u54CD\u3002</p><p>\u6B63\u662F\u56E0\u4E3A\u809A\u8110\u54EA\u91CC\u4E5F\u4E0D\u8FDE\u7740\uFF0C\u8FD9\u4E2A\u90E8\u4F4D\u4E5F\u6CA1\u6709\u91CD\u8981\u7684\u795E\u7ECF\u8840\u7BA1\uFF0C\u8FD9\u91CC\u7684\u8840\u4F9B\u4E5F\u4E0D\u4F1A\u6BD4\u5176\u5B83\u90E8\u4F4D\u66F4\u4E30\u5BCC\uFF0C\u518D\u52A0\u4E0A\u4F24\u53E3\u5728\u8FD9\u91CC\u5F88\u9690\u853D\uFF0C\u6240\u4EE5\u5FAE\u521B\u624B\u672F\u7684\u5207\u53E3\u6700\u5E38\u505A\u5728\u8FD9\u91CC\u3002</p><p>\u65E2\u7136\u809A\u8110\u548C\u54EA\u91CC\u90FD\u4E0D\u901A\u4E0D\u8FDE\uFF0C\u4E3A\u4EC0\u4E48\u8981\u5728\u809A\u8110\u8D34\u836F\u5462\uFF1F</p><p>\u8110\u8D34\u6CBB\u75C5\u7684\u4F9D\u636E\u7684\u662F\u4E2D\u533B\u7406\u8BBA\uFF0C\u4E2D\u533B\u8BA4\u4E3A\u809A\u8110\u901A\u5411\u4E94\u810F\uFF0C\u662F\u771F\u6C14\u5F80\u6765\u7684\u95E8\uFF0C\u809A\u8110\u5728\u4E2D\u533B\u91CC\u88AB\u79F0\u4E3A\u201C\u795E\u9619\u7A74\u201D\uFF0C\u6240\u4EE5\u8BA4\u4E3A\u5728\u8FD9\u91CC\u7528\u836F\u53EF\u4EE5\u4EA7\u751F\u5F88\u597D\u7684\u6548\u679C\u3002</p><p>\u53E4\u4EBA\u5BF9\u4EBA\u4F53\u6CA1\u6709\u6DF1\u5165\u4E86\u89E3\u65F6\uFF0C\u51ED\u501F\u4E00\u4E9B\u6734\u7D20\u7684\u60F3\u8C61\u8BA4\u4E3A\u4EBA\u4F53\u91CC\u6709\u7ECF\u7EDC\u548C\u7A74\u4F4D\uFF0C\u6709\u6C14\u8840\u4E4B\u7C7B\u7684\u7269\u8D28\u5728\u4EBA\u4F53\u91CC\u6D41\u52A8\u3002\u4F46\u8FD9\u4E9B\u90FD\u662F\u60F3\u8C61\u800C\u5DF2\uFF0C\u968F\u7740\u73B0\u4EE3\u89E3\u5256\u5B66\u7684\u5EFA\u7ACB\uFF0C\u6211\u4EEC\u5BF9\u4EBA\u4F53\u7684\u89E3\u5256\u7ED3\u6784\u5DF2\u7ECF\u5F88\u6E05\u695A\uFF0C\u4EBA\u4F53\u6709\u5FAA\u73AF\u548C\u795E\u7ECF\u7CFB\u7EDF\uFF0C\u6709\u8840\u7BA1\u795E\u7ECF\uFF0C\u4F46\u6CA1\u6709\u4E2D\u533B\u6982\u5FF5\u91CC\u7684\u90A3\u4E9B\u6C14\u8840\uFF0C\u4E5F\u6CA1\u6709\u7ECF\u7EDC\u548C\u7A74\u4F4D\u3002\u8FD9\u4E9B\u4E1C\u897F\u89E3\u5256\u4E0B\u627E\u4E0D\u5230\uFF0C\u4EEA\u5668\u4E5F\u68C0\u6D4B\u4E0D\u5230\u3002</p><p>\u6B63\u662F\u56E0\u4E3A\u4E0D\u5B58\u5728\u7ECF\u7EDC\u548C\u7A74\u4F4D\u8FD9\u4E9B\u4E1C\u897F\uFF0C\u6211\u4EEC\u5916\u79D1\u533B\u751F\u505A\u624B\u672F\u7684\u65F6\u5019\u624D\u53EA\u8003\u8651\u8840\u7BA1\u548C\u795E\u7ECF\u8FD9\u7C7B\u4E1C\u897F\uFF0C\u800C\u4E0D\u9700\u8981\u8003\u8651\u7ECF\u7EDC\u548C\u7A74\u4F4D\u3002\u6211\u4EEC\u624B\u672F\u4E2D\u770B\u5230\u51FA\u8840\u70B9\u4F1A\u53BB\u6B62\u8840\uFF0C\u800C\u4E0D\u9700\u8981\u53BB\u6B62\u6C14\uFF0C\u4E5F\u4E0D\u62C5\u5FC3\u5207\u5F00\u8EAB\u4F53\u4F1A\u6709\u5143\u6C14\u6CC4\u6F0F\u4EC0\u4E48\u3002</p><p>\u771F\u8981\u6709\u7A74\u4F4D\uFF0C\u6211\u4EEC\u5728\u8FD9\u4E48\u91CD\u8981\u7684\u201C\u795E\u9619\u7A74\u201D\u4E0A\u53C8\u662F\u5207\u53C8\u662F\u6345\uFF08\u6233\u5361\uFF09\u53C8\u662F\u7F1D\uFF0C\u4F30\u8BA1\u505A\u5B8C\u624B\u672F\u4EBA\u90FD\u534A\u6B7B\u4E0D\u6D3B\u4E86\uFF0C\u4F46\u8FD9\u4E9B\u5B69\u5B50\u4E5F\u90FD\u6062\u590D\u5F97\u5F88\u597D\uFF0C\u548C\u505A\u5176\u5B83\u90E8\u4F4D\u5207\u53E3\u7684\u5B69\u5B50\u5E76\u6CA1\u6709\u4EFB\u4F55\u533A\u522B\u3002</p><p>\u6240\u8C13\u7A74\u4F4D\u548C\u7ECF\u7EDC\u548C\u4F20\u8BF4\u4E2D\u7684\u201C\u9B3C\u201D\u6CA1\u4EC0\u4E48\u533A\u522B\uFF0C\u4F60\u4E0D\u76F8\u4FE1\u5C31\u4E0D\u4F1A\u6709\u95EE\u9898\uFF0C\u6309\u7167\u7A74\u4F4D\u548C\u7ECF\u7EDC\u7406\u8BBA\u6765\u5F00\u5C55\u7684\u6CBB\u7597\uFF0C\u65E0\u8BBA\u662F\u63A8\u62FF\u6309\u6469\uFF0C\u8FD8\u662F\u9488\u7078\u827E\u7078\uFF0C\u8FD8\u662F\u8D34\u809A\u8110\uFF0C\u90FD\u4E0D\u8FC7\u662F\u88C5\u795E\u5F04\u9B3C\u800C\u5DF2\u3002</p><p>\u5C31\u7B97\u809A\u8110\u662F\u4E00\u4E2A\u7A74\u4F4D\uFF0C\u4F60\u8981\u5728\u8FD9\u91CC\u7528\u836F\uFF0C\u8D34\u4E2A\u8110\u8D34\u4F1A\u4EA7\u751F\u4EC0\u4E48\u6548\u679C\uFF1F\u5148\u4E0D\u8BF4\u8110\u8D34\u91CC\u7684\u836F\u7269\u6709\u6CA1\u6709\u7528\uFF0C\u5C31\u7B97\u6709\u7528\uFF0C\u628A\u836F\u8D34\u809A\u8110\u5E76\u4E0D\u6BD4\u8D34\u5728\u5176\u5B83\u90E8\u4F4D\u66F4\u5F3A\u3002</p><p>\u7528\u836F\u6709\u90A3\u4E48\u591A\u79CD\u65B9\u5F0F\uFF0C\u9759\u8109\u3001\u808C\u8089\u3001\u76AE\u4E0B\u6CE8\u5C04\uFF0C\u53E3\u670D\uFF0C\u820C\u4E0B\u542B\u670D\uFF0C\u76F4\u80A0\u6813\u90FD\u662F\u7ED9\u836F\u65B9\u5F0F\uFF0C\u6548\u679C\u90FD\u6BD4\u8D34\u76AE\u80A4\u4E0A\u8981\u5FEB\u3002\u7ECF\u76AE\u7ED9\u836F\u4E5F\u6709\uFF0C\u6BD4\u5982\u96CC\u6FC0\u7D20\u8D34\u7247\uFF0C\u4E3B\u8981\u9760\u76AE\u80A4\u7684\u6BDB\u7EC6\u8840\u7BA1\u5438\u6536\uFF0C\u4F46\u56E0\u4E3A\u6211\u4EEC\u7684\u76AE\u80A4\u5C4F\u969C\u7684\u4F5C\u7528\uFF0C\u76AE\u80A4\u5BF9\u836F\u7269\u7684\u5438\u6536\u5F88\u6709\u9650\uFF0C\u53EA\u9002\u5408\u5BF9\u836F\u91CF\u9700\u6C42\u5F88\u5C0F\u7684\u5C11\u6570\u51E0\u79CD\u836F\u7269\uFF0C\u7EDD\u5927\u591A\u6570\u836F\u7269\u5F88\u96BE\u901A\u8FC7\u76AE\u80A4\u7ED9\u836F\u8FBE\u5230\u6709\u6548\u7684\u6CBB\u7597\u6D53\u5EA6\u3002</p><p>\u8FD9\u4E9B\u4E2D\u836F\u5373\u4FBF\u771F\u7684\u5F88\u795E\u5947\uFF0C\u4E5F\u80FD\u7ECF\u76AE\u5438\u6536\uFF0C\u4E0D\u7BA1\u662F\u8D34\u809A\u8110\u8FD8\u662F\u8D34\u540E\u80CC\uFF0C\u8FD8\u662F\u8D34\u989D\u5934\uFF0C\u6700\u7EC8\u8FD8\u662F\u8981\u8FDB\u5165\u8840\u6DB2\u53D1\u6325\u4F5C\u7528\u3002\u4F46\u809A\u8110\u76AE\u80A4\u4E5F\u6CA1\u6709\u4EC0\u4E48\u7279\u522B\uFF0C\u5B83\u6BD4\u524D\u989D\u3001\u524D\u81C2\u3001\u8033\u540E\u90FD\u539A\uFF0C\u8840\u7BA1\u4E5F\u4E0D\u6BD4\u5176\u5B83\u90E8\u4F4D\u66F4\u4E30\u5BCC\uFF0C\u5438\u6536\u6548\u679C\u4E5F\u4E0D\u4F1A\u66F4\u597D\u3002</p><p>\u8110\u8D34\u8981\u8BC1\u660E\u81EA\u5DF1\u6709\u7528\uFF0C\u9664\u4E86\u9700\u8981\u8BC1\u660E\u91CC\u9762\u7684\u4E2D\u836F\u6709\u7528\uFF0C\u8FD8\u8981\u8BC1\u660E\u80FD\u88AB\u809A\u8110\u76AE\u80A4\u5438\u6536\uFF0C\u8FD8\u8981\u8BC1\u660E\u8D34\u809A\u8110\u6709\u989D\u5916\u7684\u795E\u5947\u4E4B\u5904\u3002\u5F53\u7136\uFF0C\u5982\u679C\u4F60\u90FD\u8BC1\u660E\u4E0D\u4E86\uFF0C\u65E0\u8BBA\u4F60\u58F0\u79F0\u80FD\u6CBB\u8179\u6CFB\u3001\u6CBB\u4FBF\u79D8\u3001\u6CBB\u611F\u5192\u3001\u6CBB\u54B3\u55FD\uFF0C\u8FD8\u662F\u6CBB\u75D4\u75AE\u3001\u8FD8\u662F\u80FD\u51CF\u80A5\uFF0C\u4F60\u8BF4\u4E0D\u6E05\u539F\u7406\uFF0C\u4F46\u5982\u679C\u80FD\u505A\u4E2A\u968F\u673A\u53CC\u76F2\u5BF9\u7167\u5B9E\u9A8C\u6765\u8BC1\u660E\u4E00\u4E0B\u5B83\u7684\u6548\u679C\uFF0C\u6211\u4E5F\u4F1A\u76F8\u4FE1\u5B83\u7684\u795E\u5947\uFF0C\u53EF\u60DC\u6CA1\u6709\u54EA\u4E2A\u5382\u5BB6\u505A\u8FC7\u3002</p><p>\u5982\u679C\u8BC1\u660E\u4E0D\u4E86\u6548\u679C\u5C31\u62FF\u51FA\u53BB\u5356\u94B1\uFF0C\u90A3\u548C\u9A97\u94B1\u6709\u4EC0\u4E48\u533A\u522B\uFF1F</p><p>\u80AF\u5B9A\u6709\u4EBA\u53C8\u8981\u8BF4\u81EA\u5DF1\u5B69\u5B50\u5C31\u662F\u8D34\u8110\u8D34\u5C31\u597D\u4E86\u4E4B\u7C7B\u7684\u8BDD\u3002\u4E00\u65B9\u9762\u4EFB\u4F55\u4E00\u4E2A\u6CBB\u7597\u90FD\u53EF\u80FD\u4EA7\u751F\u5B89\u6170\u5242\u6548\u679C\uFF0C\u53E6\u5916\u4E00\u65B9\u9762\u5B69\u5B50\u7684\u8179\u6CFB\u5F88\u5927\u90E8\u5206\u90FD\u662F\u7531\u8F6E\u72B6\u75C5\u6BD2\u611F\u67D3\u5F15\u8D77\uFF0C\u8FD9\u79CD\u75BE\u75C5\u672C\u6765\u5230\u4E86\u4E00\u5468\u57FA\u672C\u90FD\u4F1A\u597D\uFF0C\u4F60\u8D34\u4E0D\u8D34\u4E1C\u897F\u5230\u4E86\u65F6\u95F4\u4E00\u822C\u90FD\u4F1A\u597D\u3002</p><p>\u4E0D\u80FD\u8BF4\u6211\u611F\u5192\u4E86\u8FD8\u575A\u6301\u5403\u996D\uFF0C\u5403\u4E867\u5929\u611F\u5192\u597D\u4E86\uFF0C\u5C31\u8BA4\u4E3A\u5403\u996D\u80FD\u6CBB\u611F\u5192\u3002\u4E0D\u7ECF\u4E25\u683C\u4E34\u5E8A\u8BD5\u9A8C\u9A8C\u8BC1\u5C31\u5BA3\u79F0\u6709\u7597\u6548\uFF0C\u65E0\u975E\u5C31\u662F\u73A9\u5FFD\u60A0\u7F62\u4E86\u3002</p><p>\u5982\u679C\u771F\u662F\u4E2A\u83CC\u75E2\uFF0C\u4F60\u770B\u770B\u8D34\u8110\u8D34\u80FD\u4E0D\u80FD\u8D34\u597D\uFF1F\u4F60\u5982\u679C\u4ED4\u7EC6\u53BB\u7814\u7A76\u4E00\u4E0B\u4E00\u4E9B\u8110\u8D34\u7684\u8BF4\u660E\u4E66\uFF0C\u4F60\u4E5F\u53EF\u80FD\u4F1A\u6CE8\u610F\u5230\u201C\u611F\u67D3\u6027\u8179\u6CFB\u8981\u53BB\u533B\u9662\u5C31\u8BCA\u201D\u4E4B\u7C7B\u7684\u5B57\u773C\uFF0C\u5382\u5BB6\u4E00\u70B9\u90FD\u4E0D\u50BB\u3002</p><p>\u8110\u8D34\u8FD9\u6837\u7684\u4EA7\u54C1\u5356\u5F97\u8FD9\u4E48\u597D\uFF0C\u65E0\u975E\u662F\u56FD\u4EBA\u4FE1\u4E2D\u533B\u7684\u592A\u591A\u4E86\uFF0C\u8110\u8D34\u8FD9\u79CD\u6CBB\u7597\u53C8\u548C\u5176\u5B83\u6D41\u884C\u504F\u65B9\u4E00\u6837\u5177\u5907\u201C\u7B80\u5355\u65B9\u4FBF\u53C8\u7EFF\u8272\u201D\u7684\u7279\u70B9\uFF0C\u5382\u5BB6\u6293\u4F4F\u5BB6\u957F\u4E0D\u559C\u6B22\u7ED9\u5B69\u5B50\u6253\u9488\u5403\u836F\u7684\u5FC3\u7406\u505A\u505A\u5BA3\u4F20\uFF0C\u81EA\u7136\u5C31\u6D41\u884C\u5F00\u4E86\u3002</p><p>\u53E6\u5916\u7531\u4E8E\u533B\u7597\u4F53\u5236\u7B49\u95EE\u9898\uFF0C\u56FD\u5185\u6B63\u89C4\u7684\u533B\u7597\u673A\u6784\u7684\u8FC7\u5EA6\u6CBB\u7597\u4E5F\u5F88\u4E25\u91CD\uFF0C\u6297\u751F\u7D20\u548C\u8F93\u6DB2\u6CBB\u7597\u786E\u5B9E\u6EE5\u7528\uFF0C\u5BFC\u81F4\u5BB6\u957F\u4EEC\u7684\u9006\u53CD\u5FC3\u7406\u4E5F\u7ED9\u4E86\u8110\u8D34\u8FD9\u7C7B\u9A97\u94B1\u4EA7\u54C1\u7684\u7A7A\u95F4\u3002\u4F46\u4E0D\u60F3\u88AB\u6EE5\u7528\u836F\u7269\u4E5F\u4E0D\u7B49\u4E8E\u9700\u8981\u82B1\u51A4\u6789\u94B1\u4E70\u8110\u8D34\u6298\u817E\u5B69\u5B50\u3002</p><p>\u5B69\u5B50\u751F\u75C5\u4E86\u5728\u809A\u8110\u8D34\u4E00\u5757\u818F\u836F\uFF0C\u6D6A\u8D39\u94B1\u662F\u4E00\u65B9\u9762\uFF0C\u8BA9\u5B69\u5B50\u4E0D\u8212\u670D\uFF0C\u751A\u81F3\u8FC7\u654F\u662F\u53E6\u4E00\u65B9\u9762\uFF0C\u5982\u679C\u8FC7\u5EA6\u8FF7\u4FE1\u8FD9\u7C7B\u4EA7\u54C1\u7684\u529F\u6548\uFF0C\u771F\u7684\u6709\u95EE\u9898\u4E5F\u4E0D\u4E0A\u533B\u9662\u803D\u8BEF\u75C5\u60C5\u5C31\u8FFD\u6094\u83AB\u53CA\u4E86\u3002\u8110\u8D34\u4E0D\u8FC7\u662F\u5751\u5A03\u4E4B\u8D34\u3002</p><hr><p>\u9898\u56FE\u6765\u81EApixabay\uFF0C\u6388\u6743\u57FA\u4E8ECC0\u534F\u8BAE</p><p>\u8110\u8D34\u3001\u9000\u70ED\u8D34\u3001\u4E09\u4F0F\u8D34\u53EF\u4EE5\u5E76\u79F0\u4E3A\u201C\u5751\u5A03\u4E09\u8D34\u201D</p>',32),m=a("\u9000\u70ED\u8D34\u80FD\u9000\u70ED\u5417\uFF1F"),f={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651626180&idx=1&sn=48afd248cb0b87f042c22e5d1a825d1b&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},x=a("\u67E5\u770B\u539F\u6587");function b(u,k){const n=e("RouterLink"),r=e("ExternalLinkIcon");return c(),o(d,null,[h,p("p",null,[t(n,{to:"/s/__biz=MzA5NzE5ODIyOQ==&mid=200608995&idx=1&sn=1f8c868246b5db3c63ec7bd9c2a83b20&scene=21#wechat_redirect/"},{default:s(()=>[m]),_:1})]),p("p",null,[p("a",f,[x,t(r)])])],64)}var w=_(l,[["render",b]]);export{w as default};
