import{r as a,o as c,c as r,a as p,b as t,F as n,d as o,e as s}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const i={},_=o('<h3 id="\u5982\u4F55\u5E94\u5BF9-\u79CB\u5B63\u8179\u6CFB" tabindex="-1"><a class="header-anchor" href="#\u5982\u4F55\u5E94\u5BF9-\u79CB\u5B63\u8179\u6CFB" aria-hidden="true">#</a> \u5982\u4F55\u5E94\u5BF9\u201C\u79CB\u5B63\u8179\u6CFB\u201D</h3><p>\u6BCF\u5230\u79CB\u5929\uFF0C\u5929\u6C14\u8F6C\u51C9\uFF0C\u8179\u6CFB\u7684\u5B69\u5B50\u5C31\u4F1A\u591A\u8D77\u6765\u4E86\uFF0C\u539F\u56E0\u662F\u8F6E\u72B6\u75C5\u6BD2\u80A0\u708E\u5728\u79CB\u5B63\u591A\u53D1\uFF0C\u6240\u4EE5\u8F6E\u72B6\u75C5\u6BD2\u80A0\u708E\u53C8\u79F0\u4E3A\u201C\u79CB\u5B63\u8179\u6CFB\u201D\u3002\u5927\u5BB6\u53EF\u80FD\u4E0D\u77E5\u9053\u7684\u662F\uFF0C\u65F6\u81F3\u4ECA\u65E5\uFF0C\u8179\u6CFB\u4F9D\u7136\u662F5\u5C81\u4EE5\u4E0B\u513F\u7AE5\u6B7B\u4EA1\u7684\u7B2C\u4E8C\u5927\u539F\u56E0\u3002</p><p>\u636E\u4F30\u8BA1\uFF0C\u57282008\u5E74\uFF0C\u5168\u7403\u6709\u7EA645\u4E075\u5C81\u4EE5\u4E0B\u513F\u7AE5\u6B7B\u4E8E\u8F6E\u72B6\u75C5\u6BD2\u611F\u67D3\uFF0C\u5360\u56E0\u8179\u6CFB\u800C\u6B7B\u4EA1\u5B69\u5B50\u4E2D\u768437%\uFF0C\u53605\u5C81\u4EE5\u4E0B\u6B7B\u4EA1\u513F\u7AE5\u76845%\uFF0C\u6240\u4EE5\u79F0\u8F6E\u72B6\u75C5\u6BD2\u4E3A\u513F\u7AE5\u6740\u624B\u4E5F\u4E0D\u4E3A\u8FC7\u3002</p><p>\u8F6E\u72B6\u75C5\u6BD2\u7684\u4F20\u67D3\u6027\u5F88\u5F3A\uFF0C\u5B83\u5728\u4F53\u5916\u53EF\u4EE5\u5B58\u6D3B\u51E0\u4E2A\u5C0F\u65F6\u5230\u51E0\u4E2A\u6708\u3002\u5728\u6025\u6027\u671F\u75C5\u4EBA\u7684\u5927\u4FBF\u91CC\uFF0C\u6BCF1\u514B\u5927\u4FBF\u542B\u6709\u7684\u75C5\u6BD2\u6570\u91CF\u8D85\u8FC71000\u4EBF\u4E2A\uFF0C\u800C\u4E14\u5B69\u5B50\u5728\u51FA\u73B0\u75C7\u72B6\u4E4B\u524D\u5C31\u53EF\u4EE5\u6392\u51FA\u75C5\u6BD2\uFF0C\u4E00\u76F4\u6301\u7EED\u5230\u75C7\u72B6\u51FA\u73B0\u540E10\u5929\uFF0C\u6240\u4EE5\u5BB6\u5EAD\u3001\u5E7C\u513F\u56ED\u91CC\u5F88\u5BB9\u6613\u76F8\u4E92\u4F20\u64AD\u3002</p><p>\u5B69\u5B50\u611F\u67D3\u4E86\u8F6E\u72B6\u75C5\u6BD2\uFF0C\u4E00\u65E6\u8FC7\u4E861-7\u5929\u7684\u6F5C\u4F0F\u671F\uFF0C\u5F80\u5F80\u8868\u73B0\u4E3A\u53D1\u70E7\u548C\u5455\u5410\u3002\u5927\u7EA6\u4E00\u534A\u5B69\u5B50\u4F1A\u53D1\u70E7\uFF0C\u4F46\u5927\u591A\u662F\u4F4E\u70E7\uFF0C\u4F46\u4E5F\u6709\u7EA61/3\u7684\u5B69\u5B50\u4F1A\u8D85\u8FC739\u5EA6\uFF0C\u5927\u90E8\u5206\u5B69\u5B50\u4F1A\u6709\u5455\u5410\u3002\u53D1\u70E7\u5455\u5410\u6301\u7EED1-2\u5929\u540E\u5C31\u5F00\u59CB\u51FA\u73B0\u62C9\u6C34\u4E00\u6837\u7684\u5927\u4FBF\u4E86\uFF0C\u4E00\u5929\u53EF\u4EE510-20\u6B21\uFF0C\u597D\u5728\u8179\u6CFB\u4E00\u822C\u6301\u7EED3-8\u5929\u80FD\u81EA\u5DF1\u597D\u3002</p><p>\u867D\u7136\u662F\u81EA\u6108\u6027\u75BE\u75C5\uFF0C\u4F46\u56E0\u4E3A\u8F6E\u72B6\u75C5\u6BD2\u80A0\u708E\u4F20\u67D3\u6027\u5F3A\uFF0C\u53D1\u75C5\u7387\u9AD8\uFF0C\u8179\u6CFB\u80FD\u5BFC\u81F4\u8131\u6C34\u4F11\u514B\uFF0C\u8179\u6CFB\u6301\u7EED\u65F6\u95F4\u957F\u4F1A\u5E76\u53D1\u8425\u517B\u4E0D\u826F\uFF0C\u4EE5\u53CA\u5C11\u6570\u91CD\u75C7\u75C5\u4F8B\u53EF\u5BFC\u81F4\u5168\u8EAB\u6027\u53CD\u5E94\uFF0C\u6240\u4EE5\u5B83\u6BCF\u5E74\u4ECD\u7136\u593A\u8D70\u90A3\u4E48\u591A\u5B69\u5B50\u7684\u6027\u547D\u3002</p><p>\u867D\u7136\u5927\u90E8\u5206\u5B69\u5B50\u7ECF\u8FC7\u7EA6\u4E00\u5468\u90FD\u80FD\u81EA\u5DF1\u6062\u590D\uFF0C\u4F46\u8FD9\u4E2A\u8FC7\u7A0B\u4E5F\u8DB3\u4EE5\u8BA9\u5F88\u591A\u5BB6\u957F\u60CA\u614C\u5931\u63AA\u3002\u6BD4\u5982\u6700\u521D\u8868\u73B0\u51FA\u7684\u53D1\u70E7\uFF0C\u5728\u6CA1\u6709\u51FA\u73B0\u5176\u5B83\u75C7\u72B6\u7684\u65F6\u5019\uFF0C\u53EF\u80FD\u88AB\u5BB6\u957F\u4EEC\u5224\u65AD\u4E3A\u611F\u5192\uFF0C\u7136\u540E\u5C31\u7ED9\u5B69\u5B50\u5403\u4E0A\u4E86\u611F\u5192\u836F\u3002\u5982\u679C\u51FA\u73B0\u7684\u662F\u5455\u5410\uFF0C\u53C8\u8981\u62C5\u5FC3\u662F\u4E0D\u662F\u5403\u574F\u4E86\u4E1C\u897F\uFF0C\u5F00\u59CB\u7ED9\u5B69\u5B50\u5403\u6B62\u5410\u836F\u4E86\u3002\u518D\u8FC7\u6765\uFF0C\u5B69\u5B50\u5F00\u59CB\u62C9\u809A\u5B50\u4E86\uFF0C\u770B\u7740\u5B69\u5B50\u4E0D\u505C\u7684\u5657\u5657\u5657\uFF0C\u5F88\u591A\u5BB6\u957F\u53C8\u5F00\u59CB\u5FC3\u585E\u4E86\uFF0C\u7136\u540E\u5C31\u5F00\u59CB\u601D\u5BC6\u8FBE\u5988\u54AA\u7231\u4E00\u8D77\u4E0A\uFF0C\u751A\u81F3\u6297\u751F\u7D20\u4E5F\u4E00\u8D77\u5403\u4E0A\u4E86\u3002</p><p>\u786E\u5B9E\uFF0C\u6240\u6709\u5BB6\u957F\u9762\u5BF9\u81EA\u5DF1\u5B69\u5B50\u751F\u75C5\u90FD\u5F88\u96BE\u6DE1\u5B9A\uFF0C\u5DF4\u4E0D\u5F97\u6240\u6709\u7684\u75C7\u72B6\u8FDC\u79BB\u5B69\u5B50\uFF0C\u8BA9\u4ED6\u8FDC\u79BB\u4E00\u5207\u75DB\u82E6\uFF0C\u4F46\u5F88\u591A\u4E8B\u4E0D\u80FD\u987A\u4EBA\u610F\u3002\u5373\u4FBF\u662F\u7F8E\u56FD\uFF0C5\u5C81\u4EE5\u5185\u7684\u5B69\u5B50\u57FA\u672C\u90FD\u4F1A\u88AB\u8F6E\u72B6\u75C5\u6BD2\u611F\u67D3\uFF0C\u5176\u4E2D4/5\u7684\u5B69\u5B50\u4F1A\u51FA\u73B0\u8F6E\u72B6\u75C5\u6BD2\u80A0\u708E\uFF0C\u6240\u4EE5\u5927\u90E8\u5206\u5B69\u5B50\u90FD\u96BE\u8EB2\u8FC7\u8F6E\u72B6\u75C5\u6BD2\uFF0C\u800C\u4E14\u76EE\u524D\u4E5F\u6CA1\u6709\u6709\u6548\u7684\u6297\u75C5\u6BD2\u836F\u7269\uFF0C\u8DDF\u611F\u5192\u4E00\u6837\uFF0C\u4E3B\u8981\u9760\u81EA\u5DF1\u597D\uFF0C\u65E0\u8BBA\u4F60\u6025\u6216\u4E0D\u6025\uFF0C\u90FD\u6CA1\u6709\u591A\u5C11\u529E\u6CD5\u53BB\u7F29\u77ED\u751F\u75C5\u7684\u8FC7\u7A0B\u3002</p><p>\u5927\u5BB6\u53EF\u80FD\u4E5F\u6CE8\u610F\u5230\uFF0C\u8F6E\u72B6\u75C5\u6BD2\u80A0\u708E\u6240\u8868\u73B0\u51FA\u6765\u7684\u53D1\u70E7\u3001\u5455\u5410\u3001\u8179\u6CFB\u75C7\u72B6\u5E76\u6CA1\u6709\u4EC0\u4E48\u7279\u5F02\u6027\uFF0C\u5176\u5B83\u7684\u80A0\u708E\u4E5F\u53EF\u80FD\u6709\u8FD9\u4E9B\u75C7\u72B6\uFF0C\u6839\u636E\u53D1\u75C5\u7684\u8FC7\u7A0B\u548C\u5B63\u8282\uFF0C\u533B\u751F\u53EF\u80FD\u4F1A\u6709\u4E2A\u5927\u6982\u7684\u5224\u65AD\uFF0C\u4F46\u5982\u679C\u6CA1\u6709\u505A\u76F8\u5173\u75C5\u539F\u68C0\u6D4B\uFF0C\u4ED6\u4EEC\u4E5F\u6CA1\u529E\u6CD5\u786E\u5B9A\u662F\u4E0D\u662F\uFF0C\u66F4\u522B\u8BF4\u5BB6\u957F\u81EA\u5DF1\u4E86\uFF0C\u597D\u5728\u7684\u662F\uFF0C\u6240\u6709\u7684\u8179\u6CFB\u5E94\u5BF9\u7684\u539F\u5219\u5927\u540C\u5C0F\u5F02\u3002</p><p>\u5BF9\u4E8E\u8F6E\u72B6\u75C5\u6BD2\u80A0\u708E\uFF0C\u56E0\u4E3A\u6CA1\u6709\u6709\u6548\u7684\u6297\u75C5\u6BD2\u836F\u7269\uFF0C\u6240\u4EE5\u6CBB\u7597\u7684\u76EE\u6807\u4E0D\u662F\u53BB\u6CBB\u6108\u5B83\uFF0C\u800C\u662F\u7ED9\u5E2E\u52A9\u5B69\u5B50\u625B\u8FC7\u751F\u75C5\u7684\u8FC7\u7A0B\uFF0C\u7B49\u4ED6\u81EA\u5DF1\u597D\u3002\u5C31\u50CF\u98CE\u66B4\u5DF2\u7ECF\u6765\u4E86\u4F60\u963B\u6B62\u4E0D\u4E86\uFF0C\u4F60\u6240\u80FD\u505A\u7684\u662F\u5B88\u4F4F\u81EA\u5DF1\u7684\u623F\u5B50\u4E0D\u8981\u88AB\u98CE\u66B4\u5439\u57AE\u3002</p><p>\u56E0\u4E3A\u8F6E\u72B6\u75C5\u6BD2\u80A0\u708E\u4F1A\u9020\u6210\u5B69\u5B50\u77ED\u65F6\u95F4\u5185\u5927\u91CF\u4E22\u5931\u6C34\u5206\u548C\u7535\u89E3\u8D28\uFF0C\u6240\u4EE5\u4FDD\u8BC1\u5B69\u5B50\u4E0D\u8131\u6C34\u662F\u9996\u8981\u76EE\u6807\uFF0C\u540C\u65F6\u8981\u7EF4\u6301\u5B69\u5B50\u7684\u7535\u89E3\u8D28\u548C\u8425\u517B\u7684\u5E73\u8861\u3002</p><p>\u5B69\u5B50\u6BCF\u516C\u65A4\u4F53\u91CD\u5BF9\u6C34\u548C\u7535\u89E3\u8D28\u7684\u9700\u6C42\u91CF\u6BD4\u6210\u4EBA\u8981\u5927\uFF0C\u6240\u4EE5\u5B69\u5B50\u4E5F\u66F4\u5BB9\u6613\u51FA\u73B0\u8131\u6C34\u3002\u8F7B\u4E2D\u5EA6\u8131\u6C34\u7684\u65F6\u5019\u5B69\u5B50\u53EF\u80FD\u51FA\u73B0\u75B2\u4E4F\u6216\u8005\u70E6\u8E81\u4E0D\u5B89\uFF0C\u53E3\u6E34\uFF0C\u53E3\u5507\u5E72\uFF0C\u773C\u7A9D\u8F7B\u5EA6\u51F9\u9677\uFF0C\u76AE\u80A4\u6CA1\u5F39\u6027\uFF0C\u56DB\u80A2\u51C9\uFF0C\u5C3F\u5C11\u3002\u91CD\u5EA6\u8131\u6C34\u7684\u65F6\u5B69\u5B50\u5C31\u53EF\u80FD\u51FA\u73B0\u6DE1\u6F20\u3001\u660F\u8FF7\u3001\u4E0D\u559D\u6C34\uFF0C\u773C\u7A9D\u6DF1\u9677\uFF0C\u8FD9\u65F6\u5C31\u5F88\u5371\u9669\u4E86\u3002</p><p>\u8131\u6C34\u4F53\u5F81\u7684\u5224\u65AD\u6709\u4E00\u5B9A\u7684\u4E3B\u89C2\u6027\uFF0C\u7ECF\u9A8C\u4E0D\u8DB3\u7684\u533B\u751F\u5224\u65AD\u4E5F\u4F1A\u4E0D\u51C6\uFF0C\u5BB6\u957F\u7684\u4F18\u52BF\u662F\u966A\u4F34\u5B69\u5B50\u7684\u65F6\u95F4\u6BD4\u8F83\u591A\uFF0C\u89C2\u5BDF\u65F6\u95F4\u66F4\u591A\u66F4\u6301\u4E45\uFF0C\u53E6\u5916\u6BD4\u533B\u751F\u66F4\u4E86\u89E3\u5B69\u5B50\u5E73\u65F6\u7684\u72B6\u51B5\uFF0C\u751F\u75C5\u7684\u65F6\u5019\u66F4\u597D\u5BF9\u6BD4\uFF0C\u4F46\u8981\u9760\u5BB6\u957F\u81EA\u5DF1\u51C6\u786E\u5224\u65AD\u8131\u6C34\u7A0B\u5EA6\u8FD8\u662F\u5F88\u96BE\uFF0C\u800C\u4E14\u8131\u6C34\u8FDB\u5C55\u53EF\u80FD\u4F1A\u5F88\u5FEB\uFF0C\u5982\u679C\u53D1\u73B0\u5B69\u5B50\u5B69\u5B50\u7CBE\u795E\u53CD\u5E94\u4E0D\u5BF9\u548C\u5C3F\u5C11\u6216\u5176\u5B83\u5FC3\u91CC\u6CA1\u5E95\u7684\u7B49\u60C5\u51B5\u4E00\u5B9A\u8981\u53CA\u65F6\u53BB\u533B\u9662\uFF0C\u4EE5\u514D\u803D\u8BEF\u75C5\u60C5\u3002</p><p>\u8F7B\u4E2D\u5EA6\u8131\u6C34\u53EF\u4EE5\u901A\u8FC7\u53E3\u670D\u6765\u8865\u6DB2\uFF0C\u7528\u53E3\u670D\u8865\u6DB2\u76D0\u5151\u6C34\u7ED9\u5B69\u5B50\u559D\uFF0C\u4E0D\u4EC5\u53EF\u4EE5\u8865\u5145\u6C34\u5206\uFF0C\u8FD8\u53EF\u4EE5\u8865\u5145\u7535\u89E3\u8D28\uFF0C\u7B2C\u4E09\u4EE3\u8865\u6DB2\u76D0\uFF0C\u4E5F\u5C31\u662F\u4F4E\u6E17\u8865\u6DB2\u76D0\uFF0C\u5F88\u4FBF\u5B9C\u4E5F\u5F88\u5B89\u5168\uFF0C\u662FWHO\u6240\u63A8\u8350\u7684\uFF0C\u4E0D\u5EFA\u8BAE\u5BB6\u957F\u81EA\u5DF1\u7ED9\u5B69\u5B50\u8C03\u76D0\u6C34\u559D\u3002\u91CD\u5EA6\u8131\u6C34\u5E94\u8BE5\u5C3D\u91CF\u9884\u9632\uFF0C\u4E00\u65E6\u51FA\u73B0\u4E5F\u4E00\u5B9A\u8981\u5C3D\u65E9\u53BB\u533B\u9662\u3002</p><p>\u5982\u679C\u5B69\u5B50\u6CA1\u6709\u9891\u7E41\u5455\u5410\uFF0C\u8179\u80C0\u7B49\u60C5\u51B5\uFF0C\u80FD\u53E3\u670D\u8865\u6DB2\u76D0\uFF0C\u901A\u5E38\u4E5F\u5C31\u53EF\u4EE5\u5C1D\u8BD5\u6B63\u5E38\u8FDB\u98DF\u3002\u957F\u65F6\u95F4\u8179\u6CFB\u53EF\u80FD\u5F71\u54CD\u80A0\u7ED2\u6BDB\uFF0C\u4F46\u662F\u80A0\u5B50\u4ECD\u7136\u6709\u5438\u6536\u7684\u529F\u80FD\u3002</p><p>\u4E00\u65E6\u901A\u8FC7\u8865\u6DB2\u628A\u8131\u6C34\u7EA0\u6B63\u5F97\u5DEE\u4E0D\u591A\u4E86\uFF0C\u5C31\u53EF\u4EE5\u5C3D\u65E9\u6062\u590D\u5F80\u5E38\u4E00\u6837\u7684\u6BCD\u4E73\u6216\u8005\u914D\u65B9\u5976\u5582\u517B\uFF0C\u4E0D\u8981\u7A00\u91CA\uFF0C\u4E0D\u8FC7\u4E24\u6B21\u54FA\u4E73\u4E4B\u95F4\u9F13\u52B1\u53E3\u670D\u8865\u6DB2\u76D0\u3002\u5E73\u65F6\u5403\u7684\u7C73\u996D\uFF0C\u9762\u5305\uFF0C\u7626\u8089\uFF0C\u9178\u5976\uFF0C\u6C34\u679C\uFF0C\u852C\u83DC\uFF0C\u53EA\u8981\u6709\u80C3\u53E3\uFF0C\u613F\u610F\u5403\uFF0C\u4E5F\u53EF\u4EE5\u50CF\u5F80\u5E38\u4E00\u6837\u5403\u3002\u4F46\u662F\uFF0C\u8179\u6CFB\u671F\u95F4\u5E94\u8BE5\u907F\u514D\u679C\u6C41\u3001\u53EF\u4E50\u53CA\u5176\u5B83\u9AD8\u7CD6\u9AD8\u8102\u98DF\u7269\uFF0C\u4EE5\u514D\u52A0\u91CD\u8179\u6CFB\u3002</p><p>\u53E6\u5916\uFF0C\u8179\u6CFB\u65F6\u95F4\u957F\uFF0C\u6709\u65F6\u4F1A\u7EE7\u53D1\u4E73\u7CD6\u4E0D\u8010\u53D7\u3002\u5982\u679C\u5B9D\u5B9D\u8179\u6CFB\u4E4B\u540E\uFF0C\u559D\u914D\u65B9\u5976\u7C89\u6216\u8005\u725B\u5976\uFF0C\u51FA\u73B0\u809A\u5B50\u75DB\uFF0C\u80C0\u6C14\uFF0C\u8179\u6CFB\u7B49\u75C7\u72B6\uFF0C\u90A3\u4E48\u53EF\u80FD\u662F\u7EE7\u53D1\u4E86\u4E73\u7CD6\u4E0D\u8010\u53D7\u3002\u8FD9\u79CD\u73B0\u8C61\u4F1A\u968F\u7740\u80A0\u9053\u7684\u6062\u590D\u9010\u6E10\u81EA\u884C\u6062\u590D\uFF0C\u5C0F\u5E7C\u513F\u5728\u6062\u590D\u671F\u95F4\uFF0C\u6BCD\u4E73\u5582\u517B\u7684\u4ECD\u7136\u53EF\u4EE5\u5C1D\u8BD5\u7EE7\u7EED\u6BCD\u4E73\u5582\u517B\uFF0C\u914D\u65B9\u5976\u5582\u517B\u7684\u5B9D\u5B9D\u5219\u53EF\u4EE5\u8003\u8651\u6682\u65F6\u4F7F\u7528\u65E0\u4E73\u7CD6\u914D\u65B9\u5976\u7C89\uFF0C\u800C\u5E74\u9F84\u8F83\u5927\u7684\u5B69\u5B50\uFF0C\u6BD4\u59822\u5468\u5C81\u4EE5\u540E\uFF0C\u5C31\u4E0D\u5FC5\u8981\u8003\u8651\u7279\u6B8A\u914D\u65B9\u5976\u7C89\uFF0C\u53EF\u4EE5\u8003\u8651\u9499\u52A0\u5F3A\u8C46\u5976\uFF0C\u9178\u5976\u6765\u6682\u65F6\u66FF\u4EE3\u666E\u901A\u725B\u5976\uFF0C\u4EE5\u4FDD\u8BC1\u9499\u6444\u5165\u3002</p><p>\u5728\u836F\u7269\u65B9\u9762\uFF0C\u56FD\u5185\u8179\u6CFB\u7684\u5B69\u5B50\u4E00\u822C\u90FD\u4F1A\u5403\u8499\u8131\u77F3\u6563\uFF0C\u8FD9\u79CD\u836F\u7269\u4E3B\u8981\u5728\u6B27\u6D32\u3001\u4E9A\u6D32\u548C\u975E\u6D32\u4F7F\u7528\uFF0C\u4E5F\u6709\u4E9B\u8D28\u91CF\u4E0D\u9AD8\u7684\u7814\u7A76\u8BA4\u4E3A\u5B83\u80FD\u7F29\u77ED\u8179\u6CFB\u65F6\u95F4\uFF08\u5BF9\u8BDD\u6846\u56DE\u590D<strong>\u8499\u8131\u77F3</strong>\u53EF\u8BE6\u7EC6\u4E86\u89E3\uFF09\u3002\u8179\u6CFB\u65F6\u5E38\u5403\u7684\u53E6\u5916\u4E00\u79CD\u836F\u5C31\u662F\u76CA\u751F\u83CC\uFF0C\u76EE\u524D\u6709\u4E9B\u7814\u7A76\u8BA4\u4E3A\u76CA\u751F\u83CC\uFF0C\u5305\u62EC\u4E73\u9178\u6746\u83CC\u3001\u53CC\u6B67\u6746\u83CC\u3001\u5E03\u62C9\u6C0F\u9175\u6BCD\u83CC\u7B49\uFF0C\u53EF\u4EE5\u5E2E\u52A9\u6062\u590D\u80A0\u9053\u5FAE\u751F\u6001\uFF0C\u80FD\u7F29\u77ED\u8179\u6CFB\u7684\u65F6\u95F4\uFF0C\u4F46\u5F53\u524D\u7684\u8BC1\u636E\u8FD8\u4E0D\u90A3\u4E48\u8DB3\u3002</p><p>\u8499\u8131\u77F3\u548C\u76CA\u751F\u83CC\u5BF9\u8179\u6CFB\u7684\u4F5C\u7528\u8BC1\u636E\u4E0D\u90A3\u4E48\u5145\u5206\uFF0C\u4F46\u8FD8\u6CA1\u53D1\u73B0\u4EC0\u4E48\u660E\u663E\u526F\u4F5C\u7528\uFF0C\u4F7F\u7528\u4E0A\u867D\u6709\u4E9B\u4E89\u8BAE\uFF0C\u4F46\u60F3\u7528\u4E5F\u53EF\u4EE5\u7528\u3002\u76F8\u53CD\u6709\u4E9B\u836F\u7269\u6709\u6548\u8BC1\u636E\u5F88\u5145\u5206\uFF0C\u6BD4\u5982\u950C\u8865\u5145\u5242\uFF0C\u76EE\u524D\u7684\u7814\u7A76\u8868\u660E\u5B83\u53EF\u4EE5\u663E\u8457\u964D\u4F4E\u53D1\u5C55\u4E2D\u56FD\u5BB6\u7684\u5B69\u5B50\u8179\u6CFB\u7A0B\u5EA6\uFF0C\u7F29\u77ED\u8179\u6CFB\u65F6\u95F4\uFF0C\u51CF\u5C11\u4E25\u91CD\u75C5\u4F8B\u6BD4\u4F8B\uFF0C\u8FD8\u53EF\u80FD\u53EF\u4EE5\u964D\u4F4E\u8179\u6CFB\u590D\u53D1\u673A\u4F1A\uFF0C\u4E5F\u88AB\u4E16\u754C\u536B\u751F\u7EC4\u7EC7\u548C\u8054\u5408\u56FD\u513F\u7AE5\u57FA\u91D1\u4F1A\u63A8\u8350\uFF0C\u5728\u56FD\u5185\u5374\u5F88\u5C11\u5E94\u7528\u3002\u6240\u4EE5\u5982\u679C\u5B69\u5B50\u8179\u6CFB\u65F6\u6216\u8179\u6CFB\u8FC7\u540E\uFF0C\u53EF\u4EE5\u7ED9\u5B69\u5B50\u540310-14\u5929\u7684\u950C\uFF0C 6\u4E2A\u6708\u4EE5\u4E0B\u7684\u5B69\u5B5010mg/\u5929\uFF0C6\u4E2A\u6708\u4EE5\u4E0A\u7684\u5B69\u5B5020mg/\u5929\uFF0C\u786B\u9178\u950C,\u918B\u9178\u950C\u548C\u8461\u8404\u7CD6\u9178\u950C\u90FD\u53EF\u4EE5\u3002</p><p>\u5F53\u7136\u8179\u6CFB\u7684\u5B69\u5B50\u4E0D\u5168\u662F\u8F6E\u72B6\u75C5\u6BD2\u611F\u67D3\u5F15\u8D77\uFF0C\u5373\u4FBF\u662F\u8F6E\u72B6\u75C5\u6BD2\u611F\u67D3\u4E5F\u6709\u4E9B\u5B69\u5B50\u5F88\u5371\u91CD\uFF0C\u751A\u81F3\u51FA\u73B0\u795E\u7ECF\u7CFB\u7EDF\u75C7\u72B6\uFF0C\u5373\u4FBF\u5728\u7F8E\u56FD\u4E5F\u6709\u5B69\u5B50\u56E0\u4E3A\u8F6E\u72B6\u75C5\u6BD2\u611F\u67D3\u800C\u6B7B\u4EA1\uFF0C\u6240\u4EE5\u4E0D\u662F\u6240\u6709\u7684\u5B69\u5B50\u90FD\u9002\u5408\u5728\u5BB6\u91CC\u89C2\u5BDF\u6CBB\u7597\u3002</p><p>\u9664\u4E86\u8981\u89C2\u5BDF\u524D\u9762\u8BF4\u8FC7\u7684\u8131\u6C34\u72B6\u51B5\uFF0C\u4E5F\u8981\u6CE8\u610F\u5176\u5B83\u72B6\u51B5\uFF0C\u6BD4\u5982\u6301\u7EED\u9891\u7E41\u5455\u5410\uFF0C\u5C24\u5176\u662F\u5410\u51FA\u9EC4\u8272\u7EFF\u8272\u7684\u4E1C\u897F\u65F6\u5019\u5E94\u8BE5\u8B66\u60D5\u80A0\u6897\u963B\uFF0C\u5927\u4FBF\u5E26\u8840\u7684\u65F6\u5019\u4E5F\u8981\u8B66\u60D5\u80A0\u5957\u53E0\u3001\u7EC6\u83CC\u6027\u80A0\u708E\u7B49\u60C5\u51B5\uFF0C\u5B69\u5B50\u8179\u6CFB\u7684\u65F6\u5019\u53CD\u590D\u53D1\u70E7\uFF0C\u4E5F\u8981\u8003\u8651\u7EC6\u83CC\u6027\u80A0\u708E\uFF0C\u662F\u5426\u4F7F\u7528\u6297\u83CC\u7D20\u542C\u4ECE\u533B\u751F\u7684\u5EFA\u8BAE\u3002</p><p>\u8179\u6CFB\u56E0\u4E3A\u80A0\u8815\u52A8\u5F02\u5E38\uFF0C\u4E5F\u5E38\u5E38\u4F34\u6709\u8179\u75DB\uFF0C\u4F46\u5927\u591A\u4E0D\u4E25\u91CD\uFF0C\u5982\u679C\u6301\u7EED\u8179\u75DB\uFF0C\u5C24\u5176\u662F\u6709\u56FA\u5B9A\u7684\u538B\u75DB\u533A\u57DF\uFF0C\u4E5F\u8981\u8B66\u60D5\u8179\u8154\u5185\u5176\u5B83\u611F\u67D3\u6BD4\u5982\u9611\u5C3E\u708E\u4E4B\u7C7B\uFF0C\u56E0\u4E3A\u5A74\u5E7C\u513F\u7684\u9611\u5C3E\u708E\u8868\u73B0\u53EF\u80FD\u5F88\u4E0D\u5178\u578B\uFF0C\u6709\u65F6\u5019\u751A\u81F3\u5C31\u8868\u73B0\u4E3A\u8179\u6CFB\uFF0C\u8FD9\u4E9B\u90FD\u9700\u8981\u5230\u533B\u9662\u8BA9\u533B\u751F\u8FDB\u884C\u9274\u522B\u3002\u79D1\u666E\u53EA\u662F\u4F20\u9012\u77E5\u8BC6\uFF0C\u6C38\u8FDC\u66FF\u4EE3\u4E0D\u4E86\u533B\u751F\u5F53\u9762\u7684\u8BCA\u7597\uFF0C\u5BB6\u957F\u78B0\u5230\u81EA\u5DF1\u5FC3\u91CC\u6CA1\u5E95\u7684\u4E8B\uFF0C\u8FD8\u662F\u65E9\u70B9\u4E0A\u533B\u9662\u3002</p><p>\u8F6E\u72B6\u75C5\u6BD2\u4E00\u5EA6\u88AB\u6210\u4E3A\u201C\u6C11\u4E3B\u75C5\u6BD2\u201D\uFF0C\u56E0\u4E3A\u65E0\u8BBA\u4F60\u51FA\u751F\u5728\u7A77\u56FD\u8FD8\u662F\u5BCC\u56FD\uFF0C\u4E5F\u65E0\u8BBA\u4F60\u51FA\u751F\u5728\u6E29\u5E26\u8FD8\u662F\u70ED\u5E26\u5730\u533A\uFF0C\u51FA\u751F\u540E\u51E0\u5E74\u90FD\u57FA\u672C\u8981\u611F\u67D3\uFF0C\u5B83\u5BF9\u6BCF\u4E2A\u5B69\u5B50\u90FD\u662F\u6C11\u4E3B\u800C\u516C\u5E73\u7684\u3002\u4F46\u81EA\u4ECE\u6709\u4E86\u8F6E\u72B6\u75C5\u6BD2\u75AB\u82D7\uFF0C\u7A77\u56FD\u7684\u5B69\u5B50\u548C\u5BCC\u56FD\u7684\u5B69\u5B50\u7ED3\u5C40\u5C31\u5F88\u4E0D\u4E00\u6837\u4E86\uFF0C\u56E0\u4E3A\u91CD\u75C7\u75C5\u4F8B\u57FA\u672C\u90FD\u662F\u521D\u6B21\u611F\u67D3\uFF0C\u63A5\u79CD\u4E86\u75AB\u82D7\u4E0D\u7B49\u4E8E\u4E0D\u4F1A\u518D\u611F\u67D3\uFF0C\u4F46\u76F8\u5F53\u4E8E\u5B8C\u6210\u4E86\u521D\u6B21\u611F\u67D3\uFF0C\u4F1A\u4EA7\u751F\u4FDD\u62A4\u6027\u6297\u4F53\uFF0C\u518D\u611F\u67D3\u7684\u75C7\u72B6\u5C31\u5F88\u8F7B\u5FAE\u751A\u81F3\u6CA1\u6709\u75C7\u72B6\u4E86\u3002</p><p>\u8F6E\u72B6\u75C5\u6BD2\u662F\u7ECF\u7CAA-\u53E3\u9014\u5F84\u4F20\u64AD\u7684\uFF0C\u56E0\u4E3A\u5B83\u7684\u4F20\u67D3\u6027\u5F88\u5F3A\uFF0C\u901A\u8FC7\u6CE8\u610F\u536B\u751F\u6761\u4EF6\u7B49\u63AA\u65BD\u4E5F\u5F88\u96BE\u5B8C\u5168\u9884\u9632\u611F\u67D3\uFF0C\u4F46\u536B\u751F\u6761\u4EF6\u76F8\u5BF9\u66F4\u5DEE\u7684\u53D1\u5C55\u4E2D\u56FD\u5BB6\u7684\u5B69\u5B50\uFF0C\u611F\u67D3\u5E74\u9F84\u8FD8\u662F\u8981\u65E9\u4E8E\u53D1\u8FBE\u56FD\u5BB6\u7684\u5B69\u5B50\u3002\u6B64\u5916\uFF0C\u5927\u591A\u6570\u5176\u5B83\u7684\u513F\u7AE5\u8179\u6CFB\u4E5F\u548C\u996E\u98DF\u3001\u536B\u751F\u6761\u4EF6\u6709\u5173\uFF0C\u8FD9\u4E5F\u662F\u4E3A\u4EC0\u4E48\u56E0\u8179\u6CFB\u800C\u6B7B\u4EA1\u7684\u5B69\u5B50\u5927\u591A\u5728\u975E\u6D32\u548C\u5357\u4E9A\u5730\u533A\u3002</p><p>\u9664\u4E86\u63A5\u79CD\u8F6E\u72B6\u75C5\u6BD2\u75AB\u82D7\uFF0C\u63A8\u884C\u6BCD\u4E73\u5582\u517B\uFF0C\u7EF4\u6301\u5B69\u5B50\u8425\u517B\u5E73\u8861\uFF0C\u505A\u597D\u996E\u98DF\u536B\u751F\uFF0C\u8BA9\u5B69\u5B50\u52E4\u6D17\u624B\u4E5F\u662F\u9884\u9632\u5B69\u5B50\u8179\u6CFB\u7684\u4E3B\u8981\u65B9\u6CD5\u3002\u7236\u6BCD\u4EEC\u4E0E\u5176\u7B49\u5B69\u5B50\u8179\u6CFB\u65F6\u5FC3\u585E\u4E0D\u5DF2\uFF0C\u4E0D\u5982\u5E73\u65F6\u7ED9\u4ED6\u4EEC\u505A\u597D\u9884\u9632\u3002\uFF08\u672C\u6587\u9996\u53D1\u4E8E2014\u5E74\uFF0C\u6709\u5220\u6539\uFF0C\u9898\u56FE\u6765\u81EAPixabay\uFF09</p><hr><p>\u76F8\u5173\u533B\u751F\uFF1A\u5D14\u548F\u671B\uFF0C\u513F\u79D1\u526F\u4E3B\u4EFB\u533B\u751F</p><p>\u957F\u6309\u4E0B\u9762\u4E8C\u7EF4\u7801\u52A0\u5173\u6CE8\uFF0C\u53EF\u5411\u5D14\u533B\u751F\u54A8\u8BE2\uFF1A\u8179\u6CFB\u3001\u54B3\u55FD\u3001\u53D1\u70E7\u3001\u8FC7\u654F\u6027\u75BE\u75C5\u7B49\u3002</p>',28),h={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651626399&idx=1&sn=0de21f65d78759b1c1ffbc1c15107a32&chksm=8b5cb265bc2b3b731820e712bc235af5c774786c04b15d38399bd65cf19823de378014a5184c&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},l=s("\u67E5\u770B\u539F\u6587");function m(b,f){const e=a("ExternalLinkIcon");return c(),r(n,null,[_,p("p",null,[p("a",h,[l,t(e)])])],64)}var g=d(i,[["render",m]]);export{g as default};
