import{r as t,o as n,c as o,a as p,b as e,F as s,d as a,e as c}from"./app.9aaa2256.js";import{_ as g}from"./plugin-vue_export-helper.21dcd24c.js";const d={},_=a('<h3 id="\u5B69\u5B50\u62B5\u89E6\u53BB\u5E7C\u513F\u56ED-\u8FD9\u91CC\u67099\u4E2A\u5C0F\u5EFA\u8BAE" tabindex="-1"><a class="header-anchor" href="#\u5B69\u5B50\u62B5\u89E6\u53BB\u5E7C\u513F\u56ED-\u8FD9\u91CC\u67099\u4E2A\u5C0F\u5EFA\u8BAE" aria-hidden="true">#</a> \u5B69\u5B50\u62B5\u89E6\u53BB\u5E7C\u513F\u56ED\uFF0C\u8FD9\u91CC\u67099\u4E2A\u5C0F\u5EFA\u8BAE</h3><p>\u7238\u7238\u5988\u5988\u90FD\u77E5\u9053\uFF0C\u6BCF\u5E74\u5F00\u5B66\uFF0C\u521D\u9001\u5B69\u5B50\u53BB\u4E0A\u5B66\u7684\u65F6\u5019\uFF0C\u6709\u4E9B\u5B69\u5B50\u4F1A\u611F\u5230\u4E0D\u5B89\u548C\u54ED\u95F9\uFF0C\u8FD9\u662F\u5206\u79BB\u7126\u8651\u7684\u8868\u73B0\u3002</p><p>\u53EF\u662F\uFF0C\u4E00\u6BB5\u65F6\u95F4\u8FC7\u53BB\uFF0C\u5B69\u5B50\u4E5F\u4E0D\u54ED\u4E86\uFF0C\u4F46\u5374\u5E38\u5E38\u5728\u5BB6\u62B1\u6028\uFF0C\u8868\u8FBE\u201C\u4E0D\u60F3\u4E0A\u5B66\u201D\u7684\u610F\u613F\uFF0C\u6216\u8005\u5BF9\u5B66\u6821\u4EA7\u751F\u62B5\u89E6\u60C5\u7EEA\u3002\u5C24\u5176\u662F\u597D\u4E0D\u5BB9\u6613\u575A\u6301\u4E00\u4E2A\u6708\u4E0A\u5E7C\u513F\u56ED\u7684\u201C\u653B\u575A\u6218\u201D\u7684\u6548\u679C\u88AB\u5373\u5C06\u5230\u6765\u7684\u56FD\u5E86\u5047\u671F\u201C\u6E05\u96F6\u201D\u3002</p><p>\u4E00\u4E9B\u5BB6\u957F\u5BF9\u6B64\u611F\u5230\u5F88\u7591\u60D1\uFF1A\u201C\u4E0D\u54ED\u4E0D\u5C31\u662F\u4EE3\u8868\u5206\u79BB\u7126\u8651\u8FC7\u53BB\u4E86\u5417\uFF1F\u5B69\u5B50\u4E3A\u4EC0\u4E48\u8FD8\u8001\u662F\u8FD9\u6837\u8BF4\u5462\uFF1F\u8BE5\u600E\u4E48\u529E\uFF1F\u201D</p><p>\u5176\u5B9E\uFF0C\u5B69\u5B50\u4E0D\u60F3\u53BB\u4E0A\u5B66\uFF0C\u901A\u5E38\u662F\u56E0\u4E3A\u9002\u5E94\u671F\u95F4\u7684\u5404\u79CD\u4E0D\u4E60\u60EF\u3002</p><p>\u6211\u4EEC\u6362\u4F4D\u601D\u8003\u4E00\u4E0B\uFF1A</p><p>\u53BB\u5E7C\u513F\u56ED\uFF0C\u610F\u5473\u7740\u548C\u719F\u6089\u7684\u4EB2\u4EBA\u5206\u5F00\uFF0C\u72EC\u81EA\u5230\u964C\u751F\u7684\u73AF\u5883\uFF0C\u548C\u964C\u751F\u7684\u8001\u5E08\u540C\u5B66\u4E00\u8D77\u5403\u996D\u3001\u7761\u89C9\uFF0C\u5F97\u81EA\u5DF1\u53BB\u4E0A\u5395\u6240\uFF0C\u9047\u5230\u56F0\u96BE\u53EA\u80FD\u5BFB\u6C42\u4E0D\u719F\u6089\u7684\u5927\u4EBA\u5E2E\u5FD9\uFF0C\u751F\u6D3B\u89C4\u5F8B\u548C\u5B89\u6392\u4E5F\u90FD\u548C\u5BB6\u91CC\u4E0D\u4E00\u6837......</p><p>\u8F6C\u5FF5\u60F3\u60F3\uFF0C\u521A\u4E0A\u5B66\u7684\u5B69\u5B50\u8FD8\u771F\u662F\u633A\u4E0D\u5BB9\u6613\u7684\uFF0C\u4E0D\u662F\u5417\uFF1F</p><p>\u867D\u7136\u5927\u591A\u6570\u5B69\u5B50\u80FD\u591F\u5FEB\u901F\u9002\u5E94\u5E7C\u513F\u56ED\u7684\u72EC\u7ACB\u751F\u6D3B\uFF0C\u4F46\u5BF9\u4E8E\u90A3\u4E9B\u4E0A\u5B66\u6709\u60C5\u7EEA\uFF0C\u53CD\u590D\u8BF4\u7740\u4E0D\u559C\u6B22\u4E0D\u5F00\u5FC3\u7684\u5B69\u5B50\uFF0C\u5BB6\u957F\u53EF\u4EE5\u505A\u4E9B\u4EC0\u4E48\u6765\u5E2E\u52A9\u4ED6\u4EEC\u5B8C\u6210\u8FD9\u4E2A\u8FC7\u6E21\u5462\uFF1F\u6211\u4EEC\u63D0\u4F9B\u4E86\u4E5D\u4E2A\u5C0F\u5EFA\u8BAE\uFF0C\u7ED9\u6709\u6B64\u56F0\u6270\u7684\u7236\u6BCD\u4F5C\u4E3A\u53C2\u8003\uFF1A</p><p><strong>01</strong></p><p><strong>\u540C\u6B65\u5BB6\u5EAD\u548C\u5B66\u6821\u7684\u751F\u6D3B\u4F5C\u606F</strong></p><p>\u8C03\u6574\u5B69\u5B50\u7684\u751F\u6D3B\u4F5C\u606F\u65F6\u95F4\uFF0C\u5C3D\u53EF\u80FD\u8BA9\u4ED6\u5728\u5BB6\u91CC\u7684\u751F\u6D3B\u8282\u594F\u4E0E\u5E7C\u513F\u56ED\u540C\u6B65\u3002\u5C24\u5176\u662F\u5728\u52B3\u52A8\u8282\u3001\u56FD\u5E86\u8282\u8FD9\u4E9B\u5C0F\u957F\u5047\u7684\u65F6\u5019\uFF0C\u4FDD\u6301\u751F\u6D3B\u7684\u89C4\u5F8B\u6709\u52A9\u4E8E\u5B69\u5B50\u751F\u7269\u949F\u7684\u7A33\u5B9A\uFF0C\u907F\u514D\u8FC7\u52B3\u7B49\u72B6\u6001\u5F71\u54CD\u60C5\u7EEA\u3001\u98DF\u6B32\u7B49\uFF0C\u51CF\u5C11\u9002\u5E94\u5B66\u6821\u7684\u751F\u6D3B\u8282\u594F\u5E26\u6765\u7684\u6311\u6218\u3002</p><p><strong>02</strong></p><p><strong>\u52A0\u5F3A\u5B69\u5B50\u81EA\u7406\u80FD\u529B\u7684\u5B66\u4E60</strong></p><p>\u81EA\u7406\u80FD\u529B\u5305\u62EC\u81EA\u5DF1\u72EC\u7ACB\u5403\u996D\u3001\u5982\u5395\u3001\u7761\u89C9\uFF0C\u6216\u8005\u5728\u7761\u89C9\u4E0A\u5B66\u4E60\u65B0\u5165\u7761\u6A21\u5F0F\uFF08\u5982\u5403\u996D\u2014\u6D17\u624B\u2014\u6F31\u53E3\u2014\u5982\u5395\u2014\u5230\u5E8A\u4E0A\u2014\u62C9\u7A97\u5E18\u2014\u5F00\u59CB\u7761\u89C9\uFF09\u3002</p><p>\u5982\u679C\u4ED6\u8FD8\u4E0D\u80FD\u72EC\u7ACB\u5168\u90E8\u5B8C\u6210\uFF0C\u81F3\u5C11\u8BA9\u4ED6\u5C1D\u8BD5\u505A\u81EA\u5DF1\u80FD\u505A\u7684\uFF0C\u5E76\u4E14\u6E05\u695A\u6709\u54EA\u4E9B\u9700\u8981\u5E2E\u5FD9\u7684\u5730\u65B9\uFF0C\u8BE5\u5982\u4F55\u5BFB\u6C42\u5E2E\u52A9\u3002</p><p><strong>03</strong></p><p><strong>\u63D0\u524D\u4E86\u89E3\u201C\u5728\u5E7C\u513F\u56ED\u7684\u4E00\u5929\u201D</strong></p><p>\u901A\u8FC7\u7ED8\u672C\u8BA9\u5B69\u5B50\u4E86\u89E3\u201C\u4E0A\u5E7C\u513F\u56ED\u201D\u662F\u600E\u4E48\u4E00\u56DE\u4E8B\uFF0C\u4F8B\u5982\uFF1A\u7238\u7238/\u5988\u5988\u9001\u4F60\u53BB\u4E0A\u5B66\uFF0C\u5728\u5B66\u6821\u548C\u8001\u5E08\u3001\u5C0F\u670B\u53CB\u73A9\u6E38\u620F\u3001\u5403\u996D\u548C\u7761\u89C9\uFF0C\u653E\u5B66\u540E\u7238\u7238/\u5988\u5988\u53BB\u63A5\u4F60\u56DE\u5BB6\u3002</p><p><strong>04</strong></p><p><strong>\u521B\u9020\u548C\u63D0\u4F9B\u4E0E\u5E7C\u513F\u56ED\u5C0F\u670B\u53CB\u76F8\u5904\u7684\u673A\u4F1A</strong></p><p>\u5BB6\u957F\u5728\u63A5\u9001\u5B69\u5B50\u7684\u65F6\u5019\uFF0C\u53EF\u4EE5\u7559\u610F\u6709\u6CA1\u6709\u4ED6\u6BD4\u8F83\u5E38\u63A5\u89E6\u6216\u559C\u6B22\u7684\u5C0F\u670B\u53CB\uFF1F\u6709\u6CA1\u6709\u4F4F\u5728\u9644\u8FD1\u3001\u80FD\u591F\u5E38\u5E38\u89C1\u9762\u73A9\u800D\u7684\u540C\u4F34\uFF1F</p><p>\u5982\u679C\u6709\uFF0C\u653E\u5B66\u6216\u653E\u5047\u540E\uFF0C\u5927\u4EBA\u53EF\u4EE5\u7EA6\u4E2A\u65F6\u95F4\u7ED9\u5B69\u5B50\u4EEC\u805A\u805A\uFF0C\u4ECE\u76F8\u5BF9\u56FA\u5B9A\u7684\u540C\u4F34\u5F00\u59CB\uFF0C\u589E\u52A0\u719F\u6089\u548C\u6E38\u620F\u7684\u673A\u4F1A\u3002\u8FD9\u6837\u7684\u8BDD\uFF0C\u5B69\u5B50\u5728\u964C\u751F\u7684\u5730\u65B9\u9047\u5230\u719F\u6089\u7684\u540C\u4F34\u4F1A\u66F4\u52A0\u5BB9\u6613\u653E\u677E\uFF0C\u4ECE\u800C\u589E\u52A0\u5BF9\u65B0\u73AF\u5883\u7684\u63A5\u7EB3\u7A0B\u5EA6\u3002</p><p><strong>05</strong></p><p><strong>\u548C\u8001\u5E08\u4FDD\u6301\u826F\u597D\u7684\u6C9F\u901A</strong></p><p>\u5C24\u5176\u662F\u5F53\u5B69\u5B50\u6709\u4E00\u4E9B\u9700\u8981\u8001\u5E08\u7279\u522B\u5173\u6CE8\u548C\u7167\u987E\u7684\u5730\u65B9\u65F6\uFF0C\u5BB6\u957F\u66F4\u5E94\u8BE5\u79EF\u6781\u548C\u8001\u5E08\u6C9F\u901A\uFF0C\u8BA9\u8001\u5E08\u4E86\u89E3\u5B69\u5B50\u7684\u4E2A\u6027\u7279\u70B9\u4EE5\u53CA\u8868\u793A\u7279\u6B8A\u9700\u8981\u65F6\u7684\u4FE1\u53F7\uFF0C\u4F8B\u5982\u544A\u8BC9\u8001\u5E08\u5B69\u5B50\u60F3\u5C3F\u5C3F\u6216\u4E0D\u77E5\u9053\u600E\u4E48\u529E\u7684\u65F6\u5019\uFF0C\u4E00\u822C\u4F1A\u6709\u4EC0\u4E48\u8868\u73B0\uFF1F\u7761\u89C9\u524D\u6709\u4EC0\u4E48\u4E60\u60EF\u6216\u52A8\u4F5C\uFF0C\u5B69\u5B50\u7684\u54EA\u4E9B\u5C0F\u52A8\u4F5C\u662F\u4EE3\u8868\u9700\u8981\u8001\u5E08\u5E2E\u52A9\u548C\u5B89\u629A\u7B49\u7B49\u3002</p><p>\u8FD9\u6837\uFF0C\u8001\u5E08\u624D\u80FD\u7ED9\u4E88\u9002\u5F53\u5730\u5E2E\u52A9\u3002</p><p><strong>06</strong></p><p><strong>\u4E0D\u8981\u53CD\u590D\u8FFD\u95EE\u5B69\u5B50\u5728\u5E7C\u513F\u56ED\u7684\u60C5\u51B5</strong></p><p>\u5373\u4FBF\u8BED\u8A00\u80FD\u529B\u53D1\u80B2\u5F88\u597D\uFF0C\u4F46\u901A\u5E38\u60C5\u51B5\u4E0B\uFF0C\u521A\u8FDB\u5165\u5E7C\u513F\u56ED\u7684\u5B69\u5B50\u8FD8\u4E0D\u5177\u5907\u80FD\u529B\u6E05\u695A\u81EA\u5DF1\u53D1\u751F\u7684\u4E8B\u60C5\u3001\u611F\u53D7\u3001\u4EA7\u751F\u60F3\u6CD5\u5E76\u4E14\u80FD\u51C6\u786E\u8868\u8FBE\u51FA\u6765\u3002</p><p>\u53CD\u590D\u5730\u8FFD\u95EE\uFF0C\u6BD4\u5982\u95EE\u5B69\u5B50\uFF0C\u201C\u5728\u5E7C\u513F\u56ED\u6709\u6CA1\u6709\u54ED\uFF1F\u662F\u4E0D\u662F\u4E0D\u5F00\u5FC3\uFF1F\u53CD\u800C\u53EF\u80FD\u4F1A\u628A\u5BB6\u957F\u7684\u62C5\u5FC3\u548C\u7126\u8651\u4F20\u9012\u7ED9\u5B69\u5B50\u3002</p><p>\u5982\u679C\u5BB6\u957F\u60F3\u8981\u4E86\u89E3\u5B69\u5B50\u5728\u5E7C\u513F\u56ED\u7684\u751F\u6D3B\uFF0C\u53EF\u4EE5\u5C1D\u8BD5\u7528\u201C\u89D2\u8272\u626E\u6F14\u201D\u7684\u65B9\u6CD5\u6765\u548C\u5B69\u5B50\u505A\u6E38\u620F\u3002\u6BD4\u5982\u5988\u5988\u626E\u6F14\u5B69\u5B50\u53BB\u4E0A\u5E7C\u513F\u56ED\uFF0C\u5B69\u5B50\u626E\u6F14\u8001\u5E08\u3002\u53EF\u4EE5\u5148\u4ECE\u4E00\u4E2A\u60C5\u8282\u6216\u51E0\u4E2A\u60C5\u8282\u505A\u8D77\uFF0C\u6BD4\u5982\u8FDB\u6821\u95E8\u3001\u505A\u65E9\u64CD\u3001\u4E0A\u5395\u6240\u3001\u5348\u7761\u7B49\u7B49\u3002</p><p><strong>07</strong></p><p><strong>\u5F15\u5BFC\u5B69\u5B50\u628A\u6CE8\u610F\u529B\u653E\u5728\u79EF\u6781\u7684\u611F\u53D7\u4E0A</strong></p><p>\u5BF9\u5B69\u5B50\u5728\u5E7C\u513F\u56ED\u7684\u6D3B\u52A8\u8868\u793A\u8D5E\u8D4F\uFF0C\u4F8B\u5982\uFF1A\u201C\u54C7\uFF0C\u4F60\u73A9\u8FD9\u4E2A\u7B11\u5F97\u771F\u5F00\u5FC3\uFF01\u201D\u201C\u55EF\u55EF\uFF0C\u5728\u5E7C\u513F\u56ED\u53EF\u4EE5\u73A9\u5230\u4E0D\u4E00\u6837\u7684\u6E38\u620F\uFF0C\u771F\u6709\u610F\u601D\uFF01\u201D\u3002</p><p><strong>08</strong></p><p><strong>\u5B66\u4F1A\u503E\u542C\u548C\u63A5\u7EB3\u5B69\u5B50\u7684\u8D1F\u9762\u60C5\u7EEA</strong></p><p>\u5982\u679C\u5B69\u5B50\u5BF9\u5E7C\u513F\u56ED\u6709\u62B1\u6028\u6216\u8D1F\u9762\u60C5\u7EEA\uFF0C\u5BB6\u957F\u9700\u8981\u63A5\u7EB3\u5B69\u5B50\u7684\u611F\u53D7\uFF0C\u7528\u966A\u4F34\u548C\u5B89\u9759\u7684\u503E\u542C\u8868\u793A\u7406\u89E3\uFF0C\u544A\u8BC9\u4ED6\u201C\u55EF\uFF0C\u5988\u5988\u77E5\u9053\u3002\u201D</p><p>\u5F53\u5B69\u5B50\u7684\u8D1F\u9762\u60C5\u7EEA\u9010\u6E10\u5E73\u7F13\u4E86\uFF0C\u6211\u4EEC\u5C31\u53EF\u4EE5\u7A0D\u5FAE\u63D0\u4E00\u4E0B\uFF0C\u5B69\u5B50\u66FE\u7ECF\u6709\u7684\u79EF\u6781\u7ECF\u9A8C\u6216\u611F\u53D7\uFF0C\u4F8B\u5982\u201C\u5E7C\u513F\u56ED\u53EF\u4EE5\u73A9\u6C99\u5B50\u54E6\uFF01\u201D\u3002</p><p>\u5982\u679C\u5B69\u5B50\u542C\u5230\u8FD9\u4E2A\u8BDD\u9898\u5F88\u5F00\u5FC3\uFF0C\u4E0D\u751F\u6C14\u4E86\uFF0C\u90A3\u5BB6\u957F\u53EF\u4EE5\u518D\u6B21\u80AF\u5B9A\u4ED6\uFF0C\u5982\uFF1A\u201C\u6C99\u5B50\u5F88\u597D\u73A9\uFF01\u771F\u6709\u610F\u601D\uFF01\u201D\u3002</p><p>\u5982\u679C\u4E00\u63D0\u5230\u5E7C\u513F\u56ED\uFF0C\u5B69\u5B50\u7684\u60C5\u7EEA\u53C8\u6765\u4E86\uFF0C\u6211\u4EEC\u5C31\u7EE7\u7EED\u966A\u4F34\u548C\u7B49\u5F85\uFF0C\u6B63\u5E38\u7684\u8FDB\u884C\u751F\u6D3B\u4F5C\u606F\uFF0C\u6216\u8005\u4EE5\u826F\u597D\u7684\u4EB2\u5B50\u4E92\u52A8\u6765\u5B89\u6392\u8FD9\u4E9B\u65F6\u95F4\u3002</p><p><strong>09</strong></p><p><strong>\u968F\u673A\u5E94\u53D8\uFF0C\u4F46\u4EE5\u4E0D\u53D8\u4EE5\u5E94\u4E07\u53D8</strong></p><p>\u5982\u679C\u5B69\u5B50\u4ECE\u5E7C\u513F\u56ED\u56DE\u6765\uFF0C\u770B\u8D77\u6765\u6BD4\u8F83\u75B2\u60EB\u3001\u5BB9\u6613\u6709\u60C5\u7EEA\uFF0C\u65E9\u4E0A\u8D77\u5E8A\u603B\u662F\u8BF4\u6CA1\u7761\u591F\u3002\u90A3\u4E48\u665A\u4E0A\u7761\u89C9\u7684\u65F6\u95F4\u53EF\u4EE5\u5148\u5F80\u524D\u8C03\uFF0C\u8BA9\u5B69\u5B50\u4F11\u606F\u5F97\u66F4\u5145\u8DB3\u4E00\u4E9B\uFF0C\u51CF\u5C11\u7B2C\u4E8C\u5929\u4E0A\u5348\u53EF\u80FD\u4F1A\u5495\u54DD\u7740\u4E0D\u8981\u4E0A\u5B66\u6216\u54ED\u95F9\u7684\u60C5\u51B5\u3002</p><p>\u5728\u5B69\u5B50\u54ED\u95F9\u7684\u65F6\u5019\uFF0C\u4E00\u8FB9\u542C\uFF0C\u4E00\u8FB9\u884C\u52A8\u8D77\u6765\u6362\u6821\u670D\u3001\u80CC\u4E66\u5305\uFF0C\u9001\u5B69\u5B50\u4E0A\u5B66\uFF0C\u575A\u5B9A\u5730\u628A\u5B69\u5B50\u9001\u5230\u8001\u5E08\u624B\u4E0A\u3002</p><p>\u5B69\u5B50\u5165\u56ED\uFF0C\u8FD9\u662F\u4ED6\u4EBA\u751F\u4E2D\u7B2C\u4E00\u6B21\u8FDB\u884C\u72EC\u7ACB\u5B66\u4E60\u7684\u5F00\u59CB\uFF0C\u5BB6\u957F\u6709\u62C5\u5FC3\u548C\u5FC3\u75BC\u5728\u6240\u96BE\u514D\uFF0C\u4F46\u6211\u4EEC\u8FD8\u662F\u8981\u884C\u52A8\u8D77\u6765\u2014\u2014\u5728\u6362\u4F4D\u7406\u89E3\u548C\u63A5\u7EB3\u60C5\u7EEA\u7684\u540C\u65F6\uFF0C\u966A\u4F34\u5B69\u5B50\u5B66\u4E60\u9002\u5E94\u65B0\u73AF\u5883\u7684\u6280\u80FD\uFF0C\u5E76\u8BA9\u4ED6\u5728\u6B64\u8FC7\u7A0B\u4E2D\u80FD\u591F\u6709\u79EF\u6781\u7684\u4F53\u9A8C\u3002</p><p>\u8FD9\u6837\uFF0C\u5B69\u5B50\u624D\u80FD\u63A5\u53D7\u201C\u4E0A\u5B66\u662F\u4E2A\u5E38\u89C4\u7684\u4E8B\u60C5\u201D\uFF0C\u4ECE\u800C\u4E0D\u518D\u62B5\u89E6\uFF0C\u613F\u610F\u53BB\u5B66\u6821\u3002\uFF08\u533B\u5B66\u6821\u5BF9\uFF1A\u5468\u5A31\u83C1\u533B\u751F\uFF09</p><hr><p>\u521D\u5165\u5E7C\u513F\u56ED\u5BF9\u5B69\u5B50\u662F\u4E00\u4E2A\u5F88\u5927\u7684\u6311\u6218\uFF0C\u5165\u56ED\u4E4B\u540E\u8FD8\u53EF\u80FD\u4F1A\u9047\u5230\u5F88\u591A\u7684\u95EE\u9898\uFF0C\u6BD4\u5982\u4EE5\u4E0B\u8FD9\u4E9B\u3002</p><p>\u4E0A\u56FE\u4E2D\u7684\u95EE\u9898\u90FD\u662F\u5F90\u6842\u51E4\u533B\u751F\u5728\u6021\u79BE\u5F00\u8BBE\u7684\u300A\u5B9D\u5B9D\u5165\u56ED\u5B9D\u5178\u300B\u4E2D\u8BB2\u89E3\u7684\u5185\u5BB9\uFF0C\u6709\u9700\u8981\u7684\u53EF\u901A\u8FC7\u4E0B\u9762\u4E8C\u7EF4\u7801\u4E86\u89E3\u3002</p><p>\u8BFE\u7A0B\u539F\u4EF7199\u5143\uFF0C3\u4EBA\u62FC\u56E2139\u5143\uFF0C\u6021\u79BE\u4F1A\u5458\u53EF5\u6298\u8D2D\u4E70\uFF0C\u5E76\u53EF\u4EAB\u95EE\u533B\u751F8.8\u6298\u7B49\u6743\u76CA\uFF0C\u9605\u8BFB\u539F\u6587\u53EF\u4E86\u89E3\u4F1A\u5458\u3002</p>',51),i={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651634334&idx=1&sn=b68d0e6637d2ad3441245dc4fddfc691&chksm=8b5c9364bc2b1a7226632740ab1d6dc20815750e3aade79d07a6bf3e89f59f2d609b4c9e9921&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},h=c("\u67E5\u770B\u539F\u6587");function l(f,m){const r=t("ExternalLinkIcon");return n(),o(s,null,[_,p("p",null,[p("a",i,[h,e(r)])])],64)}var u=g(d,[["render",l]]);export{u as default};
