import{r as o,o as r,c as s,a as e,b as n,w as c,F as i,e as t,d}from"./app.9aaa2256.js";import{_}from"./plugin-vue_export-helper.21dcd24c.js";const m={},l=e("h3",{id:"\u4E3A\u4EC0\u4E48\u4E0D\u5EFA\u8BAE\u7528\u6613\u5766\u9759",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#\u4E3A\u4EC0\u4E48\u4E0D\u5EFA\u8BAE\u7528\u6613\u5766\u9759","aria-hidden":"true"},"#"),t(" \u4E3A\u4EC0\u4E48\u4E0D\u5EFA\u8BAE\u7528\u6613\u5766\u9759\uFF1F")],-1),h=t("\u6613\u5766\u9759\u662F\u513F\u79D1\u7528\u5F97\u975E\u5E38\u591A\u7684\u4E00\u79CD\u836F\uFF0C\u6240\u4EE5\u5728"),u=t("\u524D\u4E00\u7BC7\u6587\u7AE0"),b=t("\u7684\u8BC4\u8BBA\u91CC\u8BF4\u4E86\u53E5\u201C\u4E0D\u5EFA\u8BAE\u7528\u6613\u5766\u9759\u201D\uFF0C\u5F15\u53D1\u4E86\u5F88\u591A\u7591\u95EE\uFF0C\u6240\u4EE5\u5355\u72EC\u5199\u8FD9\u7BC7\u6587\u7AE0\u6765\u56DE\u7B54\u8FD9\u4E2A\u95EE\u9898\u3002"),f=d("<p>\u6613\u5766\u9759\u53C8\u53EB\u6C28\u6EB4\u7279\u7F57\u53E3\u670D\u6DB2\uFF0C\u662F\u4E00\u79CD\u590D\u65B9\u836F\u54C1\uFF0C\u5305\u542B\u4E86\u76D0\u9178<strong>\u6C28\u6EB4\u7D22</strong>\u548C\u76D0\u9178<strong>\u514B\u4ED1\u7279\u7F57</strong>\u4E24\u79CD\u836F\u7269\u6210\u5206\u3002</p><p>\u5148\u8BF4\u76D0\u9178<strong>\u514B\u4ED1\u7279\u7F57</strong>\u3002</p><p>\u514B\u4ED1\u7279\u7F57\u8FD9\u4E2A\u836F\u540D\u5927\u5BB6\u53EF\u80FD\u964C\u751F\uFF0C\u4F46\u201C\u7626\u8089\u7CBE\u201D\u5F88\u591A\u4EBA\u53EF\u80FD\u4F1A\u6709\u5370\u8C61\u3002\u514B\u4ED1\u7279\u7F57\u56E0\u4E3A\u53EF\u4EE5\u63D0\u9AD8\u79BD\u755C\u7684\u7626\u8089\u6BD4\uFF0C\u66FE\u88AB\u6DFB\u52A0\u5230\u732A\u9972\u6599\u91CC\uFF0C\u5BFC\u81F4\u5403\u732A\u8089\u7684\u4EBA\u51FA\u73B0\u5FC3\u60B8\u3001\u5934\u75BC\u3001\u6076\u5FC3\u3001\u9AD8\u8840\u538B\u7B49\u95EE\u9898\uFF0C\u95F9\u51FA\u4E86\u201C\u7626\u8089\u7CBE\u201D\u4E8B\u4EF6\uFF0C\u540E\u6765\u514B\u4ED1\u7279\u7F57\u4E5F\u88AB\u7981\u6B62\u6DFB\u52A0\u5230\u52A8\u7269\u9972\u6599\u4E86\u3002</p><p>\u514B\u4ED1\u7279\u7F57\u672C\u8D28\u662F\u4E00\u79CD\u5F3A\u6548\u03B22\u53D7\u4F53\u6FC0\u52A8\u836F\uFF0C\u6709\u6269\u5F20\u652F\u6C14\u7BA1\u4F5C\u7528\uFF0C\u5BF9\u4E8E\u652F\u6C14\u7BA1\u54EE\u5598\u53CA\u5598\u606F\u6027\u652F\u6C14\u7BA1\u708E\u53EF\u4EE5\u6539\u5584\u6C14\u9053\u75C9\u631B\uFF0C\u8FDB\u800C\u6539\u5584\u901A\u6C14\uFF0C\u5728\u6B27\u6D32\u548C\u4E9A\u6D32\u4E00\u4E9B\u56FD\u5BB6\u88AB\u6279\u51C6\u5E94\u7528\u54EE\u5598\u3001\u6162\u6027\u963B\u585E\u6027\u80BA\u75C5\u7B49\u547C\u5438\u9053\u75BE\u75C5\u3002</p><p>\u4F46\u540C\u7C7B\u836F\u54C1\u91CC\uFF0C\u8D77\u6548\u66F4\u5FEB\uFF0C\u66F4\u5B89\u5168\u7684\u836F\u54C1\u6709\u5F88\u591A\uFF0C\u6BD4\u5982\u53EF\u4EE5\u96FE\u5316\u5438\u5165\u7684\u6C99\u7F8E\u7279\u7F57\u548C\u798F\u83AB\u7279\u7F57\uFF0C\u6240\u4EE5FDA\u6CA1\u6709\u6279\u51C6\u514B\u4ED1\u7279\u7F57\u5728\u7F8E\u56FD\u4F5C\u4E3A\u4EBA\u7528\u836F\u4E0A\u5E02\uFF0C\u53EA\u6279\u51C6\u8FC7\u4F5C\u4E3A\u517D\u836F\u7528\u4E8E\u6CBB\u7597\u9A6C\u7684\u6C14\u9053\u963B\u585E\u3002</p><p>\u518D\u8BF4\u53E6\u5916\u4E00\u79CD\u6210\u5206\uFF1A\u76D0\u9178<strong>\u6C28\u6EB4\u7D22</strong></p><p>\u76D0\u9178\u6C28\u6EB4\u7D22\u8FD9\u4E2A\u540D\u5B57\u5927\u5BB6\u542C\u8D77\u6765\u53EF\u80FD\u4E5F\u5F88\u964C\u751F\uFF0C\u4F46\u5982\u679C\u8BF4\u6C90\u8212\u5766\u5927\u5BB6\u5C31\u77E5\u9053\u4E86\u3002\u6C90\u8212\u5766\u5C31\u662F\u76D0\u9178\u6C28\u6EB4\u7D22\u7684\u5546\u54C1\u540D\uFF0C\u8FD9\u662F\u4E00\u79CD\u5316\u75F0\u836F\u3002</p><p>\u5B69\u5B50\u54B3\u55FD\u6709\u75F0\uFF0C\u628A\u75F0\u6DB2\u5316\u5F00\u4E86\uFF0C\u75F0\u6DB2\u5C31\u80FD\u6392\u51FA\u6765\u4E86\uFF0C\u54B3\u55FD\u5C31\u80FD\u597D\u4E86\uFF0C\u8FD9\u662F\u4F7F\u7528\u5316\u75F0\u836F\u7684\u4E00\u4E2A\u903B\u8F91\uFF0C\u4F46\u5B9E\u9645\u662F\u5426\u5982\u6B64\u5462\uFF1F\u5E76\u4E0D\u4E00\u5B9A\u3002</p><p>\u75F0\u6DB2\u662F\u547C\u5438\u9053\u9ECF\u819C\u4EA7\u751F\u7684\u5206\u6CCC\u7269\uFF0C\u53EF\u4EE5\u7C98\u9644\u5C18\u57C3\u3001\u75C5\u539F\u4F53\uFF0C\u547C\u5438\u9053\u9ECF\u819C\u7684\u4E00\u4E9B\u574F\u6B7B\u8131\u843D\u7684\u7EC6\u80DE\u4E5F\u53EF\u4EE5\u6DF7\u6742\u5728\u75F0\u6DB2\u91CC\u3002</p><p>\u5F53\u547C\u5438\u9053\u53D7\u5230\u523A\u6FC0\uFF0C\u6BD4\u5982\u70DF\u96FE\u6216\u75C5\u539F\u4F53\u611F\u67D3\uFF0C\u75F0\u6DB2\u4F1A\u5206\u6CCC\u5F97\u66F4\u65FA\u76DB\uFF0C\u4EE5\u66F4\u597D\u5730\u7C98\u9644\u3001\u6E05\u9664\u8FD9\u4E9B\u523A\u6FC0\u7269\uFF0C\u75F0\u6DB2\u79EF\u805A\u591A\u4E86\uFF0C\u4E5F\u4F1A\u523A\u6FC0\u547C\u5438\u9053\u5F15\u53D1\u54B3\u55FD\uFF0C\u628A\u75F0\u6DB2\u54B3\u51FA\uFF0C\u4E5F\u5C31\u628A\u7C98\u5728\u75F0\u6DB2\u91CC\u7684\u5C18\u57C3\u3001\u7EC6\u83CC\u3001\u75C5\u6BD2\uFF0C\u4EE5\u53CA\u574F\u6B7B\u7684\u7EC6\u80DE\u4E00\u8D77\u6392\u51FA\u4E86\u4F53\u5916\u3002\u6240\u4EE5\u75F0\u6DB2\u7684\u4EA7\u751F\u548C\u54B3\u55FD\uFF0C\u90FD\u662F\u4EBA\u4F53\u7684\u81EA\u6211\u4FDD\u62A4\u673A\u5236\uFF0C\u666E\u901A\u4EBA\u7FA4\u53D1\u751F\u7684\u5E38\u89C1\u547C\u5438\u9053\u75BE\u75C5\u53EF\u4EE5\u5728\u8FD9\u4E9B\u4FDD\u62A4\u673A\u5236\u4E0B\u81EA\u6211\u6062\u590D\u3002</p><p>\u4F46\u4E00\u4E9B\u7279\u6B8A\u75BE\u75C5\uFF0C\u6BD4\u5982\u54EE\u5598\u3001\u7EA4\u7EF4\u56CA\u6027\u75C5\u3001\u6162\u6027\u963B\u585E\u6027\u80BA\u75C5\u4F1A\u5BFC\u81F4\u75F0\u6DB2\u7279\u522B\u591A\uFF0C\u7C98\u5EA6\u4E5F\u7279\u522B\u5927\uFF0C\u6240\u4EE5\u6709\u7406\u8BBA\u4E0A\u53EF\u80FD\u9020\u6210\u547C\u5438\u9053\uFF0C\u5C24\u5176\u662F\u4E00\u4E9B\u6BDB\u7EC6\u652F\u6C14\u7BA1\u7684\u5835\u585E\uFF0C\u5F71\u54CD\u901A\u6C14\uFF0C\u751A\u81F3\u5F71\u54CD\u547C\u5438\u9053\u5206\u6CCC\u7269\u7684\u5F15\u6D41\uFF0C\u8BF1\u53D1\u6216\u52A0\u91CD\u80BA\u90E8\u611F\u67D3\uFF08\u53EA\u662F\u7406\u8BBA\u4E0A\uFF09\u3002\u6240\u4EE5\u4EBA\u4EEC\u5C31\u5E0C\u671B\u6709\u836F\u7269\u80FD\u5316\u6389\u75F0\uFF0C\u6240\u4EE5\u5C31\u6709\u4E86\u5316\u75F0\u836F\u3002</p><p>\u76D0\u9178\u6C28\u6EB4\u7D22\u5C31\u662F\u4E00\u79CD\u5316\u75F0\u836F\uFF0C\u6839\u636E\u836F\u54C1\u8BF4\u660E\u4E66\u7684\u8BF4\u660E\uFF0C\u5B83\u4F3C\u4E4E\u6709\u5F88\u591A\u79CD\u4F5C\u7528\uFF0C\u5305\u62EC\u8C03\u8282\u7C98\u6DB2\u5206\u6CCC\uFF0C\u4FC3\u8FDB\u7EA4\u6BDB\u6446\u52A8\uFF0C\u4FC3\u8FDB\u80BA\u8868\u9762\u6D3B\u6027\u7269\u8D28\u5408\u6210\uFF0C\u6297\u6C27\u5316\u3001\u51CF\u5C11\u708E\u6027\u4ECB\u8D28\u91CA\u653E\u7B49\u7B49\uFF0C\u4F3C\u4E4E\u547C\u5438\u9053\u75BE\u75C5\u9700\u8981\u7684\u529F\u6548\u5B83\u90FD\u6709\uFF0C\u4F46\u9057\u61BE\u7684\u662F\uFF0C\u6C28\u6EB4\u7D22\u7684\u5316\u75F0\u6548\u679C\uFF0C\u5E76\u6CA1\u6709\u5F88\u597D\u7684\u4E34\u5E8A\u7ED3\u679C\u8BC1\u5B9E\uFF0C\u8FD9\u4E5F\u662F\u4E3A\u4EC0\u4E48\u5B83\u4E0A\u5E0230\u591A\u5E74\uFF0C\u76EE\u524D\u4E5F\u4ECD\u672A\u5F97\u5230FDA\u6279\u51C6\uFF0C\u53EA\u5728\u4E00\u4E9B\u6B27\u6D32\u3001\u4E9A\u6D32\u3001\u5357\u7F8E\u6D32\u56FD\u5BB6\u4E0A\u5E02\u3002</p><p>\u8FD9\u5E76\u4E0D\u5947\u602A\uFF0C\u4E0D\u53EA\u662F\u6C28\u6EB4\u7D22\uFF0C\u5176\u5B83\u7684\u5316\u75F0\u836F\uFF0C\u6BD4\u5982\u6108\u521B\u7518\u6CB9\u919A\u3001\u4E59\u9170\u534A\u80F1\u6C28\u9178\u3001\u6EB4\u5DF1\u65B0\u548C\u7FA7\u7532\u53F8\u5766\u7B49\u5316\u75F0\u836F\uFF0C\u867D\u7136\u7528\u5F97\u5F88\u5E7F\u6CDB\uFF0C\u4F46\u5B9E\u9645\u90FD\u7F3A\u4E4F\u5F88\u597D\u7684\u4E34\u5E8A\u8BD5\u9A8C\u4F9D\u636E[1\uFF0C2],\u6240\u4EE5\u4E5F\u8FD9\u4E9B\u836F\u7269\u4E5F\u5728\u547C\u5438\u76F8\u5173\u7684\u5FAA\u8BC1\u6307\u5357\u91CC\u4E5F\u5C11\u6709\u88AB\u63A8\u8350\u3002</p><p>\u5C31\u6C28\u6EB4\u7D22\u800C\u8A00\uFF0C\u4E00\u65B9\u9762\u6709\u6548\u6027\u4E0D\u786E\u5B9A\uFF0C\u540C\u65F6\u8FD8\u6709\u4E0D\u826F\u53CD\u5E94\u65B9\u9762\u7684\u62C5\u5FC3\uFF0C2016\u5E74\u6B27\u6D32\u836F\u54C1\u7BA1\u7406\u5C40\u8FD8\u7279\u522B\u66F4\u65B0\u8FC7\u5173\u4E8E\u5B83\u7684\u4E25\u91CD\u8FC7\u654F\u53CD\u5E94\u53CA\u4E25\u91CD\u76AE\u80A4\u635F\u5BB3\u53CD\u5E94\uFF08SCARs\uFF09\u65B9\u9762\u7684\u5B89\u5168\u4FE1\u606F[3]\u3002</p><p>\u5BF9\u4E8E\u6B62\u54B3\u5316\u75F0\u836F\uFF0C\u7F8E\u56FD\u513F\u79D1\u5B66\u4F1A\u662F\u4E0D\u63A8\u83504\u5C81\u4EE5\u4E0B\u5B69\u5B50\u7528\u7684\uFF0C\u82F1\u56FD\u3001\u52A0\u62FF\u5927\u662F\u4E0D\u5EFA\u8BAE6\u5C81\u4EE5\u4E0B\u5B69\u5B50\u7528\uFF0C\u6C90\u8212\u5766\u6CA1\u80FD\u5728\u7F8E\u3001\u52A0\u3001\u82F1\u8FD9\u4E9B\u56FD\u5BB6\u4E0A\u5E02\u3002\u6B27\u6D32\u4F5C\u4E3A\u8FD9\u4E2A\u836F\u7684\u6765\u6E90\u5730\uFF0C\u76EE\u524D\u5728\u5FB7\u56FD\u3001\u6BD4\u5229\u65F6\u3001\u6CD5\u56FD\u7B49\u56FD\u5BB6\u6709\u4F7F\u7528[4]\uFF0C\u4F46\u6B27\u6D32\u836F\u54C1\u7BA1\u7406\u5C40\u4E5F\u662F\u4E0D\u5EFA\u8BAE\u7528\u4E8E2\u5C81\u4EE5\u4E0B\u513F\u7AE5\uFF0C2-6\u5C81\u53EA\u6709\u5728\u533B\u5B66\u5EFA\u8BAE\u4E0B\u624D\u80FD\u7528[5]\u3002\u4F46\u6B27\u6D32\u5728\u4E2D\u56FD\u7684\u836F\u5382\u52C3\u6797\u683C\u6BB7\u683C\u7FF0\u751F\u4EA7\u7684\u6C90\u8212\u5766\uFF0C\u5374\u53EF\u4EE5\u7528\u4E8E1\u5C81\u7684\u5B69\u5B50\u3002</p><p>\u56DE\u5230<strong>\u6613\u5766\u9759</strong>\uFF0C\u4F5C\u4E3A\u514B\u4ED1\u7279\u7F57\u548C\u6C28\u6EB4\u7D22\u7684\u590D\u65B9\u836F\u7269\uFF0C\u6613\u5766\u9759\u662F\u4E00\u4E2A\u5947\u602A\u7684\u7EC4\u5408\u3002</p><p>\u514B\u4ED1\u7279\u7F57\u4F5C\u4E3A\u652F\u6C14\u7BA1\u6269\u5F20\u5242\uFF0C\u5C31\u9650\u5236\u4E86\u5B83\u5728\u513F\u7AE5\u7684\u9002\u5E94\u75C7\u51E0\u4E4E\u53EA\u6709\u54EE\u5598\uFF0C\u611F\u5192\u540E\u7684\u54B3\u55FD\u3001\u666E\u901A\u7684\u652F\u6C14\u7BA1\u708E\u3001\u80BA\u708E\uFF0C\u8FD9\u4E9B\u975E\u6C14\u9053\u75C9\u631B\u75BE\u75C5\u7528\u514B\u4ED1\u7279\u7F57\u6CA1\u6709\u4EFB\u4F55\u610F\u4E49\u3002</p><p>\u5982\u679C\u662F\u54EE\u5598\uFF0C\u514B\u4ED1\u7279\u7F57\u7684\u6709\u6548\u6027\u548C\u5B89\u5168\u6027\u51B3\u5B9A\u4E86\u5B83\u4E0D\u662F\u652F\u6C14\u7BA1\u6269\u5F20\u5242\u91CC\u597D\u7684\u9009\u62E9\uFF0C\u800C\u6C28\u6EB4\u7D22\u7684\u5316\u75F0\u6548\u679C\u4E5F\u4E0D\u53EF\u9760\uFF0C\u6240\u4EE5\u540C\u6837\u4E0D\u9700\u8981\u9009\u7528\u6613\u5766\u9759\uFF0C\u4E5F\u6CA1\u6709\u54EA\u4E2A\u56FD\u5BB6\u7684\u54EE\u5598\u6307\u5357\u6709\u63A8\u8350\u8FD9\u4E2A\u836F\u54C1\uFF0C\u6B27\u6D32\u836F\u54C1\u7BA1\u7406\u5C40\u4E5F\u660E\u786E\u8BF4\uFF1A<em>\u8003\u8651\u5230\u6C28\u6EB4\u7D22-\u76D0\u9178\u514B\u4ED1\u7279\u7F57\u6CA1\u6709\u6536\u76CA\uFF0C\u800C\u4F7F\u7528\u6C28\u6EB4\u7D22\u548C\u76D0\u9178\u514B\u4F26\u7279\u7F57\u6709\u91CD\u8981\u5B89\u5168\u98CE\u9669\uFF0C\u201C\u5728\u6240\u6709\u5E74\u9F84\u7EC4\u60A3\u8005\u7684\u6025\u6027\u548C\u6162\u6027\u547C\u5438\u9053\u75BE\u75C5\u2026.\u4E2D \u53D7\u76CA/\u98CE\u9669\u6BD4 \u90FD\u662F\u8D1F\u7684\u3002__[5]</em></p><p>\u8FD9\u5C31\u662F\u4E3A\u4EC0\u4E48\u4E0D\u5EFA\u8BAE\u7528\u6613\u5766\u9759\uFF0C\u4E5F\u4E0D\u5EFA\u8BAE\u4F7F\u7528\u6C90\u8212\u5766\u7B49\u5316\u75F0\u836F\u3002</p><p>\u53C2\u8003\u8D44\u6599\uFF1A</p><p>[1]https://www.ncbi.nlm.nih.gov/pubmed/17716385</p><p>[2]Uptodate:Role of mucoactive agents and secretion clearance techniques in COPD.</p><p>[3]https://www.ema.europa.eu/documents/referral/ambroxol-bromhexine-article-31-referral-ambroxol-bromhexine-expectorants-safety-information-be_en.pdf</p><p>[4]https://www.ema.europa.eu/documents/psusa/ambroxol-list-nationally-authorised-medicinal-products-psusa/00000130/201609_en.pdf</p><p>[5]https://www.ema.europa.eu/en/medicines/human/referrals/ambroxol-bromhexine-containing-medicines</p>",25),x={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651627960&idx=1&sn=9b5a739a2d9d101e53db7aea67fc7ea3&chksm=8b5c8842bc2b015446a96af26b60edd77fcb63431c095b58e7c128970b87ae6319b7fddd117c&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},g=t("\u67E5\u770B\u539F\u6587");function w(k,z){const p=o("RouterLink"),a=o("ExternalLinkIcon");return r(),s(i,null,[l,e("p",null,[h,n(p,{to:"/s/__biz=MzA5NzE5ODIyOQ==&mid=2651627947&idx=1&sn=50775b5e2ea78eebd900c78820478ccd/"},{default:c(()=>[u]),_:1}),b]),f,e("p",null,[e("a",x,[g,n(a)])])],64)}var A=_(m,[["render",w]]);export{A as default};
