import{r as p,o as r,c as n,a as e,b as a,F as o,d as s,e as c}from"./app.9aaa2256.js";import{_ as i}from"./plugin-vue_export-helper.21dcd24c.js";const d={},_=s('<h3 id="\u5F53\u5229\u5DF4\u97E6\u6797\u9047\u5230\u611F\u5192-\u5F53\u4E34\u5E8A\u6307\u5357\u9047\u5230\u4E4C\u9F99" tabindex="-1"><a class="header-anchor" href="#\u5F53\u5229\u5DF4\u97E6\u6797\u9047\u5230\u611F\u5192-\u5F53\u4E34\u5E8A\u6307\u5357\u9047\u5230\u4E4C\u9F99" aria-hidden="true">#</a> \u5F53\u5229\u5DF4\u97E6\u6797\u9047\u5230\u611F\u5192\uFF0C\u5F53\u4E34\u5E8A\u6307\u5357\u9047\u5230\u4E4C\u9F99</h3><p>\u666E\u901A\u611F\u5192\u662F\u75C5\u6BD2\u611F\u67D3\u5F15\u8D77\uFF0C\u9664\u975E\u5408\u5E76\u7EC6\u83CC\u611F\u67D3\uFF0C\u4E0D\u9700\u8981\u7528\u6297\u83CC\u836F\uFF0C\u4F46\u611F\u5192\u6EE5\u7528\u6297\u83CC\u7D20\u5F88\u5E38\u89C1\uFF0C\u6297\u83CC\u7D20\u6EE5\u7528\u95EE\u9898\u4ECA\u5929\u5C31\u4E0D\u591A\u8BF4\u4E86\uFF0C\u4ECA\u5929\u4E3B\u8981\u8BF4\u8BF4\u611F\u5192\u7528\u6297\u75C5\u6BD2\u836F\u7684\u4E8B\u3002</p><p>\u5F15\u8D77\u611F\u5192\u7684\u75C5\u6BD2\u6709200\u591A\u79CD\uFF0C\u76EE\u524D\u6CA1\u6709\u9488\u5BF9\u5BFC\u81F4\u611F\u5192\u7684\u6297\u75C5\u6BD2\u836F\uFF0C\u6240\u4EE5\u4E0D\u9700\u8981\u7528\u6297\u75C5\u6BD2\u836F\u7269\uFF0C\u8FD9\u51E0\u4E4E\u662F\u533B\u5B66\u754C\u7684\u5171\u8BC6\u3002\u4F46\u5B9E\u9645\u4E0A\uFF0C\u5728\u56FD\u5185\uFF0C\u4EE5\u5229\u5DF4\u97E6\u6797\u4E3A\u4E3B\u7684\u6297\u75C5\u6BD2\u836F\u88AB\u5E7F\u6CDB\u5E94\u7528\u4E8E\u5305\u62EC\u611F\u5192\u5728\u5185\u7684\u547C\u5438\u9053\u611F\u67D3\u75C5\u4F8B\u4E0A\u3002</p><p>\u5229\u5DF4\u97E6\u6797\u662F\u6838\u82F7\u7C7B\u6297\u75C5\u6BD2\u836F\uFF0C\u4F53\u5916\u8BD5\u9A8C\u5BF9\u547C\u5438\u9053\u5408\u80DE\u75C5\u6BD2\u5177\u6709\u9009\u62E9\u6027\u6291\u5236\u4F5C\u7528\uFF0C\u6211\u56FD\u836F\u76D1\u5C40\u6279\u51C6\u5229\u5DF4\u97E6\u6797\u9897\u7C92\u7528\u4E8E\u547C\u5438\u9053\u5408\u80DE\u75C5\u6BD2\uFF08RSV\uFF09\u5F15\u8D77\u7684\u75C5\u6BD2\u6027\u80BA\u708E\u4E0E\u652F\u6C14\u7BA1\u708E\uFF0C\u76AE\u80A4\u75B1\u75B9\u75C5\u6BD2\u611F\u67D3\u3002</p><p>\u7F8E\u56FD\u4E8E1985\u5E74\u6279\u51C6\u5C06\u5229\u5DF4\u97E6\u6797\u6C14\u96FE\u5242\u7528\u4E8ERSV\u4E0B\u547C\u5438\u9053\u611F\u67D3\u7684\u4F4F\u9662\u5B69\u5B50\uFF0C\u56E0\u4E3A\u65E9\u671F\u7684\u4E00\u4E9B\u4E34\u5E8A\u7814\u7A76\u53D1\u73B0\u5229\u5DF4\u97E6\u6797\u5BF9RSV\u611F\u67D3\u6709\u6548\uFF0C\u7F8E\u56FD\u513F\u79D1\u5B66\u4F1A1993\u5E74\u63A8\u8350\u5C06\u5229\u5DF4\u97E6\u6797\u7528\u4E8E\u6709\u91CD\u5EA6RSV\u611F\u67D3\u7684\u9AD8\u5371\u5B69\u5B50\uFF0C\u4F46\u540E\u6765\u7684\u5206\u6790\u53D1\u73B0\u4E4B\u524D\u7684\u7814\u7A76\u6709\u7F3A\u9677\uFF0C\u5229\u5DF4\u97E6\u6797\u7684\u4E34\u5E8A\u6548\u679C\u5E76\u4E0D\u5982\u4E4B\u524D\u8BC4\u4F30\u7684\u90A3\u4E48\u660E\u663E\uFF0C1996\u5E74\u7F8E\u56FD\u513F\u79D1\u5B66\u4F1A\u5C06\u4E4B\u524D\u7684\u201C\u5FC5\u987B\u7528\u201D\u4FEE\u6539\u4E3A\u201C\u53EF\u4EE5\u8003\u8651\u4F7F\u7528\u201D\uFF0C\u4F46\u4E5F\u4EC5\u9650\u4E8E\u91CD\u75C7RSV\u611F\u67D3\u7684\u9AD8\u5371\u75C5\u4EBA[1]\u3002</p><p>\u7F8E\u56FD\u513F\u79D1\u5B66\u4F1A\u4E4B\u6240\u4EE5\u5C06\u5229\u5DF4\u97E6\u6797\u7684\u5E94\u7528\u6307\u5F81\u63A7\u5236\u5F97\u8FD9\u4E48\u7A84\uFF0C\u4E00\u662F\u76EE\u524D\u7684\u4E34\u5E8A\u6548\u679C\u8FD8\u4E0D\u90A3\u4E48\u786E\u5B9A\uFF0C\u4E8C\u662F\u5229\u5DF4\u97E6\u6797\u7684\u526F\u4F5C\u7528\u6BD4\u8F83\u5927\u3002\u52A8\u7269\u5B9E\u9A8C\u663E\u793A\u5229\u5DF4\u97E6\u6797\u6709\u660E\u663E\u7684\u81F4\u7578\u6027\u548C\u81F4\u764C\u6027\uFF0C\u6240\u4EE5\u5B55\u671F\u548C\u54FA\u4E73\u671F\u90FD\u4E0D\u80FD\u4F7F\u7528\uFF0C\u6B64\u5916\u5229\u5DF4\u97E6\u6797\u53EF\u4EE5\u5BFC\u81F4\u6EB6\u8840\u6027\u8D2B\u8840\uFF0C\u5728\u53E3\u670D\u6CBB\u7597\u540E\u6700\u521D1~2\u5468\u5185\u51FA\u73B0\u8840\u7EA2\u86CB\u767D\u4E0B\u964D\u3001\u7EA2\u7EC6\u80DE\u4E0B\u964D\u3001\u767D\u7EC6\u80DE\u4E0B\u964D\uFF0C\u5176\u4E2D\u7EA610%\u7684\u75C5\u4EBA\u53EF\u4F34\u968F\u5FC3\u80BA\u65B9\u9762\u7684\u526F\u4F5C\u7528\u3002\u6211\u4EEC\u7684\u836F\u76D1\u5C40\u8FD8\u66FE\u5BF9\u8FD9\u4E2A\u836F\u54C1\u53D1\u5E03\u8FC7\u836F\u54C1\u4E0D\u826F\u53CD\u5E94\u4FE1\u606F\u901A\u62A5[2]\u3002</p><p>\u4F46\u5947\u602A\u7684\u662F\uFF0C\u540C\u6837\u662F\u6211\u4EEC\u7684\u836F\u76D1\u5C40\uFF0C\u5BF9\u5229\u5DF4\u97E6\u6797\u6C14\u96FE\u5242\u6279\u51C6\u7684\u9002\u5E94\u75C7\u4E3A\uFF1A\u7528\u4E8E\u75C5\u6BD2\u6027\u4E0A\u547C\u5438\u9053\u611F\u67D3\uFF0C\u6BD4\u5982\u75C5\u6BD2\u6027\u9F3B\u708E\u3001\u54BD\u5477\u708E\u3001\u54BD\u7ED3\u819C\u70ED\u6216\u53E3\u54BD\u90E8\u75C5\u6BD2\u611F\u67D3\u3002\uFF08\u53C2\u89C1\u5229\u5DF4\u97E6\u6797\u6C14\u96FE\u5242\u8BF4\u660E\u4E66\uFF09\uFF0C\u8FD9\u7B49\u4E8E\u5C31\u662F\u6279\u51C6\u5E94\u7528\u4E8E\u611F\u5192\u7684\u6CBB\u7597\u4E86\uFF0C\u6240\u4EE5\u4E5F\u4E0D\u96BE\u7406\u89E3\u4E3A\u4EC0\u4E48\u5728\u513F\u79D1\u95E8\u8BCA\u88AB\u5E7F\u6CDB\u4F7F\u7528\u4E86\u3002</p><p>\u867D\u7136\u836F\u76D1\u90E8\u95E8\u6279\u51C6\u4E86\uFF0C\u4F46\u8FD8\u6709\u6211\u4EEC\u7684\u4E34\u5E8A\u6307\u5357\u53EF\u4EE5\u89C4\u8303\u4E00\u4E0B\u6211\u4EEC\u533B\u751F\u7684\u7528\u836F\uFF0C\u56DE\u5230\u611F\u5192\u7684\u95EE\u9898\u4E0A\uFF0C\u6211\u4EEC\u6765\u770B\u770B\u56FD\u5185\u7684\u6307\u5357\u662F\u600E\u4E48\u8BF4\u7684\u3002\u4E0B\u9762\u8FD9\u6BB5\u6587\u5B57\u6458\u81EA\u53BB\u5E74\u53D1\u8868\u4E8E\u300A\u4E2D\u56FD\u5B9E\u7528\u513F\u79D1\u6742\u5FD7\u300B\u7684\u300A\u4E2D\u56FD\u513F\u7AE5\u666E\u901A\u611F\u5192\u89C4\u8303\u8BCA\u6CBB\u4E13\u5BB6\u5171\u8BC6\uFF082013\u5E74\uFF09\u300B\u7684\u6307\u5357\u3002</p><p>\u603B\u7ED3\u4E00\u4E0B\u5C31\u662F\u5BF9\u666E\u901A\u611F\u5192\u6CA1\u6709\u6297\u75C5\u6BD2\u836F\u53EF\u4EE5\u7528\uFF0C\u8FD9\u548C\u533B\u5B66\u754C\u7684\u4E3B\u6D41\u89C2\u70B9\u5F88\u543B\u5408\uFF0C\u6CA1\u6709\u95EE\u9898\u3002\u4F46\u6709\u610F\u601D\u7684\u662F\uFF0C\u5C31\u5728\u8FD9\u540C\u4E00\u7BC7\u6307\u5357\u91CC\uFF0C\u5374\u8FD8\u6709\u4E0B\u9762\u8FD9\u6837\u4E00\u6BB5\u8BDD\u3002</p><p>\u8FD9\u6BB5\u8BDD\u5237\u65B0\u4E86\u6211\u7684\u8BA4\u77E5\uFF0C\u597D\u5728\u91CC\u9762\u6807\u6CE8\u4E86\u51FA\u5904\uFF0C\u4E8E\u662F\u6211\u7FFB\u5230\u4E86\u6807\u53F7\u4E3A[12]\u7684\u90A3\u7BC7\u53C2\u8003\u6587\u732E\u3002</p><p>\u5982\u679C\u4F60\u6709\u5174\u8DA3\uFF0C\u4E5F\u53EF\u4EE5\u627E\u8FD9\u7BC7\u6587\u732E\u770B\u4E00\u4E0B\uFF0C\u6211\u4ECE\u5934\u770B\u5230\u5C3E\uFF0C\u770B\u4E86\u4E09\u904D\u4E5F<strong>\u6CA1\u6709\u770B\u5230\u5229\u5DF4\u97E6\u6797\u7684\u5B57\u773C</strong>\uFF0C\u91CC\u5173\u4E8E\u836F\u7269\u6CBB\u7597\u662F\u4E0B\u9762\u8FD9\u6837\u5199\u7684\u3002</p><p>\u666E\u901A\u611F\u5192\u75C5\u7A0B\u65E9\u671F\u5E94\u7528\u5229\u5DF4\u97E6\u6797\u6C14\u96FE\u5242\u55B7\u54BD\u90E8\u53EF\u80FD\u6709\u4E00\u5B9A\u76CA\u5904\uFF0C\u8FD9\u53E5\u8BDD\u51E0\u4E4E\u5C31\u662F\u63D0\u793A\u533B\u751F\u53EF\u4EE5\u7ED9\u611F\u5192\u7684\u5B69\u5B50\u5F00\u5229\u5DF4\u97E6\u6797\u6C14\u96FE\u5242\uFF0C\u6211\u5F53\u7136\u76F8\u4FE1\u6211\u4EEC\u533B\u5B66\u6743\u5A01\u4EEC\u63D0\u51FA\u7684\u89C2\u70B9\u662F\u53EF\u80FD\u6709\u4F9D\u636E\u7684\uFF0C\u4F60\u62FF\u51FA\u4E00\u4E2A\u53EF\u4FE1\u7684\u8BC1\u636E\u6211\u4E5F\u4F1A\u9009\u62E9\u76F8\u4FE1\u3002\u4E00\u9762\u63D0\u51FA\u6807\u65B0\u7ACB\u5F02\u7684\u89C2\u70B9\uFF0C\u63D0\u4F9B\u7684\u6587\u732E\u8BC1\u636E\u91CC\u5374\u627E\u4E0D\u5230\u51FA\u5904\uFF0C\u4E00\u4E2A\u7531\u4F17\u591A\u6743\u5A01\u533B\u5B66\u4E13\u5BB6\u5236\u5B9A\u7684\u4E13\u4E1A\u4E34\u5E8A\u6307\u5357\u72AF\u8FD9\u79CD\u9519\u8BEF\uFF0C\u51E0\u4E4E\u662F\u8038\u4EBA\u542C\u95FB\u7684\u3002</p><p>\u6211\u4EEC\u7684\u836F\u76D1\u90E8\u95E8\u6C34\u5E73\u5927\u5BB6\u90FD\u662F\u6E05\u695A\u7684\uFF0C\u672C\u6765\u8FD8\u6307\u671B\u9760\u4E34\u5E8A\u6307\u5357\u8FD9\u9053\u9632\u7EBF\u80FD\u5B88\u4F4F\u4E00\u70B9\u5E95\u7EBF\uFF0C\u7ED3\u679C\u5374\u662F\u717D\u98CE\u70B9\u706B\uFF0C\u8FDB\u4E00\u6B65\u8BEF\u5BFC\u533B\u751F\u5F00\u672C\u4E0D\u8BE5\u5F00\u7684\u836F\u3002\u8FD9\u4E2A\u95EE\u9898\u6211\u5F88\u56F0\u60D1\uFF0C\u56E0\u4E3A\u4E00\u7BC7\u6307\u5357\u7684\u5B8C\u6210\u4E0D\u662F\u4E00\u4E2A\u4EBA\u5B8C\u6210\u7684\uFF0C\u4E00\u4E2A\u4EBA\u628A\u5185\u5BB9\u641E\u9519\u4E86\u8FD8\u53EF\u4EE5\u7406\u89E3\uFF0C\u628A\u53C2\u8003\u6587\u732E\u641E\u9519\u4E86\u4E5F\u53EF\u4EE5\u7406\u89E3\u3002\u4F46\u8FD9\u7BC7\u6307\u5357\u662F\u4E0B\u9762\u8FD939\u4F4D\u4E13\u5BB6\u4E00\u8D77\u5B8C\u6210\u7684\uFF0C39\u4F4D\uFF0C\u6CA1\u9519\uFF0C\u662F39\u4F4D\uFF0C\u4E0D\u4FE1\u4F60\u6570\u4E00\u6570\u3002</p><p>\u6B63\u5982\u8FD9\u7BC7\u6307\u5357\u91CC\u6240\u8BB2\uFF0C\u666E\u901A\u611F\u5192\u662F\u513F\u7AE5\u975E\u5E38\u5E38\u89C1\u7684\u75BE\u75C5\uFF0C\u6BCF\u4E2A\u513F\u7AE5\u5E73\u5747\u6BCF\u5E745-7\u6B21\u3002\u8FD9\u79CD\u53EF\u4EE5\u76F4\u63A5\u5F71\u54CD\u5168\u56FD\u513F\u79D1\u533B\u5E08\u7528\u836F\u7684\u6307\u5357\uFF0C\u7ED9\u51FA\u8FD9\u6837\u7684\u4FE1\u606F\u5C31\u53EF\u80FD\u8BA9\u6570\u4EE5\u4EBF\u8BA1\u7684\u5B69\u5B50\u7528\u4E0A\u672C\u4E0D\u8BE5\u7528\u7684\u836F\u3002\u836F\u76D1\u5C40\u6279\u51C6\u5BBD\u677E\u7684\u9002\u5E94\u75C7\u52A0\u4E0A\u4E34\u5E8A\u6307\u5357\u7684\u8BEF\u5BFC\uFF0C\u7ED3\u5C40\u5C31\u662F\u90A3\u4E9B\u611F\u5192\u7684\u5B69\u5B50\u82B1\u4E86\u94B1\uFF0C\u7528\u4E0A\u4E86\u5BF9\u75C5\u60C5\u6CA1\u6709\u836F\uFF0C\u8FD8\u9762\u4E34\u6EB6\u8840\u3001\u81F4\u7578\u3001\u81F4\u764C\u7B49\u836F\u7269\u4E0D\u826F\u53CD\u5E94\uFF0C\u800C\u8FD9\u4E9B\u672C\u662F\u5B8C\u5168\u53EF\u4EE5\u907F\u514D\u7684\u3002</p><p>\u53D1\u73B0\u8FD9\u4E2A\u5229\u5DF4\u97E6\u6797\u7528\u4E8E\u611F\u5192\u95EE\u9898\u6E90\u4E8E\u4E00\u4E2A\u533B\u5B66\u7F51\u7AD9\u4E3A\u4E86\u5229\u76CA\u63A8\u9001\u4E86\u4E00\u7BC7\u8F6F\u6587\uFF0C\u8FD9\u5DF2\u7ECF\u5F88\u8BA9\u4EBA\u5403\u60CA\u3002\u4F46\u4E13\u4E1A\u671F\u520A\u7684\u4E34\u5E8A\u6307\u5357\u51FA\u8FD9\u79CD\u4E4C\u9F99\uFF0C\u6211\u4E0D\u77E5\u9053\u600E\u4E48\u53BB\u5F62\u5BB9\u3002\u8FD9\u4E2A\u9519\u8BEF\u662F\u65E0\u610F\u4E2D\u53D1\u751F\u7684\u8FD8\u597D\u63A5\u53D7\u4E00\u70B9\uFF0C\u8981\u662F\u6709\u610F\u9020\u6210\uFF0C\u6211\u89C9\u5F97\u8FD9\u4E2A\u540D\u5355\u4E0A\u7684\u4EBA\u90FD\u662F\u4E0D\u53EF\u539F\u8C05\u7684\u3002\uFF08\u539F\u521B\u6587\u7AE0\u672A\u7ECF\u8BB8\u53EF\u8C22\u7EDD\u8F6C\u8F7D\uFF0C\u5173\u6CE8\u672C\u8D26\u53F7\u6709\u76CA\u5B69\u5B50\u5065\u5EB7\uFF09</p><p>\u53C2\u8003\u8D44\u6599\uFF1A</p><p>[1]Committee on Infectious Disease, American Academy of Pediatrics (1996) Reassessment of the indications for ribavirin therapy in respiratory syncytial virus infections.</p><p>[2]http://www.sda.gov.cn/WS01/CL0078/11283.html</p><p><strong>\u6743\u5A01\u7684\u80B2\u513F\u79D1\u666E\uFF0C\u6DF1\u5EA6\u7684\u533B\u5B66\u4EBA\u6587</strong></p><p><strong>\u4EC5\u63A8\u9001\u539F\u521B\u6587\u7AE0\uFF0C\u6B22\u8FCE\u5173\u6CE8drpeihg</strong></p>',20),h={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=201191425&idx=1&sn=543c3bc19878d0157654e42bb0930f69&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},l=c("\u67E5\u770B\u539F\u6587");function m(f,g){const t=p("ExternalLinkIcon");return r(),n(o,null,[_,e("p",null,[e("a",h,[l,a(t)])])],64)}var V=i(d,[["render",m]]);export{V as default};
