import{r as a,o as r,c as t,a as e,b as n,F as o,d as c,e as s}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const _={},i=c('<h3 id="\u590F\u5929\u5B69\u5B50\u53D1\u70E7-\u4F1A\u4E0D\u4F1A\u662F\u4E2D\u6691" tabindex="-1"><a class="header-anchor" href="#\u590F\u5929\u5B69\u5B50\u53D1\u70E7-\u4F1A\u4E0D\u4F1A\u662F\u4E2D\u6691" aria-hidden="true">#</a> \u590F\u5929\u5B69\u5B50\u53D1\u70E7\uFF0C\u4F1A\u4E0D\u4F1A\u662F\u4E2D\u6691\uFF1F</h3><p>\u4F5C\u4E3A\u5BB6\u957F\uFF0C\u5B69\u5B50\u4E00\u53D1\u70E7\u5C31\u4F1A\u60F3\u5F88\u591A\u95EE\u9898\uFF0C\u53EF\u80FD\u4E0D\u53EF\u80FD\u7684\u90FD\u4F1A\u62C5\u5FC3\u3002\u6BD4\u5982\u73B0\u5728\u590F\u5929\u5929\u6C14\u70ED\uFF0C\u5B69\u5B50\u4E00\u53D1\u70E7\uFF0C\u9664\u4E86\u62C5\u5FC3\u611F\u5192\u3001\u624B\u8DB3\u53E3\u75C5\u8FD9\u4E9B\uFF0C\u5F88\u591A\u5BB6\u957F\u8FD8\u4F1A\u62C5\u5FC3\uFF1A\u4F1A\u4E0D\u4F1A\u662F\u4E2D\u6691\uFF1F</p><p>\u5728\u56DE\u7B54\u8FD9\u4E2A\u95EE\u9898\u4E4B\u524D\uFF0C\u6211\u4EEC\u5148\u4E86\u89E3\u4E00\u4E0B\u53D1\u70E7\u548C\u4E2D\u6691\u7684\u533A\u522B\u3002</p><p>\u5F53\u8EAB\u4F53\u7531\u4E8E\u611F\u67D3\u3001\u708E\u75C7\u6216\u5176\u5B83\u95EE\u9898\u4EA7\u751F\u4E86\u81F4\u70ED\u6E90\uFF0C\u4E0B\u4E18\u8111\u91CC\u7684\u4F53\u6E29\u4E2D\u67A2\u80FD\u63A5\u6536\u5230\u4FE1\u53F7\uFF0C\u7136\u540E\u8C03\u9AD8\u4F53\u6E29\u8BBE\u5B9A\u70B9\uFF0C\u540C\u65F6\u53D1\u51FA\u4FE1\u53F7\u8BA9\u8EAB\u4F53\u589E\u5F3A\u4EA7\u70ED\uFF0C\u6536\u7F29\u76AE\u80A4\u6BDB\u7EC6\u8840\u7BA1\u51CF\u5C11\u6563\u70ED\uFF0C\u7136\u540E\u4F53\u6E29\u4E0A\u5347\uFF0C\u8FD9\u5C31\u662F\u53D1\u70E7\u3002</p><p>\u5F53\u4EBA\u5728\u9AD8\u6E29\u73AF\u5883\u91CC\uFF0C\u6216\u5267\u70C8\u6D3B\u52A8\u65F6\uFF0C\u5916\u754C\u4F20\u5BFC\u8FC7\u6765\u7684\u70ED\u91CF\u6216\u8005\u81EA\u8EAB\u6D3B\u52A8\u4EA7\u751F\u7684\u70ED\u91CF\u8FC7\u591A\uFF0C\u4EBA\u7684\u6E29\u5EA6\u611F\u53D7\u5668\u611F\u53D7\uFF0C\u4E5F\u4F1A\u5C06\u4FE1\u53F7\u4F20\u9012\u7ED9\u4F53\u6E29\u4E2D\u67A2\uFF0C\u4F53\u6E29\u4E2D\u67A2\u4F1A\u7ED9\u8EAB\u4F53\u53D1\u51FA\u6563\u70ED\u4FE1\u53F7\uFF0C\u589E\u5F3A\u51FA\u6C57\u8FD9\u4E9B\u6563\u70ED\u65B9\u5F0F\uFF0C\u4EE5\u7EF4\u6301\u6B63\u5E38\u4F53\u6E29\u3002</p><p>\u4F46\u5982\u679C\u6301\u7EED\u5904\u4E8E\u9AD8\u6E29\u73AF\u5883\u6216\u5267\u70C8\u6D3B\u52A8\u72B6\u6001\u4E0B\uFF0C\u73AF\u5883\u4F20\u5BFC\u8FC7\u6765\u7684\u70ED\u91CF\u548C\u81EA\u8EAB\u4EA7\u751F\u7684\u70ED\u91CF\u8D85\u51FA\u4E86\u4EBA\u4F53\u6563\u70ED\u80FD\u529B\uFF0C\u4F53\u6E29\u5C31\u4F1A\u88AB\u52A8\u5730\u4E0A\u5347\uFF0C\u751A\u81F3\u8D85\u51FA\u4EBA\u4F53\u7684\u8010\u53D7\u6781\u9650\uFF0C\u5C31\u5BFC\u81F4\u4E86\u4E2D\u6691\u3002</p><p>\u6240\u4EE5\uFF0C\u867D\u7136\u7ED3\u679C\u90FD\u662F\u4F53\u6E29\u9AD8\uFF0C\u4F46\u53D1\u70E7\u548C\u4E2D\u6691\u7684\u673A\u5236\u662F\u5B8C\u5168\u4E0D\u540C\u7684\u3002</p><p>\u53D1\u70E7\u662F\u8EAB\u4F53\u4E3B\u52A8\u5347\u6E29\uFF0C\u800C\u4E2D\u6691\u662F\u8EAB\u4F53\u88AB\u52A8\u5347\u6E29\u3002\u5982\u679C\u628A\u4EBA\u4F53\u6BD4\u4F5C\u623F\u5B50\uFF0C\u53D1\u70E7\u66F4\u50CF\u6211\u4EEC\u4E3B\u52A8\u5F00\u4E86\u6696\u6C14\uFF0C\u800C\u4E2D\u6691\u66F4\u50CF\u623F\u5B50\u5931\u706B\u3002</p><p>\u53EA\u4E0D\u8FC7\u53D1\u70E7\u5E38\u89C1\u4E2D\u6691\u5C11\u89C1\uFF0C\u6240\u4EE5\u6211\u4EEC\u5E73\u65F6\u4E60\u60EF\u4E8E\u628A\u4F53\u6E29\u5347\u9AD8\u90FD\u53EB\u53D1\u70E7\uFF0C\u4EE5\u81F3\u4E8E\u5927\u5BB6\u5206\u4E0D\u6E05\u53D1\u70E7\u548C\u4E2D\u6691\u4E86\u3002</p><p>\u7406\u89E3\u4E86\u4E0A\u9762\u8FD9\u4E9B\uFF0C\u5C31\u5F88\u5BB9\u6613\u56DE\u7B54\u524D\u9762\u7684\u95EE\u9898\u4E86\u3002\u5982\u679C\u5B69\u5B50\u5728\u592A\u9633\u5E95\u4E0B\u66B4\u6652\uFF0C\u5C24\u5176\u662F\u88AB\u5173\u5728\u8F66\u91CC\u65E0\u6CD5\u9003\u751F\u7684\u65F6\u5019\uFF0C\u8EAB\u4F53\u53D1\u70EB\uFF0C\u5C31\u8981\u8003\u8651\u662F\u4E2D\u6691\u4E86\uFF0C\u5982\u679C\u5728\u5BB6\u91CC\u5439\u7740\u7A7A\u8C03\u5374\u53D1\u70E7\u4E86\uFF0C\u90A3\u5C31\u4E0D\u8981\u62C5\u5FC3\u4E2D\u6691\u4E86\u3002</p><p>\u7406\u89E3\u4E86\u53D1\u70E7\u548C\u4E2D\u6691\u7684\u4E0D\u540C\u673A\u5236\uFF0C\u4E5F\u5C31\u80FD\u66F4\u597D\u7406\u89E3\u600E\u4E48\u600E\u4E48\u53BB\u5904\u7406\u53D1\u70E7\u548C\u4E2D\u6691\u3002</p><p>\u5982\u679C\u623F\u95F4\u5F00\u7740\u6696\u6C14\uFF0C\u8981\u964D\u6E29\u53EA\u9700\u8981\u7528\u9065\u63A7\u5668\u628A\u6696\u6C14\u5173\u4E86\uFF0C\u5982\u679C\u4E0D\u5173\u6696\u6C14\u800C\u60F3\u901A\u8FC7\u5728\u623F\u95F4\u91CC\u6D47\u6C34\u6765\u964D\u6E29\uFF0C\u81EA\u7136\u6548\u679C\u5DEE\u3002\u6240\u4EE5\uFF0C\u53D1\u70E7\u964D\u6E29\u8981\u9760\u9000\u70E7\u836F\uFF0C\u800C\u4E0D\u662F\u7269\u7406\u964D\u6E29\u3002\uFF08\u56DE\u590D**\u300C\u6E29\u6C34\u300D**\u53EF\u8BE6\u7EC6\u4E86\u89E3\uFF09</p><p>\u5982\u679C\u623F\u5B50\u7740\u706B\u4E86\uFF0C\u8981\u505A\u7684\u662F\u6D47\u6C34\u706D\u706B\uFF0C\u800C\u4E0D\u662F\u53BB\u5173\u6696\u6C14\uFF0C\u56E0\u4E3A\u4E0D\u662F\u6696\u6C14\u5BFC\u81F4\u7684\u7740\u706B\u3002\u6240\u4EE5\uFF0C\u4E2D\u6691\u964D\u6E29\u8981\u9760\u7269\u7406\u964D\u6E29\uFF0C\u800C\u4E0D\u662F\u5403\u9000\u70E7\u836F\u3002</p><p>\u53D1\u70E7\u662F\u4EBA\u4F53\u542F\u52A8\u7684\u81EA\u6211\u4FDD\u62A4\u673A\u5236\uFF0C\u4F53\u6E29\u9AD8\u4E86\u4F53\u6E29\u4E2D\u67A2\u4E5F\u4F1A\u542F\u52A8\u6563\u70ED\u673A\u5236\uFF0C\u6240\u4EE5\u53D1\u70E7\u5BFC\u81F4\u7684\u6E29\u5EA6\u4E0A\u5347\u662F\u6709\u9650\u5236\u7684\uFF0C\u6781\u5C11\u4F1A\u8D85\u8FC741\u2103\uFF0C\u6240\u4EE5\u53D1\u70E7\u4E0D\u4F1A\u70E7\u574F\u5B69\u5B50\u3002</p><p>\u800C\u4E2D\u6691\u662F\u88AB\u52A8\u7684\u4F53\u6E29\u4E0A\u5347\uFF0C\u6240\u4EE5\u4F53\u6E29\u4E0A\u5347\u4E0D\u53D7\u9650\u5236\uFF0C\u4F1A\u7ED9\u5927\u8111\u548C\u5176\u5B83\u5668\u5B98\u9020\u6210\u4F24\u5BB3\uFF0C\u662F\u4E00\u79CD\u6B7B\u4EA1\u7387\u5F88\u9AD8\u7684\u5371\u9669\u72B6\u6001\u3002</p><p>\u5982\u679C\u4F60\u53D1\u73B0\u6709\u4EBA\u4E2D\u6691\u4E86\uFF0C\u9996\u5148\u8981\u505A\u7684\u662F\u628A\u4ED6\u79FB\u5230\u9634\u51C9\u7684\u5730\u65B9\uFF0C\u7136\u540E\u5C3D\u91CF\u9664\u53BB\u8863\u7269\u6563\u70ED\uFF0C\u7528\u51C9\u6C34\u6216\u8005\u51B0\u888B\u964D\u6E29\uFF0C\u540C\u65F6\u62E8\u6253\u6C42\u6551\u7535\u8BDD\u3002</p><p>\u5373\u4FBF\u62A2\u6551\u5F97\u5F53\uFF0C\u5F88\u591A\u4EBA\u4E5F\u6551\u4E0D\u8FC7\u6765\uFF0C\u6240\u4EE5\u9884\u9632\u4E2D\u6691\u5F88\u5173\u952E\u3002\u5929\u6C14\u70ED\u8BB0\u5F97\u5F00\u7A7A\u8C03\uFF0C\u8981\u907F\u514D\u7ED9\u7ED9\u5B69\u5B50\u8FC7\u5EA6\u7A7F\u8863\uFF0C\u5C24\u5176\u662F\u4E0D\u4F1A\u8BF4\u8BDD\u7684\u5A74\u513F\uFF0C\u9AD8\u6E29\u5929\u6C14\u4E0D\u8981\u8BA9\u5B69\u5B50\u957F\u65F6\u95F4\u5728\u6237\u5916\u6D3B\u52A8\uFF0C\u4E5F\u5343\u4E07\u4E0D\u8981\u8BA9\u5B69\u5B50\u4E00\u4E2A\u4EBA\u5355\u72EC\u5446\u7740\u8F66\u91CC\uFF0C\u54EA\u6015\u662F\u5F00\u7740\u7A97\uFF0C\u56E0\u4E3A\u4F60\u4E0D\u77E5\u9053\u6709\u591A\u5371\u9669\u3002</p><p>\u56DE\u590D**\u300C\u8F66\u5185\u300D**\u4E86\u89E3\uFF1A\u628A\u5B69\u5B50\u72EC\u7559\u8F66\u5185\uFF0C\u5230\u5E95\u6709\u591A\u5371\u9669\uFF1F</p><p>\u9898\u56FE\u4F5C\u8005Sean Hurley\uFF0C\u6388\u6743\u57FA\u4E8ECC0\u534F\u8BAE</p><p>\u70B9<strong>\u9605\u8BFB\u539F\u6587</strong>\uFF0C\u53EF\u5411\u6211\u548C\u5176\u5B83\u533B\u751F\u54A8\u8BE2</p>',20),l={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651626726&idx=1&sn=8dd508abc20175e80aea0ff530e52418&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},h=s("\u67E5\u770B\u539F\u6587");function m(f,x){const p=a("ExternalLinkIcon");return r(),t(o,null,[i,e("p",null,[e("a",l,[h,n(p)])])],64)}var g=d(_,[["render",m]]);export{g as default};
