import{r as a,o as r,c as t,a as e,b as n,F as c,d as o,e as s}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const _={},i=o('<h3 id="\u5B69\u5B50\u4FBF\u79D8\u600E\u4E48\u529E" tabindex="-1"><a class="header-anchor" href="#\u5B69\u5B50\u4FBF\u79D8\u600E\u4E48\u529E" aria-hidden="true">#</a> \u5B69\u5B50\u4FBF\u79D8\u600E\u4E48\u529E\uFF1F</h3><p>\u505A\u4E86\u7236\u6BCD\u540E\uFF0C\u5B69\u5B50\u7684\u5403\u559D\u62C9\u6492\u90FD\u6210\u4E86\u5BB6\u5EAD\u5934\u7B49\u5927\u4E8B\uFF0C\u5B69\u5B50\u4FBF\u79D8\u4E5F\u56F0\u6270\u7740\u5F88\u591A\u7236\u6BCD\uFF0C\u8981\u4E48\u51E0\u5929\u4E0D\u62C9\uFF0C\u8981\u4E48\u4E00\u62C9\u5C31\u5927\u4FBF\u51FA\u8840\uFF0C\u5B69\u5B50\u75BC\u5F97\u76F4\u54ED\uFF0C\u5404\u79CD\u529E\u6CD5\u8BD5\u4E86\u90FD\u4E0D\u89C1\u6548\uFF0C\u771F\u662F\u8BA9\u4EBA\u5934\u5927\u3002</p><p>\u5176\u5B9E\u548C\u5BB6\u957F\u4E00\u6837\uFF0C\u513F\u79D1\u533B\u751F\u540C\u6837\u5F88\u5173\u6CE8\u5B69\u5B50\u7684\u5927\u4FBF\uFF0C\u56E0\u4E3A\u5F88\u591A\u75BE\u75C5\u90FD\u548C\u5927\u4FBF\u6709\u5173\uFF0C\u8FD9\u79CD\u5173\u6CE8\u4ECE\u5B69\u5B50\u751F\u540E\u7B2C\u4E00\u6B21\u6392\u4FBF\u5C31\u5F00\u59CB\u4E86\u3002</p><p>90\uFF05\u4EE5\u4E0A\u7684\u8DB3\u6708\u513F\u5728\u751F\u540E24\u5C0F\u65F6\u5185\u4F1A\u6392\u51FA\u58A8\u7EFF\u8272\u7684\u7A20\u539A\u80CE\u4FBF\uFF0C\u8FD9\u5C31\u662F\u6B63\u5E38\u7684\u80CE\u4FBF\uFF0C\u5982\u679C\u7B2C\u4E00\u6B21\u80CE\u4FBF\u6392\u51FA\u5EF6\u8FDF\uFF0C\u5C24\u5176\u662F\u8D85\u8FC748\u5C0F\u65F6\uFF0C\u6216\u8005\u5927\u4FBF\u989C\u8272\u3001\u6027\u72B6\u5F02\u5E38\uFF0C\u6BD4\u5982\u53EA\u6709\u4E00\u4E9B\u767D\u767D\u7684\u6216\u6DE1\u9EC4\u8272\u7684\u9ECF\u6DB2\uFF0C\u90A3\u5C31\u8981\u5BC6\u5207\u6CE8\u610F\u6709\u6CA1\u6709\u5176\u5B83\u5408\u5E76\u75C7\u72B6\uFF0C\u6BD4\u5982\u809A\u5B50\u80C0\uFF0C\u5455\u5410\uFF0C\u5C24\u5176\u662F\u5410\u9EC4\u7684\u7EFF\u7684\uFF0C\u5982\u679C\u6709\u8FD9\u4E9B\u75C7\u72B6\u5C31\u9700\u8981\u627E\u65B0\u751F\u513F\u5916\u79D1\u533B\u751F\u68C0\u67E5\u4E00\u4E0B\u4E86\uFF0C\u770B\u770B\u6709\u6CA1\u6709\u5148\u5929\u7684\u80A0\u9053\u7578\u5F62\u3002</p><p>\u5916\u79D1\u533B\u751F\u5BF9\u80CE\u4FBF\u6392\u51FA\u5EF6\u8FDF\u540C\u65F6\u5408\u5E76\u5455\u5410\u7684\u65B0\u751F\u513F\uFF0C\u4E00\u822C\u4F1A\u5EFA\u8BAE\u505A\u809A\u5B50\u7684X\u7EBF\u68C0\u67E5\uFF0C\u6392\u9664\u80A0\u95ED\u9501\uFF0C\u80CE\u7CAA\u6027\u80A0\u6897\u963B\u7B49\u9700\u8981\u624B\u672F\u5904\u7406\u7684\u75BE\u75C5\uFF0C\u56E0\u4E3A\u8FD9\u4E9B\u95EE\u9898\u62D6\u4E45\u4E86\u4F1A\u5BFC\u81F4\u80A0\u7A7F\u5B54\u7B49\u4E25\u91CD\u7684\u95EE\u9898\uFF0C\u800C\u6709\u4E9B\u80A0\u6897\u963B\u5BF9\u75C7\u5904\u7406\u4E4B\u540E\u75C7\u72B6\u5C31\u6D88\u5931\u4E86\uFF0C\u6BD4\u5982\u80CE\u4FBF\u8FC7\u4E8E\u7C98\u7A20\uFF0C\u901A\u8FC7\u6D17\u80A0\u3001\u901A\u4FBF\u7684\u5904\u7406\uFF0C\u5927\u4FBF\u5C31\u9010\u6E10\u6B63\u5E38\u4E86\u3002</p><p>\u8FD8\u6709\u4E9B\u80A0\u6897\u963B\u5BF9\u75C7\u6CBB\u7597\u4E4B\u540E\u75C7\u72B6\u7F13\u89E3\u4E86\uFF0C\u4F46\u8FC7\u6BB5\u65F6\u95F4\u5927\u4FBF\u53C8\u62C9\u4E0D\u51FA\u6765\u4E86\uFF0C\u540C\u65F6\u8FD8\u4F1A\u51FA\u73B0\u809A\u5B50\u80C0\u3001\u5455\u5410\u3002\u6BD4\u5982\u7532\u72B6\u817A\u529F\u80FD\u4F4E\u4E0B\uFF0C\u9700\u8981\u670D\u7528\u7532\u72B6\u817A\u7D20\u624D\u80FD\u6539\u5584\u75C7\u72B6\uFF0C\u5F53\u7136\u8FD8\u6709\u5DE8\u7ED3\u80A0\uFF0C\u4E00\u79CD\u80A0\u795E\u7ECF\u5F02\u5E38\u7684\u5148\u5929\u6027\u75BE\u75C5\uFF0C\u9760\u8FD1\u809B\u95E8\u7684\u4E00\u6BB5\u80A0\u5B50\u5C11\u4E86\u795E\u7ECF\u8282\u7EC6\u80DE\u5BFC\u81F4\u80A0\u5B50\u75C9\u631B\uFF0C\u5927\u4FBF\u5230\u4E86\u75C5\u53D8\u80A0\u7BA1\u8FD9\u91CC\u5C31\u5F88\u96BE\u6392\u4E0B\u53BB\u4E86\uFF0C\u7ED3\u679C\u5C31\u662F\u4FBF\u79D8\uFF0C\u809A\u5B50\u80C0\uFF0C\u8FD9\u79CD\u75C5\u4E00\u65E6\u786E\u8BCA\u4E86\u4E5F\u662F\u9700\u8981\u624B\u672F\u6CBB\u7597\u3002</p><p>\u8FD8\u6709\u4E00\u4E9B\u60C5\u51B5\u662F\u5B69\u5B50\u521A\u51FA\u751F\u7684\u65F6\u5019\u5927\u4FBF\u633A\u6B63\u5E38\u7684\uFF0C\u6BCF\u5929\u90FD\u80FD\u62C9\uFF0C\u4F46\u52A0\u8F85\u98DF\u4E4B\u540E\u5C31\u53D1\u73B0\u5B69\u5B50\u7ECF\u5E38\u4FBF\u79D8\uFF0C\u51E0\u5929\u624D\u62C9\u4E00\u6B21\uFF0C\u800C\u4E14\u4E00\u62C9\u5C31\u618B\u5F97\u6EE1\u8138\u901A\u7EA2\uFF0C\u540E\u6765\u5230\u533B\u9662\u68C0\u67E5\uFF0C\u5374\u53D1\u73B0\u5B69\u5B50\u6CA1\u6709\u6B63\u5E38\u5230\u809B\u95E8\uFF0C\u809B\u95E8\u53E3\u7279\u522B\u72ED\u5C0F\u6216\u8005\u5F00\u53E3\u4E0D\u5728\u6B63\u5E38\u7684\u4F4D\u7F6E\uFF0C\u6709\u7684\u5F00\u53E3\u9760\u8FD1\u9634\u56CA\uFF0C\u6709\u7684\u5F00\u53E3\u5728\u9634\u9053\u4E0B\u65B9\uFF0C\u8FD8\u6709\u4E9B\u809B\u95E8\u5916\u5F62\u6B63\u5E38\u4F46\u76F4\u80A0\u72ED\u7A84\u3002</p><p>\u8FD9\u4E9B\u5B69\u5B50\u4EE5\u524D\u4E00\u76F4\u6CA1\u6709\u4FBF\u79D8\u7684\u75C7\u72B6\u662F\u56E0\u4E3A\u4E00\u76F4\u5403\u6BCD\u4E73\u6216\u8005\u914D\u65B9\u5976\uFF0C\u5927\u4FBF\u4E3A\u7A00\u7CCA\u72B6\uFF0C\u76F4\u80A0\u809B\u95E8\u5C0F\u4E5F\u6392\u5F97\u5F88\u901A\u7545\uFF0C\u800C\u4E00\u65E6\u52A0\u8F85\u98DF\u540E\u5927\u4FBF\u53D8\u786C\u53D8\u7C97\u4E86\uFF0C\u5C31\u6392\u4E0D\u51FA\u6765\u4E86\uFF0C\u8FD9\u4E9B\u60C5\u51B5\u4E5F\u9700\u8981\u624B\u672F\u624D\u80FD\u89E3\u51B3\u3002\u66F4\u5C11\u89C1\u7684\u8FD8\u6709\u4E00\u4E9B\u957F\u5927\u4E4B\u540E\u624D\u51FA\u73B0\u7684\u987D\u56FA\u6027\u4FBF\u79D8\uFF0C\u540C\u65F6\u53EF\u4F34\u6709\u5C3F\u5931\u7981\u3001\u5927\u4FBF\u5931\u7981\u3001\u4E0B\u80A2\u75C9\u631B\uFF0C\u505A\u68C0\u67E5\u53D1\u73B0\u662F\u810A\u67F1\u7578\u5F62\u5BFC\u81F4\u7684\u795E\u7ECF\u635F\u5BB3\u3002</p><p>\u4E0A\u8FF0\u7684\u8FD9\u4E9B\u4FBF\u79D8\u4E3B\u8981\u7279\u5F81\u662F\u627E\u5230\u4E86\u660E\u786E\u7684\u75C5\u56E0\uFF0C\u7EDF\u79F0\u4E3A\u5668\u8D28\u6027\u4FBF\u79D8\u53EF\u80FD\u4E0D\u662F\u5F88\u5408\u9002\uFF0C\u4F46\u4E3B\u8981\u662F\u4E3A\u4E86\u533A\u522B\u4E0B\u9762\u8981\u8BB2\u7684\u201C\u529F\u80FD\u6027\u4FBF\u79D8\u201D\uFF0C\u4E5F\u5C31\u662F\u5927\u5BB6\u5E73\u65F6\u8BF4\u7684\u90A3\u79CD\u4FBF\u79D8\u3002</p><p>\u8BF4\u5230\u4FBF\u79D8\u5927\u5BB6\u90FD\u77E5\u9053\u5927\u4FBF\u5E72\u3001\u5927\u4FBF\u786C\uFF0C\u6B21\u6570\u5C11\uFF0C\u4F46\u5C11\u5230\u4EC0\u4E48\u7A0B\u5EA6\u624D\u80FD\u7B97\u662F\u4FBF\u79D8\uFF1F\u5927\u90E8\u5206\u4EBA\u5E76\u4E0D\u6E05\u695A\uFF0C\u5305\u62EC\u5F88\u591A\u533B\u751F\u3002\u513F\u7AE5\u529F\u80FD\u6027\u4FBF\u79D8\u4E2D\u56FD\u8FD8\u6CA1\u6709\u81EA\u5DF1\u7684\u8BCA\u65AD\u6807\u51C6\uFF0C\u6211\u4EEC\u4E5F\u662F\u53C2\u7167\u4E0B\u56FE\u4E2D\u7684\u56FD\u9645\u901A\u7528\u7684\u7F57\u9A6C\u6807\u51C6\u3002</p><p>\u8FD9\u4E2A\u6807\u51C6\u56E0\u4E3A\u662F\u56FD\u5916\u5B9A\u7684\uFF0C\u6240\u4EE5\u7FFB\u8BD1\u8FC7\u6765\u6709\u4E9B\u4E0D\u597D\u8868\u8FBE\u548C\u7406\u89E3\u3002\u4F46\u5BF9\u7167\u4E00\u4E0B\u8FD9\u4E2A\u6807\u51C6\u5F88\u591A\u5BB6\u957F\u8FD8\u662F\u53EF\u4EE5\u7ED9\u81EA\u5DF1\u5B69\u5B50\u5224\u65AD\u4E00\u4E0B\uFF0C\u6BD4\u5982\u4E00\u4E9B\u6BCD\u4E73\u5582\u517B\u7684\u5B69\u5B50\u4E94\u516D\u5929\u624D\u62C9\u4E00\u6B21\u5927\u4FBF\uFF0C\u4F46\u5403\u5F97\u597D\uFF0C\u5927\u4FBF\u4E5F\u4E0D\u5E72\uFF0C\u4E5F\u6CA1\u6709\u5176\u5B83\u75C7\u72B6\uFF0C\u5C31\u4E0D\u662F\u4FBF\u79D8\u3002</p><p>\u8DDF\u5668\u8D28\u6027\u4FBF\u79D8\u4E0D\u540C\u7684\u662F\uFF0C\u529F\u80FD\u6027\u4FBF\u79D8\u5E76\u4E0D\u662F\u600E\u4E48\u62C9\u90FD\u62C9\u4E0D\u51FA\u6765\uFF0C\u800C\u662F\u591A\u5C11\u90FD\u5B58\u5728\u6709\u610F\u65E0\u610F\u7684\u618B\u5927\u4FBF\u884C\u4E3A\uFF0C\u81F3\u4E8E\u4E3A\u4EC0\u4E48\u4F1A\u618B\u5927\u4FBF\uFF0C\u4E3A\u4EC0\u4E48\u4F1A\u4FBF\u79D8\uFF0C\u539F\u56E0\u5E76\u4E0D\u5341\u5206\u6E05\u695A\uFF0C\u76EE\u524D\u8BA4\u4E3A\u548C\u996E\u98DF\u53CA\u6392\u4FBF\u4E60\u60EF\u5173\u7CFB\u6BD4\u8F83\u5BC6\u5207\uFF0C\u751F\u6D3B\u5B66\u4E60\u73AF\u5883\u7684\u53D8\u5316\u4E5F\u4F1A\u8BF1\u53D1\u4FBF\u79D8\uFF0C\u9057\u4F20\u548C\u7CBE\u795E\u5FC3\u7406\u4E5F\u53EF\u80FD\u548C\u4FBF\u79D8\u6709\u5173\u7CFB\uFF0C\u539F\u56E0\u4E0D\u540C\uFF0C\u6CBB\u7597\u7684\u91CD\u70B9\u4E5F\u4F1A\u6709\u4E9B\u4E0D\u540C\u3002</p><p>\u5F88\u591A\u5BB6\u957F\u53EF\u80FD\u4E5F\u6CE8\u610F\u5230\uFF0C\u5B69\u5B50\u5728\u5403\u6BCD\u4E73\u7684\u65F6\u5019\u5927\u4FBF\u90FD\u5F88\u597D\uFF0C\u4F46\u4E00\u5403\u914D\u65B9\u5976\u5C31\u5F00\u59CB\u4FBF\u79D8\uFF0C\u6709\u65F6\u5019\u6362\u4E00\u79CD\u5976\u7C89\u53C8\u597D\u4E86\uFF0C\u8FD8\u6709\u4E9B\u5B69\u5B50\u5927\u4FBF\u4E00\u76F4\u597D\u597D\u7684\uFF0C\u6DFB\u52A0\u56FA\u4F53\u8F85\u98DF\u4E4B\u540E\u5C31\u5F00\u59CB\u4FBF\u79D8\u4E86\uFF0C\u539F\u56E0\u53EF\u80FD\u5C31\u662F\u98DF\u7269\u4E2D\u4E00\u4E9B\u6210\u4EFD\u5F71\u54CD\u4E86\u6392\u4FBF\u3002</p><p>\u521A\u5F00\u59CB\u5982\u5395\u8BAD\u7EC3\u7684\u65F6\u5019\uFF0C\u6709\u7684\u5B69\u5B50\uFF0C\u56E0\u4E3A\u9002\u5E94\u4E0D\u4E86\u65B0\u7684\u6392\u4FBF\u65B9\u5F0F\uFF0C\u4E0D\u80AF\u6392\u4FBF\uFF0C\u5982\u679C\u5BB6\u957F\u64CD\u4E4B\u8FC7\u6025\uFF0C\u8FC7\u5206\u5F3A\u8FEB\u5B69\u5B50\uFF0C\u4F1A\u8BA9\u4ED6\u5BF9\u6392\u4FBF\u4EA7\u751F\u6050\u60E7\uFF0C\u53CD\u800C\u5BFC\u81F4\u4FBF\u79D8\u3002</p><p>\u8FD8\u6709\u4E9B\u5B69\u5B50\u7531\u4E8E\u4E0A\u5B66\uFF0C\u5916\u51FA\u6E38\u73A9\uFF0C\u4E0D\u613F\u610F\u5728\u964C\u751F\u7684\u73AF\u5883\u4E0B\u6392\u4FBF\uFF0C\u6709\u4FBF\u610F\u7684\u65F6\u5019\u618B\u4FBF\uFF0C\u5927\u4FBF\u5728\u76F4\u80A0\u5185\u957F\u65F6\u95F4\u6F74\u7559\uFF0C\u8BA9\u76F4\u80A0\u9002\u5E94\u4E86\u7CAA\u4FBF\u7684\u5F20\u529B\uFF0C\u4FBF\u610F\u53CD\u800C\u6D88\u5931\u4E86\u3002\u7CAA\u4FBF\u5728\u76F4\u80A0\u91CC\u9010\u6E10\u7D2F\u79EF\uFF0C\u6C34\u5206\u8FDB\u4E00\u6B65\u88AB\u5438\u6536\uFF0C\u53D8\u5F97\u8D8A\u6765\u8D8A\u5E72\u786C\uFF0C\u6392\u51FA\u4E5F\u5C31\u66F4\u56F0\u96BE\uFF0C\u5927\u4FBF\u7C97\u786C\u5BFC\u81F4\u809B\u88C2\uFF0C\u6392\u4FBF\u75BC\u75DB\uFF0C\u53C8\u8FDB\u4E00\u6B65\u6291\u5236\u6392\u4FBF\uFF0C\u5F62\u6210\u6076\u6027\u5FAA\u73AF\uFF0C\u4E25\u91CD\u7684\u4F1A\u5BFC\u81F4\u7CAA\u5757\u5835\u585E\u4E8E\u76F4\u80A0\u5185\uFF0C\u4E0A\u9762\u7684\u7A00\u4FBF\u4ECE\u7CAA\u5757\u5468\u56F4\u6D41\u51FA\uFF0C\u51FA\u73B0\u6C61\u4FBF\u548C\u5931\u7981\u3002</p><p>\u4FBF\u79D8\u7ED9\u5B69\u5B50\u5E26\u6765\u5F88\u591A\u8EAB\u4F53\u4E0A\u7684\u75DB\u82E6\uFF0C\u8179\u80C0\u3001\u8179\u75DB\uFF0C\u98DF\u6B32\u5DEE\uFF0C\u751A\u81F3\u5927\u4FBF\u5931\u7981\uFF0C\u8FD8\u4F1A\u5F71\u54CD\u5B69\u5B50\u7684\u60C5\u7EEA\u548C\u5FC3\u7406\uFF0C\u800C\u957F\u671F\u4FBF\u79D8\u6DF1\u5733\u53EF\u4EE5\u5F71\u54CD\u5B69\u5B50\u7684\u53D1\u80B2\uFF0C\u6240\u4EE5\u4E0D\u8BBA\u662F\u54EA\u79CD\u539F\u56E0\u5F15\u8D77\u7684\u4FBF\u79D8\u90FD\u9700\u8981\u6CBB\u7597\uFF0C\u8D8A\u65E9\u6CBB\u7597\u8D8A\u5BB9\u6613\u89C1\u6548\uFF0C\u8FD9\u4E5F\u662F\u56FD\u9645\u8BCA\u65AD\u6807\u51C6\u7684\u65F6\u9650\u4ECE\u539F\u6765\u768412\u5468\u6539\u4E3A\u73B0\u5728\u76841\u6708\u548C2\u6708\u7684\u539F\u56E0\u3002</p><p>\u90A3\u5B69\u5B50\u4FBF\u79D8\u600E\u4E48\u6CBB\u5462\uFF1F\u5982\u679C\u5B69\u5B50\u5E73\u65F6\u504F\u98DF\uFF0C\u6C34\u679C\u852C\u83DC\u5403\u5F97\u5C11\uFF0C\u559D\u6C34\u4E5F\u6BD4\u8F83\u5C11\uFF0C\u53EF\u4EE5\u8BA9\u5B69\u5B50\u591A\u559D\u70B9\u6C34\uFF0C\u591A\u5403\u70B9\u7EA4\u7EF4\u7D20\u542B\u91CF\u9AD8\u7684\u679C\u852C\uFF0C\u6BD4\u5982\u897F\u6885\u3001\u674F\u3001\u674E\u5B50\u3001\u8461\u8404\u5E72\u3001\u897F\u5170\u82B1\u3001\u8C46\u7C7B\u7B49\u3002\u4F46\u76EE\u524D\u6CA1\u6709\u5145\u8DB3\u7684\u8BC1\u636E\u8BC1\u5B9E\u989D\u5916\u8865\u5145\u7EA4\u7EF4\u7D20\u3001\u591A\u559D\u6C34\u3001\u589E\u52A0\u8FD0\u52A8\u91CF\u5BF9\u4FBF\u79D8\u6709\u6548\uFF0C\u6240\u4EE5\u4FDD\u6301\u6B63\u5E38\u7684\u7EA4\u7EF4\u7D20\u3001\u6DB2\u4F53\u6444\u5165\u91CF\uFF0C\u4FDD\u6301\u6B63\u5E38\u7684\u8FD0\u52A8\u91CF\u5373\u53EF\u3002</p><p>\u6CBB\u7597\u4FBF\u79D8\u66F4\u591A\u7684\u65F6\u5019\u8FD8\u662F\u8981\u9760\u836F\u7269\uFF0C\u5BF9\u6709\u786C\u4FBF\u5D4C\u585E\u6392\u4E0D\u51FA\u6765\u7684\u5B69\u5B50\uFF0C\u4E00\u5C81\u4EE5\u5185\u53EF\u4EE5\u7528\u5F00\u585E\u9732\u5E2E\u52A9\u6392\u7A7A\uFF0C\u4E00\u5C81\u4EE5\u4E0A\u53EF\u4EE5\u53E3\u670D\u805A\u4E59\u4E8C\u9187\u7C7B\u836F\u7269\uFF08\u6BCF\u59291-1.5\u514B\uFF0F\u516C\u65A4\u4F53\u91CD\uFF093-6\u5929\uFF0C\u6216\u8005\u9694\u65E5\u5230\u533B\u9662\u91CC\u704C\u80A0\u6E05\u9664\u76F4\u80A0\u5185\u6F74\u7559\u7684\u786C\u4FBF\uFF0C\u6D88\u9664\u5B69\u5B50\u6392\u4FBF\u7684\u75DB\u82E6\uFF0C\u7136\u540E\u7528\u805A\u4E59\u4E8C\u9187\uFF08\u901A\u5E38\u6BCF\u59290.4\u514B\uFF0F\u516C\u65A4\u4F53\u91CD\u5F00\u59CB\uFF0C\u9700\u8981\u6839\u636E\u627E\u533B\u751F\u6839\u636E\u7528\u836F\u53CD\u5E94\u8C03\u6574\uFF09\u7EF4\u6301\u5927\u4FBF\u8F6F\u5316\uFF0C\u6CA1\u6709\u805A\u4E59\u4E8C\u9187\u65F6\u53EF\u4EE5\u7528\u4E73\u679C\u7CD6\uFF0C\u901A\u5E38\u5EFA\u8BAE\u7EF4\u6301\u7528\u836F\u81F3\u5C112\u4E2A\u6708\uFF0C\u8981\u5728\u4FBF\u79D8\u75C7\u72B6\u6D88\u5931\u540E\u81F3\u5C11\u4E00\u4E2A\u6708\u9010\u6E10\u51CF\u91CF\u3002\u76EE\u524D\u4E5F\u6CA1\u6709\u5145\u8DB3\u7684\u8BC1\u636E\u8BC1\u5B9E\u76CA\u751F\u83CC\u5BF9\u4FBF\u79D8\u6709\u7528\uFF0C\u4E0D\u63A8\u8350\u5E38\u89C4\u7528\u76CA\u751F\u83CC\u6765\u6CBB\u4FBF\u79D8\u3002</p><p>\u5728\u501F\u52A9\u836F\u7269\u3001\u4FDD\u6301\u826F\u597D\u996E\u98DF\u4E60\u60EF\u7684\u540C\u65F6\uFF0C\u4E5F\u8981\u6CE8\u610F\u57F9\u517B\u5B69\u5B50\u7684\u6392\u4FBF\u4E60\u60EF\uFF0C\u5B69\u5B50\u5403\u5B8C\u996D\u540E\uFF0C\u7ED3\u80A0\u77ED\u65F6\u95F4\u5185\u7684\u8815\u52A8\u4F1A\u589E\u5F3A\uFF0C\u6709\u5229\u4E8E\u6392\u4FBF\uFF0C\u6240\u4EE5\u53EF\u4EE5\u5728\u996D\u540E10\u5206\u949F\u5DE6\u53F3\u8BA9\u5B69\u5B50\u53BB\u6392\u4FBF\uFF0C\u6709\u52A9\u4E8E\u8BAD\u7EC3\u5B69\u5B50\u517B\u6210\u6BCF\u5929\u81EA\u4E3B\u6392\u4FBF\u7684\u4E60\u60EF\u3002</p><p>\u529F\u80FD\u6027\u4FBF\u79D8\u867D\u7136\u4E0D\u662F\u4E25\u91CD\u75BE\u75C5\uFF0C\u4F46\u6CBB\u7597\u5E76\u4E0D\u90A3\u4E48\u5BB9\u6613\uFF0C\u7528\u4E86\u4E0A\u9762\u8FD9\u4E9B\u65B9\u6CD5\u5F88\u591A\u5B69\u5B50\u4E5F\u5E76\u6CA1\u6709\u6548\uFF0C\u5982\u679C\u5B69\u5B50\u540C\u65F6\u6709\u8179\u80C0\u3001\u8425\u517B\u4E0D\u826F\u7B49\u60C5\u51B5\u9700\u8981\u53CA\u65F6\u627E\u533B\u751F\u590D\u67E5\uFF0C \u5FC5\u8981\u65F6\u9700\u8981\u627E\u5916\u79D1\u533B\u751F\u68C0\u67E5\uFF0C\u4F46\u5927\u90E8\u5206\u529F\u80FD\u6027\u4FBF\u79D8\u7684\u5B69\u5B50\u9884\u540E\u90FD\u5F88\u826F\u597D\uFF0C\u968F\u7740\u65F6\u95F4\u7684\u63A8\u79FB\u5927\u90E8\u5206\u5B69\u5B50\u90FD\u80FD\u597D\u3002</p><p>\u672C\u6587\u6839\u636E\u6B27\u6D32\u548C\u5317\u7F8E\u5C0F\u513F\u80C3\u80A0\u809D\u75C5\u548C\u8425\u517B\u5B66\u4F1A2014\u5E74\u7684\u6307\u5357\u610F\u89C1\u5BF9\u65E7\u6587\u8FDB\u884C\u91CD\u5199\uFF0C\u5173\u6CE8\u672C\u516C\u4F17\u53F7\u540E\uFF0C\u56DE\u590D\u201C<strong>67</strong>\u201D\u53EF\u67E5\u770B\u300A\u5B69\u5B50\u6392\u4FBF\u5C11\uFF0C\u4FBF\u79D8\u8FD8\u662F\u5DE8\u7ED3\u80A0\u300B\uFF0C\u56DE\u590D\u201C<strong>35</strong>\u201D\u4E86\u89E3\u300A\u513F\u7AE5\u809B\u88C2\u300B\u3002</p>',21),l={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=403598768&idx=1&sn=560fa407ee052d7812bd9d4fbd769575&chksm=0d5e444a3a29cd5c64d13dc494d67f986b11c934be24ceb6f5b2fb5612d71a53d573c45cfec0&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},f=s("\u67E5\u770B\u539F\u6587");function h(m,b){const p=a("ExternalLinkIcon");return r(),t(c,null,[i,e("p",null,[e("a",l,[f,n(p)])])],64)}var g=d(_,[["render",h]]);export{g as default};
