import{r as t,o as n,c as o,a as e,b as r,F as s,d as a,e as c}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const i={},m=a('<h3 id="\u827E\u7545-\u56E0\u7231\u800C\u751F" tabindex="-1"><a class="header-anchor" href="#\u827E\u7545-\u56E0\u7231\u800C\u751F" aria-hidden="true">#</a> \u827E\u7545\uFF0C\u56E0\u7231\u800C\u751F\uFF1F</h3><p>\u73B0\u4EE3\u533B\u5B66\u8D77\u6E90\u81EA\u897F\u65B9\uFF0C\u73B0\u4EE3\u836F\u54C1\u4E5F\u4E00\u6837\uFF0C\u5927\u591A\u662F\u56FD\u5916\u836F\u5382\u7814\u53D1\u7684\u3002\u4F5C\u4E3A\u540E\u6765\u8005\uFF0C\u56FD\u5185\u836F\u5382\u4EE5\u4EFF\u5236\u4E3A\u4E3B\uFF0C\u4F46\u56E0\u4E3A\u6280\u672F\u5DE5\u827A\u7B49\u95EE\u9898\uFF0C\u836F\u54C1\u8D28\u91CF\u603B\u4F53\u6BD4\u56FD\u5916\u836F\u4F01\u843D\u540E\u3002\u6211\u4E00\u76F4\u8BA4\u4E3A\uFF0C\u5982\u679C\u7ECF\u6D4E\u6761\u4EF6\u5141\u8BB8\uFF0C\u540C\u7C7B\u836F\u7269\u5982\u679C\u6709\u8FDB\u53E3\u7684\u5C3D\u91CF\u7528\u8FDB\u53E3\uFF0C\u6709\u5927\u578B\u5916\u4F01\u7684\u4EA7\u54C1\u5C3D\u91CF\u4E0D\u7528\u672C\u571F\u4EA7\u54C1\u3002</p><p>\u5F3A\u751F\u516C\u53F8\u5C31\u662F\u4E00\u4E2A\u5927\u578B\u8DE8\u56FD\u836F\u4F01\uFF0C\u53BB\u5E74\u4E3A\u5168\u7403\u5E02\u503C\u6700\u5927\u7684\u836F\u4F01\uFF0C\u4E5F\u4E00\u76F4\u662F\u56FD\u5185\u533B\u836F\u5E02\u573A\u4E3B\u8981\u5916\u8D44\u4F01\u4E1A\u4E4B\u4E00\uFF0C\u65D7\u4E0B\u6709\u5F88\u591A\u5927\u5BB6\u8033\u719F\u80FD\u8BE6\u7684\u836F\u54C1\uFF1A\u5417\u4E01\u5549\u3001\u7F8E\u6797\u3001\u6CF0\u8BFA\u6797\u7B49\uFF0C\u533B\u751F\u4EEC\u5BF9\u8FD9\u5BB6\u516C\u53F8\u7684\u4EA7\u54C1\u5927\u591A\u4E5F\u5F88\u4FE1\u8D56\u3002</p><p>\u4F46\u56E0\u4E3A\u6709\u53E6\u5916\u4E00\u6B3E\u4EA7\u54C1\uFF0C\u6211\u5BF9\u8FD9\u5BB6\u8DE8\u56FD\u836F\u4F01\u4E00\u76F4\u5FC3\u5B58\u8B66\u60D5\uFF0C\u8FD9\u6B3E\u4EA7\u54C1\u5F88\u591A\u5BB6\u957F\u53EF\u80FD\u4E5F\u5F88\u719F\u6089\uFF0C\u90A3\u5C31\u662F\u6CBB\u7597\u5A74\u5E7C\u513F\u611F\u5192\u7684\u7F8E\u6797\xAE\u827E\u7545\xAE\u3002</p><p>\u827E\u7545\u4E3A\u5546\u54C1\u540D\uFF0C\u5B9E\u4E3A\u4F2A\u9EBB\u7F8E\u6C99\u82AC\u6EF4\u5242\uFF0C\u6839\u636E\u836F\u54C1\u8BF4\u660E\u4E66\uFF0C\u6BCF1ml\u827E\u7545\u542B\u6709\u76D0\u9178\u4F2A\u9EBB\u9EC4\u78B19.375mg\uFF0C\u542B\u6709\u7F9F\u6EB4\u9178\u53F3\u7F8E\u6C99\u82AC3.125mg\u3002</p><p>\u4F2A\u9EBB\u9EC4\u78B1\u80FD\u523A\u6FC0\u53BB\u7532\u80BE\u4E0A\u817A\u7D20\u5206\u6CCC\uFF0C\u5BFC\u81F4\u9F3B\u7C98\u819C\u8840\u7BA1\u6536\u7F29\uFF0C\u4ECE\u800C\u51CF\u8F7B\u9F3B\u585E\u75C7\u72B6\u3002\u540C\u65F6\uFF0C\u4F2A\u9EBB\u9EC4\u78B1\u8FD8\u662F\u4E00\u79CD\u795E\u7ECF\u5174\u594B\u5242\uFF0C\u5174\u594B\u4F5C\u7528\u867D\u6BD4\u9EBB\u9EC4\u78B1\u5F31\u5F88\u591A\uFF0C\u4F46\u4F9D\u7136\u53EF\u80FD\u5BFC\u81F4\u5931\u7720\u3001\u7126\u8E81\u3001\u5174\u594B\u3001\u7729\u6655\u3001\u7126\u8651\u7B49\u95EE\u9898\u3002\u7F9F\u6EB4\u9178\u53F3\u7F8E\u6C99\u82AC\u4E3A\u4E2D\u67A2\u6B62\u54B3\u836F\uFF0C\u901A\u8FC7\u6291\u5236\u5EF6\u9AD3\u54B3\u55FD\u4E2D\u67A2\u4EA7\u751F\u6B62\u54B3\u4F5C\u7528\u3002</p><p>\u8FD9\u4E24\u79CD\u6210\u4EFD\uFF0C\u4F2A\u9EBB\u9EC4\u78B1\u5BB9\u6613\u88AB\u5236\u6210\u6BD2\u54C1\uFF0C\u5F88\u591A\u56FD\u5BB6\u90FD\u6709\u7BA1\u5236\uFF0C\u6211\u56FD\u4E5F\u4E00\u6837\uFF0C\u5355\u4F4D\u5242\u91CF\u9EBB\u9EC4\u78B1\u7C7B\u836F\u7269\u542B\u91CF\u5927\u4E8E30mg\u7684\u542B\u9EBB\u9EC4\u78B1\u7C7B\u590D\u65B9\u5236\u5242\u5217\u4E3A\u5904\u65B9\u836F\u7BA1\u7406\uFF0C\u8D2D\u4E70\u65F6\u8FD8\u9700\u8981\u767B\u8BB0\u8EAB\u4EFD\u8BC1\u4FE1\u606F\uFF0C\u827E\u7545\u4E5F\u56E0\u6B64\u5217\u4E3A\u5904\u65B9\u836F\u7BA1\u7406\u3002\u53F3\u7F8E\u6C99\u82AC\u8FC7\u91CF\u4F7F\u7528\u4E00\u6837\u53EF\u4EE5\u6210\u4E3A\u6BD2\u54C1\uFF0C\u4E00\u4E9B\u56FD\u5BB6\u4E5F\u5728\u8FDB\u884C\u7BA1\u5236\u3002</p><p>\u4F2A\u9EBB\u9EC4\u78B1\u548C\u53F3\u7F8E\u6C99\u82AC\u90FD\u662F\u611F\u5192\u6B62\u54B3\u836F\u7684\u5E38\u7528\u6210\u4EFD\uFF0C\u4E3B\u8981\u7528\u6765\u7F13\u89E3\u9F3B\u585E\u3001\u54B3\u55FD\u7684\u75C7\u72B6\uFF0C\u5728\u6210\u4EBA\u662F\u6709\u6548\u7684\u3002\u4F46\u611F\u5192\u54B3\u55FD\u836F\u5728\u513F\u7AE5\u8EAB\u4E0A\u7684\u6548\u679C\u5E76\u672A\u8BC1\u5B9E\uFF0C\u800C\u4E14\u5B89\u5168\u5242\u91CF\u4E5F\u4E0D\u5F88\u6E05\u695A\uFF0C\u7528\u836F\u8FC7\u91CF\u7B49\u95EE\u9898\u5BFC\u81F4\u4E86\u4E00\u4E9B\u5B69\u5B50\u51FA\u73B0\u62BD\u6410\u3001\u5FC3\u7387\u5FEB\u3001\u610F\u8BC6\u4E0B\u964D\u3001\u751A\u81F3\u6B7B\u4EA1\u7B49\u4E25\u91CD\u4E0D\u826F\u4E8B\u4EF6\uFF0C\u5C24\u5176\u662F2\u5C81\u4EE5\u4E0B\u7684\u5B69\u5B50\u3002</p><p>\u7F8E\u56FDFDA\u57282007\u53D1\u5E03\u4E86\u4E00\u4EFD\u516C\u5171\u5065\u5EB7\u62A5\u544A\uFF3B1\uFF3D\uFF0C\u5EFA\u8BAE\u4E0D\u8981\u7ED92\u5C81\u4EE5\u4E0B\u5B69\u5B50\u7528\u975E\u5904\u65B9\uFF08OTC\uFF09\u513F\u7AE5\u611F\u5192\u54B3\u55FD\u836F\uFF0C\u968F\u540E\u76F8\u5173\u836F\u5382\u81EA\u613F\u5C06\u7ED92\u5C81\u4EE5\u4E0B\u5B69\u5B50\u7684OTC\u611F\u5192\u54B3\u55FD\u836F\u64A4\u5E02\uFF0C\u5E76\u57282008\u5E74\u81EA\u613F\u5C06\u8FD9\u7C7B\u836F\u6807\u6CE8\u4E0A\u201C\u4E0D\u8981\u7ED94\u5C81\u4EE5\u4E0B\u5B69\u5B50\u4F7F\u7528\u201D\uFF3B2\uFF3D\u3002FDA\u5BF9\u6B64\u4E3E\u8868\u793A\u652F\u6301\u3002\u82F1\u56FD\u3001\u6FB3\u6D32\u3001\u52A0\u62FF\u5927\u8D70\u5F97\u66F4\u8FDC\uFF0C\u8981\u6C426\u5C81\u4EE5\u4E0B\u7684\u5B69\u5B50\u90FD\u4E0D\u8981\u4F7F\u7528\u6B64\u7C7B\u836F\u7269\uFF0C\u4F2A\u9EBB\u9EC4\u78B1\u548C\u53F3\u7F8E\u6C99\u82AC\u90FD\u5728\u5176\u5217\u3002\u6211\u56FD\u7684\u836F\u76D1\u5C40\u4E5F\u8F6C\u8F7D\u8FC7\u76F8\u5173\u8B66\u8BAF\uFF3B3\uFF0C4\uFF3D\u3002</p><p>\u6839\u636E\u5728drugs.com\u4E0A\u7684\u641C\u7D22\uFF0C\u827E\u7545\uFF08Antuss\uFF09\u4EC5\u5728\u4E2D\u56FD\u6709\u751F\u4EA7\u3002\u6839\u636E\u5F3A\u751F\u516C\u53F8\u7684\u8BF4\u660E\u4E66\uFF0C\u5B83**\u4EC5\u9002\u7528\u4E8E\u5A74\u5E7C\u513F\uFF0C\u4E5F\u5C31\u662F3\u5C81\u4EE5\u4E0B\u7684\u5B69\u5B50\uFF0C**\u6309\u7167\u5242\u91CF\u4F7F\u7528\u8BF4\u660E\uFF0C\u751A\u81F3\u4ECE0\u5C81\uFF0C\u4E5F\u5C31\u662F\u65B0\u751F\u513F\uFF0C\u4F53\u91CD2.5kg\u4EE5\u4E0A\u5C31\u53EF\u4EE5\u7528\u3002</p><p>\u4F5C\u4E3A\u4E00\u5BB6\u6765\u81EA\u7F8E\u56FD\u7684\u8DE8\u56FD\u516C\u53F8\uFF0C\u5F3A\u751F\u4E0D\u592A\u53EF\u80FD\u4E0D\u77E5\u9053FDA\u7684\u610F\u89C1\uFF0C\u5B83\u5728\u7F8E\u56FD\u672C\u571F\u7684\u513F\u7AE5\u611F\u5192\u836F\uFF0C\u65E0\u8BBA\u662F\u542B\u6709\u4F2A\u9EBB\u9EC4\u78B1\u8FD8\u662F\u542B\u6709\u53F3\u7F8E\u6C99\u82AC\uFF0C\u90FD\u6807\u6CE8\u4E0A\u4E86\u201C <strong>under 4 years\uFF1Ado not use</strong>\u201D\uFF3B5\uFF3D\u3002\u5728\u7F8E\u56FD\u4E0D\u5EFA\u8BAE\u7528\u4E8E4\u5C81\u4EE5\u4E0B\u5B69\u5B50\u7684\u836F\uFF0C\u5728\u4E2D\u56FD\u5374\u4E13\u95E8\u7ED93\u5C81\u4EE5\u4E0B\u7684\u5B69\u5B50\u7528\u3002\u4E0B\u56FE\u4E2D\u53E6\u5916\u4E00\u6B3E\u611F\u5192\u54B3\u55FD\u836F\u4E3A\u5F3A\u751F\u516C\u53F8\u5728\u7F8E\u56FD\u7684\u4EA7\u54C1\uFF0C\u540C\u6837\u542B\u6709\u53F3\u7F8E\u6C99\u82AC\uFF0C\u548C\u827E\u7545\u4E0D\u540C\u7684\u662F\u7F29\u8840\u7BA1\u836F\u4EE5\u53BB\u6C27\u80BE\u4E0A\u817A\u7D20\u4EE3\u66FF\u4E86\u4F2A\u9EBB\u9EC4\u78B1\u3002</p><p>\u4E0D\u662F\u8BF4\u7F8E\u56FD\u6709\u8FD9\u6837\u7684\u89C4\u5B9A\uFF0C\u5728\u4E2D\u56FD\u5C31\u5E94\u8BE5\u540C\u6837\u6267\u884C\uFF0C\u6BD5\u7ADF\u4E2D\u56FD\u7684\u836F\u76D1\u90E8\u95E8\u6CA1\u6709\u63D0\u51FA\u76F8\u5173\u89C4\u5B9A\u3002\u4F46\u662F\uFF0C\u4F5C\u4E3A\u4E00\u5BB6\u4E16\u754C\u6700\u5927\u7684\u836F\u5382\uFF0C\u5728\u660E\u77E5\u6B64\u7C7B\u836F\u7269\u5BF9\u8FD9\u4E2A\u5E74\u9F84\u6BB5\u5B69\u5B50\u6548\u679C\u4E0D\u786E\u5207\uFF0C\u4EA7\u751F\u7684\u4E25\u91CD\u4E0D\u826F\u53CD\u5E94\u4E3B\u8981\u53D1\u751F\u57282\u5C81\u4EE5\u4E0B\uFF0C\u836F\u54C1\u9002\u7528\u5E74\u9F84\u5374\u7279\u522B\u6307\u5B9A\u4E3A0-3\u5C81\u7684\u5B69\u5B50\uFF0C\u8FD9\u6837\u505A\u662F\u4E0D\u662F\u5F88\u4E0D\u539A\u9053\uFF1F</p><p>\u800C\u53E6\u5916\u4E00\u5BB6\u7F8E\u56FD\u836F\u5382\u60E0\u6C0F\u5236\u836F\u5728\u4E2D\u56FD\u7684\u4E00\u6B3E\u540C\u7C7B\u4EA7\u54C1\uFF0C\u60E0\u83F2\u5B81\uFF0C\u548C\u827E\u7545\u4E00\u6837\u4E3B\u8981\u4E3A\u4F2A\u9EBB\u9EC4\u78B1\u548C\u53F3\u7F8E\u6C99\u82AC\uFF0C\u91CC\u9762\u591A\u4E86\u4E2A\u6251\u5C14\u654F\uFF0C\u6BCF\u6BEB\u5347\u542B\u6C22\u6EB4\u9178\u53F3\u7F8E\u6C99\u82AC1\u6BEB\u514B\uFF0C\u76D0\u9178\u4F2A\u9EBB\u9EC4\u78B13\u6BEB\u514B\uFF0C\u6BD4\u827E\u7545\u5C11\u5F88\u591A\uFF0C\u5B83\u7684\u8BF4\u660E\u4E66\u91CC\u9002\u7528\u4EBA\u7FA4\u5374\u4E3A<strong>2\u5C81\u4EE5\u4E0A</strong>\u3002</p><p>\u6240\u4E0D\u540C\u7684\u662F\uFF0C\u60E0\u83F2\u5B81\u662F\u975E\u5904\u65B9\u836F\uFF0C\u827E\u7545\u662F\u5904\u65B9\u836F\u3002</p><p>\u4F46\u4E00\u4E2A\u836F\u54C1\u7684\u529F\u6548\u548C\u526F\u4F5C\u7528\u4E0D\u4F1A\u56E0\u4E3A\u5B83\u7684\u836F\u54C1\u9500\u552E\u65B9\u5F0F\u800C\u6539\u53D8\uFF0C\u65E0\u8BBA\u662F\u5904\u65B9\u836F\u8FD8\u662F\u975E\u5904\u65B9\u836F\uFF0C\u90FD\u6539\u53D8\u4E0D\u4E86\u611F\u5192\u54B3\u55FD\u836F\u5BF9\u5E7C\u513F\u6548\u679C\u4E0D\u786E\u5207\uFF0C\u53EF\u80FD\u53D1\u751F\u4E25\u91CD\u526F\u4F5C\u7528\u98CE\u9669\u8FD9\u4E00\u4E8B\u5B9E\u3002\u7F8E\u82F1\u7B49\u56FD\u53EA\u5BF9OTC\u611F\u5192\u54B3\u55FD\u836F\u4F5C\u51FA\u5E74\u9F84\u89C4\u5B9A\uFF0C\u662F\u56E0\u4E3A\u8FD9\u7C7B\u836F\u7269\u5728\u8FD9\u4E9B\u56FD\u5BB6\u90FD\u662FOTC\u5F62\u5F0F\u9500\u552E\uFF0C\u5E76\u4E0D\u7B49\u4E8E\u533B\u751F\u5F00\u8FD9\u7C7B\u836F\u7ED9\u5B69\u5B50\u5C31\u662F\u5408\u7406\u7684\u3002</p><p>\u7F8E\u56FD\u513F\u79D1\u5B66\u4F1A(AAP)\u836F\u54C1\u59D4\u5458\u4F1A\u57281997\u5E74\u5C31\u66FE\u53D1\u5E03\u8FC7\u4E00\u4EFD\u58F0\u660E\uFF3B6\uFF3D\uFF0C\u8BA4\u4E3A\u53EF\u5F85\u56E0\u6216\u53F3\u7F8E\u6C99\u82AC\u7528\u4F5C\u513F\u7AE5\u6B62\u54B3\u836F\uFF0C\u5B89\u5168\u548C\u6709\u6548\u6027\u90FD\u6CA1\u6709\u7ECF\u8FC7\u5F88\u597D\u7684\u9A8C\u8BC1\uFF0C\u76EE\u524D\u6CA1\u6709\u5236\u5B9A\u7ED9\u5B69\u5B50\u7528\u8FD9\u4E9B\u836F\u7684\u6307\u5F81\uFF0C\u8FD9\u4EFD\u58F0\u660E\u57282006\u5E74\u8FDB\u884C\u4E86\u91CD\u7533\u3002AAP\u4E5F\u5EFA\u8BAE\u4E0D\u8981\u7ED96\u5C81\u4EE5\u4E0B\u5B69\u5B50\u4F7F\u7528\u975E\u5904\u65B9\u611F\u5192\u54B3\u55FD\u836F\u3002\u79D1\u666E\u5F53\u7136\u4E0D\u80FD\u8BA9\u5927\u5BB6\u62D2\u7EDD\u533B\u751F\u5F00\u7684\u5904\u65B9\u836F\uFF0C\u4F46\u77E5\u8BC6\u53CA\u65F6\u66F4\u65B0\u5E76\u9075\u5FAA\u533B\u5B66\u539F\u5219\u7684\u533B\u751F\u5E94\u8BE5\u4E0D\u4F1A\u7ED9\u5B69\u5B50\u5F00\u8FD9\u7C7B\u836F\u3002</p><p>\u4F46\u4E2D\u56FD\u533B\u751F\u77E5\u8BC6\u66F4\u65B0\u6BD4\u8F83\u6162\uFF0C\u5927\u90E8\u5206\u533B\u751F\u7528\u836F\u662F\u53C2\u8003\u836F\u54C1\u8BF4\u660E\u4E66\uFF0C\u5BF9\u8FD9\u79CD\u5927\u578B\u8DE8\u56FD\u836F\u4F01\u7684\u4EA7\u54C1\u5F80\u5F80\u66F4\u4FE1\u4EFB\uFF0C\u5F3A\u751F\u516C\u53F8\u5374\u63D0\u4F9B\u4E86\u4E00\u4EFD\u548C\u533B\u5B66\u539F\u5219\u660E\u663E\u51B2\u7A81\u7684\u8BF4\u660E\u4E66\uFF0C\u5B9E\u9645\u4E0A\u8D77\u7740\u8BEF\u5BFC\u533B\u751F\u7528\u836F\u7684\u4F5C\u7528\uFF0C\u8BA9\u533B\u751F\u8BEF\u8BA4\u4E3A\u8FD9\u4E2A\u836F\u5BF9\u5A74\u513F\u611F\u5192\u662F\u6709\u6548\u800C\u5B89\u5168\u800C\u5F00\u7ED9\u5B69\u5B50\uFF0C\u4EE5\u81F3\u4E8E\u5728\u5FAE\u535A\u4E0A\u8FD8\u770B\u5230\u513F\u79D1\u533B\u751F\u63A8\u8350\u8FC72\u5C81\u4EE5\u5185\u7684\u5B69\u5B50\u7528\u8FD9\u4E2A\u836F\u3002</p><p>\u66F4\u4F55\u51B5\u4E2D\u56FD\u5904\u65B9\u836F\u7BA1\u7406\u5F88\u4E0D\u89C4\u8303\uFF0C\u6CA1\u6709\u5904\u65B9\u8FDE\u6297\u751F\u7D20\u90FD\u53EF\u4EE5\u8F7B\u6613\u4E70\u5230\uFF0C\u66F4\u522B\u8BF4\u8FD9\u6837\u4E00\u6B3E\u513F\u7AE5\u611F\u5192\u836F\u4E86\uFF0C\u5728\u5FAE\u535A\u4E0A\u8C03\u67E5\u5927\u90E8\u5206\u5BB6\u957F\u53CD\u5E94\u81EA\u5DF1\u53EF\u4EE5\u5728\u836F\u5E97\u4E70\u5230\uFF0C\u6709\u7684\u8FDE\u8EAB\u4EFD\u8BC1\u4E5F\u4E0D\u9700\u8981\u767B\u8BB0\u3002</p><p>\u5F53\u7136\uFF0C\u827E\u7545\u7684\u8BF4\u660E\u4E66\u4E5F\u662F\u836F\u76D1\u90E8\u95E8\u6838\u5B9A\u7684\uFF0C\u4ECE\u6CD5\u5F8B\u4E0A\u6765\u8BF4\uFF0C\u5F3A\u751F\u516C\u53F8\u8FD9\u6837\u9500\u552E\u827E\u7545\u4E5F\u662F\u5408\u6CD5\u5408\u89C4\u7684\uFF0C\u5373\u4FBF\u51FA\u4E86\u4E25\u91CD\u5E76\u53D1\u75C7\uFF0C\u4E5F\u4E0D\u592A\u53EF\u80FD\u50CF\u5728\u53D1\u8FBE\u56FD\u5BB6\u90A3\u6837\u9762\u4E34\u9AD8\u989D\u8D54\u507F\uFF0C\u8981\u602A\u4E5F\u53EA\u80FD\u602A\u6211\u4EEC\u7684\u76D1\u7BA1\u843D\u540E\u3002</p><p>\u611F\u5192\u51E0\u4E4E\u662F\u6700\u5E38\u89C1\u7684\u75BE\u75C5\uFF0C\u513F\u7AE5\u53C8\u662F\u611F\u5192\u591A\u53D1\u4EBA\u7FA4\uFF0C\u513F\u7AE5\u611F\u5192\u836F\u662F\u4E00\u4E2A\u5F88\u5927\u7684\u5E02\u573A\uFF0C\u5F3A\u751F\u516C\u53F8\u4E4B\u6240\u4EE5\u4E0D\u60DC\u8FDD\u80CC\u533B\u5B66\u539F\u5219\uFF0C\u5728\u4E2D\u56FD\u5E02\u573A\u751F\u4EA7\u4E0D\u8BE5\u7ED9\u5A74\u5E7C\u513F\u7528\u7684\u827E\u7545\uFF0C\u505A\u522B\u4EBA\u4E0D\u6562\u505A\u7684\u5E02\u573A\uFF0C\u5F53\u7136\u662F\u4E3A\u4E86\u66F4\u591A\u5229\u6DA6\u3002</p><p>\u836F\u4F01\u5728\u4E0D\u8FDD\u89C4\u8FDD\u6CD5\u7684\u524D\u63D0\u4E0B\uFF0C\u4E3A\u4E86\u5229\u76CA\u6700\u5927\u5316\u505A\u51FA\u4EC0\u4E48\u4E8B\u90FD\u53EF\u4EE5\u7406\u89E3\uFF0C\u76F8\u5BF9\u4E8E\u56FD\u5185\u90A3\u4E9B\u7ED9\u5C0F\u513F\u611F\u5192\u836F\u91CC\u6DFB\u52A0\u91D1\u521A\u70F7\u80FA\u4E4B\u7C7B\u6297\u75C5\u6BD2\u836F\u7269\u7684\u672C\u571F\u836F\u4F01\uFF0C\u5F3A\u751F\u4E5F\u8BB8\u8FD8\u4E0D\u7B97\u6700\u4E0D\u582A\u7684\uFF0C\u4F46\u5177\u6709\u8BBD\u523A\u610F\u5473\u7684\u662F\uFF0C\u8FD9\u5BB6\u8DE8\u56FD\u516C\u53F8\u5728\u4E2D\u56FD\u7684\u4E00\u4E2A\u5BA3\u4F20\u53E3\u53F7\u662F\u201C\u56E0\u7231\u800C\u751F\u201D\u2026</p><p>\u4F5C\u4E3A\u5BB6\u957F\u9700\u8981\u77E5\u9053\u7684\u662F\uFF0C\u65E0\u8BBA\u662F\u672C\u571F\u7684\u836F\u4F01\uFF0C\u8FD8\u662F\u8DE8\u56FD\u836F\u4F01\uFF0C\u5728\u843D\u540E\u7684\u836F\u76D1\u7BA1\u7406\u4E4B\u4E0B\uFF0C\u90FD\u4F1A\u89C1\u5229\u5FD8\u4E49\uFF0C\u518D\u52A0\u4E0A\u533B\u7597\u4F53\u5236\u7684\u843D\u540E\uFF0C\u56FD\u5185\u6307\u5357\u4E5F\u6CA1\u63D0\u8FC7\u8FD9\u7C7B\u611F\u5192\u836F\u7684\u4F7F\u7528\u5E74\u9F84\u9650\u5236\uFF0C\u533B\u751F\u7ED9\u65B0\u751F\u513F\u5F00\u8FD9\u4E2A\u836F\u4E5F\u7B97\u5408\u89C4\u7684\uFF0C\u5B69\u5B50\u7528\u836F\u9677\u9631\u65E0\u5904\u4E0D\u5728\uFF0C\u53EA\u6709\u81EA\u5DF1\u591A\u5B66\u4E60\u6765\u4FDD\u62A4\u81EA\u5DF1\u5B69\u5B50\u3002</p><p>\u56E0\u4E3A\u827E\u7545\u4E4B\u7C7B\u7684\u611F\u5192\u836F\u4E0D\u826F\u53CD\u5E94\u53D1\u751F\u7387\u603B\u4F53\u4E0D\u662F\u90A3\u4E48\u9AD8\uFF0C\u5E76\u4E0D\u662F\u6240\u6709\u5403\u4E86\u5C31\u4E00\u5B9A\u4F1A\u6709\u95EE\u9898\uFF0C\u5982\u679C\u5B69\u5B50\u5403\u8FC7\u4E86\u6CA1\u6709\u4E0D\u826F\u53CD\u5E94\u5C31\u4E0D\u7528\u4E3A\u6B64\u62C5\u5FC3\uFF0C\u6CA1\u5403\u8FC7\u7684\u5C31\u4E0D\u503C\u5F97\u8BA9\u5B69\u5B50\u4E3A\u4E86\u4E0D\u786E\u5207\u7684\u6548\u679C\u53BB\u5192\u8FD9\u4E9B\u98CE\u9669\uFF0C\u5C24\u5176\u662F\u98CE\u9669\u66F4\u9AD8\u7684\u5A74\u5E7C\u513F\u3002</p><p>\u5BF9\u4E8E\u611F\u5192\u4F60\u9700\u8981\u8BB0\u4F4F\u8FD9\u6BB5\u8BDD\uFF1A\u666E\u901A\u611F\u5192\u662F\u81EA\u6108\u6027\u75BE\u75C5\uFF0C\u9F3B\u585E\u3001\u54B3\u55FD\u9664\u4E86\u8BA9\u4EBA\u96BE\u53D7\u5916\uFF0C\u4E0D\u4F1A\u5BF9\u8EAB\u4F53\u9020\u6210\u4EC0\u4E48\u4F24\u5BB3\uFF0C\u611F\u5192\u54B3\u55FD\u836F\u90FD\u662F\u5BF9\u75C7\u6CBB\u7597\uFF0C\u65E0\u8BBA\u662F\u56FD\u4EA7\u8FD8\u662F\u8FDB\u53E3\uFF0C\u90FD\u4E0D\u4F1A\u5BF9\u75C5\u60C5\u6062\u590D\u6709\u5B9E\u8D28\u6027\u5E2E\u52A9\uFF0C\u7528\u4E86\u9876\u591A\u80FD\u6539\u5584\u75C7\u72B6\uFF0C\u4E0D\u7528\u4E5F\u803D\u8BEF\u4E0D\u4E86\u75C5\u60C5\u3002\u5BF9\u513F\u7AE5\u6765\u8BF4\uFF0C\u7528\u4E86\u4E5F\u672A\u5FC5\u80FD\u7F13\u89E3\u75C7\u72B6\uFF0C\u5BF9\u5A74\u5E7C\u513F\u53CD\u800C\u53EF\u80FD\u4EA7\u751F\u4E25\u91CD\u526F\u4F5C\u7528\uFF0C\u5982\u679C\u4F60\u5B69\u5B50\u4E0D\u52304\u5C81\uFF0C\u4E0D\u8981\u7ED9\u4ED6\u5403\u8FD9\u7C7B\u836F\u3002</p><p><strong>\u53C2\u8003\u8D44\u6599\uFF1A</strong></p><p>\uFF3B1\uFF3Dhttp://www.fda.gov/NewsEvents/Newsroom/PressAnnouncements/2008/ucm116839.htm</p><p>\uFF3B2\uFF3Dhttp://www.fda.gov/drugs/resourcesforyou/specialfeatures/ucm263948.htm</p><p>\uFF3B3\uFF3Dhttp://www.sda.gov.cn/WS01/CL0389/37698.html</p><p>\uFF3B4\uFF3Dhttp://www.sda.gov.cn/WS01/CL0389/78161.html</p><p>[5] http://www.sudafed.com/products/childrens</p><p>\uFF3B6\uFF3DAAP Committee on Drugs.Use of Codeine- and Dextromethorphan-Containing Cough Remedies in Children.Pediatrics. 1997 Jun;99(6):918-20</p><p>\u76F8\u5173\u9605\u8BFB\uFF0C\u5173\u6CE8\u8FD9\u4E2A\u8D26\u53F7\u540E\u56DE\u590D\u6570\u5B57\u5173\u952E\u8BCD\u53EF\u67E5\u770B\u6587\u7AE0</p><p>69 \u975E\u5904\u65B9\u836F\u91CC\u7684\u5904\u65B9\u836F</p><p>92 \u6CA1\u7528\u8FD8\u53EF\u80FD\u6709\u5BB3\u7684\u513F\u79D1\u836F\u7269</p><p>100 \u5982\u4F55\u5E94\u5BF9\u5B69\u5B50\u611F\u5192</p>',35),h={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=400200254&idx=1&sn=142b937fc7d5d410f88c027e5f81fa6e&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},u=c("\u67E5\u770B\u539F\u6587");function l(_,g){const p=t("ExternalLinkIcon");return n(),o(s,null,[m,e("p",null,[e("a",h,[u,r(p)])])],64)}var x=d(i,[["render",l]]);export{x as default};
