import{r as a,o as r,c as t,a as e,b as c,F as n,d as o,e as s}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const _={},i=o('<h3 id="\u6D88-\u5B57\u53F7\u76AE\u80A4\u4EA7\u54C1\u4E0D\u5B8C\u5168\u540D\u5355" tabindex="-1"><a class="header-anchor" href="#\u6D88-\u5B57\u53F7\u76AE\u80A4\u4EA7\u54C1\u4E0D\u5B8C\u5168\u540D\u5355" aria-hidden="true">#</a> \u201C\u6D88\u201D\u5B57\u53F7\u76AE\u80A4\u4EA7\u54C1\u4E0D\u5B8C\u5168\u540D\u5355</h3><p>\u201C\u6D88\u201D\u5B57\u53F7\u662F\u7ED9\u6D88\u6BD2\u4EA7\u54C1\u7684\u6279\u53F7\uFF0C\u4EC0\u4E48\u662F\u6D88\u6BD2\u4EA7\u54C1\u5462\uFF1F\u7528\u4E8E\u76AE\u80A4\u3001\u9ECF\u819C\uFF0C\u9910\u996E\u5177\uFF0C\u74DC\u679C\u852C\u83DC\uFF0C\u7269\u4F53\u8868\u9762\u7684\u6D88\u6BD2\u5242\u90FD\u662F\u6D88\u6BD2\u4EA7\u54C1\uFF0C\u6BD4\u5982\u5927\u5BB6\u6240\u719F\u77E5\u768484\u6D88\u6BD2\u6DB2\u3002</p><p>\u533B\u9662\u91CC\u7528\u7684\u6CE8\u5C04\u5668\u3001\u8F93\u6DB2\u5668\u3001\u6D88\u6BD2\u68C9\u7B7E\u3001\u7EB1\u5E03\uFF0C\u4EE5\u53CA\u5927\u5BB6\u5E73\u65F6\u7528\u7684\u536B\u751F\u7528\u54C1\uFF0C\u6BD4\u5982\u7EB8\u5DFE\u3001\u6E7F\u5DFE\u3001\u536B\u751F\u5DFE\u3001\u5C3F\u4E0D\u6E7F\u6DB2\u4E5F\u662F\u6D88\u6BD2\u4EA7\u54C1\uFF0C\u6234\u7684\u9690\u5F62\u773C\u955C\u62A4\u7406\u6DB2\uFF0C\u751A\u81F3\u907F\u5B55\u5957\u90FD\u662F\u6D88\u6BD2\u4EA7\u54C1\uFF0C\u5177\u4F53\u7684\u4EA7\u54C1\u7C7B\u522B\u53EF\u4EE5\u53BB\u7F51\u4E0A\u641C\u300A\u6D88\u6BD2\u4EA7\u54C1\u5206\u7C7B\u76EE\u5F55\u300B\u770B\u770B\u3002</p><p>\u6211\u62FF\u8D77\u6211\u684C\u5B50\u4E0A\u7684\u8FD9\u5305\u7EF4\u8FBE\u7EB8\u5DFE\uFF0C\u5728\u5916\u5305\u88C5\u4E0A\u5C31\u53EF\u4EE5\u5F88\u6E05\u6670\u7684\u770B\u5230\uFF1A\u6D59\u536B\u6D88\u8BC1\u5B57\uFF082015\uFF09\u7B2C0031\u53F7\u3002</p><p>\u65E2\u7136\u662F\u6D88\u6BD2\u4EA7\u54C1\u7684\u6279\u53F7\uFF0C\u4E3A\u4EC0\u4E48\u90A3\u4E9B\u6CBB\u6E7F\u75B9\u7684\u818F\u836F\u4E5F\u80FD\u62FF\u5230\u5462\uFF1F</p><p>\u6309\u89C4\u5B9A\u672A\u5217\u5165\u6D88\u6BD2\u4EA7\u54C1\u5206\u7C7B\u76EE\u5F55\u7684\u662F\u4E0D\u80FD\u7533\u8BF7\u7684\uFF0C\u4F46\u662F\u6D88\u5B57\u53F7\u7684\u5BA1\u6279\u6BD4\u8F83\u5BBD\u677E\uFF0C\u53EA\u9700\u8981\u5411\u5F53\u5730\u7684\u7701\u7EA7\u536B\u751F\u673A\u6784\u7533\u8BF7\u5373\u53EF\uFF0C\u800C\u4E14\u5BA1\u6838\u4E0D\u4E25\u53EF\u4EE5\u6253\u64E6\u8FB9\u7403\uFF0C\u53EA\u8981\u6253\u70B9\u5230\u4F4D\uFF0C\u62A4\u80A4\u7528\u7684\u4EA7\u54C1\u91CC\u5982\u679C\u58F0\u79F0\u542B\u6709\u6291\u83CC\u6210\u5206\u5C31\u53EF\u4EE5\u6279\u4E0B\u6765\u3002</p><p>\u56E0\u4E3A\u6D88\u6BD2\u4EA7\u54C1\u6CA1\u6709\u4EFB\u4F55\u7684\u6CBB\u7597\u529F\u6548\uFF0C\u6240\u4EE5\u4E0D\u9700\u8981\u50CF\u6B63\u89C4\u836F\u54C1\u90A3\u6837\u505A\u4E2A\u51E0\u671F\u4E34\u5E8A\u8BD5\u9A8C\u6765\u9A8C\u8BC1\u6709\u6548\u6027\u548C\u5B89\u5168\u6027\uFF0C\u4E00\u822C\u4E00\u4E2A\u6708\u5C31\u53EF\u4EE5\u62FF\u5230\u6279\u53F7\uFF0C\u5BA1\u6279\u8D39\u4E5F\u53EA\u8981\u51E0\u5343\u5757\uFF0C\u800C\u4E14\u751F\u4EA7\u7684\u6761\u4EF6\u3001\u68C0\u6D4B\u7684\u8981\u6C42\u4E5F\u5F88\u4F4E\uFF0C\u6240\u4EE5\u4EA7\u54C1\u6210\u5206\u8FD9\u4E9B\u4E5F\u6709\u5F88\u5927\u7684\u64CD\u4F5C\u7A7A\u95F4\u3002</p><p>\u6BD5\u7ADF\u662F\u6D88\u6BD2\u4EA7\u54C1\uFF0C\u552F\u4E00\u7684\u7528\u9014\u4E5F\u5C31\u662F\u6D88\u6BD2\u6E05\u6D17\u7C7B\u7C7B\uFF0C\u6240\u4EE5\u56FD\u5BB6\u4E5F\u89C4\u5B9A\uFF0C\u6D88\u5B57\u53F7\u7684\u4EA7\u54C1\u6807\u7B7E\u548C\u8BF4\u660E\u4E66\u90FD\u4E0D\u80FD\u6807\u6CE8\u75BE\u75C5\u75C7\u72B6\u548C\u75BE\u75C5\u540D\u79F0\uFF0C\u4E5F\u4E0D\u80FD\u6697\u793A\u6709\u4EFB\u4F55\u7684\u6CBB\u7597\u529F\u6548\u3002</p><p>\u4F46\u5B9E\u9645\u64CD\u4F5C\u4E2D\uFF0C\u76D1\u7BA1\u5374\u975E\u5E38\u5BBD\u677E\uFF0C\u6240\u4EE5\u6309\u6D88\u6BD2\u4EA7\u54C1\u7533\u8BF7\u4E86\u4E0B\u6765\uFF0C\u7528\u9014\u5C31\u53EF\u4EE5\u81EA\u884C\u53D1\u6325\u3002\u53EF\u4EE5\u628A\u4EA7\u54C1\u5916\u89C2\u8BBE\u8BA1\u6210\u836F\u54C1\u3001\u5316\u5986\u54C1\u7684\u6A21\u6837\uFF0C\u522B\u4EBA\u6839\u672C\u5C31\u533A\u5206\u4E0D\u4E86\u8FD9\u662F\u836F\u54C1\u3001\u5316\u5986\u54C1\u8FD8\u662F\u6D88\u6BD2\u54C1\uFF1B\u8FD8\u53EF\u4EE5\u628A\u6D88\u5B57\u53F7\u6279\u53F7\u5370\u9690\u853D\u4E00\u70B9\u5C3D\u91CF\u4E0D\u8BA9\u4EBA\u6CE8\u610F\uFF0C\u54EA\u6015\u88AB\u6CE8\u610F\u5230\u4E86\uFF0C\u5927\u90E8\u5206\u4EBA\u4E5F\u5206\u4E0D\u6E05\u8FD9\u4E9B\u4EA7\u54C1\u6279\u53F7\u7684\u5BF9\u5E94\u7684\u4EA7\u54C1\u6027\u8D28\u3002</p><p>\u800C\u4E14\u73B0\u5728\u7684\u7F51\u7EDC\u5BA3\u4F20\u9014\u5F84\u548C\u65B9\u5F0F\u4E5F\u975E\u5E38\u591A\uFF0C\u54EA\u6015\u4EA7\u54C1\u6807\u7B7E\u548C\u5305\u88C5\u4E0A\u4E0D\u597D\u5BA3\u4F20\uFF0C\u5728\u7F51\u7EDC\u4E0A\u5356\u7684\u65F6\u5019\u8FD8\u662F\u53EF\u4EE5\u505A\u5404\u79CD\u6216\u660E\u6216\u6697\u7684\u529F\u6548\u6697\u793A\uFF0C\u8FD8\u53EF\u4EE5\u5728\u627E\u5FAE\u5546\u505A\u4EE3\u7406\uFF0C\u53EA\u8981\u8425\u9500\u80FD\u529B\u5F3A\uFF0C\u6216\u8005\u5728\u4EA7\u54C1\u91CC\u52A0\u70B9\u6FC0\u7D20\u6765\u589E\u5F3A\u6548\u679C\uFF0C\u5C06\u51E0\u6BDB\u94B1\u6210\u672C\u7684\u51E1\u58EB\u6797\u5356\u5230\u51E0\u767E\u5757\u5E76\u4E0D\u96BE\u3002</p><p>\u653F\u5E9C\u867D\u7136\u4E5F\u53D1\u8FC7\u6587\u8BF4\u8981\u5927\u5BB6\u8B66\u60D5\u6807\u8BC6\u201C\u6D88\u5B57\u53F7\u201D\u4EA7\u54C1\u5192\u5145\u836F\u54C1\u7684\u516C\u544A\uFF0C\u5B9E\u5982\u679C\u6CA1\u6709\u5B9E\u9645\u884C\u52A8\u7684\u76D1\u7BA1\uFF0C\u8FD9\u6837\u7684\u516C\u544A\u4E5F\u5C31\u6D41\u4E8E\u5F62\u5F0F\u3002</p><p>\u4E5F\u6B63\u56E0\u5982\u6B64\uFF0C\u201C\u6D88\u201D\u5B57\u53F7\u6210\u4E3A\u5F88\u591A\u5C0F\u5546\u5BB6\u7684\u9996\u9009\uFF0C\u6240\u4EE5\u5E02\u9762\u4E0A\u624D\u6709\u90A3\u4E48\u591A\u529F\u6548\u795E\u5947\u7684\u6D88\u5B57\u53F7\u4EA7\u54C1\u3002\u5F88\u591A\u5927\u5BB6\u53E3\u53E3\u76F8\u4F20\uFF0C\u751A\u81F3\u82B1\u51E0\u5341\u751A\u81F3\u51E0\u767E\u5757\u4E70\u6765\u7684\u4E00\u5C0F\u7F50\u7684\u795E\u5947\u6E7F\u75B9\u818F\uFF0C\u5176\u5B9E\u90FD\u662F\u201C\u6D88\u201D\u5B57\u53F7\u7684\u6D88\u6BD2\u5242\uFF0C\u5B83\u4EEC\u5BF9\u76AE\u80A4\u95EE\u9898\u7684\u6548\u679C\uFF0C\u4E0D\u4F1A\u8D85\u8FC7\u4E00\u7F50\u51E1\u58EB\u6797\u5E26\u6765\u7684\u4FDD\u6E7F\u6548\u679C\uFF0C\u8981\u662F\u6709\u5176\u5B83\u7684\u6548\u679C\uFF0C\u8981\u4E48\u662F\u865A\u5047\u5BA3\u4F20\uFF0C\u8981\u4E48\u662F\u52A0\u4E86\u6FC0\u7D20\u6216\u5176\u5B83\u836F\u7269\uFF0C\u5426\u5219\u771F\u90A3\u4E48\u795E\u5947\uFF0C\u65E9\u5C31\u53BB\u7533\u8BF7\u51C6\u5B57\u53F7\u7684\u836F\u54C1\u6279\u53F7\u5230\u533B\u9662\u3001\u836F\u5E97\u53BB\u5356\u4E86\u3002</p><p>\u4E0B\u9762\u8FD9\u4E9B\u662F\u6839\u636E\u5927\u5BB6\u7559\u8A00\u540E\u67E5\u5230\u7684\u4E00\u4E9B\u6D88\u5B57\u53F7\u7684\u76AE\u80A4\u4EA7\u54C1\uFF0C\u5176\u4E2D\u4E0D\u4E4F30g\u5356280\u5143\uFF0C\u5355\u5E97\u6708\u9500\u51E0\u5343\u652F\u7684\u6D88\u6BD2\u5242\uFF0C\u5728\u8FD9\u91CC\u5E2E\u5B83\u4EEC\u514D\u8D39\u5E7F\u544A\u4E00\u4E0B\uFF0C\u5927\u5BB6\u53EF\u4EE5\u5BF9\u7167\u4E00\u4E0B\u81EA\u5DF1\u5BB6\u7528\u7684\u4EA7\u54C1\u3002</p><p>**\u4EAC\u513F\u80A4\u4E50\u971C\uFF1A**\u9ED1\u536B\u6D88\u8BC1\u5B57\uFF082004\uFF09\u7B2C0270\u53F7</p><p>**\u6212\u4E4B\u9986\u5A74\u4EB2\u971C\uFF1A**\u8C6B\u536B\u6D88\u8BC1\u5B57\uFF082008\uFF09\u7B2C0012\u53F7</p><p><strong>\u80A4\u4E13\u5BB6</strong>\uFF1A\u9102\u536B\u6D88\u8BC1\u5B57\uFF082012\uFF09\u7B2C0025\u53F7</p><p><strong>\u79E6\u6717</strong>\uFF1A\u8D63\u536B\u6D88\u8BC1\u5B57\uFF082008\uFF09\u7B2C0462\u53F7</p><p>**\u767E\u5349\u818F\uFF1A**\u7CA4\u536B\u6D88\u8BC1\u5B57\uFF082002\uFF09\u7B2C0101\u53F7</p><p>**\u674F\u749E\u971C\uFF1A**\u5180\u536B\u6D88\u8BC1\u5B572013\u7B2C0041\u53F7</p><p><strong>\u7EA2\u82F9\u679C\u5B9D\u5B9D\u723D</strong>\uFF1A\u6D59\u536B\u6D88\u8BC1\u5B57\uFF082005\uFF09\u7B2C0066\u53F7</p><p>**\u5B9D\u80A4\u723D\uFF1A**\u8D63\u536B\u6D88\u8BC1\u5B572010 \u7B2C0072\u53F7</p><p>\u5F53\u7136\u8FD9\u4E2A\u540D\u5355\u662F\u4E5F\u662F\u8FDC\u4E0D\u9F50\u5168\u7684\uFF0C\u5927\u5BB6\u53EF\u4EE5\u7FFB\u770B\u4E00\u4E0B\u81EA\u5BB6\u7528\u8FC7\u7684\u6E7F\u75B9\u818F\u7684\u5305\u88C5\uFF0C\u770B\u5230\u6709\u6D88\u5B57\u53F7\u7684\u53EF\u4EE5\u4EA7\u54C1\u5305\u88C5\u4E0A\u5E26\u6709\u4EA7\u54C1\u6279\u53F7\u90E8\u4F4D\u53D1\u5230\u540E\u53F0\u6765\uFF0C\u6709\u65F6\u95F4\u7684\u8BDD\u6211\u518D\u6574\u7406\u4E00\u4E0B\u516C\u544A\u51FA\u6765\uFF0C\u5927\u5BB6\u4E5F\u53EF\u4EE5\u8F6C\u53D1\u7ED9\u66F4\u591A\u4EBA\u770B\u5230\uFF0C\u907F\u514D\u66F4\u591A\u4EBA\u82B1\u4E86\u51A4\u6789\u94B1\u8FD8\u803D\u8BEF\u5B69\u5B50\u6CBB\u7597\u3002</p>',22),l={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651627218&idx=1&sn=53ba0bdf782c21ac6e449eb9f41ee04c&chksm=8b5cb728bc2b3e3e5e13d1b4df97924a32f85b6700e088eabfc737902e7af7b890c5c0cc01c5&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},f=s("\u67E5\u770B\u539F\u6587");function h(b,m){const p=a("ExternalLinkIcon");return r(),t(n,null,[i,e("p",null,[e("a",l,[f,c(p)])])],64)}var u=d(_,[["render",h]]);export{u as default};
