import{r as t,o as a,c as r,a as e,b as o,F as n,d as c,e as s}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const _={},i=c('<h3 id="\u300C\u4E0D\u6253\u6270\u300D\u5B69\u5B50-\u5C31\u662F\u8BA9\u5B69\u5B50\u81EA\u5DF1\u4E00\u4E2A\u4EBA\u73A9\u5417" tabindex="-1"><a class="header-anchor" href="#\u300C\u4E0D\u6253\u6270\u300D\u5B69\u5B50-\u5C31\u662F\u8BA9\u5B69\u5B50\u81EA\u5DF1\u4E00\u4E2A\u4EBA\u73A9\u5417" aria-hidden="true">#</a> \u300C\u4E0D\u6253\u6270\u300D\u5B69\u5B50\uFF0C\u5C31\u662F\u8BA9\u5B69\u5B50\u81EA\u5DF1\u4E00\u4E2A\u4EBA\u73A9\u5417\uFF1F</h3><blockquote><p>\u6211\u542C\u522B\u4EBA\u8BF4\u5B69\u5B50\u4E00\u4EBA\u8BA4\u771F\u73A9\u7684\u65F6\u5019\uFF0C\u5927\u4EBA\u4E0D\u80FD\u6253\u6270\u4ED6\uFF0C\u5426\u5219\u4F1A\u635F\u5BB3\u4ED6\u4EEC\u7684\u6CE8\u610F\u529B\u3002\u4F46\u662F\u6211\u5BB6\u5B9D\u5B9D\u81EA\u5DF1\u6CA1\u529E\u6CD5\u4E00\u4E2A\u4EBA\u8BA4\u771F\u73A9\u7684\uFF0C\u4ED6\u505A\u4EC0\u4E48\u90FD\u8981\u6211\u966A\u7740\uFF0C\u8981\u4E48\u6307\u4E1C\u897F\u7ED9\u6211\u770B\u5634\u91CC\u55EF\u55EF\u53EB\uFF0C\u8981\u4E48\u73A9\u4E00\u4E0B\u5C31\u8981\u5230\u6211\u7684\u6000\u91CC\u6765\u78E8\u8E6D\u3002\u6211\u662F\u4E0D\u662F\u5E94\u8BE5\u4E0D\u8981\u7406\u4ED6\uFF0C\u624D\u80FD\u8BA9\u4ED6\u96C6\u4E2D\u6CE8\u610F\u529B\u5462\uFF1F</p></blockquote><p>\u7C7B\u4F3C\u7684\u63D0\u95EE\u6211\u65F6\u4E0D\u65F6\u4F1A\u6536\u5230\u3002\u6709\u8FD9\u79CD\u56F0\u60D1\uFF0C\u5BB6\u957F\u5176\u5B9E\u53EA\u4E86\u89E3\u4E86\u4E00\u4E9B\u96F6\u788E\u7684\u4FE1\u606F\uFF0C\u6240\u4EE5\u64CD\u4F5C\u65F6\u505C\u7559\u4E8E\u8868\u9762\uFF0C\u6CA1\u6709\u770B\u5230\u4E8B\u60C5\u7684\u672C\u8D28\u3002\u8FD9\u4E2A\u95EE\u9898\u6211\u4EEC\u9700\u8981\u4ECE\u4E09\u4E2A\u65B9\u9762\u6765\u770B\uFF1A</p><p><strong>\u7B2C\u4E00\u3001\u201C\u4E0D\u6253\u6270\u201D\u5B69\u5B50\uFF0C\u4E0D\u662F\u8BA9\u5B69\u5B50\u4E00\u4E2A\u4EBA\u73A9</strong></p><p>\u966A\u4F34\u5B69\u5B50\u7684\u6E38\u620F\uFF0C\u662F\u8FFD\u968F\u5B69\u5B50\u7684\u5174\u8DA3\uFF0C\u53C2\u4E0E\u5B69\u5B50\u7684\u6E38\u620F\uFF0C\u4E0D\u662F\u8981\u6C42\u5B69\u5B50\u8FFD\u968F\u81EA\u5DF1\u5174\u8DA3\uFF0C\u81EA\u5DF1\u4E3B\u5BFC\u6E38\u620F\u3002</p><p>\u6211\u5728\u505A\u4EB2\u5B50\u4E92\u52A8\u8D28\u91CF\u8BC4\u4F30\u7684\u89C6\u9891\u89E3\u7801\u65F6\uFF0C\u5206\u6790\u9879\u76EE\u4E4B\u4E00\u5C31\u662F\uFF1A\u5F53\u5B69\u5B50\u770B\u5411\u67D0\u4E2A\u65B9\u5411\u67D0\u4E2A\u7269\u54C1\u65F6\uFF0C\u5BB6\u957F\u662F\u8BA9\u5B69\u5B50\u81EA\u5DF1\u53BB\u9009\u62E9\u53BB\u62FF\uFF0C\u5728\u5B69\u5B50\u62FF\u4E0D\u5230\u7684\u65F6\u5019\u63D0\u4F9B\u5E2E\u52A9\uFF0C\u8FD8\u662F\u5BB6\u957F\u62FF\u8D77\u4E00\u4E2A\u81EA\u5DF1\u89C9\u5F97\u201C\u5E94\u8BE5\u73A9\u201D\u7684\u73A9\u5177\u6216\u8005\u7269\u4F53\uFF0C\u8BA9\u5B69\u5B50\u8DDF\u968F\u81EA\u5DF1\u7684\u9009\u62E9\u3002</p><p>\u5982\u679C\u80FD\u5728\u201C\u8FFD\u968F\u5B69\u5B50\u7684\u5174\u8DA3\uFF0C\u53C2\u4E0E\u5B69\u5B50\u7684\u6E38\u620F\u201D\u8FD9\u4E2A\u5927\u524D\u63D0\u4E0B\uFF0C\u5B69\u5B50\u73A9\u7684\u5185\u5BB9\uFF0C\u5C31\u662F\u4F60\u5173\u6CE8\u7684\u5185\u5BB9\uFF0C\u4E5F\u662F\u4F60\u53C2\u4E0E\u7684\u5185\u5BB9\uFF0C\u800C\u6211\u4EEC\u7684\u53C2\u4E0E\u4F1A\u8BA9\u6E38\u620F\u66F4\u52A0\u6709\u8DA3\u548C\u6DF1\u5165\u3002\u5728\u8FD9\u4E2A\u8FC7\u7A0B\u4E2D\uFF0C\u81EA\u7136\u800C\u7136\u4F1A\u8BA9\u5B69\u5B50\u5BF9\u6E38\u620F\u7684\u8FC7\u7A0B\u548C\u5185\u5BB9\u66F4\u52A0\u6301\u7EED\u5173\u6CE8\u3002\u6240\u4EE5\u8FD9\u6837\u7684\u966A\u73A9\u5E76\u4E0D\u4F1A\u635F\u5BB3\u5B69\u5B50\u7684\u6CE8\u610F\u529B\uFF0C\u53CD\u800C\u5BF9\u5B69\u5B50\u7684\u6CE8\u610F\u529B\u6709\u5E2E\u52A9\u3002</p><p>\u4F46\u662F\uFF0C\u5982\u679C\u5BB6\u957F\u5728\u5B69\u5B50\u7684\u6E38\u620F\u4E2D\u4E0D\u987E\u5B69\u5B50\u5728\u505A\u4EC0\u4E48\u5174\u8DA3\u70B9\u662F\u4EC0\u4E48\uFF0C\u4E00\u5473\u53BB\u6253\u65AD\u5B69\u5B50\uFF0C\u8981\u6C42\u5B69\u5B50\u8DDF\u7740\u4F60\u7684\u601D\u8DEF\u8D70\uFF0C\u4E25\u683C\u6765\u8BF4\uFF0C\u5C31\u662F\u5BB6\u957F\u548C\u5B69\u5B50\u7684\u6CE8\u610F\u529B\u538B\u6839\u4E0D\u662F\u540C\u6837\u4E00\u4E2A\u4E1C\u897F\uFF0C\u8FD9\u4E2A\u65F6\u5019\u7236\u6BCD\u7684\u8A00\u8BED\u884C\u4E3A\u624D\u66F4\u6709\u53EF\u80FD\u5E72\u6270\u5B69\u5B50\u672C\u6765\u7684\u6E38\u620F\u601D\u8DEF\u548C\u6CE8\u610F\u529B\u70B9\u3002\u53CD\u53CD\u590D\u590D\uFF0C\u5B69\u5B50\u7684\u6CE8\u610F\u529B\u662F\u53CD\u590D\u6709\u8F6C\u79FB\u7684\u673A\u4F1A\u4E86\uFF0C\u4F46\u662F\u6CA1\u6709\u96C6\u4E2D\u6301\u4E45\u7684\u673A\u4F1A\u3002</p><p>\u5728\u7F16\u7801\u4EB2\u5B50\u4E92\u52A8\u89C6\u9891\u4E2D\u6211\u8FD8\u53D1\u73B0\uFF1A\u6709\u4E9B\u7236\u6BCD\u6216\u8005\u8001\u4EBA\u966A\u4F34\u5B69\u5B50\uFF0C\u5176\u5B9E\u662F\u6709\u70B9\u624B\u8DB3\u65E0\u63AA\u7684\uFF0C\u4E0D\u77E5\u9053\u600E\u4E48\u53C2\u4E0E\u3001\u600E\u4E48\u8FFD\u968F\u5B69\u5B50\u7684\u5174\u8DA3\u548C\u6E38\u620F\u3002\u54EA\u6015\u5B69\u5B50\u5DF2\u7ECF\u5728\u4E13\u6CE8\u73A9\u4E00\u4E2A\u81EA\u5DF1\u611F\u5174\u8DA3\u7684\u4E1C\u897F\u4E86\uFF0C\u4ED6\u4EEC\u8FD8\u662F\u89C9\u5F97\u81EA\u5DF1\u9700\u8981\u505A\u70B9\u4EC0\u4E48\uFF0C\u4E8E\u662F\u4ED6\u4EEC\u5C31\u53CD\u590D\u62FF\u4E0D\u540C\u7684\u73A9\u5177\u6765\u6572\u4E00\u6572\u6643\u4E00\u6643\uFF0C\u5E0C\u671B\u5B69\u5B50\u8868\u793A\u5174\u8DA3\u3002</p><p>\u5982\u679C\u5B69\u5B50\u7EE7\u7EED\u4F4E\u5934\u770B\u624B\u91CC\u7684\u4E1C\u897F\uFF0C\u5BF9\u5BB6\u957F\u624B\u91CC\u7684\u73A9\u5177\u4E0D\u611F\u5174\u8DA3\uFF0C\u5BB6\u957F\u4F1A\u53CD\u590D\u9891\u7E41\u62FF\u5176\u5B83\u4E1C\u897F\u6765\u8BD5\u3002\u4E5F\u6709\u4E00\u4E9B\u65F6\u5019\u5B69\u5B50\u771F\u7684\u88AB\u5438\u5F15\u4E86\u611F\u5174\u8DA3\u4E86\uFF0C\u4ED6\u4EEC\u53C8\u4E0D\u77E5\u9053\u5982\u4F55\u7EE7\u7EED\u5C31\u8FD9\u4E2A\u73A9\u5177\u6765\u89C2\u5BDF\u3001\u8FFD\u968F\u548C\u53C2\u4E0E\u6E38\u620F\u4E86\uFF0C\u53C8\u53BB\u62FF\u5176\u5B83\u73A9\u5177\u6765\u7ED9\u5B69\u5B50\u2026\u2026\u5176\u5B9E\u8FD9\u79CD\u4E92\u52A8\u4E0D\u4EC5\u4E8E\u6CE8\u610F\u529B\u65E0\u76CA\uFF0C\u5B69\u5B50\u548C\u5927\u4EBA\u7684\u4E92\u52A8\u8D28\u91CF\u4E5F\u5E76\u4E0D\u597D\u3002</p><p><strong>\u7B2C\u4E8C\u3001\u4E0D\u8981\u8D70\u6781\u7AEF</strong></p><p>\u6211\u7ECF\u5E38\u63D0\u9192\u5BB6\u957F\u7684\u4E00\u70B9\u662F\uFF1A\u4E0D\u8981\u523B\u677F\u8D70\u6781\u7AEF\u3002\u786E\u5B9E\u6709\u7814\u7A76\u53D1\u73B0\uFF0C\u5982\u679C\u5B69\u5B50\u751F\u6D3B\u7684\u73AF\u5883\u975E\u5E38\u5608\u6742\u5435\u95F9\uFF0C\u4ED6\u4EEC\u7684\u6CE8\u610F\u7EF4\u6301\u7ECF\u5E38\u4F1A\u88AB\u5404\u79CD\u4E0D\u76F8\u5173\u7684\u4E8B\u60C5\u6240\u6253\u65AD\u548C\u5E72\u6270\u3002\u5728\u8FD9\u79CD\u60C5\u51B5\u4E0B\uFF0C\u5982\u679C\u5B69\u5B50\u8FDB\u884C\u6CE8\u610F\u529B\u9700\u8981\u9AD8\u5EA6\u96C6\u4E2D\u7684\u4E8B\u60C5\uFF0C\u4ED6\u4EEC\u7684\u6CE8\u610F\u529B\u6301\u7EED\u6027\u662F\u4F1A\u53D7\u5230\u5F71\u54CD\u7684\u3002\u4F46\u662F\uFF0C\u8FD9\u5176\u4E2D\u53D1\u6325\u5F71\u54CD\u7684\u4E0D\u4EC5\u4EC5\u662F\u73AF\u5883\u5608\u6742\u672C\u8EAB\uFF0C\u8FD8\u6709\u5176\u4ED6\u4E00\u4E9B\u76F8\u5173\u56E0\u7D20\uFF0C\u6BD4\u5982\u5E73\u5747\u6BCF\u4E2A\u4EBA\u7684\u751F\u6D3B\u7A7A\u95F4\u5927\u5C0F\u3001\u5BB6\u5EAD\u7ECF\u6D4E\u6536\u5165\u3001\u7236\u6BCD\u60C5\u7EEA\u72B6\u6001\u7B49\u3002</p><p>\u6240\u4EE5\uFF0C\u8FD9\u5E76\u4E0D\u4EE3\u8868\u5B69\u5B50\u5728\u96C6\u4E2D\u6CE8\u610F\u529B\u7684\u65F6\u5019\uFF0C\u6211\u4EEC\u7EDD\u5BF9\u4E0D\u80FD\u6709\u4EFB\u4F55\u5E72\u6270\uFF0C\u8FD9\u79CD\u8981\u6C42\u5176\u5B9E\u662F\u4E0D\u73B0\u5B9E\u7684\u3002\u771F\u5B9E\u751F\u6D3B\u4E2D\u6211\u4EEC\u6BCF\u4E2A\u4EBA\u90FD\u96BE\u514D\u4F1A\u6709\u5728\u505A\u4E00\u4EF6\u4E8B\u60C5\u88AB\u6253\u65AD\u7684\u65F6\u5019\uFF0C\u53EA\u8981\u4E0D\u662F\u9891\u7E41\u6781\u7AEF\u7684\u60C5\u51B5\uFF0C\u5176\u5B9E\u5927\u53EF\u4E0D\u5FC5\u90A3\u4E48\u62C5\u5FC3\u3002</p><p>\u8981\u77E5\u9053\uFF0C\u6CE8\u610F\u529B\u4E0D\u662F\u523B\u677F\u4E00\u6210\u4E0D\u53D8\u7684\uFF0C\u5B83\u672C\u8EAB\u5C31\u662F\u7075\u6D3B\u53D8\u6362\u7684\uFF0C\u8FD9\u662F\u6211\u4EEC\u4EBA\u7C7B\u751F\u5B58\u4E0B\u53BB\u7684\u672C\u80FD\u3002\u6CE8\u610F\u529B\u88AB\u4E0D\u540C\u523A\u6FC0\u6240\u6355\u83B7\uFF0C\u4E5F\u4F1A\u56E0\u4E3A\u5174\u8DA3\u3001\u52A8\u673A\u7B49\u8FDB\u884C\u6CE8\u610F\u5185\u5BB9\u7684\u9009\u62E9\uFF0C\u540C\u65F6\u6291\u5236\u90A3\u4E9B\u4E0D\u88AB\u9009\u62E9\u7684\u6D3B\u52A8\u5185\u5BB9\u3002</p><p><strong>\u7B2C\u4E09\u3001\u4E0D\u8981\u53EA\u770B\u5230\u4E00\u5757\u62FC\u56FE\uFF0C\u8981\u770B\u5230\u6574\u4E2A\u753B\u9762</strong></p><p>\u6587\u7AE0\u5F00\u5934\u7684\u95EE\u9898\u91CC\u8FD8\u6709\u4E00\u4E2A\u91CD\u8981\u7684\u90E8\u5206\uFF1A\u5B9D\u5B9D\u73A9\u7684\u8FC7\u7A0B\u4E2D\u9700\u8981\u5BB6\u957F\u966A\u7740\uFF0C\u6307\u4E1C\u897F\u7ED9\u5BB6\u957F\u770B\uFF0C\u5E76\u7528\u58F0\u97F3\u8BED\u8A00\u8868\u8FBE\u4ED6\u4EEC\u7684\u5174\u8DA3\u6216\u8005\u5E0C\u671B\u5BB6\u957F\u5173\u6CE8\u5230\u7684\u5730\u65B9\uFF0C\u6216\u8005\u73A9\u4E00\u4E0B\u5C31\u8981\u5230\u5BB6\u957F\u7684\u6000\u91CC\u3002\u8FD9\u4E9B\u8868\u73B0\u8BA9\u5BB6\u957F\u62C5\u5FC3\u5F71\u54CD\u6CE8\u610F\u529B\uFF0C\u6240\u4EE5\u72B9\u8C6B\u8981\u4E0D\u8981\u7406\u5B69\u5B50\u3002</p><p>\u901A\u8FC7\u524D\u9762\u7684\u5185\u5BB9\uFF0C\u6211\u60F3\u5927\u5BB6\u90FD\u660E\u767D\u5982\u679C\u53C2\u4E0E\u5B69\u5B50\u6E38\u620F\u7684\u65B9\u6CD5\u5408\u9002\uFF0C\u5E76\u4E0D\u4F1A\u5F71\u54CD\u5B69\u5B50\u7684\u6CE8\u610F\u529B\u3002\u8FD9\u91CC\u6211\u60F3\u8981\u7ED9\u5927\u5BB6\u590D\u4E60\u4E00\u4E2A\u6211\u4E4B\u524D\u6587\u7AE0\u91CC\u548C\u5927\u5BB6\u4ECB\u7ECD\u8FC7\u7684\u4E00\u4E2A\u6982\u5FF5\uFF1A<strong>\u5B89\u5168\u73AF\u8DEF</strong>\u3002</p><p>\u987E\u540D\u601D\u4E49\uFF0C\u5B89\u5168\u73AF\u8DEF\u6709\u4E00\u4E2A\u5706\u73AF\u901A\u8DEF\uFF0C\u8FD9\u4E2A\u73AF\u8DEF\u7684\u7EC4\u6210\u5305\u62EC\u57FA\u672C\u7684\u4E09\u5927\u5757\u3002\u5176\u4E2D\u4E24\u4E2A\u5F88\u91CD\u8981\u7684\u90E8\u5206\u662F\u5706\u73AF\u6700\u5DE6\u8FB9\u7684\u4E24\u53EA\u624B\uFF0C\u8FD9\u4E24\u53EA\u624B\u5206\u522B\u4EE3\u8868\u5728\u4EB2\u5B50\u4F9D\u604B\u5173\u7CFB\u4E2D\uFF0C\u4E3B\u8981\u7167\u6599\u8005\u7684\u4E24\u5927\u4F5C\u7528\u2014\u2014\u5B89\u5168\u57FA\u5730\u548C\u5B89\u5168\u6E2F\u6E7E\u3002</p><p>\u4F5C\u4E3A\u5B89\u5168\u57FA\u5730\uFF0C\u4E3B\u8981\u7167\u6599\u8005\u8981\u7ED9\u5B69\u5B50\u201C\u5145\u6EE1\u80FD\u91CF\u201D\uFF0C\u8FD9\u79CD\u80FD\u91CF\u7684\u6765\u6E90\u662F\u5B69\u5B50\u5BF9\u4E8E\u517B\u80B2\u8005\u7684\u4FE1\u4EFB\uFF0C\u800C\u8FD9\u79CD\u4FE1\u4EFB\u7684\u6765\u6E90\u662F\u65E5\u5E38\u751F\u6D3B\u4E2D\u4E00\u70B9\u4E00\u6EF4\u7684\u4E92\u52A8\u3002\u4ECE\u8FD8\u662F\u5C0F\u5B9D\u5B9D\u5F00\u59CB\uFF0C\u5B69\u5B50\u5C31\u4F1A\u901A\u8FC7\u4EB2\u5B50\u4E4B\u95F4\u7684\u4E92\u52A8\u6A21\u5F0F\u6765\u786E\u5B9A\u517B\u80B2\u8005\u662F\u5426\u503C\u5F97\u4FE1\u4EFB\uFF0C\u6765\u786E\u5B9A\u81EA\u5DF1\u662F\u5426\u503C\u5F97\u88AB\u7231\u3002</p><p>\u4ECE\u5B89\u5168\u57FA\u5730\u51FA\u53D1\uFF0C\u5B69\u5B50\u5411\u5916\u63A2\u7D22\uFF0C\u63A5\u4E0B\u6765\u5706\u73AF\u7684\u90E8\u5206\u662F\u5B69\u5B50\u63A2\u7D22\u7684\u8FC7\u7A0B\uFF0C\u5728\u8FD9\u4E2A\u8FC7\u7A0B\u4E2D\uFF0C\u4EE5\u5B69\u5B50\u4E3A\u4E3B\u5BFC\uFF0C\u652F\u6301\u4ED6\u3001\u89C2\u5BDF\u4ED6\u3001\u4E3A\u4ED6\u7684\u8FDB\u6B65\u611F\u5230\u9AD8\u5174\uFF0C\u548C\u4ED6\u4E00\u8D77\u4EAB\u53D7\u63A2\u7D22\u6E38\u620F\u7684\u4E50\u8DA3\uFF0C\u5FC5\u8981\u7684\u65F6\u5019\u63D0\u4F9B\u9002\u5EA6\u7684\u5E2E\u52A9\u3002</p><p>\u63A2\u7D22\u7684\u8FC7\u7A0B\u4E2D\uFF0C\u6211\u4EEC\u4F1A\u53D1\u73B0\u5B69\u5B50\u9700\u8981\u65F6\u4E0D\u65F6\u56DE\u5230\u7236\u6BCD\u8EAB\u8FB9\uFF0C\u6709\u7684\u65F6\u5019\u662F\u56E0\u4E3A\u4ED6\u4EEC\u53D7\u4F24\u4E86\uFF0C\u96BE\u8FC7\u4E86\uFF0C\u5BB3\u6015\u4E86\uFF0C\u75B2\u5026\u4E86\uFF0C\u6709\u7684\u65F6\u5019\u7EAF\u7CB9\u662F\u4ED6\u4EEC\u9700\u8981\u56DE\u5230\u7236\u6BCD\u8EAB\u8FB9\u6765\u8BF4\u53E5\u8BDD\u4F9D\u504E\u4E00\u4E0B\u3002</p><p>\u5728\u8FD9\u4E2A\u8FC7\u7A0B\u91CC\u7236\u6BCD\u5C31\u662F\u5B89\u5168\u73AF\u8DEF\u5DE6\u8FB9\u7684\u7B2C\u4E8C\u53EA\u624B\uFF0C\u5145\u5F53\u7740\u5B89\u5168\u6E2F\u6E7E\u7684\u4F5C\u7528\u3002\u5728\u8FD9\u4E2A\u6E2F\u6E7E\u91CC\uFF0C\u7236\u6BCD\u7ED9\u5B69\u5B50\u63D0\u4F9B\u966A\u4F34\u3001\u503E\u542C\u3001\u5B89\u629A\u3001\u4FDD\u62A4\uFF0C\u5B69\u5B50\u7684\u60C5\u611F\u4E4B\u676F\u3001\u52C7\u6C14\u4E4B\u676F\u3001\u81EA\u4FE1\u4E4B\u676F\u90FD\u5728\u6E2F\u6E7E\u91CC\u88AB\u7EED\u6EE1\uFF0C\u7136\u540E\u4ED6\u4EEC\u5C31\u53EF\u4EE5\u91CD\u65B0\u518D\u6B21\u51FA\u53D1\u53BB\u63A2\u7D22\u3002</p><p>\u8FD9\u79CD\u56DE\u5230\u5B89\u5168\u6E2F\u6E7E\u7684\u60C5\u51B5\uFF0C\u5728\u5C0F\u5B9D\u5B9D\u7684\u65E5\u5E38\u4E92\u52A8\u4E2D\u975E\u5E38\u9891\u7E41\u88AB\u770B\u5230\u3002\u4F60\u4ED4\u7EC6\u89C2\u5BDF\u4E00\u4E0B\uFF1A\u5B69\u5B50\u73A9\u4E00\u4F1A\u513F\u5C31\u4F1A\u8DD1\u5230\u4F60\u8EAB\u8FB9\u6765\u7ED9\u4F60\u5C55\u793A\u4E00\u4E2A\u7269\u4F53\uFF0C\u6216\u8005\u8981\u4F60\u6402\u4E00\u4E0B\uFF0C\u4F60\u7ED9\u4E88\u56DE\u5E94\uFF0C\u4ED6\u9A6C\u4E0A\u53C8\u53EF\u4EE5\u7EE7\u7EED\u8DD1\u5F00\u53BB\u73A9\u73A9\u5177\uFF0C\u5F53\u4ED6\u4EEC\u751F\u75C5\u7684\u65F6\u5019\uFF0C\u5BB3\u6015\u7684\u65F6\u5019\uFF0C\u53D7\u4F24\u7684\u65F6\u5019\uFF0C\u4ED6\u4EEC\u4F1A\u66F4\u52A0\u6328\u5728\u7236\u6BCD\u8EAB\u8FB9\u3002</p><p>\u597D\uFF0C\u8BFB\u5230\u8FD9\u91CC\uFF0C\u6211\u4EEC\u56DE\u8FC7\u5934\u518D\u53BB\u770B\u770B\u8FD9\u4E2A\u63D0\u95EE\u4E2D\u7684\u63CF\u8FF0\uFF0C\u5176\u5B9E\u5C31\u662F\u5B89\u5168\u73AF\u8DEF\u7684\u5B9E\u9645\u4F53\u73B0\u554A\uFF01\u5B69\u5B50\u9700\u8981\u5BB6\u957F\u966A\u7740\uFF0C\u5BB6\u957F\u662F\u63A2\u7D22\u65F6\u7684\u5B89\u5168\u5730\u57FA\uFF1B\u73A9\u7684\u65F6\u5019\u6307\u4E1C\u897F\u7ED9\u5BB6\u957F\u770B\uFF0C\u5E0C\u671B\u5BB6\u957F\u5173\u6CE8\u4ED6\u6240\u73A9\u7684\u5185\u5BB9\uFF0C\u5411\u5BB6\u957F\u5C55\u793A\u5E0C\u671B\u5F15\u8D77\u5BB6\u957F\u7684\u5171\u540C\u6CE8\u610F\uFF0C\u8FD9\u662F\u5B69\u5B50\u60F3\u8981\u5206\u4EAB\u63A2\u7D22\u8FC7\u7A0B\u4E2D\u7684\u4E50\u8DA3\uFF1B\u7136\u540E\u65F6\u4E0D\u65F6\u5230\u5BB6\u957F\u6000\u91CC\u8E6D\u4E00\u4F1A\u513F\uFF0C\u8FD9\u4E0D\u5C31\u662F\u56DE\u5230\u5B89\u5168\u6E2F\u6E7E\u5145\u4E2A\u7535\u7EED\u4E2A\u676F\u5417\uFF1F</p><p>\u6240\u4EE5\u5462\uFF0C\u517B\u80B2\u5B69\u5B50\u662F\u4E2A\u9700\u8981\u770B\u5230\u6574\u4F53\uFF0C\u5C31\u50CF\u62FC\u62FC\u56FE\u7684\u65F6\u5019\uFF0C\u4F60\u9700\u8981\u5148\u6709\u4E00\u4E2A\u6574\u4F53\u7684\u4E86\u89E3\uFF0C\u5982\u679C\u53EA\u662F\u770B\u5230\u4E00\u4E9B\u96F6\u788E\u4FE1\u606F\uFF0C\u5C31\u4E3A\u4E86\u4E0D\u5FC5\u8981\u7684\u62C5\u5FC3\uFF0C\u5207\u65AD\u5B89\u5168\u73AF\u8DEF\uFF0C\u5C31\u6CA1\u6709\u5F97\u53EA\u6709\u5931\u4E86\u3002</p><hr><p>\u672C\u6587\u9996\u53D1\u4E8E\u300C\u6021\u79BE\u5FC3\u7406\u300D\uFF0C\u5173\u6CE8\u6021\u79BE\u5FC3\u7406\u540E\u53EF\u6536\u770B\u66F4\u591A\u517B\u80B2\u548C\u5FC3\u7406\u76F8\u5173\u7684\u6587\u7AE0\u3002</p><p>\u5173\u4E8E\u5982\u4F55\u505A\u597D\u4EB2\u5B50\u4E92\u52A8\uFF0C\u5982\u4F55\u6784\u5EFA\u826F\u597D\u7684\u4EB2\u5B50\u5173\u7CFB\uFF0C\u8BA9\u5B69\u5B50\u5EFA\u7ACB\u5B89\u5168\u611F\uFF0C\u5F90\u6842\u51E4\u533B\u751F\u4E5F\u5728\u6021\u79BE\u4E0A\u5F00\u8BBE\u8FC7\u4E00\u4E2A\u4E13\u680F\uFF0C\u5927\u7EB2\u5982\u4E0B\u3002</p><p>\u6709\u9700\u8981\u7684\u53EF\u901A\u8FC7\u4E0B\u9762\u4E8C\u7EF4\u7801\u4E86\u89E3</p><p>\u6021\u79BE\u4F1A\u5458\u53EF\u4EAB5\u6298\u542C</p>',30),l={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651633368&idx=1&sn=cd543214ea88afad1cb903b58ec1107c&chksm=8b5c9f22bc2b16346bfba51e9f073db150843bb1c8dd5ef8e5e3acf91e3a57588692b5aa3245&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},b=s("\u67E5\u770B\u539F\u6587");function h(f,m){const p=t("ExternalLinkIcon");return a(),r(n,null,[i,e("p",null,[e("a",l,[b,o(p)])])],64)}var u=d(_,[["render",h]]);export{u as default};
