import{r as a,o as n,c as r,a as e,b as o,F as p,d as i,e as c}from"./app.9aaa2256.js";import{_ as s}from"./plugin-vue_export-helper.21dcd24c.js";const d={},h=i('<h3 id="\u5B69\u5B50\u62C9\u809A\u5B50-\u8981\u4E0D\u8981\u5403-\u8179\u6CFB\u5976\u7C89" tabindex="-1"><a class="header-anchor" href="#\u5B69\u5B50\u62C9\u809A\u5B50-\u8981\u4E0D\u8981\u5403-\u8179\u6CFB\u5976\u7C89" aria-hidden="true">#</a> \u5B69\u5B50\u62C9\u809A\u5B50\uFF0C\u8981\u4E0D\u8981\u5403\u201C\u8179\u6CFB\u5976\u7C89\u201D\uFF1F</h3><p>\u5B9D\u5B9D\u62C9\u809A\u5B50\u53BB\u770B\u533B\u751F\uFF0C\u5E38\u5E38\u4F1A\u88AB\u5EFA\u8BAE\u6362\u201C\u8179\u6CFB\u5976\u7C89\u201D\uFF0C\u8179\u6CFB\u5976\u7C89\u666E\u904D\u66F4\u8D35\uFF0C\u5B83\u4EEC\u5230\u5E95\u6709\u4EC0\u4E48\u7528\uFF1F\u6709\u5FC5\u8981\u6362\u5417\uFF1F\u5728\u56DE\u7B54\u8FD9\u4E9B\u95EE\u9898\u4E4B\u524D\uFF0C\u6211\u4EEC\u9700\u8981\u5148\u4E86\u89E3\u4E00\u4E0B\u4E73\u7CD6\u4EE5\u53CA\u4E73\u7CD6\u4E0D\u8010\u53D7\u3002</p><p>\u8179\u6CFB\u5976\u7C89\u548C\u666E\u901A\u5976\u7C89\u7684\u4E3B\u8981\u533A\u522B\u5728\u4E8E\u4F4E\u4E73\u7CD6\u6216\u4E0D\u542B\u4E73\u7CD6\uFF0C\u4E73\u7CD6\u662F\u4E00\u79CD\u5728\u5976\u91CC\u53D1\u73B0\u7684\u53CC\u7CD6\uFF0C\u4E0D\u8BBA\u662F\u6BCD\u4E73\u3001\u725B\u5976\u8FD8\u662F\u5176\u5B83\u52A8\u7269\u5976\uFF0C\u90FD\u542B\u6709\u4E73\u7CD6\u3002\u5976\u88AB\u559D\u4E0B\u53BB\u540E\uFF0C\u4E73\u7CD6\u4F1A\u88AB\u5C0F\u80A0\u5206\u6CCC\u7684\u4E73\u7CD6\u9176\u6D88\u5316\u6210\u8461\u8404\u7CD6\u548C\u534A\u4E73\u7CD6\uFF0C\u7136\u540E\u88AB\u5438\u6536\u5165\u8840\uFF0C\u4E3A\u4EBA\u4F53\u63D0\u4F9B\u80FD\u91CF\u3002</p><p>\u54FA\u4E73\u52A8\u7269\u5728\u5A74\u5E7C\u513F\u671F\u4E3B\u8981\u9760\u559D\u5976\uFF0C\u6240\u4EE5\u4E73\u7CD6\u9176\u5728\u5A74\u5E7C\u513F\u9636\u6BB5\u6BD4\u8F83\u4E30\u5BCC\uFF0C\u968F\u7740\u5E74\u9F84\u7684\u589E\u957F\uFF0C\u5976\u9010\u6E10\u4E0D\u518D\u662F\u4E3B\u8981\u7684\u98DF\u7269\uFF0C\u4E73\u7CD6\u9176\u5206\u6CCC\u4E5F\u4F1A\u51CF\u5C11\uFF0C\u6709\u7684\u4EBA\u4F1A\u51FA\u73B0\u4E73\u7CD6\u9176\u4E0D\u8DB3\u4EE5\u6D88\u5316\u5976\u91CC\u7684\u4E73\u7CD6\u7684\u60C5\u51B5\uFF0C\u672A\u88AB\u6D88\u5316\u7684\u4E73\u7CD6\u4F1A\u88AB\u80A0\u9053\u7EC6\u83CC\u964D\u89E3\uFF0C\u4EA7\u751F\u6C14\u4F53\u548C\u4E73\u9178\u7B49\u4EE3\u8C22\u4EA7\u7269\uFF0C\u5F15\u8D77\u8179\u6CFB\u3001\u80C0\u6C14\u3001\u809A\u5B50\u75DB\u7B49\u75C7\u72B6\uFF0C\u8FD9\u5C31\u662F\u4E73\u7CD6\u4E0D\u8010\u53D7\u3002</p><p>\u968F\u7740\u5E74\u9F84\u589E\u957F\u9010\u6E10\u51FA\u73B0\u7684\u4E73\u7CD6\u4E0D\u8010\u53D7\uFF0C\u53EB\u539F\u53D1\u6027\u4E73\u7CD6\u4E0D\u8010\u53D7\u3002\u539F\u53D1\u6027\u4E73\u7CD6\u4E0D\u8010\u53D7\u7684\u53D1\u751F\u6709\u660E\u663E\u7684\u4EBA\u79CD\u5DEE\u5F02\uFF0C\u6211\u4EEC\u4E2D\u56FD\u4EBA\u5927\u90FD\u4F1A\u6709\u3002\u4F46\u539F\u53D1\u6027\u4E73\u7CD6\u4E0D\u8010\u53D7\u5728\u5927\u9F84\u513F\u7AE5\u53CA\u6210\u4EBA\u624D\u4F1A\u51FA\u73B0\uFF0C\u4E0D\u8BBA\u4EC0\u4E48\u4EBA\u79CD\uFF0C2-3\u5C81\u524D\u4E0D\u4F1A\u6709\u539F\u53D1\u6027\u4E73\u7CD6\u4E0D\u8010\u53D7\u7684\u95EE\u9898[1]\u3002</p><p>\u80A0\u9053\u53D1\u751F\u611F\u67D3\u7684\u65F6\u5019\uFF0C\u6BD4\u5982\u8F6E\u72B6\u75C5\u6BD2\u80A0\u708E\uFF0C\u4F1A\u5BFC\u81F4\u5C0F\u80A0\u7ED2\u6BDB\u4E0A\u76AE\u7EC6\u80DE\u7684\u8131\u843D\uFF0C\u800C\u65B0\u751F\u4EA7\u7684\u4E0A\u76AE\u7EC6\u80DE\u5408\u6210\u4E73\u7CD6\u9176\u7684\u80FD\u529B\u8FD8\u4E0D\u90A3\u4E48\u8DB3\uFF0C\u8FD9\u4E5F\u53EF\u80FD\u5BFC\u81F4\u4E73\u7CD6\u4E0D\u8010\u53D7\uFF0C\u8FD9\u79CD<strong>\u7EE7\u53D1\u4E8E\u75BE\u75C5\u7684\u4E73\u7CD6\u4E0D\u8010\u53D7\u53EB\u505A\u7EE7\u53D1\u6027\u4E73\u7CD6\u4E0D\u8010\u53D7</strong>\u3002</p><p>\u51FA\u73B0\u4E73\u7CD6\u4E0D\u8010\u53D7\u4E4B\u540E\uFF0C\u5982\u679C\u7EE7\u7EED\u559D\u542B\u4E73\u7CD6\u7684\u5976\uFF0C\u5C31\u53EF\u80FD\u5BFC\u81F4\u8179\u6CFB\u75C7\u72B6\u52A0\u91CD\u6216\u8005\u6301\u7EED\u66F4\u4E45\uFF0C\u57FA\u4E8E\u8FD9\u4E2A\u8003\u8651\uFF0C\u6240\u4EE5\u533B\u751F\u53EF\u80FD\u4F1A\u5EFA\u8BAE\u8179\u6CFB\u7684\u5B69\u5B50\u66F4\u6362\u4E0D\u542B\u4E73\u7CD6\u7684\u201C\u8179\u6CFB\u5976\u7C89\u201D\u3002</p><p>\u90A3\u662F\u4E0D\u662F\u5B69\u5B50\u4E00\u8179\u6CFB\u5C31\u5EFA\u8BAE\u91C7\u53D6\u65E0\u4E73\u7CD6\u7684\u996E\u98DF\uFF1F\u5E76\u4E0D\u662F\u3002</p><p>\u5BF9\u4E8E\u6BCD\u4E73\u5582\u517B\u7684\u5B69\u5B50\uFF0C\u56E0\u4E3A\u5F53\u524D\u5DF2\u7ECF\u6709\u5F3A\u6709\u529B\u7684\u8BC1\u636E\u8BC1\u5B9E\u6BCD\u4E73\u5582\u517B\u80FD\u964D\u4F4E\u8179\u6CFB\u7684\u53D1\u75C5\u7387\u548C\u6B7B\u4EA1\u7387[2]\uFF0C\u6240\u4EE5\u5BF9\u4E8E<strong>\u6BCD\u4E73\u5582\u517B\u7684\u8179\u6CFB\u5B69\u5B50</strong>\uFF0C\u76EE\u524D\u56FD\u5185\u5916\u7684\u6307\u5357\u90FD\u5F88\u4E00\u81F4\u7684\u8BA4\u4E3A\u5E94\u8BE5\u7EE7\u7EED\u4FDD\u6301\u6216\u589E\u52A0\u6BCD\u4E73\u5582\u517B\uFF0C<strong>\u4E0D\u9700\u8981\u6362\u666E\u901A\u914D\u65B9\u5976\u6216\u65E0\u4E73\u7CD6\u98DF\u7269\u3002</strong></p><p>\u5BF9\u4E8E\u914D\u65B9\u5976\u5582\u517B\u7684\u5B69\u5B50\uFF0C\u76EE\u524D\u867D\u7136\u6709\u4E00\u4E9B\u4E89\u8BBA\uFF0C\u4F46\u5927\u90E8\u5206\u6743\u5A01\u533B\u5B66\u673A\u6784\u4ECD\u8BA4\u4E3A\u6CA1\u5FC5\u8981\u5E38\u89C4\u6362\u65E0\u4E73\u7CD6\u5976\u7C89\u3002</p><p>\u4E16\u754C\u536B\u751F\u7EC4\u7EC7\u4E00\u76F4\u5EFA\u8BAE\u6025\u6027\u8179\u6CFB\u7EE7\u7EED\u539F\u6765\u7684\u5582\u517B,\u5E76\u5F3A\u8C03\u8BF4\u5E7F\u544A\u4E2D\u5BA3\u79F0\u7684\u7528\u4E8E\u8179\u6CFB\u60A3\u513F\u7684\u7279\u6B8A\u914D\u65B9\u5976\u7C89\u662F\u6602\u8D35\u548C\u4E0D\u5FC5\u8981\u7684\uFF0C\u4E0D\u5E94\u5E38\u89C4\u98DF\u7528\uFF0C\u6709\u4E34\u5E8A\u610F\u4E49\u7684\u725B\u5976\u4E0D\u8010\u53D7\u6781\u5C11\u89C1[3]\u3002</p><p>\u7F8E\u56FD\u513F\u79D1\u5B66\u4F1A\u7684\u6307\u5357\u8BA4\u4E3A\u8179\u6CFB\u867D\u7136\u53EF\u80FD\u5F71\u54CD\u4E73\u7CD6\u9176\u5206\u6CCC\uFF0C\u4F46\u5927\u90E8\u5206\u90FD\u4E0D\u4F1A\u5230\u51FA\u73B0\u4E34\u5E8A\u75C7\u72B6\u6216\u5F71\u54CD\u5438\u6536\u7684\u7A0B\u5EA6\uFF0C\u6240\u4EE5\u4E5F\u63A8\u8350\u8179\u6CFB\u5B69\u5B50\u91C7\u7528\u5B69\u5B50\u5E74\u9F84\u7684\u666E\u901A\u996E\u98DF\uFF0C\u5305\u62EC\u542B\u4E73\u7CD6\u7684\u5976\u5236\u54C1\uFF0C\u800C\u4E0D\u9700\u8981\u505A\u4E25\u683C\u7684\u9650\u5236[4]\u3002\u52A0\u62FF\u5927\u6307\u5357\u540C\u6837\u8BA4\u4E3A\u7ED9\u65E0\u4E73\u7CD6\u5976\u7C89\u662F\u6CA1\u5FC5\u8981\u7684[5]\u3002</p><p>WHO\uFF0C\u7F8E\u56FD\u548C\u52A0\u62FF\u5927\u7684\u8179\u6CFB\u6307\u5357\u53D1\u5E03\u81F3\u4ECA\u65F6\u95F4\u90FD\u6BD4\u8F83\u4E45\u4E86\uFF0C\u89C2\u70B9\u4F1A\u4E0D\u4F1A\u8FC7\u65F6\u4E86\u5462\uFF1F</p><p>2013\u5E74Cochrane\u6709\u7BC7\u7EFC\u8FF0\u603B\u7ED3\u4E86\u65E0\u4E73\u7CD6\u996E\u98DF\u5BF9\u8179\u6CFB\u5F71\u54CD\u76F8\u5173\u7684\u7814\u7A76\uFF0C\u8BA4\u4E3A\u4F7F\u7528\u65E0\u4E73\u7CD6\u996E\u98DF\u5927\u7EA6\u80FD\u8BA9\u5B69\u5B50\u8179\u6CFB\u7684\u6301\u7EED\u65F6\u95F4\u7F29\u77ED\u5927\u7EA618\u5C0F\u65F6\uFF0C\u4E5F\u80FD\u8BA9\u6CBB\u7597\u5931\u8D25\uFF08\u8179\u6CFB\u6216\u5455\u5410\u75C7\u72B6\u65E0\u6539\u5584\uFF09\u7684\u673A\u4F1A\u51CF\u5C11\u4E00\u534A[6]\u3002</p><p>\u4F46\u8FD9\u4E9B\u7814\u7A76\u6765\u81EA\u4E0D\u540C\u6536\u5165\u6C34\u5E73\u7684\u56FD\u5BB6\uFF0C\u5927\u90E8\u5206\u662F\u5BF9\u75C5\u60C5\u76F8\u5BF9\u8F83\u91CD\u7684\u4F4F\u9662\u5B69\u5B50\u7684\u7814\u7A76\uFF0C\u4E0D\u5177\u6709\u666E\u9002\u6027\uFF0C\u800C\u4E14\u4E0D\u662F\u53CC\u76F2\u7814\u7A76\uFF0C\u7ED3\u8BBA\u7684\u53EF\u4FE1\u5EA6\u4E0D\u90A3\u4E48\u9AD8\u3002\u52A0\u62FF\u5927\u7684\u533B\u751F\u8BA4\u4E3A\u4E0D\u8DB3\u4EE5\u636E\u6B64\u6539\u53D8\u4E4B\u524D\u6307\u5357\u7684\u63A8\u8350[7]\u3002</p><p>\u8FD9\u4EFDCochrane\u7EFC\u8FF0\u51FA\u6765\u4E4B\u540E\uFF0CWHO\uFF0C\u7F8E\u56FD\u3001\u52A0\u62FF\u5927\u6CA1\u6709\u51FA\u53F0\u65B0\u7684\u6307\u5357\u63A8\u8350\u610F\u89C1\uFF0C2014\u5E74\u6B27\u6D32\u5012\u662F\u51FA\u4E86\u4E00\u4EFD\u65B0\u7684\u6025\u6027\u80C3\u80A0\u708E\u6307\u5357[8]\uFF0C\u8FD9\u4EFD\u6307\u5357\u4E5F\u63D0\u5230\u4E86Cochrane\u7684\u90A3\u7BC7\u7EFC\u8FF0\uFF0C\u4F46\u4ECD\u7136\u660E\u786E\u8BF4\u201C\u76EE\u524D\u4E0D\u63A8\u8350\u5728\u95E8\u8BCA\u5E38\u89C4\u4F7F\u7528\u65E0\u4E73\u7CD6\u5976\u7C89\u201D\u3002</p><p>\u603B\u7684\u8BF4\u6765\uFF0C\u73B0\u5728\u666E\u904D\u7684\u89C2\u70B9\u662F<strong>\u8179\u6CFB\u53EF\u80FD\u5F71\u54CD\u4E73\u7CD6\u9176\u7684\u5206\u6CCC\uFF0C\u4F46\u51FA\u73B0\u7EE7\u53D1\u6027\u4E73\u7CD6\u4E0D\u8010\u53D7\u8868\u73B0\u7684\u5F88\u5C11\uFF0C\u6240\u4EE5\u8179\u6CFB\u4E0D\u9700\u8981\u5E38\u89C4\u6362\u65E0\u4E73\u7CD6\u996E\u98DF</strong>\uFF0C\u5C24\u5176\u662F\u75C5\u60C5\u4E0D\u91CD\u7684\u95E8\u8BCA\u75C5\u4EBA\uFF0C\u6240\u4EE5\u76EE\u524D\u5927\u90E8\u5206\u56FD\u5BB6\u5927\u90E8\u5206\u56FD\u5BB6\u7684\u4E34\u5E8A\u6307\u5357\u4E0D\u63A8\u8350\u5E38\u89C4\u6362\u65E0\u4E73\u7CD6\u5976\u7C89\u3002\u4F46\u4F4F\u9662\u75C5\u4EBA\uFF0C\u6216\u8005\u8179\u6CFB\u6301\u7EED\u65F6\u95F4\u6BD4\u8F83\u957F\u662F\u53EF\u4EE5\u6362\uFF0C\u6BD4\u5982WHO\u5C31\u5EFA\u8BAE\u8179\u6CFB\u8D85\u8FC72\u5468\u4E14\u666E\u901A\u996E\u98DF\u72B6\u51B5\u4E0B\u75C7\u72B6\u6CA1\u6709\u6539\u5584\u6362\u65E0\u4E73\u7CD6\u996E\u98DF[9]\u3002</p><p>\u5927\u90E8\u5206\u56FD\u5BB6\u8BA4\u4E3A\u8179\u6CFB\u6CA1\u6709\u5FC5\u8981\u5E38\u89C4\u6362\u65E0\u4E73\u7CD6\u5976\u7C89\uFF0C\u4F46\u6211\u56FD\u53C8\u662F\u4E00\u4E2A\u4F8B\u5916\u3002\u53BB\u5E74\u300A\u4E2D\u534E\u513F\u79D1\u6742\u5FD7\u300B\u65B0\u51FA\u4E86\u90A3\u4EFD\u300A\u4E2D\u56FD\u513F\u7AE5\u6025\u6027\u611F\u67D3\u6027\u8179\u6CFB\u75C5\u4E34\u5E8A\u5B9E\u8DF5\u6307\u5357\u300B\uFF0C\u91CC\u9762\u5199\u7740\uFF1A\u201C\u914D\u65B9\u5976\u5582\u517B\u8005\u53EF\u9009\u62E9\u5E94\u7528\u4F4E\u4E73\u7CD6\u6216\u65E0\u4E73\u7CD6\u914D\u65B9\u201D\uFF0C\u8FD9\u6837\u7B49\u4E8E\u8BF4\u53EA\u8981\u8179\u6CFB\u5C31\u53EF\u4EE5\u6362\u8179\u6CFB\u5976\u7C89\u3002</p><p>\u7B2C\u4E00\u6B21\u770B\u5230\u8FD9\u4EFD\u6307\u5357\u6211\u5C31\u5F88\u597D\u5947\u5B83\u8FD9\u6837\u63A8\u8350\u7684\u7406\u7531\u662F\u4EC0\u4E48\uFF0C\u7136\u540E\u5C31\u53BB\u67E5\u4E86\u4E00\u5B83\u89C2\u70B9\u7684\u4F9D\u636E\uFF0C\u5F15\u7528\u6587\u732E\u662F\u4E00\u7BC7\u6807\u9898\u4E3A\u300AClinical study on infantile lactose intolerance\u300B\u7684\u6587\u7AE0\uFF0C\u6211\u4EE5\u4E3A\u662F\u4E00\u7BC7\u56FD\u5916\u7684\u7814\u7A76\u8BBA\u6587\uFF0C\u5C31\u53BBpubmed\u641C\uFF0C\u7ED3\u679C\u5374\u641C\u4E0D\u5230\uFF0C\u540E\u6765\u628A\u6807\u9898\u7FFB\u8BD1\u6210\u4E2D\u6587\uFF0C\u7136\u540E\u5728\u4E2D\u6587\u6570\u636E\u5E93\u641C\u51FA\u6765\u4E86\uFF0C\u539F\u6765\u662F\u4E00\u7BC7\u8BB2\u4E73\u7CD6\u4E0D\u8010\u53D7\u7684\u4E2D\u6587\u7EFC\u8FF0\u3002</p><p>\u8FD9\u7BC7\u7EFC\u8FF0\u91CC\u4E5F\u6CA1\u6709\u627E\u5230\u4EFB\u4F55\u652F\u6301\u8179\u6CFB\u5B69\u5B50\u5E38\u89C4\u4F7F\u7528\u65E0\u4E73\u7CD6\u914D\u65B9\u7684\u8BC1\u636E\uFF0C\u4E0D\u77E5\u9053\u8FD9\u4E9B\u4EE3\u8868\u4E2D\u56FD\u513F\u79D1\u6700\u9AD8\u5B66\u672F\u6C34\u5E73\u7684\u4E34\u5E8A\u6307\u5357\u600E\u4E48\u5236\u5B9A\u51FA\u6765\u7684\u3002</p><p>\u8FD9\u4EFD\u6307\u5357\u5BF9\u8499\u8131\u77F3\u4F7F\u7528\u7684\u63A8\u8350\u610F\u89C1\u4E5F\u662F\u5F88\u5947\u602A\u7684\uFF0C\u6211\u4EE5\u524D\u5199\u6587\u7AE0\u5206\u6790\u8FC7\uFF0C\u5728\u5BF9\u8BDD\u6846\u91CC\u56DE\u590D\u201C<strong>\u8499\u8131\u77F3</strong>\u201D\u53EF\u4EE5\u67E5\u770B\u6587\u7AE0\u3002</p><p>\u53C2\u8003\u8D44\u6599\uFF1A</p><p>[1]Melvin B. Heyman and for the Committee on Nutrition.Lactose Intolerance in Infants, Children, and Adolescents. Pediatrics 2006;118;1279-1286</p><p>[2].https://www.ncbi.nlm.nih.gov/pubmed/21501432</p><p>3.http://apps.who.int/iris/bitstream/10665/43209/3/9241593180_chi.pdf</p><p>[4]American Academy of Pediatrics, Provisional Committee on Quality Improvement, Subcommittee on Acute Gastroenteritis. Practice parameter: The management of acute gastroenteritis in young children. Pediatrics 1996;97:424-35.</p><p>[5]Leung A, Prince T. Oral rehydration therapy and early refeeding in the management of childhood gastroenteritis. Paediatr Child Health 2006;11:527-31.</p><p>[6]MacGillivray S, Fahey T, McGuire W. Lactose avoidance for young children with acute diarrhea. Cochrane Database Syst Rev 2013;(10):CD005433.</p><p>[7]Saunders N, Friedman JN. Lactose avoidance for young children with acute diarrhea. Paediatr Child Health. 2014 Dec;19(10):529-30.</p><p>[8]http://www.espghan.org/fileadmin/user_upload/guidelines_pdf/Guidelines_2404/European_Society_for_Pediatric_Gastroenterology_.26.pdf</p><p>[9].http://apps.who.int/iris/bitstream/10665/54204/1/bulletin_1996_74%285%29_479-489.pdf</p><p>\u70B9<strong>\u9605\u8BFB\u539F\u6587</strong>\uFF0C\u53EF\u5411\u6211\u548C\u5176\u5B83\u533B\u751F\u54A8\u8BE2</p>',32),l={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651626855&idx=1&sn=c909284be64c33de6953ce5afcd23625&chksm=8b5cb49dbc2b3d8bcdff2aaed71e9adff0e9d055d54111720a981e9b90b2972bda80d060939a&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},m=c("\u67E5\u770B\u539F\u6587");function _(f,u){const t=a("ExternalLinkIcon");return n(),r(p,null,[h,e("p",null,[e("a",l,[m,o(t)])])],64)}var y=s(d,[["render",_]]);export{y as default};
