import{r as n,o as a,c as s,a as e,b as t,w as d,F as r,e as p,d as _}from"./app.9aaa2256.js";import{_ as i}from"./plugin-vue_export-helper.21dcd24c.js";const l={},b=e("h3",{id:"\u6021\u79BE\u8BCA\u6240-\u4F55\u65F6\u624D\u80FD\u5F00\u5230\u5927\u5BB6\u6240\u5728\u5730\u533A",tabindex:"-1"},[e("a",{class:"header-anchor",href:"#\u6021\u79BE\u8BCA\u6240-\u4F55\u65F6\u624D\u80FD\u5F00\u5230\u5927\u5BB6\u6240\u5728\u5730\u533A","aria-hidden":"true"},"#"),p(" \u6021\u79BE\u8BCA\u6240\uFF0C\u4F55\u65F6\u624D\u80FD\u5F00\u5230\u5927\u5BB6\u6240\u5728\u5730\u533A\uFF1F")],-1),h=p("\u524D2\u5929\u63A8\u9001 \u300A "),m=p("\u4E3A\u4EC0\u4E48\u533B\u751F\u79D1\u666E\u7684\u90A3\u79CD\u533B\u7597\uFF0C\u5728\u533B\u9662\u91CC\u5F88\u96BE\u4F53\u9A8C\u5230\uFF1F"),x=p(" \u300B \u8FD9\u7BC7\u6587\u7AE0\uFF0C\u8BC4\u8BBA\u533A\u91CC\u5927\u5BB6\u51E0\u4E4E\u6E05\u4E00\u8272\u5730\u547C\u5401\u6211\u4EEC\u597D\u597D\u7ECF\u8425\u95E8\u8BCA\u90E8\uFF0C\u65E9\u65E5\u5F00\u5230\u81EA\u5DF1\u6240\u5728\u7684\u57CE\u5E02\u53BB\u3002"),f=_("<p>\u8FD9\u4E9B\u547C\u5401\u6211\u90FD\u80FD\u7406\u89E3\uFF0C\u56E0\u4E3A\u5176\u5B9E\u4E5F\u662F\u6211\u7684\u613F\u671B\u3002</p><p>\u6700\u521D\u6211\u8F9E\u804C \u51FA\u6765\u521B\u4E1A\uFF0C\u672C\u6765\u4E5F\u662F\u5E0C\u671B\u80FD\u5F00\u4E00\u5BB6\u80FD\u8DF5\u884C\u81EA\u5DF1\u533B\u7597\u7406\u5FF5\u7684\u8BCA\u6240\uFF0C\u73B0\u5728 5 \u5E74\u8FC7\u53BB\u4E86\uFF0C\u8BCA\u6240\u5DF2\u7ECF\u5F00\u4E86 3 \u5BB6\uFF0C\u6240\u5B9E\u73B0\u7684\u5DF2\u7ECF\u6BD4\u6211\u5F53\u521D\u7684\u8BBE\u60F3\u5927\u4E86\u5F88\u591A\uFF0C\u5F53\u7136 \u73B0 \u5728\u4E00\u8D77 \u6765\u505A\u8FD9\u4EF6\u4E8B\u7684\uFF0C\u4E5F\u65E9\u5DF2\u4E0D\u662F \u6211\u4E00\u4E2A\u4EBA\uFF0C\u800C\u662F\u6574\u4E2A \u6021\u79BE\u56E2\u961F\u3002</p><p>\u76F8\u6BD4\u4E8E\u5176\u4ED6\u884C\u4E1A\uFF0C\u5728 3 \u5E74\u65F6\u95F4\u5F00\u4E86 3 \u5BB6\u95E8\u8BCA\uFF0C\u5E94\u8BE5\u7B97\u6BD4\u8F83\u6162\u7684\uFF0C\u8FD9\u80CC\u540E\u5F53\u7136\u53EF\u80FD\u6709\u6211\u4EEC\u81EA\u5DF1\u505A\u4E8B\u98CE\u683C\u539F\u56E0\uFF0C\u4E5F\u548C\u5F53\u524D\u533B\u7597\u884C\u4E1A\u73B0\u72B6\u4EE5\u53CA\u6211\u4EEC\u9009\u62E9\u7684\u533B\u7597\u6A21\u5F0F\u6709\u5173\u3002</p><p>\u6211\u5728\u4EE5\u524D\u7684\u6587\u7AE0\u91CC\u4E5F\u8BF4\u8FC7\uFF0C\u5F00\u4E00\u5BB6\u51E0\u767E\u5230\u4E00\u5343\u5E73\u7C73\u7684\u7EFC\u5408\u95E8\u8BCA\uFF0C\u4ECE\u88C5\u4FEE\u5230\u5F00\u4E1A\u8981\u5927\u51E0\u4E2A\u6708\uFF0C\u88C5\u4FEE+\u8BBE\u5907\u8981\u597D\u51E0\u767E\u4E07\uFF0C\u5F00\u4E1A\u540E\u6BCF\u4E2A\u6708\u623F\u79DF\u5341\u51E0\u4E07\uFF0C\u518D\u52A0\u4E0A\u533B\u62A4\u53CA\u7BA1\u7406\u4EBA\u5458\u7684\u85AA\u916C\u548C\u8FD0\u8425\u7684\u6295\u5165\uFF0C\u6BCF\u4E2A\u6708\u90FD\u9700\u8981\u5927\u51E0\u5341\u4E07\u3002</p><p>\u5728\u4E00\u5207\u987A\u5229\u7684\u60C5\u51B5\u4E0B\uFF0C\u5728\u4E00\u7EBF\u57CE\u5E02\u5F00\u4E00\u5BB6\u7EFC\u5408\u95E8\u8BCA\u90FD\u9700\u8981\u9884\u5907\u4E0A\u5343\u4E07\uFF0C\u5982\u679C\u5728\u9009\u5740\u3001\u8BBE\u8BA1\u3001\u88C5\u4FEE\u3001\u6D88\u9632\u3001\u533B\u7597\u8BB8\u53EF\u5BA1\u6838\u4EFB\u4F55\u4E00\u4E2A\u73AF\u8282\u9047\u5230\u95EE\u9898\uFF0C\u9884\u7B97\u90FD\u53EF\u80FD\u5927\u5E45\u589E\u52A0\u3002</p><p>\u5C31\u7B97\u987A\u5229\u5F00\u4E1A\uFF0C\u4E25\u683C\u9075\u5FAA\u5FAA\u8BC1\u533B\u5B66\u8FDB\u884C\u8BCA\u7597\uFF0C\u5E38\u89C1\u75C5\u7684\u7528\u836F\u3001\u68C0\u67E5\u3001\u6CBB\u7597\u5176\u5B9E\u5F88\u5C11\uFF0C\u63D0\u4F9B\u76F8\u5BF9\u5145\u88D5\u7684\u5C31\u8BCA\u6C9F\u901A\u65F6\u95F4\uFF0C\u533B\u751F\u6BCF\u5929\u63A5\u8BCA\u4EBA\u6570\u4E5F\u5F88\u96BE\u8D85\u8FC720\u4E2A\uFF0C\u54EA\u6015\u4E00\u4E2A\u75C5\u4EBA\u8BCA\u91D1500\u5143\uFF0C\u90FD\u5F88\u96BE\u8986\u76D6\u5230\u6210\u672C\u652F\u51FA\u3002</p><p>\u8BCA\u91D1\u5982\u679C\u66F4\u4F4E\uFF0C\u8BCA\u6240\u6536\u5165\u66F4\u5C11\uFF0C\u8BF7\u4E0D\u5230\u597D\u7684\u533B\u751F\uFF0C\u63D0\u4F9B\u4E0D\u4E86\u53EF\u9760\u7684\u533B\u7597\uFF0C\u5C31\u80CC\u79BB\u4E86\u5F00\u8BCA\u6240\u7684\u521D\u8877\uFF1B\u8BCA\u91D1\u5982\u679C\u66F4\u9AD8\uFF0C\u5C31\u533B\u7684\u95E8\u69DB\u66F4\u9AD8\uFF0C\u75C5\u4EBA\u91CF\u518D\u4E0B\u964D\uFF0C\u6536\u5165\u4E0B\u964D\u540C\u6837\u5F88\u96BE\u6DB5\u76D6\u6210\u672C\u3002</p><p>\u8BCA\u6240\u5F00\u4E1A\u540E\uFF0C\u5982\u679C\u9A6C\u4E0A\u5C31\u6709\u5F88\u591A\u75C5\u4EBA\uFF0C\u6216\u8BB8\u8FD8\u6709\u76C8\u4E8F\u5E73\u8861\u751A\u81F3\u76C8\u5229\u7684\u673A\u4F1A\uFF0C\u5982\u679C\u75C5\u4EBA\u7F13\u6162\u589E\u957F\uFF0C\u5219\u8981\u4E8F\u635F\u5F88\u957F\u4E00\u6BB5\u65F6\u95F4\uFF0C\u5982\u679C\u4E00\u76F4\u4E0D\u589E\u957F\uFF0C\u5219\u8981\u4E00\u76F4\u4E8F\u635F\uFF0C\u800C\u4E14\u533B\u751F\u62A4\u58EB\u4E5F\u7559\u4E0D\u4F4F\u3002</p><p>\u6240\u4EE5\u76EE\u524D\u5F00\u8BCA\u6240\u7684\uFF0C\u5C24\u5176\u662F\u4F9D\u9760\u8BCA\u91D1\u800C\u4E0D\u662F\u4F9D\u9760\u836F\u54C1\u3001\u68C0\u67E5\u3001\u6CBB\u7597\u6765\u8D5A\u94B1\u7684\u8BCA\u6240\uFF0C\u5927\u90E8\u5206\u5728\u4E8F\u94B1\u800C\u4E0D\u662F\u8D5A\u94B1\uFF0C\u518D\u52A0\u4E0A\u8FD9\u4E24\u5E74\u75AB\u60C5\u9632\u63A7\u5E26\u6765\u7684\u5F71\u54CD\uFF0C\u5F88\u591A\u8BCA\u6240\u90FD\u5DF2\u7ECF\u5173\u95E8\u6216\u8005\u5373\u5C06\u5173\u95E8\u3002</p><p>\u6021\u79BE\u7531\u4E8E\u6709\u7EBF\u4E0A\u7528\u6237\u7684\u79EF\u7D2F\uFF0C\u5C11\u4E86\u5F88\u591A\u5E02\u573A\u6295\u5165\u7684\u8D39\u7528\u652F\u51FA\uFF0C\u62DB\u533B\u751F\u4E5F\u6709\u4E00\u5B9A\u4F18\u52BF\uFF0C\u7B2C\u4E00\u5BB6\u8BCA\u6240\u5728\u75AB\u60C5\u524D\u8FD8\u662F\u80FD\u6253\u5E73\uFF0C\u4F46\u540E\u9762\u75AB\u60C5\u4E00\u6765\uFF0C\u513F\u75BE\u5927\u90E8\u5206\u4E0D\u80FD\u63A5\u8BCA\uFF0C\u513F\u79D1\u53EA\u80FD\u770B\u513F\u4FDD\uFF0C\u5C31\u4E00\u76F4\u5904\u4E8E\u4E8F\u635F\u72B6\u6001\u3002</p><p>\u6240\u5E78\u7684\u662F\u6211\u4EEC\u5728\u7B2C\u4E8C\u5BB6\u95E8\u8BCA\u53CA\u65F6\u6269\u5C55\u4E86\u76AE\u80A4&amp;\u533B\u7F8E\u4EE5\u53CA\u53E3\u8154\u4E13\u79D1\uFF0C\u5728\u8425\u6536\u4E0A\u624D\u6709\u6240\u6539\u5584\uFF0C\u4F46\u76EE\u524D3\u5BB6\u8BCA\u6240\u7684\u6574\u4F53\u4E1A\u52A1\u4ECD\u5904\u4E8E\u5C0F\u5E45\u4E8F\u635F\u72B6\u6001\u3002</p><p>\u4E00\u4EF6\u4E8B\u80FD\u5426\u505A\u5F97\u66F4\u591A\u66F4\u5FEB\uFF0C\u80AF\u5B9A\u662F\u53D6\u51B3\u4E8E\u505A\u4E8B\u6240\u9700\u8981\u7684\u8D44\u6E90\uFF0C\u5BF9\u5F00\u8BCA\u6240\u6765\u8BF4\uFF0C\u8D44\u91D1\u6295\u5165\u8FD9\u4E48\u5927\uFF0C\u5982\u679C\u8FD8\u6301\u7EED\u4E8F\u635F\uFF0C\u90A3\u80AF\u5B9A\u4F1A\u5E26\u6765\u7ECF\u8425\u4E0A\u7684\u98CE\u9669\uFF0C\u9664\u975E\u662F\u540E\u7EED\u80FD\u63A2\u7D22\u51FA\u53EF\u6301\u7EED\u7ECF\u8425\u7684\u6A21\u5F0F\uFF0C\u5426\u5219\u8FD9\u4EF6\u4E8B\u5C31\u5F88\u96BE\u6301\u7EED\u3002</p><p>\u6240\u5E78\u7684\u662F\u6211\u4EEC\u8C03\u6574\u4E86\u4E13\u79D1\u8303\u56F4\u540E\uFF0C\u6211\u4EEC\u7684\u95E8\u8BCA\u4E1A\u52A1\u7684\u7ECF\u8425\u72B6\u51B5\u6709\u660E\u663E\u7684\u6539\u5584\uFF0C\u5F53\u524D\u7684\u4E8F\u635F\u4E5F\u5728\u6211\u4EEC\u6240\u80FD\u63A5\u53D7\u7684\u5B89\u5168\u7EBF\u4EE5\u5185\uFF0C\u6240\u4EE5\u6211\u4EEC\u8FD8\u4F1A\u7EE7\u7EED\u63A2\u7D22\u95E8\u8BCA\u4E1A\u52A1\uFF0C\u5305\u62EC\u5728\u65B0\u7684\u57CE\u5E02\u5F00\u8BBE\u65B0\u7684\u95E8\u8BCA\uFF0C\u4F46\u5728\u95E8\u8BCA\u4E1A\u52A1\u6574\u4F53\u5B9E\u73B0\u76C8\u5229\u4E4B\u524D\uFF0C\u63A2\u7D22\u7684\u901F\u5EA6\u80AF\u5B9A\u4E0D\u4F1A\u5F88\u5FEB\u3002</p><p>\u6240\u4EE5\u5927\u5BB6\u7684\u547C\u5401\u7684\u7B2C\u4E00\u53E5\u8BDD\u4E5F\u662F\u5F88\u5BF9\u7684\uFF0C\u5C31\u662F\u597D\u597D\u7ECF\u8425\u6211\u4EEC\u73B0\u5728\u95E8\u8BCA\u90E8\uFF0C\u5E0C\u671B\u5728\u6211\u4EEC\u89C4\u8303\u884C\u533B\u7684\u6807\u51C6\u4E0B\uFF0C\u80FD\u63A2\u7D22\u51FA\u80FD\u76C8\u5229\u7684\u4E1A\u52A1\u6A21\u5F0F\u548C\u8FD0\u8425\u65B9\u5F0F\uFF0C\u8FD9\u6837\u6211\u4EEC\u624D\u6709\u673A\u4F1A\u66F4\u5FEB\u628A\u8BCA\u6240\u5F00\u5230\u66F4\u591A\u57CE\u5E02\uFF0C\u8BA9\u5927\u5BB6\u5728\u6709\u5C31\u533B\u9700\u6C42\u65F6\uFF0C\u80FD\u6709\u4E00\u4E2A\u66F4\u89C4\u8303\u66F4\u8212\u9002\u7684\u5C31\u533B\u9009\u62E9\u3002</p><p>\u5F53\u4E0B\u5F00\u9075\u5FAA\u5FAA\u8BC1\u533B\u5B66\u7406\u5FF5\u7684\u8BCA\u6240\u4E4B\u6240\u4EE5\u8FD8\u5F88\u96BE\uFF0C\u4E5F\u6B63\u5982\u6211\u4EE5\u524D\u6240\u5199\u8FC7\u7684\uFF0C\u6700\u5927\u95EE\u9898\u662F\u4E86\u89E3\u5FAA\u8BC1\u533B\u5B66\u7684\u4EBA\u8FD8\u6BD4\u8F83\u5C11\uFF0C\u613F\u610F\u9009\u62E9\u8FD9\u79CD\u8FD9\u79CD\u533B\u7597\u7684\u7FA4\u4F53\u8FD8\u592A\u5C0F\u3002</p><p>\u770B\u75C5\u5230\u516C\u7ACB\u533B\u9662\uFF0C\u6302\u53F7\u5341\u51E0\u4E8C\u5341\u5757\u94B1\uFF0C\u82B1\u4E00\u4E24\u767E\u67E5\u4E2A\u8840\u62CD\u4E2A\u7247\uFF0C\u518D\u82B1\u4E00\u4E24\u767E\u62FF\u51E0\u79CD\u836F\uFF1B\u6309\u5FAA\u8BC1\u533B\u5B66\u7684\u65B9\u5F0F\u770B\u75C5\uFF0C\u8BCA\u91D1\u56DB\u4E94\u767E\uFF0C\u5076\u5C14\u505A\u68C0\u67E5\u6216\u5F00\u836F\u3002 \u76F8\u6BD4\u8D77\u6765\uFF0C\u867D\u7136\u82B1\u7684\u94B1\u5176\u5B9E\u5DEE\u4E0D\u592A\u591A\uFF0C\u4F46\u524D\u8005\u95E8\u69DB\u4F4E\uFF0C\u62CD\u7247\u62FF\u836F\u83B7\u5F97\u611F\u66F4\u5F3A\uFF0C\u540E\u8005\u95E8\u69DB\u9AD8\uFF0C\u9664\u4E86\u533B\u751F\u8BB2\u89E3\u66F4\u8010\u5FC3\u8BE6\u7EC6\u5916\uFF0C\u6CA1\u6709\u5F88\u5F3A\u83B7\u5F97\u611F\u3002 \u6240\u4EE5\u5927\u90E8\u5206\u4EBA\u8FD8\u662F\u9009\u62E9\u524D\u8005\uFF0C\u53EA\u6709\u770B\u79D1\u666E\u6BD4\u8F83\u591A\uFF0C\u5BF9\u5FAA\u8BC1\u533B\u5B66\u6709\u6240\u4E86\u89E3\uFF0C\u624D\u80FD\u66F4\u597D\u5730\u610F\u8BC6\u5230\u540E\u8005\u7684\u4EF7\u503C\u3002</p><p>\u8FD9\u91CC\u7684\u8BFB\u8005\uFF0C\u5927\u591A\u4ECE\u5B69\u5B50\u51FA\u751F\u5F00\u59CB\u5C31\u8BFB\u79D1\u666E\uFF0C\u5BF9\u5FAA\u8BC1\u533B\u5B66\u6709\u6240\u4E86\u89E3\uFF0C\u53EF\u80FD\u4F1A\u671F\u671B\u9644\u8FD1\u6709\u6021\u79BE\u8BCA\u6240\uFF0C\u5728\u6709\u9700\u8981\u65F6\u80FD\u5F97\u5230\u66F4\u89C4\u8303\u7684\u533B\u7597\uFF0C\u4F46\u628A\u5927\u5BB6\u653E\u5230\u666E\u901A\u5988\u5988\u7FA4\u6216\u5BB6\u5EAD\u7FA4\u91CC\uFF0C\u5927\u5BB6\u5176\u5B9E\u90FD\u8FD8\u662F\u201C\u53E6\u7C7B\u201D\uFF0C\u4EC5\u9760\u670D\u52A1\u4EBA\u7FA4\u4E2D\u7684\u201C\u53E6\u7C7B\u201D\uFF0C\u8FD0\u8425\u81EA\u7136\u5C31\u8270\u96BE\u3002</p><p>\u53EA\u6709\u5F53\u8BA4\u540C\u8FD9\u79CD\u533B\u7597\u670D\u52A1\u65B9\u5F0F\u7684\u4EBA\u8D8A\u6765\u8D8A\u591A\uFF0C\u6211\u4EEC\u5F00\u8FD9\u6837\u7684\u8BCA\u6240\u5B9E\u73B0\u6536\u652F\u5E73\u8861\u7684\u65F6\u95F4\u624D\u80FD\u66F4\u77ED\uFF0C\u6021\u79BE\u8BCA\u6240\u624D\u80FD\u5F00\u5F97\u66F4\u591A\u66F4\u5FEB\u3002\u6309\u7167\u6211\u4EEC\u81EA\u5DF1\u7684\u6D4B\u7B97\uFF0C\u5982\u679C\u4E00\u4E2A\u57CE\u5E02\u67095000\u4E2A\u6021\u79BE\u4F1A\u5458\uFF0C\u90A3\u6211\u4EEC\u8FD0\u8425\u4E00\u5BB6\u8BCA\u6240\u5C31\u4E0D\u4F1A\u6709\u4EC0\u4E48\u8D22\u52A1\u538B\u529B\u3002</p><p><strong>\u6240\u4EE5\u53EA\u8981\u4EFB\u4F55\u4E00\u4E2A\u57CE\u5E02\u7684\u4F1A\u5458\u8FBE\u5230\u8FD9\u4E2A\u6570\u91CF\uFF0C\u6211\u4EEC\u5C31\u786E\u5B9A\u4F1A\u53BB\u5F00\uFF0C\u5728\u8FBE\u5230\u8FD9\u4E2A\u6570\u91CF\u4E4B\u524D\uFF0C\u6211\u4EEC\u4F1A\u6839\u636E\u6021\u79BE\u5F53\u524D\u7684\u8D44\u6E90\uFF0C\u4F18\u5148\u9009\u62E9\u4F1A\u5458\u6570\u91CF\u548C\u533B\u751F\u6570\u91CF\u6BD4\u8F83\u591A\u7684\u57CE\u5E02\uFF0C\u5728\u6761\u4EF6\u5141\u8BB8\u7684\u60C5\u51B5\u4E0B\u53BB\u5F00\u3002</strong></p><p>\u800C\u4E14\u6211\u76F8\u4FE1\uFF0C\u968F\u7740\u6709\u5FAA\u8BC1\u89C2\u5FF5\u7684\u5BB6\u957F\u8D8A\u6765\u8D8A\u591A\uFF0C\u6021\u79BE\u6301\u7EED\u7ECF\u8425\u7684\u80FD\u529B\u4E5F\u4F1A\u8D8A\u6765\u8D8A\u5F3A\uFF0C\u4E5F\u4E00\u5B9A\u4F1A\u6709\u8D8A\u6765\u8D8A\u591A\u7684\u6021\u79BE\u8BCA\u6240\u5F00\u5230\u5927\u5BB6\u6240\u5728\u7684\u5730\u533A\u53BB\u3002</p><p>\u53E6\u5916\u6211\u60F3\u8BF4\u7684\u662F\uFF0C\u8BCA\u6240\u5C31\u533B\u4E5F\u53EA\u662F\u89E3\u51B3\u5065\u5EB7\u95EE\u9898\u7684\u4E00\u4E2A\u73AF\u8282\u800C\u5DF2\uFF0C\u54EA\u6015\u6CA1\u6709\u6021\u79BE\u8BCA\u6240\uFF0C\u4F9D\u6258\u6021\u79BE\u73B0\u5728\u7684\u5176\u5B83\u670D\u52A1\u80FD\u529B\uFF0C\u4E5F\u5DF2\u7ECF\u80FD\u89E3\u51B3\u5F88\u591A\u5F88\u591A\u95EE\u9898\u4E86\u3002</p><p>\u6BD4\u5982\u901A\u8FC7\u9605\u8BFB\u6021\u79BE\u7684\u79D1\u666E\u6216\u8BFE\u7A0B\uFF0C\u5EFA\u7ACB\u4E86\u6B63\u786E\u7684\u5065\u5EB7\u89C2\u5FF5\uFF0C\u4E86\u89E3\u4E86\u4E00\u4E9B\u533B\u5B66\u5E38\u8BC6\uFF0C\u5728\u5B69\u5B50\u6216\u5BB6\u4EBA\u9047\u5230\u95EE\u9898\u65F6\uFF0C\u6709\u4E00\u4E9B\u81EA\u5DF1\u7684\u521D\u6B65\u5224\u5B9A\uFF0C\u5C31\u5DF2\u7ECF\u907F\u514D\u4E86\u4E00\u4E9B\u56E0\u4E3A\u76F2\u76EE\u7684\u7126\u8651\u800C\u8DD1\u53BB\u533B\u9662\uFF0C\u6216\u8005\u5403\u4E00\u4E9B\u6CA1\u7528\u8FD8\u53EF\u80FD\u6709\u5BB3\u7684\u836F\u3002</p><p>\u5B9E\u5728\u81EA\u5DF1\u5224\u65AD\u4E0D\u51C6\u7684\uFF0C\u8FD8\u53EF\u4EE5\u901A\u8FC7\u7EBF\u4E0A\u300C\u95EE\u533B\u751F\u300D\u670D\u52A1\uFF0C\u6210\u4EBA\u3001\u513F\u79D1\u5404\u4E2A\u4E13\u79D1\u90FD\u6709\uFF0C\u54A8\u8BE2\u4E00\u4E0B\u533B\u751F\uFF0C\u540C\u6837\u4E5F\u53EF\u80FD\u907F\u514D\u4E00\u6B21\u5C31\u533B\u3002</p><p>\u5B9E\u5728\u9700\u8981\u5C31\u533B\u68C0\u67E5\u7684\uFF0C\u5982\u679C\u5728\u5E7F\u6DF1\u5730\u533A\u81EA\u7136\u53EF\u4EE5\u53BB\u6021\u79BE\u95E8\u8BCA\u3002</p><p>\u5982\u679C\u4E0D\u662F\u5728\u5E7F\u6DF1\u5730\u533A\uFF0C \u53EF\u4EE5\u641C\u4E00\u4E0B\u6021\u79BE\u4E0A\u6709\u6CA1\u6709\u672C\u5730\u7684\u533B\u751F\uFF0C\u6709\u7684\u8BDD\u53BB\u627E\u4ED6\u4EEC\u5C31\u533B\u65F6\u8BF4\u662F\u4ECE\u6021\u79BE\u8FC7\u6765\u7684\uFF0C\u5927\u6982\u7387\u4E5F\u80FD\u5F97\u5230\u89C4\u8303\u7684\u8BCA\u7597\uFF0C\u5F53\u7136\u8981\u6C42\u533B\u751F\u5728\u516C\u7ACB\u533B\u9662\u7684\u5341\u51E0\u5757\u6302\u53F7\u8D39\u4E0B\u63D0\u4F9B\u5728\u6021\u79BE\u7EBF\u4E0A\u4E00\u6837\u8010\u5FC3\u7EC6\u81F4\u7684\u8BB2\u89E3\u4E5F\u4E0D\u592A\u73B0\u5B9E\u3002</p><p>\u5982\u679C\u6CA1\u6709\u672C\u5730\u7684\u6021\u79BE\u533B\u751F\uFF0C\u5230\u9644\u8FD1\u793E\u5EB7\u6216\u533B\u9662\u5C31\u8BCA\u6216\u68C0\u67E5\u540E\uFF0C\u62C5\u5FC3\u533B\u751F\u5F00\u7684\u836F\u4E0D\u5408\u7406\uFF0C\u8FD8\u53EF\u4EE5\u901A\u8FC7\u300C\u95EE\u533B\u751F\u300D\u670D\u52A1\u5BFB\u6C42\u4E00\u4E2A\u7B2C\u4E8C\u8BCA\u7597\u610F\u89C1\uFF0C\u901A\u8FC7\u8FD9\u79CD\u7EBF\u4E0B\u4F53\u68C0+\u7EBF\u4E0A\u95EE\u8BCA\u7684\u65B9\u5F0F\uFF0C\u4E5F\u5927\u6982\u7387\u80FD\u5F97\u5230\u4E00\u4E2A\u66F4\u5408\u7406\u7684\u89E3\u51B3\u65B9\u6848\u3002</p><p>\u800C\u8FD9\u4E9B\u670D\u52A1\uFF0C\u90FD\u53EF\u4EE5\u901A\u8FC7\u300C\u6021\u79BE\u4F1A\u5458\u300D\u4EAB\u53D7\u5230\u66F4\u591A\u7684\u6743\u76CA\uFF0C\u6BD4\u5982\u514D\u8D39\u542C\u7EDD\u5927\u90E8\u5206\u7684\u8BFE\u7A0B\uFF0C\u95EE\u533B\u751F\u4EAB8.8\u6298\u6263\uFF0C\u6BD4\u5982\u4F1A\u5458\u793E\u7FA4\u548C\u7406\u5FF5\u4E00\u81F4\u7684\u5BB6\u957F\u4E00\u8D77\u5B66\u4E60\u8BA8\u8BBA\u7B49\u3002</p><p>\u6240\u4EE5\uFF0C\u5982\u679C\u4F60\u4E0D\u662F\u6021\u79BE\u4F1A\u5458\uFF0C\u53EF\u4EE5\u901A\u8FC7\u4E0A\u9762\u4E8C\u7EF4\u7801\u8BE6\u7EC6\u4E86\u89E3\uFF0C\u5982\u679C\u4F60\u5DF2\u7ECF\u662F\u4F1A\u5458\uFF0C\u4E5F\u53EF\u4EE5\u628A\u6021\u79BE\u63A8\u8350\u7ED9\u66F4\u591A\u4EBA\uFF0C\u5F53\u6021\u79BE\u4F1A\u5458\u63A5\u8FD15000\u4EBA\uFF0C\u5F53\u5730\u6709\u6021\u79BE\u8BCA\u6240\u4E5F\u662F\u8FDF\u65E9\u7684\u4E8B\u3002</p>",28),u={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651634217&idx=1&sn=43595531117977908551cb8932ef67d4&chksm=8b5c93d3bc2b1ac5d2a4be38d1a46ccdddb8e1382129294290e1018d46d9b49a2e5b829fc56c&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},z=p("\u67E5\u770B\u539F\u6587");function k(N,E){const c=n("RouterLink"),o=n("ExternalLinkIcon");return a(),s(r,null,[b,e("p",null,[h,t(c,{to:"/s/__biz=MzA5NzE5ODIyOQ==&mid=2651634189&idx=1&sn=71e49b6354e4ecb3a98d7ab3cd1c6d87/"},{default:d(()=>[m]),_:1}),x]),e("p",null,[t(c,{to:"/s/__biz=MzA5NzE5ODIyOQ==&mid=2651634189&idx=1&sn=71e49b6354e4ecb3a98d7ab3cd1c6d87/"})]),f,e("p",null,[e("a",u,[z,t(o)])])],64)}var I=i(l,[["render",k]]);export{I as default};
