import{r as t,o as a,c,a as p,b as n,w as d,F as _,d as o,e}from"./app.9aaa2256.js";import{_ as i}from"./plugin-vue_export-helper.21dcd24c.js";const h={},l=o('<h3 id="\u51FA\u6C57\u548C\u5C3F\u5E8A" tabindex="-1"><a class="header-anchor" href="#\u51FA\u6C57\u548C\u5C3F\u5E8A" aria-hidden="true">#</a> \u51FA\u6C57\u548C\u5C3F\u5E8A</h3><p>\u201C\u4F60\u597D\uFF0C\u88F4\u533B\u751F\u3002\u6211\u5C0F\u5B696\u5C81\u534A\u4E86\uFF0C\u8FD8\u5C3F\u5E8A\uFF0C\u665A\u4E0A\u7761\u89C9\u521A\u5165\u7761\u534A\u4E2A\u5C0F\u65F6\u51FA\u6C57\u5F88\u5389\u5BB3\uFF0C\u662F\u80BE\u865A\u5417\uFF1F\u8BE5\u68C0\u67E5\u4EC0\u4E48\u9879\u76EE\u5417\uFF1F\u80FD\u79D1\u666E\u4E00\u4E0B\u5417\uFF1F\u201D</p><p>\u56E0\u4E3A\u79C1\u4FE1\u54A8\u8BE2\u592A\u591A\uFF0C\u6240\u4EE5\u6CA1\u7528\u7CBE\u529B\u56DE\u7B54\u79C1\u4FE1\u54A8\u8BE2\u95EE\u9898\uFF0C\u4F46\u8FD9\u4E2A\u95EE\u9898\u91CC\u5305\u542B\u4E86\u4E24\u4E2A\u6BD4\u8F83\u5E38\u89C1\u7684\u95EE\u9898\uFF0C\u53C8\u4E0D\u662F\u4E09\u8A00\u4E24\u8BED\u80FD\u8BF4\u6E05\uFF0C\u6240\u4EE5\u5C31\u4E13\u95E8\u5199\u7BC7\u6587\u7AE0\u56DE\u7B54\uFF0C\u671F\u671B\u80FD\u5BF9\u5176\u4ED6\u88AB\u5B69\u5B50\u51FA\u6C57\u548C\u5C3F\u5E8A\u56F0\u6270\u7684\u5BB6\u957F\u6709\u6240\u5E2E\u52A9\u3002</p><p><strong>\u51FA\u6C57\u591A</strong></p><p>\u51FA\u6C57\u662F\u4EBA\u4F53\u8C03\u8282\u4F53\u6E29\u7684\u4E00\u4E2A\u91CD\u8981\u65B9\u5F0F\uFF0C\u662F\u751F\u7406\u73B0\u8C61\uFF0C\u5F53\u5916\u754C\u6E29\u5EA6\u9AD8\u4E86\uFF0C\u51FA\u6C57\u5C31\u591A\u4E86\u3002\u6BCF\u4E2A\u4EBA\u4F53\u8D28\u4E0D\u4E00\u6837\uFF0C\u5728\u76F8\u540C\u7684\u6E29\u5EA6\u4E0B\uFF0C\u51FA\u6C57\u7684\u60C5\u51B5\u4E5F\u4F1A\u4E0D\u4E00\u6837\uFF0C\u5927\u4EBA\u5982\u6B64\uFF0C\u5B69\u5B50\u4E5F\u5982\u6B64\u3002\u53E6\u5916\u76F8\u5BF9\u5927\u4EBA\u800C\u8A00\uFF0C\u5B69\u5B50\u7684\u65B0\u9648\u4EE3\u8C22\u66F4\u65FA\u76DB\uFF0C\u51FA\u6C57\u4E5F\u53EF\u80FD\u6BD4\u5927\u4EBA\u66F4\u591A\u3002</p><p>\u5B69\u5B50\u7761\u89C9\u65F6\u51FA\u6C57\u591A\u4E5F\u5F88\u5E38\u89C1\uFF0C\u539F\u56E0\u4E0D\u662F\u5F88\u6E05\u695A\uFF0C\u6709\u4EBA\u8BA4\u4E3A\u548C\u5B69\u5B50\u7761\u7720\u6A21\u5F0F\u6709\u5173\uFF0C\u5728\u6DF1\u5EA6\u7761\u7720\u9636\u6BB5\uFF0C\u547C\u5438\u3001\u5FC3\u8DF3\u4E0B\u964D\uFF0C\u808C\u8089\u653E\u677E\uFF0C\u6709\u7684\u4EBA\u5C31\u4F1A\u51FA\u6C57\uFF0C\u5B69\u5B50\u7684\u6DF1\u5EA6\u7761\u7720\u6BD4\u5927\u4EBA\u591A\uFF0C\u6240\u4EE5\u7761\u89C9\u51FA\u6C57\u4E5F\u5E38\u5E38\u6BD4\u5927\u4EBA\u8981\u591A\u3002\u9999\u6E2F\u7684\u4E00\u9879\u7814\u7A76\uFF0C\u8C03\u67E56\u5343\u591A\u4E2A\u5B69\u5B50\u591C\u95F4\u51FA\u6C57\u7684\u60C5\u51B5\uFF0C\u53D1\u73B0\u4E00\u5E74\u5185\u5E73\u5747\u6BCF\u5468\u591C\u95F4\u51FA\u6C57\u6709\u4E00\u5929\u6216\u4E00\u5929\u4EE5\u4E0A\u8FBE\u523011.7%\uFF0C\u7537\u5B69\u6BD4\u5973\u5B69\u591A\u3002</p><p>\u5728\u9AD8\u6E29\u73AF\u5883\u4E0B\u5927\u90E8\u5206\u4EBA\u90FD\u4F1A\u51FA\u6C57\uFF0C\u5982\u679C\u89C9\u5F97\u5B69\u5B50\u51FA\u6C57\u7279\u522B\u591A\uFF0C\u9996\u5148\u8981\u68C0\u67E5\u4E00\u4E0B\u623F\u95F4\u6E29\u5EA6\u548C\u5B69\u5B50\u7A7F\u8863\u76D6\u88AB\u60C5\u51B5\u3002\u5927\u4EBA\u5E38\u5E38\u559C\u6B22\u7528\u81EA\u5DF1\u7684\u89C2\u611F\u6765\u5224\u65AD\u5B69\u5B50\u7684\u9002\u5B9C\u73AF\u5883\u6E29\u5EA6\uFF0C\u4E8B\u5B9E\u4E0A\u5B69\u5B50\u4EE3\u8C22\u65FA\u76DB\uFF0C\u6BD4\u5927\u4EBA\u66F4\u6015\u70ED\uFF0C\u7ED9\u5B69\u5B50\u7A7F\u76D6\u592A\u591A\u7684\u7ED3\u679C\u662F\u88AB\u5B50\u5F88\u5FEB\u88AB\u8E22\u5F00\uFF0C\u7ED3\u679C\u662F\u4EC0\u4E48\u90FD\u76D6\u4E0D\u4F4F\uFF0C\u8FD8\u4E0D\u5982\u6362\u4E2A\u8584\u7684\u3002\u5BF9\u4E8E\u5A74\u513F\u6765\u8BF4\uFF0C\u8FC7\u70ED\u8FD8\u4F1A\u589E\u52A0\u5A74\u513F\u731D\u6B7B\u7EFC\u5408\u5F81\u7684\u98CE\u9669\u3002</p><p>\u5982\u679C\u6392\u9664\u4E86\u73AF\u5883\u6E29\u5EA6\u7684\u539F\u56E0\uFF0C\u8FD8\u662F\u89C9\u5F97\u5B69\u5B50\u7279\u522B\u7231\u51FA\u6C57\uFF0C\u90A3\u5C31\u8981\u6CE8\u610F\u4E00\u4E0B\u6709\u6CA1\u6709\u522B\u7684\u75C7\u72B6\u3002\u6BD4\u5982\u4F5D\u507B\u75C5\u7684\u5B69\u5B50\u9664\u4E86\u51FA\u6C57\u591A\uFF0C\u8FD8\u4F1A\u6709\u7CBE\u795E\u548C\u9AA8\u9ABC\u7684\u8868\u73B0\uFF1B\u5148\u5929\u6027\u5FC3\u810F\u75C5\u7684\u5B69\u5B50\u5BB9\u6613\u51FA\u6C57\uFF0C\u4F46\u5F80\u5F80\u4E5F\u4F1A\u6709\u53E3\u5507\u53D1\u7EC0\u3001\u6C14\u5598\u7B49\u8868\u73B0\uFF1B\u5982\u679C\u662F\u7532\u72B6\u817A\u529F\u80FD\u4EA2\u8FDB\uFF0C\u4E5F\u5BB9\u6613\u51FA\u6C57\uFF0C\u4F46\u540C\u65F6\u53EF\u80FD\u4F1A\u6709\u7532\u72B6\u817A\u80BF\u5927\uFF0C\u6D88\u7626\u7B49\u75C7\u72B6\u3002</p><p>\u5982\u679C\u5B69\u5B50\u53EA\u662F\u5728\u7761\u89C9\u65F6\u5019\u7279\u522B\u5BB9\u6613\u51FA\u6C57\uFF0C\u540C\u6837\u9700\u8981\u6CE8\u610F\u6709\u6CA1\u6709\u4F34\u968F\u75C7\u72B6\uFF0C\u6BD4\u5982\u4F4E\u70ED\u3001\u6253\u9F3E\u7B49\u3002\u4E00\u4E9B\u80BF\u7624\u3001\u6162\u6027\u611F\u67D3\u53EF\u80FD\u5BFC\u81F4\u4F4E\u70ED\u3001\u4EE3\u8C22\u4E0A\u5347\uFF0C\u5BFC\u81F4\u51FA\u6C57\u589E\u591A\uFF0C\u9F3B\u708E\u3001\u817A\u6837\u4F53\u80A5\u5927\u7B49\u95EE\u9898\u8BA9\u5B69\u5B50\u7761\u89C9\u65F6\u547C\u5438\u8D39\u529B\uFF0C\u7761\u7720\u969C\u788D\uFF0C\u4E5F\u4F1A\u5BFC\u81F4\u51FA\u6C57\u589E\u591A\u3002</p><p>\u5982\u679C\u6709\u4F34\u968F\u75C7\u72B6\uFF0C\u8981\u53CA\u65F6\u627E\u533B\u751F\u5C31\u8BCA\u67E5\u627E\u539F\u56E0\uFF0C\u5982\u679C\u6CA1\u6709\uFF0C\u5B69\u5B50\u5355\u7EAF\u51FA\u6C57\u591A\u4E00\u70B9\u662F\u6B63\u5E38\u7684\u4E0D\u5FC5\u5728\u610F\u3002</p><p><strong>\u5C3F\u5E8A</strong></p><p>\u4E00\u822C\u5230\u4E865\u5C81\uFF0C\u5B69\u5B50\u57FA\u672C\u90FD\u80FD\u81EA\u5DF1\u63A7\u5236\u5927\u5C0F\u4FBF\uFF0C\u4F46\u5C3F\u5E8A\u4ECD\u7136\u6BD4\u8F83\u5E38\u89C1\uFF0C5\u5C81\u7684\u5B69\u5B501/5\u8FD8\u4F1A\u5C3F\u5E8A\uFF0C7\u5C81\u7684\u5B69\u5B50\u8FD8\u67091/10\u4F1A\u5C3F\u5E8A\uFF0C\u5230\u4E8610\u5C81\u8FD8\u67091/20\u4F1A\u5C3F\u5E8A\u3002\u6709\u5C3F\u5E8A\u5BB6\u65CF\u53F2\u7684\uFF0C\u5B69\u5B50\u4E5F\u5BB9\u6613\u5C3F\u5E8A\uFF1B\u7537\u5B69\uFF0C\u591A\u52A8\u75C7\u7684\u5B69\u5B50\u51FA\u73B0\u5C3F\u5E8A\u7684\u673A\u4F1A\u66F4\u9AD8\u3002\u4F46\u548C\u51FA\u6C57\u591A\u4E00\u6837\uFF0C\u5C3F\u5E8A\u7684\u539F\u56E0\u4E5F\u4E0D\u662F\u90A3\u4E48\u6E05\u695A\u3002</p><p>\u53EF\u80FD\u6709\u7684\u5B69\u5B50\u8180\u80F1\u5BB9\u79EF\u6BD4\u8F83\u5C0F\uFF0C\u88C5\u4E0D\u4E0B\u90A3\u4E48\u591A\u5C3F\u6DB2\uFF1B\u6216\u8005\u6709\u7684\u5B69\u5B50\u56E0\u4E3A\u6297\u5229\u5C3F\u6FC0\u7D20\u5206\u6CCC\u5F97\u5C11\u4E00\u70B9\uFF0C\u665A\u4E0A\u4EA7\u5C3F\u6BD4\u8F83\u591A\uFF1B\u6216\u8005\u6709\u7684\u5B69\u5B50\u7761\u5F97\u7279\u522B\u6C89\uFF0C\u4EE5\u81F3\u4E8E\u8180\u80F1\u6DA8\u5F97\u5389\u5BB3\u4E5F\u4E0D\u4F1A\u9192\uFF0C\u8FD9\u4E9B\u90FD\u53EF\u80FD\u5BFC\u81F4\u5C3F\u5E8A\uFF0C\u4F46\u5F88\u96BE\u786E\u5B9A\u5177\u4F53\u539F\u56E0\u3002\u4E00\u822C\u8BA4\u4E3A\uFF0C\u5B69\u5B50\u7684\u8180\u80F1\u3001\u795E\u7ECF\u7CFB\u7EDF\u6216\u8005\u4E24\u8005\u4E4B\u95F4\u7684\u534F\u8C03\u63A7\u5236\u80FD\u529B\u56E0\u4E3A\u4F53\u8D28\u7684\u5DEE\u5F02\u843D\u540E\u4E00\u4E9B\uFF0C\u662F\u5C3F\u5E8A\u7684\u4E3B\u8981\u539F\u56E0\uFF0C\u5927\u90E8\u5206\u548C\u75BE\u75C5\u6CA1\u6709\u5173\u7CFB\u3002</p>',13),f=e("\u4F46\u4E5F\u6709\u5C11\u90E8\u5206\u5B69\u5B50\u53EF\u80FD\u548C\u75BE\u75C5\u6709\u5173\u7CFB\uFF0C\u6BD4\u5982\u4FBF\u79D8\u7684\u5B69\u5B50\uFF0C\u76F4\u80A0\u91CC\u7CAA\u5757\u53EF\u80FD\u5BF9\u8180\u80F1\u9020\u6210\u538B\u8FEB\uFF0C\u5BFC\u81F4\u8180\u80F1\u5BB9\u79EF\u53D8\u5C0F\u5BB9\u6613\u5C3F\u5E8A\uFF0C\u6CCC\u5C3F\u7CFB\u611F\u67D3\u4E5F\u53EF\u80FD\u5BFC\u81F4\u5C3F\u8DEF\u523A\u6FC0\uFF0C\u51FA\u73B0\u5C3F\u5E8A\u3002\u5982\u679C\u5B69\u5B50\u5C3F\u5E8A\u7684\u540C\u65F6\u6709\u5C3F\u6025\u3001\u5C3F\u75DB\u3001\u8840\u5C3F\uFF0C\u4E5F\u8981\u53BB\u627E\u533B\u751F\u770B\u770B\u3002\u53E6\u5916\u5C3F\u5E8A\u4E5F\u53EF\u80FD\u662F\u963B\u585E\u6027\u7761\u7720\u547C\u5438\u6682\u505C\u75C7\u7684\u4E00\u4E2A\u8868\u73B0\uFF0C\u5982\u679C\u5B69\u5B50\u6709"),m=e("\u6253\u9F3E"),x=e("\uFF0C\u4E5F\u8981\u65E9\u70B9\u627E\u533B\u751F\u770B\u770B\u3002"),g=o("<p>\u7279\u522B\u8981\u6307\u51FA\u7684\u662F\uFF0C\u5982\u679C\u5B69\u5B50\u5728\u767D\u5929\u6E05\u9192\u7684\u65F6\u5019\u4E5F\u6709\u5C3F\u88E4\u5B50\uFF0C\u90A3\u591A\u534A\u6709\u95EE\u9898\uFF0C\u4E5F\u9700\u8981\u53BB\u770B\u533B\u751F\uFF0C\u6392\u9664\u4E00\u4E9B\u5148\u5929\u6027\u7684\u6CCC\u5C3F\u7CFB\u7EDF\u548C\u795E\u7ECF\u7CFB\u7EDF\u75BE\u75C5\u3002\u5982\u679C\u5B69\u5B50\u4EE5\u524D\u90FD\u5F88\u597D\uFF0C\u6216\u8005\u4E4B\u524D\u534A\u5E74\u5185\u90FD\u6CA1\u6709\uFF0C\u7A81\u7136\u5F00\u59CB\u5C3F\u5E8A\u4E86\u4E5F\u8981\u8B66\u60D5\u662F\u4E0D\u662F\u7EE7\u53D1\u4E8E\u7CD6\u5C3F\u75C5\u4E4B\u7C7B\u7684\u75BE\u75C5\u3002</p><p>\u5982\u679C\u6CA1\u6709\u522B\u7684\u4F34\u968F\u75C7\u72B6\uFF0C\u5C3F\u5E8A\u4E00\u822C\u4F1A\u968F\u7740\u5E74\u9F84\u7684\u589E\u957F\u9010\u6E10\u597D\u8F6C\u3001\u6D88\u5931\uFF0C\u6240\u4EE5\u5BB6\u957F\u4E5F\u4E0D\u5FC5\u7D27\u5F20\u3002\u5B69\u5B50\u5C3F\u5E8A\u4E86\u9700\u8981\u6E05\u6D17\u88AB\u8925\uFF0C\u53EF\u80FD\u4F1A\u8BA9\u5BB6\u957F\u5F88\u70E6\uFF0C\u4E5F\u53EF\u80FD\u8BA9\u5B69\u5B50\u611F\u89C9\u5C34\u5C2C\uFF0C\u81EA\u5351\uFF0C\u6240\u4EE5\u8FD8\u662F\u9700\u8981\u5E72\u9884\u3002\u6839\u636E\u7F8E\u56FD\u513F\u79D1\u5B66\u4F1A\u65D7\u4E0B\u79D1\u666E\u7F51\u7AD9healthychildren.org\u7684\u610F\u89C1\uFF0C\u5BB6\u957F\u53EF\u4EE5\u8FD9\u6837\u505A\uFF1A</p><p>***\u4E0D\u8D23\u602A\uFF1A**\u8BB0\u4F4F\u5C3F\u5E8A\u4E0D\u662F\u5B69\u5B50\u7684\u9519\u3002</p><p>***\u544A\u8BC9\u5B69\u5B50\u5C3F\u5E8A\u662F\u600E\u4E48\u56DE\u4E8B\uFF1A**\u8BA9\u4ED6\u77E5\u9053\u5C3F\u5E8A\u4E0D\u662F\u4ED6\u7684\u9519\uFF0C\u957F\u5927\u4E86\u5927\u90E8\u5206\u4F1A\u81EA\u5DF1\u597D\u3002</p><p>***\u7167\u987E\u5B69\u5B50\u7684\u611F\u53D7\uFF1A**\u5982\u679C\u4F60\u4E0D\u628A\u5C3F\u5E8A\u5F53\u4EC0\u4E48\u5927\u4E8B\uFF0C\u540C\u65F6\u544A\u8BC9\u5B69\u5B50\u522B\u7684\u5B69\u5B50\u5F88\u591A\u4E5F\u5C3F\u5E8A\uFF0C\u5B69\u5B50\u5C31\u6CA1\u90A3\u4E48\u5927\u5FC3\u7406\u538B\u529B\u3002</p><p>***\u4FDD\u62A4\u5E8A\u94FA\uFF1A**\u53EF\u4EE5\u5728\u88AB\u5355\u4E0B\u76D6\u4E2A\u5851\u6599\u5E03\u4FDD\u62A4\u4E0B\u5E8A\u57AB\u4EE5\u514D\u5C3F\u6E7F\u3002</p><p>***\u8BA9\u5B69\u5B50\u6765\u5E2E\u5FD9\uFF1A**\u9F13\u52B1\u5B69\u5B50\u5E2E\u5FD9\u6362\u88AB\u5B50\u5E8A\u5355\uFF0C\u6709\u52A9\u4E8E\u63D0\u5347\u4ED6\u7684\u8D23\u4EFB\u611F\uFF0C\u4F46\u5982\u679C\u5B69\u5B50\u628A\u8FD9\u89C6\u4F5C\u4E00\u79CD\u60E9\u7F5A\u5C31\u4E0D\u8981\u8FD9\u6837\u505A\u3002</p><p>***\u8BA9\u5BB6\u4EBA\u4E0D\u5632\u7B11\u4ED6\uFF1A**\u4E0D\u8981\u8BA9\u5BB6\u91CC\u5176\u4ED6\u4EBA\uFF0C\u5C24\u5176\u662F\u5144\u5F1F\u59D0\u59B9\u5632\u7B11\u5B69\u5B50\uFF0C\u8BA9\u4ED6\u4EEC\u77E5\u9053\u8FD9\u4E0D\u662F\u4ED6\u7684\u9519\u3002</p><p>***\u7761\u524D\u6B65\u9AA4\uFF1A**\u4E0A\u5E8A\u524D\u4E0A\u5395\u6240\uFF0C\u4E0D\u5927\u91CF\u559D\u6C34\u548C\u996E\u6599\u3002</p><p>***\u8BD5\u7740\u53EB\u9192\u4ED6\u53BB\u5C3F\u5C3F\uFF1A**\u7761\u4E861-2\u5C0F\u65F6\u53BB\u5C3F\u4E00\u6B21\u53EF\u4EE5\u5E2E\u52A9\u5B69\u5B50\u6574\u591C\u4E0D\u518D\u5C3F\u3002</p><p>***\u8BA9\u5B69\u5B50\u4FDD\u6301\u4E50\u89C2\uFF1A**\u6CA1\u5C3F\u5E8A\u53EF\u4EE5\u5956\u52B1\uFF0C\u5C3F\u5E8A\u4E86\u8981\u5B89\u6170\u652F\u6301\uFF0C\u4E0D\u60E9\u7F5A\u3002</p><p><strong>*\u6E05\u695A\u5B69\u5B50\u65E5\u5E38\u5C3F\u5C3F\u548C\u6392\u4FBF\u4E60\u60EF\u3002</strong></p><p>\u53E6\u5916\u8981\u5B69\u5B50\u907F\u514D\u907F\u514D\u542B\u5496\u5561\u56E0\u7684\u996E\u98DF\uFF0C\u5982\u5496\u5561\u3001\u8336\u3001\u5DE7\u514B\u529B\u7B49\uFF0C\u56E0\u4E3A\u5496\u5561\u56E0\u53EF\u80FD\u4FC3\u8FDB\u8180\u80F1\u7684\u6D3B\u52A8\u3002\u5982\u679C\u6709\u4FBF\u79D8\u53EF\u4EE5\u5403\u70B9\u8F6F\u5316\u5927\u4FBF\u7684\u836F\u7269\uFF0C\u5982\u4E73\u679C\u7CD6\u4E4B\u7C7B\u3002\u5982\u679C\u4F7F\u7528\u8FD9\u4E9B\u65B9\u6CD51-3\u4E2A\u6708\u6CA1\u6548\uFF0C\u53EF\u4EE5\u7ED9\u5B69\u5B50\u8BD5\u8BD5\u5C3F\u6E7F\u63D0\u9192\u5668\uFF08\u7F51\u4E0A\u6709\u552E\uFF09\uFF0C\u8FD9\u4E9B\u88C5\u7F6E\u4E00\u9047\u6C34\u5C31\u4F1A\u53D1\u51FA\u63D0\u9192\u58F0\uFF0C\u53EF\u4EE5\u5524\u9192\u5B69\u5B50\u8BA9\u4ED6\u53BB\u5C3F\u5C3F,\u5F88\u591A\u5B69\u5B50\u53EF\u4EE5\u901A\u8FC7\u8FD9\u4E2A\u65B9\u6CD5\u77EB\u6B63\u597D\u30026\u5C81\u4EE5\u4E0A\u7684\u5B69\u5B50\u4E5F\u53EF\u4EE5\u627E\u533B\u751F\u770B\u770B\u662F\u5426\u9700\u8981\u5403\u70B9\u836F\u7269\u8BD5\u8BD5\u3002</p><p>\u5B69\u5B50\u5C3F\u5E8A\u5BB6\u957F\u786E\u5B9E\u5F88\u70E6\uFF0C\u4F46\u7761\u89C9\u5C3F\u5E8A\u662F\u4E0D\u81EA\u77E5\u7684\uFF0C\u5B69\u5B50\u81EA\u5DF1\u4E5F\u4E0D\u60F3\u5C3F\u5E8A\uFF0C\u8981\u5E2E\u52A9\u4ED6\u800C\u4E0D\u662F\u60E9\u7F5A\u4ED6\uFF0C\u5426\u5219\u53EA\u4F1A\u589E\u52A0\u4ED6\u7684\u7CBE\u795E\u538B\u529B\uFF0C\u53CD\u800C\u4E0D\u5229\u4E8E\u75C5\u60C5\u3002</p><p><strong>\u80BE\u865A</strong></p><p>\u80BE\u865A\u662F\u4E2D\u533B\u7684\u8BF4\u6CD5\uFF0C\u662F\u4E0D\u662F\u80BE\u865A\u8FD9\u79CD\u95EE\u9898\uFF0C\u6211\u5C31\u6CA1\u529E\u6CD5\u56DE\u7B54\u4E86\u3002\u591A\u6C57\u548C\u5C3F\u5E8A\u8FD9\u4E24\u4E2A\u95EE\u9898\u4E9B\u5171\u540C\u70B9\uFF0C\u5C31\u662F\u73B0\u4EE3\u533B\u5B66\u5BF9\u5B83\u4EEC\u7684\u539F\u56E0\u4E0D\u662F\u7279\u522B\u660E\u786E\uFF0C\u4E5F\u6CA1\u6709\u7B80\u5355\u660E\u4E86\u7ACB\u7AFF\u89C1\u5F71\u7684\u5904\u7406\u65B9\u6848\uFF0C\u8FD9\u5C31\u4E3A\u4E2D\u533B\u63D0\u4F9B\u4E86\u5F88\u5927\u7684\u7A7A\u95F4\uFF0C\u5F88\u591A\u5B69\u5B50\u56E0\u4E3A\u4E24\u4E2A\u95EE\u9898\u53BB\u770B\u4E86\u4E2D\u533B\uFF0C\u5403\u4E86\u4E2D\u836F\uFF0C\u867D\u7136\u6548\u679C\u4ECE\u6765\u6CA1\u4E25\u683C\u8BC1\u5B9E\u8FC7\u3002</p><p>\u76F8\u5173\u9605\u8BFB\uFF1A\u5B69\u5B50\u6CA1\u6709\u5C3F\u5E8A\uFF0C\u4F46\u662F\u5C3F\u9891\u600E\u4E48\u529E\uFF1F\u5173\u6CE8\u8FD9\u4E2A\u8D26\u53F7\u540E\u56DE\u590D76\u53EF\u67E5\u770B\u7B54\u6848\u3002</p>",17),u={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=206661394&idx=1&sn=a826b81477ddb6de718523fafa6e3584&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},b=e("\u67E5\u770B\u539F\u6587");function k(z,N){const r=t("RouterLink"),s=t("ExternalLinkIcon");return a(),c(_,null,[l,p("p",null,[f,n(r,{to:"/s/__biz=MzA5NzE5ODIyOQ==&mid=205260611&idx=1&sn=c131f616a3c77d0ce3d2c4e9455f5de3&scene=21#wechat_redirect/"},{default:d(()=>[m]),_:1}),x]),g,p("p",null,[p("a",u,[b,n(s)])])],64)}var I=i(h,[["render",k]]);export{I as default};
