import{r as a,o as r,c as t,a as e,b as n,F as o,d as c,e as s}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const i={},_=c('<h3 id="\u4F60\u4EE5\u4E3A\u4E16\u4E0A\u53EA\u6709\u4E00\u4E2A\u6768\u6C38\u4FE1" tabindex="-1"><a class="header-anchor" href="#\u4F60\u4EE5\u4E3A\u4E16\u4E0A\u53EA\u6709\u4E00\u4E2A\u6768\u6C38\u4FE1" aria-hidden="true">#</a> \u4F60\u4EE5\u4E3A\u4E16\u4E0A\u53EA\u6709\u4E00\u4E2A\u6768\u6C38\u4FE1\uFF1F</h3><p>\u5173\u4E8E\u5B69\u5B50\u5403\u996D\u95EE\u9898\uFF0C\u5173\u4E8E\u79EF\u98DF\u95EE\u9898\uFF0C\u6211\u5199\u8FC7\u51E0\u7BC7\u6587\u7AE0\uFF0C\u89C9\u5F97\u8BE5\u8BF4\u7684\u90FD\u8BF4\u8FC7\u4E86\uFF0C\u6211\u4E5F\u4E0D\u60F3\u78B0\u4E2D\u533B\u836F\u8FD9\u6837\u7684\u53E3\u6C34\u8BDD\u9898\u3002</p><p>\u4F46\u524D\u51E0\u5929\u63A5\u5230\u4E00\u4E2A\u540C\u5728\u6DF1\u5733\u7684\u672C\u5BB6\u5F1F\u5F1F\u7684\u7535\u8BDD\uFF0C\u8BF4\u5B69\u5B50\u4E0D\u5403\u4E1C\u897F\uFF0C\u53BB\u793E\u5EB7\u770B\u75C5\uFF0C\u533B\u751F\u8BF4\u6709\u75B3\u79EF\uFF0C\u7528\u4E86\u9488\u6311\u7597\u6CD5\u3002</p><p>\u6211\u77E5\u9053\u6240\u8C13\u7684\u9488\u6311\u7597\u6CD5\u662F\u600E\u4E48\u56DE\u4E8B\uFF0C\u60F3\u8C61\u5F97\u5230\u5B69\u5B50\u88AB\u51E0\u4E2A\u5927\u4EBA\u6309\u4F4F\uFF0C\u7136\u540E\u201C\u533B\u751F\u201D\u7528\u9488\u6216\u8005\u5200\u53BB\u624E\u4ED6\u624B\u6307\u6216\u8005\u624B\u638C\uFF0C\u7136\u540E\u628A\u7EC4\u7EC7\u6DB2\u6216\u8005\u76AE\u4E0B\u8102\u80AA\u6324\u51FA\u6765\uFF0C\u5B69\u5B50\u56E0\u4E3A\u75BC\u75DB\u54ED\u5F97\u6495\u5FC3\u88C2\u80BA\uFF0C\u4F7F\u52B2\u6323\u624E\u5374\u65E0\u65E0\u6CD5\u6323\u8131\u7684\u573A\u666F\uFF0C\u8FD9\u5C31\u662F\u6240\u8C13\u7684\u6311\u75B3\u79EF\u3002</p><p>\u56FE\u7247\u6765\u81EA\u7F51\u7EDC</p><p>\u6211\u4EEC\u8FD9\u4E00\u4EE3\u4E0D\u5C11\u4EBA\u5C0F\u65F6\u5019\u63A5\u53D7\u8FC7\u8FD9\u79CD\u201C\u6CBB\u7597\u201D\uFF0C\u539F\u56E0\u662F\u4E2D\u533B\u91CC\u6709\u201C\u4E45\u79EF\u6210\u75B3\u201D\u7684\u8BF4\u6CD5\uFF0C\u800C\u4E2D\u533B\u5BF9\u56FD\u4EBA\u5F71\u54CD\u751A\u5E7F\uFF0C\u4E0D\u597D\u597D\u5403\u996D\u7684\u5B69\u5B50\u5C31\u53EF\u80FD\u88AB\u8BA4\u4E3A\u662F\u79EF\u98DF\u4E86\uFF0C\u79EF\u98DF\u4E45\u4E86\u5C31\u8BF4\u4F60\u957F\u4E86\u75B3\u79EF\uFF0C\u9700\u8981\u53BB\u6311\u75B3\u79EF\u3002</p><p>\u6211\u5728\u4EE5\u524D\u7684\u6587\u7AE0\u91CC\u8BB2\u8FC7\uFF0C\u4E2D\u533B\u8BA4\u4E3A\u5403\u592A\u591A\u4E86\uFF0C\u8D85\u8FC7\u813E\u80C3\u7684\u8FD0\u5316\u80FD\u529B\u4F1A\u5BFC\u81F4\u79EF\u98DF\uFF0C\u800C\u4E2D\u533B\u91CC\u7684\u201C\u813E\u201D\u53C8\u4E0D\u662F\u5B9E\u9645\u7684\u813E\u810F\uFF0C\u800C\u662F\u4E00\u4E2A\u542B\u7CCA\u7684\u6982\u5FF5\uFF0C\u6240\u4EE5\u5230\u5E95\u4EC0\u4E48\u662F\u201C\u79EF\u98DF\u201D\u4E5F\u6CA1\u6709\u660E\u786E\u6807\u51C6\uFF0C\u51E1\u662F\u4E0D\u597D\u89E3\u91CA\u6216\u8005\u6CA1\u80FD\u529B\u89E3\u91CA\u7684\u538C\u98DF\u3001\u6311\u98DF\u3001\u8425\u517B\u4E0D\u826F\u3001\u53D1\u70E7\u3001\u54B3\u55FD\u3001\u8179\u6CFB\u3001\u8179\u75DB\u7B49\u90FD\u53EF\u4EE5\u5F52\u56E0\u4E8E\u201C\u79EF\u98DF\u201D\u3002</p><p>\u548C\u201C\u79EF\u98DF\u201D\u6CA1\u6709\u660E\u786E\u7684\u6807\u51C6\u4E00\u6837\uFF0C\u201C\u75B3\u201D\u7684\u5224\u65AD\u540C\u6837\u6CA1\u6709\u6807\u51C6\uFF0C\u201C\u79EF\u98DF\u201D\u4E45\u4E86\u5C31\u8BF4\u6709\u75B3\u3002\u4EFB\u4F55\u4E00\u4E2A\u5B69\u5B50\u4E0D\u7BA1\u4EC0\u4E48\u539F\u56E0\u5BFC\u81F4\u7684\u8425\u517B\u4E0D\u826F\uFF0C\u5934\u53D1\u7A00\u758F\uFF0C\u6311\u98DF\u504F\u98DF\uFF0C\u90FD\u53EF\u80FD\u88AB\u5BB6\u957F\u81EA\u5DF1\u5224\u65AD\u4E3A\u6709\u75B3\uFF0C\u7136\u540E\u5C31\u88AB\u62C9\u5230\u4E2D\u533B\u54EA\u91CC\u3002</p><p>\u800C\u5728\u4E2D\u533B\u7684\u773C\u91CC\uFF0C\u4EC0\u4E48\u662F\u75B3\uFF0C\u540C\u6837\u6CA1\u6709\u6807\u51C6\uFF0C\u5728\u4E00\u4E9B\u5730\u65B9\uFF0C\u53EA\u8981\u5BB6\u957F\u628A\u5B69\u5B50\u62B1\u8FC7\u6765\uFF0C\u5C31\u6765\u8005\u4E0D\u62D2\uFF0C\u4E00\u6982\u6311\u4E4B\u3002\u4E0D\u8981\u4EE5\u4E3A\u6311\u75B3\u79EF\u90FD\u662F\u4E0D\u6B63\u89C4\u7684\u571F\u90CE\u4E2D\u624D\u4F1A\u505A\uFF0C\u4E09\u7532\u533B\u9662\u7684\u4E2D\u533B\u533B\u751F\u4E00\u6837\u5728\u505A\u3002\u5E7F\u4E1C\u7701\u4E2D\u533B\u9662\u73E0\u6D77\u533B\u9662\u7684\u51E0\u4E2A\u4E13\u5BB6\u8DD1\u5230\u4E00\u4E2A\u5E7C\u513F\u56ED\uFF0C\u7ED3\u679C400\u591A\u4F4D\u5B69\u5B50\u88AB\u6311\u4E86\u75B3\u79EF\u3002</p><p>\u5B69\u5B50\u4E0D\u5403\u996D\uFF0C\u5C31\u6311\u624B\u6307\uFF0C\u6216\u8005\u5272\u624B\u638C\uFF0C\u7406\u8BBA\u4F9D\u636E\u4F9D\u7136\u662F\u4E2D\u533B\u7684\u7ECF\u7EDC\u7A74\u4F4D\u5B66\u8BF4\uFF0C\u7ECF\u7EDC\u7A74\u4F4D\u6D41\u4F20\u4E86\u4E0A\u5343\u5E74\uFF0C\u5230\u4E86\u4ECA\u5929\u4F9D\u7136\u65E0\u6CD5\u88AB\u53D1\u73B0\uFF0C\u4F46\u5E76\u4E0D\u59A8\u4E2D\u533B\u7528\u8FD9\u4E9B\u7406\u8BBA\u6765\u7ED9\u5B69\u5B50\u6311\u624B\u6307\uFF0C\u5272\u624B\u638C\u3002\u4E0B\u9762\u662F\u4E24\u4E2A\u533B\u751F\u8BBA\u6587\u91CC\u63CF\u8FF0\u7684\u5272\u75B3\u65B9\u6CD5\u3002</p><p>\u8FD9\u8BA9\u6211\u60F3\u8D77\u90A3\u4E9B\u56E0\u4E3A\u7ECF\u5E38\u4E0A\u7F51\uFF0C\u88AB\u5BB6\u957F\u8BA4\u4E3A\u6709\u201C\u7F51\u763E\u201D\u9001\u8FDB\u7F51\u6212\u4E2D\u5FC3\uFF0C\u7136\u540E\u88AB\u7ED1\u8D77\u6765\u63A5\u53D7\u201C\u533B\u751F\u201D\u7535\u4F11\u514B\u6CBB\u7597\u7684\u9752\u5C11\u5E74\u3002\u540C\u6837\u662F\u4E00\u6279\u4EBA\u6839\u636E\u81EA\u5DF1\u72EC\u6709\u7684\u7406\u8BBA\uFF0C\u7F16\u7EC7\u51FA\u4E00\u4E2A\u75BE\u75C5\uFF0C\u7136\u540E\u518D\u7ED9\u4E00\u6279\u4EBA\u505A\u5F3A\u5236\u6027\u7684\u6CBB\u7597\u3002</p><p>\u4F60\u5982\u679C\u8BF4\u201C\u75B3\u79EF\u201D\u662F\u4E00\u4E9B\u4EBA\u60F3\u8C61\u51FA\u6765\u7684\u75BE\u75C5\uFF0C\u7ECF\u7EDC\u7A74\u4F4D\u662F\u4E0D\u5B58\u5728\u7684\uFF0C\u7528\u9488\u6311\u5B69\u5B50\u624B\u6307\uFF0C\u7528\u5200\u5272\u5B69\u5B50\u7684\u624B\u638C\uFF0C\u662F\u5728\u7528\u6CBB\u75C5\u7684\u540D\u4E49\u4F24\u5BB3\u5B69\u5B50\u3002\u5F88\u591A\u4EBA\u4F1A\u8BF4\uFF1A\u5B58\u5728\u5C31\u662F\u5408\u7406\u7684\uFF0C\u4F60\u770B\u4E0D\u89C1\u5C31\u4E0D\u7B49\u4E8E\u4E0D\u5B58\u5728\uFF0C\u4F60\u8BF4\u4E0D\u5B58\u5728\u4F60\u62FF\u51FA\u8BC1\u636E\u6765\u554A......\u6211\u4E0D\u77E5\u9053\u8FD9\u4E9B\u4EBA\u5982\u679C\u88AB\u6768\u6C38\u4FE1\u5F53\u7F51\u763E\u60A3\u8005\u62C9\u53BB\u505A\u7535\u4F11\u514B\u7684\u65F6\u5019\u4F1A\u4E0D\u4F1A\u53CD\u6297\u3002</p><p>\u8FD8\u6709\u4EBA\u53EF\u80FD\u8981\u8BF4\uFF0C\u5F88\u591A\u5B69\u5B50\u5272\u5B8C\u4E4B\u540E\u771F\u7684\u98DF\u6B32\u597D\u4E86\u554A\u3002\u6211\u4E5F\u76F8\u4FE1\u6311\u75B3\u79EF\u771F\u7684\u53EF\u80FD\u53EF\u4EE5\u8BA9\u5B69\u5B50\u591A\u5403\u996D\uFF0C\u4F46\u8FD9\u4E0D\u662F\u6311\u5230\u4E86\u7A74\u4F4D\u8D77\u4E86\u4F5C\u7528\uFF0C\u800C\u662F\u5728\u6311\u624B\u6307\u5272\u624B\u638C\u4EA7\u751F\u7684\u75BC\u75DB\u548C\u6050\u60E7\uFF0C\u8BA9\u5B69\u5B50\u4E0D\u6562\u4E0D\u5403\u3002</p><p>\u4E0A\u7F51\u5C31\u7ED1\u8D77\u6765\u7535\uFF0C\u4F60\u662F\u9009\u62E9\u4E0A\u7F51\u8FD8\u662F\u9009\u62E9\u88AB\u7535\u5462\uFF1F\u4E0D\u5403\u996D\u5C31\u62C9\u53BB\u5272\u624B\u638C\uFF0C\u4F60\u662F\u9009\u62E9\u5403\u996D\u8FD8\u662F\u9009\u62E9\u5272\u624B\u638C\u5462\uFF1F\u6B63\u5982\u4E00\u4E2A\u5FAE\u535A\u7F51\u53CB\u8F6C\u8FF0\u81EA\u5DF1\u5988\u5988\u7684\u8BDD\uFF1A\u201C\u4E0D\u597D\u597D\u5403\u996D\u518D\u5E26\u4F60\u53BB\u6311\u75B3\u79EF\u201D\uFF0C\u4F30\u8BA1\u5927\u4FBF\u90FD\u80FD\u5403\u4E0B\u53BB\u3002\u201D</p><p>\u5B69\u5B50\u4E0D\u5403\u4E1C\u897F\uFF0C\u8425\u517B\u4E0D\u826F\uFF0C\u6709\u5C11\u90E8\u5206\u662F\u5B58\u5728\u6D88\u5316\u9053\u3001\u4EE3\u8C22\u3001\u7CBE\u795E\u5FC3\u7406\u65B9\u9762\u7684\u95EE\u9898\uFF0C\u62C5\u5FC3\u6709\u8FD9\u4E9B\u95EE\u9898\u7684\u5E94\u8BE5\u597D\u597D\u53BB\u627E\u4E13\u79D1\u533B\u751F\u53BB\u505A\u68C0\u67E5\uFF0C\u518D\u505A\u9488\u5BF9\u6027\u6CBB\u7597\uFF0C\u548C\u4E2D\u533B\u7406\u8BBA\u4E0D\u4E00\u6837\u7684\u662F\uFF0C\u73B0\u4EE3\u533B\u5B66\u5BF9\u75BE\u75C5\u7684\u8BCA\u65AD\u4F1A\u6709\u5BA2\u89C2\u548C\u660E\u786E\u7684\u4F9D\u636E\u548C\u6807\u51C6\u3002</p><p>\u4F46\u5B69\u5B50\u4E0D\u597D\u597D\u5403\u996D\uFF0C\u66F4\u591A\u7684\u662F\u56E0\u4E3A\u5BB6\u957F\u5BF9\u5B69\u5B50\u5403\u4E1C\u897F\u6709\u4E0D\u5207\u5B9E\u9645\u7684\u671F\u671B\uFF0C\u603B\u62C5\u5FC3\u5B69\u5B50\u6CA1\u5403\u591F\u5403\u9971\uFF0C\u8F93\u5728\u8D77\u8DD1\u7EBF\u4E0A\uFF0C\u6240\u4EE5\u6574\u5929\u4E00\u52FA\u52FA\u586B\u9E2D\u5F0F\u5582\u996D\uFF0C\u5BFC\u81F4\u5B69\u5B50\u5BF9\u5403\u996D\u4EA7\u751F\u4E86\u538C\u70E6\u548C\u6050\u60E7\uFF0C\u672C\u6765\u662F\u5BB6\u957F\u81EA\u5DF1\u7684\u95EE\u9898\uFF0C\u5374\u8BF4\u5B69\u5B50\u6709\u75B3\u79EF\uFF0C\u7136\u540E\u62C9\u53BB\u6311\u3001\u5272\u624B\u3002</p><p>\u63A5\u53D7\u7F51\u763E\u8FD9\u4E2A\u8BCA\u65AD\u7684\u533B\u751F\u5F88\u5C11\uFF0C\u4E00\u4E2A\u6768\u6C38\u4FE1\u53EF\u4EE5\u5173\u4E00\u6279\u5B69\u5B50\u8FDB\u884C\u7535\u7597\uFF0C\u4F46\u63A5\u53D7\u4E2D\u533B\u836F\u7406\u8BBA\u7684\u5BB6\u957F\u548C\u533B\u751F\u592A\u591A\u592A\u591A\uFF0C\u4E00\u4EE3\u4EE3\u7684\u5B69\u5B50\uFF0C\u88AB\u81EA\u5DF1\u6700\u4FE1\u4EFB\u7684\u4EBA\u9001\u53BB\u4E2D\u533B\u90A3\u91CC\u63A5\u53D7\u9177\u5211\u4E00\u822C\u7684\u201C\u6CBB\u7597\u201D\uFF0C\u5F88\u591A\u4EBA\u624B\u4E0A\u4F9D\u7136\u6B8B\u7559\u7740\u5C0F\u65F6\u5019\u88AB\u5272\u75B3\u79EF\u7559\u4E0B\u7684\u75A4\u75D5\u3002\u4F60\u4EE5\u4E3A\u4E16\u4E0A\u53EA\u6709\u4E00\u4E2A\u6768\u6C38\u4FE1\uFF1F</p><p>\u56FE\u7247\u6765\u81EA\u5FAE\u535A\u7F51\u53CB</p><p>\u793E\u4F1A\u7684\u53D1\u5C55\uFF0C\u4E00\u5EA6\u8BA9\u6211\u4EE5\u4E3A\u6311\u75B3\u79EF\u8FD9\u79CD\u8352\u86EE\u7684\u505A\u6CD5\u5DF2\u7ECF\u79BB\u6211\u4EEC\u8FDC\u53BB\uFF0C\u6216\u8005\u53EA\u5B58\u5728\u4E00\u4E9B\u504F\u8FDC\u843D\u540E\u7684\u5C71\u6751\uFF0C\u4F46\u8FD9\u6B21\u53D1\u751F\u5728\u6DF1\u5733\u8FD9\u6837\u7684\u73B0\u4EE3\u5316\u5927\u90FD\u5E02\uFF0C\u53D1\u751F\u5728\u81EA\u5DF1\u4EB2\u4EBA\u5B69\u5B50\u7684\u8EAB\u4E0A\uFF0C\u8BA9\u6211\u77E5\u9053\u5B83\u4F9D\u7136\u50CF\u5E7D\u9B42\u4E00\u6837\u5371\u5BB3\u7740\u793E\u4F1A\uFF0C\u5305\u62EC\u81EA\u5DF1\u8EAB\u8FB9\u7684\u4EBA\u3002\u6240\u4EE5\u54EA\u6015\u88AB\u55B7\u518D\u591A\u7684\u53E3\u6C34\uFF0C\u6211\u8FD8\u662F\u8981\u5199\u4E0B\u8FD9\u7BC7\u6587\u7AE0\uFF0C\u4E5F\u5E0C\u671B\u66F4\u591A\u7684\u4EBA\u8BFB\u5230\u5B83\uFF0C\u8BA9\u53D7\u8FD9\u79CD\u611A\u6627\u4E4B\u4F24\u7684\u5B69\u5B50\u66F4\u5C11\u4E00\u70B9\u3002</p><hr><p>\u9898\u56FE\u6765\u81EAPixabay</p><p>\u56DE\u590D\u201C**\u79EF\u98DF\u201D**\u67E5\u770B\u79EF\u98DF\u76F8\u5173\u6587\u7AE0</p><p>\u56DE\u590D\u201C<strong>\u5403\u996D</strong>\u201D\u67E5\u770B\u5982\u4F55\u57F9\u517B\u5B69\u5B50\u81EA\u4E3B\u8FDB\u98DF</p>',23),l={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651626330&idx=1&sn=bea67fd1d596e1843334c20426a88f63&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},h=s("\u67E5\u770B\u539F\u6587");function m(f,x){const p=a("ExternalLinkIcon");return r(),t(o,null,[_,e("p",null,[e("a",l,[h,n(p)])])],64)}var g=d(i,[["render",m]]);export{g as default};
