import{r as o,o as r,c,a as e,b as a,w as s,F as d,d as i,e as t}from"./app.9aaa2256.js";import{_}from"./plugin-vue_export-helper.21dcd24c.js";const h={},l=i('<h3 id="\u5982\u4F55\u627E\u5230\u9760\u8C31\u7684\u80B2\u513F\u4FE1\u606F" tabindex="-1"><a class="header-anchor" href="#\u5982\u4F55\u627E\u5230\u9760\u8C31\u7684\u80B2\u513F\u4FE1\u606F" aria-hidden="true">#</a> \u5982\u4F55\u627E\u5230\u9760\u8C31\u7684\u80B2\u513F\u4FE1\u606F</h3><p>\u4E92\u8054\u7F51\u7ED9\u5927\u5BB6\u63D0\u4F9B\u4E86\u5927\u91CF\u7684\u8D44\u8BAF\uFF0C\u4E5F\u5305\u62EC\u533B\u5B66\u4FE1\u606F\u3002\u6709\u5065\u5EB7\u95EE\u9898\uFF0C\u5148\u767E\u5EA6\u4E00\u4E0B\uFF0C\u6216\u8005\u4E0A\u7F51\u627E\u4E2A\u533B\u751F\u95EE\u95EE\u6210\u4E3A\u5F88\u591A\u4EBA\u7684\u505A\u6CD5\uFF0C\u4F30\u8BA1\u5FAE\u535A\u5FAE\u4FE1\u4E0A\u5F88\u591A\u533B\u751F\u8DDF\u6211\u4E00\u6837\uFF0C\u6BCF\u5929\u4F1A\u5728\u8BC4\u8BBA\u91CC\uFF0C\u79C1\u4FE1\u91CC\u6536\u5230\u5927\u91CF\u7684\u5065\u5EB7\u54A8\u8BE2\u3002</p><p>\u4E0D\u53EF\u5426\u8BA4\u7684\u662F\uFF0C\u7F51\u7EDC\u4E3A\u5927\u5BB6\u83B7\u53D6\u533B\u5B66\u4FE1\u606F\u63D0\u4F9B\u4E86\u4E00\u4E2A\u4FBF\u6377\u5FEB\u901F\u7684\u65B9\u5F0F\uFF0C\u4E00\u4E9B\u9760\u8C31\u7684\u533B\u5B66\u6587\u7AE0\uFF0C\u89E3\u51B3\u4E86\u5F88\u591A\u4EBA\u5FC3\u4E2D\u7684\u56F0\u60D1\uFF0C\u8FD9\u4E5F\u662F\u533B\u5B66\u79D1\u666E\u5B58\u5728\u7684\u610F\u4E49\u3002\u627E\u5230\u4E2A\u9760\u8C31\u533B\u751F\uFF0C\u5728\u624B\u673A\u4E0A\u7F51\u7EDC\u95EE\u8BCA\uFF0C\u4E0D\u7528\u8DD1\u533B\u9662\uFF0C\u4E0D\u8981\u6392\u957F\u961F\uFF0C\u4E0D\u7528\u5FCD\u53D7\u533B\u9662\u91CC\u5608\u6742\u55A7\u95F9\u7684\u73AF\u5883\u5C31\u89E3\u51B3\u4E86\u56F0\u6270\u5DF2\u4E45\u7684\u95EE\u9898\uFF0C\u8FD9\u4E5F\u662F\u8FD1\u4E9B\u5E74\u201C\u79FB\u52A8\u533B\u7597\u201C\u5982\u706B\u5982\u837C\u7684\u539F\u56E0\u3002</p><p>\u4F46\u7F51\u7EDC\u4FE1\u606F\u6D69\u5982\u70DF\u6D77\u4E14\u6CE5\u6C99\u4FF1\u4E0B\uFF0C\u641C\u7D22\u4E00\u4E2A\u75BE\u75C5\uFF0C\u5F97\u5230\u7684\u53EF\u80FD\u4E0D\u662F\u6B63\u786E\u7684\u533B\u5B66\u77E5\u8BC6\uFF0C\u800C\u662F\u4E00\u4E9B\u533B\u836F\u5E7F\u544A\uFF0C\u751A\u81F3\u4F2A\u88C5\u6210\u79D1\u666E\u7684\u5E7F\u544A\uFF0C\u7136\u540E\u4F60\u88AB\u4E00\u8DEF\u8DEF\u5F15\u5165\u533B\u7597\u9677\u9631\u91CC\uFF0C\u5728\u7F51\u4E0A\u63D0\u4E2A\u95EE\uFF0C\u53EF\u80FD\u4E00\u5806\u201C\u597D\u5FC3\u201D\u7684\u4E13\u5BB6\u56F4\u8FC7\u6765\u70ED\u5FC3\u5E2E\u4F60\u89E3\u7B54\uFF0C\u987A\u5E26\u63A8\u8350\u54EA\u5BB6\u533B\u9662\u6216\u8005\u54EA\u4E2A\u4EA7\u54C1\uFF0C\u7136\u540E\u88AB\u9A97\u5F97\u4EBA\u8D22\u4E24\u7A7A\u7684\u4E5F\u4E0D\u5C11\u89C1\u3002\u6240\u4EE5\uFF0C\u7F51\u7EDC\u867D\u7136\u4FBF\u6377\u65B9\u4FBF\uFF0C\u4F46\u8981\u5230\u627E\u5230\u9760\u8C31\u7684\u533B\u5B66\u4FE1\u606F\u5374\u5E76\u4E0D\u90A3\u4E48\u5BB9\u6613\uFF0C\u5982\u4F55\u624D\u80FD\u505A\u5230\u5462\uFF1F</p><p>\u5982\u679C\u4F60\u82F1\u8BED\u597D\uFF0C\u90A3\u5C31\u4F1A\u65B9\u4FBF\u5F88\u591A\uFF0C\u73B0\u4EE3\u533B\u5B66\u53D1\u6E90\u4E8E\u897F\u65B9\uFF0C\u5C31\u76EE\u524D\u800C\u8A00\uFF0C\u5728\u533B\u5B66\u9886\u57DF\uFF0C\u8981\u67E5\u770B\u66F4\u65B0\u66F4\u6743\u5A01\u7684\u4FE1\u606F\uFF0C\u8FD8\u662F\u5F97\u53BB\u82F1\u8BED\u8D44\u8BAF\u91CC\u627E\uFF0C\u540C\u4E00\u4E2A\u95EE\u9898\uFF0C\u7528\u82F1\u6587\u5173\u952E\u8BCD\u5728Google\u91CC\u641C\u548C\u4E2D\u6587\u5173\u952E\u8BCD\u5728\u767E\u5EA6\u91CC\u641C\uFF0C\u7ED3\u679C\u4F1A\u662F\u4E91\u6CE5\u4E4B\u522B\u3002</p><p>\u5982\u679C\u68C0\u7D22\u80FD\u529B\u66F4\u5F3A\uFF0C\u4E5F\u53EF\u4EE5\u76F4\u63A5\u4E0A\u7F8E\u56FD\u56FD\u7ACB\u533B\u5B66\u56FE\u4E66\u9986\u63D0\u4F9B\u7684Pubmed\uFF08http://www.ncbi.nlm.nih.gov/pubmed/\uFF09\uFF0C\u533B\u5B66\u65B9\u9762\u7684\u8BBA\u6587\u8FD9\u4E2A\u5F15\u64CE\u662F\u51E0\u4E4E\u662F\u6700\u5168\u9762\u7684\uFF0C\u4F60\u53EF\u4EE5\u6839\u636E\u68C0\u7D22\u7ED3\u679C\u627E\u5230\u5F88\u591A\u76F8\u5173\u7684\u6700\u65B0\u8BBA\u6587\uFF0C\u4F60\u4E5F\u53EF\u4EE5\u770B\u5230\u5BF9\u67D0\u4E2A\u95EE\u9898\u7684\u7814\u7A76\u7EFC\u8FF0\uFF0C\u4ECE\u800C\u4E86\u89E3\u67D0\u4E2A\u95EE\u9898\u7684\u6700\u65B0\u8FDB\u5C55\u3002\u4F46Pubmed\u7684\u641C\u7D22\u7ED3\u679C\u4ECD\u7136\u5F88\u7E41\u6742\uFF0C\u5BF9\u4E8E\u7EDD\u5927\u90E8\u5206\u5BB6\u957F\u6765\u8BF4\u5E76\u6CA1\u6709\u8FD9\u4E2A\u80FD\u529B\u4E5F\u6CA1\u6709\u8FD9\u4E2A\u5FC5\u8981\u53BB\u4E86\u89E3\u5230\u90A3\u4E48\u6DF1\u5165\u3002</p><p>\u5C31\u513F\u79D1\u77E5\u8BC6\u800C\u8A00\uFF0C\u7F8E\u56FD\u513F\u79D1\u5B66\u4F1A\uFF08AAP\uFF09\u7684\u5B98\u7F51\uFF1Ahttp://www.aap.org \u7528\u75BE\u75C5\u7684\u5173\u952E\u8BCD\u53EF\u4EE5\u641C\u7D22\u5230\u5F88\u591A\u76F8\u5173\u4FE1\u606F\uFF0C\u5728Advocacy&amp; Policy\u91CC\u53EF\u4EE5\u641C\u5230AAP\u7684\u75BE\u75C5\u6307\u5357\uFF0C\u57FA\u672C\u90FD\u662F\u5168\u6587\u514D\u8D39\u7684\u3002\u8FD9\u4E9B\u77E5\u8BC6\u5BF9\u4E8E\u513F\u79D1\u533B\u751F\u6765\u8BF4\u5F88\u6709\u9488\u5BF9\u6027\uFF0C\u5BB6\u957F\u4EEC\u5982\u679C\u89C9\u5F97\u81EA\u5DF1\u6709\u80FD\u529B\u8BFB\u61C2\u4E5F\u53EF\u4EE5\u770B\u770B\u3002</p><p>\u4F46\u4E13\u4E1A\u8BBA\u6587\u548C\u4E34\u5E8A\u6307\u5357\u5BF9\u5927\u90E8\u5206\u5BB6\u957F\u6765\u8BF4\u90FD\u6BD4\u8F83\u96BE\uFF0C\u8BFB\u8D77\u6765\u4F1A\u5F88\u8D39\u529B\uFF0C\u76F4\u63A5\u770B\u9762\u5411\u5BB6\u957F\u4EEC\u7684\u80B2\u513F\u79D1\u666E\u4F1A\u66F4\u65B9\u4FBF\u3002\u8FD9\u7C7B\u7F51\u7AD9\u9996\u63A8\u7684\u5C31\u662FAAP\u65D7\u4E0B\u7684\u79D1\u666E\u7F51\u7AD9childrenhealthy.org,\u91CC\u9762\u65E2\u6709\u6309\u5E74\u9F84\u548C\u9636\u6BB5\u5217\u51FA\u7684\u95EE\u9898\uFF0C\u4E5F\u6709\u6309\u75BE\u75C5\u90E8\u4F4D\u548C\u540D\u79F0\u5217\u51FA\u7684\u75C5\u75C7\uFF0C\u5F53\u7136\u4E5F\u53EF\u4EE5\u7528\u5173\u952E\u8BCD\u5728\u641C\u7D22\u680F\u91CC\u641C\u7D22\uFF0C\u800C\u4E14\u8FD9\u4E9B\u6587\u7AE0\u90FD\u662F\u9762\u5BF9\u5BB6\u957F\u7684\uFF0C\u7528\u7B80\u5355\u6613\u61C2\u7684\u8BED\u8A00\u8868\u8FF0\u5F97\u5F88\u6E05\u6670\uFF0C\u800C\u4E14\u5185\u5BB9\u53EF\u9760\uFF0C\u5728\u4E0A\u9762\u627E\u5230\u4E86\u7B54\u6848\u4E0D\u9700\u8981\u518D\u53BB\u522B\u5904\u53CD\u590D\u8003\u8BC1\uFF0C\u662F\u6700\u503C\u5F97\u63A8\u8350\u7684\uFF0C\u7F3A\u70B9\u662F\u91CC\u9762\u63D0\u5230\u7684\u4E00\u4E9B\u836F\u7269\u548C\u75AB\u82D7\u56FD\u5185\u6CA1\u6709\u6216\u8005\u4E0D\u4E00\u6837\uFF0C\u6709\u4E9B\u75BE\u75C5\u4E1C\u897F\u65B9\u4EBA\u79CD\u8FD8\u662F\u5B58\u5728\u4E00\u4E9B\u5DEE\u5F02\uFF0C\u4E0D\u80FD\u5B8C\u5168\u7167\u642C\uFF0C\u53E6\u5916\u5F88\u591A\u4E2D\u56FD\u7279\u8272\u7684\u80B2\u513F\u95EE\u9898\u91CC\u9762\u76F8\u5173\u5185\u5BB9\u4F1A\u6BD4\u8F83\u5C11\u3002</p><p>\u6B64\u5916\u8FD8\u6709\u52A0\u62FF\u5927\u513F\u79D1\u534F\u4F1A\u65D7\u4E0B\u9762\u5BF9\u7236\u6BCD\u4EEC\u7684Caring for Kids\uFF08http://www.caringforkids.cps.ca\uFF09\u867D\u7136\u5185\u5BB9\u4E0D\u5982\u524D\u8005\u90A3\u4E48\u4E30\u5BCC\uFF0C\u4F46\u8FD9\u4E9B\u6743\u5A01\u513F\u79D1\u673A\u6784\u63D0\u4F9B\u7684\u77E5\u8BC6\u90FD\u662F\u975E\u5E38\u9760\u8C31\uFF0C\u4E5F\u662F\u503C\u5F97\u63A8\u8350\u3002WebMD\uFF08www.webMD.com)\u662F\u4E00\u4E2A\u975E\u5E38\u503C\u5F97\u63A8\u8350\u7684\u7F51\u7AD9\uFF0C\u91CC\u9762\u6709\u4E30\u5BCC\u7684\u533B\u7597\u5065\u5EB7\u77E5\u8BC6\uFF0C\u5728FAMILY&amp;PREGNANCY \u91CC\u6709\u4E5F\u6709\u4E0D\u5C11\u5411\u5B69\u5B50\u7236\u6BCD\u7684\u77E5\u8BC6\u3002</p><p>\u5982\u679C\u82F1\u8BED\u4E0D\u592A\u597D\uFF0C\u8BFB\u8FD9\u4E9B\u7F51\u7AD9\u4ECD\u7136\u6709\u56F0\u96BE\uFF0C\u90A3\u5C31\u9700\u8981\u5728\u4E2D\u6587\u4E16\u754C\u91CC\u5BFB\u627E\u4E86\uFF0C\u4E2D\u6587\u7684\u597D\u5904\u662F\u9605\u8BFB\u65B9\u4FBF\uFF0C\u4F46\u574F\u5904\u662F\u9760\u8C31\u7684\u5185\u5BB9\u6BD4\u8F83\u8D2B\u4E4F\uFF0C\u865A\u5047\u5BA3\u4F20\u7684\u5E7F\u544A\u592A\u591A\uFF0C\u800C\u4E14\u9632\u4E0D\u80DC\u9632\uFF0C\u627E\u8D77\u6765\u6BD4\u8F83\u8D39\u529B\u4F46\u8FD8\u662F\u6709\u4E00\u4E9B\uFF0C\u6BD4\u5982\u9999\u6E2F\u536B\u751F\u7F72\u7684\u5BB6\u5EAD\u5065\u5EB7\u670D\u52A1\uFF08http://www.fhs.gov.hk/\uFF09\u91CC\u6709\u4E00\u4E9B\uFF0C\u4F46\u4E5F\u662F\u5185\u5BB9\u504F\u5C11\u3002</p><p>\u56E0\u4E3A\u5B58\u5728\u592A\u591A\u7684\u6C11\u8425\u533B\u9662\u6536\u8D39\u63A8\u5E7F\uFF0C\u4E2A\u4EBA\u4E0D\u63A8\u8350\u4F7F\u7528\u767E\u5EA6\u641C\u7D22\uFF0C\u76F8\u53CD\u5012\u662F\u53EF\u4EE5\u4E0A\u597D\u5927\u592B\u7F51\u7AD9\uFF08www.haodf.com\uFF09\u7684\u77E5\u8BC6\u5E93\u91CC\u641C\u7D22\u8BD5\u8BD5\uFF0C\u91CC\u9762\u5927\u591A\u662F\u533B\u751F\u5199\u7684\u79D1\u666E\u6587\u7AE0\uFF0C\u5185\u5BB9\u4F1A\u6BD4\u767E\u5EA6\u5230\u7684\u9760\u8C31\u5F88\u591A\uFF0C\u53EF\u4EE5\u6311\u9009\u5927\u4E00\u70B9\u7684\u533B\u9662\u533B\u751F\u7684\u6587\u7AE0\uFF0C\u5185\u5BB9\u53EF\u9760\u7684\u6982\u7387\u4F1A\u66F4\u5927\uFF0C\u800C\u4E14\u8FD8\u53EF\u4EE5\u5728\u4E0A\u9762\u54A8\u8BE2\u3002</p><p>\u56FD\u5185\u5B98\u65B9\u79D1\u666E\u505A\u5F97\u5B9E\u5728\u592A\u4E0D\u5165\u6D41\uFF0C\u6240\u4EE5\u7ED9\u4E86\u6C11\u95F4\u79D1\u666E\u5F88\u5927\u7684\u7A7A\u95F4\uFF0C\u800C\u4E14\u786E\u5B9E\u6709\u4E9B\u505A\u5F97\u5F88\u4E0D\u9519\u7684\u673A\u6784\uFF0C\u6BD4\u5982\u679C\u58F3\u7F51\uFF0C\u4F46\u4ED6\u4EEC\u7684\u5185\u5BB9\u662F\u6CDB\u79D1\u5B66\u7684\uFF0C\u4E0D\u592A\u4E13\u6CE8\u4E8E\u533B\u5B66\uFF0C\u7C7B\u4F3C\u7684\u7F51\u7AD9\u8FD8\u6709\u79D1\u5B66\u516C\u56ED\u3002\u533B\u836F\u4E13\u4E1A\u516C\u53F8\u4E01\u9999\u56ED\u4EE5\u5F80\u6BD4\u8F83\u6CE8\u91CD\u505A\u9762\u5411\u533B\u751F\u7684\u7684\u4E13\u4E1A\u5185\u5BB9\uFF0C\u53BB\u5E74\u5F00\u59CB\u5728\u9762\u5411\u5927\u4F17\u7684\u533B\u5B66\u79D1\u666E\u4E0A\u53D1\u529B\u4E86\uFF0C\u5178\u578B\u7684\u4EA7\u54C1\u5C31\u662F\u4E01\u9999\u533B\u751F\uFF0C\u82B1\u4E86\u4E0D\u5C11\u94B1\u8BF7\u4E13\u4E1A\u533B\u751F\u6765\u5199\u533B\u5B66\u79D1\u666E\uFF0C\u5185\u5BB9\u5728APP\u548C\u5FAE\u4FE1\u516C\u4F17\u53F7\u540C\u6B65\u63A8\u9001\uFF0C\u975E\u5E38\u503C\u5F97\u5173\u6CE8\uFF0C\u4F46\u7F3A\u70B9\u662F\u5185\u5BB9\u662F\u6CDB\u533B\u5B66\u7684\uFF0C\u800C\u4E0D\u662F\u4E13\u95E8\u9762\u5411\u5BB6\u957F\u7684\u80B2\u513F\u77E5\u8BC6\uFF0C\u540E\u6765\u4ED6\u4EEC\u8FD0\u8425\u53E6\u5916\u4E00\u4E2A\u5FAE\u4FE1\u516C\u4F17\u53F7\u4E01\u9999\u5988\u5988\uFF0C\u63A8\u8350\u5173\u6CE8\u3002</p><p>\u81F3\u4E8E\u53E6\u5916\u4E00\u4E2A\u4EBA\u6C14\u7B97\u4E0D\u9519\u7684\u533B\u5B66\u754C\u513F\u79D1\u9891\u9053\uFF0C\u6CA1\u6709\u4EC0\u4E48\u539F\u521B\u5185\u5BB9\uFF0C\u5185\u5BB9\u5927\u591A\u662F\u5230\u5904\u642C\u8FD0\u8FC7\u6765\uFF0C\u5B9A\u4F4D\u4E5F\u6A21\u7CCA\uFF0C\u6709\u7684\u9762\u5411\u4E13\u4E1A\u4EBA\u7FA4\uFF0C\u6709\u7684\u9762\u5411\u666E\u901A\u4EBA\u7FA4\uFF0C\u800C\u4E14\u65F6\u4E0D\u65F6\u63A8\u9001\u4E00\u4E24\u7BC7\u8F6F\u6587\uFF0C\u6CA1\u6709\u8FA8\u8BC6\u80FD\u529B\u4F1A\u88AB\u8BEF\u5BFC\uFF0C\u4E2A\u4EBA\u4E0D\u63A8\u8350\u5173\u6CE8\u3002</p><p>\u548C\u673A\u6784\u76F8\u6BD4\uFF0C\u4E2A\u4EBA\u79D1\u666E\u7684\u529B\u91CF\u4E5F\u4E0D\u5BB9\u5C0F\u89D1\uFF0C\u6B63\u662F\u56E0\u4E3A\u56FD\u5185\u513F\u79D1\u533B\u7597\u670D\u52A1\u7684\u843D\u540E\uFF0C\u7ED9\u4E86\u4E2A\u4EBA\u80B2\u513F\u79D1\u666E\u5DE8\u5927\u7684\u7A7A\u95F4\uFF0C\u5728\u5FAE\u535A\u5FAE\u4FE1\u4E0A\uFF0C\u548C\u533B\u7597\u6709\u5173\u7684\u4EBA\u6C14\u6BD4\u8F83\u65FA\u7684\u9664\u4E86\u8425\u517B\u6050\u6015\u5C31\u662F\u80B2\u513F\u4E86\uFF0C\u6709\u5F88\u591A\u4EBA\u6C14\u975E\u5E38\u9AD8\u7684\u533B\u751F\uFF0C\u4E2A\u4EBA\u7684\u89C2\u611F\u662F\u8001\u4E00\u8F88\u4EBA\u4E0D\u592A\u6CE8\u91CD\u8BC1\u636E\uFF0C\u6240\u4EE5\u5E38\u5E38\u4F1A\u8BF4\u4E9B\u60F3\u5F53\u7136\u7684\u8BDD\uFF0C\u66F4\u5E74\u8F7B\u7684\u90A3\u4E00\u6279\u80B2\u513F\u79D1\u666E\u4F5C\u8005\u66F4\u6CE8\u91CD\u5FAA\u8BC1\uFF0C\u8BF4\u8BDD\u6BD4\u8F83\u6709\u8BC1\u636E\u3002</p><p>\u54EA\u4E9B\u4EBA\u503C\u5F97\u5173\u6CE8\u5927\u5BB6\u53EF\u80FD\u4F1A\u6709\u81EA\u5DF1\u7684\u5224\u65AD\uFF0C\u6211\u5C31\u4E0D\u518D\u7279\u522B\u63A8\u8350\u4E86\uFF0C\u65E0\u8BBA\u662F\u770B\u8C01\u7684\u89C2\u70B9\uFF0C\u90FD\u9700\u8981\u6709\u81EA\u5DF1\u7684\u601D\u7EF4\uFF0C\u591A\u770B\u770B\u8BC1\u636E\uFF0C\u591A\u60F3\u60F3\u5176\u4E2D\u7684\u903B\u8F91\uFF0C\u4E0D\u80FD\u8FF7\u4FE1\u4EFB\u4F55\u4EBA\u3002\u5982\u679C\u8BA4\u4E3A\u81EA\u5DF1\u4E0D\u5177\u5907\u5224\u65AD\u80FD\u529B\uFF0C\u8FD8\u662F\u591A\u770B\u770B\u6743\u5A01\u673A\u6784\u7684\u610F\u89C1\uFF0C\u6BD4\u5982\u7F8E\u56FD\u513F\u79D1\u5B66\u4F1A\uFF0C\u63A8\u8350\u6BCF\u4E2A\u6709\u5B69\u5B50\u7684\u5BB6\u5EAD\u4E70\u4E00\u672C\u300A\u7F8E\u56FD\u513F\u79D1\u5B66\u4F1A\u80B2\u513F\u767E\u79D1\u300B\uFF08\u76EE\u524D\u4E3A\u7B2C\u516D\u7248\uFF0C\u4E2D\u6587\u7248\u76EE\u524D\u53EA\u6709\u7B2C\u4E94\u7248\uFF09\uFF0C\u5982\u679C\u770B\u4E86\u8FD9\u672C\u4E66\uFF0C\u4E5F\u5C31\u4E0D\u4F1A\u4E3A\u5B69\u5B50\u80FD\u4E0D\u80FD\u7AD6\u62B1\u8FD9\u6837\u7684\u95EE\u9898\u56F0\u6270\u8FD9\u4E48\u4E45\u4E86\u3002</p>',15),w=t("\u76F8\u5173\u9605\u8BFB\uFF1A"),m=t("\u79FB\u52A8\u533B\u7597\u7684\u6625\u5929\uFF1F"),f={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=207968338&idx=1&sn=d3158da033f21101c09df56d03810549&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},u=t("\u67E5\u770B\u539F\u6587");function x(b,A){const p=o("RouterLink"),n=o("ExternalLinkIcon");return r(),c(d,null,[l,e("p",null,[w,a(p,{to:"/s/__biz=MzA5NzE5ODIyOQ==&mid=205824845&idx=1&sn=59d80e3110746591f12256b328a5e1e8&scene=21#wechat_redirect/"},{default:s(()=>[m]),_:1})]),e("p",null,[e("a",f,[u,a(n)])])],64)}var P=_(h,[["render",x]]);export{P as default};
