import{r as a,o as c,c as r,a as e,b as t,F as n,d as o,e as s}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const i={},_=o('<h3 id="\u5B9D\u5B9D\u9EC4\u75B8-\u600E\u4E48\u5E94\u5BF9" tabindex="-1"><a class="header-anchor" href="#\u5B9D\u5B9D\u9EC4\u75B8-\u600E\u4E48\u5E94\u5BF9" aria-hidden="true">#</a> \u5B9D\u5B9D\u9EC4\u75B8\uFF0C\u600E\u4E48\u5E94\u5BF9\uFF1F</h3><p>\u5BF9\u4E8E\u9EC4\u75B8\uFF0C\u5F88\u591A\u5BB6\u957F\u90FD\u4E0D\u964C\u751F\uFF0C\u8FD9\u4E24\u4E2A\u5B57\u4E5F\u53EF\u80FD\u662F\u5F88\u591A\u7236\u6BCD\u7684\u5669\u68A6\u3002\u5B69\u5B50\u51FA\u751F\u6CA1\u591A\u4E45\uFF0C\u5C31\u53D1\u73B0\u76AE\u80A4\u548C\u773C\u73E0\u53D1\u9EC4\uFF0C\u7136\u540E\u533B\u751F\u8BF4\u5B69\u5B50\u6709\u9EC4\u75B8\u9700\u8981\u5403\u836F\u6216\u8005\u4F4F\u9662\u7167\u5149\u3002</p><p>\u5341\u6708\u6000\u80CE\uFF0C\u5404\u79CD\u62C5\u5FC3\u53D7\u6015\uFF0C\u597D\u4E0D\u5BB9\u6613\u89C1\u5230\u5B9D\u5B9D\uFF0C\u521A\u5728\u6000\u91CC\u62B1\u4E86\u51E0\u5929\uFF0C\u8FD8\u6CA1\u770B\u591F\uFF0C\u5C31\u88AB\u62B1\u8FDB\u4E86\u75C5\u623F\uFF0C\u8499\u4F4F\u773C\u775B\uFF0C\u5B64\u96F6\u96F6\u7684\u8EBA\u5728\u6696\u7BB1\u91CC\uFF0C\u5168\u8EAB\u88AB\u84DD\u8272\u7684\u5149\u7B3C\u7F69\u7740\u3002\u770B\u5230\u8FD9\u6837\u7684\u60C5\u5F62\uFF0C\u5F88\u591A\u7236\u6BCD\u90FD\u662F\u4F24\u5FC3\u6B32\u7EDD\uFF0C\u521D\u4E3A\u4EBA\u7236\u4EBA\u6BCD\u7684\u6B23\u559C\u70DF\u6D88\u4E91\u6563\u3002</p><p>\u7ED9\u90A3\u4E48\u591A\u5BB6\u5EAD\u5E26\u6765\u4F24\u75DB\u7684\u9EC4\u75B8\uFF0C\u5230\u5E95\u662F\u600E\u4E48\u56DE\u4E8B\uFF0C\u4E3A\u4EC0\u4E48\u521A\u51FA\u751F\u7684\u5B9D\u5B9D\u90A3\u4E48\u591A\u9EC4\u75B8\uFF1F</p><p>\u5728\u80CE\u513F\u671F\u95F4\uFF0C\u5B69\u5B50\u7684\u6C27\u6C14\u9760\u5988\u5988\u901A\u8FC7\u8110\u5E26\u4F9B\u5E94\uFF0C\u4ED6\u7684\u6C27\u5206\u538B\u6BD4\u8F83\u4F4E\uFF0C\u6240\u4EE5\u4F1A\u6709\u66F4\u591A\u7684\u7EA2\u7EC6\u80DE\u6765\u642C\u8FD0\u6C27\u6C14\uFF0C\u7B49\u5B69\u5B50\u51FA\u751F\u540E\uFF0C\u4ED6\u6709\u4E86\u81EA\u5DF1\u7684\u547C\u5438\uFF0C\u6C27\u6C14\u4F9B\u5E94\u5927\u5E45\u589E\u591A\uFF0C\u4ED6\u5C31\u4E0D\u518D\u9700\u8981\u90A3\u4E48\u591A\u7EA2\u7EC6\u80DE\u4E86\uFF0C\u6240\u4EE5\u5927\u91CF\u7684\u7EA2\u7EC6\u80DE\u8981\u88AB\u7834\u574F\u6389\uFF0C\u7834\u574F\u6389\u7684\u7EA2\u7EC6\u80DE\u4F1A\u91CA\u653E\u8840\u8272\u7D20\uFF0C\u8840\u8272\u7D20\u5206\u89E3\u6700\u540E\u4F1A\u4EA7\u751F\u80C6\u7EA2\u7D20\u3002</p><p>\u53E6\u5916\u4E00\u65B9\u9762\uFF0C\u56E0\u4E3A\u8EAB\u4F53\u53D1\u80B2\u4E0D\u5B8C\u5584\uFF0C\u5C24\u5176\u662F\u65E9\u4EA7\u513F\uFF0C\u4ED6\u4EEC\u8EAB\u4F53\u5904\u7406\u5E76\u5C06\u80C6\u7EA2\u7D20\u6392\u51FA\u53BB\u7684\u80FD\u529B\u4E5F\u4E0D\u8DB3\uFF0C\u6240\u4EE5\u5C31\u66F4\u5BB9\u6613\u5BFC\u81F4\u8840\u91CC\u7684\u80C6\u7EA2\u7D20\u589E\u591A\u3002\u80C6\u7EA2\u7D20\u662F\u6A59\u9EC4\u8272\u7684\uFF0C\u6240\u4EE5\u5B69\u5B50\u770B\u8D77\u6765\u9EC4\u9EC4\u7684\uFF0C\u8FD9\u5C31\u662F\u9EC4\u75B8\u4E86\u3002</p><p>\u9EC4\u75B8\u662F\u65B0\u751F\u513F\u975E\u5E38\u5E38\u89C1\u7684\u73B0\u8C61\uFF0C\u597D\u5728\u5927\u90E8\u5206\u662F\u6B63\u5E38\u7684\u751F\u7406\u6027\u7684\uFF0C\u4E00\u822C\u57282\u5468\u5DE6\u53F3\u4F1A\u6D88\u5931\uFF0C\u4E0D\u4F1A\u7ED9\u5B69\u5B50\u9020\u6210\u5371\u5BB3\u3002\u4F46\u6709\u4E00\u5C0F\u90E8\u5206\u5B69\u5B50\uFF0C\u56E0\u4E3A\u65E9\u4EA7\u3001\u6EB6\u8840\u3001\u611F\u67D3\u3001\u5582\u517B\u4E0D\u5F53\u3001\u6216\u8005\u80C6\u9053\u5835\u585E\u7B49\u539F\u56E0\uFF0C\u80C6\u7EA2\u7D20\u4EA7\u751F\u5F97\u7279\u522B\u591A\uFF0C\u6216\u8005\u4EE3\u8C22\u3001\u6392\u51FA\u7279\u522B\u6162\uFF0C\u5BFC\u81F4\u8840\u91CC\u7684\u80C6\u7EA2\u7D20\u7279\u522B\u9AD8\uFF0C\u6301\u7EED\u65F6\u95F4\u7279\u522B\u957F\uFF0C\u90A3\u5C31\u53EF\u80FD\u662F\u75C5\u7406\u6027\u7684\u9EC4\u75B8\u3002</p><p>\u75C5\u7406\u6027\u9EC4\u75B8\u4E3B\u8981\u7684\u5371\u5BB3\u662F\u672A\u7ED3\u5408\u7684\u80C6\u7EA2\u7D20\u53EF\u80FD\u4F1A\u635F\u5BB3\u5927\u8111\u795E\u7ECF\uFF0C\u4E5F\u5C31\u662F\u5927\u5BB6\u8BF4\u7684\u6838\u9EC4\u75B8\uFF0C\u4E00\u65E6\u51FA\u73B0\u6838\u9EC4\u75B8\uFF0C\u5C31\u53EF\u80FD\u51FA\u73B0\u4E25\u91CD\u540E\u9057\u75C7\uFF0C\u751A\u81F3\u6B7B\u4EA1\u3002\u6838\u9EC4\u75B8\u5E76\u4E0D\u591A\u89C1\uFF0C\u5927\u90E8\u5206\u7684\u75C5\u7406\u6027\u9EC4\u75B8\u4E5F\u90FD\u4E0D\u4F1A\u5BFC\u81F4\u8FD9\u4E2A\u95EE\u9898\uFF0C\u4F46\u56E0\u4E3A\u6838\u9EC4\u75B8\u540E\u679C\u5F88\u4E25\u91CD\uFF0C\u6240\u4EE5\u533B\u751F\u4E0D\u6562\u5BF9\u75C5\u7406\u6027\u9EC4\u75B8\u6389\u4EE5\u8F7B\u5FC3\u3002</p><p>\u90A3\u75C5\u7406\u6027\u9EC4\u75B8\u8BE5\u600E\u4E48\u6CBB\u5462\uFF1F</p><p>\u56E0\u4E3A\u9EC4\u75B8\u7684\u539F\u56E0\u4E0D\u5916\u4E4E\u80C6\u7EA2\u7D20\u4EA7\u751F\u591A\u4E86\u6216\u8005\u80C6\u7EA2\u7D20\u4EE3\u8C22\u3001\u6392\u51FA\u6162\u4E86\uFF0C\u6240\u4EE5\u6CBB\u7597\u75C5\u7406\u6027\u7684\u9EC4\u75B8\u4E5F\u4E3B\u8981\u4ECE\u6D88\u9664\u75C5\u56E0\u53CA\u4FC3\u8FDB\u6392\u51FA\u4E24\u65B9\u9762\u7740\u624B\u3002</p><p>\u5BF9\u4E8E\u611F\u67D3\u3001\u5582\u517B\u3001\u80C6\u9053\u5835\u585E\u7B49\u75C5\u56E0\uFF0C\u81EA\u7136\u5E94\u8BE5\u9488\u5BF9\u75C5\u56E0\u505A\u5904\u7406\uFF0C\u4F46\u4E0D\u7BA1\u662F\u4EC0\u4E48\u539F\u56E0\u5F15\u8D77\u7684\u9EC4\u75B8\uFF0C\u628A\u672A\u7ED3\u5408\u80C6\u7EA2\u7D20\u964D\u4E0B\u6765\uFF0C\u5C31\u53EF\u4EE5\u6D88\u9664\u795E\u7ECF\u635F\u5BB3\u7684\u98CE\u9669\uFF0C\u6240\u4EE5\u65B0\u751F\u513F\u9EC4\u75B8\u4E00\u65E6\u8FBE\u4E86\u4E0D\u5B89\u5168\u7684\u8303\u56F4\uFF0C\u5C31\u9700\u8981\u6CBB\u7597\u4E86\u3002</p><p>\u76EE\u524D\u8BA4\u4E3A\uFF0C\u5BF9\u4E8E\u65B0\u751F\u513F\u9EC4\u75B8\uFF0C\u84DD\u5149\u7167\u5C04\u662F\u6700\u5B89\u5168\u6709\u6548\u7684\u6CBB\u7597\u65B9\u5F0F\u3002\u84DD\u5149\u53EF\u4EE5\u5C06\u5B69\u5B50\u4F53\u5185\u7684\u53EF\u80FD\u5371\u5BB3\u795E\u7ECF\u7684\u90A3\u79CD\u80C6\u7EA2\u7D20\uFF0C\u8F6C\u5316\u6210\u4E00\u79CD\u53EF\u4EE5\u76F4\u63A5\u4ECE\u80C6\u6C41\u548C\u5C3F\u6DB2\u6392\u51FA\u7684\u80C6\u7EA2\u7D20\uFF0C\u4ECE\u800C\u8FBE\u5230\u9000\u9EC4\u7684\u76EE\u7684\uFF0C\u7167\u84DD\u5149\u4E5F\u662F\u76EE\u524D\u65B0\u751F\u513F\u9EC4\u75B8\u6700\u5E38\u7528\u7684\u6CBB\u7597\u65B9\u5F0F\u3002</p><p>\u4F46\u5E76\u4E0D\u662F\u6240\u6709\u7684\u75C5\u7406\u6027\u9EC4\u75B8\u90FD\u8981\u7167\u5149\uFF0C\u4EC0\u4E48\u60C5\u51B5\u624D\u7B97\u4E0D\u5B89\u5168\uFF0C\u9700\u8981\u7167\u5149\uFF0C\u4E5F\u4E0D\u80FD\u4E00\u6982\u800C\u8BBA\uFF0C\u800C\u662F\u9700\u8981\u5728\u4E86\u89E3\u80C6\u7EA2\u7D20\u6C34\u5E73\u7684\u57FA\u7840\u4E0A\uFF0C\u7531\u533B\u751F\u7EFC\u5408\u5B69\u5B50\u7684\u80CE\u9F84\u3001\u51FA\u751F\u4F53\u91CD\u3001\u4E00\u822C\u72B6\u51B5\u7B49\u56E0\u7D20\u6765\u51B3\u5B9A\u3002\u5728\u56FD\u5185\u7684\u533B\u7597\u73AF\u5883\u4E0B\uFF0C\u56E0\u4E3A\u533B\u60A3\u5173\u7CFB\u7D27\u5F20\uFF0C\u800C\u6838\u9EC4\u75B8\u7684\u5371\u5BB3\u53C8\u7279\u522B\u5927\uFF0C\u6240\u4EE5\u5149\u7597\u6807\u51C6\u5F80\u5F80\u653E\u5F97\u677E\u4E00\u4E9B\u3002</p><p>\u901A\u8FC7\u5149\u7597\uFF0C\u7EDD\u5927\u90E8\u5206\u7684\u9EC4\u75B8\u90FD\u53EF\u4EE5\u964D\u4E0B\u6765\uFF0C\u4F46\u4E5F\u6709\u4E00\u4E9B\u5B69\u5B50\u9700\u8981\u914D\u5408\u4E00\u4E9B\u5176\u5B83\u6CBB\u7597\uFF0C\u6BD4\u5982\u514D\u75AB\u7403\u86CB\u767D\uFF0C\u7279\u522B\u4E25\u91CD\u7684\uFF0C\u6BD4\u5982\u6EB6\u8840\u5F15\u8D77\u7684\u9EC4\u75B8\uFF0C\u8FD8\u53EF\u80FD\u9700\u8981\u8FDB\u884C\u6362\u8840\u6CBB\u7597\uFF0C\u8FD9\u4E9B\u90FD\u662F\u533B\u751F\u624D\u80FD\u505A\u7684\u51B3\u5B9A\uFF0C\u5BB6\u957F\u6240\u8981\u505A\u7684\u5C31\u662F\u542C\u4ECE\u533B\u751F\u7684\u5B89\u6392\u3002</p><p>\u7167\u5149\u901A\u5E38\u9700\u8981\u5728\u533B\u9662\u91CC\u8FDB\u884C\uFF0C\u800C\u4E14\u65B0\u751F\u513F\u901A\u5E38\u90FD\u4F4F\u5728\u9694\u79BB\u75C5\u623F\uFF0C\u5BFC\u81F4\u5B69\u5B50\u548C\u7236\u6BCD\u5206\u79BB\uFF0C\u6240\u4EE5\u5F88\u591A\u5BB6\u957F\u90FD\u671F\u671B\u6709\u4E00\u4E9B\u4E0D\u7528\u7167\u5149\u7684\u65B9\u6CD5\uFF0C\u6BD4\u5982\u6652\u592A\u9633\u3001\u5403\u4E2D\u836F\u3002</p><p>\u6652\u592A\u9633\u662F\u80FD\u5E2E\u5B69\u5B50\u9000\u9EC4\u7684\uFF0C\u4F46\u5C0F\u9762\u79EF\u7684\u66B4\u9732\u6548\u679C\u5F88\u6709\u6548\uFF0C\u5927\u9762\u79EF\u7684\u66B4\u9732\u5B69\u5B50\u65E0\u6CD5\u4FDD\u6696\uFF0C\u800C\u4E14\u76F4\u63A5\u8BA9\u65B0\u751F\u513F\u66B4\u9732\u5728\u9633\u5149\u4E0B\uFF0C\u7D2B\u5916\u7EBF\u8FD8\u4F1A\u635F\u4F24\u5B69\u5B50\u76AE\u80A4\uFF0C\u6240\u4EE5\u4E0D\u5EFA\u8BAE\u901A\u8FC7\u6652\u592A\u9633\u6765\u7ED9\u5B69\u5B50\u9000\u9EC4\u3002</p><p>\u4E2D\u836F\u662F\u56FD\u5185\u7279\u6709\u7684\u9000\u9EC4\u65B9\u5F0F\uFF0C\u6BD4\u5982\u8335\u6800\u9EC4\u666E\u904D\u7528\u4E8E\u9EC4\u75B8\u7684\u6CBB\u7597\uFF0C\u8FD9\u4E2A\u836F\u65E2\u6709\u6CE8\u5C04\u6DB2\uFF0C\u4E5F\u6709\u53E3\u670D\u6DB2\u548C\u9897\u7C92\uFF0C\u76F4\u5230\u6700\u8FD1\uFF0C\u836F\u76D1\u5C40\u624D\u7981\u6B62\u5C06\u8335\u6800\u9EC4\u6CE8\u5C04\u6DB2\u7528\u4E8E\u5A74\u5E7C\u513F\uFF0C\u4F46\u53E3\u670D\u6DB2\u548C\u9897\u7C92\u4F9D\u7136\u4E0D\u5728\u88AB\u7981\u4E4B\u5217\u3002\u6240\u4EE5\uFF0C\u5373\u4FBF\u4E0A\u5E02\u524D\u6CA1\u505A\u8FC7\u4E25\u683C\u7684\u4E34\u5E8A\u836F\u7269\u8BD5\u9A8C\uFF0C\u8335\u6800\u9EC4\u4F9D\u7136\u4F1A\u662F\u5F88\u591A\u4E2D\u56FD\u5B69\u5B50\u6765\u5230\u8FD9\u4E16\u4E0A\u5403\u7684\u7B2C\u4E00\u79CD\u201C\u836F&quot;\u3002</p><p>\u4E86\u89E3\u6211\u5BF9\u4E2D\u533B\u836F\u6001\u5EA6\u7684\u4EBA\u81EA\u7136\u77E5\u9053\uFF0C\u6211\u4E0D\u5EFA\u8BAE\u4F7F\u7528\u4EFB\u4F55\u4E2D\u836F\uFF08\u6309\u4E2D\u533B\u7406\u8BBA\u6765\u4F7F\u7528\u7684\u836F\uFF09\uFF0C\u65E0\u8BBA\u662F\u5B69\u5B50\u8FD8\u662F\u5927\u4EBA\uFF0C\u65E0\u8BBA\u662F\u6CE8\u5C04\u6DB2\u8FD8\u662F\u53E3\u670D\u836F\uFF0C\u90FD\u4E0D\u5EFA\u8BAE\u7528\u3002</p><p>\u6240\u4EE5\u5982\u679C\u5B69\u5B50\u6709\u9EC4\u75B8\uFF0C\u5BB6\u957F\u80FD\u505A\u7684\u5C31\u662F\u5C3D\u91CF\u9891\u7E41\u6309\u9700\u5582\u517B\uFF0C\u8BA9\u5B69\u5B50\u591A\u5403\u591A\u6392\u4FBF\uFF0C\u540C\u65F6\u89C2\u5BDF\u5B69\u5B50\u9EC4\u75B8\u53D8\u5316\u60C5\u51B5\uFF0C\u6309\u533B\u751F\u7684\u610F\u89C1\u53BB\u590D\u67E5\u76D1\u6D4B\uFF0C\u6709\u9EC4\u75B8\u52A0\u91CD\uFF0C\u4E0D\u5403\u5976\uFF0C\u53CD\u5E94\u5DEE\uFF0C\u5927\u4FBF\u53D1\u767D\u8FD9\u4E9B\u60C5\u51B5\u53CA\u65F6\u53BB\u533B\u9662\uFF0C\u81F3\u4E8E\u8981\u4E0D\u8981\u7167\u5149\uFF0C\u8981\u4E0D\u8981\u505A\u5176\u5B83\u68C0\u67E5\u67E5\u627E\u9EC4\u75B8\u539F\u56E0\uFF0C\u542C\u533B\u751F\u7684\u610F\u89C1\u5C31\u597D\u3002\uFF08\u9898\u56FE\u6765\u81EAPixabay\uFF0C\u6388\u6743\u57FA\u4E8ECC0\u534F\u8BAE\u3002\u4E0B\u4E00\u7BC7\u6211\u8BE6\u7EC6\u8BF4\u8BF4\u4E3A\u4EC0\u4E48\u53E3\u670D\u8335\u6800\u9EC4\u4E5F\u4E0D\u5EFA\u8BAE\u7528\u3002\uFF09</p><hr><p>\u76F8\u5173\u533B\u751F\u63A8\u8350\uFF1A\u4E25\u864E\uFF0C\u590D\u65E6\u5927\u5B66\u513F\u79D1\u5B66\u535A\u58EB\uFF0C\u4E0A\u6D77\u5E02\u7B2C\u4E00\u5987\u5A74\u4FDD\u5065\u9662\u513F\u79D1\u4E3B\u6CBB\u533B\u5E08\u3002</p><p>\u957F\u6309\u4E0B\u9762\u4E8C\u7EF4\u7801\u52A0\u5173\u6CE8\uFF0C\u53EF\u5411\u4E25\u533B\u751F\u54A8\u8BE2\u65B0\u751F\u513F\u75BE\u75C5\u3001\u513F\u7AE5\u547C\u5438\u9053\u3001\u6D88\u5316\u9053\u75BE\u75C5\u3002</p>',22),b={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651626355&idx=1&sn=be7fb0dac31351aca0d07b42b7eff672&chksm=8b5cb289bc2b3b9f02029a0c3d20cf6c1a16816b801f9de4eb644b40e9e664ffedbed9b5bb3c&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},f=s("\u67E5\u770B\u539F\u6587");function h(l,m){const p=a("ExternalLinkIcon");return c(),r(n,null,[_,e("p",null,[e("a",b,[f,t(p)])])],64)}var k=d(i,[["render",h]]);export{k as default};
