import{r as a,o as n,c as r,a as e,b as t,F as i,d as o,e as s}from"./app.9aaa2256.js";import{_ as c}from"./plugin-vue_export-helper.21dcd24c.js";const d={},l=o('<h3 id="\u5B69\u5B50\u53D1\u70E7\u9996\u9009\u7269\u7406\u964D\u6E29" tabindex="-1"><a class="header-anchor" href="#\u5B69\u5B50\u53D1\u70E7\u9996\u9009\u7269\u7406\u964D\u6E29" aria-hidden="true">#</a> \u5B69\u5B50\u53D1\u70E7\u9996\u9009\u7269\u7406\u964D\u6E29\uFF1F</h3><p>\u53D1\u70E7\u5148\u7ED9\u5B69\u5B50\u64E6\u4E2A\u6FA1\uFF0C\u7528\u51B0\u888B\u51B0\u5934\u3002\u8BA4\u4E3A\u7269\u7406\u964D\u6E29\u5F88\u6709\u6548\u800C\u4E14\u6CA1\u526F\u4F5C\u7528\uFF0C\u9000\u70E7\u5E94\u8BE5\u9996\u9009\u7269\u7406\u964D\u6E29\uFF0C\u7269\u7406\u964D\u6E29\u4E0D\u884C\u518D\u5403\u9000\u70E7\u836F\u3002\u4F60\u662F\u8FD9\u6837\u8BA4\u4E3A\u5417\uFF1F\u606D\u559C\u4F60\uFF0C\u5168\u9519\u4E86\u3002\u76EE\u524D\u6CA1\u6709\u54EA\u5BB6\u6743\u5A01\u7684\u4E13\u4E1A\u673A\u6784\u628A\u5B83\u5F53\u505A\u9000\u70E7\u7684\u9996\u9009\u65B9\u5F0F\uFF0C\u8FD9\u662F\u4E3A\u4EC0\u4E48\u5462\uFF1F</p><p>\u5728\u7F8E\u56FD\u513F\u79D1\u5B66\u4F1A\u5173\u4E8E\u9000\u70E7\u7684\u6700\u65B0\u6307\u5357[1]\u91CC\uFF0C\u53CD\u590D\u5F3A\u8C03\u7684\u4E00\u70B9\u662F\uFF0C\u9664\u975E\u662F\u8D85\u9AD8\u70ED\uFF0C\u53D1\u70E7\u4E0D\u4F1A\u5BF9\u5B69\u5B50\u9020\u6210\u4F24\u5BB3\uFF0C\u76F8\u53CD\u662F\u6709\u597D\u5904\u7684\u3002\u6709\u660E\u786E\u539F\u56E0\u7684\u53D1\u70E7\uFF0C\u6BD4\u5982\u4E25\u91CD\u7684\u7EC6\u83CC\u611F\u67D3\uFF0C\u81EA\u7136\u53EF\u4EE5\u9488\u5BF9\u75C5\u56E0\u8FDB\u884C\u6297\u611F\u67D3\u6CBB\u7597\u3002\u4F46\u53D1\u70E7\u6709\u5229\u4E8E\u63D0\u5347\u5B69\u5B50\u7684\u514D\u75AB\u529B\uFF0C\u6709\u5229\u4E8E\u5BF9\u6297\u611F\u67D3\uFF0C\u9664\u975E\u662F\u5F15\u8D77\u7684\u5B69\u5B50\u660E\u663E\u4E0D\u9002\uFF0C\u5E76\u4E0D\u9700\u8981\u9000\u70E7\uFF0C\u53D1\u70E7\u7684\u6CBB\u7597\u76EE\u6807\u662F\u6539\u5584\u5B69\u5B50\u7684\u8212\u9002\u5EA6\u800C\u4E0D\u662F\u53BB\u9000\u70E7\u3002</p><p>\u5B69\u5B50\u8212\u9002\u5EA6\u5F88\u96BE\u7528\u5BA2\u89C2\u7684\u6307\u6807\u53BB\u8861\u91CF\uFF0C\u7F8E\u56FD\u4E13\u5BB6\u7684\u5171\u8BC6\u662F\u572839\xB0C\u4EE5\u4E0A\uFF08\u4E2D\u56FD\u6307\u5357\u662F38.5\xB0C\u4EE5\u4E0A\uFF09\u5B69\u5B50\u4F1A\u6BD4\u8F83\u96BE\u53D7\uFF0C\u9664\u4E86\u4E00\u4E9B\u6709\u9057\u4F20\u4EE3\u8C22\u75C5\u3001\u5FC3\u80BA\u529F\u80FD\u75BE\u75C5\u3001\u766B\u75EB\u7B49\u75BE\u75C5\u7684\u5B69\u5B50\uFF0C\u5982\u679C\u5B69\u5B50\u6CA1\u6709\u660E\u663E\u4E0D\u8212\u670D\uFF0C\u5065\u5EB7\u7684\u5B69\u5B50\u572839\xB0C\u4EE5\u4E0B\u4E0D\u9700\u8981\u9000\u70E7\uFF0C\u81EA\u7136\u4E0D\u9700\u8981\u64E6\u6FA1\u4E5F\u4E0D\u9700\u8981\u51B0\u888B\u3002\u8D85\u8FC7\u4E8639\xB0C\u5B69\u5B50\u660E\u663E\u4E0D\u8212\u670D\u624D\u9700\u8981\u9000\u70E7\u3002</p><p>\u6709\u4E9B\u6CBB\u7597\u4E0D\u80FD\u9000\u70E7\u4F46\u53EF\u4EE5\u8BA9\u5B69\u5B50\u66F4\u8212\u670D\uFF0C\u6BD4\u5982\u51CF\u5C11\u8863\u7269\u5F00\u7A7A\u8C03\uFF0C\u81EA\u7136\u53EF\u4EE5\u91C7\u7528\u3002\u6709\u4E9B\u6CBB\u7597\u53EF\u4EE5\u9000\u70E7\u4E5F\u53EF\u4EE5\u6539\u5584\u5B69\u5B50\u7684\u8212\u9002\u5EA6\uFF0C\u6BD4\u5982\u5E03\u6D1B\u82AC\u548C\u5BF9\u4E59\u9170\u6C28\u57FA\u915A\u8FD9\u6837\u7684\u9000\u70E7\u836F\uFF0C\u4E0D\u4F46\u9000\u70E7\u6548\u679C\u597D\uFF0C\u8FD8\u53EF\u4EE5\u7F13\u89E3\u75BC\u75DB\uFF0C\u6539\u5584\u5B69\u5B50\u7684\u8212\u9002\u5EA6\uFF0C\u5F53\u7136\u4E5F\u53EF\u4EE5\u91C7\u7528\u3002\u76F8\u53CD\uFF0C\u6709\u4E9B\u6CBB\u7597\u4E5F\u53EF\u4EE5\u9000\u70ED\uFF0C\u4F46\u6548\u679C\u4E0D\u597D\uFF0C\u8FD8\u53EF\u80FD\u8BA9\u5B69\u5B50\u66F4\u96BE\u53D7\uFF0C\u6BD4\u5982\u5439\u98CE\u6247\u3001\u6E29\u6C34\u64E6\u6D74\u3001\u51B0\u888B\u6577\u5934\u7B49\u3002</p><p>\u7269\u7406\u964D\u6E29\u7684\u65B9\u5F0F\u5F88\u591A\uFF0C\u5305\u62EC\u8131\u8863\u670D\u6563\u70ED\u3001\u6CE1\u6FA1\u3001\u6E29\u6C34\u64E6\u6D74\u3001\u9152\u7CBE\u64E6\u6D74\u3001\u51B0\u888B\u51B0\u6577\u3001\u9000\u70ED\u8D34\u3001\u5439\u98CE\u6247\u3001\u5F00\u7A7A\u8C03\u7B49\u7B49\u3002\u539F\u7406\u662F\u901A\u8FC7\u76AE\u80A4\u548C\u5916\u754C\u7684\u73AF\u5883\u7684\u63A5\u89E6\uFF0C\u501F\u52A9\u6D41\u52A8\u7684\u6C34\u6216\u7A7A\u6C14\u5C06\u4EBA\u4F53\u70ED\u91CF\u5E26\u8D70\uFF0C\u8FBE\u5230\u9000\u70ED\u7684\u76EE\u7684\u3002\u6E29\u6C34\u64E6\u6D74\u662F\u7528\u5F97\u6BD4\u8F83\u591A\u7684\u65B9\u5F0F\uFF0C\u7814\u7A76\u4E5F\u6BD4\u8F83\u591A\uFF0C\u6211\u4EEC\u6765\u770B\u770B\u6E29\u6C34\u64E6\u6D74\u7684\u7814\u7A76\u3002</p><p>\u65E9\u57281970\u5E74\uFF0C\u6709\u7814\u7A76\u7ED9\u4E24\u7EC4\u5B69\u5B50\u5206\u522B\u91C7\u7528\u6E29\u6C34\u64E6\u6D74\u548C\u5403\u5B89\u6170\u5242\u7684\u6CBB\u7597\uFF0C\u53D1\u73B01\u5C0F\u65F6\u540E\u9000\u70ED\u4E86\u7684\u5B69\u5B50\u6570\u91CF\u4E0A\u6CA1\u6709\u660E\u663E\u7684\u5DEE\u522B\uFF0C\u5F53\u7136\u4ED6\u7684\u6837\u672C\u5F88\u5C0F\uFF0C\u6BCF\u7EC4\u53EA\u670915\u4EBA\u3002[2]\u5176\u4ED6\u5927\u90E8\u5206\u7684\u7814\u7A76[3,4,5]\u8BA4\u4E3A\uFF0C\u64E6\u6D74\u5728\u77ED\u65F6\u95F4\u5185\u662F\u53EF\u4EE5\u964D\u4F4E\u4F53\u6E29\u7684\uFF0C\u4F46\u4E3B\u8981\u662F\u572830\u5206\u949F\u5185\uFF0C\u4E4B\u540E\u4F53\u6E29\u5C31\u9010\u6E10\u56DE\u5347\u4E86\u3002\u5F53\u7136\u64E6\u6D74\u7684\u65B9\u5F0F\u6709\u5F88\u5927\u7684\u53D8\u6570\uFF0C\u6301\u7EED\u65F6\u95F4\u3001\u6C34\u6E29\u90FD\u53EF\u80FD\u5F71\u54CD\u6548\u679C\uFF0C\u6781\u7AEF\u4E00\u70B9\u628A\u5B69\u5B50\u76F4\u63A5\u6CE1\u51B7\u6C34\u4E86\uFF0C\u6211\u76F8\u4FE1\u53EA\u6709\u8DB3\u591F\u957F\u7684\u65F6\u95F4\uFF0C\u4E0D\u4F46\u80FD\u9000\u70E7\uFF0C\u8FDE\u4F53\u6E29\u4E5F\u80FD\u964D\u6CA1\u6389\u3002</p><p>\u64E6\u6D74\u662F\u7528\u6C34\u548C\u8EAB\u4F53\u7684\u6E29\u5DEE\u6765\u5E26\u8D70\u70ED\u91CF\uFF0C\u6E29\u5DEE\u5C0F\u4E86\u6CA1\u6709\u6548\u679C\uFF0C\u6E29\u5DEE\u5927\u4E86\u4F1A\u5F15\u8D77\u8EAB\u4F53\u7684\u4E0D\u9002\uFF0C\u6240\u4EE5\u8FD9\u79CD\u65B9\u5F0F\u662F\u8FDD\u53CD\u751F\u7406\u7684\u3002\u800C\u4E14\u70E7\u523039\u5EA6\u4EE5\u4E0A\u7684\u5B69\u5B50\u5F88\u591A\u90FD\u660F\u660F\u6C89\u6C89\u60F3\u7761\u89C9\uFF0C\u8C01\u4F1A\u4E50\u610F\u8FD9\u65F6\u88AB\u4EBA\u7528\u51C9\u6C34\u64E6\u6765\u64E6\u53BB\uFF1F\u5F88\u591A\u7814\u7A76\u4E5F\u90FD\u8BC1\u5B9E\uFF0C\u548C\u9000\u70ED\u836F\u51E0\u4E4E\u4E0D\u4F1A\u5F15\u8D77\u5B69\u5B50\u7684\u4E0D\u9002\u76F8\u6BD4\uFF0C\u64E6\u6D74\u4F1A\u5F15\u8D77\u5F88\u5927\u4E00\u90E8\u5206\u5B69\u5B50\u54ED\u95F9\u3001\u5BD2\u6218[6]\u3002</p><p>\u6CBB\u7597\u76EE\u7684\u662F\u8BA9\u5B69\u5B50\u4E0D\u96BE\u53D7\uFF0C\u6E29\u6C34\u64E6\u6D74\u5374\u53EF\u80FD\u8BA9\u5B69\u5B50\u66F4\u96BE\u53D7\uFF0C\u800C\u4E14\u8FD9\u79CD\u964D\u6E29\u7684\u6548\u679C\u5DEE\uFF0C\u6301\u7EED\u65F6\u95F4\u77ED\uFF0C\u81EA\u7136\u4E0D\u662F\u4E00\u4E2A\u597D\u7684\u6CBB\u7597\u65B9\u5F0F\u3002\u4F60\u4E5F\u8BB8\u4F1A\u89C9\u5F97\u6211\u5F15\u7528\u7684\u7814\u7A76\u4E0D\u5168\u9762\uFF0C\u4F46\u4E13\u4E1A\u6307\u5357\u548C\u5404\u4E2A\u6743\u5A01\u673A\u6784\u5BF9\u7269\u7406\u964D\u6E29\u7684\u8BC4\u4EF7\u662F\u4E1A\u5185\u4E13\u5BB6\u4EEC\u5728\u5168\u9762\u8BC4\u4F30\u5F53\u524D\u7684\u7814\u7A76\uFF0C\u6839\u636E\u73B0\u6709\u7684\u8BC1\u636E\u505A\u51FA\u7684\u5EFA\u8BAE\uFF0C\u6211\u4EEC\u53EF\u4EE5\u770B\u770B\u4ED6\u4EEC\u600E\u4E48\u8BF4\u3002</p><p>1.\u4E2D\u56FD0\u81F35\u5C81\u513F\u7AE5\u75C5\u56E0\u4E0D\u660E\u7684\u6025\u6027\u53D1\u70ED\u8BCA\u65AD\u5904\u7406\u6307\u5357\uFF08\u53D1\u5E03\u65F6\u95F4\uFF1A2008\u5E7411\u6708\uFF09\uFF1A</p><p>\u7269\u7406\u964D\u6E29\u6548\u679C\u4E0D\u53CA\u9000\u70ED\u5242\uFF0C\u53EF\u4F5C\u4E3A\u8F85\u52A9\u9000\u70ED\u65B9\u6CD5\uFF0C\u7269\u7406\u964D\u6E29\u4E0E\u9000\u70ED\u5242\u8054\u5408\u5E94\u7528\u65F6\uFF0C\u4F53\u6E29\u4E0B\u964D\u5FEB\u4E8E\u5355\u7528\u9000\u70ED\u5242\uFF0C\u9AD8\u70ED\u65F6\u63A8\u8350\u4F7F\u7528\u9000\u70ED\u5242\u540C\u65F6\u8054\u5408\u6E29\u70ED\u643D\u8EAB\u7684\u7269\u7406\u964D\u6E29\u65B9\u6CD5\u3002</p><p>2.\u7F8E\u56FD\u513F\u79D1\u5B66\u4F1A\u65D7\u4E0B\u7684\u79D1\u666E\u7F51\u7AD9healthychildren.org[7]\uFF08\u6700\u540E\u66F4\u65B0\u65F6\u95F4\uFF1A2013\u5E745\u6708\uFF09\uFF1A</p><p>\u5927\u90E8\u5206\u60C5\u51B5\uFF0C\u8BA9\u53D1\u70E7\u7684\u5B69\u5B50\u66F4\u8212\u670D\u7684\u6700\u65B9\u4FBF\u65B9\u5F0F\u662F\u53E3\u670D\u5BF9\u4E59\u9170\u6C28\u57FA\u915A\u6216\u5E03\u6D1B\u82AC\uFF0C\u7136\u800C\uFF0C\u6709\u65F6\u5019\u4F60\u53EF\u80FD\u60F3\u7ED3\u5408\u6E29\u6C34\u64E6\u6D74\u6216\u53EA\u60F3\u6E29\u6C34\u64E6\u6D74\u3002\u6BD4\u5982\u77E5\u9053\u5B69\u5B50\u5BF9\u9000\u70E7\u836F\u8FC7\u654F\uFF0C\u6216\u8005\u4E0D\u80FD\u8010\u53D7\uFF08\u7F55\u89C1\uFF09\u53EF\u4EE5\u4F18\u5148\u9009\u7528\u64E6\u6D74\u3002\u5982\u679C\u53D1\u70E7\u8BA9\u4F60\u7684\u5B69\u5B50\u6781\u5EA6\u4E0D\u9002\uFF0C\u6216\u8005\u5B69\u5B50\u5455\u5410\uFF0C\u53EF\u5EFA\u8BAE\u914D\u5408\u9000\u70E7\u836F\u4F7F\u7528\u64E6\u6D74\u3002</p><p>3.\u9999\u6E2F\u536B\u751F\u7F72[8]\uFF08\u6700\u540E\u4FEE\u8BA2\u65F6\u95F4\uFF1A2013\u5E748\u6708\uFF09\uFF1A</p><p>\u6E29\u6C34\u6D74\u5E76\u4E0D\u80FD\u5E2E\u52A9\u5B9D\u5B9D\u9000\u70E7\uFF0C\u4F46\u82E5\u5B9D\u5B9D\u6709\u4EE5\u4E0B\u60C5\u51B5\uFF0C\u5F88\u591A\u4EBA\u90FD\u4F1A\u7ED9\u5B9D\u5B9D\u6CE1\u6E29\u6C34\u6D74\u6765\u4EE4\u4ED6\u8212\u670D\u4E00\u70B9\uFF1A(1)\u4E0D\u80FD\u670D\u7528\u53E3\u670D\u836F\u7269(2)\u670D\u836F\u540E\u5455\u5410(3)\u8868\u73B0\u70E6\u8E81\u6216\u975E\u5E38\u4E0D\u9002\u3002</p><p>4.\u82F1\u56FDNICE\uFF08\u56FD\u5BB6\u536B\u751F\u4E0E\u4E34\u5E8A\u4F18\u5316\u7814\u7A76\u6240\uFF09\u7684\u300A\u513F\u7AE5\u53D1\u70ED\uFF1A5\u5C81\u4EE5\u4E0B\u7684\u8BC4\u4F30\u548C\u521D\u6B65\u6CBB\u7597\u300B\u6307\u5357[9]\uFF08\u6700\u540E\u66F4\u65B0\u65F6\u95F4\uFF1A2013\u5E745\u6708\uFF09\uFF1A</p><p>\u6E29\u6C34\u64E6\u6D74\u4E0D\u63A8\u8350\u7528\u4E8E\u6CBB\u7597\u53D1\u70ED\u3002</p><p>5.\u52A0\u62FF\u5927\u513F\u79D1\u533B\u751F\u534F\u4F1A\uFF08\u6700\u540E\u66F4\u65B0\u65F6\u95F4\uFF1A2013\u5E749\u6708\uFF09[10]\uFF1A</p><p>\u4E0D\u63A8\u8350\u7ED9\u5B69\u5B50\u6E29\u6C34\u64E6\u6D74\uFF0C\u9152\u7CBE\u6D74\u6216\u64E6\u6D74\u3002</p><p>6.\u7B2C19\u7248\u300A\u5C3C\u5C14\u68EE\u513F\u79D1\u5B66\u300B\uFF08\u51FA\u7248\u65F6\u95F4\uFF1A2011\u5E74\uFF09\uFF1A</p><p>\u4E0D\u8BA4\u4E3A\u6E29\u6C34\u6D74\u548C\u51B7\u6BEF\u8FD9\u6837\u7684\u7269\u7406\u65B9\u5F0F\u5BF9\u9000\u70ED\u6709\u6548\u3002</p><p>7.\u7F8E\u56FD\u513F\u79D1\u5B66\u4F1A\u300A\u513F\u7AE5\u53D1\u70ED\u4E0E\u9000\u70E7\u836F\u7684\u4F7F\u7528\u300B\u6307\u5357\uFF08\u53D1\u5E03\u65F6\u95F4\uFF1A2011\u5E743\u6708\uFF09[1]:</p><p>\u628A\u4F53\u6E29\u964D\u5230\u6B63\u5E38\u662F\u4E0D\u662F\u5C31\u6539\u5584\u4E86\u5B69\u5B50\u7684\u8212\u9002\u5EA6\uFF0C\u6211\u4EEC\u5E76\u4E0D\u6E05\u695A\uFF0C\u5916\u90E8\u964D\u6E29\u65B9\u5F0F\uFF0C\u6BD4\u5982\u6E29\u6C34\u64E6\u6D74\uFF0C\u53EF\u4EE5\u964D\u4F4E\u4F53\u6E29\u4F46\u63D0\u9AD8\u4E0D\u4E86\u8212\u9002\u5EA6\u3002</p><p>\u770B\u770B\u8FD9\u4E9B\u4E13\u4E1A\u673A\u6784\u7684\u610F\u89C1\uFF0C\u5373\u4FBF\u662F\u6700\u66A7\u6627\u7684\u4E2D\u56FD\u6307\u5357\uFF0C\u4E5F\u53EA\u662F\u8BF4\u7269\u7406\u964D\u6E29\u53EF\u4EE5\u4F5C\u4E3A\u8F85\u52A9\u9000\u70ED\u65B9\u6CD5\uFF0C\u800C\u4E14\u8FD9\u8FD8\u662F2008\u5E74\u7684\u89C2\u70B9\u3002\u81F3\u4E8E\u6E29\u6C34\u64E6\u6D74\uFF0C\u5883\u5916\u7684\u673A\u6784\u8981\u4E48\u660E\u786E\u8BF4\u6CA1\u7528\uFF0C\u4E0D\u63A8\u8350\uFF0C\u8981\u4E48\u8BF4\u5728\u9000\u70E7\u836F\u4E0D\u80FD\u7528\u7684\u65F6\u5019\u624D\u53EF\u4EE5\u8003\u8651\uFF0C\u6216\u8005\u5728\u70E7\u5F97\u592A\u5389\u5BB3\u5B69\u5B50\u975E\u5E38\u96BE\u53D7\u7684\u65F6\u5019\u914D\u5408\u9000\u70E7\u836F\u7528\u4E00\u4E0B\u3002</p><p>\u5F53\u7136\u7269\u7406\u964D\u6E29\u65B9\u5F0F\u5F88\u591A\uFF0C\u5E76\u4E0D\u662F\u6240\u6709\u7684\u90FD\u4E0D\u80FD\u7528\u3002\u6B63\u5982\u524D\u9762\u6240\u8BF4\uFF0C\u5C11\u7A7F\u8863\u670D\u5F00\u7A7A\u8C03\u4E5F\u5C5E\u4E8E\u7269\u7406\u964D\u6E29\u7684\u8303\u7574\uFF0C\u56E0\u4E3A\u53EF\u4EE5\u8BA9\u5B69\u5B50\u66F4\u8212\u670D\uFF0C\u51B7\u5C31\u7A7F\uFF0C\u70ED\u5C31\u8131\uFF0C\u7A7A\u8C03\u4E5F\u4E00\u6837\uFF0C\u70ED\u5C31\u5F00\u51B7\u6C14\uFF0C\u51B7\u5C31\u5F00\u6696\u6C14\u3002\u81F3\u4E8E\u51B0\u888B\u51B0\u6577\u3001\u9000\u70ED\u8D34\u8FD9\u6837\u7684\u5C0F\u9762\u79EF\u63A5\u89E6\u76AE\u80A4\u7684\u964D\u6E29\u65B9\u5F0F\uFF0C\u65E2\u964D\u4E0D\u4E86\u6E29\u53C8\u8BA9\u5B69\u5B50\u4E0D\u8212\u670D\uFF0C\u81EA\u7136\u5E94\u8BE5\u653E\u5F03\uFF0C\u800C\u9152\u7CBE\u64E6\u6D74\u53EF\u4EE5\u964D\u6E29\uFF0C\u4F46\u53EF\u80FD\u5BFC\u81F4\u5B69\u5B50\u9152\u7CBE\u4E2D\u6BD2\uFF0C\u5E94\u8BE5\u7981\u6B62\u3002</p><p>\u6E29\u6C34\u64E6\u6D74\u548C\u6CE1\u6FA1\u8FD9\u6837\u7684\u65B9\u5F0F\uFF0C\u572839\xB0C\u4EE5\u4E0B\u6839\u672C\u5C31\u4E0D\u9700\u8981\u7528\uFF0C39\xB0C\u4EE5\u4E0A\u4E5F\u662F\u5148\u5403\u9000\u70E7\u836F\uFF0C\u53EA\u6709\u5F53\u5B69\u5B50\u4E0D\u80FD\u5403\u9000\u70E7\u836F\u65F6\uFF0C\u624D\u53EF\u8003\u8651\u9009\u7528\uFF0C\u6216\u8005\u5F53\u5B69\u5B50\u975E\u5E38\u96BE\u53D7\uFF0C\u4E5F\u5C31\u662F\u53D1\u70E7\u6240\u5E26\u6765\u7684\u4E0D\u9002\u5DF2\u7ECF\u5927\u5927\u8D85\u8FC7\u4E86\u64E6\u6D74\u6240\u5F15\u8D77\u7684\u4E0D\u9002\uFF0C\u624D\u5E94\u8BE5\u8003\u8651\u52A0\u7528\uFF0C\u6240\u4EE5\u8BF4\u767D\u4E86\u5C31\u662F\u4E2A\u5907\u80CE\uFF0C\u4F60\u628A\u5B83\u5F53\u9996\u9009\u7684\u65B9\u5F0F\u5C31\u9519\u4E86\u3002</p><p>\u5F53\u7136\uFF0C\u63A5\u53D7\u4E0A\u9762\u7684\u89C2\u70B9\uFF0C\u9996\u5148\u8981\u63A5\u53D7<strong>\u9000\u70E7\u7684\u6CBB\u7597\u76EE\u6807\u662F\u63D0\u9AD8\u5B69\u5B50\u7684\u8212\u9002\u5EA6</strong>\u8FD9\u4E2A\u533B\u5B66\u754C\u7684\u4E3B\u6D41\u89C2\u70B9\uFF0C\u8FD9\u5176\u5B9E\u7275\u6D89\u5230\u4EF7\u503C\u89C2\u7684\u5224\u65AD\uFF0C\u4E5F\u5E76\u4E0D\u662F\u6240\u6709\u7684\u533B\u751F\u90FD\u8BA4\u540C\u3002\u4F5C\u4E3A\u5BB6\u957F\uFF0C\u5982\u679C\u4F60\u975E\u5E38\u62C5\u5FC3\u9000\u70E7\u836F\u7684\u526F\u4F5C\u7528\uFF0C\u60C5\u613F\u5B69\u5B50\u5FCD\u53D7\u90A3\u4E9B\u4E0D\u9002\u53BB\u83B7\u5F97\u64E6\u6D74\u90A3\u70B9\u964D\u6E29\u6548\u679C\uFF0C\u90A3\u4E5F\u662F\u81EA\u5DF1\u7684\u81EA\u7531\u3002\uFF08\u539F\u521B\u6587\u7AE0\u672A\u7ECF\u8BB8\u53EF\u8C22\u7EDD\u8F6C\u8F7D\uFF09</p><p>\u4E3A\u4E86\u4E86\u89E3\u8FD9\u7BC7\u6587\u7AE0\u7684\u6548\u679C\uFF0C\u505A\u4E2A\u5C0F\u8C03\u67E5\uFF0C\u8C22\u8C22\u53C2\u4E0E\u3002</p><p>\u53C2\u8003\u8D44\u6599\uFF1A</p><p><em>[1].Janice E. Sullivan, MD, Henry C.Fever and Antipyretic Use in Children.PEDIATRICS 2011,127: 580 -587.</em></p><p><em>[2]. Steele RW, Tanaka PT, Lara RP, Bass JW. Evaluation of sponging and of oral antipyretic therapy to reduce fever. Journal of Pediatrics 1970;<strong>77</strong>(5):824\u20139.</em></p><p><em>[3].Agbolosu NB, Cuevas LE, Milligan P, Broadhead RL, Brewster D, Graham SM. Efficacy of tepid sponging versus paracetamol in reducing temperature in febrile children. Ann Trop Paediatr. 1997;17:283\u20138.</em></p><p><em>[4]. Aksoylar S, Ak\u015Fit S, Ca\u011Flayan S, Yaprak I, Bakiler R, Cetin F. Evaluation of sponging and antipyretic medication to reduce body temperature in febrile children. Acta Paediatr Jpn. 1997;39:215\u20137.</em></p><p><em>[5].Kinmonth AL, Fulton Y, Campbell MJ. Management of feverish children at home. BMJ. 1992;305:1134\u20136.</em></p><p><em>[6].Aluka TM,Gyuse AN,Udonwa NE. Comparison of Cold Water Sponging and Acetaminophen in Control of Fever Among Children Attending a Tertiary Hospital in South Nigeria.J Family Med Prim Care. 2013;2(2):153-8</em></p><p><em>[7].http://www.healthychildren.org/English/health-issues/conditions/fever/Pages/Treating-a-Fever-Without-Medicine.aspx</em></p><p><em>[8]http://www.fhs.gov.hk/sc_chi/health_info/child/14743.html</em></p><p><em>[9].https://www.nice.org.uk/guidance/cg160/resources/guidance-feverish-illness-in-children-pdf</em></p><p><em>[10].http://www.caringforkids.cps.ca/handouts/fever_and_temperature_taking</em></p><p><strong>\u6743\u5A01\u7684\u80B2\u513F\u79D1\u666E\uFF0C\u6DF1\u5EA6\u7684\u533B\u5B66\u4EBA\u6587</strong></p><p><strong>\u4EC5\u63A8\u9001\u539F\u521B\u6587\u7AE0\uFF0C\u6B22\u8FCE\u5173\u6CE8drpeihg</strong></p>',41),h={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=200623156&idx=1&sn=744b3dfe0f7fdf4484db6f67975a275b&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},m=s("\u67E5\u770B\u539F\u6587");function g(f,u){const p=a("ExternalLinkIcon");return n(),r(i,null,[l,e("p",null,[e("a",h,[m,t(p)])])],64)}var v=c(d,[["render",g]]);export{v as default};
