import{r as a,o as t,c as r,a as e,b as n,F as o,d as c,e as s}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const i={},_=c('<h3 id="\u8B66\u60D5\u6EE5\u7528\u7684\u5229\u5DF4\u97E6\u6797" tabindex="-1"><a class="header-anchor" href="#\u8B66\u60D5\u6EE5\u7528\u7684\u5229\u5DF4\u97E6\u6797" aria-hidden="true">#</a> \u8B66\u60D5\u6EE5\u7528\u7684\u5229\u5DF4\u97E6\u6797</h3><p>\u4E4B\u524D\u5199\u8FC7\u4E00\u7BC7\u300A\u5F53\u5229\u5DF4\u97E6\u6797\u9047\u5230\u611F\u5192\uFF0C\u5F53\u4E34\u5E8A\u6307\u5357\u9047\u5230\u4E4C\u9F99\u300B\uFF08\u56DE\u590D64\u53EF\u67E5\u770B\uFF09\uFF0C\u8BF4\u7684\u662F\u6211\u4EEC\u7684\u513F\u7AE5\u611F\u5192\u6307\u5357\u91CC\u5173\u4E8E\u5229\u5DF4\u97E6\u6797\u4F7F\u7528\u7684\u4E4C\u9F99\u95EE\u9898\uFF0C\u4F46\u5229\u5DF4\u97E6\u6797\u7684\u6EE5\u7528\uFF0C\u7EDD\u4E0D\u53EA\u662F\u5728\u6CBB\u7597\u611F\u5192\u4E0A\u3002</p><p>\u53BB\u533B\u9662\uFF0C\u65E0\u8BBA\u662F\u57FA\u5C42\u793E\u533A\u533B\u9662\u8FD8\u662F\u4E09\u7EA7\u533B\u9662\uFF0C\u65E0\u8BBA\u662F\u7EFC\u5408\u533B\u9662\u8FD8\u662F\u513F\u7AE5\u4E13\u79D1\u533B\u9662\uFF0C\u65E0\u8BBA\u662F\u666E\u901A\u611F\u5192\u8FD8\u662F\u8F6E\u72B6\u75C5\u6BD2\u80A0\u708E\uFF0C\u8FD8\u662F\u666E\u901A\u578B\u624B\u8DB3\u53E3\u75C5\uFF0C\u90FD\u53EF\u80FD\u88AB\u5F00\u4E0A\u5229\u5DF4\u97E6\u6797\u3002\u867D\u7136\u5FAE\u535A\u4E0A\u5F88\u591A\u533B\u751F\u90FD\u8BF4\u8FC7\u8FD9\u4E2A\u95EE\u9898\uFF0C\u4F46\u9274\u4E8E\u5176\u6EE5\u7528\u7A0B\u5EA6\uFF0C\u6709\u5FC5\u8981\u518D\u63D0\u9192\u4E00\u4E0B\u5927\u5BB6\u3002</p><p>\u6839\u636E\u5317\u4EAC\u513F\u7AE5\u533B\u9662\u5BF9\u533B\u9662\u91CC2007\u5E74\u4E0B\u534A\u5E74\u7684\u95E8\u8BCA\u5904\u65B9\u62BD\u6837\u5206\u6790\uFF0C12.82%\u7684\u5904\u65B9\u91CC\u542B\u6709\u5229\u5DF4\u97E6\u6797\uFF0C\u5176\u4E2D\u8D85\u8FC781.17%\u662F\u9488\u5242\uFF0C\u5C06\u8FD170%\u662F\u7528\u4E8E3\u5C81\u4EE5\u4E0B\u7684\u5B69\u5B50\uFF0C\u540C\u65F6\u5408\u7528\u6297\u83CC\u7D20\u7684\u536085.80%\u3002\u5317\u4EAC\u513F\u7AE5\u533B\u9662\u4F5C\u4E3A\u56FD\u5185\u513F\u7AE5\u4E13\u79D1\u533B\u9662\u7684\u7FD8\u695A\uFF0C\u4F7F\u7528\u60C5\u51B5\u662F\u5982\u6B64\uFF0C\u5176\u4ED6\u4E13\u79D1\u533B\u9662\uFF0C\u7EFC\u5408\u533B\u9662\uFF0C\u4EE5\u53CA\u66F4\u57FA\u5C42\u7684\u533B\u9662\u5C31\u53EF\u60F3\u800C\u77E5\u3002</p><p>\u5229\u5DF4\u97E6\u6797\u4E4B\u6240\u4EE5\u88AB\u5982\u6B64\u5E7F\u6CDB\u7684\u4F7F\u7528\uFF0C\u539F\u56E0\u53EF\u80FD\u662F\u5229\u5DF4\u97E6\u6797\u53E6\u4E00\u4E2A\u540D\u5B57\u53EB\u201C\u75C5\u6BD2\u5511\u201D\uFF0C\u4E0D\u77E5\u9053\u5F53\u521D\u8C01\u7ED9\u5B83\u53D6\u4E86\u8FD9\u4E48\u4E2A\u540D\u5B57\uFF0C\u6240\u4EE5\u5F88\u591A\u533B\u751F\u628A\u5B83\u5F53\u505A\u4E00\u4E2A\u65E0\u6240\u4E0D\u80FD\u7684\u6297\u75C5\u6BD2\u836F\u7269\uFF0C\u65E0\u8BBA\u662F\u547C\u5438\u9053\u3001\u6D88\u5316\u9053\u8FD8\u662F\u5176\u4ED6\u90E8\u4F4D\uFF0C\u53EA\u8981\u6000\u7591\u75C5\u6BD2\u611F\u67D3\uFF0C\u5C31\u53EF\u80FD\u4F1A\u7528\u4E0A\u5B83\u3002</p><p>\u4F53\u5916\u8BD5\u9A8C\u5229\u5DF4\u97E6\u6797\u786E\u5B9E\u6709\u8F83\u5E7F\u6CDB\u7684\u6297\u75C5\u6BD2\u4F5C\u7528\uFF0C\u4F46\u4EBA\u4F53\u8BD5\u9A8C\u8BC1\u5B9E\u6709\u6548\u7684\u75BE\u75C5\u5E76\u4E0D\u591A\uFF08\u56FD\u5185\u7684\u7814\u7A76\u9664\u5916\uFF0C\u867D\u7136\u51E0\u4E4E\u6240\u6709\u7684\u6743\u5A01\u533B\u7597\u673A\u6784\u90FD\u8BA4\u4E3A\u666E\u901A\u611F\u5192\u548C\u624B\u8DB3\u53E3\u75C5\u4E0D\u9700\u8981\u6297\u75C5\u6BD2\u6CBB\u7597\uFF0C\u4F46\u4E0A\u6D77\u7684\u51E0\u5BB6\u533B\u9662\uFF0C\u5305\u62EC\u4E00\u5BB6\u513F\u7AE5\u4E13\u79D1\u533B\u9662\uFF0C\u7528\u4E0A\u6D77\u4E00\u5BB6\u836F\u5382\u751F\u4EA7\u7684\u5229\u5DF4\u97E6\u6797\u55B7\u96FE\u5242\u505A\u4E86\u513F\u7AE5\u4E0A\u547C\u5438\u9053\u75C5\u6BD2\u6027\u611F\u67D3\u548C\u624B\u8DB3\u53E3\u75C5\u7684\u4E34\u5E8A\u7814\u7A76\uFF0C\u8BA4\u4E3A\u6709\u6548\uFF0C\u4E0D\u826F\u53CD\u5E94\u5C0F\u3002\uFF09\uFF0C\u5728\u56FD\u5916\u6279\u51C6\u4F7F\u7528\u5229\u5DF4\u97E6\u6797\u7684\u75BE\u75C5\u5F88\u5C11\u3002</p><p>\u53E3\u670D\u7684\u5229\u5DF4\u97E6\u6797\u5728\u7F8E\u56FD\u88AB\u6279\u51C6\u7528\u4E8E\u6CBB\u75973\u5C81\u4EE5\u4E0A\u6CA1\u7ECF\u8FC7\u03B1-\u5E72\u6270\u7D20\u6CBB\u7597\u7684\u6162\u6027\u4E19\u578B\u809D\u708E\uFF0C\u4F46\u5FC5\u987B\u8054\u5408\u5E72\u6270\u7D20\u624D\u6709\u6548\uFF0C\u800C\u4E14\u5F88\u591A\u75C5\u4EBA\u56E0\u4E3A\u526F\u4F5C\u7528\u800C\u4E2D\u9014\u505C\u836F\u3002\u5229\u5DF4\u97E6\u6797\u55B7\u96FE\u5242\u88AB\u6279\u51C6\u7528\u4E8E\u6CBB\u7597\u513F\u7AE5\u547C\u5438\u9053\u5408\u80DE\u75C5\u6BD2\u5F15\u8D77\u7684\u91CD\u5EA6\u80BA\u708E\uFF0C\u4F46\u6709\u4E89\u8BAE\uFF0C\u5C0F\u89C4\u6A21\u7684\u7814\u7A76\u8BA4\u4E3A\u5229\u5DF4\u97E6\u6797\u53EF\u4EE5\u964D\u4F4E\u8FD9\u7C7B\u75C5\u4EBA\u7684\u4E0A\u547C\u5438\u673A\u65F6\u95F4\uFF0C\u4F46\u5E76\u4E0D\u80FD\u6539\u5584\u8FDC\u671F\u80BA\u529F\u80FD\uFF0C\u7F8E\u56FD\u513F\u79D1\u5B66\u4F1A\u7684\u610F\u89C1\u662F\u533B\u751F\u53EF\u4EE5\u6839\u636E\u81EA\u5DF1\u504F\u597D\u81EA\u884C\u9009\u62E9\u662F\u5426\u5BF9\u547C\u5438\u9053\u5408\u80DE\u75C5\u6BD2\u80BA\u708E\u91CD\u75C7\u75C5\u4F8B\u4F7F\u7528\u5229\u5DF4\u97E6\u6797\u55B7\u96FE\u5242\u3002\u5728\u4E16\u754C\u536B\u751F\u7EC4\u7EC7\u7684\u513F\u7AE5\u57FA\u672C\u836F\u7269\u793A\u8303\u540D\u5355\u91CC\uFF0C\u5229\u5DF4\u97E6\u6797\u552F\u4E00\u7684\u6307\u5F81\u662F\u7528\u4E8E\u6CBB\u7597\u75C5\u6BD2\u6027\u51FA\u8840\u70ED\uFF08\u4E00\u7EC4\u75C5\u6BD2\u6027\u75AB\u6E90\u75C5\uFF09\u3002</p><p>\uFF08http://apps.who.int/iris/bitstream/10665/93143/1/EMLc_4_eng.pdf?ua=1\uFF09</p><p>\u5728\u4E4B\u524D\u7684\u6587\u7AE0\u91CC\u6211\u4E5F\u8BF4\u8FC7\uFF0C\u56FD\u5916\u5229\u5DF4\u97E6\u6797\u7684\u4F7F\u7528\u6307\u5F81\u4E4B\u6240\u4EE5\u8FD9\u4E48\u4E25\uFF0C\u662F\u56E0\u4E3A\u4E34\u5E8A\u6548\u679C\u8FD8\u4E0D\u90A3\u4E48\u786E\u5B9A\u800C\u526F\u4F5C\u7528\u6BD4\u8F83\u5927\u3002\u52A8\u7269\u5B9E\u9A8C\u663E\u793A\u5229\u5DF4\u97E6\u6797\u6709\u660E\u663E\u7684\u81F4\u7578\u6027\u548C\u81F4\u764C\u6027\uFF0C\u800C\u4E14\u5229\u5DF4\u97E6\u6797\u534A\u8870\u671F\u5F88\u957F\uFF0C\u7528\u836F\u540E\u53EF\u518D\u4F53\u5185\u5B58\u7559\u957F\u8FBE6\u4E2A\u6708\uFF0C\u6240\u4EE5\u5B55\u671F\u548C\u54FA\u4E73\u671F\u90FD\u4E0D\u80FD\u4F7F\u7528\uFF0C\u914D\u5076\u53CC\u65B9\u6709\u4E00\u4EBA\u7528\u4E86\u8FD9\u4E2A\u836F\uFF0C6\u4E2A\u6708\u5185\u90FD\u8981\u907F\u5B55\uFF0C\u6B64\u5916\u5229\u5DF4\u97E6\u6797\u53EF\u4EE5\u5BFC\u81F4\u6EB6\u8840\u6027\u8D2B\u8840\uFF0C\u5FC3\u80BA\u65B9\u9762\u7684\u526F\u4F5C\u7528\u3002\u5BF9\u4E8E\u5229\u5DF4\u97E6\u6797\uFF0CFDA\u4E0A\u6709\u4E00\u4E2A\u957F\u8FBE4\u9875\u7684\u7528\u836F\u6307\u5F15\uFF0C\u91CD\u70B9\u662F\u8BA9\u60A3\u8005\u77E5\u6653\u7528\u836F\u6CE8\u610F\u4E8B\u9879\u53CA\u5176\u526F\u4F5C\u7528\u3002</p><p>\uFF08http://www.fda.gov/downloads/drugs/drugsafety/ucm089017.pdf\uFF09</p><p>\u56FD\u5185\u5229\u5DF4\u97E6\u6797\u6709\u5404\u79CD\u5242\u578B\uFF0C\u5305\u62EC\u53E3\u670D\u7684\u9897\u7C92\u3001\u7247\u5242\u3001\u80F6\u56CA\u7B49\uFF0C\u8FD8\u6709\u6CE8\u5C04\u9488\u5242\u548C\u55B7\u96FE\u5242\uFF0C\u836F\u76D1\u90E8\u95E8\u6279\u51C6\u7684\u9002\u5E94\u75C7\u5F88\u5BBD\uFF0C\u9664\u4E86\u547C\u5438\u9053\u5408\u80DE\u75C5\u6BD2\u5F15\u8D77\u7684\u75C5\u6BD2\u6027\u80BA\u708E\u548C\u652F\u6C14\u7BA1\u708E\uFF0C\u76AE\u80A4\u75B1\u75B9\u75C5\u6BD2\u611F\u67D3\uFF08\u53E3\u670D\u53CA\u9488\u5242\uFF09\uFF0C\u8FD8\u53EF\u4EE5\u7528\u4E8E\u6D41\u884C\u6027\u611F\u5192\u7684\u9884\u9632\u548C\u6CBB\u7597\uFF08\u55B7\u5242\uFF09\uFF0C\u8FDE\u666E\u901A\u7684\u75C5\u6BD2\u6027\u4E0A\u547C\u5438\u9053\u611F\u67D3\u90FD\u53EF\u4EE5\u7528\uFF08\u55B7\u96FE\u5242\uFF09,\u800C\u8FD9\u4E9B\u6307\u5F81\uFF0C\u5E76\u6CA1\u6709\u53EF\u9760\u7684\u7814\u7A76\u8BC1\u636E\u652F\u6301\u3002</p><p>\u867D\u7136\u836F\u76D1\u90E8\u95E8\u6279\u51C6\u7684\u9002\u5E94\u5F81\u5DF2\u7ECF\u5F88\u5BBD\u4E86\uFF0C\u4F46\u5B9E\u9645\u4E34\u5E8A\u4E0A\u533B\u751F\u4EEC\u8FD8\u5E38\u5E38\u5728\u8D85\u9002\u5E94\u75C7\u7528\u836F\uFF0C\u6BD4\u5982\u4E0D\u5728\u9002\u7528\u5F81\u91CC\u7684\u8F6E\u72B6\u75C5\u6BD2\u80A0\u708E\uFF0C\u624B\u8DB3\u53E3\u75C5\u3002\u8FD9\u4E9B\u90FD\u662F\u513F\u79D1\u95E8\u8BCA\u7684\u5E38\u89C1\u75C5\u591A\u53D1\u75C5\uFF0C\u8FD9\u4E5F\u662F\u4E3A\u4EC0\u4E48\u8FDE\u5317\u4EAC\u513F\u7AE5\u533B\u9662\u8FD9\u6837\u7684\u56FD\u5185\u9876\u7EA7\u513F\u7AE5\u4E13\u79D1\u533B\u9662\uFF0C\u542B\u6709\u5229\u5DF4\u97E6\u6797\u7684\u95E8\u8BCA\u5904\u65B9\u90FD\u8D85\u8FC710%\u3002</p><p>\u4E00\u4E2A\u836F\u7528\u4E0A\u53BB\uFF0C\u5982\u679C\u6536\u76CA\u5927\u4E8E\u98CE\u9669\uFF0C\u800C\u4E14\u6536\u76CA\u98CE\u9669\u6BD4\u5728\u6240\u6709\u53EF\u9009\u836F\u7269\u91CC\u662F\u6700\u5927\u7684\uFF0C\u90A3\u81EA\u7136\u53EF\u4EE5\u7528\uFF0C\u5982\u679C\u7528\u4E86\u4E0D\u4F46\u6CA1\u7528\uFF0C\u82B1\u94B1\u4E0D\u8BF4\uFF0C\u53CD\u800C\u53EF\u80FD\u9020\u6210\u4E00\u4E9B\u526F\u4F5C\u7528\uFF0C\u90A3\u81EA\u7136\u662F\u6EE5\u7528\uFF0C\u5C24\u5176\u662F\u5BF9\u666E\u901A\u611F\u5192\u3001\u8F6E\u72B6\u75C5\u6BD2\u80A0\u708E\u3001\u624B\u8DB3\u53E3\u75C5\u8FD9\u6837\u57FA\u672C\u9760\u81EA\u6108\uFF0C\u5076\u5C14\u9700\u5BF9\u75C7\u6CBB\u7597\u7684\u75BE\u75C5\u3002</p><p>\u5229\u5DF4\u97E6\u6797\u662F\u5904\u65B9\u836F\uFF0C\u6EE5\u7528\u7684\u4E3B\u8981\u8D23\u4EFB\u5728\u836F\u76D1\u90E8\u95E8\u548C\u6574\u4E2A\u533B\u7597\u4F53\u7CFB\uFF0C\u5BB6\u957F\u4EEC\u65E0\u6CD5\u6539\u53D8\u836F\u76D1\u90E8\u95E8\u548C\u81EA\u5DF1\u6240\u9762\u5BF9\u7684\u533B\u7597\u4F53\u7CFB\uFF0C\u4E5F\u6CA1\u529E\u6CD5\u5224\u65AD\u54EA\u4E9B\u60C5\u51B5\u4E0D\u9700\u8981\u7528\u5229\u5DF4\u97E6\u6797\u3002\u4F46\u4F60\u53EF\u4EE5\u77E5\u9053\uFF1A</p><p>\u5229\u5DF4\u97E6\u6797\u5728\u56FD\u5916\u4E3B\u8981\u7528\u4E8E\u6CBB\u7597\u6162\u6027\u4E19\u578B\u809D\u708E\u548C\u51FA\u8840\u70ED\uFF0C\u91CD\u75C7\u547C\u5438\u9053\u5408\u80DE\u75C5\u6BD2\u80BA\u708E\u4F4F\u9662\u65F6\u53EF\u4EE5\u9009\u7528\u3002\u5982\u679C\u56E0\u4E3A\u611F\u5192\u62C9\u809A\u5B50\u624B\u8DB3\u53E3\u75C5\uFF0C\u95E8\u8BCA\u533B\u751F\u7ED9\u5B69\u5B50\u5F00\u8FD9\u4E2A\u836F\uFF0C\u4F60\u53EF\u4EE5\u6E29\u548C\u5730\u63D0\u9192\u533B\u751F\u4E0D\u60F3\u7528\u8FD9\u4E2A\u836F\uFF0C\u4F60\u4E5F\u53EF\u4EE5\u8F6C\u53D1\u8FD9\u7BC7\u6587\u7AE0\u8BA9\u66F4\u591A\u5BB6\u957F\u77E5\u9053\u3002</p><p>\u6587\u4E2D\u63D0\u5230\u768412.8%\u4F7F\u7528\u7387\u662F2007\u5E74\u7684\u6570\u636E\uFF0C\u5982\u679C\u4F60\u5E26\u5B69\u5B50\u53BB\u770B\u8FC7\u75C5\uFF0C\u53EF\u4EE5\u53C2\u4E0E\u4E0B\u9762\u8FD9\u4E2A\u5C0F\u8C03\u67E5\uFF0C\u5927\u6982\u4E86\u89E3\u4E00\u4E0B\u5229\u5DF4\u97E6\u6797\u4F7F\u7528\u7684\u73B0\u51B5\u3002</p>',16),h={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=204405464&idx=1&sn=608cea0fa25f2fa7915716c0d8e07139&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},l=s("\u67E5\u770B\u539F\u6587");function f(m,u){const p=a("ExternalLinkIcon");return t(),r(o,null,[_,e("p",null,[e("a",h,[l,n(p)])])],64)}var w=d(i,[["render",f]]);export{w as default};
