import{r as a,o as r,c as t,a as e,b as n,F as o,d as c,e as s}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const i={},_=c('<h3 id="\u628A\u4FBF\u6709\u591A\u5C11\u5371\u5BB3" tabindex="-1"><a class="header-anchor" href="#\u628A\u4FBF\u6709\u591A\u5C11\u5371\u5BB3" aria-hidden="true">#</a> \u628A\u4FBF\u6709\u591A\u5C11\u5371\u5BB3\uFF1F</h3><p>\u4ECE\u5F00\u59CB\u5199\u80B2\u513F\u79D1\u666E\u4EE5\u6765\uFF0C\u5F88\u591A\u4EBA\u547C\u5401\u6211\u5199\u5199\u628A\u5C4E\u628A\u5C3F\uFF0C\u5305\u62EC\u5A92\u4F53\u4E5F\u7EA6\u8FC7\u7A3F\uFF0C\u5927\u6982\u662F\u56E0\u4E3A\u8FD9\u4E2A\u73B0\u8C61\u56FD\u5185\u592A\u666E\u904D\u4E86\u3002\u6211\u4E00\u76F4\u6CA1\u5199\u4E0D\u662F\u56E0\u4E3A\u4E0D\u60F3\u5199\uFF0C\u800C\u662F\u8FD9\u4E2A\u95EE\u9898\u76EE\u524D\u6CA1\u6709\u5F88\u597D\u76F8\u5173\u7814\u7A76\uFF0C\u6709\u54EA\u4E9B\u597D\u5904\uFF1F\u6709\u54EA\u4E9B\u5371\u5BB3\uFF1F\u76EE\u524D\u6CA1\u6709\u76F4\u63A5\u6570\u636E\uFF0C\u4E0D\u597D\u505A\u7ED3\u8BBA\u3002</p><p>\u6CA1\u6709\u6709\u8D28\u91CF\u7684\u7814\u7A76\uFF0C\u4E5F\u6CA1\u6709\u76F8\u5173\u6570\u636E\uFF0C\u4F46\u5E76\u4E0D\u59A8\u788D\u4E00\u4E9B\u4E13\u5BB6\u4F20\u64AD\u4E00\u4E9B\u4EE4\u4EBA\u60CA\u6050\u7684\u89C2\u70B9\uFF0C\u628A\u5C4E\u628A\u5C3F\u4F1A\u5BFC\u81F4\u4FBF\u79D8\uFF0C\u809B\u88C2\u3001\u75D4\u75AE\u3001\u76F4\u80A0\u8131\u5782\uFF0C\u4F1A\u5BFC\u81F4\u5C3F\u5E8A\u3001\u5FC3\u7406\u95EE\u9898\u7B49\u7B49\uFF0C\u4F46\u8FD9\u4E9B\u89C2\u70B9\u5E76\u6CA1\u6709\u4F9D\u636E\uFF0C\u66F4\u591A\u4EBA\u662F\u5148\u5165\u4E3A\u4E3B\u7684\u628A\u201C\u628A\u4FBF\u201D\u8BA4\u5B9A\u4E3A\u4E00\u79CD\u5F3A\u5236\u7684\u8FC7\u65E9\u6392\u4FBF\u8BAD\u7EC3\u65B9\u5F0F\uFF0C\u7136\u540E\u628A\u5F3A\u884C\u7684\u5982\u5395\u8BAD\u7EC3\u4EA7\u751F\u7684\u95EE\u9898\u90FD\u52A0\u5230\u4E86\u201C\u628A\u4FBF\u201D\u5934\u4E0A\uFF0C\u7136\u540E\u8D34\u4E0A\u201C\u964B\u4E60\u201D\u7684\u6807\u7B7E\u3002</p><p>\u73B0\u5B9E\u662F\u201C\u628A\u4FBF\u201D\u5728\u56FD\u5185\u5B9E\u5728\u592A\u666E\u904D\uFF0C\u5F88\u591A\u662F\u4ECE\u5B69\u5B50\u51E0\u4E2A\u661F\u671F\u751A\u81F3\u521A\u51FA\u751F\u5C31\u5F00\u59CB\uFF0C\u4ECA\u5929\u6211\u4EEC\u8FD9\u4E9B\u505A\u7236\u6BCD\u7684\u5F53\u5E74\u5927\u6982\u90FD\u662F\u8FD9\u6837\u88AB\u628A\u5927\u7684\uFF0C\u56E0\u4E3A\u8FD9\u662F\u6211\u4EEC\u7684\u4F20\u7EDF\uFF0C\u4EE5\u524D\u6CA1\u6709\u5C3F\u4E0D\u6E7F\uFF0C\u5E2E\u5B9D\u9002\u8FD9\u4E9B\u4E1C\u897F\u662F\u76F4\u5230\u4E0A\u4E16\u7EAA\u4E94\u516D\u5341\u5E74\u4EE3\u624D\u5F00\u59CB\u5728\u897F\u65B9\u56FD\u5BB6\u9762\u5E02\u7684[1]\uFF0C\u800C\u6211\u56FD\u6539\u9769\u5F00\u653E\u624D\u4E09\u5341\u51E0\u5E74\uFF0C\u81F3\u5C11\u6211\u5C0F\u65F6\u5019\u8FD8\u6CA1\u89C1\u8FC7\u3002</p><p>\u5230\u4E86\u6211\u4EEC\u81EA\u5DF1\u505A\u7236\u6BCD\u7684\u4ECA\u5929\uFF0C\u6211\u4EEC\u7684\u5B69\u5B50\u8FD8\u662F\u53EF\u80FD\u96BE\u514D\u4E8E\u88AB\u201C\u628A\u4FBF\u201D\uFF0C\u56E0\u4E3A\u4F20\u7EDF\u5EF6\u7EED\u4E0B\u6765\u5F88\u96BE\u4E00\u4E0B\u6539\u53D8\uFF0C\u5F88\u591A\u5E74\u8F7B\u7684\u7236\u6BCD\u662F\u5B8C\u5168\u9075\u4ECE\u957F\u8F88\u7684\u6307\u5BFC\u6765\u5E26\u5B69\u5B50\uFF0C\u63A5\u53D7\u4E86\u65B0\u89C2\u5FF5\u7684\u7236\u6BCD\uFF0C\u81EA\u5DF1\u4E5F\u672A\u5FC5\u6709\u65F6\u95F4\u5168\u5929\u5B88\u7740\u5B69\u5B50\u800C\u4E0D\u9700\u8981\u8001\u4EBA\u3001\u4FDD\u59C6\u5E2E\u5FD9\uFF0C\u6240\u4EE5\u5B8C\u5168\u907F\u514D\u5B69\u5B50\u88AB\u628A\u4FBF\u771F\u7684\u5F88\u96BE\uFF0C\u4E07\u4E00\u5B69\u5B50\u771F\u88AB\u201C\u628A\u4FBF\u201D\u4E86\u4F1A\u6709\u90A3\u4E48\u591A\u5371\u5BB3\u5417\uFF1F</p><p>\u5728\u5206\u6790\u628A\u4FBF\u7684\u5371\u5BB3\u4E4B\u524D\uFF0C\u6211\u4EEC\u5148\u6765\u660E\u786E\u4E00\u4E0B\u4EC0\u4E48\u662F\u628A\u4FBF\uFF0C\u5982\u679C\u6211\u6CA1\u641E\u9519\uFF0C\u628A\u4FBF\u5927\u6982\u662F\u8FD9\u4E2A\u6837\u5B50\uFF1A\u5BB6\u957F\u4E24\u4E2A\u624B\u5206\u522B\u63E1\u7740\u5B69\u5B50\u7684\u5927\u817F\uFF0C\u7136\u540E\u7A0D\u5206\u5F00\uFF0C\u66B4\u9732\u5B69\u5B50\u7684\u809B\u95E8\u548C\u751F\u6B96\u5668\uFF0C\u8BA9\u5B69\u5B50\u534A\u5750\u4E8E\u5927\u4EBA\u7684\u819D\u4E0A\uFF0C\u540C\u65F6\u53D1\u51FA\u5618\u5618\u58F0\u6216\u8005\u55EF\u55EF\u58F0\u9F13\u52B1\u5B69\u5B50\u6392\u5C3F\u6216\u8005\u6392\u4FBF\u3002\u628A\u4FBF\u548C\u76EE\u524D\u6743\u5A01\u673A\u6784\u4E3B\u5F20\u7684\u5982\u5395\u8BAD\u7EC3\u7684\u533A\u522B\u4E3B\u8981\u6709\u4E0B\u8868\u7684\u4E24\u4E2A\u3002</p><p>\u5F88\u591A\u4EBA\u4EE5\u4E3A\u628A\u4FBF\u662F\u4E2D\u56FD\u7279\u8272\uFF0C\u5176\u5B9E\u5728\u4E16\u754C\u8303\u56F4\u5185\uFF0C\u5305\u62EC\u975E\u6D32\u3001\u5357\u4E9A\u3001\u5357\u7F8E\u8FD9\u4E9B\u4E0D\u53D1\u8FBE\u5730\u533A\u90FD\u662F\u6BD4\u8F83\u5E38\u89C1\u7684[2]\u3002\u867D\u7136\u662F\u56FD\u5185\u666E\u904D\u7684\u73B0\u8C61\uFF0C\u53EF\u60DC\u7684\u662F\u6211\u4EEC\u5E76\u6CA1\u6709\u4EBA\u6DF1\u5165\u7814\u7A76\u8FC7\u8FD9\u4E2A\u4E8B\u60C5\u7684\u5229\u5F0A\uFF0C\u5012\u662F\u7F8E\u56FD\u4EBA\u8FD8\u771F\u7814\u7A76\u8FC7\uFF0C\u4E0D\u8FC7\u4E0D\u662F\u5728\u6211\u4EEC\u56FD\u5BB6\u800C\u662F\u5728\u4E1C\u975E\u3002\u4E0B\u56FE\u8FD9\u4E2A\u59FF\u52BF\u662F\u4E0D\u662F\u5F88\u4EB2\u5207\uFF1F\u8FD9\u6837\u63E1\u7740\u5B69\u5B50\u7684\u53CC\u817F\uFF0C\u6446\u653E\u597D\u59FF\u52BF\uFF0C\u7136\u540E\u5634\u91CC\u53D1\u51FA\u201CShuus\u201D\u7684\u58F0\u97F3\uFF0C\u662F\u4E0D\u662F\u8FDE\u58F0\u97F3\u90FD\u8DDF\u6211\u4EEC\u5F88\u50CF\uFF1F</p><p>\uFF08\u56FE\u7247\u6765\u81EAPEDIATRICS August 1, 1977\uFF09</p><p>\u4ECE\u5B69\u5B50\u51FA\u751F\u540E\u51E0\u5468\u5F00\u59CB\uFF0C\u8FD9\u4E9B\u5988\u5988\u901A\u8FC7\u89C2\u5BDF\u5B69\u5B50\u7684\u80A2\u4F53\u52A8\u4F5C\u3001\u8868\u60C5\u3001\u58F0\u97F3\u7B49\u6765\u5224\u5B9A\u5B69\u5B50\u662F\u4E0D\u662F\u60F3\u6392\u5C3F\u6392\u4FBF\uFF0C\u4E00\u65E6\u53D1\u73B0\u6709\u60F3\u6392\u4FBF\u7684\u8FF9\u8C61\uFF0C\u5C31\u5F00\u59CB\u628A\u4FBF\uFF0C\u901A\u8FC7\u8FD9\u79CD\u65B9\u5F0F\uFF0C\u6700\u521D\u68C0\u67E534\u4E2A\u5B69\u5B50\u91CC\u670930\u4E2A\u57284-6\u4E2A\u6708\u5C31\u8BAD\u7EC3\u5F97\u5F88\u6210\u529F\uFF0C\u968F\u8BBF\u52305\u4E2A\u6708\u768416\u4E2A\u5B69\u5B50\u91CC\u670910\u4E2A\u53EF\u4EE5\u767D\u5929\u4E0D\u5C3F\u6E7F\uFF0C\u665A\u4E0A\u4E5F\u5F88\u5C11\u5C3F\u5E8A\uFF0C\u5230\u4E861\u5C81\u540E\u5B69\u5B50\u80FD\u8D70\u4E4B\u540E\uFF0C\u80FD\u5230\u751F\u6D3B\u533A\u57DF\u5916\u6392\u4FBF\u3002\u8FD9\u4E2A\u7814\u7A76\u662F1977\u5E74\u53D1\u8868\u5728\u7F8E\u56FD\u300A\u513F\u79D1\u5B66\u300B\u6742\u5FD7\u4E0A\u7684\u6587\u7AE0[3]\u3002</p><p>\u4F5C\u8005\u53D1\u73B0\u8FD9\u79CD\u201C\u628A\u4FBF\u201D\u5B69\u5B50\u5E76\u4E0D\u662F\u6D88\u6781\u88AB\u52A8\u7684\u88AB\u5F3A\u8FEB\u6392\u4FBF\uFF0C\u800C\u662F\u6BCD\u4EB2\u6CE8\u610F\u5230\u5B69\u5B50\u6392\u4FBF\u7684\u610F\u613F\u540E\u7136\u540E\u5E2E\u52A9\u4ED6\uFF0C\u6392\u5B8C\u4FBF\u540E\u901A\u8FC7\u5582\u5976\u3001\u4EB2\u6635\u6216\u5176\u4ED6\u5F00\u5FC3\u7684\u6D3B\u52A8\u6765\u5956\u8D4F\u9F13\u52B1\u4ED6\uFF0C\u901A\u8FC7\u8FD9\u79CD\u91CD\u590D\u7684\u52A8\u4F5C\u5B69\u5B50\u628A\u8FD9\u79CD\u58F0\u97F3\u3001\u7279\u6B8A\u4F53\u4F4D\u548C\u6392\u4FBF\u5173\u8054\u8D77\u6765\u4E86\uFF0C\u6162\u6162\u5B9E\u73B0\u81EA\u4E3B\u63A7\u5236\u6392\u4FBF\uFF0C\u8FD9\u4E2A\u7ED3\u679C\u5176\u5B9E\u662F\u6BCD\u5A74\u4E92\u52A8\u914D\u5408\u7684\u7ED3\u679C\u3002</p><p>\u5B69\u5B50\u8FD9\u4E48\u5C0F\u5C31\u80FD\u914D\u5408\u5927\u4EBA\u7684\u6307\u5F15\u81EA\u4E3B\u63A7\u5236\u6392\u5C3F\u6392\u4FBF\uFF0C\u663E\u7136\u8BA9\u4F5C\u8005\u611F\u5230\u5F88\u65B0\u5947\uFF0C\u56E0\u4E3A\u8FD9\u548C\u7F8E\u56FD\u7684\u4E3B\u6D41\u7684\u505A\u6CD5\u548C\u89C2\u70B9\u662F\u76F8\u6096\u7684\u3002\u5F53\u65F6\u7F8E\u56FD\u7684\u4E3B\u6D41\u7684\u5982\u5395\u8BAD\u7EC3\u65B9\u6CD5\u548C\u4ECA\u5929\u7684\u662F\u57FA\u672C\u4E00\u81F4\u7684\uFF0C\u5F3A\u8C03\u7684\u662F\u201C\u4EE5\u5B69\u5B50\u4E3A\u4E2D\u5FC3\u201D\uFF0C\u4E5F\u5C31\u662F\u8981\u7B49\u5B69\u5B50\u8EAB\u4F53\u548C\u5FC3\u7406\u90FD\u51C6\u5907\u597D\u4E86\u624D\u5F00\u59CB\uFF0C\u901A\u5E38\u8BA4\u4E3A\u898118\u4E2A\u6708\u4EE5\u540E\u624D\u80FD\u8FBE\u5230\u8981\u6C42\uFF0C\u8FD9\u4E2A\u8BAD\u7EC3\u8FC7\u7A0B\u4E5F\u662F\u8981\u6301\u7EED\u51E0\u4E2A\u6708\u751A\u81F3\u4E00\u4E24\u5E74\uFF0C\u5F88\u591A\u5B69\u5B50\u8981\u52303-4\u5C81\u624D\u80FD\u5B8C\u6210\u3002</p><p>\u8FD9\u4E5F\u8BA9\u4F5C\u8005\u610F\u8BC6\u5230\u5E76\u4E0D\u662F\u6240\u6709\u7684\u65E9\u671F\u6392\u4FBF\u8BAD\u7EC3\u90FD\u662F\u65E0\u6548\u548C\uFF08\u6216\uFF09\u5F3A\u8FEB\u7684\uFF0C\u6210\u719F\u7684\u65F6\u673A\u548C\u201C\u51C6\u5907\u597D\u4E86\u201C\u7684\u89C2\u5FF5\u9700\u8981\u8003\u8651\u5177\u4F53\u7684\u6587\u5316\u3001\u517B\u80B2\u73AF\u5883\u3002\u8FD9\u7BC7\u6587\u7AE0\u53D1\u8868\u540E\u8FD8\u6709\u4E24\u4E2A\u533B\u751F\u4E13\u95E8\u7ED9\u6742\u5FD7\u5199\u4FE1\u8868\u793A\u8D5E\u540C[4\uFF0C5]\uFF0C\u8FC4\u4ECA\u4E3A\u6B62\u5F15\u7528\u8FD9\u7BC7\u6587\u7AE0\u7684\u7EFC\u8FF0\u4E0D\u5C11\uFF0C\u5012\u6CA1\u89C1\u8FC7\u8C01\u6279\u5224\u8FC7\u8FD9\u7BC7\u6587\u7AE0\u7684\u89C2\u70B9\u3002</p><p>\u53EF\u80FD\u6709\u4EBA\u8BA4\u4E3A\u8FD9\u90FD\u662F1977\u5E74\u7684\u7814\u7A76\u65E9\u843D\u4F0D\u4E86\uFF0C\u4F46\u662F\u8BF7\u6CE8\u610F\u53E6\u4E00\u70B9\uFF0C\u4ECA\u5929\u7F8E\u56FD\u513F\u79D1\u5B66\u4F1A\u5982\u5395\u8BAD\u7EC3\u7684\u89C2\u70B9\u8FD8\u4E3B\u8981\u6765\u6E90\u4E8E1962\u5E74\u7684\u7814\u7A76[6]\u3002\u5F53\u7136\u8DDF1962\u5E74\u7684\u90A3\u4E2A\u7814\u7A76\u76F8\u6BD4\uFF0C\u524D\u8005\u7684\u6837\u672C\u5C11\u5F88\u591A\uFF0C\u89C2\u5BDF\u968F\u8BBF\u65F6\u95F4\u4E5F\u6BD4\u8F83\u77ED\uFF0C\u8BC4\u4EF7\u6307\u6807\u4E5F\u5F88\u7C97\u7565\uFF0C\u8FD9\u6216\u8BB8\u4E5F\u662F\u65E9\u671F\u8BAD\u7EC3\u7684\u89C2\u70B9\u6CA1\u6709\u6210\u4E3A\u4E3B\u6D41\u7684\u4E00\u4E2A\u539F\u56E0\u3002</p><p>\u56E0\u4E3A\u5C3F\u4E0D\u6E7F\u5F88\u65B9\u4FBF\uFF0C\u5373\u4FBF\u5B69\u5B50\u5DF2\u7ECF\u5177\u5907\u5982\u5395\u8BAD\u7EC3\u7684\u6761\u4EF6\uFF0C\u5F88\u591A\u5BB6\u957F\u90FD\u61D2\u5F97\u4E3B\u52A8\u53BB\u7ED9\u5B69\u5B50\u8BAD\u7EC3\uFF0C\u4E09\u56DB\u5C81\u8FD8\u79BB\u4E0D\u5F00\u5C3F\u4E0D\u6E7F\u5F88\u5E38\u89C1\uFF0C\u8FD9\u5BFC\u81F4\u56FD\u5916\u4E00\u4E9B\u4EBA\u5F00\u59CB\u53CD\u601D\u73B0\u884C\u7684\u5982\u5395\u8BAD\u7EC3\u65B9\u6CD5\u662F\u5426\u5408\u9002[7]\uFF0C\u548C\u56FD\u5185\u5996\u9B54\u5316\u201C\u628A\u4FBF\u201D\u76F8\u53CD\u7684\u662F\uFF0C\u897F\u65B9\u56FD\u5BB6\u53CD\u800C\u5F00\u59CB\u5BF9\u201C\u628A\u4FBF\u201D\u4E4B\u7C7B\u5173\u6CE8\u5B69\u5B50\u6392\u4FBF\u4FE1\u53F7\u7136\u540E\u8F85\u52A9\u6392\u4FBF\u7684\u8BAD\u7EC3\u65B9\u5F0F\u611F\u5174\u8DA3\u4E86[8]\uFF0C\u6BD4\u5982\u4E0B\u56FE\u91CC\u7684\u8FD9\u4E9B\u3002</p><p>(\u56FE\u7247\u6765\u81EAEvol Psychol January 2007\uFF09</p><p>\u5BF9\u65E9\u671F\u5982\u5395\u8BAD\u7EC3\u4E5F\u5F00\u59CB\u6709\u4E9B\u7814\u7A76\u4E86\uFF0C\u610F\u5927\u5229\u4EBA\u7684\u4E00\u9879\u7814\u7A76\u91CC\u5BF9286\u4E2A\u5BB6\u5EAD\u7684\u95EE\u5377\u8C03\u67E5\uFF0C\u53D1\u73B0\u5728\u59346\u4E2A\u6708\u5185\u5F00\u59CB\u201C\u628A\u4FBF\u201D\u7684\u5B69\u5B50\u6BD4\u90A3\u4E9B\u665A\u5F00\u59CB\u8BAD\u7EC3\u7684\u5B69\u5B50\u66F4\u65E9\u8BAD\u7EC3\u6210\u529F\uFF0C\u800C\u4E14\u6CA1\u6709\u89C2\u5BDF\u5230\u660E\u663E\u7684\u526F\u4F5C\u7528[9]\u3002</p><p>\u4F46\u5C11\u6570\u7684\u8D28\u91CF\u4E0D\u9AD8\u7684\u7814\u7A76\u663E\u7136\u8FD8\u4E0D\u80FD\u5F88\u5145\u5206\u8BC1\u660E\u201C\u628A\u4FBF\u201D\u662F\u6709\u6548\u800C\u65E0\u5371\u5BB3\u7684\uFF0C\u4F46\u81F3\u5C11\u76EE\u524D\u5728\u6211\u56FD\u8FD8\u662F\u4E00\u79CD\u5E7F\u6CDB\u91C7\u7528\u800C\u4E14\u5177\u6709\u53EF\u64CD\u4F5C\u6027\u7684\u65B9\u6CD5\u3002\u5728\u6CA1\u6709\u76F4\u63A5\u8BC1\u636E\u7684\u65F6\u5019\u6211\u4EEC\u6839\u636E\u628A\u4FBF\u7684\u7279\u70B9\u5206\u6790\u4E00\u4E0B\u3002</p><p>\u5F88\u591A\u4EBA\u6279\u8BC4\u201C\u628A\u4FBF\u201D\u65F6\u8BA4\u4E3A\u8FD9\u662F\u5F3A\u8FEB\u5B69\u5B50\u6392\u4FBF\uFF0C\u4F46\u6B63\u5982\u56FE\u7247\u91CC\u975E\u6D32\u90A3\u4E2A\u88AB\u628A\u4FBF\u7684\u5B69\u5B50\u4E00\u6837\uFF0C\u6211\u5728\u533B\u9662\u91CC\u770B\u5230\u88AB\u628A\u4FBF\u7684\u5B69\u5B50\u5F88\u5C11\u6709\u54ED\u95F9\uFF0C\u56E0\u4E3A\u5F88\u5C11\u89C1\u5230\u7236\u6BCD\u975E\u8981\u5F3A\u884C\u8BA9\u8FD9\u4E48\u5C0F\u7684\u5B69\u5B50\u6392\u4FBF\uFF0C\u5927\u90E8\u5206\u662F\u89C9\u5F97\u5B69\u5B50\u60F3\u6392\u4FBF\u65F6\u5019\u5C31\u628A\u4E00\u4E0B\uFF0C\u6CA1\u6709\u5C31\u7B97\u4E86\uFF0C\u628A\u4FBF\u4E0D\u7B49\u4E8E\u5F3A\u8FEB\u6392\u4FBF\u3002</p><p>\u628A\u4FBF\u662F\u4E00\u79CD\u5F88\u65E9\u5C31\u5F00\u59CB\u7684\u5982\u5395\u8BAD\u7EC3\uFF0C\u6709\u4E9B\u4EBA\u8BA4\u4E3A\u8FC7\u65E9\u8BAD\u7EC3\u4F1A\u5BFC\u81F4\u4FBF\u79D8\u7B49\u95EE\u9898\uFF0C\u4F46\u8FD9\u662F\u65E0\u6839\u636E\u7684\u731C\u6D4B\uFF0C\u56FD\u5185\u628A\u4FBF\u8FD9\u4E48\u5E38\u89C1\uFF0C\u4F46\u513F\u7AE5\u4FBF\u79D8\u7684\u53D1\u75C5\u7387\u7EA64.73%\uFF0C\u5E76\u4E0D\u6BD4\u5F88\u5C11\u6709\u628A\u4FBF\u7684\u53D1\u8FBE\u56FD\u5BB6\u9AD8\u3002\u9664\u4E86\u4E4B\u524D\u610F\u5927\u5229\u7684\u90A3\u9879\u8C03\u67E5[9]\u5916\uFF0C\u5728\u4E00\u9879\u9488\u5BF9\u4E00\u5343\u591A\u5B69\u5B50\u7684\u95EE\u5377\u8C03\u67E5\u91CC\uFF0C\u7F8E\u56FD\u9ED1\u4EBA\u5B69\u5B50\u6392\u4FBF\u8BAD\u7EC3\u6BD4\u767D\u4EBA\u5B69\u5B50\u66F4\u65E9\uFF0C\u4F46\u4ED6\u4EEC\u7684\u6392\u4FBF\u4E60\u60EF\u5E76\u6CA1\u6709\u533A\u522B[10]\u3002\u81F3\u4E8E\u5BF9\u8180\u80F1\u529F\u80FD\u7684\u5F71\u54CD\uFF0C\u6709\u7814\u7A76\u8BA4\u4E3A\u65E9\u671F\uFF08\u65E9\u4E8E2\u5C81\uFF09\u5982\u5395\u8BAD\u7EC3\u548C\u8180\u80F1\u529F\u80FD\u5F02\u5E38\u65E0\u5173[11\uFF0C12]\u751A\u81F3\u6709\u8BA4\u4E3A\u5BF9\u8180\u80F1\u529F\u80FD\u8FD8\u6709\u597D\u5904[13]\u3002</p><p>\u81F3\u4E8E\u8BF4\u8FD9\u79CD\u628A\u4FBF\u7684\u4F53\u4F4D\u4F1A\u5BFC\u81F4\u809B\u88C2\u3001\u75D4\u75AE\u3001\u76F4\u80A0\u8131\u5782\u66F4\u662F\u51ED\u7A7A\u60F3\u8C61\uFF0C\u628A\u4FBF\u8FD9\u4E2A\u4F53\u4F4D\u5BF9\u5B69\u5B50\u6765\u8BF4\u548C\u8E72\u5750\u5728\u9A6C\u6876\u4E0A\u5E76\u6CA1\u6709\u592A\u591A\u533A\u522B\uFF0C\u809B\u88C2\u4E3B\u8981\u548C\u4FBF\u79D8\u6709\u5173\uFF0C\u76F4\u80A0\u8131\u5782\u66F4\u591A\u548C\u5148\u5929\u53D1\u80B2\u3001\u8425\u517B\u4E0D\u826F\u6709\u5173\uFF0C\u76EE\u524D\u6CA1\u6709\u4EFB\u4F55\u8BC1\u636E\u8BC1\u5B9E\u4ED6\u4EEC\u548C\u628A\u4FBF\u6709\u5173\uFF0C\u513F\u7AE5\u75D4\u75AE\u66F4\u662F\u6781\u5C11\u89C1\uFF0C\u6210\u5E74\u540E\u7684\u75D4\u75AE\u4E0D\u5F52\u548E\u4E8E\u51E0\u5341\u5E74\u95F4\u7684\u751F\u6D3B\u6392\u4FBF\u4E60\u60EF\u5374\u5F52\u548E\u4E8E\u5A74\u513F\u671F\u77ED\u77ED\u7684\u628A\u4FBF\u65F6\u95F4\u66F4\u662F\u601D\u7EF4\u5954\u9038\u3002</p><p>\u81F3\u4E8E\u5FC3\u7406\u7684\u5F71\u54CD\uFF0C\u53EA\u8981\u4E0D\u662F\u5F3A\u5236\u7ED9\u5B69\u5B50\u628A\u4FBF\uFF0C\u4E0D\u662F\u5728\u516C\u5171\u573A\u5408\u628A\u4FBF\uFF0C\u5B69\u5B50\u4E5F\u6CA1\u6709\u4EFB\u4F55\u4E0D\u826F\u60C5\u7EEA\u7684\u8868\u73B0\uFF0C\u6211\u89C9\u5F97\u8FD9\u79CD\u62C5\u5FC3\u4E5F\u662F\u591A\u4F59\u7684\u3002AAP\u867D\u7136\u63A8\u835018\u4E2A\u6708\u540E\u8FDB\u884C\u8BAD\u7EC3\uFF0C\u4F46\u5176\u65D7\u4E0B\u7684\u79D1\u666E\u7F51\u7AD9healthychildren\u91CC\u4E5F\u8BF4\u4E86\uFF0C\u53EA\u8981\u4E0D\u5F3A\u8FEB\u3001\u8650\u5F85\u5B69\u5B50\uFF0C\u572818\u4E2A\u6708\u4E4B\u524D\u8BAD\u7EC3\u4E5F\u4E0D\u592A\u53EF\u80FD\u7ED9\u5B69\u5B50\u9020\u6210\u4EFB\u4F55\u4F24\u5BB3\u3002[14]</p><p>\u4E0D\u80FD\u56E0\u4E3A\u81EA\u5DF1\u78B0\u5230\u4E00\u4E2A\u6216\u51E0\u4E2A\u628A\u4FBF\u7684\u5B69\u5B50\u6709\u8FD9\u4E9B\u95EE\u9898\u5C31\u8BA4\u4E3A\u662F\u628A\u4FBF\u4F1A\u5BFC\u81F4\u8FD9\u4E9B\u95EE\u9898\uFF0C\u8FD9\u4E9B\u628A\u4FBF\u7684\u5B69\u5B50\u8FD8\u90FD\u547C\u5438\u7A7A\u6C14\u5462\uFF0C\u603B\u4E0D\u80FD\u8BF4\u547C\u5438\u7A7A\u6C14\u4F1A\u5BFC\u81F4\u809B\u88C2\u548C\u76F4\u80A0\u8131\u5782\u3002\u53EF\u4EE5\u5408\u7406\u7684\u6000\u7591\uFF0C\u4F46\u4E0B\u7ED3\u8BBA\u524D\u6700\u597D\u5148\u8BC1\u5B9E\u4E00\u4E0B\u3002</p><p>\u65E2\u7136\u628A\u4FBF\u6CA1\u6709\u4EC0\u4E48\u4F24\u5BB3\u90A3\u662F\u4E0D\u662F\u5EFA\u8BAE\u5927\u5BB6\u628A\u4FBF\u5462\uFF1F\u5C31\u5F53\u524D\u7684\u7814\u7A76\u8BC1\u636E\u6765\u8BF4\u6709\u6548\u548C\u65E0\u5BB3\u7684\u4F9D\u636E\u4E0D\u8DB3\uFF0C\u8FD8\u662F\u4E0D\u5EFA\u8BAE\u5927\u5BB6\u8FD9\u4E48\u505A\u3002\u524D\u9762\u8BF4\u7684\u8FD9\u4E9B\u7814\u7A76\u8FD8\u6BD4\u8F83\u7C97\u7565\uFF0C\u6709\u7684\u5E76\u4E0D\u662F\u76F4\u63A5\u7684\u7814\u7A76\uFF0C\u800C\u662F\u7814\u7A76\u65E9\u671F\u5982\u5395\u8BAD\u7EC3\u3002\u76F8\u5BF9\u76EE\u524D\u4E3B\u6D41\u7684\u5982\u5395\u8BAD\u7EC3\u65B9\u6CD5\u7684\u7814\u7A76\u8BC1\u636E\uFF0C\u628A\u4FBF\u7684\u76F8\u5173\u7814\u7A76\u8BC1\u636E\u66F4\u5C11\uFF0C\u7EA7\u522B\u66F4\u5F31\uFF0C\u54EA\u5929\u6709\u4E86\u66F4\u6DF1\u5165\u7684\u7814\u7A76\uFF0C\u66F4\u53EF\u9760\u7684\u8BC1\u636E\uFF0C\u6539\u4E3A\u63A8\u8350\u751F\u540E\u5F00\u59CB\u6392\u4FBF\u8BAD\u7EC3\u4E5F\u4E0D\u662F\u6CA1\u53EF\u80FD\uFF0C\u4F46\u5728\u6B64\u4E4B\u524D\u8FD8\u662F\u6309\u7167\u5F53\u524D\u7684\u6700\u4F73\u8BC1\u636E\uFF0C\u4E5F\u5C31\u662FAAP\u63A8\u8350\u7684\u65B9\u6848\u6765\u5427\u3002</p><p>\u5982\u679C\u8BF4\u628A\u4FBF\u6709\u4EC0\u4E48\u9700\u8981\u62C5\u5FC3\u7684\uFF0C\u6211\u4F1A\u62C5\u5FC3\u4E00\u65E6\u5B69\u5B50\u5EFA\u7ACB\u4E86\u4F9D\u8D56\u4E8E\u5927\u4EBA\u628A\u4FBF\u624D\u6392\u4FBF\u7684\u4E60\u60EF\uFF0C\u5C31\u4E0D\u4F1A\u6216\u4E0D\u6562\u5728\u5C3F\u5E03\u91CC\u6392\u4FBF\uFF0C\u4E07\u4E00\u5927\u4EBA\u7C97\u5FC3\u4E86\u6CA1\u6CE8\u610F\u5230\u4ED6\u6392\u4FBF\u7684\u610F\u613F\uFF0C\u4ED6\u53EF\u80FD\u4F1A\u618B\u4FBF\uFF0C\u5BFC\u81F4\u4E0D\u597D\u7684\u7ED3\u679C\uFF0C\u4F46\u8FD9\u4E5F\u53EA\u662F\u6211\u7684\u62C5\u5FC3\uFF0C\u4E5F\u662F\u6CA1\u6709\u4F9D\u636E\u7684\uFF0C\u90FD\u9700\u8981\u8FDB\u4E00\u6B65\u7684\u7814\u7A76\u53BB\u660E\u786E\uFF0C\u56E0\u4E3A\u6709\u8FD9\u4E48\u591A\u4E0D\u786E\u5B9A\uFF0C\u6240\u4EE5\u5F53\u4E0B\u8FD8\u662F\u63A8\u8350\u66F4\u786E\u5B9A\u7684\u4E3B\u6D41\u5982\u5395\u8BAD\u7EC3\u65B9\u6CD5\u3002\u5199\u8FD9\u7BC7\u4E5F\u4E0D\u662F\u4E3A\u4E86\u7ED9\u628A\u4FBF\u7FFB\u6848\uFF0C\u800C\u662F\u56E0\u4E3A\u6709\u592A\u591A\u6CA1\u6709\u4F9D\u636E\u7684\u89C2\u70B9\u5728\u6D41\u4F20\uFF0C\u7406\u6E05\u8FD9\u4E9B\u4E8B\u5B9E\u53EF\u4EE5\u51CF\u5C11\u5927\u5BB6\u4E0D\u5FC5\u8981\u7684\u5185\u759A\u548C\u62C5\u5FC3\u3002</p><p>\u9700\u8981\u5F3A\u8C03\u7684\u662F\uFF0C\u4E0D\u7BA1\u662F\u628A\u4FBF\u8FD8\u662F\u5728\u6309\u4E3B\u6D41\u7684\u65B9\u6CD5\u7ED9\u5B69\u5B50\u5982\u5395\u8BAD\u7EC3\uFF0C\u90FD\u4E0D\u8981\u5F3A\u8FEB\u3001\u52C9\u5F3A\u5B69\u5B50\uFF0C\u5F53\u7136\u4E5F\u8981\u7167\u987E\u5B69\u5B50\u9690\u79C1\uFF0C\u4E0D\u8981\u7A7F\u5F00\u88C6\u88E4\uFF0C\u66F4\u4E5F\u4E0D\u8981\u5728\u516C\u5171\u573A\u5408\u968F\u610F\u628A\u4FBF\u3002</p><p>\u672C\u6587\u7684\u53C2\u8003\u8D44\u6599\u8FC7\u591A\u5C31\u4E0D\u9644\u5728\u4E0B\u9762\u4E86\uFF0C\u60F3\u67E5\u770B\u7684\u56DE\u590D\u201C\u8D44\u6599\u201D\u4E8C\u5B57\u53EF\u4EE5\u67E5\u770B\u3002\u4E5F\u6B22\u8FCE\u6709\u8FC7\u628A\u4FBF\u7ECF\u9A8C\u7684\u5BB6\u957F\u53C2\u4E0E\u4E0B\u9762\u7684\u8C03\u67E5\uFF0C\u6839\u636E\u8C03\u67E5\u7ED3\u679C\u4E5F\u8BB8\u6211\u4F1A\u518D\u5206\u6790\u4E00\u4E0B\uFF0C\u5982\u679C\u8D5E\u8D4F\u4E00\u4E0B\u6216\u8F6C\u53D1\u9605\u8BFB\u91CF\u5927\u4E86\uFF0C\u53EF\u80FD\u5199\u5F97\u66F4\u5FEB\u3002</p>',26),l={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=208387218&idx=1&sn=637e5abea17d2285df6895fb7e0fa6d4&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},h=s("\u67E5\u770B\u539F\u6587");function f(m,u){const p=a("ExternalLinkIcon");return r(),t(o,null,[_,e("p",null,[e("a",l,[h,n(p)])])],64)}var b=d(i,[["render",f]]);export{b as default};
