import{r as n,o as t,c as a,a as e,b as r,F as o,d as c,e as s}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const i={},_=c('<h3 id="\u9519\u8FC7\u4E86\u8981\u7B49\u4E00\u5E74-\u4F46\u4E09\u4F0F\u8D34\u8FD9\u4EFD\u667A\u5546\u7A0E\u4F60\u771F\u7684\u8981\u53BB\u4EA4\u5417" tabindex="-1"><a class="header-anchor" href="#\u9519\u8FC7\u4E86\u8981\u7B49\u4E00\u5E74-\u4F46\u4E09\u4F0F\u8D34\u8FD9\u4EFD\u667A\u5546\u7A0E\u4F60\u771F\u7684\u8981\u53BB\u4EA4\u5417" aria-hidden="true">#</a> \u9519\u8FC7\u4E86\u8981\u7B49\u4E00\u5E74\uFF0C\u4F46\u4E09\u4F0F\u8D34\u8FD9\u4EFD\u667A\u5546\u7A0E\u4F60\u771F\u7684\u8981\u53BB\u4EA4\u5417\uFF1F</h3><p>\u6BCF\u5E74\u590F\u5929\uFF0C\u4E34\u8FD1\u4E09\u4F0F\u5929\u65F6\uFF0C\u4E0D\u7BA1\u662F\u4E2D\u533B\u9662\uFF0C\u8FD8\u662F\u7EFC\u5408\u533B\u9662\u3001\u513F\u7AE5\u533B\u9662\u7684\u4E2D\u533B\u79D1\uFF0C\u90FD\u8981\u70ED\u95F9\u8D77\u6765\uFF0C\u65E9\u65E9\u5730\u62C9\u8D77\u6A2A\u5E45\uFF0C\u544A\u8BC9\u5927\u5BB6\uFF1A\u201C\u4E09\u4F0F\u8D34\u201D\u8981\u5F00\u8D34\u4E86\uFF0C\u9519\u8FC7\u4E86\u8981\u7B49\u4E00\u5E74\u2026..</p><p>\u7B49\u5230\u4E86\u4E09\u4F0F\u5929\uFF0C\u8001\u4E2D\u533B\u4EEC\u5728\u8BCA\u5BA4\u91CC\u652F\u8D77\u5F20\u684C\u5B50\uFF0C\u6446\u4E0A\u76D8\u5B50\u548C\u818F\u836F\uFF0C\u7ED9\u6392\u7740\u957F\u9F99\u7684\u60A3\u8005\u4E00\u4E2A\u4E2A\u8D34\u4E0A\uFF0C\u6709\u65F6\u75C5\u4EBA\u592A\u591A\u5C31\u5E72\u8106\u5728\u8D70\u5ECA\u4E0A\uFF0C\u751A\u81F3\u533B\u9662\u5927\u5385\u91CC\u5F00\u8D34\uFF0C\u4E00\u5E45\u70ED\u706B\u671D\u5929\u7684\u666F\u8C61\u3002</p><p>\u201C\u4E09\u4F0F\u8D34\u201D\u662F\u4E2D\u533B\u91CC\u201C\u5929\u7078\u7597\u6CD5\u201D\u7684\u4E00\u79CD\uFF0C\u4E2D\u533B\u8BA4\u4E3A\u4E09\u4F0F\u5929\u91CC\uFF0C\u4EBA\u7684\u6C14\u8840\u901A\u7545\uFF0C\u8FD9\u4E2A\u65F6\u5019\u5728\u7A74\u4F4D\u8D34\u4E00\u4E9B\u523A\u6FC0\u6027\u7684\u818F\u836F\uFF0C\u836F\u6027\u66F4\u5BB9\u6613\u8FDB\u5165\u80BA\u8151\uFF0C\u80FD\u6FC0\u53D1\u7ECF\u7EDC\uFF0C\u80FD\u8C03\u6574\u6C14\u8840\uFF0C\u5F3A\u8EAB\u5065\u4F53\u3002\u80FD\u6CBB\u53CD\u590D\u611F\u5192\u3001\u6162\u6027\u54B3\u55FD\u3001\u54EE\u5598\u3001\u9F3B\u708E\u8FD9\u4E9B\u79CB\u51AC\u5B63\u591A\u53D1\u75C5\uFF0C\u79F0\u4E4B\u4E3A\u201C\u51AC\u75C5\u590F\u6CBB\u201D\uFF0C\u8FD8\u53EF\u4EE5\u6CBB\u7597\u9057\u5C3F\u3001\u538C\u98DF\u7B49\u513F\u7AE5\u95EE\u9898\u3002</p><p>\u6211\u4EEC\u77E5\u9053\u836F\u6709\u534A\u8870\u671F\uFF0C\u5F88\u591A\u836F\u5403\u8FDB\u53BB\u4E0D\u5230\u4E00\u5929\u5C31\u88AB\u4EE3\u8C22\u5F97\u5DEE\u4E0D\u591A\uFF0C\u6240\u4EE5\u4E00\u5929\u8981\u5403\u597D\u51E0\u6B21\u3002\u800C\u4E09\u4F0F\u8D34\u590F\u5929\u8D34\u51E0\u6B21\uFF0C\u6FC0\u53D1\u4E00\u4E0B\u7ECF\u7EDC\uFF0C\u5C31\u53EF\u4EE5\u6CBB\u51AC\u5929\u7684\u75C5\uFF0C\u542C\u8D77\u6765\u6BD4\u75AB\u82D7\u8FD8\u795E\u5947\u3002</p><p>\u4E09\u4F0F\u8D34\u5230\u5E95\u6709\u591A\u795E\u5947\uFF1F</p><p>\u4E09\u4F0F\u8D34\u7684\u7406\u8BBA\u57FA\u7840\u662F\u4E2D\u533B\u7684\u7ECF\u7EDC\u548C\u7A74\u4F4D\u5B66\u8BF4\u3002\u6211\u4EE5\u524D\u8BF4\u8FC7\uFF0C\u7A74\u4F4D\u548C\u7ECF\u7EDC\u662F\u53E4\u4EBA\u60F3\u8C61\u4E2D\u7684\u4E1C\u897F\uFF0C\u5E76\u4E0D\u662F\u771F\u7684\u5B58\u5728\uFF0C\u5426\u5219\u4E0D\u4F1A\u5168\u4E16\u754C\u90A3\u4E48\u591A\u89E3\u5256\u5B66\u5BB6\u90FD\u627E\u4E0D\u5230\u3002\u5916\u79D1\u533B\u751F\u505A\u624B\u672F\u4F1A\u5C0F\u5FC3\u907F\u5F00\u795E\u7ECF\u8840\u7BA1\uFF0C\u4F46\u4E0D\u4F1A\u7279\u610F\u53BB\u907F\u5F00\u7ECF\u7EDC\u548C\u7A74\u4F4D\uFF0C\u5728\u7ECF\u7EDC\u7A74\u4F4D\u7684\u4F4D\u7F6E\u4E0B\u5200\u4E5F\u4E0D\u4F1A\u53D1\u751F\u5F15\u53D1\u4EFB\u4F55\u610F\u5916\uFF0C\u8FD9\u5DF2\u7ECF\u8BF4\u660E\u4E86\u95EE\u9898\u3002</p><p>\u4E0D\u662F\u8BF4\u770B\u4E0D\u89C1\u5C31\u7EDD\u5BF9\u4E0D\u5B58\u5728\uFF0C\u8C01\u63D0\u51FA\u5B66\u8BF4\uFF0C\u9700\u8981\u81EA\u5DF1\u62FF\u8BC1\u636E\u6765\u8BC1\u5B9E\u3002\u5C31\u50CF\u4F60\u8BF4\u4E16\u754C\u4E0A\u6709\u9B3C\uFF0C\u4F60\u5F97\u81EA\u5DF1\u53BB\u6293\u4E2A\u9B3C\u51FA\u6765\uFF0C\u81EA\u7136\u5927\u5BB6\u624D\u4F1A\u4FE1\u3002\u800C\u4E0D\u662F\u56B7\u56B7\u6709\u9B3C\uFF0C\u81EA\u5DF1\u53C8\u6293\u4E0D\u51FA\u9B3C\u6765\uFF0C\u5C31\u529D\u5927\u5BB6\u6765\u4E70\u7B26\u3002</p><p>\u5C31\u7B97\u8FD9\u4E9B\u7A74\u4F4D\u548C\u7ECF\u7EDC\u5B58\u5728\uFF0C\u5C31\u7B97\u4E09\u4F0F\u8D34\u5F88\u795E\u5947\uFF0C\u91CC\u9762\u7684\u836F\u7269\u6210\u5206\u80FD\u88AB\u76AE\u80A4\u5438\u6536\uFF0C\u6216\u8005\u80FD\u523A\u6FC0\u7A74\u4F4D\u6FC0\u6D3B\u7ECF\u7EDC\uFF0C\u4E3A\u4EC0\u4E48\u975E\u8981\u9009\u5728\u4E09\u4F0F\u5929\u91CC\u8D34\uFF1F</p><p>\u4E09\u4F0F\u5929\u91CC\u6C14\u6E29\u662F\u9AD8\uFF0C\u4F46\u6B63\u5E38\u4EBA\u4F53\u4F53\u6E29\u662F\u6052\u5B9A\uFF0C\u5C31\u7B97\u5728\u9AD8\u5BA4\u6E29\u4E0B\u836F\u7269\u7684\u5438\u6536\u66F4\u591A\u4E00\u70B9\uFF0C\u836F\u6548\u66F4\u597D\u4E00\u70B9\uFF0C\u5F00\u4E2A\u6696\u6C14\u6216\u8005\u628A\u818F\u836F\u52A0\u70ED\u4E00\u70B9\u4E5F\u53EF\u4EE5\u5230\u8FBE\u76EE\u7684\uFF0C\u4E3A\u4EC0\u4E48\u975E\u8981\u5B9A\u5728\u4E09\u4F0F\u5929\u8D34\uFF1F\u6709\u7684\u8FD8\u8BF4\u8981\u5728\u4E09\u4F0F\u5929\u7684\u4E2D\u5348\u8D34\u6548\u679C\u66F4\u597D\u3002</p><p>\u518D\u8BF4\u4E86\uFF0C\u4EE5\u524D\u7684\u751F\u6D3B\u6761\u4EF6\u5DEE\uFF0C\u5927\u5BB6\u7684\u5C45\u4F4F\u73AF\u5883\u53D7\u5929\u6C14\u5F71\u54CD\u5F88\u5927\uFF0C\u4E09\u4F0F\u5929\u9177\u6691\u96BE\u71AC\u4F60\u4E5F\u5F97\u71AC\u7740\uFF0C\u4F46\u73B0\u5728\u5927\u70ED\u5929\u6709\u51E0\u6237\u4EBA\u5BB6\u4E0D\u5F00\u7A7A\u8C03\u5462\uFF1F\u6709\u51E0\u5BB6\u5F00\u5C55\u4E09\u4F0F\u8D34\u7684\u4E1A\u52A1\u7684\u533B\u9662\u91CC\u9762\u6CA1\u5F00\u7A7A\u8C03\u5462\uFF1F</p><p>\u4E09\u4F0F\u5929\u91CC\u4EBA\u6C14\u8840\u901A\u7545\uFF0C\u53EF\u8001\u4E2D\u533B\u4EEC\u4E3A\u4EC0\u4E48\u81EA\u5DF1\u8981\u8EB2\u5728\u7A7A\u8C03\u623F\u91CC\u7ED9\u4EBA\u8D34\u5462\uFF1F\u5C31\u4E0D\u6015\u75C5\u4EBA\u5439\u4E86\u7A7A\u8C03\u6C14\u8840\u7600\u6EDE\u5F71\u54CD\u6548\u679C\u5417\uFF1F\u4E2D\u533B\u5982\u679C\u771F\u7684\u76F8\u4FE1\u4E09\u4F0F\u8D34\u7684\u90A3\u5957\u7406\u8BBA\uFF0C\u5C31\u5E94\u8BE5\u5728\u4E09\u4F0F\u5929\u4E2D\u5348\u7AD9\u5728\u592A\u9633\u5E95\u4E0B\u7ED9\u75C5\u4EBA\u8D34\u3002</p><p>\u6709\u7684\u5BB6\u957F\u53EF\u80FD\u8981\u8BF4\uFF0C\u5B69\u5B50\u8D34\u4E86\u4E09\u4F0F\u8D34\u4E4B\u540E\uFF0C\u786E\u5B9E\u6709\u6548\u679C\uFF0C\u5B69\u5B50\u611F\u5192\u53D1\u70E7\u662F\u6BD4\u4EE5\u524D\u597D\u4E86\u554A\uFF0C\u54B3\u55FD\u4E5F\u6BD4\u4EE5\u524D\u5C11\u4E86\uFF0C\u5C3F\u5E8A\u4E5F\u597D\u4E86\uFF0C\u996D\u4E5F\u613F\u610F\u5403\u4E86\u3002\u6709\u8FD9\u4E2A\u53EF\u80FD\u5417\uFF1F</p><p>\u6709\u7684\uFF0C\u800C\u4E14\u53EF\u80FD\u6027\u8FD8\u4E0D\u5C0F\u3002\u4E00\u662F\u4EFB\u4F55\u6CBB\u7597\u90FD\u53EF\u80FD\u6709\u5B89\u6170\u5242\u6548\u5E94\uFF0C\u6B64\u5916\u751F\u75C5\u6709\u5F88\u5927\u7684\u5076\u7136\u6027\uFF0C\u6BCF\u5E74\u6D41\u611F\u75C5\u6BD2\u6D41\u884C\u60C5\u51B5\u90FD\u4E0D\u4E00\u6837\uFF0C\u5B69\u5B50\u6BCF\u5E74\u53D1\u70E7\u54B3\u55FD\u7684\u6B21\u6570\u6709\u53D8\u5316\u4E5F\u662F\u5F88\u6B63\u5E38\u7684\u3002</p><p>\u53E6\u5916\u6211\u4EEC\u90FD\u77E5\u9053\uFF0C\u5B69\u5B50\u5C0F\u7684\u65F6\u5019\u514D\u75AB\u7CFB\u7EDF\u8FD8\u4E0D\u5B8C\u5584\uFF0C\u63A5\u89E6\u4E86\u75C5\u539F\u4F53\u66F4\u5BB9\u6613\u751F\u75C5\uFF0C\u6240\u4EE5\uFF0C\u5A74\u5E7C\u513F\u611F\u5192\u6BD4\u5927\u4EBA\u591A\uFF0C\u53D1\u70E7\u54B3\u55FD\u81EA\u7136\u4E5F\u66F4\u591A\u3002\u4F46\u548C\u75C5\u539F\u4F53\u63A5\u89E6\u7684\u8FC7\u7A0B\uFF0C\u4E5F\u662F\u5B69\u5B50\u514D\u75AB\u7CFB\u7EDF\u5B8C\u5584\u7684\u8FC7\u7A0B\uFF0C\u968F\u7740\u5B69\u5B50\u5E74\u9F84\u7684\u589E\u957F\uFF0C\u751F\u75C5\u7684\u6B21\u6570\u591A\u4E86\uFF0C\u514D\u75AB\u7CFB\u7EDF\u4E5F\u5728\u9010\u6E10\u5B8C\u5584\uFF0C\u4F60\u4E0D\u53BB\u505A\u4EFB\u4F55\u6CBB\u7597\uFF0C\u5B69\u5B50\u53D1\u70E7\u54B3\u55FD\u4E5F\u4F1A\u8D8A\u6765\u8D8A\u5C11\u3002</p><p>\u5C3F\u5E8A\u66F4\u662F\u4E00\u4E2A\u968F\u5E74\u9F84\u589E\u957F\uFF0C\u53D1\u75C5\u7387\u9010\u6E10\u964D\u4F4E\u7684\u95EE\u9898\u30025\u5C81\u7684\u5B69\u5B501/5\u4F1A\u5C3F\u5E8A\uFF0C\u4E0D\u505A\u4EFB\u4F55\u4E4B\u7C7B\uFF0C\u5230\u4E867\u5C81\u5C31\u8FD8\u67091/10\uFF0C\u5230\u4E8610\u5C81\u5C31\u53EA\u67091/20\u3002\u968F\u7740\u5B69\u5B50\u5E74\u9F84\u589E\u957F\uFF0C\u5B69\u5B50\u7684\u996D\u91CF\u589E\u5927\uFF0C\u4E0D\u5403\u996D\u7684\u73B0\u8C61\u6539\u5584\uFF0C\u4E5F\u662F\u5F88\u6B63\u5E38\u7684\u73B0\u8C61\u3002</p><p>\u4E09\u4F0F\u8D34\u4E00\u822C\u662F2\u5C81\u4EE5\u4E0A\u7684\u5B69\u5B50\u624D\u7ED9\u8D34\uFF0C\u590F\u5929\u8D34\u4E09\u4F0F\u8D34\uFF0C\u6548\u679C\u8981\u51AC\u5929\u624D\u770B\u5F97\u5230\uFF0C\u5BB6\u957F\u8981\u548C\u4E0A\u4E00\u5E74\u554A\u7684\u51AC\u5929\u5BF9\u6BD4\uFF0C\u624D\u77E5\u9053\u4E09\u4F0F\u8D34\u6709\u6CA1\u6709\u6548\u679C\uFF0C\u4E24\u4E2A\u5BF9\u6BD4\u7684\u65F6\u95F4\u5176\u5B9E\u5DF2\u7ECF\u5DEE\u4E86\u4E00\u5E74\uFF0C\u5B69\u5B50\u6BCF\u957F\u4E00\u5C81\uFF0C\u751F\u75C5\u7684\u673A\u4F1A\u672C\u6765\u964D\u4F4E\u4E86\u5F88\u591A\uFF0C\u8FD9\u672C\u6765\u662F\u5B69\u5B50\u751F\u957F\u53D1\u80B2\u548C\u514D\u75AB\u7CFB\u7EDF\u5B8C\u5584\u7684\u7ED3\u679C\uFF0C\u4F60\u53BB\u8D34\u4E86\u4E09\u4F0F\u8D34\uFF0C\u5C31\u4EE5\u4E3A\u662F\u4E09\u4F0F\u8D34\u8D77\u6548\u4E86\u3002</p><p>\u800C\u4E2D\u533B\u4EEC\u8FD8\u544A\u8BC9\u4F60\uFF0C\u4E09\u4F0F\u8D34\u8981\u8D34\u4E09\u5E74\u6548\u679C\u624D\u597D\uFF0C\u8D34\u836F\u671F\u95F4\u8981\u614E\u98DF\u8F9B\u8FA3\u3001\u6D77\u9C9C\u3001\u7F8A\u8089\u3001\u8611\u83C7\u7B49\u98DF\u7269\u6548\u679C\u624D\u597D\u3002\u54EA\u6015\u5B69\u5B50\u662F2\u5C81\u624D\u5F00\u59CB\u8D34\uFF0C\u8D34\u5B8C\u4E09\u5E74\u4E5F\u90FD\u5DF2\u7ECF5\u5C81\u4E86\uFF0C\u90FD\u5DF2\u7ECF\u8FC7\u4E86\u751F\u75C5\u7684\u9AD8\u5CF0\u5E74\u9F84\u4E86\uFF0C\u751F\u75C5\u81EA\u7136\u5C11\u4E86\u3002</p><p>\u4E07\u4E00\u4F60\u5B69\u5B50\u771F\u7684\u8FD8\u662F\u8001\u611F\u5192\uFF0C\u53BB\u95EE\u4E2D\u533B\uFF0C\u4E2D\u533B\u53C8\u53EF\u4EE5\u95EE\u4F60\uFF1A\u201C\u662F\u4E0D\u662F\u7ED9\u5B69\u5B50\u5403XX\u4E86\uFF1F\u201D\u90A3\u4E48\u591A\u98DF\u7269\u603B\u80FD\u95EE\u51FA\u4E00\u4E2A\u5B69\u5B50\u5403\u8FC7\u7684\uFF0C\u7136\u540E\u7528\u4E2D\u533B\u81EA\u5DF1\u62E5\u6709\u7EDD\u5BF9\u89E3\u91CA\u6743\u7684\u7406\u8BBA\u544A\u8BC9\u4F60\u4E3A\u4EC0\u4E48\u5403\u4E86\u8FD9\u4E2A\u5C31\u4F1A\u5F71\u54CD\u6548\u679C\uFF0C\u4F60\u5C31\u65E0\u8BDD\u53EF\u8BF4\u4E86\u3002</p><p><strong>\u5B89\u6170\u5242\u6548\u5E94\u52A0\u4E0A\u60A3\u75C5\u7684\u5076\u7136\u6027\uFF0C\u518D\u52A0\u4E0A\u5B69\u5B50\u751F\u957F\u53D1\u80B2\u548C\u514D\u75AB\u7CFB\u7EDF\u7684\u81EA\u6211\u5B8C\u5584\uFF0C\u8FD9\u5C31\u662F\u4E09\u4F0F\u8D34\u80FD\u8DE8\u65F6\u7A7A\u51AC\u75C5\u590F\u6CBB\u7684\u795E\u5947\u4E4B\u5904</strong>\u3002</p><p>\u5176\u5B9E\u6211\u53EF\u4EE5\u505A\u5230\u66F4\u795E\u5947\uFF0C\u968F\u4FBF\u62B1\u4E2A\u5B69\u5B50\u8FC7\u6765\u8BA9\u6211\u5439\u53E3\u6C14\uFF0C3\u5E74\u540E\u8FD9\u5B69\u5B50\u53D1\u70E7\u54B3\u55FD\u7684\u6B21\u6570\u6BD4\u73B0\u5728\u5C11\u4E5F\u662F\u5927\u6982\u7387\u4E8B\u4EF6\u3002</p><p>\u81EA\u5DF1\u53BB\u8D34\u8FC7\u7684\u4EBA\u53EF\u80FD\u8FD8\u4F1A\u8BF4\uFF0C\u8D34\u4E0A\u786E\u5B9E\u611F\u89C9\u8EAB\u4F53\u6709\u53D8\u5316\uFF0C\u611F\u89C9\u7A74\u4F4D\u6696\u6696\u7684\u3002\u4E00\u65B9\u9762\u662F\u5E7F\u544A\u5BA3\u4F20\u548C\u533B\u751F\u8A00\u8BED\u6697\u793A\u4EA7\u751F\u7684\u5FC3\u7406\u4F5C\u7528\uFF0C\u836F\u818F\u52A0\u70ED\u4E86\u518D\u8D34\u4E0A\u81EA\u7136\u6696\u6696\u7684\uFF0C\u53E6\u5916\u4E00\u65B9\u9762\uFF0C\u4F60\u8981\u662F\u4E86\u89E3\u4E00\u4E0B\u818F\u836F\u91CC\u7684\u6210\u5206\u4E5F\u5C31\u66F4\u4E0D\u89C9\u5F97\u5947\u602A\u4E86\u3002</p><p>\u4E09\u4F0F\u8D34\u91CC\u5E38\u7528\u7684\u5C31\u662F\u6591\u8765\u3001\u6BDB\u83A8\u3001\u7EC6\u8F9B\u3001\u767D\u82A5\u5B50\u8FD9\u4E9B\u5F3A\u523A\u6FC0\u6027\u836F\u7269\u3002\u6591\u8765\u91CC\u6709\u6591\u8765\u7D20\uFF0C\u6BDB\u83A8\u542B\u6709\u539F\u767D\u5934\u7FC1\u7D20\uFF0C\u767D\u82A5\u5B50\u542B\u6709\u767D\u82A5\u5B50\u7519\uFF0C\u7EC6\u8F9B\u542B\u6709\u9EC4\u6A1F\u919A\uFF0C\u8FD9\u4E9B\u90FD\u662F\u5F3A\u523A\u6FC0\u6027\u7684\u6709\u6BD2\u7269\u8D28\uFF0C\u63A5\u89E6\u76AE\u80A4\u540E\u53EF\u80FD\u4EA7\u751F\u70E7\u707C\u611F\u3001\u7EA2\u80BF\u3001\u6C34\u6CE1\u7B49\u53CD\u5E94\uFF0C\u8FD9\u4E9B\u672C\u6765\u662F\u5316\u5B66\u6027\u70E7\u4F24\u7684\u75C7\u72B6\uFF08\u5982\u4E0B\u56FE\uFF09\uFF0C\u5374\u88AB\u4E2D\u533B\u4EEC\u89E3\u8BFB\u4E3A\u4E09\u4F0F\u8D34\u8D77\u6548\u7684\u8868\u73B0\uFF0C\u6709\u7684\u751A\u81F3\u8BF4\u8D8A\u8D77\u6CE1\u8BF4\u660E\u6548\u679C\u8D8A\u597D\u3002</p><p>news.china.com.cn/live/2015-08/20/content_34002581.htm</p><p>\u6309\u4E2D\u533B\u4EEC\u7684\u8BF4\u6CD5\uFF0C\u8D34\u4E09\u4F0F\u8D34\u7684\u76EE\u7684\u662F\u523A\u6FC0\u7A74\u4F4D\u6FC0\u53D1\u7ECF\u7EDC\uFF0C\u90A3\u8D77\u6548\u5E94\u8BE5\u662F\u7A74\u4F4D\u548C\u7ECF\u7EDC\u6709\u53CD\u5E94\u5427\uFF0C\u600E\u4E48\u7EA2\u80BF\u8D77\u6CE1\u751A\u81F3\u6E83\u70C2\u7684\u5C31\u662F\u4F0F\u8D34\u63A5\u89E6\u7684\u76AE\u80A4\uFF1F\u96BE\u9053\u7ECF\u7EDC\u548C\u7A74\u4F4D\u662F\u6210\u7247\u7684\u5417\uFF1F<strong>\u628A\u526F\u4F5C\u7528\u8BF4\u6210\u8D77\u6548\u7684\u8868\u73B0\uFF0C\u8FD9\u5C31\u662F\u4E09\u4F0F\u8D34\u795E\u5947\u7684\u53E6\u5916\u4E00\u4E2A\u91CD\u8981\u539F\u56E0\u3002</strong></p><p>\u800C\u4E8B\u5B9E\u4E0A\u4E5F\u662F\u56E0\u4E3A\u6545\u610F\u91C7\u7528\u8FD9\u4E9B\u6709\u523A\u6FC0\u6027\uFF0C\u6709\u6BD2\u6027\u7684\u836F\u7269\u6765\u505A\u4E09\u4F0F\u8D34\uFF0C\u6240\u4EE5\u51FA\u73B0\u526F\u4F5C\u7528\u7684\u98CE\u9669\u4E5F\u66F4\u5927\uFF0C\u4E09\u4F0F\u8D34\u7684\u5371\u5BB3\u4E5F\u6BD4\u9000\u70ED\u8D34\u3001\u8110\u8D34\u7684\u5371\u5BB3\u66F4\u5927\u3002\u8BF4\u6765\u8981\u611F\u8C22\u7684\u662F\u76AE\u80A4\u5C4F\u969C\u529F\u80FD\uFF0C\u8981\u662F\u771F\u7684\u5438\u6536\u5F88\u591A\u7684\u8BDD\uFF0C\u4F60\u9664\u4E86\u8981\u62C5\u5FC3\u76AE\u80A4\u70E7\u4F24\u7684\u95EE\u9898\uFF0C\u66F4\u8981\u62C5\u5FC3\u7684\u662F\u8FD9\u4E9B\u836F\u7269\u7684\u6BD2\u526F\u4F5C\u7528\u4E86\u3002</p><p>\u65E0\u8BBA\u662F\u4E2D\u836F\u897F\u836F\uFF0C\u5224\u65AD\u6709\u6CA1\u6709\u6548\u679C\uFF0C\u662F\u5426\u5B89\u5168\uFF0C\u6807\u51C6\u5176\u5B9E\u662F\u4E00\u6837\u7684\uFF0C\u90A3\u5C31\u662F\u5927\u6837\u672C\u53CC\u76F2\u968F\u673A\u5BF9\u7167\u7684\u4E34\u5E8A\u836F\u7269\u8BD5\u9A8C\uFF0C\u53EF\u60DC\u4E09\u4F0F\u8D34\u548C\u8110\u8D34\u4E00\u6837\uFF0C\u867D\u7136\u5C06\u529F\u6548\u5439\u7684\u5929\u82B1\u4E71\u5760\uFF0C\u4E8B\u5B9E\u4E0A\u4E00\u4E2A\u6548\u679C\u4E5F\u6CA1\u4E25\u683C\u9A8C\u8BC1\u8FC7\u3002</p><p>\u4E0D\u662F\u6211\u5BF9\u4E2D\u533B\u6709\u504F\u89C1\uFF0C\u800C\u662F\u6211\u4EEC\u7684\u56FD\u5BB6\u7ED9\u4E86\u4E2D\u533B\u836F\u7279\u6743\uFF0C\u8FD9\u79CD\u6CA1\u6709\u9A8C\u8BC1\u8FC7\u6548\u679C\u800C\u53C8\u660E\u786E\u6709\u6BD2\u526F\u4F5C\u7528\u7684\u818F\u836F\uFF0C\u90FD\u53EF\u4EE5\u76F4\u63A5\u7528\u5728\u75C5\u4EBA\u8EAB\u4E0A\uFF0C\u533B\u9662\u8FD8\u53EF\u4EE5\u5927\u8086\u5BA3\u4F20\u8FD9\u4E9B\u6CA1\u8BC1\u5B9E\u7684\u529F\u6548\uFF0C\u8BA9\u90A3\u4E48\u591A\u5BB6\u957F\u5927\u70ED\u5929\u6392\u534A\u5929\u961F\uFF0C\u82B1\u94B1\u8BA9\u5B69\u5B50\u5192\u7740\u5316\u5B66\u6027\u70E7\u4F24\u7684\u98CE\u9669\u53BB\u8D34\u4E09\u4F0F\u8D34\u3002</p><p>\u4E00\u5E74\u4E00\u5EA6\u7684\u4E09\u4F0F\u5929\u5FEB\u5230\u4E86\uFF0C\u5404\u5BB6\u533B\u9662\u53C8\u8981\u5F00\u59CB\u5F81\u6536\u667A\u5546\u7A0E\u4E86\uFF0C\u6211\u4EEC\u6539\u53D8\u4E0D\u4E86\u836F\u76D1\u90E8\u95E8\uFF0C\u4E5F\u6539\u53D8\u4E0D\u4E86\u533B\u9662\uFF0C\u53EA\u80FD\u591A\u8F6C\u53D1\u63D0\u9192\u4E00\u4E0B\u81EA\u5DF1\u7684\u4EB2\u4EBA\u548C\u670B\u53CB\uFF1A<strong>\u54EA\u6015\u9519\u8FC7\u4E86\u8981\u7B49\u4E00\u767E\u5E74\uFF0C\u4E5F\u4E0D\u8981\u53BB\u4EA4\u8FD9\u79CD\u667A\u5546\u7A0E\u4E86\u3002</strong></p><hr><p>\u672C\u6587\u4E3A\u9996\u53D1\u4E8E2016\u5E74\uFF0C\u4E3A**\u300C\u5751\u5A03\u4E09\u8D34\u300D<strong>\u7CFB\u5217\u4E4B\u4E00\uFF0C\u5206\u522B\u56DE\u590D</strong>\u300C\u9000\u70ED\u8D34\u300D<strong>\u3001</strong>\u300C\u8110\u8D34\u300D**\u53EF\u9605\u8BFB\u53E6\u5916\u4E24\u7BC7\u6587\u7AE0\u3002</p>',31),f={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651626743&idx=1&sn=eaedd6452acf4e4a6a26402fba33bcc5&chksm=8b5cb50dbc2b3c1b9f0dee868af4e9f210af22bbefa540db557b0fd132681dbe0b4c2a13f9cd&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},h=s("\u67E5\u770B\u539F\u6587");function l(b,m){const p=n("ExternalLinkIcon");return t(),a(o,null,[_,e("p",null,[e("a",f,[h,r(p)])])],64)}var u=d(i,[["render",l]]);export{u as default};
