import{r as s,o as c,c as r,a as p,b as t,w as n,F as d,e,d as _}from"./app.9aaa2256.js";import{_ as i}from"./plugin-vue_export-helper.21dcd24c.js";const b={},l=p("h3",{id:"\u5982\u4F55\u770B\u5F85\u5A74\u513F\u7761\u7720\u95EE\u9898",tabindex:"-1"},[p("a",{class:"header-anchor",href:"#\u5982\u4F55\u770B\u5F85\u5A74\u513F\u7761\u7720\u95EE\u9898","aria-hidden":"true"},"#"),e(" \u5982\u4F55\u770B\u5F85\u5A74\u513F\u7761\u7720\u95EE\u9898\uFF1F")],-1),f=e("\u6628\u5929\u5173\u4E8E\u5173\u4E8E"),h=e("\u5B69\u5B50\u54ED\u8981\u4E0D\u8981\u62B1\u7684\u6587\u7AE0"),m=e("\uFF0C\u540E\u53F0\u7559\u8A00\u6709\u5F88\u591A\u5173\u4E8E\u5B69\u5B50\u7761\u89C9\u95EE\u9898\u7684\u8BA8\u8BBA\uFF0C\u518D\u5206\u4EAB\u4E00\u4E0B\u9648\u5EFA\u519B\u533B\u751F\u7684\u8FD9\u7BC7\u6587\u7AE0\u4F9B\u5927\u5BB6\u53C2\u8003\u3002"),u=_("<p>\u5065\u5EB7\u7684\u5A74\u513F\u7761\u7720\u6A21\u5F0F\u662F\u591A\u6837\u6027\u4E2A\u4F53\u5316\u7684\uFF0C\u6709\u7684\u5B69\u5B50\u53EF\u4EE5\u7761\u6574\u89C9\uFF0C\u6709\u7684\u6682\u65F6\u4E0D\u53EF\u4EE5\uFF0C\u8FD9\u4E0D\u662F\u533A\u5206\u5B69\u5B50\u4F18\u52A3\u7684\u6807\u51C6\uFF0C\u4F46\u8FD9\u663E\u7136\u662F\u4EE4\u4EBA\u7126\u707C\u7684\u95EE\u9898\u3002</p><p>\u201C\u5B69\u5B50\u597D\u5E26\u5417\uFF1F\u201D</p><p>\u8FD9\u662F\u6211\u513F\u4FDD\u65F6\u60EF\u7528\u7684\u5F00\u573A\u767D\u3002</p><p>\u201C\u4E0D\u597D\u5E26\uFF0C\u4ED6\u7761\u7720\u4E0D\u597D......\u201D</p><p>\u8FD9\u79CD\u8BF4\u8F9E\u6211\u542C\u8FC7\u65E0\u6570\u6B21\u3002</p><p><strong>\u7761\u7720\u5E38\u89C1\u5173\u952E\u8BCD</strong></p><p>1. \u5165\u7761\u56F0\u96BE</p><p>2. \u591C\u9192</p><p>\u987A\u5229\u5165\u7761\u8FD9\u79CD\u4E8B\uFF0C\u4E00\u822C\u9700\u8981\u6EE1\u8DB32\u4E2A\u57FA\u672C\u6761\u4EF6\uFF1A1. \u56F0\u4E86\u60F3\u7761\uFF1B2. \u53EF\u4EE5\u653E\u677E\u4E0B\u6765\uFF08\u8FD9\u5305\u62EC\u964D\u4F4E\u5E72\u6270\u548C\u7ED9\u4E88\u5FC5\u8981\u534F\u52A9\uFF09\uFF1B\u591C\u9192\u5C5E\u4E8E\u63A5\u89C9\u95EE\u9898\uFF0C\u672C\u8D28\u662F\u91CD\u65B0\u5165\u7761\u3002</p><p>\u95EE\u9898\u6765\u4E86\uFF0C\u5B69\u5B50\u4EC0\u4E48\u65F6\u5019\u4F1A\u56F0\uFF1F\u6587\u5B57\u63CF\u8FF0\u7684\u7761\u7720\u4FE1\u53F7\uFF0C\u9047\u5230\u5B9E\u8DF5\u603B\u4F1A\u53D8\u5F97\u82CD\u767D\u65E0\u529B\u3002</p><p>\u901A\u5E38\u654F\u9510\u7684\u3001\u5929\u5929\u5E26\u5B69\u5B50\u7684\u4EBA\uFF0C\u81EA\u7136\u4F1A\u77E5\u9053\uFF0C\u4E0D\u8981\u95EE\u4E3A\u4EC0\u4E48\uFF0C\u56E0\u4E3A\u4F60\u5C31\u662F\u77E5\u9053\u3002\u5982\u679C\u4F60\u4E0D\u80FD\u77E5\u9053\uFF0C\u8BF7\u63A5\u7740\u5F80\u4E0B\u770B\u3002</p><p>\u654F\u9510\u51B3\u5B9A\u4E86\u4F60\u7684\u5224\u65AD\u529B\uFF0C\u5929\u5929\u5E26\u51B3\u5B9A\u4F60\u6709\u4E86\u89E3\u5B69\u5B50\u7684\u673A\u4F1A\uFF0C\u6B63\u5982\u201C\u6625\u6C5F\u6C34\u6696\u9E2D\u5148\u77E5\u201D\u3002</p><p>\u5224\u65AD\u529B\u5B58\u5728\u4E2A\u4F53\u7684\u5DEE\u5F02\uFF0C\u5185\u5728\u51B3\u5B9A\u56E0\u7D20\uFF1A\u4E2A\u6027\u7279\u8D28\u3001\u77E5\u8BC6\u50A8\u5907\u3001\u60C5\u7EEA\u72B6\u6001\u3001\u75B2\u52B3\u72B6\u6001\uFF1B\u5916\u5728\u7684\u5F71\u54CD\u56E0\u7D20\uFF1A\u5BB6\u5EAD\u5173\u7CFB\u3001\u7ECF\u6D4E\u57FA\u7840\u548C\u6587\u5316\u80CC\u666F\u3002</p><p>\u6700\u5E38\u89C1\u7684\u5F71\u54CD\u56E0\u7D20\u5E94\u5C5E\u60C5\u7EEA\u548C\u75B2\u52B3\u72B6\u6001\uFF0C\u6BD4\u5982\uFF1A\u4EA7\u540E\u6291\u90C1\u3001\u517B\u80B2\u7126\u8651\u548C\u6CA1\u6709\u5E2E\u624B\u5BFC\u81F4\u7684\u8FC7\u5EA6\u4F53\u529B\u652F\u51FA\u3002</p><p>\u5BB6\u5EAD\u80B2\u513F\u5206\u6B67\u66F4\u53EF\u80FD\u5E26\u6765\u8D1F\u9762\u538B\u529B\u3002\u589E\u6D3E\u4EBA\u624B\u3001\u7EDF\u4E00\u6218\u7EBF\u3001\u5BFB\u6C42\u4E13\u4E1A\u5FC3\u7406\u652F\u6301\u670D\u52A1\u662F\u89E3\u51B3\u7684\u9014\u5F84\u3002\u8FD9\u6709\u5229\u4E8E\u63D0\u5347\u5224\u65AD\u529B\uFF0C\u4FC3\u8FDB\u517B\u80B2\u4EBA\u53CA\u65F6\u51C6\u786E\u56DE\u5E94\u5A74\u513F\u7684\u9700\u6C42\u80FD\u529B\u3002</p><p>\u80FD\u5F97\u5230\u79EF\u6781\u51C6\u786E\u56DE\u5E94\u7684\u5A74\u513F\u4E0E\u7167\u6599\u8005\u4E4B\u95F4\u5173\u7CFB\u66F4\u597D\uFF0C\u5B89\u5168\u611F\u66F4\u8DB3\uFF0C\u54ED\u95F9\u66F4\u5C11\uFF0C\u66F4\u5BB9\u6613\u517B\u80B2\u3002</p><p>\u5C31\u7B97\u662F\u96BE\u517B\u578B\u6C14\u8D28\u7684\u5A74\u513F\uFF0C\u4E5F\u53EF\u4EE5\u901A\u8FC7\u4E0E\u7167\u517B\u8005\u5EFA\u7ACB\u5B89\u5168\u578B\u4F9D\u604B\u5173\u7CFB\uFF0C\u8FDB\u800C\u6539\u5584\u517B\u80B2\u51B2\u7A81\u3002</p><p>\u82E5\u517B\u80B2\u8005\u6CA1\u6709\u65F6\u95F4\u6216\u4E0D\u613F\u610F\u628A\u65F6\u95F4\u53CA\u7CBE\u529B\u82B1\u5728\u5A74\u513F\u8EAB\u4E0A\uFF0C\u53EA\u60F3\u5BFB\u6C42\u6377\u5F84\uFF0C\u53EF\u80FD\u4F1A\u5931\u53BB\u5BF9\u5B69\u5B50\u7684\u4E86\u89E3\u673A\u4F1A\u3002\u6CA1\u6709\u4E86\u89E3\u505A\u57FA\u7840\uFF0C\u8C08\u5E72\u9884\u51E0\u4E4E\u4E0D\u53EF\u80FD\u6210\u529F\u3002</p><p>\u77E5\u9053\u5B69\u5B50\u4EC0\u4E48\u65F6\u5019\u56F0\uFF0C\u4EE3\u8868\u4F60\u6293\u4F4F\u4E86\u5B69\u5B50\u7684\u7761\u7720\u65F6\u673A\u3002\u65F6\u673A\u662F\u5165\u7761\u7684\u5173\u952E\uFF0C\u56E0\u4E3A\u4F60\u65E0\u6CD5\u987A\u5229\u54C4\u7761\u4E00\u4E2A\u4E0D\u56F0\u7684\u5B69\u5B50\uFF0C\u4E5F\u52BF\u5FC5\u5F88\u96BE\u5B89\u629A\u4E00\u4E2A\u8FC7\u5EA6\u75B2\u52B3\u7684\u5B9D\u5B9D\u3002\u76F8\u6BD4\u7761\u524D\u505A\u4EC0\u4E48\uFF0C\u65E0\u7591\u7761\u7720\u7684\u65F6\u673A\u624D\u662F\u6700\u91CD\u8981\u7684\u3002</p><p>\u81F3\u4E8E\u653E\u677E\u8FD9\u4E2A\u4E8B\uFF0C\u5B69\u5B50\u662F\u4E3B\u4F53\uFF0C\u5728\u4E0D\u501F\u52A9\u5916\u5728\u56E0\u7D20\u7684\u60C5\u51B5\u4E0B\uFF0C\u4E0D\u540C\u7684\u5B69\u5B50\u8868\u73B0\u4F1A\u6709\u4E0D\u540C\u3002</p><p>\u8FD9\u79CD\u5DEE\u5F02\u4E0E\u4E2A\u4F53\u7279\u8D28\u548C\u53D1\u80B2\u8FDB\u7A0B\u6709\u5173\uFF0C\u57FA\u672C\u5C5E\u4E8E\u4E0D\u53EF\u77ED\u671F\u6539\u53D8\u7684\u90E8\u5206\uFF0C\u552F\u6709\u65F6\u95F4\u624D\u80FD\u52A9\u4ED6\u4E00\u81C2\u4E4B\u529B\u3002\u7136\u800C\uFF0C\u4F17\u591A\u5916\u5728\u56E0\u7D20\u6211\u4EEC\u662F\u53EF\u4EE5\u8C03\u6574\u7684\uFF0C\u5229\u4E8E\u534F\u52A9\u5A74\u513F\u5165\u7761\u3002</p><p><strong><em>01</em></strong></p><p><strong>\u5E38\u89C4\u56E0\u7D20</strong></p><p>\u5E38\u89C4\u56E0\u7D20\u5728\u5B69\u5B50\u6210\u957F\u8FC7\u7A0B\u4E2D\u9700\u8981\u8D2F\u7A7F\u59CB\u7EC8\uFF0C\u5982\uFF1A\u5B89\u5168\u3001\u5149\u7EBF\u3001\u58F0\u97F3\u3001\u6E29\u5EA6\u3001\u4F5C\u606F\u505A\u597D\u6CA1\u6709\uFF1F\u8FD9\u4E9B\u76F8\u5173\u5185\u5BB9\u968F\u5904\u53EF\u89C1\uFF0C\u6B64\u5904\u4E0D\u8D58\u8FF0\u3002</p><p>\u8FD9\u91CC\u5F3A\u8C03\u7761\u7720\u5730\u70B9\u4E5F\u662F\u975E\u5E38\u91CD\u8981\u7684\uFF0C\u63A8\u8350\u201C\u540C\u623F\u4E0D\u540C\u5E8A\u201D\u7761\u3002\u867D\u7136\u5728\u4E0D\u540C\u7684\u6C11\u65CF\u6587\u5316\u91CC\u89C2\u70B9\u4E0D\u540C\uFF0C\u4F46\u662F\u7814\u7A76\u8868\u660E\u201C\u540C\u623F\u4E0D\u540C\u5E8A\u201D\u7761\u4E0D\u4EC5\u6709\u5229\u4E8E\u5B89\u5168\u3001\u65B9\u4FBF\u7167\u987E\uFF0C\u4E5F\u6709\u5229\u4E8E\u5B69\u5B50\u540C\u7167\u517B\u4EBA\u7684\u7761\u7720\u8D28\u91CF\u3002</p><p><strong><em>02</em></strong></p><p><strong>\u75C5\u7406\u56E0\u7D20</strong></p><p>\u4E00\u8FC7\u6027\u7684\u75BE\u75C5\u95EE\u9898\u5982\uFF1A\u9F3B\u585E\u3001\u54B3\u55FD\u3001\u53D1\u70ED\u3001\u6E7F\u75B9\u7B49\u7B49\u5E38\u89C1\u75BE\u75C5\uFF0C\u8FD9\u9700\u8981\u9488\u5BF9\u6027\u6CBB\u7597\uFF0C\u6216\u63D0\u4F9B\u8F85\u52A9\u7684\u652F\u6301\u3002</p><p>\u5728\u5E38\u89C4\u4E0D\u53D8\u7684\u60C5\u51B5\u4E0B\uFF0C\u75C5\u6108\u540E\u4E0D\u9700\u8981\u591A\u4E45\u5B69\u5B50\u4F1A\u56DE\u5230\u4ECE\u524D\u3002\u6301\u7EED\u6027\u7684\u53D1\u80B2\u5F02\u5E38\uFF1A\u8111\u635F\u4F24\u540E\u3001\u5B64\u72EC\u75C7\u8C31\u7CFB\u969C\u788D\u7B49\uFF0C\u8FD9\u9700\u8981\u4E13\u79D1\u6CBB\u7597\u548C\u5E72\u9884\u3002\u6B64\u7C7B\u5C5E\u4E8E\u7279\u6B8A\u60C5\u51B5\uFF0C\u975E\u666E\u904D\u73B0\u8C61\u3002</p><p><strong><em>03</em></strong></p><p><strong>\u751F\u7406\u56E0\u7D20</strong></p><p>\u4F34\u968F\u6B63\u5E38\u751F\u957F\u53D1\u80B2\u7684\u4E8B\u4EF6\uFF0C\u5982\uFF1A\u5A74\u513F\u80A0\u7EDE\u75DB\uFF1B\u51FA\u7259\uFF1B\u5927\u8FD0\u52A8\u53D1\u80B2\uFF08\u6BD4\u59824\u6708\u9F84\u5DE6\u53F3\u7684\u7FFB\u8EAB\uFF0C6\u6708\u9F84\u5DE6\u53F3\u7684\u5B66\u5750\uFF0C7\u30018\u30019\u6708\u9F84\u9646\u7EED\u51FA\u73B0\u7684\u6EDA\u3001\u722C\u3001\u7AD9\u7ACB\uFF09\uFF1B\u731B\u6DA8\u671F\u7684\u9891\u7E41\u7D22\u98DF\uFF1B5\u6708\u9F84\u5DE6\u53F3\u5F00\u59CB\u51FA\u73B0\u7684\u201C\u5206\u79BB\u7126\u8651\u201D\uFF0C\u7A0B\u5EA6\u9010\u6E10\u4E0A\u5347\u76F4\u8FBE18\u6708\u9F84\u540E\u3002</p><p>\u5BF9\u751F\u7406\u56E0\u7D20\u7684\u5904\u7406\u6709\u4E24\u4E2A\u65B9\u5411\uFF1A</p><p>1. \u71AC\u3001\uFF08\u548C\uFF09\u7ED9\u4E88\u9002\u5F53\u652F\u6301\uFF1B</p><p>2. \u4E0D\u8FC7\u5206\u5173\u6CE8\u548C\u5E72\u9884\u3002</p><p>\u6BD4\u5982\uFF1A\u8FD0\u52A8\u53D1\u80B2\uFF0C\u767D\u5929\u8981\u8FDB\u884C\u5145\u5206\u7684\u4E92\u52A8\uFF0C\u7ED9\u4E88\u5145\u5206\u7EC3\u4E60\u7684\u673A\u4F1A\uFF1B\u591C\u665A\u786E\u4FDD\u5B69\u5B50\u7684\u5B89\u5168\uFF0C\u5FC5\u8981\u7684\u65F6\u5019\u5E2E\u5B69\u5B50\u6062\u590D\u4F53\u4F4D\u3002</p><p>\u9884\u89C1\u6027\u4E86\u89E3\u5206\u79BB\u7126\u8651\u8FD9\u4E2A\u95EE\u9898\u3002\u575A\u6301\u5206\u5E8A\u7761\uFF0C\u4ECE\u51FA\u751F\u540E\u4E0D\u4E45\u5C31\u8981\u6709\u610F\u8BC6\u7684\u5728\u5B69\u5B50\u660F\u660F\u6B32\u7761\u65F6\u575A\u6301\u5C1D\u8BD5\u653E\u5165\u5A74\u513F\u5E8A\u4E2D\u3002</p><p>3-4\u6708\u540E\uFF0C\u5B69\u5B50\u793E\u4EA4\u9700\u6C42\u52A0\u5927\uFF0C\u767D\u5929\u9700\u8981\u79EF\u6781\u7684\u4E92\u52A8\u3002\u5F00\u59CB\u73A9\u201C\u8EB2\u732B\u732B\u201D\u6E38\u620F\u5E76\u575A\u6301\uFF0C\u53EF\u4EE5\u9010\u6E10\u5E2E\u5B69\u5B50\u660E\u767D\uFF0C\u770B\u4E0D\u89C1\u5E76\u4E0D\u4EE3\u8868\u6D88\u5931\u4E0D\u89C1\u3002\u5BF9\u5B69\u5B50\u7684\u54ED\u95F9\u53CA\u65F6\u4F5C\u51FA\u6070\u5F53\u7684\u54CD\u5E94\uFF08\u4F55\u4E3A\u6070\u5F53\uFF1F\u5C3D\u91CF\u547D\u4E2D\u9700\u6C42\u3002\u5982\u4F55\u547D\u4E2D\uFF1F\u8FD9\u5F97\u9760\u4F60\u7684\u5224\u65AD\u529B\uFF0C\u5982\u4E0A\u6587\u4E2D\u63D0\u53CA\uFF09\u3002</p><p>\u56DE\u5E94\u7684\u65B9\u5F0F\uFF0C\u6CA1\u6709\u4F18\u52A3\u4E4B\u5206\uFF0C\u5DEE\u522B\u5728\u4E8E\u7A0B\u5EA6\u3002\u62B1\u4E5F\u597D\u3001\u62CD\u4E5F\u597D\u3001\u5976\u4E5F\u597D\uFF0C\u5982\u679C\u4F60\u5224\u65AD\u4E3A\u5FC5\u8981\uFF0C\u5C3D\u7BA1\u53BB\u505A\uFF0C\u8981\u8BB0\u4F4F\u5728\u5B69\u5B50\u660F\u660F\u6B32\u7761\u65F6\u52AA\u529B\u653E\u4E0B\u3002</p><p>\u5C0F\u5A74\u513F\u8FD8\u6CA1\u6709\u80FD\u529B\u7406\u89E3\u4F60\u8A00\u8BED\u4F20\u8FBE\u7684\u610F\u56FE\uFF0C\u4E5F\u6CA1\u6709\u80FD\u529B\u53BB\u4F53\u8C05\u5927\u4EBA\u7684\u9700\u6C42\u3002\u8FD9\u4E2A\u65F6\u671F\uFF0C\u51FA\u4E8E\u751F\u7269\u7684\u672C\u80FD\u4ED6\u4F1A\u4E0D\u65AD\u7D22\u53D6\u6709\u5229\u4E8E\u81EA\u8EAB\u7684\u4E00\u5207\u8D44\u6E90\uFF0C\u5C55\u73B0\u51FA\u6765\u7684\u884C\u4E3A\u4E3B\u8981\u662F\u901A\u8FC7\u4E0D\u65AD\u7684\u7D2F\u79EF\u7ECF\u9A8C\u800C\u6210\u3002</p><p>\u6BD4\u5982\u7761\u524D\u7A0B\u5E8F\uFF0C\u5982\u679C\u4F60\u628A\u5B83\u5F53\u6210\u662F\u5B89\u629A\u624B\u6BB5\u5C31\u9519\u4E86\u3002\u5EFA\u7ACB\u7761\u524D\u7A0B\u5E8F\u7684\u76EE\u7684\u662F\u5F62\u6210\u4E00\u79CD\u826F\u6027\u7684\u5165\u7761\u5173\u8054\uFF0C\u672C\u8D28\u662F\u5EFA\u7ACB\u6761\u4EF6\u53CD\u5C04\u3002\u901A\u8FC7\u4E00\u5929\u5929\uFF0C\u56FA\u5B9A\u7684\u65F6\u95F4\u3001\u56FA\u5B9A\u7684\u573A\u666F\u3001\u505A\u56FA\u5B9A\u7684\u4E8B\u60C5\uFF0C\u83B7\u5F97\u56FA\u5B9A\u7684\u7ED3\u679C\uFF08\u7761\u89C9\uFF09\uFF0C\u8BA9\u5B69\u5B50\u5F62\u6210\u5E38\u89C4\u3002</p><p>\u5173\u8054\u5F62\u6210\u540E\uFF0C\u4E00\u65E6\u6211\u4EEC\u5F00\u542F\u7A0B\u5E8F\uFF0C\u5B69\u5B50\u4FBF\u4F1A\u660E\u767D\u63A5\u4E0B\u6765\u8BE5\u7761\u89C9\u3002\u7761\u524D\u7A0B\u5E8F\u7684\u672C\u610F\u4E0D\u662F\u4E3A\u4E86\u4E92\u52A8\u548C\u73A9\u800D\uFF0C\u53EA\u662F\u4E00\u4E2A\u63D0\u9192\uFF0C\u5C31\u5982\u542F\u52A8\u7761\u7720\u7684\u4E00\u4E2A\u6309\u94AE\uFF0C\u5E2E\u52A9\u5B69\u5B50\u83B7\u5F97\u81EA\u4E3B\u5165\u7761\u7684\u80FD\u529B\u3002</p><p>\u65E2\u7136\u662F\u80FD\u529B\uFF0C\u4FBF\u4E0D\u5FC5\u5F3A\u6C42\u4EBA\u4EBA\u4E00\u6837\uFF0C\u6709\u7684\u5B69\u5B50\u5F88\u5FEB\u80FD\u8FBE\u5230\uFF0C\u6709\u5B69\u5B50\u5374\u9700\u8981\u66F4\u4E45\u3002\u4F5C\u4E3A\u7167\u6837\u8005\u53EA\u7BA1\u505A\u5C31\u662F\u4E86\uFF0C\u65F6\u95F4\u7EC8\u4F1A\u7ED9\u51FA\u7B54\u6848\u3002\u63D0\u524D\u9884\u6D4B\u7ED3\u679C\u6BEB\u65E0\u610F\u4E49\uFF0C\u5F92\u589E\u538B\u529B\u3002</p><p>\u8FD9\u91CC\u6709\u4E2A\u5FAE\u5999\u7684\u73B0\u8C61\uFF0C\u5A74\u513F\u4EE5\u4EC0\u4E48\u65B9\u5F0F\u5165\u7761\uFF0C\u901A\u5E38\u5C31\u4F1A\u4EE5\u4EC0\u4E48\u65B9\u5F0F\u6765\u63A5\u89C9\u3002\u5176\u5B83\u56E0\u7D20\u4E0D\u53D8\u7684\u524D\u63D0\u4E0B\uFF0C\u5177\u5907\u81EA\u4E3B\u5165\u7761\u80FD\u529B\u7684\u5B69\u5B50\u591C\u9192\u65F6\u81EA\u4E3B\u63A5\u89C9\u7684\u80FD\u529B\u66F4\u5F3A\u3002</p><p>\u5F53\u5177\u5907\u81EA\u4E3B\u5165\u7761\u7684\u5B69\u5B50\u51FA\u73B0\u591C\u9192\u65F6\uFF0C\u9996\u5148\u8981\u505A\u7684\u4E8B\u60C5\u5C31\u662F\u601D\u8003\uFF0C\u9759\u5FC3\u5206\u6790\u53EF\u80FD\u7684\u539F\u56E0\uFF0C\u53EF\u4EE5\u5148\u7B49\u7B49\u3002\u82E5\u5FC3\u4E2D\u62C5\u5FE7\u53BB\u67E5\u770B\uFF0C\u5185\u5BB9\u4EC5\u9650\u4E8E\u5173\u6CE8\u5B89\u5168\u3001\u751F\u75C5\u53CA\u8212\u9002\u5EA6\u95EE\u9898\uFF0C\u514B\u5236\u63D0\u4F9B\u8FC7\u5EA6\u5E72\u9884\u3002</p><p>\u603B\u4E4B\uFF0C\u5A74\u513F\u7761\u7720\u76F8\u5173\u7684\u95EE\u9898\u5177\u6709\u666E\u904D\u6027\uFF0C\u6210\u56E0\u5404\u6709\u4E0D\u540C\uFF0C\u5F80\u5F80\u662F\u513F\u7AE5\u4E2A\u6027\u7279\u8D28\u3001\u75BE\u75C5\u3001\u53D1\u80B2\u6C34\u5E73\u3001\u517B\u80B2\u4EBA\u53CA\u73AF\u5883\u591A\u91CD\u56E0\u7D20\u5171\u540C\u4F5C\u7528\u7684\u7ED3\u679C\u3002</p><p>\u9700\u4E0D\u9700\u8981\u89E3\u51B3\uFF0C\u5982\u4F55\u89E3\u51B3\uFF0C\u4E2A\u4F53\u95F4\u7684\u5DEE\u5F02\u662F\u663E\u8457\u5B58\u5728\u7684\u3002\u6539\u5584\u7684\u65B9\u6CD5\u6709\uFF0C\u4E00\u8E74\u800C\u5C31\u836F\u5230\u75C5\u9664\u7684\u65B9\u6CD5\u6CA1\u6709\u3002</p><hr><p>\u76F8\u5173\u9605\u8BFB\uFF1A</p>",49),g=e("\u5B69\u5B50\u5230\u5E95\u51E0\u70B9\u4E0A\u5E8A\u7761\u89C9\u6700\u5408\u9002\uFF1F"),x=e("\u5B69\u5B50\u7761\u89C9\u524D\u53EF\u4E0D\u53EF\u4EE5\u559D\u725B\u5976\uFF1F\u4F1A\u5F71\u54CD\u5B69\u5B50\u957F\u9AD8\u5417\uFF1F"),z=e("\u7761\u89C9\u78E8\u7259\u5BF9\u7259\u9F7F\u6709\u5BB3\u5417\uFF1F\u6709\u65B9\u6CD5\u6CBB\u7597\u5417\uFF1F"),O=p("p",null,"\u5982\u679C\u4F60\u5728\u5E7F\u5DDE\u3001\u6DF1\u5733\u53CA\u5468\u8FB9\uFF0C\u6709\u513F\u7AE5\u4FDD\u5065\u5C31\u8BCA\u3001\u53E3\u8154\u3001\u533B\u7F8E\u5C31\u8BCA\u9700\u6C42\uFF0C\u53EF\u901A\u8FC7\u4E0B\u9762\u516C\u4F17\u53F7\u9884\u7EA6\u3002",-1),N={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651634295&idx=1&sn=094a120b53d8deac3c9839b7587b246d&chksm=8b5c938dbc2b1a9bb6500aeaa73a4312fce4d5f0ebfa925f2b0813fbb04476a2b6d40fa4a44e&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},k=e("\u67E5\u770B\u539F\u6587");function E(I,y){const o=s("RouterLink"),a=s("ExternalLinkIcon");return c(),r(d,null,[l,p("p",null,[f,t(o,{to:"/s/__biz=MzA5NzE5ODIyOQ==&mid=2651634291&idx=1&sn=b7c328c744d5cba7a8813945c07a0c7a/"},{default:n(()=>[h]),_:1}),m]),u,p("p",null,[t(o,{to:"/s/__biz=MzA5NzE5ODIyOQ==&mid=2651632747&idx=1&sn=63ec8d8dd0658588f4375f167ba84ebc/"},{default:n(()=>[g]),_:1})]),p("p",null,[t(o,{to:"/s/__biz=MzA5NzE5ODIyOQ==&mid=2651632647&idx=1&sn=db9291b5193fca46c952f5d4a9ec7880/"},{default:n(()=>[x]),_:1})]),p("p",null,[t(o,{to:"/s/__biz=MzA5NzE5ODIyOQ==&mid=2651633815&idx=2&sn=b948f0b58e7abe70fdf6ac1944eeb753/"},{default:n(()=>[z]),_:1})]),O,p("p",null,[p("a",N,[k,t(a)])])],64)}var M=i(b,[["render",E]]);export{M as default};
