import{r,o as t,c as n,a as e,b as o,F as a,d as s,e as c}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const _={},i=s('<h3 id="\u65E5\u672C\u533B\u751F\u8C08\u65E0\u6BD4\u6EF4" tabindex="-1"><a class="header-anchor" href="#\u65E5\u672C\u533B\u751F\u8C08\u65E0\u6BD4\u6EF4" aria-hidden="true">#</a> \u65E5\u672C\u533B\u751F\u8C08\u65E0\u6BD4\u6EF4</h3><p>\u65E0\u6BD4\u6EF4\u76F8\u4FE1\u5F88\u591A\u5BB6\u957F\u90FD\u4E0D\u964C\u751F\uFF0C\u4F5C\u4E3A\u4E00\u4E2A\u6765\u81EA\u65E5\u672C\u7684\u201C\u7F51\u7EA2\u201D\u4EA7\u54C1\uFF0C\u4E3B\u8981\u88AB\u7528\u4E8E\u6CBB\u7597\u868A\u866B\u53EE\u54AC\u4E4B\u540E\u5F15\u8D77\u7684\u7EA2\u80BF\u75BC\u75DB\u3002\u5B83\u5230\u5E95\u6709\u6CA1\u6709\u7528\u5462\uFF1F\u662F\u5426\u503C\u5F97\u4E70\uFF1F\u662F\u5F88\u591A\u5BB6\u957F\u5173\u6CE8\u7684\u95EE\u9898\uFF0C\u6211\u5C31\u6765\u5E2E\u5927\u5BB6\u5206\u6790\u4E00\u4E0B\u3002</p><p>\u868A\u866B\u53EE\u54AC\u540E\uFF0C\u4F1A\u51FA\u73B0\u4E18\u75B9\u3001\u98CE\u56E2\u3001\u7EA2\u80BF\u7B49\u8868\u73B0\uFF0C\u540C\u65F6\u4E5F\u4F1A\u5F15\u8D77\u5B69\u5B50\u7684\u7619\u75D2\u751A\u81F3\u75BC\u75DB\uFF0C\u8FD9\u662F\u56E0\u4E3A\u868A\u866B\u53EE\u54AC\u65F6\u7559\u5728\u76AE\u80A4\u91CC\u7684\u5206\u6CCC\u7269\u5F15\u8D77\u4E86\u8FC7\u654F\u53CD\u5E94\u3002\u513F\u7AE5\u7684\u53CD\u5E94\u4E00\u822C\u6BD4\u5927\u4EBA\u660E\u663E\uFF0C\u4F46\u901A\u5E38\u51E0\u5929\u540E\u76AE\u75B9\u4E5F\u53EF\u4EE5\u81EA\u7136\u6062\u590D\uFF0C\u5F88\u5C11\u51FA\u73B0\u4E25\u91CD\u7684\u8FC7\u654F\u53CD\u5E94\u3002</p><p>\u6240\u4EE5\u6CBB\u7597\u866B\u54AC\u6027\u76AE\u708E\u7684\u91CD\u70B9\u5728\u4E8E\u7F13\u89E3\u7619\u75D2\u548C\u75BC\u75DB\u3002\u9664\u4E86\u51B7\u6577\uFF0C\u5E76\u544A\u8BC9\u5B69\u5B50\u5C3D\u91CF\u907F\u514D\u6293\u6320\u4EE5\u5916\uFF0C\u4E5F\u53EF\u4EE5\u7528\u4E00\u4E9B\u836F\u7269\u6765\u7F13\u89E3\u75C7\u72B6\uFF0C\u836F\u7269\u901A\u5E38\u6709\u4E24\u7C7B\u53EF\u4EE5\u9009\u62E9\uFF1A</p><p><strong>1. \u5916\u7528\u6FC0\u7D20\u6210\u5206</strong></p><p><strong>2. \u53E3\u670D\u6297\u7EC4\u80FA\u6210\u5206</strong></p><p><strong>\u65E0\u6BD4\u6EF4\u5C5E\u4E8E\u54EA\u4E00\u7C7B\u5462\uFF1F</strong></p><p>\u4E2D\u56FD\u7684\u7535\u5546\u5E73\u53F0\u4E0A\uFF0C\u53EF\u4EE5\u770B\u5230\u5F88\u591A\u5F88\u591A\u6807\u793A\u4E3A\u201C\u65E0\u6BD4\u6EF4\u201D\u7684\u4EA7\u54C1\uFF0C\u9664\u4E86\u65E5\u672C\u7248\u548C\u9999\u6E2F\u7248\u4EE5\u5916\uFF0C\u8FD8\u6709\u5F88\u591A\u6210\u5206\u3001\u5242\u578B\u3001\u4EA7\u5730\u90FD\u4E0D\u660E\u786E\u7684\u7248\u672C\uFF0C\u5C31\u4E0D\u5728\u8FD9\u4E2A\u8BA8\u8BBA\u8303\u56F4\u5185\u4E86\u3002</p><p>\u65E5\u672C\u7248\u7684\u65E0\u6BD4\u6EF4\u4E3B\u8981\u6709\u4E24\u79CD</p><p>\u300C\u6DB2\u4F53\u30E0\u30D2S2a\u300D\uFF08\u6210\u4EBA\u7248\uFF09</p><p>\u300C\u6DB2\u4F53\u30E0\u30D2\u30D9\u30D3\u30FC\u300D\uFF08\u513F\u7AE5\u7248\uFF09</p><p>\u5E38\u89C1\u7684\u65E5\u672C\u7248\u65E0\u6BD4\u6EF4\uFF1A\u5DE6\u4FA7\u6210\u4EBA\u7248\uFF1B\u53F3\u4FA7\u513F\u7AE5\u7248</p><p>\u6839\u636E\u6709\u6548\u6210\u5206\u7684\u533A\u522B\uFF0C\u65E0\u6BD4\u6EF4\u53EF\u4EE5\u5206\u4E3A\u4E09\u7C7B\uFF1A</p><p><strong>1. \u6297\u7EC4\u80FA\u6210\u5206</strong></p><p><strong>2. \u6297\u7EC4\u80FA\u6210\u5206+\u8584\u8377\u9187\u3001\u6A1F\u8111\uFF08\u6E05\u51C9\u4F5C\u7528\uFF09</strong></p><p><strong>3. \u6297\u7EC4\u80FA\u6210\u5206+\u6FC0\u7D20\u6210\u5206+\u8584\u8377\u9187\u3001\u6A1F\u8111</strong></p><p>\u4E0A\u56FE\u91CC\u5DE6\u4FA7\u6210\u4EBA\u7248\u5C5E\u4E8E\u7B2C3\u7C7B\uFF0C\u53F3\u4FA7\u513F\u7AE5\u7248\u5C5E\u4E8E\u7B2C1\u7C7B\u3002</p><p>\u65E0\u6BD4\u6EF4\u91CC\u7528\u7684\u6297\u7EC4\u80FA\u6210\u5206\u662F\u82EF\u6D77\u62C9\u660E\uFF0C\u6FC0\u7D20\u5219\u662F\u5730\u585E\u7C73\u677E\u6216\u6CFC\u5C3C\u677E\u9F99\u3002\u6297\u7EC4\u80FA\u6210\u5206\u201C\u82EF\u6D77\u62C9\u660E\u201D\u662F\u5E38\u89C1\u7684\u975E\u5904\u65B9\u6B62\u75D2\u836F\u7269\uFF0C\u4F46\u662F\u76EE\u524D\u7684\u7814\u7A76\u8868\u660E\uFF0C\u5728\u7F13\u89E3\u7619\u75D2\u65B9\u9762\uFF0C<strong>\u5916\u7528\u6297\u7EC4\u80FA\u6210\u5206\u7684\u6548\u679C\u975E\u5E38\u4F4E\uFF0C\u5E76\u4E14\u6709\u53EF\u80FD\u5F15\u8D77\u63A5\u89E6\u6027\u76AE\u708E</strong>\uFF0C\u6240\u4EE5\u76AE\u80A4\u79D1\u533B\u751F\u4E00\u822C\u4E0D\u4F1A\u63A8\u8350\u5916\u7528\u6297\u7EC4\u80FA\u836F\u7269\u6765\u6CBB\u7597\u866B\u54AC\u6027\u76AE\u708E\u3002</p><p><strong>\u65E0\u6BD4\u6EF4\u5BF9\u4E8E\u866B\u54AC\u6027\u76AE\u708E\u6709\u6CA1\u6709\u6548\u679C\uFF1F</strong></p><p>\u6240\u4EE5\uFF0C\u5BF9\u4E8E\u5927\u5BB6\u90FD\u5173\u5FC3\u7684\u95EE\u9898\u662F\uFF1A\u65E0\u6BD4\u6EF4\u5BF9\u4E8E\u866B\u54AC\u6027\u76AE\u708E\u6709\u6CA1\u6709\u6548\u679C\uFF1F\u7B80\u5355\u7684\u56DE\u7B54\u662F\uFF1A\u6709\u4E9B\u6CA1\u6709\uFF08\u7B2C1\u30012\u7C7B\uFF09\uFF0C\u6709\u4E9B\u6709\uFF08\u7B2C3\u7C7B\uFF09\u3002</p><p>\u6709\u6548\u7684\u90A3\u7C7B\u4EA7\u54C1\uFF0C\u6548\u679C\u5C31\u662F\u6765\u81EA\u5176\u4E2D\u7684\u6FC0\u7D20\uFF0C\u6BD4\u5982\u5730\u585E\u7C73\u677E\u6216\u6CFC\u5C3C\u677E\u9F99\u3002\u4F5C\u4E3A\u975E\u5904\u65B9\u836F\u7269\uFF0C\u65E0\u6BD4\u6EF4\u91CC\u6FC0\u7D20\u7684\u6D53\u5EA6\u662F\u5BF9\u5E94\u5904\u65B9\u836F\u7269\u7684\u4E00\u534A\uFF0C\u4F5C\u7528\u5F3A\u5EA6\u9AD8\u4E8E\u6C22\u5316\u53EF\u7684\u677E\uFF0C\u7A0D\u5FAE\u4F4E\u4E8E\u5730\u5948\u5FB7\uFF0C\u4ECD\u7136\u5C5E\u4E8E\u5F31\u6548\u6FC0\u7D20\u6210\u5206\uFF0C\u53EF\u4EE5\u77ED\u671F\u4F7F\u7528\u662F\u5B89\u5168\u7684\uFF0C\u4F46\u91CC\u9762\u542B\u5B69\u5B50\u4E0D\u80FD\u7528\u7684\u6A1F\u8111\uFF0C\u6240\u4EE5\u5B83\u53EA\u80FD\u7ED9\u5927\u4EBA\u81EA\u5DF1\u7528\uFF0C\u800C\u4E0D\u80FD\u7ED9\u5B69\u5B50\u7528\u3002</p><p>\u603B\u7684\u6765\u8BF4\uFF0C\u513F\u7AE5\u578B\u7684\u65E0\u6BD4\u6EF4\u5BF9\u4E8E\u5B69\u5B50\u866B\u54AC\u6027\u76AE\u708E\u6CA1\u6709\u6548\u679C\uFF0C\u6210\u4EBA\u578B\u7684\u65E0\u6BD4\u6EF4\u542B\u6709\u5BF9\u513F\u7AE5\u4E0D\u5B89\u5168\u7684\u6A1F\u8111\u6210\u5206\uFF0C\u6240\u4EE5\u4E0D\u662F\u597D\u7684\u9009\u62E9\u3002</p><p>\u90A3\u5B69\u5B50\u88AB\u868A\u866B\u53EE\u54AC\u8BE5\u7528\u4EC0\u4E48\u836F\u5462\uFF1F\u56DE\u5230\u524D\u9762\u8BF4\u8FC7\u7684\uFF0C\u6211\u4EEC\u53EF\u4EE5\u5728\u533B\u751F\u6307\u5BFC\u4E0B\u53E3\u670D\u6297\u7EC4\u80FA\u836F\u7269\uFF0C\u5C31\u5916\u7528\u836F\u800C\u8A00\uFF0C\u6211\u63A8\u8350\u5927\u5BB6\u9009\u62E9\u5355\u7EAF\u7684\u6FC0\u7D20\u836F\u818F\uFF0C\u5408\u9002\u7684\u5916\u7528\u6FC0\u7D20\uFF0C\u77ED\u671F\u4F7F\u7528\u4E5F\u662F\u5B89\u5168\u7684\u3002</p><hr><p>\u957F\u6309\u8BC6\u522B\u4E8C\u7EF4\u7801\uFF0C\u53EF\u5411\u672C\u6587\u4F5C\u8005\u54A8\u8BE2</p>',25),g={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651627336&idx=1&sn=59d3bf07ee02a56b24fdfbc714f006e7&chksm=8b5cb6b2bc2b3fa4101ba84ee35938a4783fa9682563ba90400c315b2dca88d8128af8a1ce23&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},f=c("\u67E5\u770B\u539F\u6587");function h(l,b){const p=r("ExternalLinkIcon");return t(),n(a,null,[i,e("p",null,[e("a",g,[f,o(p)])])],64)}var u=d(_,[["render",h]]);export{u as default};
