import{r as p,o as r,c as t,a as e,b as n,F as c,d as o,e as s}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const i={},_=o('<h3 id="\u5B69\u5B50\u6392\u4FBF\u5C11-\u4FBF\u79D8\u8FD8\u662F\u5DE8\u7ED3\u80A0" tabindex="-1"><a class="header-anchor" href="#\u5B69\u5B50\u6392\u4FBF\u5C11-\u4FBF\u79D8\u8FD8\u662F\u5DE8\u7ED3\u80A0" aria-hidden="true">#</a> \u5B69\u5B50\u6392\u4FBF\u5C11\uFF0C\u4FBF\u79D8\u8FD8\u662F\u5DE8\u7ED3\u80A0\uFF1F</h3><p>\u5148\u5929\u6027\u5DE8\u7ED3\u80A0\u4E0D\u662F\u4E00\u4E2A\u592A\u5E38\u89C1\u7684\u75C5\uFF0C\u5F88\u591A\u5BB6\u957F\u53EF\u80FD\u6CA1\u542C\u8FC7\uFF0C\u6B63\u56E0\u5982\u6B64\uFF0C\u6709\u4E9B\u5BB6\u957F\u4E00\u76F4\u4EE5\u4E3A\u5B69\u5B50\u4FBF\u79D8\uFF0C\u6CA1\u5F53\u56DE\u4E8B\uFF0C\u76F4\u5230\u5B69\u5B50\u8179\u80C0\u5982\u7403\u6D88\u7626\u5982\u67F4\u624D\u5E26\u5230\u533B\u9662\u68C0\u67E5\uFF0C\u7ED3\u679C\u88AB\u8BC1\u5B9E\u662F\u5DE8\u7ED3\u80A0\uFF0C\u7136\u540E\u505A\u4E86\u624B\u672F\uFF0C\u4F46\u56E0\u4E3A\u80A0\u7BA1\u6269\u5F20\u592A\u5389\u5BB3\uFF0C\u624B\u672F\u66F4\u56F0\u96BE\uFF0C\u5207\u9664\u7684\u80A0\u7BA1\u4E5F\u66F4\u591A\uFF0C\u672F\u540E\u5E76\u53D1\u75C7\u7684\u98CE\u9669\u4E5F\u589E\u5927\uFF0C\u5B69\u5B50\u4E5F\u66F4\u75DB\u82E6\u3002</p><p>\u968F\u7740\u7ECF\u6D4E\u6587\u5316\u7684\u53D1\u5C55\u548C\u533B\u5B66\u77E5\u8BC6\u7684\u666E\u53CA\uFF0C\u5BB6\u957F\u4EEC\u8D8A\u6765\u8D8A\u91CD\u89C6\u5B69\u5B50\u7684\u5065\u5EB7\uFF0C\u62D6\u5230\u5F88\u5927\u518D\u505A\u624B\u672F\u7684\u5B69\u5B50\u5076\u5C14\u4E5F\u80FD\u9047\u5230\uFF0C\u4F46\u8D8A\u6765\u8D8A\u5C11\uFF0C\u76F8\u53CD\uFF0C\u5728\u6211\u4EEC\u666E\u5916\u79D1\u95E8\u8BCA\uFF0C\u56E0\u4E3A\u5B69\u5B50\u51E0\u5929\u6CA1\u62C9\u5927\u4FBF\u6765\u627E\u6392\u9664\u662F\u4E0D\u662F\u5DE8\u7ED3\u80A0\u7684\u5BB6\u957F\u8D8A\u6765\u8D8A\u591A\u3002\u5BF9\u75BE\u75C5\u4FDD\u6301\u8B66\u60D5\u662F\u5BF9\u7684\uFF0C\u65E9\u53D1\u73B0\u65E9\u6CBB\u7597\uFF0C\u53EF\u4EE5\u8BA9\u5F88\u591A\u5B69\u5B50\u907F\u514D\u906D\u53D7\u66F4\u5927\u7684\u75DB\u82E6\u548C\u4E0D\u5E78\uFF0C\u4F46\u56E0\u4E3A\u5B69\u5B50\u51E0\u5929\u6CA1\u62C9\u5927\u4FBF\u5C31\u5BDD\u98DF\u96BE\u5B89\u4E5F\u6CA1\u5FC5\u8981\uFF0C\u90A3\u54EA\u4E9B\u60C5\u51B5\u9700\u8981\u8B66\u60D5\u5DE8\u7ED3\u80A0\u5462\uFF1F</p><p>\u6240\u8C13\u5DE8\u7ED3\u80A0\u4E5F\u5C31\u662F\u7ED3\u80A0\u6269\u5F20\u80A5\u539A\uFF0C\u4F46\u771F\u6B63\u75C5\u53D8\u7684\u5E76\u4E0D\u662F\u6269\u5F20\u7684\u7ED3\u80A0\uFF0C\u800C\u662F\u6269\u5F20\u80A0\u7BA1\u4E0B\u65B9\u7684\u7EC6\u5C0F\u80A0\u7BA1\uFF0C\u4E5F\u5C31\u662F\u9760\u8FD1\u809B\u95E8\u7684\u90A3\u4E00\u6BB5\uFF08\u89C1\u9898\u56FE\uFF09\u3002\u8FD9\u6BB5\u80A0\u5B50\u56E0\u4E3A\u7F3A\u5C11\u4E86\u795E\u7ECF\u8282\u7EC6\u80DE\uFF0C\u957F\u671F\u5904\u4E8E\u75C9\u631B\u7EC6\u5C0F\u7684\u72B6\u6001\uFF0C\u5927\u4FBF\u5230\u4E86\u8FD9\u91CC\u5C31\u5F88\u96BE\u901A\u8FC7\u800C\u56E4\u79EF\u5728\u4E0A\u65B9\u6B63\u5E38\u7684\u80A0\u7BA1\u91CC\uFF0C\u4E45\u800C\u4E45\u4E4B\u4E0A\u65B9\u7684\u80A0\u7BA1\u5C31\u6269\u5F20\u80A5\u539A\uFF0C\u53D8\u6210\u4E86\u201C\u5DE8\u7ED3\u80A0\u201D\u4E86\u3002</p><p>\u53EF\u60F3\u800C\u77E5\uFF0C\u4E00\u65E6\u5927\u4FBF\u6392\u51FA\u56F0\u96BE\uFF0C\u5927\u4FBF\u5B58\u79EF\u5728\u80A0\u5B50\u91CC\uFF0C\u5B69\u5B50\u5C31\u4F1A\u6162\u6162\u809A\u5B50\u80C0\uFF0C\u5403\u4E1C\u897F\u4E0D\u597D,\u751A\u81F3\u5455\u5410\uFF0C\u6240\u4EE5\u5DE8\u7ED3\u80A0\u6700\u5E38\u89C1\u7684\u8868\u73B0\u662F\uFF1A\u6392\u4FBF\u56F0\u96BE\u3001\u8179\u80C0\u3001\u5582\u517B\u56F0\u96BE\uFF0C\u65F6\u95F4\u957F\u4E86\u5C31\u4F1A\u51FA\u73B0\u8425\u517B\u4E0D\u826F\u3002\u75C5\u53D8\u80A0\u7BA1\u957F\u5EA6\u4E0D\u4E00\u6837\uFF0C\u75C7\u72B6\u7684\u8F7B\u91CD\u4E5F\u53EF\u80FD\u4F1A\u6709\u6240\u4E0D\u540C\uFF0C\u800C\u4E14\u53EF\u80FD\u56E0\u4E3A\u7ED3\u80A0\u5185\u5927\u4FBF\u957F\u671F\u6DE4\u79EF\uFF0C\u7EC6\u83CC\u589E\u6B96\u7B49\u8BF1\u53D1\u5C0F\u80A0\u7ED3\u80A0\u708E\u3001\u611F\u67D3\u6027\u4F11\u514B\u7B49\u5371\u6025\u60C5\u51B5\u3002</p><p>\u90A3\u600E\u6837\u533A\u5206\u666E\u901A\u7684\u529F\u80FD\u6027\u4FBF\u79D8\u548C\u5DE8\u7ED3\u80A0\u7684\u6392\u4FBF\u56F0\u96BE\u5462\u5462\uFF1F\u6700\u91CD\u8981\u7684\u4E00\u70B9\u5C31\u662F\u5DE8\u7ED3\u80A0\u7684\u6392\u4FBF\u56F0\u96BE\u5927\u90E8\u5206\u5728\u51FA\u751F\u65F6\u5019\u5C31\u5F00\u59CB\u4E86\uFF0C\u5065\u5EB7\u8DB3\u6708\u7684\u5B69\u5B5095%\u4EE5\u4E0A\u5728\u751F\u540E48\u5C0F\u65F6\u5185\u4F1A\u6392\u51FA\u80CE\u4FBF\uFF0C\u5982\u679C\u5B69\u5B50\u5728\u51FA\u751F\u540E48\u5C0F\u65F6\u5185\u6CA1\u6709\u81EA\u5DF1\u6392\uFF0C\u90A3\u5C31\u5E94\u8BE5\u627E\u5C0F\u513F\u5916\u79D1\u533B\u751F\u770B\u770B\u4E86\uFF0C\u5F53\u7136\u4E0D\u4E00\u5B9A\u5C31\u662F\u5DE8\u7ED3\u80A0\uFF0C\u4F46\u81F3\u5C11\u5E94\u8BE5\u8003\u8651\u8FD9\u4E2A\u53EF\u80FD\u6027\u4E86\u3002\u4E0B\u8868\u662F\u529F\u80FD\u6027\u4FBF\u79D8\u548C\u5DE8\u7ED3\u80A0\u7684\u4E00\u4E9B\u7279\u5F81\u533A\u522B\u3002</p><p>\u81F3\u4E8E\u6709\u4E9B\u5B69\u5B50\uFF0C\u5C24\u5176\u662F\u5C24\u5176\u662F\u6BCD\u4E73\u5582\u517B\u7684\u5B69\u5B50\uFF0C\u6709\u65F6\u5019\u53EA\u662F\u4E09\u56DB\u5929\u6CA1\u62C9\u5927\u4FBF\uFF0C\u5BB6\u957F\u9A6C\u4E0A\u5E26\u5230\u6211\u4EEC\u5916\u79D1\u6765\u6392\u9664\u5DE8\u7ED3\u80A0\u3002\u4FDD\u6301\u8B66\u60D5\u6CA1\u9519\uFF0C\u4F46\u6211\u4EEC\u9700\u8981\u77E5\u9053\u3002\u5982\u679C\u5B69\u5B50\u5403\u5976\u597D\uFF0C\u809A\u5B50\u4E0D\u80C0\uFF0C\u5927\u4FBF\u4E0D\u5E72\u4E0D\u786C\uFF0C\u5355\u7EAF\u6B21\u6570\u5C11\u8FDE\u4FBF\u79D8\u90FD\u7B97\u4E0D\u4E0A\uFF0C\u4E0D\u5FC5\u8FC7\u5EA6\u7D27\u5F20\u3002\u770B\u770B\u4E0B\u8868\u4E2D\u513F\u7AE5\u4FBF\u79D8\u7684\u8BCA\u65AD\u6807\u51C6\u5C31\u53EF\u4EE5\u660E\u767D\u3002</p><p>\u5176\u5B9E\u770B\u4E86\u8868\u4E00\uFF0C\u5927\u6982\u4E5F\u53EF\u4EE5\u660E\u767D\u533B\u751F\u662F\u600E\u4E48\u53BB\u5224\u65AD\u4E00\u4E2A\u5B69\u5B50\u662F\u4E0D\u662F\u5DE8\u7ED3\u80A0\uFF0C\u5728\u6709\u75C5\u53F2\u3001\u67E5\u4F53\u7684\u57FA\u7840\u4E0A\u9700\u8981\u518D\u7ED3\u5408\u4E00\u4E9B\u68C0\u67E5\u6765\u7EFC\u5408\u5224\u65AD\u3002\u6700\u5E38\u7528\u7684\u68C0\u67E5\u662F\u94A1\u704C\u80A0\u9020\u5F71\uFF0C\u8FD9\u4E2A\u68C0\u67E5\u6BD4\u8F83\u65B9\u4FBF\uFF0C\u5C31\u662F\u628A\u9020\u5F71\u5242\uFF08\u94A1\u5242\u6216\u7898\u9020\u5F71\u5242\u4ECE\u809B\u95E8\u6CE8\u5165\u7ED3\u80A0\uFF09\uFF0C\u5728X\u7EBF\u4E0B\u663E\u793A\u7ED3\u80A0\u5F62\u6001\uFF0C\u770B\u770B\u6709\u6CA1\u6709\u9898\u56FE\u6240\u793A\u7684\u75C9\u631B\u6BB5\u3001\u79FB\u884C\u6BB5\u3001\u6269\u5F20\u6BB5\u3002\u8FD9\u4E2A\u68C0\u67E5\u7684\u4F18\u52BF\u662F\u76F4\u89C2\u53EF\u9760\uFF0C\u65E0\u521B\u4F24\uFF0C\u8FD8\u53EF\u4EE5\u901A\u8FC7\u94A1\u5242\u7684\u6392\u7A7A\u65F6\u95F4\u6765\u5224\u65AD\u7ED3\u80A0\u7684\u529F\u80FD\u3002\u7F3A\u70B9\u662F\u6709\u70B9\u8F90\u5C04\uFF0C\u4F46\u89C4\u8303\u64CD\u4F5C\u7684\u8BDD\uFF0C\u8F90\u5C04\u91CF\u4E5F\u5E76\u4E0D\u5927\uFF0C\u662F\u5F88\u5B89\u5168\u7684\u68C0\u67E5\u65B9\u5F0F\uFF0C\u4E5F\u51E0\u4E4E\u662F\u8BCA\u65AD\u5DE8\u7ED3\u80A0\u5FC5\u505A\u7684\u68C0\u67E5\u3002\u4F46\u8FD9\u4E2A\u68C0\u67E5\u5728\u4E00\u4E9B\u65B0\u751F\u513F\u671F\u95F4\u53EF\u80FD\u4E0D\u662F\u90A3\u4E48\u51C6\u786E\uFF0C\u56E0\u4E3A\u5728\u75C5\u53D8\u65E9\u671F\uFF0C\u75C5\u53D8\u80A0\u7BA1\u4E0A\u65B9\u7684\u80A0\u7BA1\u6269\u5F20\u5F97\u8FD8\u4E0D\u90A3\u4E48\u4E25\u91CD\u7684\u65F6\u5019\uFF0C\u72ED\u7A84\u3001\u79FB\u884C\u3001\u6269\u5F20\u6BB5\u7684\u5DEE\u522B\u4E0D\u90A3\u4E48\u5927\uFF0C\u5C31\u4E0D\u597D\u5224\u65AD\u662F\u6216\u8005\u4E0D\u662F\uFF0C\u5C24\u5176\u5BF9\u90A3\u4E9B\u75C5\u53D8\u80A0\u7BA1\u4E0D\u90A3\u4E48\u957F\u7684\u5B69\u5B50\u6765\u8BF4\uFF0C\u53EF\u80FD\u9700\u8981\u914D\u5408\u5176\u4ED6\u68C0\u67E5\u6216\u8FC7\u6BB5\u65F6\u95F4\u518D\u590D\u67E5\u6765\u786E\u5B9A\u3002</p><p>\u75C5\u7406\u6D3B\u68C0\u662F\u5F88\u591A\u75BE\u75C5\u7684\u91D1\u6807\u51C6\uFF0C\u5305\u62EC\u5DE8\u7ED3\u80A0\uFF0C\u6D3B\u68C0\u5C31\u662F\u5207\u9664\u4E00\u5757\u7EC4\u7EC7\u75C5\u7406\u67D3\u8272\u540E\u518D\u5728\u7531\u75C5\u7406\u533B\u751F\u5728\u663E\u5FAE\u955C\u4E0B\u89C2\u5BDF\u5224\u65AD\uFF0C\u5982\u679C\u53D1\u73B0\u5728\u6B63\u5E38\u5E94\u8BE5\u6709\u795E\u7ECF\u8282\u7EC6\u80DE\u7684\u80A0\u5B50\u91CC\u6CA1\u6709\u795E\u7ECF\u8282\u7EC6\u80DE\uFF0C\u90A3\u5C31\u53EF\u4EE5\u6709\u5DE8\u7ED3\u80A0\u4E86\u3002\u4F46\u6240\u8C13\u7684\u91D1\u6807\u51C6\u4E5F\u662F\u76F8\u5BF9\u7684\uFF0C\u56E0\u4E3A\u8FD9\u4E2A\u68C0\u67E5\u7ED3\u679C\u540C\u6837\u4F1A\u53D7\u5230\u53D6\u6750\u65B9\u6CD5\u3001\u90E8\u4F4D\uFF0C\u75C5\u7406\u67D3\u8272\u65B9\u5F0F\u3001\u65B9\u6CD5\uFF0C\u75C5\u7406\u533B\u751F\u7ECF\u9A8C\u7B49\u56E0\u7D20\u7684\u5F71\u54CD\uFF0C\u6709\u65F6\u5019\u8FD8\u9700\u8981\u7EFC\u5408\u75C5\u53F2\u67E5\u4F53\u548C\u5176\u4ED6\u68C0\u67E5\u7ED3\u679C\u6765\u7EFC\u5408\u5224\u65AD\uFF0C\u4F46\u603B\u4F53\u800C\u8A00\uFF0C\u75C5\u7406\u7684\u53C2\u8003\u610F\u4E49\u8981\u5927\u4E8E\u5176\u4ED6\u68C0\u67E5\u65B9\u6CD5\uFF0C\u4F46\u7F3A\u70B9\u662F\u6709\u4E00\u5B9A\u7684\u521B\u4F24\uFF0C\u867D\u7136\u73B0\u5728\u6709\u76F4\u80A0\u7C98\u819C\u6D3B\u68C0\u8FD9\u6837\u7684\u635F\u4F24\u66F4\u5C0F\u7684\u65B9\u6CD5\uFF0C\u4F46\u53EF\u9760\u5EA6\u4E5F\u6709\u6240\u964D\u4F4E\u3002</p><p>\u53E6\u5916\u4E00\u4E2A\u68C0\u67E5\u5C31\u662F\u76F4\u80A0\u809B\u7BA1\u6D4B\u538B\uFF0C\u901A\u8FC7\u68C0\u67E5\u5B69\u5B50\u7684\u76F4\u80A0\u809B\u7BA1\u5185\u7684\u538B\u529B\u548C\u76F4\u80A0\u809B\u95E8\u6291\u5236\u53CD\u5C04\u7B49\u60C5\u51B5\uFF0C\u4E5F\u53EF\u4EE5\u7528\u6765\u5224\u65AD\u5B69\u5B50\u662F\u5426\u5B58\u5728\u5F02\u5E38\uFF0C\u68C0\u67E5\u7ED3\u679C\u4E5F\u53EF\u4EE5\u4F5C\u4E3A\u5224\u65AD\u5DE8\u7ED3\u80A0\u7684\u53C2\u8003\u6307\u6807\uFF0C\u64CD\u4F5C\u89C4\u8303\u7684\u8BDD\u7075\u654F\u5EA6\u4E5F\u6BD4\u8F83\u9AD8\uFF0C\u5BF9\u4E8E\u90A3\u4E9B\u75C5\u53D8\u80A0\u7BA1\u6BD4\u8F83\u77ED\uFF0C\u5728\u9020\u5F71\u5F88\u96BE\u663E\u793A\u7684\u65F6\u5019\u66F4\u6709\u610F\u4E49\u3002\u81F3\u4E8E\u5DE8\u7ED3\u80A0\u809B\u95E8\u6307\u5F81\u65F6\u7684\u201C\u88F9\u624B\u611F\u201D\uFF0C\u8FD9\u79CD\u611F\u89C9\u6709\u5F88\u5927\u7684\u4E3B\u89C2\u6027\uFF0C\u5C24\u5176\u5BF9\u4E8E\u65B0\u751F\u513F\u6765\u8BF4\uFF0C\u809B\u95E8\u672C\u6765\u5C31\u6BD4\u8F83\u5C0F\uFF0C\u6210\u4EBA\u7684\u5C0F\u6307\u8FDB\u53BB\u90FD\u6BD4\u8F83\u56F0\u96BE\uFF0C\u8FDB\u53BB\u4E4B\u540E\u90FD\u4F1A\u6709\u4E0D\u540C\u7A0B\u5EA6\u7684\u201C\u88F9\u624B\u611F\u201D\uFF0C\u53EF\u4EE5\u4F5C\u4E3A\u8BCA\u65AD\u7684\u4E00\u70B9\u53C2\u8003\uFF0C\u4F46\u53C2\u8003\u610F\u4E49\u5E76\u4E0D\u5927\u3002</p><p>\u5178\u578B\u7684\u5DE8\u7ED3\u80A0\u8BCA\u65AD\u6BD4\u8F83\u5BB9\u6613\uFF0C\u4F46\u78B0\u5230\u4E0D\u5178\u578B\u7684\u75C5\u4F8B\u8BCA\u65AD\u4E5F\u4F1A\u6BD4\u8F83\u56F0\u96BE\uFF0C\u9700\u8981\u533B\u751F\u8BE6\u7EC6\u8BE2\u95EE\u75C5\u53F2\uFF0C\u4ED4\u7EC6\u505A\u4F53\u683C\u68C0\u67E5\uFF0C\u518D\u51B3\u5B9A\u662F\u5426\u9700\u8981\u505A\u8FDB\u4E00\u6B65\u7684\u94A1\u704C\u80A0\u3001\u6D3B\u68C0\u3001\u76F4\u80A0\u809B\u95E8\u6D4B\u538B\u6765\u7EFC\u5408\u5224\u65AD\uFF0C\u81F3\u4E8E\u505A\u54EA\u4E2A\u68C0\u67E5\uFF0C\u505A\u4E00\u4E2A\u8FD8\u662F\u51E0\u4E2A\uFF0C\u53EF\u80FD\u4F1A\u56E0\u4EBA\u800C\u5F02\uFF0C\u65E2\u8981\u770B\u5B69\u5B50\u7684\u60C5\u51B5\uFF0C\u4E5F\u8981\u770B\u533B\u751F\u81EA\u5DF1\u7684\u7ECF\u9A8C\u3002\u5B69\u5B50\u75C5\u60C5\u590D\u6742\u7A0B\u5EA6\u4E0D\u4E00\u6837\uFF0C\u533B\u751F\u5BF9\u8FD9\u4E9B\u68C0\u67E5\u7684\u770B\u91CD\u7A0B\u5EA6\u4E0D\u4E00\u6837\uFF0C\u90FD\u53EF\u80FD\u4F1A\u6709\u4E0D\u540C\u7684\u9009\u62E9\uFF0C\u8FD9\u4E9B\u4E13\u4E1A\u6027\u7684\u9009\u62E9\u548C\u5224\u65AD\u8FD8\u662F\u4EA4\u7ED9\u533B\u751F\u6765\u5B9A\u3002</p><p>\u5DE8\u7ED3\u80A0\u8BCA\u65AD\u4E00\u65E6\u660E\u786E\uFF0C\u90A3\u5C31\u9700\u8981\u624B\u672F\u6CBB\u7597\uFF0C\u4E5F\u5C31\u662F\u628A\u6CA1\u795E\u7ECF\u8282\u7EC6\u80DE\u7684\u75C5\u53D8\u80A0\u7BA1\u5207\u9664\uFF0C\u53E6\u5916\u6269\u5F20\u80A5\u539A\u7684\u80A0\u7BA1\u56E0\u4E3A\u529F\u80FD\u4E5F\u4F1A\u51FA\u73B0\u95EE\u9898\u4E5F\u9700\u8981\u4E00\u5E76\u5207\u9664\u3002\u5927\u90E8\u5206\u5B69\u5B50\u624B\u672F\u540E\u90FD\u4E0D\u9519\uFF0C\u5C24\u5176\u662F\u5B8C\u5168\u7ECF\u809B\u95E8\u5185\u5207\u9664\u7684\u5B69\u5B50\u6839\u672C\u770B\u4E0D\u51FA\u505A\u8FC7\u624B\u672F\uFF0C\u4F46\u4E5F\u6709\u5C11\u90E8\u5206\u5B69\u5B50\u51FA\u73B0\u4E00\u4E9B\u5E76\u53D1\u75C7\uFF0C\u6BD4\u5982\u6C61\u7CAA\u3001\u4FBF\u79D8\u7B49\u95EE\u9898\uFF0C\u4F46\u5E76\u4E0D\u591A\u89C1\u3002</p><p>\u8981\u4E0D\u8981\u505A\u68C0\u67E5\uFF0C\u505A\u4EC0\u4E48\u68C0\u67E5\uFF0C\u6839\u636E\u68C0\u67E5\u600E\u4E48\u5224\u65AD\u662F\u4E0D\u662F\u5DE8\u7ED3\u80A0\uFF0C\u8FD9\u4E9B\u4E13\u4E1A\u95EE\u9898\u8FD8\u662F\u4EA4\u7ED9\u4E13\u4E1A\u7684\u533B\u751F\u53BB\u5224\u65AD\u597D\u4E86\uFF0C\u5BB6\u957F\u6240\u8981\u505A\u7684\u662F\u63D0\u4F9B\u5B69\u5B50\u8BE6\u7EC6\u7684\u75C5\u53F2\u7ED9\u533B\u751F\uFF0C\u6BD4\u5982\u51FA\u751F\u540E\u7B2C\u4E00\u6B21\u6392\u4FBF\u7684\u65F6\u95F4\uFF0C\u5E73\u65F6\u809A\u5B50\u80C0\u7684\u60C5\u51B5\uFF0C\u80FD\u591F\u5927\u81F4\u533A\u5206\u4E00\u4E0B\u666E\u901A\u7684\u4FBF\u79D8\u548C\u5DE8\u7ED3\u80A0\uFF0C\u6709\u95EE\u9898\u53CA\u65F6\u5C31\u533B\uFF0C\u65E2\u4E0D\u5EF6\u8BEF\u6CBB\u7597\uFF0C\u4E5F\u4E0D\u8FC7\u5EA6\u6050\u60E7\u3002\uFF08\u539F\u521B\u6587\u7AE0\u672A\u7ECF\u8BB8\u53EF\u8C22\u7EDD\u8F6C\u8F7D\uFF09</p><p><strong>\u6743\u5A01\u7684\u80B2\u513F\u79D1\u666E\uFF0C\u6DF1\u5EA6\u7684\u533B\u5B66\u4EBA\u6587</strong></p><p><strong>\u4EC5\u63A8\u9001\u539F\u521B\u6587\u7AE0\uFF0C\u6B22\u8FCE\u5173\u6CE8drpeihg</strong></p>',15),h={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=202640618&idx=1&sn=237c9c3ef6d935e51162ba302bb8b391&chksm=194425102e33ac0666856e8bb06039a36fff224ad2d68bd6942c2de0f06cead1cfe310b1dce0&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},l=s("\u67E5\u770B\u539F\u6587");function f(m,b){const a=p("ExternalLinkIcon");return r(),t(c,null,[_,e("p",null,[e("a",h,[l,n(a)])])],64)}var u=d(i,[["render",f]]);export{u as default};
