import{r as a,o as r,c as t,a as e,b as n,F as o,d as c,e as s}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const _={},i=c('<h3 id="\u53D1\u70E7\u62BD\u7B4B-\u5BB6\u957F\u9700\u8981\u77E5\u9053\u54EA\u4E9B" tabindex="-1"><a class="header-anchor" href="#\u53D1\u70E7\u62BD\u7B4B-\u5BB6\u957F\u9700\u8981\u77E5\u9053\u54EA\u4E9B" aria-hidden="true">#</a> \u53D1\u70E7\u62BD\u7B4B\uFF0C\u5BB6\u957F\u9700\u8981\u77E5\u9053\u54EA\u4E9B\uFF1F</h3><p>\u5B69\u5B50\u53D1\u70E7\u5C31\u6015\u62BD\u7B4B\uFF0C\u62BD\u7B4B\u5C31\u6015\u62BD\u574F\u8111\u5B50\uFF0C\u56E0\u4E3A\u5BF9\u70ED\u6027\u60CA\u53A5\u7684\u62C5\u5FC3\uFF0C\u5F88\u591A\u5BB6\u957F\u5BF9\u53D1\u70E7\u5145\u6EE1\u6050\u60E7\uFF0C\u4EE5\u81F3\u4E8E\u5B69\u5B50\u4E00\u53D1\u70E7\uFF0C\u5C31\u60F3\u5C3D\u4E00\u5207\u529E\u6CD5\u7ED9\u5B69\u5B50\u9000\u70E7\uFF0C\u5403\u9000\u70E7\u836F\uFF0C\u51B0\u888B\u51B0\u5934\uFF0C\u8D34\u9000\u70ED\u8D34\u2026\u8FD9\u4E9B\u62C5\u5FC3\u6709\u5FC5\u8981\u5417?\u8FD9\u6837\u505A\u6709\u7528\u5417\uFF1F\u5173\u4E8E\u53D1\u70E7\u62BD\u7B4B\uFF0C\u7236\u6BCD\u4EEC\u9700\u8981\u77E5\u9053\u54EA\u4E9B\uFF1F</p><p>\u70ED\u6027\u60CA\u53A5\u662F\u6307\u7531\u53D1\u70E7\u5F15\u53D1\u7684\u62BD\u6410\uFF0C\u4EE5\u524D\u8BA4\u4E3A\u662F\u9AD8\u70E7\u5F15\u53D1\u7684\uFF0C\u6240\u4EE5\u53EB\u9AD8\u70ED\u60CA\u53A5\uFF0C\u4F46\u4E8B\u5B9E\u4E0A\u53EA\u8981\u4F53\u6E29\u9AD8\u4E8E38\u5EA6\u90FD\u53EF\u80FD\u53D1\u751F\u60CA\u53A5\uFF0C\u800C\u4E0D\u53EA\u53D1\u751F\u4E8E\u9AD8\u70ED\u72B6\u6001\uFF0C\u6240\u4EE5\u53EB\u201C\u70ED\u6027\u60CA\u53A5\u201D\u3002\u60CA\u53A5\u591A\u89C1\u4E8E\u4F53\u6E29\u4E0A\u5347\u7684\u9636\u6BB5\uFF0C\u6BCF100\u4E2A\u5B69\u5B50\u91CC\u5927\u7EA6\u67094\u4E2A\u5B69\u5B50\u4F1A\u9047\u5230\u70ED\u6027\u60CA\u53A5\u3002</p><p>\u522B\u4EBA\u5BB6\u5B69\u5B50\u53D1\u70E7\u4E0D\u62BD\uFF0C\u4E3A\u4EC0\u4E48\u6211\u7684\u5B69\u5B50\u4F1A\u62BD\uFF1F\u70ED\u6027\u60CA\u53A5\u7684\u539F\u56E0\u73B0\u5728\u8FD8\u4E0D\u90A3\u4E48\u6E05\u695A\uFF0C\u6BD4\u8F83\u660E\u786E\u7684\u662F\u548C\u9057\u4F20\u4F53\u8D28\u6709\u5173\uFF0C\u76F4\u7CFB\u4EB2\u5C5E\u6709\u70ED\u6027\u60CA\u53A5\u53F2\u7684\uFF0C\u5B69\u5B50\u7684\u98CE\u9669\u4E5F\u8D8A\u9AD8\uFF0C\u6B64\u5916\u70ED\u6027\u60CA\u53A5\u4E3B\u8981\u53D1\u751F\u57286-60\u6708\u4E4B\u95F4\u7684\u5B69\u5B50\uFF0C\u53EF\u80FD\u548C\u5B69\u5B50\u8FD8\u5904\u4E8E\u4E00\u4E2A\u795E\u7ECF\u53D1\u80B2\u4E0D\u6210\u719F\u7684\u5E74\u9F84\u6BB5\u6709\u5173\u3002</p><p>\u5B69\u5B50\u53D1\u70E7\u60CA\u53A5\u65F6\uFF0C\u5F80\u5F80\u5168\u8EAB\u50F5\u76F4\uFF0C\u56DB\u80A2\u62BD\u52A8\uFF0C\u53CC\u773C\u7FFB\u767D\uFF0C\u610F\u8BC6\u4E0D\u6E05\uFF0C\u751A\u81F3\u53E3\u5410\u767D\u6CAB\u5927\u5C0F\u4FBF\u5931\u7981\uFF0C\u6BCF\u4E2A\u5BB6\u957F\u770B\u5230\u5B69\u5B50\u8FD9\u4E2A\u6837\u5B50\u90FD\u4F1A\u60CA\u614C\u5931\u63AA\uFF0C\u6709\u7684\u5BB6\u957F\u6309\u4F4F\u5B69\u5B50\u4E0D\u8BA9\u4ED6\u62BD\uFF0C\u6709\u7684\u53BB\u6390\u4EBA\u4E2D\uFF0C\u6709\u7684\u5F80\u5B69\u5B50\u5634\u91CC\u585E\u4E1C\u897F\u6765\u9632\u6B62\u54AC\u4F24\u3002\u4F46\u614C\u4E71\u4E0D\u4F1A\u5BF9\u5B69\u5B50\u6709\u4EFB\u4F55\u5E2E\u52A9\uFF0C\u4E5F\u4E0D\u662F\u505A\u5F97\u8D8A\u591A\u5C31\u8D8A\u597D\uFF0C\u8FD9\u4E9B\u9519\u8BEF\u7684\u505A\u6CD5\u4E0D\u4F46\u5BF9\u5B69\u5B50\u6CA1\u4EFB\u4F55\u597D\u5904\uFF0C\u8FD8\u53EF\u80FD\u7ED9\u5B69\u5B50\u9020\u6210\u4E0D\u5FC5\u8981\u7684\u4F24\u5BB3\u3002</p><p>\u6B63\u786E\u505A\u6CD5\u662F\u8BA9\u5B69\u5B50\u5728\u5E8A\u4E0A\u6216\u5B89\u5168\u7684\u5E73\u5730\u8EBA\u4E0B\uFF0C\u89E3\u5F00\u8863\u9886\uFF0C\u53EF\u4EE5\u8BA9\u5B69\u5B50\u5934\u4FA7\u7740\u6216\u8005\u4FA7\u5367\uFF0C\u4EE5\u9632\u5455\u5410\u65F6\u8BEF\u5438\u545B\u54B3\u7A92\u606F\u3002\u62BD\u7B4B\u4E00\u822C\u4E0D\u4F1A\u54AC\u4F24\u820C\u5934\uFF0C\u5373\u4F7F\u54AC\u4F24\u4E5F\u4F1A\u5F88\u5FEB\u957F\u4E0A\uFF0C\u4E0D\u8981\u5F80\u5B69\u5B50\u5634\u91CC\u585E\u4E1C\u897F\u6216\u7ED9\u836F\uFF0C\u5F3A\u884C\u63B0\u5F00\u5B69\u5B50\u5634\u53EF\u80FD\u9020\u6210\u635F\u4F24\uFF0C\u585E\u8FDB\u53BB\u7684\u4E1C\u897F\u8FD8\u53EF\u80FD\u5835\u585E\u547C\u5438\u9053\uFF0C\u5F15\u8D77\u7A92\u606F\uFF0C\u8FD8\u53EF\u80FD\u635F\u4F24\u7259\u9F7F\u3002\u76F8\u53CD\uFF0C\u62BD\u6410\u65F6\u5982\u679C\u5B69\u5B50\u5634\u91CC\u6709\u4E1C\u897F\uFF0C\u53EF\u80FD\u7684\u8BDD\u8FD8\u5E94\u8BE5\u8F7B\u67D4\u5730\u53D6\u51FA\u3002\u6309\u4F4F\u5B69\u5B50\u3001\u6390\u4EBA\u4E2D\u90FD\u963B\u6B62\u4E0D\u4E86\u62BD\u6410\uFF0C\u53CD\u800C\u53EF\u80FD\u7ED9\u5B69\u5B50\u9020\u6210\u635F\u4F24\u3002</p><p>\u4E0E\u5176\u614C\u4E71\u7684\u505A\u8FD9\u4E9B\u6709\u5BB3\u65E0\u76CA\u7684\u4E8B\uFF0C\u4E0D\u5982\u9547\u5B9A\u4E0B\u6765\u8BB0\u5F55\u4E00\u4E0B\u5B69\u5B50\u62BD\u6410\u7684\u65F6\u95F4\uFF0C\u5982\u679C\u5185\u5FC3\u8DB3\u591F\u5F3A\u5927\uFF0C\u8FD8\u53EF\u4EE5\u62FF\u624B\u673A\u628A\u5B69\u5B50\u62BD\u6410\u7684\u60C5\u5F62\u5F55\u4E0B\u6765\uFF0C\u597D\u8BA9\u533B\u751F\u5224\u65AD\u75C5\u60C5\u3002\u5927\u90E8\u5206\u7684\u70ED\u6027\u60CA\u53A5\u6301\u7EED\u65F6\u95F4\u5F88\u77ED\uFF0C\u901A\u5E38\u4E0D\u5230\u4E00\u5206\u949F\uFF0C90%\u57285\u5206\u949F\u5185\u81EA\u53D1\u7F13\u89E3\uFF0C\u5982\u679C\u6301\u7EED\u8D85\u8FC75\u5206\u949F\u9700\u8981\u5C31\u8FD1\u5C31\u533B\u6216\u8005\u6253120\u6C42\u6551\uFF0C\u5982\u679C\u65E2\u5F80\u6709\u8FC730\u5206\u949F\u4EE5\u4E0A\u7684\u70ED\u6027\u60CA\u53A5\u7684\u5B69\u5B50\uFF0C\u518D\u6B21\u51FA\u73B0\u957F\u65F6\u95F4\u53D1\u4F5C\u7684\u53EF\u80FD\u6027\u8F83\u5927\uFF0C\u4E00\u65E6\u518D\u6B21\u53D1\u4F5C\uFF0C\u5E94\u5C3D\u65E9\u5C31\u8BCA\uFF0C\u4F7F\u7528\u6B62\u75C9\u836F\u7EC8\u6B62\u62BD\u6410\u3002</p><p>\u65E0\u8BBA\u62BD\u6410\u6301\u7EED\u591A\u4E45\uFF0C\u62BD\u6410\u7ED3\u675F\u540E\u6700\u597D\u8FD8\u662F\u53BB\u533B\u9662\u68C0\u67E5\u4E00\u4E0B\uFF0C\u5982\u679C\u6700\u540E\u786E\u8BA4\u662F\u7B80\u5355\u578B\u7684\u70ED\u6027\u60CA\u53A5\uFF0C\u4E00\u822C\u6CA1\u6709\u7279\u522B\u5904\u7406\uFF0C\u4F46\u533B\u751F\u9700\u8981\u68C0\u67E5\u4E00\u4E0B\u53D1\u70E7\u7684\u539F\u56E0\uFF0C\u5C24\u5176\u8981\u6392\u9664\u4E00\u4E0B\u9885\u5185\u611F\u67D3\u7684\u53EF\u80FD\uFF0C\u4E00\u5C81\u534A\u4EE5\u5185\u7684\u5B69\u5B50\u53EF\u80FD\u9700\u8981\u505A\u8170\u7A7F\uFF0C\u662F\u5426\u9700\u8981\u8FDB\u4E00\u6B65\u505A\u522B\u7684\u68C0\u67E5\u9700\u8981\u533B\u751F\u68C0\u67E5\u5B69\u5B50\u540E\u518D\u51B3\u5B9A\u3002</p><p>\u70ED\u6027\u60CA\u53A5\u770B\u8D77\u6765\u5F88\u5413\u4EBA\uFF0C\u5C0F\u90E8\u5206\u5B69\u5B50\u60CA\u53A5\u7684\u65F6\u5019\u6454\u5012\uFF0C\u6216\u8005\u545B\u5230\uFF0C\u4F46\u7EDD\u5927\u90E8\u5206\u4E0D\u5BF9\u5B69\u5B50\u9020\u6210\u4F24\u5BB3\uFF0C\u4E5F\u4E0D\u4F1A\u5F71\u54CD\u8111\u5B50\uFF0C\u957F\u5927\u4E86\u5B66\u4E60\u4E5F\u4E0D\u4F1A\u6BD4\u522B\u7684\u5B69\u5B50\u5DEE\u3002\u70ED\u6027\u60CA\u53A5\u672C\u8EAB\u4E0D\u4F24\u5BB3\u5B69\u5B50\uFF0C\u53EA\u4E0D\u8FC7\u6709\u70ED\u6027\u60CA\u53A5\u7684\u5B69\u5B50\u8FDC\u671F\u51FA\u73B0\u766B\u75EB\u7684\u98CE\u9669\u7565\u9AD8\u4E8E\u666E\u901A\u4EBA\u7FA4\uFF0C\u603B\u4F53\u6765\u8BB2\u5927\u7EA62%\u7684\u70ED\u6027\u60CA\u53A5\u4F1A\u53D1\u5C55\u4E3A\u766B\u75EB\uFF0C\u590D\u6742\u578B\u7684\u6982\u7387\u66F4\u9AD8\uFF0C\u8FD9\u79CD\u8D8B\u52BF\u662F\u7531\u5B69\u5B50\u8EAB\u4F53\u6761\u4EF6\uFF0C\u5C24\u5176\u662F\u9057\u4F20\u7D20\u8D28\u51B3\u5B9A\u7684\uFF0C\u4E0D\u662F\u70ED\u6027\u60CA\u53A5\u76F4\u63A5\u5BFC\u81F4\u7684\uFF0C\u836F\u7269\u6CBB\u7597\u4E5F\u6539\u53D8\u4E0D\u4E86\u8FD9\u4E2A\u8D8B\u52BF\u3002</p><p>\u5373\u4FBF\u8FD9\u6837\uFF0C\u9274\u4E8E\u5B69\u5B50\u70ED\u6027\u60CA\u53A5\u65F6\u53EF\u6015\u7684\u6837\u5B50\uFF0C\u5F88\u591A\u5BB6\u957F\u8FD8\u662F\u60F3\u77E5\u9053\u600E\u4E48\u53BB\u9884\u9632\uFF0C\u53EF\u60DC\u7684\u662F\uFF0C\u5B69\u5B50\u662F\u5426\u4F1A\u518D\u62BD\u53D6\u51B3\u4E8E\u5B69\u5B50\u81EA\u8EAB\uFF0C\u76EE\u524D\u6CA1\u529E\u6CD5\u6539\u53D8\u3002\u53D1\u751F\u70ED\u6027\u60CA\u53A5\u7684\u5E74\u9F84\u8D8A\u5C0F\uFF0C\u518D\u62BD\u7684\u53EF\u80FD\u6027\u8D8A\u5927\uFF0C\u9996\u6B21\u70ED\u6027\u60CA\u53A5\u65F6\u4E0D\u5230\u4E00\u5C81\uFF0C\u518D\u62BD\u7684\u53EF\u80FD\u6027\u5927\u7EA650%\uFF0C\u76F4\u7CFB\u4EB2\u5C5E\u6709\u70ED\u6027\u60CA\u53A5\u53F2\u7684\uFF0C\u518D\u62BD\u7684\u98CE\u9669\u4E5F\u66F4\u5927\u3002</p><p>\u6BCF\u4E2A\u5B69\u5B50\u6210\u957F\u8FC7\u7A0B\u4E2D\u90FD\u8981\u7ECF\u5386\u53D1\u70E7\uFF0C\u4F60\u6CA1\u529E\u6CD5\u63A7\u5236\u5B69\u5B50\u5B8C\u5168\u4E0D\u53D1\u70E7\uFF0C\u4E5F\u6CA1\u529E\u6CD5\u63A7\u5236\u5B69\u5B50\u662F\u5426\u9047\u5230\uFF0C\u6216\u8005\u4EC0\u4E48\u65F6\u5019\u9047\u5230\u70ED\u6027\u60CA\u53A5\uFF0C\u5B69\u5B50\u51FA\u751F\u540E\u5BB6\u65CF\u9057\u4F20\u56E0\u7D20\u4F60\u4E5F\u6CA1\u529E\u6CD5\u6539\u53D8\uFF0C\u6240\u4EE5\u6CA1\u4EC0\u4E48\u597D\u529E\u6CD5\u53BB\u9884\u9632\uFF0C\u552F\u4E00\u80FD\u505A\u7684\u662F\u7B49\u5B69\u5B50\u957F\u5927\uFF0C3\u5C81\u4EE5\u540E\u673A\u4F1A\u5C31\u5C0F\u4E86\uFF0C5\u5C81\u4EE5\u540E\u5C31\u66F4\u5C11\u4E86\u3002</p><p>\u56E0\u4E3A\u53EB\u70ED\u6027\u60CA\u53A5\uFF0C\u5F88\u591A\u5BB6\u957F\uFF0C\u5305\u62EC\u4E00\u4E9B\u533B\u751F\u90FD\u8BA4\u4E3A\u5E94\u8BE5\u79EF\u6781\u9000\u70E7\uFF0C\u4F46\u4E8B\u5B9E\u4E0A\u7814\u7A76\u5DF2\u7ECF\u8BC1\u660E\uFF0C\u5305\u62EC\u5E03\u6D1B\u82AC\u548C\u5BF9\u4E59\u9170\u6C28\u57FA\u915A\u5728\u5185\u7684\u9000\u70ED\u836F\uFF0C\u90FD\u9884\u9632\u4E0D\u4E86\u70ED\u6027\u60CA\u53A5\u53D1\u751F,\u539F\u56E0\u53EF\u80FD\u662F\u60CA\u53A5\u4E3B\u8981\u53D1\u751F\u5728\u4F53\u6E29\u53D8\u5316\u9636\u6BB5\u3002\u70ED\u6027\u60CA\u53A5\u7684\u5B69\u5B50\u672C\u6765\u5C31\u4E0D\u662F\u6BCF\u6B21\u53D1\u70ED\u90FD\u4F1A\u53D1\u4F5C\uFF0C\u4E0D\u80FD\u8BF4\u8FD9\u6B21\u670D\u7528\u9000\u70ED\u836F\u6CA1\u53D1\u4F5C\uFF0C\u5C31\u8BA4\u4E3A\u9000\u70ED\u836F\u80FD\u9884\u9632\u3002\u5982\u679C\u5B69\u5B501\u5C81\u4EE5\u5185\u6BCF\u6B21\u53D1\u70ED\u90FD\u62BD\u6410\uFF0C\u90A3\u53EF\u80FD\u4E0D\u662F\u7B80\u5355\u7684\u70ED\u6027\u60CA\u53A5\uFF0C\u9700\u8981\u627E\u4E13\u4E1A\u7684\u513F\u79D1\u795E\u7ECF\u533B\u751F\u770B\u770B\u3002</p><p>\u9884\u9632\u4E0D\u4E86\u60CA\u53A5\u5E76\u4E0D\u662F\u8BF4\u5C31\u4E0D\u8981\u5403\u9000\u70E7\u836F\uFF0C\u5982\u679C\u5B69\u5B50\u4F53\u6E29\u8D85\u8FC739\u5EA6\u660E\u663E\u4E0D\u8212\u670D\u8BE5\u7528\u8FD8\u662F\u8981\u7528\uFF0C\u53D1\u70E7\u8BE5\u600E\u4E48\u5904\u7406\u8FD8\u662F\u600E\u4E48\u5904\u7406\uFF0C\u4E0D\u9700\u8981\u4E3A\u9884\u9632\u70ED\u6027\u60CA\u53A5\u989D\u5916\u505A\u4EC0\u4E48\uFF0C\u505A\u4E86\u4E5F\u6CA1\u7528\u3002</p><p>\u5982\u679C\u5B69\u5B50\u62BD\u6410\u65F6\u95F4\u6BD4\u8F83\u957F\uFF0C\u533B\u751F\u53EF\u80FD\u4F1A\u7528\u5730\u897F\u6CEE\u9759\u8109\u6CE8\u5C04\u3001\u54AA\u8FBE\u5511\u4ED1\u808C\u8089\u6CE8\u5C04\u7B49\u65B9\u5F0F\u8FDB\u884C\u6B62\u75C9\uFF0C\u9884\u9632\u6027\u4F7F\u7528\u8FD9\u4E9B\u836F\u53EF\u80FD\u53EF\u4EE5\u964D\u4F4E\u518D\u6B21\u62BD\u6410\u7684\u98CE\u9669\uFF0C\u4F46\u56E0\u4E3A\u70ED\u6027\u60CA\u53A5\u672C\u8EAB\u4E0D\u5BF9\u5B69\u5B50\u9020\u6210\u4EC0\u4E48\u4F24\u5BB3\uFF0C\u800C\u8FD9\u4E9B\u836F\u7269\u53D1\u751F\u526F\u4F5C\u7528\u51E0\u7387\u8F83\u5927\uFF0C\u6240\u4EE5\u6CA1\u5FC5\u8981\u4F7F\u7528\u3002</p><p>\u5927\u90E8\u5206\u7684\u70ED\u6027\u60CA\u53A5\u90FD\u662F\u7B80\u5355\u578B\uFF0C\u4F46\u590D\u6742\u578B\u7684\u70ED\u6027\u60CA\u53A5\uFF0C\u4E5F\u5C31\u662F\u6301\u7EED\u65F6\u95F4\u8D85\u8FC715\u5206\u949F\uFF0C\u5C40\u90E8\u7684\u3001\u4E0D\u5BF9\u79F0\u7684\u62BD\u6410\uFF0C24\u5C0F\u65F6\u5185\u53CD\u590D\u62BD\u6410\uFF0C\u8FD9\u79CD\u7C7B\u578B\u53D1\u5C55\u4E3A\u766B\u75EB\u7684\u673A\u4F1A\u8981\u5927\u4E00\u4E9B\uFF0C\u662F\u5426\u9700\u8981\u9884\u9632\u6027\u4F7F\u7528\u6297\u766B\u75EB\u836F\u7269\uFF0C\u9700\u8981\u513F\u79D1\u795E\u7ECF\u4E13\u79D1\u533B\u751F\u8FDB\u884C\u8BC4\u4F30\u540E\u518D\u5B9A\u3002</p><p>\u672C\u7BC7\u7279\u522B\u9E23\u8C22\u5317\u4EAC\u5927\u5B66\u7B2C\u4E00\u533B\u9662\u59DC\u7389\u6B66\u6559\u6388\u7ED9\u4E88\u7684\u4E13\u4E1A\u6307\u5BFC\uFF0C\u6CA1\u542C\u8FC7\u6216\u4E0D\u4E86\u89E3\u59DC\u6559\u6388\u7684\u53EF\u4EE5\u70B9\u51FB\u9605\u8BFB\u539F\u6587\u770B\u770B\u3002</p>',16),l={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=206853594&idx=1&sn=a90e5354aafd3630dba115e538f5148b&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},h=s("\u67E5\u770B\u539F\u6587");function m(f,x){const p=a("ExternalLinkIcon");return r(),t(o,null,[i,e("p",null,[e("a",l,[h,n(p)])])],64)}var k=d(_,[["render",m]]);export{k as default};
