import{r,o as t,c as n,a as e,b as a,F as o,d as c,e as s}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const _={},i=c('<h3 id="\u4E3A\u4EC0\u4E48\u4E0D\u8981\u7528\u4E2D\u836F" tabindex="-1"><a class="header-anchor" href="#\u4E3A\u4EC0\u4E48\u4E0D\u8981\u7528\u4E2D\u836F" aria-hidden="true">#</a> \u4E3A\u4EC0\u4E48\u4E0D\u8981\u7528\u4E2D\u836F\uFF1F</h3><p>\u5927\u5BB6\u53EF\u80FD\u77E5\u9053\uFF0C\u5728\u6021\u79BE\u54A8\u8BE2\u5E73\u53F0\u4E0A\uFF0C\u6211\u4EEC\u7684\u64CD\u4F5C\u89C4\u8303\u8981\u6C42\u533B\u751F\u4E0D\u80FD\u5411\u7528\u6237\u63A8\u8350\u4E2D\u533B\u836F\uFF0C\u8FD9\u8BA9\u4E00\u4E9B\u7528\u6237\u56F0\u60D1\uFF0C\u4E5F\u8BA9\u4E00\u4E9B\u533B\u751F\u56F0\u60D1\u3002\u5173\u4E8E\u4E2D\u836F\u95EE\u9898\uFF0C\u6021\u79BE\u533B\u751F\u7FA4\u91CC\u4E5F\u8BA8\u8BBA\u8FC7\u5F88\u591A\u6B21\uFF0C\u6240\u4EE5\u6211\u5C31\u4E13\u95E8\u5199\u8FD9\u7BC7\u6587\u7AE0\u6765\u8BF4\u8BF4\u4E3A\u4EC0\u4E48\u4E0D\u8981\u7528\u4E2D\u836F\uFF1F</p><p>\u56DE\u7B54\u8FD9\u4E2A\u95EE\u9898\u524D\uFF0C\u6211\u4EEC\u9700\u8981\u5148\u660E\u786E\u4EC0\u4E48\u662F\u4E2D\u836F\u3002\u6211\u4EEC\u5E73\u65F6\u6240\u7406\u89E3\u7684\u4E2D\u836F\uFF0C\u5E94\u8BE5\u662F\u6CDB\u6307\u6211\u56FD\u53E4\u4EE3\u4EBA\u6C11\u4F20\u7EDF\u7528\u4E8E\u6CBB\u7597\u75BE\u75C5\u7684\u201C\u836F\u7269\u201D\uFF0C\u8FD9\u4E9B\u201C\u836F\u7269\u201D\u4EE5\u690D\u7269\u3001\u52A8\u7269\u3001\u77FF\u7269\u4E3A\u4E3B\u3002\u800C\u51C6\u786E\u5B9A\u4E49\u7684\u4E2D\u836F\uFF0C\u5E94\u8BE5\u662F\u6307\u6309\u7167\u4E2D\u533B\u7406\u8BBA\u6765\u4F7F\u7528\u7684\u836F\u7269\uFF0C\u56E0\u4E3A\u4E0D\u662F\u6240\u6709\u7684\u4F20\u7EDF\u836F\u7269\u90FD\u662F\u6309\u4E2D\u533B\u7406\u8BBA\u4F7F\u7528\u3002</p><p>\u540C\u65F6\u6211\u4EEC\u4E5F\u8981\u77E5\u9053\uFF0C\u4F7F\u7528\u4E00\u79CD\u836F\uFF0C\u9700\u8981\u77E5\u9053\u836F\u54C1\u662F\u4EC0\u4E48\u6210\u4EFD\uFF0C\u7528\u836F\u662F\u4EC0\u4E48\u76EE\u7684\uFF0C\u600E\u4E48\u7528\uFF0C\u7528\u591A\u5C11\uFF0C\u80FD\u5E26\u6765\u4EC0\u4E48\u597D\u5904\uFF0C\u53EF\u80FD\u6709\u4EC0\u4E48\u98CE\u9669\u3002\u4F53\u73B0\u5728\u836F\u54C1\u8BF4\u660E\u4E66\u4E0A\uFF0C\u5C31\u662F\u836F\u54C1\u7684\u4E3B\u8981\u6210\u4EFD\uFF0C\u9002\u5E94\u75C7\uFF0C\u7528\u6CD5\u7528\u91CF\uFF0C\u4E0D\u826F\u53CD\u5E94\uFF0C\u7981\u5FCC\u75C7\u7B49\u3002</p><p>\u8981\u660E\u786E\u8FD9\u4E9B\uFF0C\u9700\u8981\u505A\u4E25\u683C\u7684\u4E34\u5E8A\u836F\u7269\u8BD5\u9A8C\uFF0C\u901A\u8FC7\u968F\u673A\u5BF9\u7167\u8BD5\u9A8C\u6765\u660E\u786E\u6709\u6548\u6027\u548C\u5B89\u5168\u6027\uFF0C\u800C\u4E0D\u662F\u81EA\u5DF1\u6216\u8005\u522B\u4EBA\u7528\u4E86\u89C9\u5F97\u597D\u5C31\u8BA4\u4E3A\u662F\u771F\u7684\u597D\uFF0C\u56E0\u4E3A\u5B89\u6170\u5242\u6548\u5E94\u6216\u8005\u662F\u75BE\u75C5\u81EA\u7136\u7684\u8F6C\u5F52\u90FD\u53EF\u80FD\u5F71\u54CD\u5224\u65AD\u3002\u6BD4\u5982\u611F\u5192\u5403\u836F\u597D\u4E86\uFF0C\u4F60\u5C31\u53EF\u80FD\u4EE5\u4E3A\u8FD9\u836F\u80FD\u6CBB\u611F\u5192\uFF0C\u4F46\u5B9E\u9645\u4E0A\u611F\u5192\u4E0D\u5403\u836F\u5927\u90E8\u5206\u4E5F\u4F1A\u597D\u3002</p><p>\u4E2D\u533B\u8BA4\u4E3A\u6C14\u3001\u8840\u3001\u7CBE\u3001\u6D25\u6DB2\u662F\u6784\u6210\u4EBA\u4F53\u7684\u57FA\u672C\u7269\u8D28\uFF0C\u8BA4\u4E3A\u6240\u6709\u836F\u90FD\u6709\u201C\u56DB\u6C14\u4E94\u5473\u201D\uFF0C\u6709\u201C\u5BD2\u3001\u70ED\u3001\u6E29\u3001\u51C9\u201D\u4E4B\u5206\u3002\u4E2D\u533B\u7528\u4E2D\u836F\u7684\u76EE\u7684\u662F\u201C\u6E05\u70ED\u89E3\u6BD2\u3001\u8865\u6C14\u517B\u8840\u201D\u4E4B\u7C7B\u3002\u201C\u6C14\u3001\u8840\u3001\u5BD2\u3001\u70ED\u201D\u8FD9\u4E9B\u4E2D\u533B\u6982\u5FF5\u662F\u6211\u56FD\u53E4\u4EE3\u4EBA\u6C11\u5728\u5BF9\u751F\u7406\u3001\u89E3\u5256\u6CA1\u6709\u6B63\u786E\u8BA4\u8BC6\u7684\u6761\u4EF6\u4E0B\u4EA7\u751F\u7684\u4E00\u4E9B\u6734\u7D20\u7684\u89C2\u5FF5\uFF0C\u81F3\u4ECA\u4ECD\u662F\u505C\u7559\u4E8E\u60F3\u8C61\u800C\u6CA1\u80FD\u8BC1\u5B9E\u3002</p><p>\u56E0\u4E3A\u6CA1\u6709\u786E\u5207\u5B9A\u4E49\uFF0C\u4E00\u4E2A\u4EBA\u662F\u4E0D\u662F\u6C14\u8840\u4E0D\u8DB3\uFF0C\u662F\u4E0D\u662F\u6709\u70ED\u6709\u6BD2\uFF0C\u4E00\u4E2A\u836F\u662F\u5BD2\u662F\u70ED\uFF0C\u6CA1\u6709\u7EDF\u4E00\u7684\u6807\u51C6\uFF0C\u4E5F\u6CA1\u529E\u6CD5\u9A8C\u8BC1\u3002\u7528\u836F\u4EA7\u751F\u4E86\u4EC0\u4E48\u6548\u679C\uFF1F\u540C\u6837\u6CA1\u6CD5\u5224\u65AD\uFF0C\u53EA\u80FD\u7531\u4E2D\u533B\u81EA\u5DF1\u51ED\u611F\u89C9\u6765\u8BF4\u3002\u6CA1\u6709\u660E\u786E\u7684\u5B9A\u4E49\uFF0C\u627E\u4E0D\u5230\u5BA2\u89C2\u7684\u6307\u6807\uFF0C\u5C31\u51B3\u5B9A\u4E86\u6309\u7167\u4E2D\u533B\u7406\u8BBA\u6765\u4F7F\u7528\u7684\u4E2D\u836F\uFF0C\u6548\u679C\u5C31\u662F\u65E0\u6CD5\u9A8C\u8BC1\u7684\uFF0C\u4E5F\u5C31\u4E0D\u53EF\u80FD\u901A\u8FC7\u4E34\u5E8A\u836F\u7269\u8BD5\u9A8C\u3002</p><p>\u6240\u4EE5\u5982\u679C\u4F60\u770B\u5230\u4E00\u4E2A\u836F\u54C1\u8BF4\u660E\u4E66\u4E0A\u5199\u201C\u6D3B\u8840\u5316\u6DE4\u3001\u6E05\u70ED\u89E3\u6BD2\u3001\u5065\u80C3\u6D88\u98DF\u201D\u4E4B\u7C7B\u7684\u4E2D\u533B\u672F\u8BED\u7684\u529F\u6548\uFF0C\u90A3\u80AF\u5B9A\u662F\u6CA1\u6709\u7ECF\u8FC7\u4E34\u5E8A\u836F\u7269\u8BD5\u9A8C\u9A8C\u8BC1\u8FC7\u7684\uFF0C\u56E0\u4E3A\u8FD9\u4E9B\u90FD\u662F\u65E0\u6CD5\u9A8C\u8BC1\u7684\u529F\u6548\u3002</p><p>\u6309\u4E2D\u533B\u7684\u7406\u8BBA\u65E0\u6CD5\u9A8C\u8BC1\uFF0C\u6240\u4EE5\u6709\u4E9B\u4E2D\u836F\u7684\u9002\u5E94\u75C7\u91CC\u5C31\u5217\u4E3E\u4E86\u4E00\u4E9B\u73B0\u4EE3\u533B\u5B66\u7684\u75BE\u75C5\uFF0C\u6BD4\u5982\u6D41\u611F\u3001\u816E\u817A\u708E\u4E4B\u7C7B\uFF0C\u751A\u81F3\u51FA\u73B0\u4E86\u201C\u6297\u75C5\u6BD2\u201D\u4E4B\u7C7B\u7684\u73B0\u4EE3\u533B\u5B66\u91CC\u624D\u6709\u7684\u6982\u5FF5\uFF0C\u4F46\u8FD9\u4E9B\u6548\u679C\u5374\u6CA1\u6709\u6309\u7167\u73B0\u4EE3\u533B\u5B66\u7684\u65B9\u6CD5\u9A8C\u8BC1\u8FC7\u3002</p><p>\u4E5F\u6B63\u662F\u56E0\u4E3A\u6CA1\u6709\u505A\u8FC7\u4E34\u5E8A\u836F\u7269\u8BD5\u9A8C\uFF0C\u6CA1\u6709\u660E\u786E\u7684\u7528\u836F\u6807\u51C6\uFF0C\u6240\u4EE5\u4E00\u4E9B\u4E2D\u836F\u5C31\u6210\u4E86\u4EC0\u4E48\u90FD\u80FD\u6CBB\u7684\u201C\u4E07\u91D1\u6CB9\u201D\u3002\u6CA1\u6709\u505A\u8FC7\u836F\u7269\u8BD5\u9A8C\uFF0C\u4E2D\u836F\u7684\u4E0D\u826F\u53CD\u5E94\u5927\u90FD\u5199\u7740\uFF1A\u5C1A\u4E0D\u660E\u786E\uFF0C\u4F46\u8FD9\u4E0D\u7B49\u4E8E\u6CA1\u6709\u4E0D\u826F\u53CD\u5E94\uFF0C\u4E8B\u5B9E\u4E0A\u4E2D\u836F\u4E00\u76F4\u662F\u836F\u54C1\u4E0D\u826F\u53CD\u5E94\u7684\u91CD\u707E\u533A\u3002</p><p>\u6CA1\u6709\u7ECF\u8FC7\u4E34\u5E8A\u836F\u7269\u8BD5\u9A8C\uFF0C\u4E3A\u4EC0\u4E48\u80FD\u4E0A\u5E02\uFF1F\u6CA1\u6709\u9A8C\u8BC1\u8FC7\u7684\u529F\u6548\uFF0C\u4E3A\u4EC0\u4E48\u80FD\u5199\u4E0A\u8BF4\u660E\u4E66\uFF1F</p><p>\u6B63\u5E38\u6765\u8BF4\u662F\u4E0D\u53EF\u4EE5\u7684\uFF0C\u4F46\u56E0\u4E3A\u653F\u6CBB\u3001\u5386\u53F2\u7684\u539F\u56E0\uFF0C\u6211\u56FD\u6709\u4FC3\u8FDB\u4E2D\u533B\u836F\u53D1\u5C55\u7684\u653F\u7B56\uFF0C\u6240\u4EE5\u6211\u56FD\u7684\u836F\u76D1\u90E8\u95E8\u5BF9\u4E2D\u836F\u4E5F\u7F51\u5F00\u4E00\u9762\uFF0C\u6CA1\u6709\u8FDB\u884C\u4E34\u5E8A\u836F\u7269\u8BD5\u9A8C\u7684\u836F\u4E5F\u80FD\u6CE8\u518C\uFF0C\u6CA1\u6709\u4F9D\u636E\u7684\u529F\u6548\u548C\u9002\u5E94\u75C7\uFF0C\u4E5F\u8BA9\u5199\u5728\u8BF4\u660E\u4E66\u4E0A\u3002</p><p>\u4EE5\u524D\u7684\u300A\u836F\u54C1\u6CE8\u518C\u7BA1\u7406\u529E\u6CD5\u300B\u4E3A\u4E2D\u836F\u5F00\u4E86\u8FD9\u6837\u7684\u540E\u95E8\uFF0C\u6700\u8FD1\u901A\u8FC7\u7684\u4E2D\u533B\u836F\u6CD5\uFF0C\u4E5F\u660E\u786E\u5199\u4E0A\u4E86\u201C\u751F\u4EA7\u7B26\u5408\u56FD\u5BB6\u89C4\u5B9A\u6761\u4EF6\u7684\u6765\u6E90\u4E8E\u53E4\u4EE3\u7ECF\u5178\u540D\u65B9\u7684\u4E2D\u836F\u590D\u65B9\u5236\u5242\uFF0C\u5728\u7533\u8BF7\u836F\u54C1\u6279\u51C6\u6587\u53F7\u65F6\uFF0C\u53EF\u4EE5\u4EC5\u63D0\u4F9B\u975E\u4E34\u5E8A\u5B89\u5168\u6027\u7814\u7A76\u8D44\u6599\u201D\u3002</p><p>\u8FD9\u4E9B\u4E2D\u6210\u836F\u4E0A\u5E02\u4E4B\u540E\uFF0C\u5C0F\u90E8\u5206\u662F\u4E2D\u533B\u5728\u6309\u7167\u4E2D\u533B\u7406\u5728\u7528\uFF0C\u5927\u90E8\u5206\u8FD8\u662F\u88AB\u201C\u897F\u533B\u201D\u4EEC\u7A00\u91CC\u7CCA\u6D82\u7684\u5F00\u51FA\u53BB\u4E86\u3002\u6839\u636E\u6C14\u3001\u8840\u3001\u7CBE\u3001\u6D25\u6DB2\u8FD9\u4E9B\u51ED\u7A7A\u60F3\u8C61\u7684\u7406\u8BBA\u6765\u7528\u836F\uFF0C\u81EA\u7136\u662F\u4E71\u7528\uFF0C\u7528\u4E2D\u533B\u201C\u836F\u7406\u201D\u7684\u4E2D\u836F\u6765\u6CBB\u7597\u73B0\u4EE3\u533B\u5B66\u7406\u8BBA\u4E0B\u7684\u75BE\u75C5\uFF0C\u56E0\u4E3A\u662F\u672A\u8BC1\u5B9E\u7684\u6548\u679C\uFF0C\u540C\u6837\u662F\u4E71\u7528\u3002\u6240\u4EE5\u4E0D\u8BBA\u662F\u4E2D\u533B\u5F00\u7684\u4E2D\u836F\uFF0C\u8FD8\u662F\u897F\u533B\u5F00\u7684\u4E2D\u836F\uFF0C\u90FD\u6CA1\u5FC5\u8981\u7528\u3002</p><p>\u65E2\u7136\u6CA1\u6709\u9A8C\u8BC1\u8FC7\u6709\u6548\u6027\uFF0C\u4F60\u5C31\u4E0D\u7528\u62C5\u5FC3\u4E0D\u5403\u4E2D\u836F\u4F1A\u803D\u8BEF\u4EC0\u4E48\uFF0C\u6CA1\u6709\u9A8C\u8BC1\u8FC7\u5B89\u5168\u6027\uFF0C\u4F60\u5C31\u9700\u8981\u62C5\u5FC3\u5403\u4E86\u4F1A\u4E0D\u4F1A\u51FA\u95EE\u9898\u3002\u5982\u679C\u4F60\u53BB\u770B\u75C5\u533B\u751F\u53EA\u7ED9\u4F60\u5F00\u4E86\u4E2D\u836F\u6CA1\u5F00\u522B\u7684\u836F\uFF0C\u90A3\u8981\u4E48\u662F\u4F60\u78B0\u5230\u4E86\u4E71\u7528\u836F\u7684\u533B\u751F\uFF0C\u8981\u4E48\u662F\u4F60\u7684\u75C5\u4E0D\u9700\u8981\u7528\u836F\u3002</p><p>\u6709\u4EBA\u53EF\u80FD\u8981\u8BF4\uFF0C\u4E0D\u80FD\u7528\u897F\u533B\u7684\u6807\u51C6\u6765\u770B\u5F85\u4E2D\u533B\u836F\uFF0C\u4E2D\u897F\u533B\u662F\u4E24\u4E2A\u4E0D\u540C\u7684\u7406\u8BBA\u4F53\u7CFB\u3002\u4F46\u7EC6\u80DE\u7EC4\u7EC7\uFF0C\u5316\u5B66\u7ED3\u6784\uFF0C\u7EDF\u8BA1\u65B9\u6CD5\u8FD9\u4E9B\u672C\u6765\u5C31\u662F\u81EA\u7136\u79D1\u5B66\u7684\u8303\u7574\uFF0C\u5E76\u4E0D\u662F\u4EC0\u4E48\u201C\u897F\u533B\u7684\u6807\u51C6\u201D\u3002</p><p>\u5982\u679C\u201C\u6C14\u3001\u8840\u3001\u7CBE\u3001\u6D25\u6DB2\uFF0C\u5BD2\u3001\u70ED\u3001\u6E29\u3001\u51C9\u201D\u8FD9\u4E9B\u5B58\u5728\u4E8E\u60F3\u8C61\u7684\u7406\u8BBA\u9700\u8981\u7528\u4E0D\u540C\u7684\u6807\u51C6\u6765\u5BF9\u5F85\uFF0C\u90A3\u70E7\u9999\u62DC\u4F5B\u5FF5\u7ECF\u5403\u9999\u7070\u4E5F\u53EF\u4EE5\u7528\u4E0D\u540C\u7684\u6807\u51C6\u6765\u5BF9\u5F85\u4E86\u3002\u4E2D\u836F\u6BD2\u6027\uFF0C\u6BD4\u5982\u4F55\u9996\u4E4C\u7684\u809D\u6BD2\u6027\u3001\u5173\u6728\u901A\u7684\u80BE\u6BD2\u6027\u4E5F\u6CA1\u6709\u56E0\u4E3A\u7528\u4E2D\u533B\u8FD9\u5957\u7406\u8BBA\u800C\u6D88\u5931\u6389\uFF0C\u4E2D\u836F\u91CC\u7684\u7837\u3001\u6C5E\u3001\u94C5\u4E5F\u4E0D\u4F1A\u56E0\u4E3A\u6362\u4E2A\u65B9\u5F0F\u53BB\u7406\u89E3\u800C\u53D8\u7684\u5B89\u5168\u3002</p><p>\u6709\u4EBA\u53EF\u80FD\u4F1A\u95EE\uFF0C\u96BE\u9053\u4E2D\u836F\u5C31\u4E00\u65E0\u662F\u5904\u5417\uFF1F\u9752\u84BF\u7D20\u4E0D\u5C31\u662F\u4E2D\u836F\u91CC\u6316\u6398\u51FA\u6765\u7684\u7470\u5B9D\u5417\uFF1F</p><p>\u4F20\u7EDF\u836F\u7269\u91CC\uFF0C\u786E\u5B9E\u4E0D\u80FD\u9664\u5916\u5B58\u5728\u4E00\u4E9B\u6709\u7528\u7684\u6210\u5206\uFF0C\u690D\u7269\u6216\u8005\u52A8\u7269\u91CC\u627E\u5230\u4E00\u4E9B\u6709\u6CBB\u7597\u6548\u679C\u7684\u6210\u4EFD\u662F\u5B8C\u5168\u6709\u53EF\u80FD\u3002\u62D4\u6839\u8349\u91CC\u9762\u4E5F\u80FD\u63D0\u53D6\u70B9\u7EA4\u7EF4\u7D20\uFF0C\u6361\u5757\u77F3\u5934\u91CC\u9762\u4E5F\u80FD\u63D0\u53D6\u70B9\u9499\uFF0C\u7CAA\u4FBF\u91CC\u591A\u5C11\u4E5F\u80FD\u63D0\u53D6\u70B9\u86CB\u767D\u8D28\uFF0C\u4F46\u503C\u4E0D\u503C\u5F97\u4ECE\u8FD9\u4E9B\u4E1C\u897F\u91CC\u63D0\u53D6\u81EA\u5DF1\u60F3\u8981\u7684\u4E1C\u897F\u662F\u53E6\u5916\u4E00\u56DE\u4E8B\u4E86\u3002</p><p>\u6211\u4EEC\u8BF4\u4E00\u4E2A\u201C\u836F\u7269\u201D\u6709\u7528\uFF0C\u9700\u8981\u660E\u786E\u5B83\u662F\u4EC0\u4E48\u6210\u4EFD\u5728\u4EC0\u4E48\u60C5\u51B5\u4E0B\u80FD\u6CBB\u4EC0\u4E48\u75C5\uFF0C\u5C31\u50CF\u9EC4\u82B1\u84BF\u91CC\u9762\u63D0\u53D6\u4E86\u9752\u84BF\u7D20\uFF0C\u518D\u8BC1\u5B9E\u4E86\u5B83\u80FD\u6740\u6B7B\u759F\u539F\u866B\uFF0C\u800C\u4E14\u4E0D\u826F\u53CD\u5E94\u5C11\uFF0C\u5BF9\u759F\u75BE\u75C5\u4EBA\u5229\u5927\u4E8E\u5F0A\uFF0C\u81EA\u7136\u662F\u53EF\u4EE5\u7528\u7684\u3002\u4E00\u4E2A\u4E2D\u6210\u836F\u54EA\u6015\u6210\u4EFD\u660E\u786E\u4E0D\u4E86\uFF0C\u53EA\u8981\u9A8C\u8BC1\u4E86\u6548\u679C\u548C\u5B89\u5168\u6027\uFF0C\u540C\u6837\u4E5F\u53EF\u4EE5\u7528\u3002</p><p>\u5982\u679C\u6709\u4EBA\u60F3\u7EE7\u7EED\u53BB\u4E2D\u836F\u836F\u65B9\u91CC\u6316\u5B9D\u8D1D\uFF0C\u53EA\u8981\u6309\u73B0\u4EE3\u533B\u5B66\u7684\u6807\u51C6\u9A8C\u8BC1\u4E86\u6709\u6548\u6027\u548C\u5B89\u5168\u6027\uFF0C\u5C31\u53EF\u4EE5\u7528\uFF0C\u56E0\u4E3A\u505A\u5230\u4E86\u8FD9\u4E00\u70B9\uFF0C\u5B83\u5C31\u4E0D\u662F\u6309\u4E2D\u533B\u7406\u8BBA\u4F7F\u7528\u7684\u836F\u7269\u91CC\uFF0C\u4E5F\u5C31\u4E0D\u518D\u662F\u4E2D\u836F\u800C\u662F\u6807\u51C6\u7684\u73B0\u4EE3\u836F\u7269\u3002</p><p>\u5982\u679C\u53EA\u662F\u5F04\u70B9\u8349\u6839\u6811\u76AE\u71AC\u70B9\u6C64\u6C64\u6C34\u6C34\uFF0C\u8FDE\u662F\u4EC0\u4E48\u4E1C\u897F\u90FD\u8BF4\u4E0D\u6E05\u695A\u5C31\u8BF4\u5B83\u80FD\u6E05\u70ED\u89E3\u6BD2\u6216\u8005\u6ECB\u9634\u8865\u9633\uFF0C\u90A3\u5C31\u548C\u9A97\u94B1\u65E0\u5F02\u4E86\u3002</p><p>\u6211\u4EEC\u4E5F\u5E94\u8BE5\u77E5\u9053\uFF0C\u4E0D\u7BA1\u662F\u897F\u836F\u8FD8\u662F\u4E2D\u836F\uFF0C\u5982\u679C\u836F\u5382\u771F\u7684\u5BF9\u5B83\u7684\u6709\u6548\u548C\u5B89\u5168\u6027\u6709\u4FE1\u5FC3\uFF0C\u5C31\u4E0D\u4F1A\u9009\u62E9\u907F\u5F00\u4E34\u5E8A\u836F\u7269\u8BD5\u9A8C\u7684\u8DEF\u5F84\u53BB\u6CE8\u518C\uFF0C\u5982\u679C\u771F\u6709\u4FE1\u5FC3\uFF0C\u4E5F\u4E0D\u4F1A\u653E\u7740\u6B27\u7F8E\u7684\u5E02\u573A\u5229\u6DA6\u4E0D\u8981\u800C\u53EA\u5728\u4E2D\u56FD\u6CE8\u518C\uFF0C\u4F60\u770B\u4E00\u4E2A\u4E39\u53C2\u6EF4\u4E38\u53EA\u662F\u53BB\u7F8E\u56FDFDA\u505A\u4E86\u4E00\u4E0B\u8BD5\u9A8C\uFF0C\u836F\u5382\u90FD\u53EF\u4EE5\u4E00\u904D\u904D\u62FF\u51FA\u6765\u7092\u4F5C\u8D5A\u5230\u76C6\u6EE1\u94B5\u6EE1\u3002</p><p>\u540C\u65F6\u6211\u4EEC\u8981\u77E5\u9053\uFF0C\u4EBA\u7C7B\u548C\u5176\u5B83\u52A8\u7269\u4E00\u6837\uFF0C\u7E41\u884D\u81F3\u4ECA\u4E0D\u662F\u9760\u4E2D\u836F\u4E5F\u4E0D\u662F\u9760\u897F\u836F\uFF0C\u6CA1\u6709\u836F\u7269\u4E5F\u6709\u5E78\u5B58\u8005\uFF0C\u5728\u6709\u73B0\u4EE3\u836F\u7269\u4E4B\u524D\uFF0C\u5927\u5BB6\u90FD\u662F\u5E78\u5B58\u8005\uFF0C\u4E2D\u534E\u6C11\u65CF\u4E0D\u662F\u9760\u4E2D\u836F\u624D\u6D3B\u5230\u4ECA\u5929\u3002\u5168\u4E16\u754C\u90A3\u4E48\u591A\u56FD\u5BB6\u90FD\u4E0D\u7528\u4E2D\u836F\uFF0C\u4EBA\u6C11\u7684\u5065\u5EB7\u6C34\u5E73\u4E5F\u6CA1\u6BD4\u6211\u4EEC\u4F4E\u3002</p><p>\u76EE\u524D\u6CA1\u6709\u54EA\u79CD\u75BE\u75C5\u4E0D\u7528\u4E2D\u836F\u5C31\u4F1A\u803D\u8BEF\u6CBB\u7597\uFF0C\u6211\u4EEC\u6CA1\u5FC5\u8981\u4E3A\u672A\u8BC1\u5B9E\u7684\u6548\u679C\u53BB\u82B1\u94B1\uFF0C\u4E5F\u6CA1\u5FC5\u8981\u62FF\u81EA\u5DF1\u7684\u8EAB\u4F53\u53BB\u5192\u98CE\u9669\uFF0C\u6240\u4EE5\u4E0D\u8981\u7528\u4E2D\u836F\u3002\u8FDC\u79BB\u4E2D\u836F\uFF0C\u5C31\u662F\u5BF9\u81EA\u5DF1\u548C\u5BB6\u4EBA\u6700\u5927\u7684\u4FDD\u62A4\u3002\u4E5F\u662F\u57FA\u4E8E\u8FD9\u4E00\u70B9\uFF0C\u6021\u79BE\u8981\u6C42\u533B\u751F\u4E0D\u80FD\u5411\u7528\u6237\u63A8\u8350\u4E2D\u836F\u3002</p><hr><p>\u76F8\u5173\u9605\u8BFB\uFF1A\u56DE\u590D\u201C<strong>\u4E2D\u533B</strong>\u201D\u9605\u8BFB\uFF1A<strong>\u6211\u4E3A\u4EC0\u4E48\u53CD\u5BF9\u4E2D\u533B</strong></p><p>\u957F\u6309\u4E0B\u9762\u4E8C\u7EF4\u7801\uFF0C\u5173\u6CE8\u6021\u79BE\u5065\u5EB7\uFF0C\u53EF\u5411<strong>\u513F\u79D1\u3001\u5987\u79D1\u3001\u76AE\u80A4\u79D1\u533B\u751F</strong>\u54A8\u8BE2\u5065\u5EB7\u95EE\u9898\u3002</p>',28),f={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651626521&idx=1&sn=7de65d58efd51d0fed1386748995382b&chksm=8b5cb5e3bc2b3cf58bdffe78bf45c444db01f2a3ecb3a9b9e82dd711646437039fe554e9e209&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},h=s("\u67E5\u770B\u539F\u6587");function l(b,m){const p=r("ExternalLinkIcon");return t(),n(o,null,[i,e("p",null,[e("a",f,[h,a(p)])])],64)}var u=d(_,[["render",l]]);export{u as default};
