import{r as t,o as s,c as i,a as e,b as o,w as c,F as d,d as n,e as p}from"./app.9aaa2256.js";import{_}from"./plugin-vue_export-helper.21dcd24c.js";const g={},l=n('<h3 id="\u5B69\u5B50\u8FDC\u89C6\u7ADF\u7136\u4E0D\u662F\u574F\u4E8B-\u8FD8\u80FD\u9632\u8FD1\u89C6-\u5E7C\u513F\u56ED\u89C6\u529B\u68C0\u67E5\u53EF\u9760\u5417" tabindex="-1"><a class="header-anchor" href="#\u5B69\u5B50\u8FDC\u89C6\u7ADF\u7136\u4E0D\u662F\u574F\u4E8B-\u8FD8\u80FD\u9632\u8FD1\u89C6-\u5E7C\u513F\u56ED\u89C6\u529B\u68C0\u67E5\u53EF\u9760\u5417" aria-hidden="true">#</a> \u5B69\u5B50\u8FDC\u89C6\u7ADF\u7136\u4E0D\u662F\u574F\u4E8B\uFF0C\u8FD8\u80FD\u9632\u8FD1\u89C6\uFF1F\u5E7C\u513F\u56ED\u89C6\u529B\u68C0\u67E5\u53EF\u9760\u5417\uFF1F</h3><p>\u5F88\u591A\u5BB6\u957F\u53EF\u80FD\u542C\u8BF4\u8FC7\u201C\u8FDC\u89C6\u50A8\u5907\u53EF\u4EE5\u9884\u9632\u8FD1\u89C6\u201D\u7684\u8BF4\u6CD5\uFF0C\u90A3\u4EC0\u4E48\u662F\u201C\u8FDC\u89C6\u50A8\u5907\u201D\uFF0C\u5B83\u548C\u8FD1\u89C6\u53C8\u6709\u4EC0\u4E48\u5173\u7CFB\u5462\uFF1F\u4ECA\u5929\u6211\u4EEC\u6765\u8BB2\u8BB2\u8FD9\u4E2A\u95EE\u9898\u3002</p><p>01</p><p><strong>\u5A74\u513F\u5929\u751F\u662F\u201C\u8FDC\u89C6\u201D</strong></p><p>\u5A74\u513F\u521A\u521A\u51FA\u751F\u65F6\u662F\u540D\u526F\u5176\u5B9E\u7684\u201C\u8FDC\u89C6\u201D\u773C\u3002\u4E0D\u540C\u5A74\u513F\u8FDC\u89C6\u7A0B\u5EA6\u4E0D\u4E00\u6837\uFF0C\u4F46\u662F\u5927\u90E8\u5206\u65B0\u751F\u513F\u96C6\u4E2D\u5728200\u5EA6\u5DE6\u53F3\u8FDC\u89C6\u3002</p><p>\u7B49\u5B69\u5B50\u523018\u4E2A\u6708\u65F6\uFF0C\u4E2A\u4F53\u4E4B\u95F4\u5DE8\u5927\u7684\u533A\u522B\u5C31\u57FA\u672C\u6D88\u5931\u4E86\uFF0C\u5927\u5BB6\u90FD\u5DEE\u4E0D\u591A\u7A33\u5B9A\u5728125\u5EA6\u8FDC\u89C6\uFF0C\u8FD9\u4E00\u8FC7\u7A0B\u53EB\u505A\u6B63\u89C6\u5316\uFF08Emmetropization\uFF09\u3002</p><p>\u6211\u4EEC\u77E5\u9053\u8FDC\u89C6\u4F1A\u5BFC\u81F4\u53EF\u89C1\u7684\u56FE\u50CF\u805A\u7126\u5728\u89C6\u7F51\u819C\u4E4B\u540E\uFF0C\u53EF\u4EE5\u8BA4\u4E3A\u4E00\u5F00\u59CB\u5A74\u513F\u773C\u775B\u7684\u6210\u50CF\u6A21\u5F0F\u4E5F\u662F\u5982\u6B64\u3002\u800C\u6B63\u89C6\u5316\u662F\u773C\u775B\u751F\u957F\u53D1\u80B2\u7684\u4E00\u4E2A\u8FC7\u7A0B\uFF0C\u6700\u7EC8\u773C\u775B\u6210\u4E3A\u6B63\u89C6\u773C\uFF0C\u4E5F\u5C31\u662F\u8F7B\u5FAE\u8FDC\u89C6\uFF08\u8FDC\u89C6\u8303\u56F4\u5927\u591A\u6570\u5728125\u5EA6\u4EE5\u5185\uFF09\u6216\u8005\u8FD1\u89C6\u5EA6\u6570\u4E3A0\u5EA6\u3002</p><p>\u8FDC\u89C6\u72B6\u6001\u4E0B\u773C\u90E8\u7ED3\u6784\u793A\u610F\u56FE</p><p>\u56FE \u6E90 | Uptodate</p><p>\u6B63\u89C6\u773C\u773C\u90E8\u7ED3\u6784\u793A\u610F\u56FE</p><p>\u56FE \u6E90 | Uptodate</p><p>\uFF08\u6CE8\uFF1A\u4E3A\u4FBF\u4E8E\u7406\u89E3\uFF0C\u6B64\u793A\u610F\u56FE\u4EC5\u4E3A\u6F14\u793A\u6B63\u89C6\u5316\u7684\u8FC7\u7A0B\uFF0C\u5A74\u513F\u5C1A\u6CA1\u6709\u5B8C\u5168\u5B8C\u6210\u6B63\u89C6\u5316\uFF0C\u56E0\u6B64\u4ECD\u5B58\u7559\u4E00\u5B9A\u7684\u8FDC\u89C6 \uFF09</p><p>\u53EF\u4EE5\u60F3\u8C61\uFF0C\u5982\u679C\u6309\u7167\u53E4\u4EE3\u72E9\u730E\u548C\u91C7\u96C6\u7684\u751F\u6D3B\u65B9\u5F0F\uFF0C\u4EBA\u7C7B\u773C\u775B\u7684\u5EA6\u6570\u5C31\u7A33\u5B9A\u5728125\u5EA6\u8FDC\u89C6\u5DE6\u53F3\uFF0C\u76F4\u5230\u75C5\u6B7B\u997F\u6B7B\u6216\u8005\u88AB\u8001\u864E\u5403\u4E86\u3002</p><p>\u4F46\u662F\u73B0\u4EE3\u751F\u6D3B\u53D1\u751F\u4E86\u7FFB\u5929\u8986\u5730\u53D8\u5316\uFF0C\u968F\u7740\u8FD1\u8DDD\u79BB\u7528\u773C\u7684\u5927\u5E45\u589E\u591A\uFF0C\u4EBA\u7C7B\u773C\u775B\u5EA6\u6570\u4E5F\u968F\u4E4B\u800C\u53D8\u5316\uFF0C\u513F\u7AE5\u8FD1\u89C6\u4E5F\u5C31\u6210\u4E3A\u8BA9\u7236\u6BCD\u611F\u5230\u5934\u75DB\u7684\u4E00\u4E2A\u95EE\u9898\u3002</p><p>02</p><p><strong>\u513F\u7AE5\u8FD1\u89C6\u662F\u5982\u4F55\u53D1\u5C55\u7684\u5462\uFF1F</strong></p><p>\u73B0\u4EE3\u4EBA\u7C7B\u7684\u751F\u6D3B\u65B9\u5F0F\u5F80\u5F80\u6D89\u53CA\u5230\u5927\u91CF\u7684\u9605\u8BFB\u3001\u5B66\u4E60\u548C\u8FD1\u8DDD\u79BB\u7528\u773C\uFF08\u770B\u4E66\u3001\u7535\u5B50\u5C4F\u5E55\u7B49\uFF09\uFF0C\u5E76\u4E14\u8FD9\u4E9B\u6D3B\u52A8\u4E00\u822C\u90FD\u5728\u5BA4\u5185\u8FDB\u884C\u3002</p><p>\u800C\u513F\u7AE5\u53D1\u751F\u8FD9\u79CD\u751F\u6D3B\u65B9\u5F0F\u7684\u660E\u663E\u8F6C\u53D8\u5927\u591A\u662F\u4ECE\u4E0A\u5C0F\u5B66\u5F00\u59CB\uFF0C\u4E5F\u5C31\u662F6-7\u5C81\u4EE5\u540E\u3002\u4ECE\u8FD9\u4E2A\u5E74\u9F84\u5F00\u59CB\uFF0C\u7531\u4E8E\u751F\u6D3B\u65B9\u5F0F\u7684\u6539\u53D8\uFF0C\u513F\u7AE5\u7684\u773C\u775B\u5F00\u59CB\u5F80\u201C\u8FD1\u89C6\u5316\u201D\u65B9\u5411\u53D1\u5C55\u3002</p><p>\u6BD4\u5982\uFF0C\u5B69\u5B50\u5982\u679C\u672C\u6765\u6709100\u5EA6\u8FDC\u89C6\uFF0C\u90A3\u4E48\u201C\u8FD1\u89C6\u5316\u201D\u7684\u53D1\u5C55\u5C31\u610F\u5473\u7740\u8FDC\u89C6\u5EA6\u6570\u5F00\u59CB\u964D\u4F4E\u3002\u5F53\u8FDC\u89C6\u5EA6\u6570\u964D\u4F4E\u4E3A0\u7684\u65F6\u5019\uFF0C\u518D\u53D1\u5C55\u5C31\u4F1A\u6210\u4E3A\u8FD1\u89C6\u3002</p><p>\u603B\u7684\u6765\u8BF4\uFF0C\u513F\u7AE5\u4ECE\u51FA\u751F\u5230\u51FA\u73B0\u8FD1\u89C6\uFF0C\u773C\u775B\u7ECF\u5386\u7740\u201C<strong>\u8FDC\u89C6-\u6B63\u89C6-\u8FD1\u89C6</strong>\u201D\u7684\u53D1\u5C55\u3002</p><p>03</p><p><strong>\u4EC0\u4E48\u662F\u8FDC\u89C6\u50A8\u5907\uFF1F</strong></p><p>\u6211\u4EEC\u7EDD\u5927\u591A\u6570\u4EBA\u751F\u4E0B\u6765\u90FD\u4E0D\u662F\u8FD1\u89C6\u773C\uFF0C\u5E76\u4E14\u5728\u5E7C\u513F\u65F6\u671F\u8FD8\u6709\u4F4E\u5EA6\u6570\u7684\u8FDC\u89C6\uFF0C\u8FD9\u5C31\u662F\u6240\u8C13\u7684\u201C\u8FDC\u89C6\u50A8\u5907\u201D\u3002</p><p>\u6B63\u5E38\u60C5\u51B5\u4E0B\uFF0C6\u5C81\u513F\u7AE5\u8FD8\u4F1A\u6709\u5E73\u574775-125\u5EA6\u7684\u8FDC\u89C6[1]\u3002\u7EDF\u8BA1\u53D1\u73B0\uFF0C6\u5C81\u65F6\u5019\u7684\u773C\u775B\u5C48\u5149\u5EA6\u5982\u679C\u662F75\u5EA6\u8FDC\u89C6\uFF0C\u90A3\u4E48\u5C06\u6765\u8FD1\u89C6\u7684\u98CE\u9669\u5927\u4E3A\u964D\u4F4E\uFF1B\u76F8\u53CD\uFF0C\u5982\u679C\u4F4E\u4E8E75\u5EA6\u8FDC\u89C6\u7684\u8BDD\uFF0C\u5219\u8FD1\u89C6\u98CE\u9669\u5347\u9AD8 [2]\u3002</p><p>04</p><p><strong>\u5411\u8FDC\u5904\u591A\u671B\u53EF\u4EE5\u589E\u52A0\u8FDC\u89C6\u50A8\u5907\u5417\uFF1F</strong></p><p>\u5BB6\u957F\u53EF\u80FD\u60F3\u63D0\u9AD8\u513F\u7AE5\u7684\u201C\u8FDC\u89C6\u50A8\u5907\u201D\u9884\u9632\u8FD1\u89C6\u3002\u4F46\u9057\u61BE\u7684\u662F\uFF0C\u8FDC\u89C6\u50A8\u5907\u5F88\u5927\u7A0B\u5EA6\u4E0A\u662F\u5929\u751F\u7684\uFF0C\u4E0E\u521A\u51FA\u751F\u65F6\u773C\u775B\u5EA6\u6570\u6709\u5173\u3002</p><p>\u5F53\u7136\uFF0C\u5728\u201C\u6B63\u89C6\u5316\u201D\u4EE5\u540E\uFF08\u4E5F\u5C31\u662F2-3\u5C81\u540E\uFF09\uFF0C\u8FDC\u89C6\u5EA6\u6570\u53EF\u80FD\u4F1A\u53D7\u73AF\u5883\u5F71\u54CD\uFF0C\u5C24\u5176\u662F\u8FD9\u4E2A\u5E74\u9F84\u6BB5\u7684\u5B69\u5B50\u5DF2\u7ECF\u5F00\u59CB\u5927\u91CF\u8FD1\u8DDD\u79BB\u7528\u773C\u3002</p><p>\u201C\u8FDC\u89C6\u50A8\u5907\u201D\u5C31\u50CF\u7ED9\u6BCF\u4E2A\u4EBA\u7684\u4E00\u4E2A\u5B58\u6298\uFF0C\u91CC\u9762\u6709\u4E00\u7B14\u94B1\uFF0C\u94B1\u5C31\u4EE3\u8868\u201C\u8FDC\u89C6\u50A8\u5907\u201D\u7684\u91CF\u3002\u4F46\u8FD9\u4E2A\u6570\u5B57\u56E0\u4EBA\u800C\u5F02\uFF0C\u6211\u4EEC\u4E5F\u6CA1\u529E\u6CD5\u7ED9\u91CC\u9762\u52A0\u5B58\u6B3E\uFF0C\u968F\u7740\u65F6\u95F4\uFF0C\u94B1\u8FD8\u4F1A\u6162\u6162\u6D88\u8017\u3002\u7B49\u6D88\u8017\u5B8C\u4E86\uFF0C\u4F59\u989D\u5219\u4E3A0\uFF0C\u5982\u679C\u8FD8\u7EE7\u7EED\u6D88\u8017\uFF0C\u5C31\u53D8\u6210\u8D1F\u503C\uFF0C\u4E5F\u5C31\u662F\u8FD1\u89C6\u3002</p><p>\u56E0\u6B64\uFF0C\u201C\u8FDC\u89C6\u50A8\u5907\u201D\u4E00\u822C\u4E0D\u80FD\u589E\u52A0\uFF0C\u66F4\u4E0D\u80FD\u901A\u8FC7\u5411\u8FDC\u5904\u671B\u6765\u589E\u52A0\uFF0C\u5BB6\u957F\u80FD\u505A\u7684\u662F\u51CF\u5C11\u201C\u8FDC\u89C6\u50A8\u5907\u201D\u7684\u6D88\u8017\u901F\u5EA6\u3002</p><p>05</p><p><strong>\u964D\u4F4E\u8FD1\u89C6\u98CE\u9669\uFF0C\u5982\u4F55\u5B89\u6392\u5B69\u5B50\u5B66\u4E60\u6216\u6D3B\u52A8\uFF1F</strong></p><p>\u8001\u8BFB\u8005\u53EF\u80FD\u77E5\u9053\uFF0C\u6BCF\u5929\u81F3\u5C112\u5C0F\u65F6\u6237\u5916\u6D3B\u52A8\u53EF\u4EE5\u4E00\u5B9A\u7A0B\u5EA6\u9884\u9632\u8FD1\u89C6\u3002\u4F46\u8981\u8BF4\u660E\u7684\u662F\uFF0C\u6237\u5916\u6D3B\u52A8\u9884\u9632\u8FD1\u89C6\u7684\u5173\u952E\u5728\u4E8E\u63A5\u53D7\u6237\u5916\u9633\u5149\u7684\u7167\u5C04\uFF0C\u800C\u5E76\u4E0D\u662F\u6D3B\u52A8\u672C\u8EAB\u3002</p><p>\u5982\u679C\u5B69\u5B50\u4E0D\u559C\u6B22\u8FD0\u52A8\uFF0C\u53EF\u4EE5\u5C06\u5404\u79CD\u6D3B\u52A8\u5B89\u6392\u5728\u6237\u5916\u3002\u6BD4\u5982\u8BF4\u53EF\u4EE5\u5728\u5916\u9762\u5403\u4E1C\u897F\u3001\u804A\u5929\u3001\u5531\u6B4C\u3001\u73A9\u800D\u3001\u53D1\u5446\uFF0C\u751A\u81F3\u53EF\u4EE5\u5728\u5916\u8FB9\u770B\u4E66\u548C\u5B66\u4E60\u3002</p><p>\u5F53\u7136\u5B69\u5B50\u751F\u957F\u53D1\u80B2\u8FD8\u662F\u9700\u8981\u4E00\u5B9A\u7684\u8FD0\u52A8\u91CF\uFF0C\u9F13\u52B1\u5B69\u5B50\u8FD0\u52A8\u4E4B\u4F59\uFF0C\u4E5F\u8981\u6839\u636E\u5B69\u5B50\u559C\u597D\u5B89\u6392\u6237\u5916\u6D3B\u52A8\u3002\u5728\u665A\u4E0A\u3001\u508D\u665A\u6216\u8005\u6BD4\u8F83\u6697\u7684\u9634\u96E8\u5929\u6C14\u8FDB\u884C\u6237\u5916\u6D3B\u52A8\u5BF9\u9884\u9632\u8FD1\u89C6\u7684\u6548\u679C\u4E0D\u592A\u597D\uFF0C\u56E0\u6B64\u5C3D\u91CF\u5B89\u6392\u5728\u660E\u4EAE\u7684\u65F6\u95F4\u6BB5\u3002</p><p>\u5728\u5BA4\u5185\u5149\u7EBF\u8FDC\u8FDC\u6BD4\u6237\u5916\u5DEE\uFF0C\u5BA4\u5185\u6D3B\u52A8\u4E0E\u9884\u9632\u8FD1\u89C6\u5173\u7CFB\u4E0D\u5927[3]\u3002\u4F46\u662F\uFF0C\u5982\u679C\u5929\u6C14\u5BD2\u51B7\u6216\u8005\u522E\u98CE\uFF0C\u5F88\u96BE\u53BB\u6237\u5916\u7684\u60C5\u51B5\u4E0B\uFF0C\u53EF\u4EE5\u8003\u8651\u5728\u9633\u53F0\u3001\u9633\u5149\u623F\uFF0C\u6216\u8005\u5728\u9760\u8FD1\u6709\u9633\u5149\u900F\u8FDB\u6765\u7684\u7A97\u6237\u65C1\u5B89\u6392\u5BA4\u5185\u6D3B\u52A8\uFF0C\u767D\u5929\u6253\u5F00\u6240\u6709\u7A97\u5E18\uFF0C\u8FD9\u6837\u505A\u4E5F\u80FD\u591F\u6539\u5584\u5B69\u5B50\u6D3B\u52A8\u533A\u57DF\u7684\u5149\u7EBF\u3002</p>',36),h=p("\u6B64\u5916\uFF0C\u5B69\u5B50\u5728\u5BA4\u5185\u7684\u5B66\u4E60\u548C\u6D3B\u52A8\u7A7A\u95F4\u9700\u8981\u6709\u660E\u4EAE\u7684\u706F\u5149\uFF08\u5BA4\u5185\u5149\u6E90\u5982\u4F55\u9009\u62E9\uFF0C\u53EF\u53C2\u8003\u6587\u7AE0"),m=p("\u300A\u9884\u9632\u8FD1\u89C6\uFF0C\u201C\u62A4\u773C\u706F\u201D\u600E\u4E48\u9009\uFF1F\u300B"),f=p("\uFF09\u3002\u4F7F\u7528\u7535\u5B50\u5C4F\u5E55\u5C3D\u91CF\u7528\u5927\u5C4F\u5E55\uFF0C\u5982\u679C\u80FD\u591F\u5C06\u5C4F\u5E55\u8DDD\u79BB\u62C9\u8FDC\u5219\u6700\u597D\u3002"),u=n("<p>\u56FE \u6E90 | pixabay</p><p>06</p><p><strong>\u8FDC\u89C6\u50A8\u5907\u7684\u5E38\u89C1\u95EE\u9898</strong></p><p><strong>1. \u5E7C\u513F\u56ED\u4F53\u68C0\u53D1\u73B0\u6211\u5BB6\u5B69\u5B50\u53EA\u670950\u5EA6\u8FDC\u89C6\u50A8\u5907\uFF0C\u662F\u4E0D\u662F\u5FEB\u8981\u8FD1\u89C6\u4E86\uFF1F</strong></p><p>\u5E7C\u513F\u56ED\u4F53\u68C0\u5F80\u5F80\u4E0D\u505A\u6563\u77B3\uFF0C\u53EA\u7528\u4EEA\u5668\u7B5B\u67E5\uFF0C\u8FD9\u79CD\u60C5\u51B5\u4E0B\u6D4B\u51FA\u6765\u7684\u8FDC\u89C6\u5EA6\u6570\u5927\u591A\u662F\u504F\u4F4E\u7684\u3002</p><p>\u56E0\u4E3A\u513F\u7AE5\u773C\u775B\u776B\u72B6\u808C\u7684\u6B63\u5E38\u72B6\u6001\u4E0B\u968F\u610F\u6536\u7F29\u975E\u5E38\u5E38\u89C1\uFF0C\u5F97\u51FA\u8FDC\u89C6\u504F\u5C11\u3001\u751A\u81F3\u8FD1\u89C6\u5EA6\u6570\u7684\u7ED3\u679C\u90FD\u5F88\u5E38\u89C1\uFF0C\u901A\u5E38\u4E5F\u662F\u4E0D\u51C6\u786E\u3002\u56E0\u6B64\uFF0C\u8FD9\u79CD\u89C6\u529B\u7B5B\u67E5\u4E0D\u80FD\u544A\u77E5\u6211\u4EEC\u5B69\u5B50\u7684\u8FDC\u89C6\u50A8\u5907\u8FD8\u6709\u591A\u5C11\u3002</p><p>\u53EA\u6709\u5728\u6563\u77B3\u9A8C\u5149\u540E\u624D\u80FD\u51C6\u786E\u5F97\u51FA\u8FDC\u89C6\u50A8\u5907\u7684\u6570\u5B57\u3002\u800C\u5E7C\u513F\u56ED\u4F53\u68C0\u5219\u5F88\u96BE\u63D0\u4F9B\u4E13\u4E1A\u7684\u68C0\u67E5\uFF0C\u5F80\u5F80\u9700\u8981\u53BB\u773C\u79D1\u4E13\u4E1A\u673A\u6784\u8FDB\u884C\u3002</p><p><strong>2. 6\u5C81\u53D1\u73B0\u5DF2\u7ECF\u6CA1\u6709\u8FDC\u89C6\u50A8\u5907\u4E86\uFF0C\u600E\u4E48\u529E\uFF1F</strong></p><p>\u6211\u4EEC\u6CA1\u6CD5\u589E\u52A0\u8FDC\u89C6\u50A8\u5907\uFF0C\u4F46\u662F\u53EF\u4EE5\u51CF\u6162\u5176\u6D88\u8017\u901F\u5EA6\u3002</p><p>\u5982\u679C\u5DF2\u7ECF\u5B8C\u5168\u8017\u5C3D\uFF0C\u5C31\u5C3D\u91CF\u9632\u6B62\u7EE7\u7EED\u6D88\u8017\uFF0C\u4ECE\u800C\u9632\u6B62\u63A5\u4E0B\u6765\u53D1\u751F\u7684\u8FD1\u89C6\u3002\u5177\u4F53\u9884\u9632\u8FD1\u89C6\u7684\u65B9\u6CD5\u662F\u589E\u52A0\u6237\u5916\u6D3B\u52A8\u548C\u51CF\u5C11\u8FD1\u8DDD\u79BB\u7528\u773C\u7684\u65F6\u95F4\uFF0C\u4EE5\u53CA\u6BCF\u59292\u5C0F\u65F6\u4EE5\u4E0A\u7684\u6237\u5916\u6D3B\u52A8\u3002</p><p>\u8FD9\u91CC\u7684\u8FD1\u8DDD\u79BB\u7528\u773C\u5305\u62EC\u770B\u7EB8\u8D28\u4E66\u548C\u7535\u5B50\u5C4F\u5E55\uFF0C\u6BCF\u770B20\u5206\u949F\uFF0C\u5411\u8FDC\u5904\u773A\u671B20\u79D2\u3002</p><p>07</p><p><strong>\u9884\u9632\u8FD1\u89C6\uFF0C\u9700\u8981\u505A\u4EC0\u4E48\u68C0\u67E5\uFF1F</strong></p><p>\u5F88\u591A\u5E7C\u513F\u56ED\u6709\u89C6\u529B\u7B5B\u67E5\uFF0C\u5982\u679C\u53D1\u73B0\u6709\u8FD1\u89C6\u7684\u95EE\u9898\uFF0C\u8FD8\u662F\u9700\u8981\u53BB\u773C\u79D1\u533B\u9662\u8FDB\u884C\u6563\u77B3\u9A8C\u5149\u786E\u8BCA\u3002</p><p>\u5EFA\u8BAE\u4ECE6\u5C81\u4EE5\u540E\uFF0C\u5B69\u5B50\u6BCF\u5E74\u505A\u4E00\u6B21\u6563\u77B3\u9A8C\u5149\u3001\u773C\u8F74\u957F\u5EA6\u6D4B\u91CF\u4EE5\u53CA\u773C\u5E95\u5065\u5EB7\u68C0\u67E5\uFF0C\u76F4\u523016-18\u5C81\u3002\u5982\u679C\u53D1\u73B0\u8FD1\u89C6\uFF0C\u518D\u6839\u636E\u60C5\u51B5\u9009\u62E9\u6CBB\u7597\u6216\u8005\u66F4\u52A0\u9891\u7E41\u7684\u968F\u8BBF\u89C2\u5BDF\u3002\uFF08\u533B\u5B66\u6821\u5BF9\uFF1A\u674E\u5FD7\u4F1F\u533B\u751F\uFF09</p><p>\u53C2\u8003\u8D44\u6599\uFF1A</p><p>1.Flitcroft DI. Emmetropisation and the aetiology of refractive errors. Eye (London, England). 2014;28(2):169-179.</p><p>2.Zadnik K, Mutti DO, Friedman NE, et al. Ocular predictors of the onset of juvenile myopia. Invest. Ophthalmol. Vis. Sci. Aug 1999;40(9):1936-1943.</p><p>3.M Dirani, L Tong, G Gazzard, et al. Outdoor activity and myopia in Singapore teenage children. British Journal of Ophthalmology. 2009 Aug;93(8):997-1000.</p><hr><p>\u5173\u4E8E\u8FD1\u89C6\uFF0C\u672C\u6587\u4F5C\u8005\u4E01\u5A1F\u533B\u751F\u6B64\u524D\u4E5F\u5728\u6021\u79BE\u4E0A\u5F00\u8BBE\u8FC7\u4E00\u5802\u4E13\u680F\u8BFE\u7A0B\uFF0C\u8BFE\u7A0B\u5927\u7EB2\u5982\u4E0B\uFF0C\u6709\u9700\u8981\u53EF\u901A\u8FC7<strong>\u9605\u8BFB\u539F\u6587</strong>\u8BE6\u7EC6\u4E86\u89E3\uFF0C\u6021\u79BE\u4F1A\u5458\u53EF\u514D\u8D39\u542C\u3002</p>",21),b={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651632322&idx=1&sn=5642063e9e8bf1222d10a1e42a953fdf&chksm=8b5c9b38bc2b122e2575beadc23914c60cb4a69b2caa75143ec193e7240a201dec0dd9c1783f&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},x=p("\u67E5\u770B\u539F\u6587");function k(z,E){const r=t("RouterLink"),a=t("ExternalLinkIcon");return s(),i(d,null,[l,e("p",null,[h,o(r,{to:"/s/__biz=MzIwMzM3NjI5OQ==&mid=2247485616&idx=1&sn=d7e859d3f9ec01b7d714319a106c7abb/"},{default:c(()=>[m]),_:1}),f]),u,e("p",null,[e("a",b,[x,o(a)])])],64)}var v=_(g,[["render",k]]);export{v as default};
