import{r,o as t,c as n,a as e,b as o,F as a,d as s,e as c}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const i={},_=s('<h3 id="\u5B69\u5B50\u8FC7\u5E74-\u8981\u6CE8\u610F\u54EA\u4E9B" tabindex="-1"><a class="header-anchor" href="#\u5B69\u5B50\u8FC7\u5E74-\u8981\u6CE8\u610F\u54EA\u4E9B" aria-hidden="true">#</a> \u5B69\u5B50\u8FC7\u5E74\uFF0C\u8981\u6CE8\u610F\u54EA\u4E9B\uFF1F</h3><p>\u4E00\u5E74\u53C8\u4E00\u5E74\uFF0C\u9A6C\u4E0A\u53C8\u5FEB\u8FC7\u5E74\u4E86\u3002\u6625\u8282\u662F\u4E2D\u56FD\u4F20\u7EDF\u6587\u5316\u91CC\u6700\u91CD\u8981\u7684\u8282\u65E5\uFF0C\u8F9E\u65E7\u8FCE\u65B0\u3001\u4E00\u5BB6\u56E2\u5706\uFF0C\u5B69\u5B50\u4EEC\u4E5F\u53EF\u4EE5\u56DE\u8001\u5BB6\u6B22\u5929\u559C\u5730\u73A9\u95F9\u3002</p><p>\u5C31\u5065\u5EB7\u548C\u5B89\u5168\u6765\u8BF4\uFF0C\u6625\u8282\u5BF9\u5B69\u5B50\u5E76\u4E0D\u662F\u4EC0\u4E48\u597D\u4E8B\u3002\u4E3A\u4E86\u4E00\u5E74\u4E00\u6B21\u7684\u5927\u56E2\u5706\uFF0C\u8DDF\u7740\u5927\u4EBA\u53BB\u62E5\u6324\u7684\u8F66\u7AD9\uFF0C\u5386\u7ECF\u957F\u9014\u65C5\u884C\uFF0C\u53BB\u4E0D\u5E38\u53BB\u7684\u8001\u5BB6\uFF0C\u6C14\u5019\u73AF\u5883\u7684\u53D8\u5316\uFF0C\u518D\u52A0\u4E0A\u8282\u65E5\u996E\u98DF\u548C\u4F5C\u606F\u65F6\u95F4\u7684\u53D8\u66F4\uFF0C\u90FD\u66F4\u5BB9\u6613\u5F15\u53D1\u4E00\u4E9B\u5B89\u5168\u548C\u5065\u5EB7\u95EE\u9898\u3002</p><p>\u4F46\u5B89\u5168\u548C\u5065\u5EB7\u6BD5\u7ADF\u4E0D\u662F\u751F\u5B58\u7684\u5168\u90E8\uFF0C\u6587\u5316\u548C\u60C5\u611F\u4E5F\u662F\u751F\u6D3B\u7684\u4E00\u90E8\u5206\uFF0C\u6211\u4EEC\u4E0D\u592A\u53EF\u80FD\u56E0\u4E3A\u8FD9\u4E9B\u987E\u8651\u800C\u4E0D\u8FC7\u5E74\u3002\u90A3\u5E26\u5B69\u5B50\u56DE\u5BB6\u8FC7\u5E74\uFF0C\u6211\u4EEC\u5E94\u8BE5\u6CE8\u610F\u54EA\u4E9B\u5462\uFF1F\u600E\u4E48\u5C3D\u53EF\u80FD\u51CF\u5C11\u8D1F\u9762\u5F71\u54CD\u5462\uFF1F\u6211\u4EEC\u4ECE\u8863\u98DF\u4F4F\u884C\u4E0A\u6765\u770B\u770B\u3002</p><p><strong>\u8863</strong></p><p>\u6625\u8282\u5904\u4E8E\u51AC\u6625\u4EA4\u63A5\u5B63\u8282\uFF0C\u6C14\u6E29\u53D8\u5316\u4E5F\u6BD4\u8F83\u5FEB\u3002\u5927\u90E8\u5206\u7236\u6BCD\u90FD\u662F\u79BB\u5F00\u8001\u5BB6\u591A\u5E74\uFF0C\u53EF\u80FD\u5E76\u4E0D\u8BB0\u5F97\u8001\u5BB6\u7684\u6C14\u5019\u60C5\u51B5\uFF0C\u6240\u4EE5\u603B\u62C5\u5FC3\u5B69\u5B50\u4F1A\u51BB\u5230\uFF0C\u8001\u4EBA\u770B\u5230\u5B69\u5B50\u4E5F\u603B\u62C5\u5FC3\u7A7F\u7684\u4E0D\u591F\u3002\u8863\u7269\u591A\u5E26\u70B9\u6709\u5907\u65E0\u60A3\u4E0D\u4F1A\u9519\uFF0C\u4F46\u5728\u7A7F\u8863\u4E0A\uFF0C\u6211\u4EEC\u8FD8\u662F\u8981\u8BB0\u4F4F\uFF1A\u4E0D\u8BBA\u5728\u54EA\u91CC\uFF0C\u5728\u4EC0\u4E48\u6C14\u5019\u4E0B\uFF0C\u8863\u670D\u4E0D\u662F\u7A7F\u5F97\u8D8A\u591A\u8D8A\u597D\uFF0C\u5728\u5B69\u5B50\u4E0D\u611F\u5230\u51B7\u7684\u524D\u63D0\u4E0B\uFF0C\u5E94\u8BE5\u5C3D\u91CF\u5C11\u7A7F\uFF0C\u5B69\u5B50\u624D\u4F1A\u66F4\u8212\u9002\u3002</p><p><strong>\u98DF</strong></p><p>\u6625\u8282\u671F\u95F4\u65E0\u8BBA\u662F\u62DB\u5F85\u5BA2\u4EBA\u8FD8\u662F\u8D70\u4EB2\u8BBF\u53CB\uFF0C\u90FD\u96BE\u514D\u5403\u5403\u559D\u559D\uFF0C\u5927\u4EBA\u53EF\u80FD\u56E0\u4E3A\u5E94\u916C\u800C\u758F\u4E8E\u770B\u62A4\u5B69\u5B50\uFF0C\u5B69\u5B50\u4E5F\u66F4\u5BB9\u6613\u63A5\u89E6\u5230\u4E00\u4E9B\u5E73\u65F6\u5F88\u5C11\u5403\u7684\u98DF\u7269\u3002\u6BCF\u5E74\u6625\u8282\uFF0C\u56E0\u4E3A\u5403\u82B1\u751F\u3001\u74DC\u5B50\u4EE5\u53CA\u5176\u4ED6\u575A\u679C\u800C\u53D1\u751F\u7684\u6C14\u7BA1\u5F02\u7269\u90FD\u4F1A\u589E\u591A\u3002\u6211\u4EEC\u9700\u8981\u770B\u62A4\u597D\u5B69\u5B50\uFF0C\u5BA2\u4EBA\u8D70\u4E86\u53CA\u65F6\u628A\u98DF\u7269\u6536\u8D77\u6765\uFF0C\u907F\u514D\u8BA95\u5C81\u4EE5\u4E0B\u5B69\u5B50\u53BB\u5403\u679C\u4EC1\uFF0C\u8C46\u5B50\u3001\u7389\u7C73\u7C92\u3001\u53E3\u9999\u7CD6\u3001\u8461\u8404\u3001\u6842\u5706\u3001\u8354\u679D\u3001\u679C\u51BB\u3001\u70ED\u72D7\u7B49\uFF0C\u8089\u7C7B\u8BB0\u5F97\u6311\u9664\u91CC\u9762\u7684\u788E\u9AA8\uFF0C\u4EE5\u514D\u917F\u6210\u60B2\u5267\u3002</p><p>\u6625\u8282\u7684\u996D\u684C\u4E0A\uFF0C\u4E5F\u5C11\u4E0D\u4E86\u9152\u6C34\u996E\u6599\u3002\u5927\u4EBA\u559D\u9152\u7684\u65F6\u5019\u5E38\u5E38\u4E5F\u4F1A\u7ED9\u5B69\u5B50\u6765\u70B9\u996E\u6599\uFF0C\u4F46\u6211\u4EEC\u9700\u8981\u77E5\u9053\uFF0C\u5927\u90E8\u5206\u996E\u6599\u7684\u7CD6\u5206\u542B\u91CF\u5F88\u9AD8\uFF0C\u4F1A\u5F71\u54CD\u5B69\u5B50\u5BF9\u5176\u5B83\u98DF\u7269\u7684\u98DF\u6B32\uFF0C\u4E5F\u5BF9\u7259\u9F7F\u4E0D\u597D\uFF0C\u6240\u4EE5\u5C3D\u91CF\u4E0D\u8981\u7ED9\u5B69\u5B50\u559D\u996E\u6599\uFF0C\u54EA\u6015\u662F\u7EAF\u679C\u6C41\u4E5F\u8981\u5C11\u559D\u3002\u5F53\u7136\u6211\u4EEC\u66F4\u5E94\u8BE5\u77E5\u9053\uFF0C\u9152\u7CBE\u4F1A\u5F71\u54CD\u795E\u7ECF\u53D1\u80B2\uFF0C\u6C38\u8FDC\u4E0D\u8981\u7ED9\u5B69\u5B50\u559D\u9152\uFF0C\u559D\u9152\u53EF\u80FD\u4F1A\u8BA9\u5B69\u5B50\u53D8\u50BB\u3002</p><p>\u5BF9\u5F88\u591A\u5BB6\u5EAD\u6765\u8BF4\uFF0C\u6625\u8282\u4E5F\u662F\u5B69\u5B50\u548C\u4EB2\u670B\u597D\u53CB\u4E3A\u6570\u4E0D\u591A\u76F8\u5904\u7684\u65E5\u5B50\uFF0C\u4E3A\u4E86\u5173\u7231\u5B69\u5B50\uFF0C\u53EF\u80FD\u4E5F\u4F1A\u7ED9\u5B69\u5B50\u4E70\u4E00\u4E9B\u8865\u54C1\u3001\u4FDD\u5065\u54C1\u7ED9\u5B69\u5B50\uFF0C\u4EB2\u4EBA\u7684\u5173\u7231\u53EF\u4EE5\u7406\u89E3\uFF0C\u4F46\u4FDD\u5065\u54C1\u5BF9\u5B69\u5B50\u6CA1\u6709\u989D\u5916\u7684\u597D\u5904\uFF0C\u8FD8\u53EF\u80FD\u5F15\u53D1\u4E00\u4E9B\u5065\u5EB7\u98CE\u9669\u3002</p><p><strong>\u4F4F</strong></p><p>\u751F\u6D3B\u73AF\u5883\u7684\u6539\u53D8\uFF0C\u4E5F\u4F1A\u5F71\u54CD\u5B69\u5B50\u7684\u751F\u6D3B\u4E60\u60EF\uFF0C\u5BF9\u7761\u7720\u9020\u6210\u5F71\u54CD\uFF0C\u5373\u4FBF\u5728\u8001\u5BB6\uFF0C\u6211\u4EEC\u4E5F\u5E94\u8BE5\u5C3D\u53EF\u80FD\u6309\u7167\u5E73\u65F6\u7684\u4F5C\u606F\u65F6\u95F4\uFF0C\u6BCF\u5929\u8FD8\u662F\u8981\u5237\u4E24\u6B21\u7259\uFF0C\u4FDD\u6301\u548C\u5F80\u5E38\u4E00\u6837\u7684\u5165\u7761\u7A0B\u5E8F\uFF0C\u6709\u52A9\u4E8E\u5B69\u5B50\u7684\u7761\u7720\u548C\u4F11\u606F\u3002</p><p>\u5230\u4E86\u65B0\u73AF\u5883\uFF0C\u6CE8\u610F\u901A\u5F80\u9633\u53F0\u7684\u901A\u9053\u8981\u9501\u597D\uFF0C\u4EE5\u514D\u5760\u843D\uFF0C\u901A\u5F80\u516C\u5171\u4EA4\u901A\u7684\u5927\u95E8\u4E5F\u8981\u9501\u597D\uFF0C\u4EE5\u514D\u4EA4\u901A\u610F\u5916\u3002\u4F4F\u5904\u5982\u679C\u6709\u9662\u5B50\uFF0C\u8981\u6CE8\u610F\u6C34\u5751\uFF0C\u6C34\u5858\uFF0C\u4E5F\u8981\u6CE8\u610F\u5C0F\u578B\u7684\u50A8\u6C34\u5BB9\u5668\uFF0C\u907F\u514D\u53D1\u751F\u6EBA\u6C34\u3002</p><p><strong>\u884C</strong></p><p>\u51FA\u884C\u6700\u91CD\u8981\u7684\u662F\u5B89\u5168\uFF0C\u5BF9\u4E8E\u81EA\u9A7E\u6765\u8BF4\uFF0C\u5B89\u5168\u5EA7\u6905\u5BF9\u5B69\u5B50\u5FC5\u4E0D\u53EF\u5C11\uFF0C13\u5C81\u4EE5\u4E0B\u5B69\u5B50\u8981\u5750\u5728\u540E\u6392\u7684\u5B89\u5168\u5EA7\u6905\u4E0A\uFF0C2\u5C81\u4EE5\u5185\u5B69\u5B50\u5E94\u8BE5\u80CC\u5411\u5B89\u653E\u5EA7\u6905\u3002\u53D1\u751F\u8F66\u7978\u65F6\uFF0C\u4E00\u5C81\u4EE5\u5185\u7684\u5B69\u5B50\uFF0C\u4F7F\u7528\u5B89\u5168\u5EA7\u6905\u6B7B\u4EA1\u7387\u53EF\u4E0B\u964D71%\u3002\u548C\u53EA\u4F7F\u7528\u5B89\u5168\u5E26\u76F8\u6BD4\uFF0C1-4\u5C81\u7684\u5B69\u5B50\u4F7F\u7528\u5B89\u5168\u5EA7\u6905\u53D7\u4F24\u7684\u98CE\u9669\u4E0B\u964D54%\uFF0C4-8\u5C81\u7684\u5B69\u5B50\u98CE\u9669\u4E0B\u964D45%\u3002</p><p>\u6625\u8282\u671F\u95F4\uFF0C\u5F88\u591A\u5730\u65B9\u96FE\u973E\u4E5F\u662F\u5F88\u4E25\u91CD\uFF0C\u53BB\u7A7A\u6C14\u6C61\u67D3\u4E25\u91CD\u5730\u533A\uFF0C\u8BB0\u5F97\u5E26\u4E0A\u53E3\u7F69\u3002\u5728\u8282\u65E5\u671F\u95F4\uFF0C\u5B69\u5B50\u7684\u6D3B\u52A8\u7684\u7A7A\u95F4\u4E5F\u66F4\u5927\uFF0C\u6211\u4EEC\u9700\u8981\u770B\u62A4\u597D\u5B69\u5B50\uFF0C\u8FDC\u79BB\u53A8\u623F\u8FD9\u4E9B\u76F8\u5BF9\u5371\u9669\u7684\u533A\u57DF\u3002\u4E0D\u8981\u5355\u72EC\u8BA9\u5B69\u5B50\u73A9\u70DF\u82B1\u7206\u7AF9\uFF0C\u4E5F\u4E0D\u8981\u8BA9\u5B69\u5B50\u72EC\u81EA\u53BB\u56F4\u89C2\u522B\u4EBA\u653E\u70DF\u82B1\uFF0C\u6BCF\u5E74\u6625\u8282\u533B\u9662\u90FD\u8981\u63A5\u8BCA\u5F88\u591A\u7206\u7AF9\u9020\u6210\u7684\u4F24\u5BB3\uFF0C\u70B8\u4F24\u624B\u3001\u70B8\u4F24\u773C\u775B\u7684\u90FD\u6709\uFF0C\u6709\u7684\u5B69\u5B50\u56E0\u6B64\u81F4\u6B8B\u3002</p><p>\u4EE5\u4E0A\u90FD\u662F\u8001\u751F\u5E38\u8C08\u7684\u5185\u5BB9\uFF0C\u4E00\u76F4\u8BA2\u9605\u8FD9\u4E2A\u8D26\u53F7\u7684\u8BFB\u8005\u53EF\u80FD\u65E9\u90FD\u77E5\u9053\u4E86\u3002\u4F46\u73B0\u5B9E\u662F\u4E86\u89E3\u8FD9\u4E9B\u77E5\u8BC6\u7684\u4EBA\u4E0D\u662F\u592A\u591A\u800C\u662F\u592A\u5C11\uFF0C\u6240\u4EE5\u624D\u4F1A\u6709\u90A3\u4E48\u591A\u672C\u53EF\u4EE5\u907F\u514D\u7684\u60B2\u5267\uFF0C\u6B22\u8FCE\u5927\u5BB6\u8F6C\u53D1\u7ED9\u4EB2\u670B\u597D\u53CB\u3002\u6700\u540E\u63D0\u524D\u795D\u5927\u5BB6\u65B0\u6625\u5FEB\u4E50\u3002\uFF08\u9898\u56FE\u6765\u81EAApple\uFF09</p><p>\u76F8\u5173\u9605\u8BFB\uFF0C\u5173\u6CE8\u516C\u4F17\u53F7<strong>drpei</strong>\u540E</p><p>\u56DE\u590D**\u300C\u7A7F\u8863\u300D**\u4E86\u89E3\u5982\u4F55\u7ED9\u5B69\u5B50\u7A7F\u8863</p><p>\u56DE\u590D**\u300C\u5F02\u7269\u300D**\u4E86\u89E3\u6C14\u7BA1\u5F02\u7269</p><p>\u56DE\u590D**\u300C\u559D\u9152\u300D**\u4E86\u89E3\u559D\u9152\u5BF9\u5B69\u5B50\u7684\u5371\u5BB3</p><p>\u56DE\u590D**\u300C\u8865\u54C1\u300D**\u4E86\u89E3\u8865\u54C1\u5BF9\u5B69\u5B50\u7684\u5371\u5BB3</p><hr><p>\u957F\u6309\u4E0B\u9762\u4E8C\u7EF4\u7801\uFF0C\u5173\u6CE8<strong>\u6021\u79BE\u5065\u5EB7</strong>\uFF0C\u7ED9\u5B69\u5B50\u8282\u65E5\u4FDD\u9A7E\u62A4\u822A\u3002</p>',24),l={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651626530&idx=1&sn=e030f74e17e675eda0492f55eba199d2&chksm=8b5cb5d8bc2b3cce865b8d09dc22c7635b0741a3ed48c12b6648d1053dd877397be8ec19824e&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},h=c("\u67E5\u770B\u539F\u6587");function g(m,b){const p=r("ExternalLinkIcon");return t(),n(a,null,[_,e("p",null,[e("a",l,[h,o(p)])])],64)}var u=d(i,[["render",g]]);export{u as default};
