import{r as a,o as t,c as n,a as e,b as r,F as c,d as o,e as s}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const i={},h=o('<h3 id="\u7AD6\u62B1\u4F1A\u5F71\u54CD\u810A\u67F1" tabindex="-1"><a class="header-anchor" href="#\u7AD6\u62B1\u4F1A\u5F71\u54CD\u810A\u67F1" aria-hidden="true">#</a> \u7AD6\u62B1\u4F1A\u5F71\u54CD\u810A\u67F1\uFF1F</h3><p><em>\u201C</em> <em>\u5B69\u5B50\u4ECE\u51FA\u751F\u51E0\u5929\u5C31\u559C\u6B22\u7AD6\u62B1\uFF0C\u81EA\u5DF1\u8981\u8DB4\u5728\u5927\u4EBA\u80A9\u5934\u6216\u7A9D\u5728\u5927\u4EBA\u6000\u91CC\u7761\u89C9\u3002\u542C\u8BF4\u7AD6\u62B1\u4F1A\u5F15\u8D77\u810A\u67F1\u95EE\u9898\uFF0C\u8BF7\u95EE\u5177\u4F53\u4F1A\u5F15\u8D77\u810A\u67F1\u4EC0\u4E48\u6837\u7684\u95EE\u9898\uFF1F\u201D</em></p><p>\u8FD9\u662F\u6211\u66FE\u6536\u5230\u8FC7\u7684\u4E00\u4E2A\u54A8\u8BE2\uFF0C\u770B\u5230\u8FD9\u4E2A\u95EE\u9898\u7B2C\u4E00\u53CD\u5E94\u662F\u4E00\u6123\uFF0C\u80B2\u513F\u754C\u7684\u8BF4\u6CD5\u600E\u4E48\u8FD9\u4E48\u591A\uFF1F\u6709\u65F6\u6211\u4E5F\u4E0D\u77E5\u9053\u8FD9\u4E9B\u8BF4\u6CD5\u4ECE\u54EA\u5192\u51FA\u6765\uFF0C\u540E\u6765\u641C\u4E86\u4E00\u4E0B\uFF0C\u539F\u6765\u5BB6\u957F\u542C\u5230\u7684\u8FD9\u4E2A\u8BF4\u6CD5\u5E76\u4E0D\u662F\u7A7A\u7A74\u6765\u98CE\u3002</p><p>_\u201C6\u4E2A\u6708\u5185\u4E0D\u8981\u7AD6\u62B1\uFF0C\u8FC7\u65E9\u3001\u9891\u7E41\u7AD6\u62B1\u5BF9\u5A74\u513F\u810A\u67F1\u53D1\u80B2\u4E0D\u5229\u3002\u201D _\u8FD9\u5176\u5B9E\u662F\u4E00\u4E9B\u80B2\u513F\u4E13\u5BB6\u53CD\u590D\u4F20\u64AD\u7684\u89C2\u70B9\u3002\u8FD9\u4E2A\u8BF4\u6CD5\u6709\u4F9D\u636E\u5417\uFF1F</p><p>\u6309\u5E38\u8BC6\u6765\u8BF4\uFF0C\u5E73\u62B1\u7AD6\u62B1\u53EA\u4E0D\u8FC7\u662F\u810A\u67F1\u627F\u53D7\u7684\u91CD\u91CF\u65B9\u5411\u4E0D\u4E00\u6837\u800C\u5DF2\uFF0C\u524D\u8005\u627F\u53D7\u6A2A\u5411\u7684\u529B\u91CF\uFF0C\u540E\u8005\u627F\u53D7\u7EB5\u5411\u7684\u529B\u91CF\u3002\u5E73\u62B1\u7684\u65F6\u5019\u7528\u624B\u6258\u7740\u5B69\u5B50\u7684\u8170\u81C0\u90E8\uFF0C\u540C\u65F6\u8BA9\u5B69\u5B50\u7684\u5934\u9888\u90E8\u6795\u5728\u81C2\u5F2F\u91CC\uFF0C\u810A\u67F1\u90FD\u5F97\u5230\u4E86\u4FDD\u62A4\uFF0C\u5E94\u8BE5\u6CA1\u4EC0\u4E48\u597D\u62C5\u5FC3\u7684\uFF0C\u7AD6\u62B1\u65F6\u4E00\u624B\u62B1\u4F4F\u81C0\u90E8\uFF0C\u4E00\u4E2A\u624B\u62D6\u4F4F\u5934\u9888\u90E8\u4FDD\u62A4\u9888\u80F8\u690E\uFF0C\u5B69\u5B50\u7684\u4F53\u91CD\u5927\u90E8\u5206\u9760\u5927\u4EBA\u7684\u624B\u81C2\u652F\u6491\u7740\uFF0C\u65B0\u751F\u513F\u810A\u67F1\u662F\u5F31\uFF0C\u4F46\u4F53\u91CD\u4E5F\u8F7B\uFF0C\u88AB\u7AD6\u62B1\u7684\u65F6\u5019\u4E5F\u4E0D\u662F\u5B8C\u5168\u9760\u81EA\u5DF1\u7684\u810A\u67F1\u652F\u6491\u91CD\u91CF\uFF0C\u6309\u5E38\u8BC6\u6765\u8BF4\u4E5F\u5E94\u8BE5\u6CA1\u6709\u95EE\u9898\u3002</p><p>\u4F46\u5E38\u8BC6\u786E\u5B9E\u662F\u6709\u9519\u8BEF\u7684\u53EF\u80FD\uFF0C\u7406\u8BBA\u4E0A\u6765\u8BF4\uFF0C\u5E73\u62B1\u7684\u65F6\u5019\u810A\u67F1\u627F\u53D7\u7684\u538B\u529B\u5206\u5E03\u6BD4\u7AD6\u62B1\u66F4\u5747\u5300\uFF0C\u6211\u6015\u81EA\u5DF1\u56E0\u4E3A\u4E3B\u89C2\u60F3\u8C61\u800C\u51FA\u9519\uFF0C\u5C31\u68C0\u7D22\u4E86\u4E00\u4E0BPubmed\uFF0C\u4F46\u771F\u6CA1\u770B\u5230\u54EA\u4E2A\u7814\u7A76\u8BF4\u7AD6\u62B1\u5BF9\u5B69\u5B50\u6709\u5371\u5BB3\u3002</p><p>\u5A74\u513F\u7AD6\u7740\u4E0D\u5229\u4E8E\u810A\u67F1\u7684\u8BF4\u6CD5\u56FD\u5916\u4E5F\u786E\u5B9E\u6709\u8FC7\uFF0C\u4E0D\u8FC7\u539F\u56E0\u4E0D\u662F\u62C5\u5FC3\u7AD6\u62B1\uFF0C\u800C\u662F\u56E0\u4E3A\u5A74\u513F\u540A\u5E26\u7684\u4F7F\u7528\uFF0C\u4E0A\u4E16\u7EAA\u516B\u4E5D\u5341\u5E74\u4EE3\u7684\u5A74\u513F\u540A\u5E26\u6BD4\u8F83\u7B80\u964B\uFF0C\u5B69\u5B50\u88AB\u7AD6\u7740\u653E\u5728\u540A\u5E26\u91CC\uFF0C\u5934\u9888\u90E8\u6CA1\u6709\u652F\u6491\u4FDD\u62A4\uFF0C\u5F15\u53D1\u4E86\u4E00\u4E9B\u513F\u79D1\u533B\u751F\u62C5\u5FC3\u5B69\u5B50\u690E\u4F53\u8FC7\u5EA6\u4F38\u5C55\u5F15\u8D77\u810A\u67F1\u524D\u79FB\u3002 [1] \u4F46\u624B\u62B1\u7684\u65F6\u5019\u7528\u624B\u6258\u7740\u4FDD\u62A4\u5934\u9888\u90E8\uFF0C\u5B8C\u5168\u53EF\u4EE5\u907F\u514D\u8FD9\u79CD\u62C5\u5FC3\u3002</p><p>\u4E8B\u5B9E\u4E0A\uFF0C\u7AD6\u62B1\u8FD8\u6709\u4E00\u4E9B\u5E73\u62B1\u6240\u6CA1\u6709\u7684\u4F18\u52BF\uFF0C\u6E05\u9192\u65F6\u4FDD\u6301\u7AD6\u4F4D\u7684\u4F53\u4F4D\u6CBB\u7597\uFF0C\u662F\u6CBB\u7597\u5A74\u513F\u80C3\u98DF\u7BA1\u8FD4\u6D41\u7684\u4E00\u79CD\u65B9\u5F0F [2] \uFF0C\u5582\u5976\u540E\u7AD6\u62B1\u62CD\u80CC\uFF0C\u4E5F\u662F\u51CF\u5C11\u5A74\u513F\u6EA2\u5976\u7684\u5E38\u7528\u65B9\u5F0F\u3002\u7AD6\u62B1\u7684\u65F6\u5019\u5927\u4EBA\u5C06\u5B69\u5B50\u73AF\u62B1\u5728\u6000\u91CC\uFF0C\u5B69\u5B50\u548C\u7236\u6BCD\u808C\u80A4\u7D27\u8D34\uFF0C\u8BA9\u5B69\u5B50\u611F\u89C9\u66F4\u6E29\u6696\u548C\u5B89\u5168\uFF0C\u6709\u52A9\u589E\u8FDB\u4EB2\u5B50\u611F\u60C5\u3002\u8BA9\u65E9\u4EA7\u513F\u7AD6\u7740\u4F9D\u504E\u5728\u80F8\u524D\u7684\u888B\u9F20\u80B2\u513F\u6CD5\uFF0C\u7F8E\u56FD\u513F\u79D1\u5B66\u4F1A\u4E5F\u662F\u652F\u6301\uFF0C\u4E5F\u6CA1\u6709\u7AD6\u62B1\u4E0D\u80FD\u592A\u4E45\u592A\u9891\u7E41\u4E4B\u7C7B\u7684\u8BF4\u6CD5\u3002 [3]</p><p>\u5A74\u513F\u9192\u7740\u7684\u65F6\u5019\u53EF\u4EE5\u5728\u5927\u4EBA\u7684\u770B\u62A4\u4E0B\u591A\u8DB4\u7740\u73A9\uFF0C\u953B\u70BC\u9888\u90E8\u808C\u8089\uFF0C\u4FC3\u8FDB\u52A8\u4F5C\u53D1\u80B2\uFF0C\u4F46\u5B69\u5B50\u4E0D\u53EF\u80FD\u9192\u7740\u7684\u65F6\u5019\u90FD\u613F\u610F\u8DB4\u7740\uFF0C\u8DB4\u4E45\u4E86\u5B69\u5B50\u4E5F\u4F1A\u7D2F\uFF0C\u54ED\u95F9\u7684\u65F6\u5019\u4E5F\u9700\u8981\u62B1\u7740\u5B89\u629A\uFF0C\u6216\u8005\u5076\u5C14\u7AD6\u62B1\u7740\u8D70\u8D70\uFF0C\u8BA9\u4ED6\u770B\u770B\u4E0E\u8EBA\u7740\u65F6\u5019\u4E0D\u4E00\u6837\u7684\u4E16\u754C\uFF0C\u548C\u5468\u56F4\u7684\u4EBA\u8FDB\u884C\u8868\u60C5\u773C\u775B\u7684\u4EA4\u6D41\uFF0C\u800C\u4E0D\u662F\u5E73\u62B1\u7740\u603B\u76EF\u7740\u5929\u82B1\u677F\uFF0C\u4E5F\u6709\u52A9\u4E8E\u611F\u77E5\u7684\u53D1\u80B2\u3002</p><p>\u4E3A\u4E86\u964D\u4F4E\u5A74\u513F\u731D\u6B7B\u7684\u98CE\u9669\uFF0C\u6211\u4EEC\u8981\u6C42\u4E00\u5C81\u4EE5\u5185\u7684\u5B69\u5B50\u90FD\u5E94\u8BE5\u4EF0\u5367\uFF0C\u957F\u65F6\u95F4\u7684\u4EF0\u5367\u5BB9\u6613\u5BFC\u81F4\u6241\u5934\u7578\u5F62\uFF0C\u5982\u679C\u5076\u5C14\u88AB\u5BB6\u957F\u62B1\u62B1\u4E5F\u603B\u662F\u5E73\u7740\uFF0C\u5B69\u5B50\u5934\u90E8\u91CD\u91CF\u8FD8\u662F\u538B\u5728\u624B\u81C2\u4E0A\uFF0C\u7AD6\u62B1\u5C31\u53EF\u4EE5\u907F\u514D\u8FD9\u79CD\u538B\u8FEB,\u6309\u8BF4\u4E5F\u53EF\u4EE5\u964D\u4F4E\u4E00\u70B9\u6241\u5934\u7684\u98CE\u9669\u3002\u4F46\u5728\u5B69\u5B50\u80FD\u591F\u7A33\u5B9A\u62AC\u5934\u4E4B\u524D\uFF0C\u6216\u8005\u80FD\u62AC\u5934\u4F46\u7761\u7740\u4E86\uFF0C\u7AD6\u7740\u65F6\u5E94\u8BE5\u4FDD\u62A4\u597D\u4ED6\u7684\u5934\u9888\u90E8\u3002</p><p>\u65E0\u8BBA\u662F\u5E73\u62B1\u3001\u8FD8\u662F\u7AD6\u62B1\uFF0C\u957F\u65F6\u95F4\u4FDD\u6301\u4E00\u4E2A\u59FF\u52BF\u5BF9\u5927\u4EBA\u5B69\u5B50\u90FD\u4F1A\u4EA7\u751F\u4E0D\u9002\uFF0C\u6240\u4EE5\u5404\u79CD\u62B1\u59FF\u8F6E\u7740\u6765\u662F\u5F88\u81EA\u7136\u7684\u505A\u6CD5\u3002\u5404\u79CD\u62B1\u59FF\u6709\u5404\u81EA\u7684\u4F18\u7F3A\u70B9\uFF0C\u5426\u5B9A\u4E00\u4E2A\u62B1\u59FF\u5E94\u8BE5\u7ED9\u51FA\u5408\u7406\u7684\u7406\u7531\uFF0C\u4E5F\u8981\u5145\u5206\u6743\u8861\u6536\u76CA\u548C\u98CE\u9669\u3002\u5373\u4FBF\u662F\u4E00\u4E2A\u5B69\u5B50\u56E0\u4E3A\u7AD6\u62B1\u51FA\u73B0\u4E86\u810A\u67F1\u524D\u79FB\uFF0C\u662F\u4E0D\u662F\u5C31\u5E94\u8BE5\u8BA9\u5343\u4E07\u4E2A\u5B69\u5B50\u907F\u514D\u91C7\u53D6\u8FD9\u4E2A\u6709\u8FD9\u4E48\u591A\u597D\u5904\u7684\u62B1\u59FF\uFF1F\u5C31\u50CF\u56E0\u4E3A\u53D1\u751F\u4E86\u4E00\u8D77\u8F66\u7978\u5C31\u5EFA\u8BAE\u5168\u4E16\u754C\u7684\u4EBA\u90FD\u4E0D\u8981\u5750\u8F66\u4E86\uFF1F\u66F4\u4F55\u51B5\u810A\u67F1\u524D\u79FB\u548C\u7AD6\u62B1\u7684\u5173\u7CFB\u4ECE\u6765\u6CA1\u8BC1\u5B9E\u8FC7\u3002</p><p>\u5BF9\u4E8E\u4E0D\u786E\u5B9A\uFF0C\u540E\u679C\u53C8\u6BD4\u8F83\u4E25\u91CD\u7684\u5371\u5BB3\uFF0C\u5927\u5BB6\u4E00\u822C\u662F\u91C7\u53D6\u201C\u5B81\u53EF\u4FE1\u6709\u4E0D\u53EF\u4FE1\u5176\u65E0\u201D\u7684\u6001\u5EA6\uFF0C\u7AD6\u62B1\u5BF9\u5B69\u5B50\u810A\u67F1\u4E0D\u5229\u7684\u8BF4\u6CD5\u4F20\u64AD\u5F00\u6765\u4EE5\u540E\uFF0C\u771F\u78B0\u4E0A\u957F\u5927\u540E\u6709\u810A\u67F1\u95EE\u9898\u7684\u4EBA\uFF0C\u5F88\u5BB9\u6613\u5173\u8054\u5230\u7AD6\u62B1\u4E0A\u6765\uFF0C\u7136\u540E\u8FD9\u79CD\u8BF4\u6CD5\u5C31\u88AB\u201C\u8BC1\u5B9E\u201D\u4E86\uFF0C\u7136\u540E\u8FDB\u4E00\u6B65\u6269\u5927\u4F20\u64AD\uFF0C\u6BD5\u7ADF\u6709\u51E0\u4E2A\u5B69\u5B50\u6CA1\u88AB\u7AD6\u62B1\u8FC7\u5462\uFF1F \u4F46\u810A\u67F1\u95EE\u9898\u662F\u4E0D\u662F\u7AD6\u62B1\u5F15\u8D77\u7684\uFF0C\u5C31\u6CA1\u6709\u4EBA\u597D\u5426\u5B9A\u4E86\uFF0C\u6240\u4EE5\u6050\u5413\u5F0F\u7684\u201C\u79D1\u666E\u201D\u5F88\u5BB9\u6613\u5F15\u8D77\u5BB6\u957F\u7684\u7126\u8651\uFF0C\u4E5F\u5F88\u5BB9\u6613\u88AB\u4F20\u64AD\u3002</p><p>\u56E0\u4E3A\u4E00\u53E5\u201C\u9891\u7E41\u7AD6\u62B1\u5BF9\u5A74\u513F\u810A\u67F1\u53D1\u80B2\u4E0D\u5229\u201D\uFF0C\u8BA9\u4E00\u4E9B\u5BB6\u957F\u56F0\u60D1\uFF0C\u4E5F\u8BA9\u5F88\u591A\u5BB6\u957F\u5BF9\u7AD6\u62B1\u4EA7\u751F\u4E86\u5FC3\u7406\u9634\u5F71\uFF0C\u6240\u4EE5\u4E13\u4E1A\u4EBA\u58EB\u8BF4\u8BDD\u5E94\u8BE5\u66F4\u614E\u91CD\u3002 \u5927\u5BB6\u770B\u5230\u8FD9\u79CD\u8BED\u7109\u4E0D\u8BE6\u7684\u89C2\u70B9\u65F6\u4E5F\u5E94\u8BE5\u4FDD\u7559\u4E00\u4EFD\u81EA\u6211\u601D\u8003\u3002 \uFF08\u672C\u6587\u9996\u53D1\u4E8E2015\u5E74\uFF0C\u9898\u56FE\u6765\u81EAwikihow\uFF09</p><p><strong>\u53C2\u8003\u8D44\u6599</strong></p><p>[1]http://www.continuum-concept.org/reading/spinalStress.html</p><p>[2]http://pediatrics.aappublications.org/content/131/5/e1684.full?sid=e4dc8ff7-f2ac-4d6c-b129-e2ac4e63f4fb</p><p>[3]https://www.healthychildren.org/English/ages-stages/baby/preemie/Pages/About-Skin-to-Skin-Care.aspx</p><hr><p>\u5BF9\u65B0\u624B\u7236\u6BCD\u6765\u8BF4\uFF0C \u7531\u4E8E\u6CA1\u6709\u7ECF\u9A8C\uFF0C\u5E38\u5E38\u4F1A\u628A\u4E0D\u662F\u95EE\u9898\u7684\u95EE\u9898\u5F53\u6210\u5927\u95EE\u9898\uFF0C\u7136\u540E\u5F88\u7126\u8651\uFF0C\u540C\u65F6\u4E5F\u53EF\u80FD\u628A\u662F\u95EE\u9898\u7684\u95EE\u9898\u6CA1\u5F53\u95EE\u9898\uFF0C\u7136\u540E\u803D\u8BEF\u5B69\u5B50\u7684\u75C5\u60C5\u3002</p><p>\u6240\u4EE5\u638C\u63E1\u4E00\u4E9B\u65B0\u751F\u513F\u671F\u5E38\u89C1\u77E5\u8BC6\uFF0C\u53EF\u4EE5\u51CF\u5C11\u5F88\u591A\u4E0D\u5FC5\u8981\u7684\u7126\u8651\uFF0C\u4E5F\u53EF\u4EE5\u51CF\u5C11\u5B69\u5B50\u4E00\u4E9B\u4E0D\u5FC5\u8981\u7684\u4F24\u5BB3\u3002</p><p>\u5982\u679C\u4F60\u4E0D\u6E05\u695A\u81EA\u5DF1\u5BF9\u8FD9\u4E9B\u8FD9\u4E9B\u77E5\u8BC6\u7684\u638C\u63E1\u7A0B\u5EA6\uFF0C\u53EF\u4EE5\u901A\u8FC7\u4E0B\u9762\u6D77\u62A5\u6D4B\u8BD5\u4E00\u4E0B\uFF0C\u770B\u770B\u81EA\u5DF1\u80FD\u62FF\u591A\u5C11\u5206\uFF0C\u505A\u9519\u4E86\u8FD8\u53EF\u4EE5\u67E5\u770B\u7B54\u6848\u3002</p>',21),l={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651633521&idx=1&sn=aeaadd614806ea7a64ada274f0603edf&chksm=8b5c9e8bbc2b179d4f70b5bdf5e29803b990789be2d2decec187b3d548419dd633c0b4478f34&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},_=s("\u67E5\u770B\u539F\u6587");function m(b,f){const p=a("ExternalLinkIcon");return t(),n(c,null,[h,e("p",null,[e("a",l,[_,r(p)])])],64)}var x=d(i,[["render",m]]);export{x as default};
