import{r as n,o as s,c as d,a as e,b as p,w as o,F as r,d as _,e as t}from"./app.9aaa2256.js";import{_ as i}from"./plugin-vue_export-helper.21dcd24c.js";const l={},b=_('<h3 id="\u4F24\u533B\u6848\u518D\u53D1-\u4F53\u5236\u5185\u533B\u751F\u7684\u51FA\u8DEF\u5728\u54EA\u91CC" tabindex="-1"><a class="header-anchor" href="#\u4F24\u533B\u6848\u518D\u53D1-\u4F53\u5236\u5185\u533B\u751F\u7684\u51FA\u8DEF\u5728\u54EA\u91CC" aria-hidden="true">#</a> \u4F24\u533B\u6848\u518D\u53D1\uFF0C\u4F53\u5236\u5185\u533B\u751F\u7684\u51FA\u8DEF\u5728\u54EA\u91CC\uFF1F</h3><p>2012\u5E74\u54C8\u533B\u5927\u7684\u533B\u5B66\u751F\u738B\u6D69\u88AB\u6740</p><p>2013\u5E74\u6E29\u5CAD\u5E02\u7B2C\u4E00\u4EBA\u6C11\u738B\u4E91\u6770\u533B\u751F\u88AB\u6740</p><p>2016\u5E74\u5E7F\u4E1C\u7701\u4EBA\u6C11\u533B\u9662\u9648\u4EF2\u4F1F\u533B\u751F\u88AB\u6740</p><p>\u2026.</p><p>\u8FD9\u7C7B\u6076\u6027\u4F24\u533B\u4E8B\u4EF6\u53D1\u751F\u592A\u591A\uFF0C\u4EE5\u81F3\u4E8E\u8FD9\u6B21\u6768\u6587\u533B\u751F\u88AB\u5BB3\u540E\uFF0C\u4EBA\u4EEC\u6700\u521D\u4E5F\u662F\u6CA1\u4EC0\u4E48\u53CD\u54CD\u3002\u5F88\u591A\u4EBA\u7684\u53CD\u5E94\u662F\uFF1A\u54E6\uFF0C\u53C8\u6740\u533B\u751F\u4E86\u554A\u3002</p><p>\u4E0D\u4EC5\u5F88\u591A\u666E\u901A\u4EBA\u662F\u8FD9\u79CD\u53CD\u5E94\uFF0C\u8FDE\u533B\u62A4\u4EBA\u5458\u53EF\u80FD\u4E5F\u662F\u89C1\u602A\u4E0D\u602A\u6CE2\u6F9C\u4E0D\u60CA\u4E86\u3002\u76F4\u5230\u884C\u51F6\u7684\u89C6\u9891\u6D41\u4F20\u51FA\u6765\u4E86\uFF0C\u770B\u5230\u89C6\u9891\u91CC\u51F6\u624B\u7AD9\u5728\u533B\u751F\u7684\u80CC\u540E\uFF0C\u7528\u767D\u6643\u6643\u7684\u5200\u51F6\u6B8B\u884C\u51F6\u7684\u573A\u666F\uFF0C\u624D\u5F15\u53D1\u4E86\u53CD\u54CD\u3002</p><p>\u770B\u8FC7\u89C6\u9891\u540E\uFF0C\u6211\u60F3\u6240\u6709\u7684\u4EBA\u90FD\u4F1A\u4E0D\u5BD2\u800C\u6817\uFF0C\u5728\u6124\u6168\u51F6\u624B\u7684\u51F6\u6B8B\u4E4B\u4F59\uFF0C\u4E5F\u6709\u533B\u751F\u8BF4\uFF1A\u65C1\u8FB9\u7684\u533B\u751F\u88C5\u6A21\u4F5C\u6837\u62C9\u51E0\u4E0B\u51F6\u624B\uFF0C\u6CA1\u6709\u4E00\u70B9\u8840\u6027\uFF0C\u592A\u8BA9\u4EBA\u6076\u5FC3\u4E86\u3002</p><p>\u6211\u662F\u4E0D\u8FD9\u6837\u8BA4\u4E3A\u7684\uFF0C\u7EDD\u5927\u90E8\u5206\u4EBA\u672A\u89C1\u5230\u8FC7\u884C\u51F6\u6740\u4EBA\uFF0C\u66F4\u4F55\u51B5\u662F\u8FD9\u6837\u7A77\u51F6\u6781\u6076\u7684\u6740\u4EBA\u624B\u6CD5\uFF0C\u9762\u5BF9\u8FD9\u6837\u7684\u8840\u8165\u573A\u9762\uFF0C\u672C\u80FD\u53CD\u5E94\u5E94\u8BE5\u662F\u8EB2\u907F\uFF0C\u65C1\u8FB9\u7684\u533B\u751F\u80FD\u5F80\u524D\u8D70\u5DF2\u7ECF\u662F\u5F88\u52C7\u6562\u4E86\uFF0C\u66F4\u4F55\u51B5\u8FD8\u505A\u4E86\u62C9\u51F6\u624B\u7684\u52A8\u4F5C\u3002</p><p>\u8D8B\u5229\u907F\u5BB3\u662F\u4EBA\u7684\u672C\u80FD\uFF0C\u90A3\u4E3A\u4EC0\u4E48\u5F53\u4E00\u4E2A\u804C\u4E1A\uFF0C\u4E00\u4E2A\u573A\u6240\u6210\u4E3A\u4E00\u4E2A\u5371\u9669\u7684\u804C\u4E1A\u548C\u573A\u6240\uFF0C\u5728\u4E3A\u4EC0\u4E48\u53D1\u751F\u4E86\u90A3\u4E48\u591A\u7684\u6740\u533B\u4E8B\u4EF6\u540E\uFF0C\u533B\u751F\u4EEC\u5E76\u6CA1\u6709\u5927\u89C4\u6A21\u79BB\u5F00\u5462\uFF1F</p><p>\u6211\u60F3\u65E0\u975E\u662F\u4E24\u4E2A\u539F\u56E0\uFF0C\u4E00\u662F\u6CA1\u6709\u592A\u591A\u9009\u62E9\uFF0C\u65E0\u5904\u53EF\u53BB\uFF0C\u4E8C\u662F\u4EBA\u662F\u6709\u5F88\u9002\u5E94\u80FD\u529B\uFF0C\u4E8B\u60C5\u7ECF\u5386\u591A\u4E86\uFF0C\u6E10\u6E10\u9EBB\u6728\u548C\u4E60\u60EF\u4E86\u3002</p><p>\u6240\u4EE5\uFF0C\u5373\u4FBF\u4F24\u533B\u6848\u9891\u53D1\uFF0C\u5728\u53C8\u6CA1\u6709\u66F4\u597D\u7684\u51FA\u8DEF\u53EF\u9009\u7684\u60C5\u51B5\u4E0B\uFF0C\u66F4\u591A\u7684\u9009\u62E9\u662F\u5728\u8FD9\u4E2A\u533B\u7597\u73AF\u5883\u4E0B\u5982\u4F55\u5C3D\u91CF\u505A\u81EA\u6211\u4FDD\u62A4\uFF0C\u6BD4\u5982\u8FDC\u79BB\u90A3\u4E9B\u5371\u9669\u7684\u75C5\u4EBA\uFF0C\u4E0D\u505A\u90A3\u4E9B\u9AD8\u98CE\u9669\u7684\u624B\u672F\uFF0C\u8FC1\u5C31\u75C5\u4EBA\u7B49\u7B49\u6765\u81EA\u4FDD\u3002</p><p>\u6BD4\u5982\u8FD9\u6B21\u4E8B\u4EF6\u91CC\u7684\u90A3\u4F4D\u75C5\u4EBA\uFF0C95\u5C81\u9AD8\u9F84\u8111\u6897\u7684\u75C5\u4EBA\uFF0C\u5BB6\u5C5E\u6781\u5EA6\u4E0D\u4FE1\u4EFB\u800C\u4E14\u975E\u5E38\u60C5\u7EEA\u5316\uFF0C\u5E76\u4E14\u6B64\u524D\u5DF2\u7ECF\u6709\u5BF9\u533B\u751F\u4EEC\u6709\u8FC7\u8A00\u8BED\u4E0A\u7684\u4EBA\u8EAB\u5A01\u80C1\uFF0C\u663E\u7136\u662F\u4E00\u4E2A\u9AD8\u98CE\u9669\u75C5\u4EBA\u3002</p><p>\u533B\u751F\u4EEC\u9047\u5230\u8FD9\u6837\u7684\u75C5\u4EBA\uFF0C\u672C\u80FD\u662F\u8EB2\u907F\uFF0C\u6240\u4EE5\u6C11\u822A\u603B\u533B\u9662\u7684\u533B\u751F\u5728\u56DE\u7B54\u4E3A\u4EC0\u4E48\u4E0D\u8054\u7CFB\u75C5\u4EBA\u4F4F\u9662\u65F6\u8BF4\uFF1A\u5929\u5929\u72AF\u6D51\u7684\u4E00\u5BB6\u4EBA\uFF0C\u8C01\u6562\u63A5\u8BCA\u6536\u6CBB\u3002\u5F53\u5927\u5BB6\u90FD\u60F3\u8EB2\u7684\u65F6\u5019\uFF0C\u603B\u8FD8\u662F\u4F1A\u6709\u4EBA\u8EB2\u4E0D\u5F00\uFF0C\u6BD4\u5982\u6025\u8BCA\u79D1\u7684\u533B\u751F\uFF0C\u6BD4\u5982\u4E0A\u591C\u73ED\u7684\u6768\u6587\u533B\u751F\u3002</p><p>\u77E5\u9053\u8FD9\u4E2A\u75C5\u4EBA\u6709\u98CE\u9669\uFF0C\u533B\u751F\u4EEC\u4E5F\u60F3\u6709\u591A\u4E9B\u4FDD\u62A4\u63AA\u65BD\uFF0C\u6240\u4EE5\u4E5F\u5411\u4E0A\u53CD\u5E94\uFF0C\u79D1\u91CC\u9662\u91CC\u4E5F\u505A\u4E86\u8BB0\u5F55\u548C\u5907\u6848\uFF0C\u4F46\u7ED9\u533B\u751F\u4EEC\u7684\u4FDD\u62A4\uFF0C\u4EC5\u4EC5\u662F\u5631\u5490\u6CE8\u610F\u5B89\u5168\u3002</p><p>\u6709\u5371\u9669\uFF0C\u60F3\u8EB2\uFF0C\u65E0\u5904\u53EF\u8EB2\uFF0C\u6240\u4EE5\u5FCD\u53D7\u3001\u8C03\u6574\u3001\u9002\u5E94\u3001\u8EB2\u907F\uFF0C\u4F46\u4F9D\u7136\u4F1A\u9762\u4E34\u4E00\u4E9B\u8EB2\u65E0\u53EF\u8EB2\u7684\u5883\u5730\uFF0C\u4F60\u60F3\u81EA\u4FDD\u7684\u65F6\u5019\uFF0C\u4F60\u4F1A\u53D1\u73B0\u4F60\u5728\u8FD9\u4E2A\u4F53\u7CFB\u91CC\u5E76\u6CA1\u6709\u8BDD\u8BED\u6743\uFF0C\u60F3\u81EA\u4FDD\u90FD\u5F88\u96BE\u3002</p><p>\u4E5F\u6B63\u56E0\u4E3A\u5982\u6B64\uFF0C\u6240\u4EE5\u54EA\u6015\u4F60\u9047\u5BB3\u4E86\uFF0C\u4F60\u4F9D\u7136\u53EF\u80FD\u5F97\u4E0D\u5230\u5C0A\u91CD\uFF0C\u4F60\u7684\u5BB6\u4EBA\u8FD8\u53EF\u80FD\u88AB\u4E8C\u6B21\u4F24\u5BB3\uFF0C\u6BD4\u5982\u53D1\u751F\u4E86\u8FD9\u6837\u7684\u6076\u6027\u4E8B\u4EF6\u540E\uFF0C\u533B\u9662\u9886\u5BFC\u7684\u7B2C\u4E00\u60F3\u6CD5\u8FD8\u662F\u7EF4\u7A33\uFF0C\u6D88\u9664\u5F71\u54CD\u3002</p><p>\u9662\u9886\u5BFC\u4E4B\u6240\u4EE5\u8FD9\u6837\u505A\uFF0C\u5176\u5B9E\u4E5F\u662F\u8D8B\u5229\u907F\u5BB3\u7684\u7ED3\u679C\uFF0C\u56E0\u4E3A\u4ED6\u4EEC\u7684\u804C\u8D23\u5C31\u662F\u7EF4\u6301\u7A33\u5B9A\uFF0C\u5728\u516C\u7ACB\u533B\u9662\u51E0\u4E4E\u5784\u65AD\u4E86\u533B\u7597\u884C\u4E1A\u7684\u60C5\u51B5\u4E0B\uFF0C\u533B\u751F\u7684\u60C5\u7EEA\u5E76\u4E0D\u90A3\u4E48\u91CD\u8981\uFF0C\u533B\u751F\u88AB\u6740\u4E86\uFF0C\u533B\u751F\u79BB\u804C\u4E86\uFF0C\u8FD8\u6709\u5927\u6279\u7684\u533B\u5B66\u751F\u7B49\u7740\u5165\u804C\u3002\u666E\u901A\u533B\u751F\u4EEC\u505A\u4E86\u9662\u9886\u5BFC\u540E\u4E5F\u4F1A\u8FD9\u6837\u505A\uFF0C\u8FD9\u4E2A\u4F53\u5236\u5C31\u662F\u8FD9\u6837\u8FD0\u884C\u7684\u3002</p><p>\u90A3\u533B\u751F\u4EEC\u5E94\u8BE5\u600E\u4E48\u529E\uFF1F\u5176\u5B9E\u8FD8\u662F\u53EA\u6709\u4E24\u4E2A\u9009\u62E9\uFF1A\u8981\u4E48\u5FCD\uFF0C\u8981\u4E48\u6EDA\u3002\u81F3\u4E8E\u662F\u5FCD\uFF0C\u8FD8\u662F\u6EDA\uFF0C\u80AF\u5B9A\u8FD8\u662F\u8981\u6839\u636E\u81EA\u5DF1\u7684\u80FD\u529B\u3001\u4E13\u4E1A\u3001\u673A\u4F1A\u6765\u505A\u9009\u62E9\u3002\u4F46\u4E0D\u8BBA\u662F\u5FCD\uFF0C\u8FD8\u662F\u6EDA\uFF0C\u6211\u89C9\u5F97\u90FD\u9700\u8981\u8BA4\u6E05\uFF1A</p><p>1.\u533B\u751F\u662F\u79BB\u751F\u6B7B\u5F88\u8FD1\u7684\u804C\u4E1A\uFF0C\u6240\u4EE5\u804C\u4E1A\u98CE\u9669\u4F1A\u66F4\u5927\u3002</p><p>2.\u4E2D\u56FD\u533B\u7597\u884C\u4E1A\u662F\u884C\u653F\u4E3B\u5BFC\u7684\u884C\u4E1A\uFF0C\u7EF4\u7A33\u8FD8\u662F\u884C\u653F\u4F53\u7CFB\u7684\u4E3B\u5BFC\u601D\u60F3\uFF0C\u5728\u6574\u4E2A\u884C\u4E1A\u57FA\u672C\u88AB\u516C\u7ACB\u533B\u9662\u5784\u65AD\u7684\u80CC\u666F\u4E0B\uFF0C\u533B\u751F\u7684\u8BDD\u8BED\u6743\u5F88\u5C0F\uFF0C\u6240\u4EE5\u533B\u751F\u6743\u76CA\u5F88\u5BB9\u6613\u6210\u4E3A\u7EF4\u7A33\u7684\u727A\u7272\u54C1\u3002</p><p>3.\u5927\u73AF\u5883\u77ED\u671F\u5185\u5F88\u96BE\u5927\u6539\uFF0C\u81EA\u5DF1\u5982\u679C\u4E0D\u52AA\u529B\uFF0C\u5C31\u968F\u65F6\u53EF\u80FD\u6210\u4E3A\u8EB2\u65E0\u53EF\u8EB2\u7684\u90A3\u4E2A\u4EBA\uFF0C\u4E0D\u8981\u56E0\u4E3A\u4E60\u60EF\u6216\u9EBB\u6728\u800C\u5931\u53BB\u52AA\u529B\u7684\u52A8\u529B\u3002</p><p>4.\u6211\u4EEC\u53EF\u4EE5\u53BB\u547C\u5401\u6539\u5584\u804C\u4E1A\u73AF\u5883\uFF0C\u4F46\u5C11\u53BB\u62B1\u6028\uFF0C\u6CA1\u4EBA\u559C\u6B22\u542C\u522B\u4EBA\u62B1\u6028\uFF0C\u62B1\u6028\u4E5F\u6539\u53D8\u4E0D\u4E86\u592A\u591A\uFF0C\u62B1\u6028\u591A\u4E86\u53CD\u800C\u5BB9\u6613\u88AB\u4EBA\u70E6\u3002\u4E5F\u8981\u8B66\u60D5\u90A3\u4E9B\u603B\u4E3A\u533B\u751F\u8BF4\u8BDD\u7684\u4EBA\uFF0C\u4F60\u7684\u60C5\u7EEA\u5F88\u53EF\u80FD\u6210\u4E3A\u522B\u4EBA\u6536\u5272\u7684\u5DE5\u5177\u3002</p><p>5.\u4E0D\u8981\u795E\u5723\u5316\u81EA\u5DF1\u7684\u804C\u4E1A\uFF0C\u804C\u4E1A\u5C31\u662F\u804C\u4E1A\uFF0C\u6CA1\u6709\u4E00\u4E2A\u804C\u4E1A\u503C\u5F97\u5531\u9882\u6B4C\u7684\uFF0C\u88AB\u6B4C\u9882\u5F97\u8D8A\u591A\uFF0C\u5927\u5BB6\u7684\u671F\u671B\u4E5F\u8D8A\u9AD8\uFF0C\u671F\u671B\u53D8\u6210\u5931\u671B\u65F6\uFF0C\u5C31\u53EF\u80FD\u53D8\u6210\u4F24\u4EBA\u7684\u5200\u3002</p><p>\u5728\u81EA\u5DF1\u6CA1\u6709\u8DB3\u591F\u80FD\u529B\u524D\uFF0C\u591A\u5B66\u4E60\uFF0C\u591A\u770B\u75C5\uFF0C\u591A\u505A\u624B\u672F\uFF0C\u591A\u79EF\u7D2F\u7ECF\u9A8C\u548C\u89C1\u8BC6\uFF0C\u540C\u65F6\u63D0\u9AD8\u81EA\u5DF1\u7684\u6C9F\u901A\u80FD\u529B\u548C\u4E2A\u4EBA\u54C1\u724C\u5EFA\u8BBE\u80FD\u529B\u7684\u533B\u751F\uFF0C\u52AA\u529B\u6210\u4E3A\u4E00\u4E2A\u6709\u66F4\u591A\u75C5\u4EBA\u8FFD\u968F\u7684\u533B\u751F\u3002\u540C\u65F6\u4E5F\u8981\u5584\u5F85\u75C5\u4EBA\uFF0C\u8FD9\u5BF9\u75C5\u4EBA\u66F4\u597D\uFF0C\u4E5F\u80FD\u51CF\u5C11\u4E0D\u5FC5\u8981\u7684\u98CE\u9669\uFF0C\u5BF9\u81EA\u5DF1\u4E5F\u662F\u4E00\u79CD\u4FDD\u62A4\u3002</p><p>\u603B\u800C\u8A00\u4E4B\uFF0C\u4E0D\u8981\u9EBB\u6728\uFF0C\u8981\u6709\u6210\u957F\u610F\u8BC6\u3002\u5F3A\u5927\u81EA\u5DF1\uFF0C\u624D\u6709\u9009\u62E9\u7684\u81EA\u7531\u3002</p><hr><p>3\u5E74\u524D\u6211\u4E5F\u662F\u4F53\u5236\u5185\u533B\u751F\u7684\u4E00\u5458\uFF0C\u4E5F\u7ECF\u5386\u8FC7\u6740\u533B\u6848\u9891\u53D1\u7684\u90A3\u51E0\u5E74\uFF0C\u6211\u4E5F\u66FE\u62B1\u6028\u3001\u5410\u69FD\u3001\u6323\u624E\u8FC7\u5F88\u4E45\uFF0C\u4E5F\u6B63\u662F\u56E0\u4E3A\u770B\u6E05\u4E86\u884C\u4E1A\u73B0\u72B6\uFF0C\u624D\u505A\u4E86\u9009\u62E9\u548C\u6539\u53D8\uFF0C\u4E5F\u662F\u5E0C\u671B\u81EA\u5DF1\u8D70\u51FA\u6765\u7684\u8FD9\u4E00\u6B65\uFF0C\u80FD\u4E3A\u6574\u4E2A\u884C\u4E1A\u7684\u6539\u53D8\u505A\u70B9\u52AA\u529B\u3002</p><p>\u5206\u4EAB\u4EE5\u4E0B\u51E0\u7BC7\u4EE5\u524D\u5199\u8FC7\u7684\u6587\u7AE0</p>',29),f=t("\u4E2D\u56FD\u533B\u751F\u7684\u6551\u8D4E"),h=t("\u6211\u8F9E\u804C\u4E86"),m=t("To B or To C,\u5BF9\u533B\u751F\u4E5F\u662F\u4E2A\u95EE\u9898"),u={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651629418&idx=1&sn=519bbf61d28fd65625191c6bc7d90d73&chksm=8b5c8e90bc2b0786177843595da835c6689b9a1916faec65b684aee7aa857d76a24fa86ef227&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},x=t("\u67E5\u770B\u539F\u6587");function z(k,N){const a=n("RouterLink"),c=n("ExternalLinkIcon");return s(),d(r,null,[b,e("p",null,[p(a,{to:"/s/__biz=MzA5NzE5ODIyOQ==&mid=200272238&idx=1&sn=72a2be45e2b386466d1bb795315e51bd/"},{default:o(()=>[f]),_:1})]),e("p",null,[p(a,{to:"/s/__biz=MzA5NzE5ODIyOQ==&mid=402846982&idx=1&sn=b2a412e89e0388ed0e95fe165c9ba31e/"},{default:o(()=>[h]),_:1})]),e("p",null,[p(a,{to:"/s/__biz=MzA5NzE5ODIyOQ==&mid=2651626272&idx=1&sn=0383fff1380c1092e58e901d58ba916b/"},{default:o(()=>[m]),_:1})]),e("p",null,[e("a",u,[x,p(c)])])],64)}var I=i(l,[["render",z]]);export{I as default};
