import{r as o,o as c,c as a,a as p,b as e,w as s,F as i,d as l,e as t}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const _={},b=l('<h3 id="\u4E00\u5E74\u786E\u8BCA\u8FD1\u767E\u4E07\u4EBA-\u8FD9\u4E2A\u4F20\u67D3\u75C5\u7684\u5371\u5BB3\u5F88\u591A\u4EBA\u5E76\u4E0D\u77E5\u9053" tabindex="-1"><a class="header-anchor" href="#\u4E00\u5E74\u786E\u8BCA\u8FD1\u767E\u4E07\u4EBA-\u8FD9\u4E2A\u4F20\u67D3\u75C5\u7684\u5371\u5BB3\u5F88\u591A\u4EBA\u5E76\u4E0D\u77E5\u9053" aria-hidden="true">#</a> \u4E00\u5E74\u786E\u8BCA\u8FD1\u767E\u4E07\u4EBA\uFF0C\u8FD9\u4E2A\u4F20\u67D3\u75C5\u7684\u5371\u5BB3\u5F88\u591A\u4EBA\u5E76\u4E0D\u77E5\u9053</h3><p>\u6BEB\u65E0\u7591\u95EE\uFF0C\u5F53\u4E0B\u5927\u5BB6\u6700\u5173\u5FC3\u7684\u75BE\u75C5\u65E0\u7591\u662F\u65B0\u51A0\uFF0C\u4F46\u5F88\u591A\u4EBA\u53EF\u80FD\u4E0D\u77E5\u9053\uFF0C\u8FD8\u6709\u5F88\u591A\u770B\u4E0D\u5230\u7684\u75AB\u60C5\uFF0C\u4E5F\u4E00\u76F4\u5728\u4F20\u64AD\uFF0C\u800C\u4E14\u4E5F\u5728\u6301\u7EED\u7684\u6740\u4EBA\uFF0C\u751A\u81F3\u611F\u67D3\u3001\u81F4\u6B7B\u7684\u75C5\u4EBA\u6BD4\u65B0\u51A0\u8FD8\u591A\uFF1B\u7ED3\u6838\u75C5\u5C31\u662F\u8FD9\u6837\u4E00\u79CD\u4F20\u67D3\u75C5\u3002</p><p>\u4ECA\u5929\u662F3\u670824\u65E5\uFF0C\u662F\u4E16\u754C\u9632\u6CBB\u7ED3\u6838\u75C5\u65E5\uFF0C\u518D\u5206\u4EAB\u4E00\u4E0B\u4E4B\u524D\u611F\u67D3\u79D1\u533B\u751F\u5199\u7684\u8FD9\u7BC7\u6587\u7AE0\u3002</p><p>\u7ED3\u6838\u75C5\u4F5C\u4E3A\u4E00\u4E2A\u53E4\u8001\u7684\u75BE\u75C5\uFF0C\u5DF2\u7ECF\u8086\u8650\u4E86\u4EBA\u7C7B\u6570\u5343\u5E74\uFF0C\u5373\u4F7F\u5728\u533B\u7597\u53D1\u8FBE\u7684\u4ECA\u5929\uFF0C\u4F9D\u7136\u662F\u5BFC\u81F4\u4EBA\u7C7B\u6B7B\u4EA1\u6392\u540D\u524D\u5341\u7684\u75BE\u75C5\u4E4B\u4E00\uFF0C\u4EC5\u57282018\u5E74\uFF0C\u4EC5\u5728\u6211\u4EEC\u56FD\u5BB6\uFF0C\u786E\u8BCA\u7684\u80BA\u7ED3\u6838\u75C5\u4EBA\u5C31\u8FBE\u5230823342\u4EBA\uFF0C\u5E76\u5BFC\u81F4\u6B7B\u4EA13149\u4EBA [1] \u3002</p><p>\u6240\u4EE5\uFF0C\u6211\u4EEC\u8981\u4E86\u89E3\u65B0\u51A0\uFF0C\u540C\u6837\u4E5F\u8981\u4E86\u89E3\u7ED3\u6838\u3002</p><p>\u56FE\u6E90\uFF1AWHO</p><p><strong>\u7ED3\u6838\u75C5\u662F\u5982\u4F55\u4F20\u64AD\u7684\uFF1F</strong></p><p>\u65B0\u51A0\u75AB\u60C5\u671F\u95F4\uFF0C\u6211\u4EEC\u719F\u6089\u4E86\u5F88\u591A\u4F20\u64AD\u65B9\u5F0F\uFF0C\u6BD4\u5982\u63A5\u89E6\u4F20\u64AD\u3001\u98DE\u6CAB\u4F20\u64AD\u3001\u7A7A\u6C14\u4F20\u64AD\uFF0C\u6211\u4EEC\u4E5F\u66FE\u95FB\u201C\u7A7A\u6C14\u4F20\u64AD\u201D\u800C\u8272\u53D8\uFF0C\u800C\u7ED3\u6838\u5C31\u662F\u4E00\u79CD\u7A7A\u6C14\u4F20\u64AD\u7684\u4F20\u67D3\u6027\u75BE\u75C5\u3002</p><p>\u4E0E\u65B0\u51A0\u7684\u75C5\u539F\u4F53\u201C\u75C5\u6BD2\u201D\u4E0D\u540C\u7684\u662F\uFF0C\u7ED3\u6838\u75C5\u7684\u201C\u5143\u51F6\u201D\u662F\u4E00\u79CD\u7279\u6B8A\u7684\u7EC6\u83CC\uFF0C\u53EB\u7ED3\u6838\u6746\u83CC\u3002</p><p>\u75C5\u6BD2\u7531\u4E8E\u6CA1\u6709\u5B8C\u6574\u7684\u751F\u5B58\u7CFB\u7EDF\uFF0C\u5B83\u79BB\u5F00\u5BBF\u4E3B\u540E\uFF0C\u5B58\u6D3B\u65F6\u95F4\u76F8\u5BF9\u66F4\u77ED\u3002\u800C\u7ED3\u6838\u6746\u83CC\u4F5C\u4E3A\u4E00\u4E2A\u62E5\u6709\u751F\u5B58\u7ED3\u6784\u7684\u7EC6\u83CC\uFF0C\u5176\u5916\u53F7\u201C\u5C0F\u5F3A\u201D\uFF0C\u751F\u547D\u529B\u975E\u5E38\u987D\u5F3A\uFF0C\u5728\u5E72\u71E5\u7684\u75F0\u5185\u4E5F\u53EF\u4EE5\u5B58\u6D3B6\uFF5E8\u4E2A\u6708 [2] \u3002</p><p><strong>\u611F\u67D3\u4E86\u7ED3\u6838\u6746\u83CC\u5C31\u4F1A\u5F97\u7ED3\u6838\u75C5\u5417\uFF1F</strong></p><p>\u4E0D\u4E00\u5B9A\u3002</p><p>\u7ED3\u6838\u6746\u83CC\u8FDB\u5165\u4EBA\u4F53\u540E\uFF0C\u5927\u90E8\u5206\u4F1A\u88AB\u514D\u75AB\u7CFB\u7EDF\u6740\u6B7B\u3002\u662F\u5426\u4F1A\u5F97\u7ED3\u6838\u75C5\u53D6\u51B3\u4E8E\u4EBA\u4F53\u514D\u75AB\u529B\u4E0E\u7ED3\u6838\u6746\u83CC\u6BD2\u529B\u7684\u5173\u7CFB\uFF1A</p><ul><li><p>\u5982\u679C\u4EBA\u4F53\u514D\u75AB\u529B\u5F3A\uFF0C\u7ED3\u6838\u6746\u83CC\u6BD2\u529B\u5F31\uFF0C\u7ED3\u6838\u6746\u83CC\u5C31\u4F1A\u88AB\u6D88\u706D\uFF0C\u4E00\u822C\u4E0D\u4F1A\u5F15\u8D77\u75C5\u53D8\u548C\u76F8\u5173\u75C7\u72B6\uFF1B</p></li><li><p>\u5982\u679C\u4EBA\u4F53\u514D\u75AB\u529B\u5F31\uFF0C\u7ED3\u6838\u6746\u83CC\u6BD2\u529B\u5F3A\uFF0C\u7ED3\u6838\u6746\u83CC\u5C31\u4F1A\u5927\u91CF\u7E41\u6B96\uFF0C\u4EBA\u4F53\u9010\u6E10\u51FA\u73B0\u7ED3\u6838\u75C5\u7076\u548C\u75C7\u72B6\u3002</p></li><li><p>\u5982\u679C\u4EBA\u4F53\u514D\u75AB\u529B\u548C\u7ED3\u6838\u6746\u83CC\u6BD2\u529B\u5DEE\u4E0D\u591A\uFF0C\u7ED3\u6838\u6746\u83CC\u5C31\u4F1A\u5904\u4E8E\u201C\u4F11\u7720\u201D\u72B6\u6001\u3002\u4E00\u65E6\u6761\u4EF6\u5408\u9002\uFF08\u5982\u4EBA\u4F53\u514D\u75AB\u529B\u7A81\u7136\u964D\u4F4E\uFF09\uFF0C\u4FBF\u4F1A\u82CF\u9192\u5E76\u7E41\u6B96\uFF0C\u5BFC\u81F4\u7ED3\u6838\u75C5\u3002</p></li></ul><p>\u5168\u4E16\u754C\u7EA6\u6709\u8D85\u8FC725%\u7684\u4EBA\u611F\u67D3\u8FC7\u7ED3\u6838\u6746\u83CC\uFF0C\u5176\u4E2D5%-10%\u7684\u4EBA\u4F1A\u53D1\u5C55\u6210\u7ED3\u6838\u75C5\u3002\u8001\u5E74\u4EBA\u3001\u5E7C\u513F\u3001\u9752\u5C11\u5E74\u3001\u6162\u6027\u75C5\u4EBA\u3001\u5B55\u4EA7\u5987\u3001\u827E\u6ECB\u75C5\u60A3\u8005\u7B49\u5C5E\u4E8E\u9AD8\u5371\u4EBA\u7FA4\uFF0C\u5BF9\u4E8E\u6709\u60A3\u7ED3\u6838\u75C5\u6210\u5E74\u4EBA\u7684\u5BB6\u5EAD\uFF0C\u5C24\u5176\u8981\u6CE8\u610F\u3002</p><p><strong>\u5982\u4F55\u9884\u9632\u7ED3\u6838\u75C5\uFF1F</strong></p><p>\u5927\u5BB6\u7EC8\u751F\u6234\u53E3\u7F69\u663E\u7136\u4E0D\u592A\u73B0\u5B9E\uFF0C\u90A3\u4E48\uFF0C\u6211\u4EEC\u5E94\u8BE5\u5982\u4F55\u6B63\u786E\u9884\u9632\u7ED3\u6838\u75C5\u5462\uFF1F</p><p><strong>\u513F\u7AE5\u63A5\u79CD\u201C\u5361\u4ECB\u82D7\u201D</strong></p><p>\u4E0E\u65B0\u578B\u51A0\u72B6\u75C5\u6BD2\u4E0D\u540C\uFF0C\u7531\u4E8E\u7ED3\u6838\u75C5\u5386\u53F2\u60A0\u4E45\uFF0C\u76EE\u524D\u5DF2\u6709\u76F8\u5E94\u6210\u719F\u6709\u6548\u7684\u75AB\u82D7\uFF0C\u53EF\u4EE5\u63D0\u524D\u4EA7\u751F\u514D\u75AB\u4ECE\u800C\u5728\u67D0\u79CD\u7A0B\u5EA6\u4E0A\u9884\u9632\u7ED3\u6838\u75C5\uFF0C\u5361\u4ECB\u82D7\u5C31\u662F\u8FD9\u6837\u4E00\u4E2A\u75AB\u82D7\u3002</p><p>\u65B0\u751F\u513F\u4E00\u51FA\u751F\u540E\u5C31\u5E94\u8BE5\u63A5\u79CD\u5361\u4ECB\u82D7\u3002\u63A5\u79CD\u540E\u53EF\u4EE5\u4E00\u5B9A\u7A0B\u5EA6\u4E0A\u9884\u9632\u7ED3\u6838\u75C5\uFF0C\u7279\u522B\u662F\u90A3\u4E9B\u4E25\u91CD\u7C7B\u578B\uFF0C\u5982\u7ED3\u6838\u6027\u8111\u819C\u708E\u7B49\u3002</p><p>\u5982\u679C\u51FA\u751F\u540E\u6CA1\u80FD\u53CA\u65F6\u63A5\u79CD\uFF0C\u5E94\u5C3D\u5FEB\u5230\u5F53\u5730\u7ED3\u6838\u75C5\u7EDF\u6CBB\u6240\u5361\u4ECB\u82D7\u95E8\u8BCA\u6216\u8005\u536B\u751F\u9632\u75AB\u7AD9\u8BA1\u5212\u514D\u75AB\u95E8\u8BCA\u8FDB\u884C\u8865\u79CD\u3002</p><p><strong>\u7279\u6B8A\u573A\u5408\u9700\u4F69\u6234\u53E3\u7F69</strong></p><p>\u5047\u5982\u9700\u8981\u53BB\u7ED3\u6838\u75C5\u533B\u9662\u548C\u4F20\u67D3\u75C5\u533B\u9662\u547C\u5438\u79D1\u7B49\u53EF\u80FD\u6709\u7ED3\u6838\u75C5\u4EBA\u805A\u96C6\u573A\u6240\u65F6\uFF0C\u5219\u5EFA\u8BAE\u4F69\u6234\u53E3\u7F69\uFF0C\u800C\u4E14\u662F\u5EFA\u8BAE\u4F69\u6234N95\u53E3\u7F69\u3002</p><p>\u56FE\u6E90\uFF1AWHO</p><p><strong>\u548C\u80BA\u7ED3\u6838\u60A3\u8005\u5171\u7528\u9910\u5177\u4F1A\u4F20\u67D3\u5417\uFF1F</strong></p><p>\u4E00\u822C\u60C5\u51B5\u4E0B\uFF0C\u5171\u7528\u9910\u5177\u4E0D\u4F1A\u4F20\u67D3\u7ED3\u6838\u75C5\uFF0C\u53EA\u4E0D\u8FC7\u8FD1\u8DDD\u79BB\u5171\u540C\u7528\u9910\uFF0C\u53EF\u80FD\u4F1A\u901A\u8FC7\u98DE\u6CAB\u548C\u7A7A\u6C14\u5438\u5165\u4E86\u5E26\u6709\u7ED3\u6838\u6746\u83CC\u7684\u98DE\u6CAB\uFF0C\u52A0\u4E0A\u62B5\u6297\u529B\u4E0D\u8DB3\u5BFC\u81F4\u88AB\u4F20\u67D3\uFF0C\u800C\u4E0D\u592A\u53EF\u80FD\u662F\u56E0\u4E3A\u5171\u7528\u9910\u5177\u800C\u88AB\u4F20\u67D3\u3002</p><p><strong>\u7ED3\u6838\u75C5\u90FD\u6709\u4F20\u67D3\u6027\u5417\uFF1F</strong></p><p>\u4E0D\u662F\u6240\u6709\u7684\u7ED3\u6838\u75C5\u60A3\u8005\u90FD\u5177\u5907\u4F20\u67D3\u6027\u3002\u901A\u5E38\uFF0C\u53EA\u6709\u80BA\u7ED3\u6838\u60A3\u8005\u624D\u6709\u4F20\u67D3\u6027\uFF0C\u800C\u80A0\u7ED3\u6838\u3001\u9AA8\u7ED3\u6838\u7B49\u60A3\u8005\uFF0C\u56E0\u4E3A\u75C5\u7076\u90E8\u4F4D\u4E0D\u4E0E\u5916\u754C\u76F8\u901A\uFF0C\u7ED3\u6838\u6746\u83CC\u8DD1\u4E0D\u51FA\u6765\uFF0C\u6240\u4EE5\u4E00\u822C\u60C5\u51B5\u4E0B\uFF0C\u6CA1\u6709\u4F20\u67D3\u6027\u3002</p><p>\u5982\u679C\u80BA\u7ED3\u6838\u60A3\u8005\u7ECF\u5E38\u4F34\u6709\u54B3\u55FD\u3001\u54B3\u75F0\u3001\u6253\u55B7\u568F\u7B49\u75C7\u72B6\uFF0C\u5E94\u5C3D\u5FEB\u63A5\u53D7\u6B63\u89C4\u6CBB\u7597\uFF0C\u914D\u5408\u9694\u79BB\u3001\u4F4F\u9662\uFF0C\u540C\u65F6\u4F69\u6234\u53E3\u7F69\u3002\u5BF9\u4E8E\u5BC6\u5207\u63A5\u89E6\u8FC7\u80BA\u7ED3\u6838\u60A3\u8005\u7684\u4EBA\uFF0C\u5E94\u53CA\u65F6\u53BB\u533B\u9662\u7B5B\u67E5\u3002</p><p>\u9700\u6CE8\u610F\u7684\u662F\uFF1A\u6CBB\u7597\u521D\u671F\uFF0C\u75F0\u68C0\u7ED3\u679C\u4E3A\u9634\u6027\uFF0C\u5E76\u975E\u8868\u793A\u60A3\u8005\u6CA1\u6709\u643A\u5E26\u7ED3\u6838\u6746\u83CC\uFF0C\u53EF\u80FD\u662F\u56E0\u4E3A\u75F0\u6DB2\u6807\u672C\u4E2D\u7684\u7ED3\u6838\u6746\u83CC\u975E\u5E38\u5C11\uFF0C\u4F20\u67D3\u6027\u6781\u5C0F\u3002\u53E6\u5916\uFF0C\u6CA1\u6709\u4F20\u67D3\u6027\u3001\u53EF\u4EE5\u89E3\u9664\u9694\u79BB\u7684\u60A3\u8005\u4E5F\u4E0D\u4EE3\u8868\u75CA\u6108\u4E86\u3002\u80BA\u7ED3\u6838\u7684\u6CBB\u7597\u7597\u7A0B\u4E00\u822C\u81F3\u5C11\u89816\u4E2A\u6708\uFF0C\u6CBB\u7597\u5168\u7A0B\u53CA\u505C\u836F\u5747\u9700\u5728\u4E13\u4E1A\u533B\u751F\u7684\u6307\u5BFC\u4E0B\u8FDB\u884C\u3002</p><p><strong>\u5F53\u5BB6\u91CC\u6709\u4EBA\u5F97\u4E86\u80BA\u7ED3\u6838\uFF0C\u6211\u4EEC\u5E94\u8BE5\u600E\u4E48\u505A\uFF1F</strong></p><ol><li><p>\u4E0E\u65B0\u578B\u51A0\u72B6\u75C5\u6BD2\u7C7B\u4F3C\uFF0C\u75C5\u4EBA\u5E94\u8BE5\u5446\u5728\u5BB6\u91CC\uFF0C\u75F0\u68C0\u9633\u6027\u7684\u60A3\u8005\u6700\u597D\u80FD\u5355\u72EC\u5C45\u4F4F\uFF0C\u5BB6\u4EBA\u9700\u8981\u4E0E\u60A3\u8005\u9694\u79BB\uFF0C\u5C24\u5176\u662F\u5E7C\u513F\u548C\u514D\u75AB\u529F\u80FD\u4E0D\u5168\u7684\u4EBA\u3002</p></li><li><p>\u5C45\u4F4F\u623F\u95F4\u8981\u591A\u901A\u98CE\u6362\u6C14\uFF0C\u4EE5\u964D\u4F4E\u7A7A\u6C14\u4E2D\u7ED3\u6838\u6746\u83CC\u7684\u6D53\u5EA6\u3002</p></li><li><p>\u60A3\u8005\u7684\u8863\u7269\u3001\u88AB\u8925\u53EF\u4EE5\u7ECF\u5E38\u6D17\u6652\uFF0C\u6709\u6761\u4EF6\u7684\u8BDD\u53EF\u4EE5\u4F7F\u7528\u7D2B\u5916\u7EBF\u706F\u5BF9\u60A3\u8005\u7684\u8863\u7269\u3001\u4E66\u7C4D\u8FDB\u884C\u6D88\u6BD2 [2] \u3002</p></li><li><p>\u60A3\u8005\u4E0D\u8981\u968F\u5730\u5410\u75F0\uFF0C\u6709\u75F0\u7684\u8BDD\u53EF\u4EE5\u5148\u5410\u5230\u7EB8\u5DFE\u4E0A\uFF0C\u628A\u7528\u8FC7\u7684\u7EB8\u5DFE\u653E\u5165\u5BC6\u5C01\u7684\u5851\u6599\u888B\u4E2D\u4E22\u5F03 [3] \u3002</p></li><li><p>\u76F8\u6BD4\u65B0\u578B\u51A0\u72B6\u75C5\u6BD2\u76EE\u524D\u5C1A\u672A\u786E\u8BA4\u5B58\u5728\u7279\u6548\u836F\uFF0C\u80BA\u7ED3\u6838\u6709\u5BF9\u5E94\u7684\u836F\u7269\u6CBB\u7597\uFF0C\u56E0\u6B64\u76D1\u7763\u60A3\u8005\u6309\u65F6\u670D\u836F\uFF0C\u5B9A\u671F\u590D\u67E5\uFF0C\u76F8\u5BF9\u6CBB\u7597\u53EF\u63A7\u3002</p></li><li><p>\u5BB6\u5C5E\u8981\u5173\u5FC3\u60A3\u8005\uFF0C\u5E2E\u52A9\u4ED6\u653E\u4E0B\u601D\u60F3\u5305\u88B1\uFF0C\u79EF\u6781\u914D\u5408\u533B\u751F\u6CBB\u7597\uFF0C\u5C3D\u5FEB\u6062\u590D\u8EAB\u4F53\u5065\u5EB7\u3002</p></li></ol><p><strong>\u80BA\u7ED3\u6838\u60A3\u8005\u6CBB\u7597\u540E\u8FD8\u80FD\u53C2\u52A0\u5DE5\u4F5C\u5417\uFF1F</strong></p><p>\u7ED3\u6838\u75C5\u662F\u4E00\u79CD\u6162\u6027\u4F20\u67D3\u75C5\uFF0C\u60A3\u8005\u53EA\u8981\u575A\u6301\u6B63\u89C4\u5730\u3001\u6574\u4E2A\u7597\u7A0B\u7684\u6CBB\u7597\uFF0C\u5927\u591A\u6570\u90FD\u662F\u53EF\u4EE5\u6CBB\u6108\u7684\u3002</p><p>\u5BF9\u4E8E\u75C5\u60C5\u7A33\u5B9A\uFF0C\u6CA1\u6709\u4E86\u4F20\u67D3\u6027\uFF0C\u751A\u81F3\u5DF2\u7ECF\u6CBB\u6108\u7684\u60A3\u8005\uFF0C\u5BF9\u8EAB\u8FB9\u7684\u4EBA\u4E0D\u6784\u6210\u5A01\u80C1\uFF0C\u53EF\u91CD\u65B0\u6B63\u5E38\u53C2\u4E0E\u5230\u5DE5\u4F5C\u548C\u5B66\u4E60\u4E2D\u53BB\uFF0C\u6211\u4EEC\u4E0D\u5E94\u8BE5\u5BF9\u4ED6\u62B1\u6709\u6B67\u89C6\u6216\u6392\u65A5\u3002 \uFF08\u533B\u5B66\u6821\u5BF9\uFF1A \u611F\u67D3\u79D1\u96F7\u65FB\u533B\u751F\uFF09</p><p>\u53C2\u8003\u6587\u732E</p><p>1.http://www.nhc.gov.cn/jkj/s3578/201904/050427ff32704a5db64f4ae1f6d57c6c.shtml</p><p>2.\u4E2D\u534E\u4EBA\u6C11\u5171\u548C\u56FD\u536B\u751F\u884C\u4E1A\u6807\u51C6-\u80BA\u7ED3\u6838\u8BCA\u65AD\uFF08WS 288-2017\uFF09</p><p>3.www.nhs.uk/conditions/tuberculosis-tb/treatment/</p><hr><p>\u76F8\u5173\u9605\u8BFB\uFF1A</p>',41),f=t("\u54B3\u55FD\u62BD\u8840\u652F\u539F\u4F53\u9633\u6027\uFF0C\u5C31\u662F\u652F\u539F\u4F53\u611F\u67D3\u5417\uFF1F"),h=t("\u5B69\u5B50\u54B3\u55FD\u6709\u75F0\uFF0C\u9700\u8981\u5403\u6C90\u8212\u5766\u5417\uFF1F"),g={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651632889&idx=1&sn=aadd0bb5bd01f22c0c451455152b6ce8&chksm=8b5c9d03bc2b14150c2f9feb93cdbf9b361ab10f326da74611b39741268fd1cd895844bcc9e0&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},m=t("\u67E5\u770B\u539F\u6587");function u(x,k){const n=o("RouterLink"),r=o("ExternalLinkIcon");return c(),a(i,null,[b,p("p",null,[e(n,{to:"/s/__biz=MzA5NzE5ODIyOQ==&mid=2651632180&idx=3&sn=3984beaeb182401a20eb5bb04bd0ec2b/"},{default:s(()=>[f]),_:1})]),p("p",null,[e(n,{to:"/s/__biz=MzA5NzE5ODIyOQ==&mid=2651631824&idx=2&sn=427b6dfaa171c87c445056205d3ccc8f/"},{default:s(()=>[h]),_:1})]),p("p",null,[p("a",g,[m,e(r)])])],64)}var N=d(_,[["render",u]]);export{N as default};
