import{r as t,o as s,c as i,a as p,b as n,w as c,F as a,e,d as l}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const _={},g=p("h3",{id:"\u6BCD\u4E73\u5582\u517B-\u4F60\u60F3\u77E5\u9053\u7684\u95EE\u9898\u90FD\u5728\u8FD9\u91CC",tabindex:"-1"},[p("a",{class:"header-anchor",href:"#\u6BCD\u4E73\u5582\u517B-\u4F60\u60F3\u77E5\u9053\u7684\u95EE\u9898\u90FD\u5728\u8FD9\u91CC","aria-hidden":"true"},"#"),e(" \u6BCD\u4E73\u5582\u517B\uFF0C\u4F60\u60F3\u77E5\u9053\u7684\u95EE\u9898\u90FD\u5728\u8FD9\u91CC")],-1),f=e("\u4E4B\u524D\u6211\u4EEC\u63A8\u9001\u8FC7\u6BD4\u5229\u65F6\u513F\u79D1\u533B\u751F\u90A3\u5510\u5143\u7684\u4E00\u7BC7"),h=e("\u300A\u4ED6\u5403\u4EC0\u4E48\u5976\u7C89\uFF1F\u300B"),b=e("\u7684\u6587\u7AE0\uFF0C\u5F15\u53D1\u4E86\u5F88\u591A\u5173\u4E8E\u6BCD\u4E73\u5582\u517B\u7684\u63D0\u95EE\uFF0C\u6240\u4EE5\u6211\u4EEC\u5411\u5F20\u5F81\u533B\u751F\u7EA6\u4E86\u8FD9\u7BC7\u7A3F\u5B50\u6765\u56DE\u7B54\u5927\u5BB6\u4E0B\u9762\u8FD9\u4E9B\u5927\u5BB6\u666E\u904D\u5173\u6CE8\u7684\u95EE\u9898\u3002"),m=l("<p><strong>\u4E00\u3001\u6BCD\u4E73\u5582\u517B\u5BF9\u5B69\u5B50\u6709\u4EC0\u4E48\u597D\u5904\uFF1F</strong></p><p>\u6BCD\u4E73\u5BF9\u4E8E\u5B69\u5B50\u7684\u597D\u5904\u5DF2\u7ECF\u662F\u4E2A\u8001\u751F\u5E38\u8C08\u7684\u8BDD\u9898\u4E86\uFF0C\u6211\u4EEC\u53EF\u4EE5\u4ECE\u4E24\u4E2A\u89D2\u5EA6\u6765\u770B\u5F85\u8FD9\u4E2A\u95EE\u9898\u3002</p><p>\u7B2C\u4E00\uFF0C\u6BCD\u4E73\u53EF\u4EE5\u6EE1\u8DB3\u5B69\u5B50\u751F\u957F\u53D1\u80B2\u7684\u751F\u7406\u9700\u6C42\u3002\u4ECE\u8425\u517B\u6210\u5206\u6765\u770B\uFF0C\u6BCD\u4E73\u5B8C\u7F8E\u5339\u914D\u5B9D\u5B9D\u751F\u957F\u53D1\u80B2\u7684\u9700\u6C42\u3002\u4E0E\u914D\u65B9\u5976\u7C89\u76F8\u6BD4\uFF0C\u6BCD\u4E73\u7684\u8425\u517B\u6BD4\u4F8B\u66F4\u5229\u4E8E\u5E7C\u513F\u7684\u6D88\u5316\u5438\u6536\u4ECE\u800C\u66F4\u597D\u7684\u6EE1\u8DB3\u751F\u957F\u9700\u6C42\u3002\u53E6\u5916\uFF0C\u6BCD\u4E73\u4E2D\u6240\u542B\u7684\u514D\u75AB\u56E0\u5B50\u662F\u914D\u65B9\u5976\u7C89\u65E0\u53EF\u6BD4\u62DF\u7684\uFF0C\u540C\u65F6\uFF0C\u6BCD\u4E73\u7684\u8425\u517B\u548C\u514D\u75AB\u6210\u5206\u8FD8\u4F1A\u968F\u7740\u5B69\u5B50\u751F\u957F\u53D1\u80B2\u800C\u81EA\u52A8\u8C03\u8282\u6765\u66F4\u597D\u7684\u6EE1\u8DB3\u5B69\u5B50\u7684\u9700\u6C42\u3002</p><p>\u7B2C\u4E8C\uFF0C\u6211\u4EEC\u4ECE\u5B9D\u5B9D\u7684\u5FC3\u7406\u53D1\u80B2\u89D2\u5EA6\u6765\u770B\u3002\u542E\u5438\u548C\u54FA\u4E73\u662F\u54FA\u4E73\u7C7B\u52A8\u7269\u6700\u5929\u7136\u6700\u672C\u80FD\u7684\u5929\u6027\u3002\u6BCD\u4E73\u5582\u517B\u8FC7\u7A0B\uFF0C\u5988\u5988\u7ED9\u4E88\u5B69\u5B50\u7684\u4E0D\u4EC5\u4EC5\u662F\u98DF\u7269\uFF0C\u540C\u65F6\u8FD8\u53EF\u4EE5\u901A\u8FC7\u808C\u80A4\u63A5\u89E6\u901A\u5E2E\u52A9\u5B69\u5B50\u548C\u5988\u5988\u5EFA\u7ACB\u4EB2\u5BC6\u7684\u4EB2\u5B50\u4F9D\u604B\u5173\u7CFB\u3002</p><p><strong>\u4E8C\u3001\u6BCD\u4E73\u5582\u517B\u5BF9\u5988\u5988\u6709\u4EC0\u4E48\u597D\u5904\uFF1F</strong></p><p>\u6BCD\u4E73\u5582\u517B\u5BF9\u4E8E\u5988\u5988\u7684\u77ED\u671F\u548C\u957F\u671F\u5065\u5EB7\u90FD\u662F\u6709\u975E\u5E38\u660E\u663E\u7684\u76CA\u5904\u7684,\u5582\u517B\u5BF9\u5988\u5988\u7684\u597D\u5904\u5176\u5B9E\u4E5F\u53EF\u4EE5\u4ECE\u751F\u7406\u548C\u5FC3\u7406\u5065\u5EB7\u4E24\u4E2A\u89D2\u5EA6\u6765\u8C08\u3002</p><p>\u4ECE\u4EA7\u540E\u6062\u590D\u6765\u770B\uFF0C\u6BCD\u4E73\u5582\u517B\u4F7F\u5F97\u5988\u5988\u4EA7\u540E\u5B50\u5BAB\u8FC5\u901F\u5F00\u59CB\u6536\u7F29\uFF0C\u51FA\u8840\u51CF\u5C11\u4E5F\u5C31\u51CF\u5C11\u4E86\u4EA7\u8925\u671F\u611F\u67D3\u98CE\u9669\u3002\u6BCD\u4E73\u5582\u517B\u4F7F\u5F97\u6211\u4EEC\u6BCF\u5929\u591A\u6D88\u8017500\u5361\u8DEF\u91CC\u7684\u70ED\u91CF\u76F8\u5F53\u4E8E\u6E38\u6CF3\u4E00\u5C0F\u65F6\uFF0C\u6240\u4EE5\u8FD8\u6709\u5229\u4E8E\u5988\u5988\u4EEC\u4EA7\u540E\u4F53\u578B\u6062\u590D\u3002\u8EBA\u7740\u5C31\u628A\u80A5\u7ED9\u51CF\u4E86\uFF0C\u5988\u5988\u4EEC\u4F55\u4E50\u800C\u4E0D\u4E3A\uFF1F</p><p>\u540C\u65F6\u7EAF\u6BCD\u4E73\u5582\u517B\u5728\u7279\u5B9A\u6761\u4EF6\u4E0B\u8FD8\u53EF\u4EE5\u662F\u4E00\u79CD\u6709\u6548\u7684\u907F\u5B55\u65B9\u5F0F\uFF0C\u4F46\u9700\u8981\u6EE1\u8DB3\u4E09\u4E2A\u6761\u4EF6</p><ul><li><p>\u5B69\u5B50\u5C0F\u4E8E6\u4E2A\u6708</p></li><li><p>\u5988\u5988\u4ECD\u672A\u6765\u4F8B\u5047</p></li><li><p>\u5988\u5988\u4ECD\u4E25\u683C\u9075\u7167\u6309\u9700\u7EAF\u6BCD\u4E73\u5582\u517B\uFF0C\u5373\u767D\u5929\u548C\u591C\u95F4\u90FD\u6709\u54FA\u4E73</p></li></ul><p>\u4ECE\u957F\u671F\u6765\u770B\uFF0C\u54FA\u4E73\u8FD8\u53EF\u4EE5\u5E2E\u52A9\u964D\u4F4E\u5988\u5988\u5728\u5C06\u6765\u7F79\u60A3\u5FC3\u810F\u75C5\u6216\u8005\u7CD6\u5C3F\u75C5\u7684\u98CE\u9669\uFF0C\u53E6\u5916\uFF0C\u4E73\u817A\u764C\u548C\u67D0\u4E9B\u5987\u79D1\u80BF\u7624\u7684\u98CE\u9669\u4E5F\u4F1A\u56E0\u6BCD\u4E73\u5582\u517B\u800C\u6709\u6240\u964D\u4F4E\u3002</p><p>\u4ECE\u5FC3\u7406\u65B9\u9762\u6765\u8BB2\uFF0C\u6BCD\u4E73\u5582\u517B\u4E3A\u5988\u5988\u548C\u5B9D\u5B9D\u4EB2\u5BC6\u63A5\u89E6\u521B\u9020\u4E86\u6761\u4EF6\uFF0C\u540C\u65F6\u6EE1\u8DB3\u4E86\u5B69\u5B50\u548C\u5988\u5988\u7684\u5FC3\u7406\u9700\u6C42\u3002</p><p><strong>\u4E09\u3001\u600E\u6837\u505A\u624D\u80FD\u589E\u52A0\u6BCD\u4E73\u5582\u517B\u6210\u529F\u673A\u4F1A\uFF1F</strong></p><p>\u54FA\u4E73\u662F\u4EF6\u672C\u80FD\u7684\u4E8B\u60C5\uFF0C\u7EDD\u5927\u6570\u5988\u5988\u4E5F\u53EF\u4EE5\u987A\u5229\u5B9E\u73B0\u6BCD\u4E73\u5582\u517B\uFF0C\u4F46\u6210\u529F\u4E0E\u5426\u5374\u662F\u4EF6\u9700\u8981\u5B66\u4E60\u7684\u6280\u672F\u6D3B\u3002</p><p>\u5BF9\u6BCD\u4E73\u5582\u517B\u800C\u8A00\uFF0C\u597D\u7684\u5F00\u59CB\u5C31\u4EE3\u8868\u4E86\u6210\u529F\u4E86\u4E00\u534A\uFF0C\u6240\u4EE5\u4EA7\u540E72\u5C0F\u65F6\u5185\u7684\u54FA\u4E73\u662F\u6574\u4E2A\u6BCD\u4E73\u5582\u517B\u6210\u529F\u7684\u5173\u952E\u6240\u5728\uFF0C\u6211\u4EEC\u63D0\u5021\u5728\u5B9D\u5B9D\u51FA\u751F\u540E\u5C3D\u65E9\u5F00\u59CB\u8DDF\u5988\u5988\u7684\u808C\u80A4\u63A5\u89E6\u3002</p><p>\u7814\u7A76\u8868\u660E\u4EA7\u540E1\u5C0F\u65F6\u5185\u8FDB\u884C\u808C\u80A4\u63A5\u89E6\u80FD\u63D0\u9AD8\u4E00\u534A\u4EE5\u4E0A\u7684\u6BCD\u4E73\u5582\u517B\u6210\u529F\u7387\u3002\u73B0\u5728\u5F88\u591A\u7231\u5A74\u533B\u9662\u7684\u505A\u6CD5\u662F\u4E00\u65E6\u786E\u8BA4\u5B69\u5B50\u72B6\u6001\u7A33\u5B9A\u540E\u5C3D\u65E9\u653E\u5728\u5988\u5988\u8EAB\u4F53\u4E0A\u53D1\u6325\u5B9D\u5B9D\u7684\u5BFB\u4E73\u672C\u80FD\u3002\u540C\u65F6\u8981\u8BA9\u5B9D\u5B9D\u53CA\u65E9\u5438\u542E\u4E73\u5934\uFF0C\u5F00\u59CB\u768472\u5C0F\u65F6\u5185\u5B9D\u5B9D\u7684\u5438\u542E\u662F\u5988\u5988\u4E73\u623F\u5F00\u59CB\u4EA7\u5976\u7684\u6700\u4F73\u523A\u6FC0\u4FE1\u53F7\u3002\u867D\u7136\u8FD9\u4E2A\u65F6\u5019\u521D\u4E73\u7684\u91CF\u975E\u5E38\u5C11\uFF0C\u4F46\u8DB3\u591F\u6EE1\u8DB3\u65B0\u751F\u513F\u7684\u751F\u957F\u9700\u6C42\u3002\u5C11\u91CF\u591A\u9910\u662F\u4EA7\u540E72\u5C0F\u65F6\u7684\u54FA\u4E73\u539F\u5219\u3002</p><p>\u6700\u540E\u8FD8\u8981\u63D0\u9192\u5927\u5BB6\u7684\u662F\u8981\u6CE8\u610F\u5B9D\u5B9D\u7684\u542B\u4E73\u59FF\u52BF\uFF0C\u8FD9\u662F\u8D2F\u7A7F\u6574\u4E2A\u54FA\u4E73\u8FC7\u7A0B\u7684\u3002\u54FA\u4E73\u59FF\u52BF\u6B63\u786E\u624D\u80FD\u4FDD\u8BC1\u5B9D\u5B9D\u80FD\u559D\u5230\u8DB3\u591F\u7684\u4E73\u6C41\uFF0C\u540C\u65F6\u53C8\u907F\u514D\u5988\u5988\u4E73\u5934\u53D7\u4F24\u5F15\u53D1\u4E73\u817A\u708E\u3002</p><p><strong>\u56DB\u3001\u6709\u4E0D\u80FD\u5582\u6BCD\u4E73\u7684\u60C5\u51B5\u5417\uFF1F</strong></p><p>\u603B\u4F53\u6765\u8BF4\u5065\u5EB7\u7684\u5988\u5988\u7EDD\u5927\u591A\u6570\u60C5\u51B5\u4E0B\u90FD\u662F\u53EF\u4EE5\u987A\u5229\u5B8C\u6210\u6BCD\u4E73\u5582\u517B\u7684\uFF0C\u9664\u975E\u51FA\u73B0\u4EE5\u4E0B\u60C5\u51B5\uFF1A</p><ul><li><p>\u75C5\u6BD2\u611F\u67D3\u4E2D\u7684HIV\u548CHTLV-1(\u4E0D\u8FC7WHO\u5BF9\u5728\u975E\u6D32\u7B49\u56FD\u5BB6\uFF0C\u63A8\u8350HIV\u611F\u67D3\u7684\u5988\u5988\u5728\u67D0\u4E9B\u60C5\u51B5\u4E0B\u4E5F\u53EF\u4EE5\u7EAF\u6BCD\u4E73\u5582\u517B\u52306\u4E2A\u6708)</p></li><li><p>\u5DE8\u7EC6\u80DE\u75C5\u6BD2\u611F\u67D3\u5988\u5988\u7684\u65E9\u4EA7\u513F\u6216\u6709\u75BE\u75C5\u7684\u5B9D\u5B9D</p></li><li><p>\u75B1\u75B9\u75C5\u6BD2\u3001\u6C34\u75D8\u75C5\u6BD2\u53D1\u751F\u5728\u4E73\u5934\u4E73\u6655\u5904</p></li><li><p>\u6025\u6027\u672A\u6CBB\u7597\u7684\u7ED3\u6838\u75C5</p></li><li><p>\u6BD2\u54C1\u6EE5\u7528\u6216\u4E25\u91CD\u7CBE\u795E\u75BE\u75C5\u4F1A\u4F24\u5BB3\u513F\u7AE5\u7684\u5988\u5988\u4E0D\u80FD\u72EC\u81EA\u7167\u770B\u5A74\u513F</p></li><li><p>\u5988\u5988\u63A5\u53D7\u8BCA\u65AD\u6027\u6216\u8005\u6CBB\u7597\u6027\u7684\u653E\u5C04\u6027\u540C\u4F4D\u7D20\u65F6\u6216\u8005\u66B4\u9732\u5728\u6709\u653E\u5C04\u6D3B\u6027\u7684\u7269\u8D28\u65F6\uFF0C\u53EA\u8981\u5979\u7684\u4E73\u6C41\u91CC\u6709\u653E\u5C04\u6D3B\u6027\u7684\u7269\u8D28\uFF0C\u9700\u8981\u6682\u505C\u6BCD\u4E73\u5582\u517B\u3002</p></li><li><p>\u5988\u5988\u63A5\u53D7\u6297\u4EE3\u8C22\u6216\u8005\u5316\u7597\u836F\u7269\u7684\u65F6\u5019\u9700\u8981\u6682\u505C\u6BCD\u4E73\u5582\u517B\u3002</p></li><li><p>\u6240\u6709\u7684\u75AB\u82D7\u91CC\uFF0C\u54FA\u4E73\u671F\u7684\u5988\u5988\u552F\u4E00\u4E0D\u80FD\u63A5\u79CD\u7684\u662F\u5929\u82B1\u75AB\u82D7\u3002\u56E0\u6B64\u6BCD\u4E73\u5988\u5988\u6CA1\u6709\u5FC5\u8981\u7684\u60C5\u51B5\u4E0B\uFF0C\u4E0D\u8981\u63A5\u79CD\u5929\u82B1\u75AB\u82D7\u3002</p></li></ul><p>\u5988\u5988\u60A3\u4E73\u817A\u708E\u53EF\u4EE5\u7EE7\u7EED\u6BCD\u4E73\u5582\u517B\uFF0C\u5982\u679C\u4F7F\u7528\u6297\u751F\u7D20\uFF0C\u4E5F\u5B8C\u5168\u53EF\u4EE5\u4F7F\u7528\u54FA\u4E73\u5988\u5988\u5B89\u5168\u6297\u751F\u7D20\u3002\u53EA\u6709\u5988\u5988\u7684\u4E73\u817A\u708E\u662F\u7531\u8010\u836F\u91D1\u9EC4\u8272\u8461\u8404\u7403\u83CC\uFF08MRSA\uFF09\u5F15\u8D77\u7684\uFF0C\u5E76\u4E14\u5979\u7684\u5B9D\u5B9D\u662F\u65E9\u4EA7\u7684\u65B0\u751F\u513F\u6216\u8005\u5A74\u513F\u60A3\u6709\u75BE\u75C5\u7684\u60C5\u51B5\u4E0B\uFF0C\u9700\u8981\u5C06\u4E73\u6C41\u6324\u51FA\u5E76\u4E22\u5F03\u76F4\u5230\u611F\u67D3\u6CBB\u6108\u3002</p><p>\u5988\u5988\u60A3\u6709\u5404\u79CD\u6162\u6027\u75BE\u75C5\u7684\uFF0C\u6BD4\u5982\u6291\u90C1\uFF0C\u7CD6\u5C3F\u75C5\uFF08\uFF29\u578B\u6216\u2161\u578B\uFF09\uFF0C\u5FC3\u810F\u75C5\uFF0C\u6162\u6027\u9AD8\u8840\u538B\uFF0C\u80F0\u817A\u7684\u56CA\u6027\u7EA4\u7EF4\u5316\uFF0C\u591A\u53D1\u6027\u786C\u5316\uFF0C\u766B\u75EB\uFF0C\u7532\u4EA2\u6216\u7532\u4F4E\uFF0C\u7EA2\u6591\u72FC\u75AE\uFF0C\u98CE\u6E7F\u6027\u5173\u8282\u708E\uFF0C\u5728\u5BC6\u5207\u7684\u533B\u7597\u5173\u6CE8\u4E0B\u90FD\u53EF\u4EE5\u6BCD\u4E73\u5582\u517B\u3002\u5373\u4F7F\u4E0D\u80FD\u7EAF\u6BCD\u4E73\u5582\u517B\uFF0C\u90E8\u5206\u7684\u6BCD\u4E73\u5582\u517B\u4E5F\u80FD\u7ED9\u6BCD\u5A74\u5E26\u6765\u76CA\u5904\u3002</p><p><strong>\u4E94\u3001\u4F8B\u5047\u6765\u4E86\u8FD8\u53EF\u4EE5\u7EE7\u7EED\u6BCD\u4E73\u5417\uFF1F</strong></p><p>\u5F88\u591A\u6C11\u95F4\u4F20\u8A00\u8BF4\u5988\u5988\u6765\u4E86\u4F8B\u5047\u5C31\u4E0D\u80FD\u5582\u5976\uFF0C\u5976\u6C34\u4F1A\u53D8\u5F97\u6709\u6BD2\u6216\u8005\u6CA1\u6709\u8425\u517B\uFF0C\u8FD9\u65F6\u5019\u5C31\u4E0D\u80FD\u518D\u7ED9\u5B9D\u5B9D\u54FA\u4E73\u4E86\uFF0C\u8FD9\u4E9B\u8BF4\u6CD5\u662F\u6CA1\u6839\u636E\u7684\uFF0C\u4E5F\u662F\u8352\u8C2C\u7684\u3002</p><p>\u4E73\u6C41\u7684\u6210\u5206\u662F\u4F34\u968F\u5B9D\u5B9D\u7684\u751F\u957F\u53D1\u80B2\u800C\u5728\u4E0D\u65AD\u53D8\u5316\uFF0C\u6708\u7ECF\u6765\u6F6E\u662F\u4F53\u5185\u96CC\u5B55\u6FC0\u7D20\u6C34\u5E73\u53D8\u5316\u9020\u6210\u7684\uFF0C\u4E0E\u4E73\u6C41\u7684\u8425\u517B\u6210\u5206\u6BEB\u65E0\u5173\u7CFB\u3002\u6708\u7ECF\u5BF9\u6BCD\u4E73\u7684\u5F71\u54CD\u5728\u4E00\u90E8\u5206\u5988\u5988\u4E2D\u53EF\u80FD\u8868\u73B0\u4E3A\u4E73\u6C41\u51CF\u5C11\uFF0C\u4EE5\u53CA\u6709\u65F6\u4E73\u6C41\u7684\u5473\u9053\u4F1A\u6709\u4E9B\u6539\u53D8\uFF0C\u4F46\u662F\u8FD9\u4E9B\u6539\u53D8\u90FD\u662F\u6682\u65F6\u7684\u3002\u56E0\u6B64\uFF0C\u4F8B\u5047\u6765\u4E86\uFF0C\u5F53\u7136\u53EF\u4EE5\u7EE7\u7EED\u54FA\u4E73\u3002</p><p><strong>\u516D\u3001\u5F88\u591A\u5988\u5988\u8BD5\u8FC7\u5404\u79CD\u65B9\u6CD5\u5C31\u662F\u6CA1\u6709\u6BCD\u4E73\uFF0C\u662F\u65B9\u6CD5\u4E0D\u5BF9\u8FD8\u662F\u786E\u5B9E\u5B58\u5728\u6CA1\u6BCD\u4E73\u6216\u8005\u6BCD\u4E73\u4E0D\u8DB3\u7684\u60C5\u51B5\uFF1F</strong></p><p>\u5F88\u591A\u5988\u5988\u6240\u8C13\u5C1D\u8BD5\u7684\u65B9\u6CD5\u53EF\u80FD\u5927\u591A\u662F\u6C11\u95F4\u504F\u65B9\u672C\u8EAB\u5C31\u4E0D\u5B58\u5728\u5FAA\u8BC1\u533B\u5B66\u8BC1\u636E\uFF0C\u6839\u636E\u4E73\u6C41\u4EA7\u751F\u7684\u539F\u7406\u591A\u5582\u591A\u6709\uFF0C\u6700\u7B80\u5355\u800C\u6709\u6548\u7684\u65B9\u6CD5\u5C31\u662F\u591A\u808C\u80A4\u63A5\u89E6\uFF0C\u591A\u8BA9\u5B9D\u5B9D\u5438\u542E\uFF0C\u6BCF\u5929\u8D77\u78018-12\u6B21\uFF0C\u591C\u5976\u81F3\u5C11\u4E00\u6B21\uFF0C\u4E0D\u9700\u8981\u7ED9\u5B69\u5B50\u6DFB\u52A0\u5976\u7C89\u6216\u5176\u4ED6\u6DB2\u4F53\u3002\u8FD9\u6837\u6700\u7EC8\u4F1A\u5EFA\u7ACB\u5B9D\u5B9D\u7684\u9700\u6C42\u4E0E\u4E73\u6C41\u91CF\u7684\u4F9B\u9700\u5E73\u8861\u7684\u72B6\u6001\u3002\u53E6\u5916\u4E73\u6C41\u591A\u5C11\u4E0E\u4E73\u623F\u5927\u5C0F\u672C\u8EAB\u6CA1\u6709\u76F4\u63A5\u5173\u7CFB\uFF0C\u5C0F\u4E73\u623F\u901A\u8FC7\u591A\u4EB2\u5582\u540C\u6837\u80FD\u591F\u6EE1\u8DB3\u5B9D\u5B9D\u9700\u6C42\u7684\u3002</p><p><strong>\u4E03\u3001\u5976\u6C34\u5C11\u548C\u5988\u5988\u5403\u4E1C\u897F\u6709\u5173\u5417\uFF1F\u6709\u5E2E\u52A9\u4E0B\u5976\u7684\u98DF\u7269\u6216\u836F\u7269\u5417\uFF1F</strong></p><p>\u5976\u6C34\u7684\u591A\u5C11\u5176\u5B9E\u8DDF\u6211\u4EEC\u5EFA\u7ACB\u4E73\u6C41\u5E73\u8861\u7684\u539F\u7406\u6709\u5173\u3002\u5B9D\u5B9D\u5438\u542E\u8D8A\u591A\uFF0C\u523A\u6FC0\u5988\u5988\u7684\u4E2D\u67A2\u795E\u7ECF\u7CFB\u7EDF\u5206\u6CCC\u66F4\u591A\u6FC0\u7D20\u4FC3\u8FDB\u4E73\u6C41\u5408\u6210\u3002\u7B80\u800C\u8A00\u4E4B\uFF0C\u4E73\u6C41\u662F\u5B69\u5B50\u5403\u5F97\u8D8A\u591A\uFF0C\u4E73\u6C41\u4EA7\u751F\u4E5F\u8D8A\u591A\u3002\u9664\u975E\u5988\u5988\u5B58\u5728\u6781\u5EA6\u7684\u8425\u517B\u4E0D\u826F\uFF0C\u5BF9\u4E8E\u575A\u6301\u6B63\u5E38\u996E\u98DF\u7684\u5065\u5EB7\u5988\u5988\uFF0C\u5E76\u4E0D\u4F1A\u56E0\u4E3A\u5988\u5988\u591A\u5403\u4E00\u4E9B\u6216\u8005\u7A0D\u7A0D\u5C11\u5403\u4E00\u4E9B\uFF0C\u800C\u8BA9\u5976\u6C34\u53D8\u591A\u6216\u8005\u53D8\u5C11\u3002</p><p>\u4E34\u5E8A\u4E0A\uFF0C\u7684\u786E\u6709\u4E00\u4E9B\u836F\u7269\u53EF\u4EE5\u5E2E\u52A9\u589E\u52A0\u6CCC\u4E73\uFF0C\u4F46\u662F\u8FD9\u4E9B\u836F\u7269\u7684\u4F7F\u7528\u9700\u8981\u4E25\u683C\u9075\u533B\u5631\u3002</p><p><strong>\u516B\u3001 \u7EAF\u6BCD\u4E73\u5582\u517B6\u4E2A\u6708\uFF0C\u662F\u4E0D\u662F6\u4E2A\u6708\u4EE5\u540E\u53EF\u4EE5\u52A0\u5976\u7C89\uFF1F</strong></p><p>6\u4E2A\u6708\u540E\u5B69\u5B50\u7684\u80C3\u80A0\u9053\u53D1\u80B2\u53EF\u4EE5\u9010\u6B65\u63A5\u53D7\u5176\u4ED6\u56FA\u4F53\u98DF\u7269\uFF0C\u6839\u636E\u8F85\u98DF\u6DFB\u52A0\u539F\u5219\u9010\u6B65\u5EFA\u7ACB\u8D77\u8DDF\u6210\u4EBA\u66F4\u63A5\u8FD1\u7684\u996E\u98DF\u4E60\u60EF\uFF0C\u540C\u65F66\u4E2A\u6708\u540E\u4ECD\u7136\u5E94\u8BE5\u7EE7\u7EED\u6BCD\u4E73\u5582\u517B\uFF0C\u5E76\u6CA1\u6709\u6DFB\u52A0\u914D\u65B9\u5976\u7C89\u7684\u5FC5\u8981\u3002\u5F53\u7136\uFF0C\u5BF9\u4E8E\u90A3\u4E9B\u786E\u5B9E\u65E0\u6CD5\u6301\u7EED\u6BCD\u4E73\u5582\u517B\u5230\u5B9D\u5B9D\u6EE1\u5468\u5C81\u7684\u5988\u5988\u6765\u8BF4\uFF0C\u5C31\u9700\u8981\u4F7F\u7528\u914D\u65B9\u5976\u6765\u66FF\u4EE3\u6BCD\u4E73\u3002</p><p><strong>\u4E5D\u3001 \u5B58\u5728\u5230\u4E86\u4E00\u5B9A\u65F6\u5019\u6BCD\u4E73\u6CA1\u6709\u8425\u517B\u7684\u60C5\u51B5\u5417\uFF1F</strong></p><p>\u76F4\u63A5\u7684\u56DE\u7B54\u662F\uFF1A\u6CA1\u6709\u3002</p><p>\u6211\u4EEC\u6240\u8BF4\u7684\u6BCD\u4E73\u8425\u517B\u95EE\u9898\u4E0D\u5355\u6307\u6BCD\u4E73\u7684\u8425\u517B\u4EF7\u503C\uFF0C\u540C\u65F6\u8FD8\u5E94\u8BE5\u8003\u8651\u6BCD\u4E73\u4E2D\u6240\u542B\u7684\u6297\u611F\u67D3\u56E0\u5B50\u548C\u5404\u79CD\u514D\u75AB\u56E0\u5B50\u7684\u5065\u5EB7\u4EF7\u503C\u3002\u6BCD\u4E73\u4E2D\u542B\u6709\u5404\u7C7B\u514D\u75AB\u6D3B\u6027\u56E0\u5B50\uFF0C\u5E76\u4E0D\u4F1A\u968F\u7740\u5B69\u5B50\u957F\u5927\u800C\u5931\u53BB\u5065\u5EB7\u610F\u4E49\u3002\u53E6\u5916\uFF0C\u6CF5\u51FA\u6765\u50A8\u5B58\u7740\u7684\u6BCD\u4E73\uFF0C\u89E3\u51BB\u540E\u4E5F\u4ECD\u7136\u662F\u5B69\u5B50\u7406\u60F3\u7684\u98DF\u7269\u3002\u89E3\u51BB\u6BCD\u4E73\u7684\u65F6\u5019\uFF0C\u53EF\u80FD\u4F1A\u53D1\u73B0\u5206\u5C42\u73B0\u8C61\uFF0C\u8FD9\u662F\u6B63\u5E38\u7684\u73B0\u8C61\uFF0C\u6447\u5300\u540E\u7ED9\u5B9D\u5B9D\u996E\u7528\u5373\u53EF\u3002</p><p><strong>\u5341\u3001\u6BCD\u4E73\u5230\u5E95\u5582\u5230\u4EC0\u4E48\u65F6\u5019\u5408\u9002\uFF1F</strong></p><p>\u7B54\u6848\u662F\uFF1A\u201C\u53EA\u8981\u4F60\u548C\u5B9D\u5B9D\u613F\u610F\uFF0C\u60F3\u5582\u591A\u4E45\u5C31\u5582\u591A\u4E45\u201D\u3002</p><p>\u6BCD\u4E73\u5582\u517B\u4E0D\u4EC5\u4EC5\u4E3A\u5B69\u5B50\u63D0\u4F9B\u98DF\u7269\uFF0C\u540C\u65F6\u8FD8\u4FC3\u8FDB\u4EB2\u5B50\u4F9D\u604B\u5173\u7CFB\u3002\u6BCD\u4E73\u5582\u517B\u5E76\u6CA1\u6709\u4E00\u4E2A\u8BBE\u5B9A\u671F\u9650\uFF0C\u53EA\u662F\u6BCD\u4E73\u5582\u517B\u7684\u6301\u7EED\u65F6\u95F4\u4F1A\u53D7\u5230\u5F88\u591A\u56E0\u7D20\u7684\u5F71\u54CD\u3002</p><p>\u5728\u5B9D\u5B9D\u51FA\u751F\u540E\u7684\u524D6\u4E2A\u6708\u4E2D\uFF0C\u7EAF\u6BCD\u4E73\u5C31\u53EF\u4EE5\u6EE1\u8DB3\u5B69\u5B50\u7684\u6240\u6709\u8425\u517B\u9700\u6C42 \uFF1B\u968F\u7740\u5B9D\u5B9D\u7684\u957F\u5927\uFF0C\u8F85\u98DF\u7684\u9010\u6E10\u4ECB\u5165\uFF0C\u572812\u6708\u9F84\u524D\uFF0C\u6BCD\u4E73\u4ECD\u7136\u662F\u5B69\u5B50\u7684\u4E3B\u8981\u8425\u517B\u6765\u6E90\u3002</p><p>\u5468\u5C81\u540E\uFF0C\u5B69\u5B50\u4F1A\u9010\u6E10\u878D\u5165\u5BB6\u5EAD\u996E\u98DF\uFF0C\u6BCD\u4E73\u4E0D\u518D\u662F\u4E3B\u8981\u8425\u517B\u6765\u6E90\uFF0C\u4E0D\u8FC7\u5728\u5B69\u5B50\u548C\u5988\u5988\u90FD\u613F\u610F\u7684\u524D\u63D0\u4E0B\u53EF\u4EE5\u6BCD\u4E73\u5582\u517B\u5230\u4E24\u5468\u5C81\u6216\u8005\u66F4\u4E45\u3002</p><p>\u5988\u5988\u4F1A\u62C5\u5FC3\u8FC7\u957F\u7684\u6BCD\u4E73\u5582\u517B\u65F6\u95F4\u4F1A\u8BA9\u5B9D\u5B9D\u66F4\u9ECF\u4EBA\uFF0C\u66F4\u4F9D\u8D56\u5988\u5988\uFF0C\u4F46\u7814\u7A76\u7ED3\u679C\u8BC1\u660E\uFF0C\u5403\u5976\u65F6\u95F4\u77ED\u7684\u5B69\u5B50\u5176\u5B9E\u4F1A\u66F4\u7F3A\u4E4F\u5B89\u5168\u611F\uFF0C\u66F4\u4E0D\u72EC\u7ACB\u3002</p><p><strong>\u5341\u4E00\u3001\u65AD\u6BCD\u4E73\u4EE5\u540E\uFF0C\u8981\u4E0D\u8981\u5403\u5976\u7C89\uFF1F</strong></p><p>\u9996\u5148\u6211\u4EEC\u8981\u533A\u5206\u201C\u65AD\u5976\u201D\u548C\u201C\u79BB\u4E73\u201D\uFF0C\u6211\u4EEC\u5E73\u65F6\u7406\u89E3\u7684\u65AD\u5976\u5F80\u5F80\u662F\u6307\u975E\u6B63\u5E38\u60C5\u51B5\u4E0B\uFF08\u66F4\u591A\u662F\u5988\u5988\u4E3B\u5BFC\u5F0F\u7684\uFF09\u7A81\u7136\u505C\u6B62\u54FA\u4E73\u3002\u5982\u679C\u65AD\u5976\u53D1\u751F\u5728\u5B9D\u5B9D\u5468\u5C81\u524D\uFF0C\u90A3\u4E48\u4E3A\u4E86\u4FDD\u8BC1\u751F\u957F\u53D1\u80B2\u6240\u9700\uFF0C\u5219\u9700\u8981\u914D\u65B9\u5976\u7C89\u6765\u66FF\u4EE3\u6BCD\u4E73\u3002</p><p>\u800C\u5BF9\u4E8E\u6211\u4EEC\u7ECF\u5E38\u542C\u5230\u7684\u81EA\u7136\u79BB\u4E73\uFF0C\u5219\u662F\u6307\u5B9D\u5B9D\u4E3B\u5BFC\u4E0B\u5B8C\u6210\u7684\u4ECE\u7EAF\u6BCD\u4E73\uFF0C\u8FC7\u6E21\u5230\u6BCD\u4E73\u4E3A\u4E3B\u8F85\u98DF\u4E3A\u8F85\uFF0C\u518D\u5230\u5BB6\u5EAD\u996E\u98DF\u4E3A\u4E3B\u6BCD\u4E73\u4E3A\u8F85\uFF0C\u76F4\u5230\u6700\u540E\u5B69\u5B50\u8131\u79BB\u6BCD\u4E73\uFF0C\u56E0\u6B64\uFF0C\u8FD9\u671F\u95F4\u662F\u65E0\u9700\u989D\u5916\u6DFB\u52A0\u914D\u65B9\u5976\u7C89\u7684\u3002</p><p>\u53E6\u5916\uFF0C\u6211\u4EEC\u8FD8\u8981\u6CE8\u610F\u7684\u4E00\u4E2A\u9644\u52A0\u95EE\u9898\u662F\uFF0C\u6211\u4EEC\u5E94\u8BE5\u4ECE\u5C0F\u9F13\u52B1\u5B69\u5B50\u996E\u98DF\u7684\u5747\u8861\u548C\u591A\u6837\u5316\u3002\u5F53\u5B69\u5B50\u9010\u6E10\u51CF\u5C11\u5BF9\u6BCD\u4E73\u7684\u4F9D\u8D56\uFF0C\u90A3\u4E48\u6211\u4EEC\u5E94\u8BE5\u9F13\u52B1\u5B69\u5B50\u53CA\u65E9\u5EFA\u8BAE\u826F\u597D\u7684\u996E\u98DF\u4E60\u60EF\uFF0C\u800C\u4E0D\u662F\u4F9D\u8D56\u914D\u65B9\u5976\u7C89\u6765\u4F5C\u4E3A\u8425\u517B\u6765\u6E90\uFF0C\u8C01\u90FD\u4E0D\u5E0C\u671B\u770B\u5230\u5B69\u5B50\u53EA\u62B1\u7740\u5976\u74F6\u4E0D\u597D\u597D\u5403\u996D\u3002</p><p><strong>\u5341\u4E8C\u3001\u5BB6\u4EBA\u4E0D\u652F\u6301\u6BCD\u4E73\uFF0C\u6709\u4EC0\u4E48\u597D\u7684\u529E\u6CD5\uFF1F</strong></p><p>\u5BB6\u4EBA\u7684\u652F\u6301\u5BF9\u5988\u5988\u662F\u5426\u80FD\u591F\u987A\u5229\u6BCD\u4E73\u5582\u517B\u81F3\u5173\u91CD\u8981\u3002\u6211\u4EEC\u5728\u5B55\u671F\u7684\u65F6\u5019\u5C3D\u91CF\u9F13\u52B1\u5BB6\u4EBA\u4E00\u8D77\u53C2\u4E0E\u5B66\u4E60\u6BCD\u4E73\u5582\u517B\u7684\u76F8\u5173\u77E5\u8BC6\uFF0C\u8BA9\u4ED6\u4EEC\u660E\u767D\u6BCD\u4E73\u5582\u517B\u662F\u4E00\u4EF6\u81EA\u7136\u800C\u7136\u7684\u4E8B\uFF0C\u800C\u4E14\u5B83\u6709\u52A9\u4E8E\u5B9D\u5B9D\u548C\u5988\u5988\u7684\u5065\u5EB7\u3002</p><p>\u5176\u6B21\uFF0C\u5F88\u591A\u65F6\u5019\u5BB6\u4EBA\u662F\u56E0\u4E3A\u5FC3\u75BC\u5988\u5988\u6216\u8005\u62C5\u5FC3\u5B9D\u5B9D\u624D\u4F1A\u529D\u4F60\u653E\u5F03\u6BCD\u4E73\u5582\u517B\uFF0C\u6240\u4EE5\uFF0C\u9047\u5230\u95EE\u9898\u6211\u4EEC\u5E94\u8BE5\u591A\u591A\u4E0E\u5BB6\u4EBA\u6C9F\u901A\uFF0C\u8BA9\u4ED6\u4EEC\u660E\u767D\u53EA\u8981\u4E00\u8D77\u52AA\u529B\u5C31\u53EF\u4EE5\u89E3\u51B3\u6BCD\u4E73\u5582\u517B\u4E2D\u9047\u5230\u7684\u79CD\u79CD\u96BE\u9898\u3002</p><p>\u6700\u540E\uFF0C\u8BA9\u5BB6\u4EBA\u53C2\u4E0E\u5230\u6BCD\u4E73\u5582\u517B\u4E2D\u6765\uFF0C\u8BA9\u4ED6\u4EEC\u6709\u66F4\u591A\u7684\u53C2\u4E0E\u611F\uFF0C\u6BD4\u5982\u7ED9\u4ED6\u4EEC\u4E00\u4E2A\u53EF\u4EE5\u5E2E\u4F60\u505A\u7684\u4E8B\u60C5\u7684\u6E05\u5355\uFF0C\u53EF\u4EE5\u662F\u5E2E\u4F60\u505A\u996D\uFF0C\u5E26\u5B9D\u5B9D\u6563\u6B65\uFF0C\u7ED9\u5B9D\u5B9D\u6362\u5C3F\u5E03\uFF0C\u8DDF\u5B9D\u5B9D\u505A\u808C\u80A4\u63A5\u89E6\u966A\u4ED6\u6D17\u6FA1\u7B49\u7B49\u3002 \u8BA9\u5BB6\u4EBA\u53C2\u4E0E\u5230\u80B2\u513F\u4E2D\u6765\uFF0C\u4E0D\u4EC5\u5E2E\u52A9\u5988\u5988\u5206\u62C5\u4E00\u90E8\u5206\u5BB6\u52A1\uFF0C\u8FD8\u53EF\u4EE5\u8BA9\u54FA\u4E73\u5988\u5988\u6709\u66F4\u591A\u7CBE\u529B\u6765\u4E13\u6CE8\u6BCD\u4E73\u5582\u517B\u3002</p><hr><p>\u4F5C\u8005\u4ECB\u7ECD\uFF1A\u5F20\u5F81\uFF0C\u4E0A\u6D77\u5609\u4F1A\u56FD\u9645\u533B\u9662\u4E73\u817A\u5916\u79D1\u533B\u751F\uFF0C\u56FD\u9645\u8BA4\u8BC1\u6CCC\u4E73\u987E\u95EE\uFF0C\u5979\u7684\u516C\u4F17\u53F7\u4E3A\u201C\u82B1\u623F\u95FA\u871C\u201D\uFF08drjessicazhang\uFF09\u3002</p><p>\u957F\u6309\u8BC6\u522B\u4E0B\u9762\u4E8C\u7EF4\u7801\uFF0C\u53EF\u5411\u513F\u79D1\u533B\u751F\u54A8\u8BE2\u6BCD\u4E73\u53CA\u5176\u4ED6\u5582\u517B\u95EE\u9898\u3002</p>",51),u={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651626482&idx=1&sn=2383da4c56f2bdc9c21e7d21a3e5de8a&chksm=8b5cb208bc2b3b1e787f1ec02add1a0a09db8e7ab0f1e4bef7b5ed169e12fcfcdb40fff222c0&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},x=e("\u67E5\u770B\u539F\u6587");function k(z,V){const o=t("RouterLink"),r=t("ExternalLinkIcon");return s(),i(a,null,[g,p("p",null,[f,n(o,{to:"/s/__biz=MzA5NzE5ODIyOQ==&mid=2651626277&idx=1&sn=4c24bd6fd164593a06fb69c4c410b5ff&scene=21#wechat_redirect/"},{default:c(()=>[h]),_:1}),b]),m,p("p",null,[p("a",u,[x,n(r)])])],64)}var w=d(_,[["render",k]]);export{w as default};
