import{r,o as n,c as t,a as p,b as o,F as a,d as s,e as c}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const i={},_=s('<h3 id="\u5B9D\u5B9D\u773C\u5C4E\u591A-\u662F\u4E0A\u706B\u7684\u5417" tabindex="-1"><a class="header-anchor" href="#\u5B9D\u5B9D\u773C\u5C4E\u591A-\u662F\u4E0A\u706B\u7684\u5417" aria-hidden="true">#</a> \u5B9D\u5B9D\u773C\u5C4E\u591A\uFF0C\u662F\u4E0A\u706B\u7684\u5417\uFF1F</h3><p>\u5728\u773C\u79D1\u95E8\u8BCA\uFF0C\u7ECF\u5E38\u4F1A\u9047\u5230\u5E74\u8F7B\u7236\u6BCD\u7126\u6025\u5730\u5BF9\u533B\u751F\u8BF4\uFF1A\u201C\u6211\u7684\u5B9D\u5B9D\u624D\u51FA\u751F\u51E0\u661F\u671F\uFF0C\u5C31\u6709\u5F88\u591A\u773C\u5C4E\uFF0C\u8FD8\u7ECF\u5E38\u773C\u6CEA\u6C6A\u6C6A\uFF0C\u662F\u4E0D\u662F\u4E0A\u706B\u4E86\uFF1F\u201D</p><p>\u773C\u5C4E\u5176\u5B9E\u662F\u773C\u90E8\u5206\u6CCC\u7269\uFF0C\u6211\u4EEC\u5927\u90E8\u5206\u4EBA\u90FD\u6709\u773C\u5C4E\uFF0C\u65B0\u751F\u513F\u4E5F\u4E00\u6837\uFF0C\u6709\u773C\u5C4E\u5927\u591A\u662F\u6B63\u5E38\u7684\u3002\u5982\u679C\u773C\u5C4E\u7279\u522B\u591A\uFF0C\u4E5F\u5E76\u4E0D\u662F\u4E0A\u706B\uFF0C\u800C\u662F\u5B9D\u5B9D\u7684\u773C\u775B\u53EF\u80FD\u51FA\u4E86\u95EE\u9898\u3002</p><p><strong>\u4E00\u3001\u5B9D\u5B9D\u51FA\u751F\u540E\u773C\u5C4E\u591A\uFF0C\u5E38\u89C1\u7684\u539F\u56E0\u6709\u54EA\u4E9B\uFF1F</strong></p><p>1\u3001\u7ED3\u819C\u708E</p><p>\u773C\u775B\u50CF\u4E00\u4E2A\u5F00\u653E\u7684\u7A97\u53E3\uFF0C\u521A\u51FA\u751F\u7684\u5B9D\u5B9D\u514D\u75AB\u529F\u80FD\u4E0D\u5B8C\u5584\uFF0C\u5728\u751F\u4EA7\u65F6\u6216\u751F\u4EA7\u540E\u773C\u90E8\u62A4\u7406\u4E0D\u5F53\uFF0C\u75C5\u6BD2\u3001\u7EC6\u83CC\u8FDB\u5165\u773C\u775B\uFF0C\u5C31\u6709\u53EF\u80FD\u4F1A\u5F15\u8D77\u7ED3\u819C\u53D1\u708E\uFF0C\u6216\u8005\u5B9D\u5B9D\u611F\u5192\u4E86\uFF0C\u90FD\u53EF\u4EE5\u5BFC\u81F4\u5A74\u513F\u51FA\u73B0\u773C\u5206\u6CCC\u7269\u589E\u591A\uFF0C\u773C\u7751\u7ED3\u819C\u5145\u8840\u7B49\u7ED3\u819C\u708E\u75C7\u72B6\uFF0C\u4E25\u91CD\u7684\u8FD8\u4F1A\u5BFC\u81F4\u773C\u7751\u76AE\u80A4\u7EA2\u80BF\u3002</p><p>2\u3001\u65B0\u751F\u513F\u6CEA\u56CA\u708E</p><p>\u65B0\u751F\u513F\u6CEA\u56CA\u708E\u662F\u513F\u7AE5\u773C\u79D1\u5E38\u89C1\u7684\u95EE\u9898\uFF0C\u8FD9\u662F\u7531\u4E8E\u9F3B\u6CEA\u7BA1\u4E0B\u7AEF\u7684\u80DA\u80CE\u6027\u6B8B\u819C\u6CA1\u6709\u9000\u5316\uFF0C\u963B\u585E\u4E86\u9F3B\u6CEA\u7BA1\u4E0B\u7AEF\u5F15\u53D1\u7684\u95EE\u9898\uFF0C\u8FD8\u6709\u6781\u5C11\u90E8\u5206\u662F\u7531\u4E8E\u5C0F\u5B69\u672C\u8EAB\u9AA8\u6027\u9F3B\u6CEA\u7BA1\u53D1\u80B2\u4E0D\u826F\u6216\u72ED\u7A84\u9020\u6210\u7684\u3002\u9F3B\u6CEA\u7BA1\u4E0D\u901A\u7545\uFF0C\u5BFC\u81F4\u6CEA\u6DB2\u548C\u7EC6\u83CC\u79EF\u805A\u5728\u6CEA\u56CA\u91CC\uFF0C\u6709\u7684\u7EE7\u53D1\u6CEA\u56CA\u708E\uFF0C\u5BFC\u81F4\u773C\u5C4E\u589E\u591A\u3002</p><p>\u6CEA\u56CA\u708E\u4E00\u822C\u5728\u5B9D\u5B9D\u51FA\u751F\u540E1\uFF5E7 \u5929\u6216\u7A0D\u540E\u7684\u65F6\u95F4\u5185\u53D1\u75C5\uFF0C\u5BF9\u4E8E\u5A74\u5E7C\u513F\u800C\u8A00\uFF0C\u5B83\u4E3B\u8981\u8868\u73B0\u4E3A\u773C\u5C4E\u591A\uFF0C\u7A0D\u5927\u70B9\u7684\u5A74\u513F\u53EF\u4F34\u6709\u6D41\u6CEA\uFF0C\u5373\u5728\u4E0D\u54ED\u95F9\u7684\u60C5\u51B5\u4E0B\uFF0C\u90FD\u53EF\u4EE5\u89C1\u5230\u773C\u775B\u4E0D\u7531\u81EA\u4E3B\u5730\u6D41\u6CEA\uFF0C\u6324\u538B\u6CEA\u56CA\u533A\u65F6\u5F80\u5F80\u6709\u8113\u6027\u5206\u6CCC\u7269\u6D41\u51FA\u3002\u8FD9\u79CD\u75BE\u75C5\u5E38\u5E38\u88AB\u8BEF\u4EE5\u4E3A\u662F\u201C\u4E0A\u706B\u201D\u6216\u8BEF\u8BCA\u4E3A\u7ED3\u819C\u708E\u3002</p><p>3\u3001\u65B0\u751F\u513F\u8113\u6F0F\u773C</p><p>\u53C8\u79F0\u6DCB\u83CC\u6027\u7ED3\u819C\u708E\uFF0C\u4E3A\u6DCB\u7403\u83CC\u611F\u67D3\u6240\u81F4\uFF0C\u591A\u56E0\u6BCD\u4EB2\u7F79\u60A3\u6DCB\u75C5\uFF0C\u65B0\u751F\u513F\u5728\u901A\u8FC7\u4EA7\u9053\u65F6\u88AB\u611F\u67D3\uFF0C\u901A\u5E38\u53CC\u773C\u540C\u65F6\u53D1\u75C5\uFF0C\u8868\u73B0\u4E3A\u6025\u6027\u5316\u8113\u6027\u7ED3\u819C\u708E\u75C7\u3002\u5E38\u5E38\u662F\u60A3\u513F\u7684\u773C\u76AE\u4F1A\u6781\u5EA6\u7EA2\u80BF\uFF0C\u51E0\u4E4E\u7741\u4E0D\u5F00\uFF0C\u800C\u4E14\u6709\u5927\u91CF\u7684\u8113\u72B6\u773C\u5C4E\u6E90\u6E90\u4E0D\u65AD\u5730\u4EA7\u751F\uFF0C\u82E5\u6CBB\u7597\u4E0D\u5F53\uFF0C\u53EF\u80FD\u4F1A\u53D1\u751F\u89D2\u819C\u6E83\u75A1\uFF0C\u8FDB\u800C\u5F15\u8D77\u89D2\u819C\u7A7F\u5B54\uFF0C\u751A\u81F3\u5931\u660E\u7684\u5371\u9669\u3002</p><p>4\u3001\u773C\u7751\u5185\u7FFB</p><p>\u4E00\u4E9B\u5B9D\u5B9D\u9762\u90E8\u9AA8\u9ABC\u53D1\u80B2\u4E0D\u5B8C\u5168\uFF0C\u5C24\u5176\u662F\u9F3B\u6881\u5BBD\u800C\u5E73\u5766\u7684\u5A74\u513F\uFF0C\u51FA\u73B0\u4E0A\u773C\u7751\u76AE\u80A4\u5411\u4E0B\u5EF6\u4F38\u5230\u5185\u773C\u89D2\uFF0C\u906E\u76D6\u90E8\u5206\u773C\u89D2\u7EC4\u7EC7\u751A\u81F3\u90E8\u5206\u5DE9\u819C\uFF0C\u5BFC\u81F4\u773C\u7751\u776B\u6BDB\u5411\u5185\u63A8\u79FB\uFF0C\u4F7F\u773C\u7403\u53D7\u5230\u6469\u64E6\u523A\u6FC0\uFF0C\u4EA7\u751F\u4E0D\u9002\uFF0C\u4ECE\u800C\u51FA\u73B0\u6CEA\u6C34\u591A\u3001\u5206\u6CCC\u7269\u591A\u7B49\u75C7\u72B6,\u8FD9\u79CD\u60C5\u51B5\u6BD4\u8F83\u5C11\u89C1\u3002</p><p><strong>\u4E8C\u3001\u5B9D\u5B9D\u5F97\u4E86\u7ED3\u819C\u708E\u8BE5\u600E\u4E48\u529E\uFF1F</strong></p><p>\u7ED3\u819C\u708E\u901A\u5E38\u662F\u77ED\u671F\u7684\u773C\u5C4E\u591A\uFF0C\u7ED3\u819C\u5145\u8840\uFF0C\u773C\u7751\u80BF\u80C0\u7B49\uFF0C\u70B9\u7528\u6297\u751F\u7D20\u773C\u836F\u6C34\u540E\u80FD\u5FEB\u901F\u597D\u8F6C\uFF0C\u75C7\u72B6\u660E\u663E\u51CF\u8F7B\u4E14\u4E0D\u6613\u590D\u53D1\u3002</p><p>\u800C\u9F3B\u6CEA\u7BA1\u963B\u585E\u7684\u8868\u73B0\u5219\u662F\u5B9D\u5B9D\u7ECF\u5E38\u4F1A\u6D41\u6CEA\uFF0C\u5206\u6CCC\u7269\u591A\uFF0C\u7ED3\u819C\u4E5F\u4F1A\u6709\u5145\u8840\u7684\u73B0\u8C61\uFF0C\u5982\u679C\u7528\u624B\u538B\u8FEB\u6CEA\u56CA\u533A\uFF0C\u6216\u51B2\u6D17\u6CEA\u9053\u65F6\uFF0C\u4F1A\u6709\u9EC4\u3001\u767D\u8272\u7C98\u7A20\u6027\u7684\u8113\u6027\u5206\u6CCC\u7269\u4ECE\u6CEA\u5C0F\u70B9\u6EA2\u51FA\u3002\u70B9\u7528\u6297\u751F\u7D20\u773C\u836F\u6C34\u540E\u75C7\u72B6\u7A0D\u6709\u597D\u8F6C\uFF0C\u4F46\u505C\u836F\u540E\u6781\u6613\u518D\u51FA\u73B0\u5206\u6CCC\u7269\u589E\u591A\u3002</p><p><strong>\u4E09\u3001\u65B0\u751F\u513F\u4E3A\u4EC0\u4E48\u5BB9\u6613\u6709\u9F3B\u6CEA\u7BA1\u963B\u585E\uFF1F</strong></p><p>\u5728\u80CE\u513F\u65F6\u671F\uFF0C\u5B9D\u5B9D\u9F3B\u6CEA\u7BA1\u672B\u7AEF\u90FD\u6709\u4E00\u5C42\u9ECF\u819C\u74E3\u819C(\u79F0Hasner\u74E3)\u5C01\u95ED\uFF0C\u51FA\u751F\u65F6\u53D7\u4EA7\u9053\u538B\u529B\u7B49\u4F5C\u7528\uFF0C\u53EF\u4EE5\u81EA\u52A8\u7834\u88C2\uFF0C\u6CEA\u9053\u5C31\u8D2F\u901A\u4E86\u3002\u4F46\u90E8\u5206\u65B0\u751F\u513F\u7531\u4E8E\u819C\u72B6\u7269\u8F83\u539A\uFF0C\u51FA\u751F\u540EHasner\u74E3\u6CA1\u6709\u5F00\u653E\uFF0C\u6CEA\u9053\u81EA\u7136\u4E0D\u901A\u3002</p><p>\u53E6\u5916\uFF0C\u6709\u4E00\u4E9B\u5B9D\u5B9D\u5728\u5B55\u671F\u5C31\u906D\u5230\u5BAB\u5185\u611F\u67D3\uFF0C\u6CEA\u9053\u53D7\u708E\u75C7\u523A\u6FC0\u800C\u5F62\u6210\u4E86\u72ED\u7A84\u7C98\u8FDE\uFF0C\u6216\u7531\u4E8E\u9F3B\u6CEA\u7BA1\u90E8\u5148\u5929\u6027\u7578\u5F62\u7B49\u56E0\u7D20\uFF0C\u4E5F\u90FD\u4F1A\u9020\u6210\u6CEA\u9053\u963B\u585E\u3002\u8FD9\u90FD\u79F0\u4E3A\u201C\u5148\u5929\u6027\u6CEA\u9053\u963B\u585E\u201D\u6216\u201C\u5148\u5929\u6027\u9F3B\u6CEA\u7BA1\u963B\u585E\u201D\u3002\u8FD9\u662F\u5A74\u5E7C\u513F\u5E38\u89C1\u75C5\uFF0C\u8DB3\u6708\u513F\u53D1\u75C5\u7387\u7EA66%\u3002</p><p><strong>\u56DB\u3001\u9F3B\u6CEA\u7BA1\u963B\u585E\u5E94\u8BE5\u600E\u4E48\u5904\u7406\uFF1F\u6309\u6469\u53EF\u4EE5\u5417\uFF1F</strong></p><p>\u5982\u679C\u786E\u8BCA\u4E3A\u6CEA\u9053\u963B\u585E\uFF0C\u9996\u5148\u53EF\u4EE5\u91C7\u53D6\u4FDD\u5B88\u6CBB\u7597\uFF0C\u5373\u9F3B\u90E8\u6309\u6469\u3002\u6309\u6469\u7684\u65B9\u6CD5\u6709\u4E24\u79CD\uFF1A</p><p>\u7B2C\u4E00\u79CD\uFF0C\u5982\u679C\u5B9D\u5B9D\u773C\u775B\u91CC\u9762\u6709\u8113\u6027\u5206\u6CCC\u7269\uFF0C\u5BB6\u957F\u5C31\u53EF\u7528\u98DF\u6307\u6307\u8179\u6309\u5728\u5B9D\u5B9D\u7684\u9F3B\u6839\u53CA\u773C\u775B\u7684\u5185\u7726\u4E2D\u95F4\u7684\u90E8\u4F4D\uFF0C\u5F80\u773C\u775B\u7684\u65B9\u5411\u6324\u538B\uFF0C\u53EF\u89C1\u5230\u8113\u6DB2\u4ECE\u773C\u89D2\u6D41\u51FA\u6765\uFF0C\u7ED9\u5B9D\u5B9D\u64E6\u5E72\u51C0\uFF0C\u5E76\u70B9\u7528\u6297\u751F\u7D20\u773C\u836F\u6C34\uFF1B</p><p>\u7B2C\u4E8C\u79CD\u624B\u6CD5\u662F\u5728\u8FD9\u4E2A\u4F4D\u7F6E\uFF0C\u7531\u4E0A\u5F80\u4E0B\u6309\u538B\u6CEA\u56CA\u533A\uFF0C\u5E0C\u671B\u80FD\u628A\u9F3B\u6CEA\u7BA1\u7684\u90A3\u4E2A\u819C\u7ED9\u51B2\u5F00\u3002\u4E00\u822C\u662F\u6BCF\u5929\u6309\u64693~4\u6B21\uFF0C\u6BCF\u6B21\u6309\u538B5~10\u4E0B\u5C31\u884C\u3002\u6309\u6469\u540E\u64E6\u5E72\u51C0\u773C\u6CEA\u53CA\u5206\u6CCC\u7269\uFF0C\u518D\u70B9\u836F\u6C34\u3002</p><p><strong>\u4E94\u3001\u6309\u6469\u4E0D\u597D\u8BE5\u600E\u4E48\u5904\u7406\uFF1F\u6CEA\u9053\u51B2\u6D17\u6CBB\u7597\u5F88\u75DB\u82E6\u5417\uFF1F\u63A2\u901A\u672F\u540E\u963B\u585E\u5BB9\u6613\u590D\u53D1\u5417\uFF1F</strong></p><p>\u6CEA\u9053\u51B2\u6D17\u9002\u7528\u4E8E\u7ECF\u6309\u6469\u6CD5\u65E0\u6548\u7684\u60A3\u513F\uFF0C\u53EF\u4EE5\u5728\u95E8\u8BCA\u8FDB\u884C\uFF0C\u5982\u679C\u52A0\u538B\u51B2\u6D172~3\u6B21\u4ECD\u7136\u65E0\u6548\uFF0C\u4E14\u60A3\u513F\u6708\u9F84\u8D85\u8FC73\u4E2A\u6708\uFF0C\u5219\u53EF\u884C\u6CEA\u9053\u63A2\u901A\u672F\u3002</p><p>\u6CEA\u9053\u51B2\u6D17\u53CA\u63A2\u901A\u662F\u517C\u6709\u8BCA\u65AD\u53CA\u6CBB\u7597\u4F5C\u7528\u7684\u4E00\u79CD\u624B\u672F\u64CD\u4F5C\uFF0C\u51E1\u662F\u8BCA\u65AD\u660E\u786E\u6216\u8005\u6000\u7591\u6709\u6CEA\u9053\u963B\u585E\u7684\u60A3\u513F\uFF0C\u5728\u65E0\u7981\u5FCC(\u5982\uFF1A\u53D1\u70ED\u3001\u547C\u5438\u9053\u611F\u67D3\u7B49)\u7684\u60C5\u51B5\u4E0B\uFF0C\u90FD\u53EF\u4EE5\u884C\u6CEA\u9053\u51B2\u6D17\u6216\u63A2\u901A\u624B\u672F\u3002</p><p>\u4E34\u5E8A\u4E0A\uFF0C\u5F88\u591A\u7236\u6BCD\u770B\u5230\u94F6\u5149\u95EA\u95EA\u7684\u6CEA\u9053\u51B2\u6D17\u9488\uFF0C\u4F1A\u4EE5\u4E3A\u950B\u5229\u7684\u9488\u5C16\u624E\u8FDB\u773C\u76AE\uFF0C\u4E00\u5B9A\u5F88\u75BC\u3002\u5176\u5B9E\u662F\u8BEF\u89E3\u4E86\uFF0C\u6CEA\u9053\u51B2\u6D17\u9488\u7684\u9488\u5934\u662F\u949D\u5706\u7684\uFF0C\u800C\u4E14\u5E76\u4E0D\u662F\u201C\u624E\u9488\u201D\uFF0C\u53EA\u662F\u901A\u8FC7\u773C\u76AE\u5185\u7726\u89D2\u7684\u4E00\u4E2A\u5F88\u5C0F\u7684\u6CEA\u5C0F\u70B9\u5F00\u53E3\u8FDB\u9488\u5165\u6CEA\u9053\uFF0C\u7136\u540E\u628A\u836F\u6C34\u63A8\u8FDB\u53BB\u3002</p><p>\u76EE\u524D\u6CEA\u9053\u63A2\u901A\u672F\u6CBB\u7597\u65B0\u751F\u513F\u9F3B\u6CEA\u7BA1\u963B\u585E\u7684\u624B\u672F\u6210\u529F\u7387\u8F83\u9AD8\uFF0C\u5C11\u6570\u60A3\u513F\u7531\u4E8E\u672F\u524D\u6CEA\u9053\u957F\u671F\u708E\u75C7\u9020\u6210\u7C97\u7CD9\u7C98\u8FDE\uFF0C\u6216\u8005\u672F\u540E\u9F3B\u6CEA\u7BA1\u708E\u75C7\u8F83\u91CD\u53CA\u62A4\u7406\u4E0D\u5F53\uFF0C\u53EF\u80FD\u4F1A\u51FA\u73B0\u590D\u53D1\u9700\u8981\u518D\u6B21\u624B\u672F\u3002</p><p><strong>\u516D\u3001\u9F3B\u6CEA\u7BA1\u963B\u585E\u4E0D\u53CA\u65F6\u6CBB\u7597\u4F1A\u6709\u4EC0\u4E48\u5371\u5BB3\uFF1F</strong></p><p>\u5F88\u591A\u5BB6\u957F\u770B\u89C1\u5B9D\u5B9D\u505A\u6CEA\u9053\u51B2\u6D17\u65F6\u54C7\u54C7\u5927\u54ED\uFF0C\u90FD\u4F1A\u5FC3\u75DB\u5730\u60F3\u4E2D\u65AD\u6CBB\u7597\uFF0C\u7ECF\u5E38\u54A8\u8BE2\u533B\u5E08\u201C\u5B9D\u5B9D\u53EA\u662F\u773C\u6CEA\u773C\u5C4E\u591A\u800C\u5DF2\uFF0C\u80FD\u4E0D\u80FD\u5148\u4E0D\u7BA1\u5462\uFF1F\u201D</p><p>\u6211\u4EEC\u5EFA\u8BAE\u65B0\u751F\u513F\u6CEA\u56CA\u708E\u8FD8\u662F\u8981\u53CA\u65E9\u5904\u7406\uFF0C\u5148\u5929\u6027\u6CEA\u9053\u963B\u585E\u5982\u679C\u957F\u65F6\u95F4\u4E0D\u6CBB\u7597\uFF0C\u53EF\u80FD\u4F1A\u51FA\u73B0\u708E\u75C7\u6025\u6027\u53D1\u4F5C\u5E76\u5411\u5468\u56F4\u6269\u6563\uFF1B\u6CEA\u56CA\u957F\u65F6\u95F4\u6269\u5F20\u4F1A\u4F7F\u6CEA\u56CA\u58C1\u5931\u53BB\u5F39\u529B\uFF0C\u65E5\u540E\u5373\u4F7F\u6CEA\u9053\u901A\u7545\uFF0C\u6EA2\u6CEA\u75C7\u72B6\u4F9D\u7136\u4F1A\u5B58\u5728\uFF0C\u5E76\u53EF\u80FD\u56E0\u6CEA\u9053\u708E\u75C7\u6301\u7EED\u800C\u5F62\u6210\u6C38\u4E45\u6027\u7622\u75D5\u6027\u6CEA\u9053\u95ED\u585E\uFF0C\u4F7F\u6CBB\u7597\u53D8\u5F97\u66F4\u4E3A\u590D\u6742\u3002</p><p>\u5E76\u4E14\u708E\u75C7\u53CD\u590D\u523A\u6FC0\u8FD8\u53EF\u80FD\u4F1A\u9020\u6210\u5B69\u5B50\u53D1\u751F\u6025\u6027\u6CEA\u56CA\u708E\u3001\u751A\u81F3\u7736\u8702\u7A9D\u7EC7\u708E\uFF0C\u4E0D\u4EC5\u4F7F\u5B69\u5B50\u66F4\u75DB\u82E6\uFF0C\u8FD8\u6613\u9020\u6210\u5B69\u5B50\u9762\u90E8\u7684\u7622\u75D5\u5F71\u54CD\u7F8E\u89C2\u3002\u53E6\u5916\uFF0C\u6CEA\u56CA\u8113\u6DB2\u4E0D\u65AD\u6392\u5165\u7ED3\u819C\u56CA\uFF0C\u53EF\u5BFC\u81F4\u7ED3\u819C\u548C\u89D2\u819C\u708E\u75C7\uFF0C\u5F15\u8D77\u89D2\u819C\u6E83\u75A1\uFF0C\u751A\u81F3\u53D1\u5C55\u4E3A\u773C\u5185\u708E\uFF0C\u5BF9\u773C\u7403\u9020\u6210\u4E25\u91CD\u7684\u6F5C\u5728\u5A01\u80C1\u3002\u6240\u4EE5\u5E94\u53CA\u65F6\u5E26\u5B9D\u5B9D\u5230\u6B63\u89C4\u533B\u9662\u770B\u533B\u751F\uFF0C\u660E\u786E\u8BCA\u65AD\u53CA\u65F6\u6CBB\u7597\u3002\uFF08\u672C\u6587\u7531\u6DF1\u5733\u5E02\u513F\u7AE5\u533B\u9662\u516C\u4F17\u53F7\u6388\u6743\u8F6C\u8F7D\uFF0C\u9898\u56FE\u6765\u81EAPixabay\uFF09</p><hr><p>\u957F\u6309\u8BC6\u522B\u4E0B\u9762\u4E8C\u7EF4\u7801\uFF0C\u5173\u6CE8\u6021\u79BE\u5065\u5EB7\uFF0C\u8FDB\u5165\u773C\u79D1\u53EF\u5411\u773C\u79D1\u533B\u751F\u54A8\u8BE2\uFF1A<strong>\u7ED3\u819C\u708E\u3001\u6CEA\u56CA\u708E\u3001\u659C\u89C6\u3001\u5F31\u89C6\u3001\u8FD1\u89C6\u3001\u9EA6\u7C92\u80BF</strong>\u7B49\u95EE\u9898\u3002</p>',34),f={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651626501&idx=1&sn=bc2134ed3fd9cac4a8fd579279d3f4fa&chksm=8b5cb5ffbc2b3ce90e485837d201b4698b6846c8445d8b7172e329b09345c83fb2aff03ed351&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},g=c("\u67E5\u770B\u539F\u6587");function h(l,b){const e=r("ExternalLinkIcon");return n(),t(a,null,[_,p("p",null,[p("a",f,[g,o(e)])])],64)}var u=d(i,[["render",h]]);export{u as default};
