import{r as a,o as r,c as t,a as e,b as n,F as o,d as c,e as s}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const _={},i=c('<h3 id="\u7ED9\u4F1A\u5458\u514D\u8D39\u9001\u300C\u513F\u7AE5\u5065\u5EB7\u624B\u518C\u300D-\u662F\u4E0D\u662F\u7F8A\u6BDB\u51FA\u5728\u7F8A\u8EAB\u4E0A" tabindex="-1"><a class="header-anchor" href="#\u7ED9\u4F1A\u5458\u514D\u8D39\u9001\u300C\u513F\u7AE5\u5065\u5EB7\u624B\u518C\u300D-\u662F\u4E0D\u662F\u7F8A\u6BDB\u51FA\u5728\u7F8A\u8EAB\u4E0A" aria-hidden="true">#</a> \u7ED9\u4F1A\u5458\u514D\u8D39\u9001\u300C\u513F\u7AE5\u5065\u5EB7\u624B\u518C\u300D\uFF0C\u662F\u4E0D\u662F\u7F8A\u6BDB\u51FA\u5728\u7F8A\u8EAB\u4E0A\uFF1F</h3><p>\u6700\u8FD1\u5F88\u591A\u6021\u79BE\u4F1A\u5458\u6536\u5230\u4E86\u513F\u7AE5\u5065\u5EB7\u624B\u518C\u3002</p><p>\u4E3A\u4E86\u8FD9\u4EFD\u624B\u518C\uFF0C\u540C\u4E8B\u4EEC\u82B1\u4E86\u5F88\u591A\u5FC3\u529B\uFF0C\u4ECE\u5185\u5BB9\u5230\u914D\u56FE\u5230\u6750\u8D28\uFF0C\u7ECF\u5386\u4E86\u53CD\u590D\u7684\u8C03\u6574\u548C\u6253\u78E8\uFF0C\u8017\u65F6\u597D\u51E0\u4E2A\u6708\u3002</p><p>\u5927\u5BB6\u6536\u5230\u624B\u518C\u540E\uFF0C\u666E\u904D\u7684\u53CD\u5E94\u662F\u60CA\u559C\uFF0C\u53EF\u80FD\u662F\u6CA1\u60F3\u5230\u514D\u8D39\u7684\u624B\u518C\u4E5F\u80FD\u8FD9\u4E48\u7528\u5FC3\uFF0C\u6240\u4EE5\u5FAE\u535A\u4E0A\u4E5F\u770B\u5230\u975E\u5E38\u591A\u7684\u81EA\u53D1\u5206\u4EAB\u3002</p><p>\u4F46\u4E5F\u770B\u5230\u6709\u4EBA\u8BF4\uFF1A\u7F8A\u6BDB\u51FA\u5728\u7F8A\u8EAB\u4E0A\u800C\u5DF2\u3002</p><p>\u8FD9\u6837\u7406\u89E3\uFF0C\u8BF4\u5BF9\u4E5F\u5BF9\uFF0C\u8BF4\u4E0D\u5BF9\u4E5F\u4E0D\u5BF9\u3002</p><p>\u5982\u679C\u6CA1\u6709\u5305\u62EC\u4F1A\u5458\u5728\u5185\u7684\u6021\u79BE\u7528\u6237\u4E00\u6B21\u6B21\u4ED8\u8D39\u54A8\u8BE2\uFF0C\u4E00\u6B21\u6B21\u4ED8\u8D39\u542C\u8BFE\uFF0C\u4E00\u6B21\u6B21\u5230\u6021\u79BE\u95E8\u8BCA\u5C31\u8BCA\uFF0C\u6021\u79BE\u662F\u4E0D\u53EF\u80FD\u751F\u5B58\u4E0B\u6765\u7684\uFF0C\u4E5F\u4E0D\u53EF\u80FD\u53D8\u5F97\u66F4\u5927\uFF0C\u4ECE\u8FD9\u4E2A\u89D2\u5EA6\u60F3\uFF0C\u8BF4\u7F8A\u6BDB\u51FA\u5728\u7F8A\u8EAB\u4E0A\u4E5F\u5BF9\u3002</p><p>\u8BF4\u8FD9\u6837\u7406\u89E3\u4E0D\u5BF9\uFF0C\u662F\u56E0\u4E3A\u8FD9\u4E2A\u624B\u518C\u5E76\u4E0D\u662F\u5F00\u901A\u4F1A\u5458\u65F6\u627F\u8BFA\u7684\u6743\u76CA\u91CC\u7684\u4E00\u90E8\u5206\uFF0C\u624B\u518C\u505A\u51FA\u6765\u540E\uFF0C\u4E5F\u6CA1\u6709\u8981\u6C42\u4F1A\u5458\u989D\u5916\u4ED8\u51FA\u4EC0\u4E48\u53BB\u83B7\u5F97\u5B83\u3002\u66F4\u4E0D\u662F\u56E0\u4E3A\u6021\u79BE\u8D5A\u94B1\u4E86\u7ED9\u5927\u5BB6\u8FD4\u5229\uFF0C\u6021\u79BE\u79BB\u76C8\u5229\u8FD8\u5F88\u8FDC\uFF0C\u6240\u4EE5\u4E5F\u662F\u5728\u9760\u878D\u8D44\u652F\u6301\u8FD0\u8425\u3002</p><p>\u4E4B\u6240\u4EE5\u505A\u8FD9\u4E2A\u624B\u518C\uFF0C\u662F\u6211\u4EEC\u4E00\u76F4\u5728\u60F3\u5982\u4F55\u5728\u73B0\u6709\u6761\u4EF6\u4E0B\u53BB\u4E3A\u7528\u6237\u521B\u9020\u4EF7\u503C\uFF0C\u8BA9\u6211\u4EEC\u7684\u670D\u52A1\u66F4\u6709\u5438\u5F15\u529B\uFF0C\u66F4\u6709\u7ADE\u4E89\u529B\uFF0C\u80FD\u88AB\u66F4\u591A\u5BB6\u5EAD\u9009\u62E9\uFF0C\u6021\u79BE\u7684\u6536\u5165\u80FD\u66F4\u9AD8\uFF0C\u6700\u7EC8\u5B9E\u73B0\u76C8\u5229\uFF0C\u5B9E\u73B0\u7528\u6237\u548C\u6021\u79BE\u7684\u5171\u8D62\u3002</p><p>\u800C\u4E14\u5546\u4E1A\u4E5F\u5E76\u4E0D\u662F\u7F8A\u6BDB\u5BF9\u7F8A\u6BDB\u7684\u4EA4\u6362\uFF0C\u5C31\u6021\u79BE\u4F1A\u5458\u800C\u8A00\uFF0C\u9664\u4E86\u95EE\u533B\u751F\u76848.8\u6298\u7B49\u6743\u76CA\uFF0C\u8FD8\u6709130+\u7684\u4ED8\u8D39\u8BFE\u7A0B\u514D\u8D39\u542C\uFF0C\u95E8\u8BCA\u5C31\u8BCA8\u6298\u7B49\u4F18\u60E0\uFF0C\u54EA\u6015\u56E0\u4E3A\u4F7F\u7528\u6021\u79BE\u7684\u8FC7\u7A0B\u4E2D\uFF0C\u56E0\u4E3A\u5F97\u5230\u66F4\u4E13\u4E1A\u7684\u652F\u6301\uFF0C\u5C11\u5403\u4E00\u4E2A\u672C\u53EF\u4EE5\u4E0D\u5403\u7684\u836F\uFF0C\u90FD\u5F88\u6709\u53EF\u80FD\u628A\u4E00\u5E74\u7684\u4F1A\u5458\u8D39\u7701\u4E0B\u6765\u4E86\uFF0C\u8FD8\u907F\u514D\u4E86\u53EF\u80FD\u7684\u5065\u5EB7\u635F\u5BB3\u3002</p><p>\u628A\u8FD9\u4E2A\u513F\u7AE5\u5065\u5EB7\u624B\u518C\u514D\u8D39\u6D3E\u9001\u7ED9\u4F1A\u5458\uFF0C\u53EA\u662F\u56E0\u4E3A\u5F53\u524D\u6211\u4EEC\u8FD8\u8D1F\u62C5\u5F97\u8D77\uFF0C\u4E5F\u613F\u610F\u4E3A\u66F4\u597D\u7684\u670D\u52A1\u54C1\u8D28\u6295\u5165\uFF0C\u8BA9\u6021\u79BE\u4F1A\u5458\u53D8\u5F97\u66F4\u6709\u5438\u5F15\u529B\u800C\u5DF2\u3002</p><p>\u4ECE\u8FD9\u51E0\u5E74\u7684\u7ECF\u9A8C\u6765\u770B\uFF0C\u6211\u4EEC\u8FD9\u6837\u7684\u6295\u5165\u4F1A\u6709\u56DE\u62A5\u7684\u3002\u6021\u79BE\u6210\u7ACB\u4EE5\u6765\u57FA\u672C\u6CA1\u505A\u4EC0\u4E48\u5E02\u573A\u8425\u9500\uFF0C\u7528\u6237\u589E\u957F\u4E3B\u8981\u9760\u8001\u7528\u6237\u7684\u81EA\u53D1\u5BA3\u4F20\uFF0C\u8001\u7528\u6237\u4E4B\u6240\u4EE5\u613F\u610F\u5E2E\u5FD9\u5BA3\u4F20\uFF0C\u662F\u56E0\u4E3A\u6211\u4EEC\u5728\u670D\u52A1\u8D28\u91CF\u63A7\u5236\u4E0A\u6295\u5165\u4E86\u5F88\u591A\uFF0C\u5927\u5BB6\u5728\u7528\u7684\u8FC7\u7A0B\u4E2D\u611F\u53D7\u5230\u4E86\u533B\u751F\u4EEC\u7684\u4E13\u4E1A\u548C\u7528\u5FC3\uFF0C\u5BF9\u81EA\u5DF1\u6709\u5E2E\u52A9\u7684\u4E1C\u897F\u81EA\u7136\u4E5F\u66F4\u613F\u610F\u63A8\u8350\u548C\u5206\u4EAB\u3002</p><p>\u5C31\u50CF\u8FD9\u6B21\u7684\u624B\u518C\u4E00\u6837\uFF0C\u5BC4\u51FA\u7684\u65F6\u5019\u6211\u4EEC\u4E5F\u6CA1\u6709\u8981\u6C42\u5E2E\u5FD9\u5BA3\u4F20\uFF0C\u4F46\u5927\u5BB6\u6536\u5230\u624B\u518C\u540E\uFF0C\u80FD\u611F\u53D7\u5230\u5185\u5BB9\u548C\u8BBE\u8BA1\u4E0A\u7684\u54C1\u8D28\u548C\u7528\u5FC3\uFF0C\u80FD\u5E26\u6765\u4E0D\u4E00\u6837\u7684\u4F53\u9A8C\uFF0C\u6240\u4EE5\u624D\u4F1A\u6709\u90A3\u4E48\u591A\u4EBA\u81EA\u53D1\u5730\u6652\u5355\u5BA3\u4F20\uFF0C\u7136\u540E\u6709\u66F4\u591A\u4EBA\u77E5\u9053\u6021\u79BE\u4E86\u3002</p><p>\u8FD9\u5C31\u662F\u6211\u4EEC\u60F3\u8981\u7684\u5171\u8D62\u6548\u679C\u3002</p><hr><p>\u4E5F\u6B63\u662F\u56E0\u4E3A\u89C9\u5F97\u624B\u518C\u5F88\u597D\u5F88\u5B9E\u7528\uFF0C\u9664\u4E86\u5206\u4EAB\u548C\u63A8\u8350\u5916\uFF0C\u5F88\u591A\u4EBA\u8FD8\u8868\u8FBE\u8FC7\u60F3\u4E701\u672C\u9001\u7ED9\u4EB2\u621A\u670B\u53CB\u3002</p><p>\u8003\u8651\u5230\u8FD9\u4E00\u70B9\uFF0C\u540C\u6837\u4E5F\u662F\u57FA\u4E8E\u5171\u8D62\u7684\u60F3\u6CD5\uFF0C\u5728\u7ED9\u4F1A\u5458\u514D\u8D39\u6D3E\u9001\u7684\u57FA\u7840\u4E0A\uFF0C\u73B0\u5728\u589E\u52A0\u4E00\u4E2A\u65B0\u7684\u6D3B\u52A8\uFF1A \u6652\u5355\u53EF\u518D\u5F97\u4E00\u672C \uFF0C\u89C4\u5219\u5982\u4E0B\uFF1A</p><p>\u5728\u670B\u53CB\u5708\u3001\u5FAE\u535A\u3001\u5C0F\u7EA2\u4E66\uFF0C\u5E26\u4E0A #\u6021\u79BE\u5065\u5EB7 \u8BDD\u9898\u6652\u5355\u5206\u4EAB\u8FD9\u672C\u624B\u518C\uFF0C\u628A\u6652\u5355\u63A8\u8350\u7684\u622A\u56FE\u901A\u8FC7\u4E0B\u9762\u4E8C\u7EF4\u7801\u53D1\u9001\u7ED9\u4F1A\u5458\u52A9\u624B\uFF0C\u5C31\u53EF\u4EE5\u518D\u83B7\u5F97\u4E00\u672C\u3002</p><p>\u4F1A\u5458\u52A9\u624B\u4E8C\u7EF4\u7801</p><p>\u4F9D\u7136\u662F\u7531\u6021\u79BE\u5305\u90AE\u5BC4\u51FA\uFF0C\u53EF\u4EE5\u7559\u4F5C\u81EA\u7528\u6216\u9001\u7ED9\u670B\u53CB\uFF0C\u6D3B\u52A8\u622A\u6B62\u52307\u670831\u65E5\u3002</p><p>\u5982\u679C\u4F60\u8FD8\u6CA1\u6709\u624B\u518C\uFF0C\u901A\u8FC7 \u9605\u8BFB\u539F\u6587 \u5F00\u901A\u540E\uFF0C\u518D\u901A\u8FC7\u4E0B\u9762\u4E8C\u7EF4\u7801\u586B\u5199\u6536\u4EF6\u4FE1\u606F\u5373\u53EF\u83B7\u5F97\u3002</p><p>\u53E6\u5916\uFF0C\u5173\u4E8E\u8FD9\u672C\u624B\u518C\u7684\u4F7F\u7528\uFF0C\u6211\u4EEC\u4E5F\u9080\u8BF7\u624B\u518C\u5185\u5BB9\u5236\u5B9A\u7684\u91CD\u8981\u53C2\u4E0E\u8005\uFF0C <strong>\u9648\u5EFA\u519B</strong> \u548C <strong>\u6797\u6C49\u70BC</strong> \u533B\u751F\u4ECA\u665A\u5728\u6021\u79BE\u89C6\u9891\u53F7\u76F4\u64AD\u95F4\u505A\u4E00\u573A\u76F4\u64AD\uFF0C\u5173\u4E8E\u600E\u4E48\u7528\u597D\u8FD9\u672C\u624B\u518C\uFF0C\u6216\u513F\u7AE5\u5065\u5EB7\u53D1\u80B2\u76F8\u5173\u95EE\u9898\uFF0C\u4E5F\u53EF\u4EE5\u4ED6\u4EEC\u4E92\u52A8\u8BA8\u8BBA\u3002</p><p>\u5E76\u4E14\uFF0C\u76F4\u64AD\u4E2D\u5C06 <strong>\u62BD\u5956\u6D3E\u900120\u672C</strong> \u624B\u518C\uFF0C\u6709\u5174\u8DA3\u7684\u53EF\u901A\u8FC7\u4E0B\u9762\u6309\u94AE\u9884\u7EA6\u76F4\u64AD\u3002</p>',23),h={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651633964&idx=1&sn=a078279ae14157dd099eb97de4308cf2&chksm=8b5c90d6bc2b19c0d305a16174f21fe20e5a6b9612619cafd318bb679b9fa43549935ad8b867&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},l=s("\u67E5\u770B\u539F\u6587");function f(m,b){const p=a("ExternalLinkIcon");return r(),t(o,null,[i,e("p",null,[e("a",h,[l,n(p)])])],64)}var u=d(_,[["render",f]]);export{u as default};
