import{r,o as t,c as a,a as e,b as n,F as o,d as c,e as s}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const i={},_=c('<h3 id="\u5B69\u5B50\u9047\u9669\u540E\u65E0\u6059-\u5BB6\u957F\u4E3A\u4F55\u8981\u63CD\u4ED6" tabindex="-1"><a class="header-anchor" href="#\u5B69\u5B50\u9047\u9669\u540E\u65E0\u6059-\u5BB6\u957F\u4E3A\u4F55\u8981\u63CD\u4ED6" aria-hidden="true">#</a> \u5B69\u5B50\u9047\u9669\u540E\u65E0\u6059\uFF0C\u5BB6\u957F\u4E3A\u4F55\u8981\u63CD\u4ED6\uFF1F</h3><p>\u4E2D\u56FD\u81EA\u53E4\u6709\u201C\u68CD\u68D2\u51FA\u5B5D\u5B50\u201D\u7684\u8BF4\u6CD5\uFF0C\u6253\u9A82\u5B69\u5B50\u5728\u56FD\u5185\u5F88\u5E38\u89C1\uFF0C\u5F88\u591A\u4EBA\u8FD9\u6837\u505A\u89C9\u5F97\u81EA\u5DF1\u662F\u4E3A\u5B69\u5B50\u597D,\u800C\u4E8B\u5B9E\u5982\u4F55\u5462\uFF1F\u8FD1\u65E5\u770B\u5230\u4E00\u7BC7\u6587\u7AE0\uFF0C\u4F5C\u8005\u5199\u81EA\u5DF1\u5C0F\u65F6\u5019\u88AB\u6253\u7684\u4E00\u6B21\u7ECF\u5386\u548C\u5206\u6790\uFF0C\u89C9\u5F97\u5199\u5F97\u7279\u522B\u597D\uFF0C\u5F97\u5230\u4F5C\u8005\u9648\u8BFA\u6388\u6743\u540E\u5206\u4EAB\u7ED9\u5927\u5BB6\u3002</p><p>\u5C0F\u65F6\u5019\u7684\u6211\u662F\u4E2A\u4E0D\u8BA9\u4EBA\u7701\u5FC3\u7684\u5B69\u5B50\uFF0C\u5728\u4EBA\u591A\u7684\u5730\u65B9\u548C\u5927\u4EBA\u8D70\u6563\u8FC7\u51E0\u6B21\u3002\u5370\u8C61\u6700\u6DF1\u7684\u4E00\u6B21\u662F\u5C0F\u5B66\u4E00\u4E8C\u5E74\u7EA7\uFF0C\u5728\u767E\u8D27\u5546\u573A\u91CC\uFF0C\u4E0A\u4E0B\u6276\u624B\u7535\u68AF\u65F6\u9009\u4E86\u9519\u8BEF\u7684\u65B9\u5411\uFF0C\u5927\u4EBA\u4EEC\u4E0B\u697C\uFF0C\u6211\u5374\u4E0A\u4E86\u697C\u3002</p><p>\u53D1\u73B0\u8EAB\u8FB9\u6CA1\u5927\u4EBA\u4EE5\u540E\u6211\u6CA1\u600E\u4E48\u5BB3\u6015\uFF0C\u8001\u795E\u5728\u5728\u7684\u901B\u6765\u901B\u53BB\uFF0C\u76F4\u5230\u88AB\u6211\u5988\u53D1\u73B0\uFF0C\u5927\u8001\u8FDC\u5C31\u542C\u89C1\u5979\u6C14\u6025\u8D25\u574F\u558A\u6211\u7684\u540D\u5B57\uFF0C\u6211\u7AD9\u5728\u539F\u5730\u7B49\u5979\u8DD1\u8FC7\u6765\u3002\u556A\uFF01\u4E00\u4E2A\u7ED3\u5B9E\u7684\u5DF4\u638C\u5C31\u76D6\u5728\u6211\u8138\u4E0A\uFF0C\u7D27\u63A5\u7740\u662F\u4E00\u901A\u75BE\u98CE\u9AA4\u96E8\u4E00\u6837\u7684\u5F53\u8857\u75DB\u9A82\u3002\u6211\u4E94\u96F7\u8F70\u9876\uFF0C\u7AD9\u5728\u4EBA\u6765\u4EBA\u5F80\u7684\u767E\u8D27\u4E2D\u5FC3\u773C\u6CEA\u5C31\u6389\u4E0B\u6765\uFF0C\u4E0D\u77E5\u9053\u81EA\u5DF1\u505A\u9519\u4EC0\u4E48\u4E86\u3002</p><p>\u5BF9\u4E8E\u6211\u5988\u6765\u8BF4\uFF0C\u6211\u4E22\u4E86\uFF0C\u7ED9\u5979\u9020\u6210\u4E86\u5DE8\u5927\u7684\u7CBE\u795E\u538B\u529B\uFF0C\u5979\u5728\u6BEB\u65E0\u9632\u5907\u7684\u60C5\u51B5\u4E0B\u906D\u53D7\u4E86\u60CA\u5413\uFF0C\u800C\u8FD9\u80A1\u60CA\u5413\u5728\u6709\u60CA\u65E0\u9669\u4E4B\u540E\u9700\u8981\u4E00\u4E2A\u53D1\u6CC4\u7684\u51FA\u53E3\u3002\u5C3D\u7BA1\u6211\u4E0D\u662F\u6545\u610F\u8D70\u4E22\u7684\uFF0C\u4E0D\u662F\u6709\u610F\u5413\u5979\u7684\uFF0C\u5C3D\u7BA1\u6211\u5E76\u4E0D\u77E5\u9053\u81EA\u5DF1\u8D70\u5931\u4F1A\u7ED9\u5979\u9020\u6210\u591A\u5927\u7684\u62C5\u5FE7\uFF0C\u5C3D\u7BA1\u6211\u5DF2\u7ECF\u88AB\u627E\u56DE\u6765\u4E86\uFF0C\u4F46\u5F88\u591A\u5927\u4EBA\u4F1A\u7406\u6240\u5E94\u5F53\u7684\u8BA4\u4E3A\uFF0C\u5C0F\u5B69\u5B50\u662F\u5E94\u8BE5\u4E3A\u8FD9\u6B21\u4E8B\u4EF6\u53D7\u60E9\u7F5A\u7684\uFF0C\u800C\u4E14\u8FD9\u4E2A\u65F6\u5019\u7684\u6253\u9A82\u4F53\u73B0\u7684\u662F\u4E00\u79CD\u5173\u5FC3\uFF0C\u4E00\u79CD\u5728\u4E4E\uFF0C\u5E76\u4E0D\u662F\u771F\u6B63\u7684\u538C\u6076\u75DB\u6068\uFF0C\u5927\u4EBA\u4EEC\u4E0D\u4F1A\u8BA4\u4E3A\u8FD9\u6709\u4F24\u5BB3\u5230\u5B69\u5B50\u7684\u611F\u60C5\u3002</p><p>\u800C\u5BF9\u4E8E\u6211\u6765\u8BF4\uFF0C\u5C0F\u5B69\u5B50\u4E0D\u662F\u5927\u4EBA\uFF0C\u611F\u53D7\u4E0D\u5230\u5F88\u591A\u5927\u4EBA\u89C9\u5F97\u7406\u6240\u5E94\u5F53\u7684\u9053\u7406\uFF0C\u6211\u662F\u771F\u7684\u771F\u7684\u89C9\u5F97\u201C\u6211\u5988\u7A81\u7136\u7FFB\u8138\u4E86\uFF0C\u5929\u7A81\u7136\u584C\u4E86\uFF0C\u800C\u6211\u5B8C\u5168\u4E0D\u77E5\u9053\u4E3A\u4EC0\u4E48\u201D\u3002\u5F53\u7136\u4E8B\u540E\u5979\u4E5F\u6CA1\u89E3\u91CA\uFF0C\u53D1\u6CC4\u5B8C\u76F4\u63A5\u62C9\u8D77\u6211\u6C14\u51B2\u51B2\u5730\u8D70\u4E86\uFF0C\u7559\u6211\u6C89\u6D78\u5728\u65B9\u624D\u7684\u6050\u6016\u4E4B\u4E2D\uFF0C\u4E3A\u81EA\u5DF1\u7236\u6BCD\u7684\u9634\u6674\u4E0D\u5B9A\u6DF1\u611F\u7591\u60D1\u3001\u81EA\u8D23\u548C\u75DB\u82E6\u3002</p><p>\u968F\u7740\u5E74\u9F84\u6162\u6162\u589E\u5927\uFF0C\u6211\u5F00\u59CB\u53D1\u73B0\uFF0C\u7231\u5149\u6709\u610F\u613F\u662F\u4E0D\u591F\u7684\uFF0C\u8FD8\u9700\u8981\u8DB3\u591F\u7684\u80FD\u529B\u548C\u5408\u9002\u7684\u65B9\u5F0F\u6765\u8868\u8FBE\u3002\u6211\u5988\u5988\u5BF9\u6211\u7684\u5173\u5FC3\u548C\u7231\u6BCB\u5EB8\u7F6E\u7591\uFF0C\u4E0D\u900A\u4E8E\u4E16\u4E0A\u4EFB\u4F55\u4E00\u4F4D\u6BCD\u4EB2\uFF0C\u4F46\u8FC7\u53BB\u5979\u8868\u8FBE\u7231\u7684\u65B9\u5F0F\u8BA9\u6211\u4E0D\u80FD\u7406\u89E3\uFF0C\u751A\u81F3\u5F88\u4E0D\u5FEB\u4E50\u3002\u5982\u679C\u5979\u5F53\u65F6\u80FD\u591F\u6291\u5236\u4F4F\u5C06\u81EA\u5DF1\u7684\u4E0D\u5B89\u60E9\u7F5A\u5728\u6211\u8EAB\u4E0A\u7684\u51B2\u52A8\uFF0C\u80FD\u591F\u6B63\u786E\u5730\u8BA4\u6E05\u201C\u5979\u6240\u906D\u53D7\u7684\u7126\u8651\u201D\u548C\u201C\u5979\u5B69\u5B50\u7684\u4E3B\u89C2\u610F\u8BC6\u201D\u4E4B\u95F4\u4E0D\u5B58\u5728\u56E0\u679C\uFF0C\u4E5F\u8BB8\u5979\u5C31\u80FD\u628A\u5BF9\u6211\u7684\u62C5\u5FE7\u8F6C\u5316\u4E3A\u91CD\u65B0\u627E\u5230\u6211\u7684\u559C\u60A6\uFF0C\u987A\u5229\u7684\u8868\u8FBE\u51FA\u6765\uFF0C\u4E5F\u8BB8\u5979\u5C31\u80FD\u6709\u4F59\u529B\u544A\u8BC9\u6211\uFF0C\u5988\u5988\u8FD9\u6B21\u7740\u6025\u662F\u56E0\u4E3A\u4EC0\u4E48\u539F\u56E0\uFF0C\u4F60\u4EE5\u540E\u5E94\u8BE5\u600E\u4E48\u505A\u5988\u5988\u5C31\u4E0D\u4F1A\u8FD9\u4E48\u7740\u6025\u96BE\u53D7\u3002</p><p>\u4F46\u66F4\u591A\u65F6\u5019\uFF0C\u7236\u6BCD\u5F88\u96BE\u987E\u53CA\u5230\u8FD9\u4E9B\uFF0C\u90A3\u4E00\u523B\u7684\u611F\u6027\u4F7F\u7136\uFF0C\u4E8B\u540E\u7684\u89E3\u91CA\u7F3A\u5931\uFF0C\u7ED9\u5B69\u5B50\u7559\u4E0B\u7236\u6BCD\u559C\u6012\u65E0\u5E38\u7684\u5370\u8C61\uFF0C\u957F\u6B64\u4EE5\u5F80\uFF0C\u5B69\u5B50\u5C31\u50CF\u88AB\u5173\u5728\u7B3C\u5B50\u91CC\uFF0C\u4E00\u4F1A\u513F\u7ED9\u7CD6\u4E00\u4F1A\u513F\u88AB\u7535\u7684\u5C0F\u767D\u9F20\uFF0C\u5BF9\u73AF\u5883\u7684\u628A\u63A7\u611F\u5F7B\u5E95\u9519\u4E71\uFF0C\u5B89\u5168\u611F\u7F3A\u5931\u7684\u4EBA\u683C\u7531\u6B64\u800C\u751F\u3002</p><p>\u4E8B\u5B9E\u4E0A\u5F88\u591A\u5BB6\u957F\u627E\u5230\u5B69\u5B50\u7B2C\u4E00\u53CD\u5E94\u662F\u9AD8\u5174\uFF0C\u4F46\u4EE5\u4E2D\u56FD\u5BB6\u957F\u5185\u655B\u7684\u6027\u683C\uFF0C\u5F53\u4F17\u5BF9\u81EA\u5BB6\u5B69\u5B50\u8868\u8FBE\u559C\u60A6\u548C\u4F9D\u604B\u4F1A\u975E\u5E38\u8BA9\u4ED6\u4EEC\u5BB3\u7F9E\u548C\u96BE\u582A\uFF0C\u8FD9\u79CD\u60F3\u8981\u8868\u8FBE\u559C\u60A6\u800C\u5F15\u53D1\u7684\u96BE\u582A\u5F80\u5F80\u8F6C\u5316\u6210\u5BA3\u8A93\u6743\u5A01\u7684\u6124\u6012\u548C\u9119\u5937\u3002\u540C\u6837\u7684\u573A\u666F\u4E5F\u53D1\u751F\u5728\u522B\u4EBA\u5938\u4F60\u5B66\u4E60\u597D\u7684\u65F6\u5019\uFF0C\u5F80\u5F80\u90FD\u662F\u522B\u4EBA\u5938\u4F60\uFF0C\u800C\u4F60\u7238\u5988\u4E00\u4E2A\u52B2\u513F\u8C26\u865A\uFF0C\u4E00\u4E2A\u52B2\u513F\u635F\u4F60\u3002</p><p>\u4EB2\u5B50\u6C9F\u901A\u4E2D\u5F88\u56F0\u96BE\u7684\u4E00\u4E2A\u5730\u65B9\uFF0C\u5C31\u662F\u7236\u6BCD\u548C\u5B69\u5B50\u7684\u5FC3\u667A\u4E0D\u76F8\u5339\u914D\u7684\u60C5\u51B5\u4E0B\uFF0C\u5982\u4F55\u8BA9\u7236\u6BCD\u65F6\u523B\u7167\u987E\u5B69\u5B50\u672A\u6210\u719F\u7684\u5FC3\u667A\uFF0C\u505A\u51FA\u8BA9\u5B69\u5B50\u80FD\u591F\u7406\u89E3\u7684\u4E3E\u52A8\uFF0C\u8FDB\u800C\u4E00\u6B65\u6B65\u6559\u4ED6\u8BA4\u8BC6\u793E\u4F1A\uFF0C\u8BA4\u8BC6\u4EBA\u60C5\u3002</p><p>\u80FD\u505A\u5230\u8FD9\u4E00\u70B9\u7684\u7236\u6BCD\u9996\u5148\u5C31\u5FC5\u987B\u5C06\u81EA\u8EAB\u7684\u5FC3\u667A\u63D0\u5347\u5230\u4E00\u4E2A\u76F8\u5BF9\u6210\u719F\u7684\u9636\u6BB5\uFF0C\u624D\u80FD\u5728\u5173\u952E\u65F6\u523B\u548C\u5B69\u5B50\u6362\u4F4D\u601D\u8003\uFF0C\u624D\u80FD\u7ED9\u5B69\u5B50\u521B\u9020\u4E00\u4E2A\u7A33\u5B9A\u7684\u63D0\u5347\u73AF\u5883\u3002</p><p>\u6BCF\u4E00\u4E2A\u5B69\u5B50\u5176\u5B9E\u90FD\u5E0C\u671B\u81EA\u5DF1\u80FD\u83B7\u5F97\u7236\u6BCD\u7684\u8BA4\u540C\u548C\u8D5E\u626C\uFF0C\u53EA\u662F\u4ED6\u4EEC\u4E0D\u77E5\u9053\u600E\u6837\u624D\u80FD\u505A\u5F97\u597D\uFF0C\u800C\u5927\u4EBA\u4EEC\u603B\u8BA4\u4E3A\u5B69\u5B50\u505A\u4E0D\u597D\u662F\u56E0\u4E3A\u201C\u4E3B\u89C2\u4E0A\u201D\u4E0D\u60F3\u505A\u597D\uFF0C\u65E0\u6570\u8BEF\u89E3\u7531\u6B64\u800C\u751F\u3002\u81F3\u4E8E\u90A3\u4E9B\u4E00\u65E6\u53D7\u4E86\u60CA\u53D7\u4E86\u6C14\u5C31\u6025\u7740\u5F80\u5B69\u5B50\u8EAB\u4E0A\u53D1\u6CC4\u60C5\u7EEA\u7684\uFF0C\u5F80\u5F80\u5F88\u591A\u81EA\u5DF1\u90FD\u662F\u5B69\u5B50\u3002</p><p>\u7406\u89E3\u6211\u5988\u7684\u60C5\u7EEA\u4E5F\u662F\u5230\u6211\u5927\u4E86\u4EE5\u540E\uFF0C\u8D8A\u662F\u7406\u89E3\uFF0C\u8D8A\u662F\u5BBD\u5BB9\uFF0C\u4E5F\u8D8A\u662F\u660E\u767D\u4E86\u4E3A\u4EBA\u7236\u6BCD\u7684\u8270\u8F9B\u548C\u4E0D\u6613\u3002\u5982\u4ECA\u6211\u60F3\u5230\u4ECA\u540E\u8981\u6559\u80B2\u81EA\u5DF1\u7684\u5B69\u5B50\uFF0C\u66F4\u662F\u5FC3\u6709\u621A\u621A\u3002\u6211\u4E0D\u6562\u8BF4\u81EA\u5DF1\u5DF2\u662F\u4E00\u4E2A\u5B8C\u5168\u6210\u719F\u7684\u4EBA\uFF0C\u4E5F\u4E0D\u6562\u8BF4\u81EA\u5DF1\u4E00\u5B9A\u80FD\u6210\u4E3A\u4E00\u4E2A\u597D\u7236\u6BCD\uFF0C\u4F46\u65E0\u8BBA\u4F55\u65F6\uFF0C\u4FDD\u6301\u4E00\u4E2A\u5F00\u653E\u7684\u5FC3\u6001\uFF0C\u8DF3\u8131\u51FA\u81EA\u5DF1\u7684\u60C5\u7EEA\uFF0C\u66F4\u591A\u4E3A\u4ED6\u4EBA\u7684\u611F\u53D7\u7740\u60F3\uFF0C\u4E00\u5B9A\u662F\u4E0E\u4EBA\u4EA4\u5F80\u7684\u5FC5\u8981\u539F\u5219\u3002</p><p>\u4F5C\u4E3A\u513F\u79D1\u533B\u751F\uFF0C\u8BFB\u5B8C\u8FD9\u4E9B\u6587\u5B57\u633A\u611F\u6982\uFF0C\u56DE\u60F3\u81EA\u5DF1\u5728\u5B66\u6821\u63A5\u53D7\u7684\u533B\u5B66\u6559\u80B2\uFF0C\u5173\u4E8E\u513F\u7AE5\u5FC3\u7406\u7684\u5185\u5BB9\u6781\u5C11\uFF0C\u5982\u4F55\u548C\u5B69\u5B50\u76F8\u5904\uFF0C\u5982\u4F55\u7BA1\u6559\u5B69\u5B50\uFF0C\u5176\u5B9E\u771F\u4E0D\u592A\u61C2\uFF0C\u597D\u5728\u5973\u513F\u7B97\u542C\u8BDD\u7684\uFF0C\u4E5F\u4ECE\u6CA1\u60F3\u8FC7\u53BB\u6253\u5979\u3002</p><p>\u4F46\u6211\u5728\u95E8\u8BCA\u786E\u5B9E\u89C1\u8FC7\u5F88\u591A\u6781\u96BE\u914D\u5408\u7684\u5B69\u5B50\uFF0C\u6709\u6B21\u4E00\u4E2A\u5B69\u5B50\u5C31\u662F\u4E3A\u4E86\u62D2\u7EDD\u68C0\u67E5\u6B47\u65AF\u5E95\u91CC\u7684\u54ED\u558A\u4E86\u534A\u4E2A\u5C0F\u65F6\uFF0C\u76F8\u4FE1\u5927\u5BB6\u4E5F\u53EF\u80FD\u89C1\u8FC7\u5F88\u591A\u6240\u8C13\u7684\u201C\u718A\u5B69\u5B50\u201D\uFF0C\u4E0D\u7BA1\u53C8\u6015\u4ED6\u72AF\u9519\u5BB3\u4EBA\u5BB3\u5DF1\uFF0C\u7BA1\u7684\u8BDD\u8BB2\u53C8\u4E0D\u542C\uFF0C\u6253\u53C8\u6015\u4F24\u5BB3\u5B69\u5B50\u3002</p><p>\u600E\u6837\u7BA1\u5B69\u5B50\uFF0C\u80FD\u4E0D\u80FD\u6253\u5B69\u5B50\uFF1F\u5F88\u591A\u5BB6\u957F\u4E5F\u5F88\u56F0\u60D1\uFF0C\u770B\u5B8C\u9648\u8BFA\u7684\u6587\u5B57\uFF0C\u6211\u4E5F\u6076\u8865\u4E86\u4E00\u4E0B\u76F8\u5173\u77E5\u8BC6\uFF0C\u51C6\u5907\u4E0B\u4E00\u7BC7\u548C\u5927\u5BB6\u63A2\u8BA8\u8FD9\u4E2A\u95EE\u9898\uFF0C\u5148\u505A\u4E2A\u9488\u5BF9\u7236\u6BCD\u4EEC\u7684\u5C0F\u8C03\u67E5\uFF08\u524D\u53F0\u540E\u53F0\u90FD\u4E0D\u4F1A\u663E\u793A\u4E2A\u4EBA\u6295\u7968\u4FE1\u606F\uFF0C\u53EF\u4EE5\u653E\u5FC3\u4F5C\u771F\u5B9E\u9009\u62E9\uFF09\u3002</p><p><strong>\u6743\u5A01\u7684\u80B2\u513F\u79D1\u666E\uFF0C\u6DF1\u5EA6\u7684\u533B\u5B66\u4EBA\u6587</strong></p><p><strong>\u4EC5\u63A8\u9001\u539F\u521B\u6587\u7AE0\uFF0C\u6B22\u8FCE\u5173\u6CE8drpeihg</strong></p>',18),l={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=203788304&idx=1&sn=6dfb76cad08692fbafdfd308c49b690f&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},h=s("\u67E5\u770B\u539F\u6587");function f(m,x){const p=r("ExternalLinkIcon");return t(),a(o,null,[_,e("p",null,[e("a",l,[h,n(p)])])],64)}var b=d(i,[["render",f]]);export{b as default};
