import{r,o as t,c as n,a as p,b as o,F as a,d as s,e as c}from"./app.9aaa2256.js";import{_ as d}from"./plugin-vue_export-helper.21dcd24c.js";const _={},i=s('<h3 id="\u5B9D\u5B9D\u5410\u5976\u600E\u4E48\u529E" tabindex="-1"><a class="header-anchor" href="#\u5B9D\u5B9D\u5410\u5976\u600E\u4E48\u529E" aria-hidden="true">#</a> \u5B9D\u5B9D\u5410\u5976\u600E\u4E48\u529E\uFF1F</h3><p>\u4E0D\u8BBA\u662F\u6BCD\u4E73\u5582\u517B\u8FD8\u662F\u914D\u65B9\u5976\u5582\u517B\uFF0C\u5A74\u513F\u5410\u5976\u5176\u5B9E\u90FD\u5F88\u5E38\u89C1\u3002\u548C\u56E0\u4E3A\u8179\u808C\u548C\u8188\u808C\u6536\u7F29\u6536\u7F29\u5F15\u8D77\u7684\u5455\u5410\u4E0D\u4E00\u6837\uFF0C\u5410\u5976\u4E3B\u8981\u662F\u80C3\u91CC\u7684\u98DF\u7269\u8FD4\u6D41\u5230\u98DF\u7BA1\u53CA\u53E3\u8154\uFF0C\u5B69\u5B50\u901A\u5E38\u4E0D\u4F1A\u4E0D\u8212\u670D\uFF0C\u6240\u4EE5\u53C8\u79F0\u4E3A\u201C\u6EA2\u5976\u201D\u3002</p><p>\u5410\u5976\u4E3B\u8981\u53D1\u751F\u5728\u4E00\u5C81\u4EE5\u5185\u7684\u5A74\u513F\uFF0C\u5927\u591A\u4F1A\u968F\u5B69\u5B50\u53D1\u80B2\u9010\u6E10\u597D\u8F6C\u3001\u6D88\u5931\uFF0C\u5410\u5976\u901A\u5E38\u4E5F\u4E0D\u5F71\u54CD\u5B69\u5B50\u751F\u957F\u3002\u4F46\u9891\u7E41\u7684\u5410\u5976\u4E5F\u8BA9\u4E00\u90E8\u5206\u5BB6\u957F\u62C5\u5FC3\uFF0C\u6216\u8005\u56E0\u4E3A\u5410\u5976\u4EA7\u751F\u7684\u4E00\u4E9B\u76F8\u5173\u95EE\u9898\u800C\u56F0\u60D1\uFF0C\u4E5F\u6709\u5C0F\u90E8\u5206\u5410\u5976\u662F\u56E0\u4E3A\u5B69\u5B50\u5B58\u5728\u80C3\u98DF\u7BA1\u8FD4\u6D41\u75C5\uFF0C\u8FD9\u662F\u4E00\u79CD\u4F1A\u5F71\u54CD\u5B69\u5B50\u7684\u751F\u6D3B\u548C\u53D1\u80B2\u7684\u75BE\u75C5\uFF0C\u662F\u9700\u8981\u6CBB\u7597\u7684\u3002</p><p>\u8FD9\u7BC7\u6587\u7AE0\u5C31\u548C\u5927\u5BB6\u5206\u4EAB\u4E00\u4E0B\u5410\u5976\u3001\u80C3\u98DF\u7BA1\u8FD4\u6D41\u75C5\u76F8\u5173\u7684\u77E5\u8BC6\u3002</p><p><strong>\u4E3A\u4EC0\u4F1A\u5410\u5976\uFF1F</strong></p><p>\u4EBA\u4F53\u80C3\u548C\u98DF\u7BA1\u7684\u8FDE\u63A5\u5904\u6709\u4E00\u5708\u808C\u8089\uFF0C\u808C\u8089\u7684\u5F20\u529B\u53EF\u4EE5\u5E2E\u52A9\u63A7\u5236\u80C3\u91CC\u7684\u4E1C\u897F\u4E0D\u5411\u4E0A\u6D41\u5230\u98DF\u7BA1\u91CC\u3002\u5728\u5A74\u513F\u671F\uFF0C\u5C24\u5176\u662F\u5728\u751F\u540E\u524D\u51E0\u4E2A\u6708\u91CC\uFF0C\u8FD9\u4E00\u5708\u808C\u8089\u8FD8\u6CA1\u6709\u5B8C\u5168\u53D1\u80B2\u6210\u719F\u65F6\uFF0C\u5BFC\u81F4\u80C3\u5185\u5BB9\u7269\u5BB9\u6613\u5F80\u4E0A\u8FD4\u3002\u636E\u7EDF\u8BA1\uFF0C\u4E09\u6708\u4EE5\u5185\u7684\u5B9D\u5B9D50%\u4F1A\u6709\u5410\u5976\u73B0\u8C61\uFF0C\u5C24\u5176\u662F\u5728\u5582\u5976\u4E4B\u540E\u3002</p><p>\u90E8\u5206\u5B9D\u5B9D\u56E0\u4E3A\u8FD4\u6D41\u51FA\u73B0\u5410\u5976\uFF0C\u800C\u6709\u90E8\u5206\u5B9D\u5B9D\u867D\u6709\u8FD4\u6D41\uFF0C\u4F46\u662F\u5E76\u4E0D\u5410\u51FA\u6765\uFF0C\u53EA\u662F\u5988\u5988\u4EEC\u6CE8\u610F\u5230\u54FA\u4E73\u540E\u5B9D\u5B9D\u6709\u541E\u54BD\u7684\u58F0\u97F3\u6216\u52A8\u4F5C\uFF0C\u6216\u8005\u611F\u89C9\u5B9D\u5B9D\u564E\u7740\u7684\u611F\u89C9\u3002\u8FD9\u4E9B\u5B9D\u5B9D\u901A\u5E38\u4E0D\u4F1A\u56E0\u4E3A\u8FD4\u6D41\u800C\u611F\u5230\u75DB\u82E6\uFF0C\u6240\u4EE5\u4E0D\u4F1A\u54ED\u95F9\uFF0C\u800C\u4E14\u751F\u957F\u53D1\u80B2\u4E5F\u6B63\u5E38\uFF0C\u4E0D\u4F1A\u51FA\u73B0\u4F53\u91CD\u4E0D\u589E\u6216\u964D\u4F4E\u7684\u95EE\u9898\u3002</p><p>\u6709\u4E00\u90E8\u5206\u5B9D\u5B9D\u53D1\u751F\u8FD4\u6D41\u65F6\uFF0C\u5185\u5BB9\u7269\u4E2D\u7684\u80C3\u9178\u5BF9\u98DF\u7BA1\u9020\u6210\u523A\u6FC0\uFF0C\u751A\u81F3\u5BFC\u81F4\u98DF\u7BA1\u708E\uFF0C\u53EF\u4EE5\u611F\u5230\u75BC\u75DB\u7B49\u4E0D\u9002\uFF0C\u5F71\u54CD\u5B69\u5B50\u8FDB\u98DF\u751A\u81F3\u751F\u957F\u53D1\u80B2\uFF0C\u8FD9\u79CD\u8FD4\u6D41\u5219\u53EF\u80FD\u662F\u80C3\u98DF\u7BA1\u8FD4\u6D41\u75C5\u3002</p><p>\u80C3\u98DF\u7BA1\u8FD4\u6D41\u75C7\u662F\u6BD4\u8F83\u5C11\u89C1\u7684\uFF0C\u4F46\u5982\u679C\u5B9D\u5B9D\u6709\u4E0B\u9762\u8FD9\u4E9B\u95EE\u9898\uFF0C\u9700\u8981\u8B66\u60D5\u80C3\u98DF\u7BA1\u8FD4\u6D41\u75C5\uFF1A</p><p>1.\u5B9D\u5B9D\u5410\u5976\uFF0C\u8868\u73B0\u975E\u5E38\u7D27\u7EF7\u548C\u75DB\u82E6\uFF0C\u54ED\u95F9\u6253\u633A\u7B49\u3002</p><p>2.\u5B9D\u5B9D\u5F88\u997F\uFF0C\u731B\u5403\u51E0\u53E3\u4E4B\u540E\u5C31\u5F00\u59CB\u54ED\u95F9\uFF0C\u7136\u540E\u62D2\u7EDD\u518D\u5403\u5976\u3002</p><p>3.\u65E0\u6CD5\u5B89\u7761\uFF0C\u6BD4\u5982\u7761\u7740\u4E4B\u540E20\u5206\u949F\u5185\u5C31\u9192\u6765\u4F34\u54ED\u95F9\u3002</p><p>4.\u5403\u5976\u4E4B\u540E\u51FA\u73B0\u54B3\u55FD\u3002</p><p>5.\u5B9D\u5B9D\u7AD6\u7ACB\u4F4D\u65F6\uFF0C\u611F\u89C9\u7A0D\u7A0D\u8212\u670D\u4E00\u4E9B\u3002</p><p>6.\u5B9D\u5B9D\u9075\u533B\u5631\u4F7F\u7528\u6297\u9178\u836F\u540E\uFF0C\u77ED\u65F6\u95F4\u5185\u8868\u73B0\u51FA\u660E\u663E\u7684\u597D\u8F6C\u3002</p><p>\u9700\u8981\u6CE8\u610F\u7684\u662F\uFF0C\u4E0A\u9762\u8FD9\u4E9B\u8868\u73B0\u5E76\u4E0D\u5177\u6709\u7279\u5F02\u6027\uFF0C\u5E76\u4E0D\u662F\u6709\u8FD9\u4E9B\u73B0\u8C61\u5C31\u7B49\u4E8E\u6709\u80C3\u98DF\u7BA1\u8FD4\u6D41\u75C5\uFF0C\u53EA\u662F\u8BF4\u6709\u8FD9\u4E9B\u8868\u73B0\u9700\u8981\u8B66\u60D5\uFF0C\u5FC5\u8981\u65F6\u627E\u533B\u751F\u6392\u67E5\u80C3\u98DF\u7BA1\u8FD4\u6D41\u75C5\u3002</p><p><strong>\u5410\u5976\u600E\u4E48\u5582\uFF1F</strong></p><p>\u5BF9\u4E8E\u8F7B\u5EA6\u7684\u5410\u5976\uFF0C\u6BD4\u5982\u4EC5\u4EC5\u6709\u5410\u5976\u6216\u8005\u8FD4\u6D41\u7684\u73B0\u8C61\uFF0C\u4F46\u662F\u5B9D\u5B9D\u4E0D\u611F\u53D7\u75DB\u82E6\uFF0C\u751F\u957F\u4E5F\u826F\u597D\uFF0C\u90A3\u5C31\u65E0\u9700\u7279\u522B\u5904\u7406\u3002\u5728\u5582\u5976\u65F6\u548C\u5582\u5976\u540E\uFF0C\u6CE8\u610F\u5C06\u4E0A\u534A\u8EAB\u62AC\u9AD8\u4E00\u4E9B\uFF1B\u5582\u5976\u540E\u81F3\u5C1120\u5206\u949F\u8BA9\u5B9D\u5B9D\u5B89\u9759\uFF0C\u53EF\u4EE5\u7AD6\u62B1\u7740\u8D70\u8D70\uFF0C\u907F\u514D\u5267\u70C8\u6D3B\u52A8\u548C\u5E73\u8EBA\u3002</p><p>\u5BF9\u4E8E\u4E2D\u5EA6\u7684\u5410\u5976\uFF0C\u6BD4\u5982\u5B9D\u5B9D\u5410\u5976\u6216\u8005\u8FD4\u6D41\u65F6\uFF0C\u8868\u73B0\u4E0D\u9002\uFF0C\u4F46\u662F\u751F\u957F\u4ECD\u7136\u662F\u826F\u597D\u7684\uFF0C\u90A3\u8F7B\u5EA6\u8FD4\u6D41\u7684\u517B\u80B2\u8981\u70B9\u4ECD\u7136\u9002\u7528\u3002\u4F46\u662F\uFF0C\u5982\u679C\u5582\u5976\u65F6\u5B9D\u5B9D\u54ED\u95F9\u6709\u4E9B\u4E25\u91CD\uFF0C\u53EF\u4EE5\u5C1D\u8BD5\u4E2D\u95F4\u6B47\u606F15-30\u5206\u949F\u540E\u518D\u7EE7\u7EED\u54FA\u4E73\u3002</p><p>\u5982\u679C\u54FA\u4E73\u56F0\u96BE\u800C\u4E14\u65F6\u95F4\u6301\u7EED\u5F88\u4E45\uFF0C\u90A3\u5728\u4E25\u5BC6\u76D1\u6D4B\u5B9D\u5B9D\u4F53\u91CD\u589E\u52A0\u72B6\u51B5\u7684\u524D\u63D0\u4E0B\uFF0C\u53EF\u4EE5\u5C1D\u8BD5\u7A0D\u7A0D\u7F29\u77ED\u4E00\u4E0B\u54FA\u4E73\u542E\u5438\u7684\u65F6\u95F4\uFF0C\u6216\u8005\u7A0D\u7A0D\u51CF\u5C11\u4E00\u4E9B\u5976\u74F6\u5582\u517B\u7684\u5976\u91CF\uFF0C\u7136\u540E\u6CE8\u610F\u89C2\u5BDF\u5B9D\u5B9D\u4E0D\u9002\u7684\u72B6\u51B5\u662F\u5426\u6709\u7F13\u89E3\u3002\u5B9D\u5B9D\u54ED\u95F9\u65F6\uFF0C\u4E5F\u53EF\u4EE5\u4F7F\u7528\u5E38\u89C4\u7684\u5B89\u629A\u65B9\u6CD5\uFF0C\u6BD4\u5982\u9002\u5F53\u7684\u5305\u88F9\uFF0C\u5B89\u629A\u5976\u5634\uFF08\u4EC5\u5728\u6BCD\u4E73\u5582\u517B\u5DF2\u7ECF\u6210\u529F\u5B9E\u73B0\u4EE5\u540E\u7684\u5B9D\u5B9D\uFF09\uFF0C\u4F7F\u7528\u5A74\u5E7C\u513F\u80CC\u5DFE\u7B49\u3002</p><p>\u5F53\u7136\uFF0C\u5982\u679C\u5B9D\u5B9D\u4ECD\u7136\u54ED\u95F9\u4E25\u91CD\uFF0C\u6CA1\u6709\u4EFB\u4F55\u7F13\u89E3\uFF0C\u6216\u8005\u4E25\u91CD\u5F71\u54CD\u5927\u4EBA\u7684\u4F11\u606F\uFF0C\u90A3\u4E48\u9700\u8981\u53CA\u65F6\u5C31\u533B\u6392\u67E5\u539F\u56E0\uFF0C\u7136\u540E\u6839\u636E\u539F\u56E0\u91C7\u53D6\u9488\u5BF9\u6027\u7684\u6CBB\u7597\u6216\u8005\u5E94\u5BF9\u65B9\u5F0F\u3002</p><p>\u800C\u5BF9\u4E8E\u80C3\u98DF\u7BA1\u8FD4\u6D41\u75C5\uFF0C\u5982\u679C\u56E0\u4E3A\u75BC\u75DB\u5BFC\u81F4\u54ED\u95F9\u4E0D\u5B89\uFF0C\u901A\u5E38\u4F1A\u5B58\u5728\u5582\u517B\u56F0\u96BE\u7684\u95EE\u9898\uFF0C\u5219\u9700\u8981\u5728\u533B\u751F\u548C\u5176\u4ED6\u4E13\u4E1A\u4EBA\u58EB\u6307\u5BFC\u4E0B\u8FDB\u884C\u989D\u5916\u6CBB\u7597\u3002\u5E38\u89C1\u7684\u5E94\u5BF9\u65B9\u5F0F\u5305\u62EC\uFF1A</p><p>1.\u524D\u9762\u63D0\u53CA\u7684\u5BF9\u4E8E\u8F7B\u5EA6\uFF0C\u4E2D\u5EA6\u5410\u5976\u65F6\u7684\u5E38\u89C4\u517B\u80B2\u8981\u70B9\uFF0C\u4ECD\u53EF\u4EE5\u8D77\u5230\u4E00\u5B9A\u7684\u5E2E\u52A9\uFF0C\u6240\u4EE5\u53EF\u4EE5\u7EE7\u7EED\u5C1D\u8BD5\u3002</p><p>2.\u4F7F\u7528\u589E\u7A20\u5242\u3002\u5BB6\u957F\u53EF\u4EE5\u9009\u62E9\u5DF2\u7ECF\u9884\u5148\u589E\u7A20\u7684\u7279\u6B8A\u914D\u65B9\u5976\u7C89\uFF1B\u6216\u8005\u4F7F\u7528\u589E\u7A20\u5242\u81EA\u884C\u6DFB\u52A0\u5230\u666E\u901A\u7684\u914D\u65B9\u5976\u4E2D\uFF0C\u8FD9\u79CD\u589E\u7A20\u7684\u65B9\u6CD5\u6709\u4E00\u4E2A\u597D\u5904\u662F\u5BB6\u957F\u53EF\u4EE5\u6839\u636E\u5B9D\u5B9D\u8868\u73B0\u81EA\u884C\u63A7\u5236\u589E\u7A20\u7684\u7A0B\u5EA6\u3002\u589E\u7A20\u7684\u914D\u65B9\u5976\u4F1A\u5F71\u54CD\u6D41\u901F\uFF0C\u9700\u8981\u6839\u636E\u5B9D\u5B9D\u5438\u542E\u529B\u5EA6\u8C03\u6574\u5976\u5634\u5B54\u5F84\u5927\u5C0F\u3002\u5BF9\u4E8E\u6BCD\u4E73\u5582\u517B\u7684\u5B9D\u5B9D\uFF0C\u6709\u4E00\u4E9B\u589E\u7A20\u5242\u53EF\u4EE5\u7528\u767D\u5F00\u6C34\u9884\u5148\u8C03\u597D\uFF0C\u7136\u540E\u5728\u6BCD\u4E73\u5582\u517B\u6362\u4FA7\u7684\u95F4\u9699\u5C1D\u8BD5\u5582\u7ED9\u5B9D\u5B9D\uFF08\u64CD\u4F5C\u4E0A\u53EF\u80FD\u4F1A\u6709\u4E00\u5B9A\u7684\u56F0\u96BE\uFF0C\u6211\u4EEC\u9700\u8981\u6709\u4E00\u5B9A\u7684\u5FC3\u7406\u51C6\u5907\uFF09\u3002</p><p>3.\u4F7F\u7528\u6297\u9178\u836F\u6216\u8005\u6291\u5236\u80C3\u9178\u5206\u6CCC\u7684\u836F\u7269\u3002\u5F53\u5E38\u89C4\u5904\u7406\u65B9\u6CD5\u6CA1\u6709\u6539\u5584\u65F6\uFF0C\u5BB6\u957F\u53EF\u80FD\u4F1A\u88AB\u5EFA\u8BAE\u4F7F\u7528\u836F\u7269\u3002\u6297\u9178\u836F\u901A\u5E38\u5728\u54FA\u4E73\u65F6\u6216\u8005\u54FA\u4E73\u540E\u534A\u5C0F\u65F6\u5230\u4E00\u5C0F\u65F6\u4F7F\u7528\u3002\u53E6\u5916\uFF0C\u6297\u9178\u836F\u5E94\u8BE5\u6309\u9700\u4F7F\u7528\uFF0C\u800C\u4E0D\u662F\u5E38\u89C4\u5B9A\u91CF\u4F7F\u7528\u3002\u5982\u679C\u4F7F\u75281-2\u5468\u540E\u6CA1\u6709\u6548\u679C\uFF0C\u90A3\u5C31\u9700\u8981\u627E\u533B\u751F\u8FDB\u4E00\u6B65\u6392\u67E5\u548C\u8BC4\u4F30\u3002</p><p>\u5982\u679C\u5B69\u5B50\u6709\u80C3\u98DF\u7BA1\u53CD\u6D41\u75C5\uFF0C\u5BF9\u7236\u6BCD\u6765\u8BB2\u662F\u5F88\u5927\u7684\u6311\u6218\uFF0C\u5728\u517B\u80B2\u8FC7\u7A0B\u4E2D\u4F1A\u7ECF\u5386\u5F88\u591A\u8270\u8F9B\uFF0C\u5E38\u5E38\u89C9\u5F97\u81EA\u5DF1\u5DF2\u7ECF\u5C3D\u4E86\u5168\u529B\u53BB\u79D1\u5B66\u5582\u517B\uFF0C\u5B69\u5B50\u5374\u4ECD\u7136\u751F\u957F\u4E0D\u597D\uFF0C\u5BF9\u5BB6\u957F\u7684\u7CBE\u795E\u548C\u60C5\u7EEA\u90FD\u662F\u8003\u9A8C\uFF0C\u5728\u5E26\u597D\u5B69\u5B50\u7684\u540C\u65F6\uFF0C\u5BB6\u957F\u4E5F\u9700\u8981\u6CE8\u610F\u7167\u987E\u81EA\u5DF1\u7684\u60C5\u7EEA\u548C\u5065\u5EB7\uFF0C\u5FC5\u8981\u7684\u65F6\u5019\u8981\u53CA\u65F6\u5BFB\u6C42\u5E2E\u52A9\u3002</p><p>\u53E6\u5916\uFF0C\u5927\u90E8\u5206\u80C3\u98DF\u7BA1\u8FD4\u6D41\u75C5\u7684\u5E7C\u513F\u5728\u4ED6\u4EEC\u5230\u4E866-8\u4E2A\u6708\uFF0C\u4ED6\u4EEC\u80FD\u591F\u72EC\u5750\u7684\u65F6\u5019\uFF0C\u75C7\u72B6\u4F1A\u9010\u6E10\u7F13\u89E3\uFF0C\u56E0\u6B64\u966A\u4F34\u5B69\u5B50\u7ECF\u5386\u5EA6\u8FC7\u8FD9\u4E2A\u56F0\u96BE\u9636\u6BB5\uFF0C\u672C\u8EAB\u5C31\u662F\u4E00\u79CD\u5E94\u5BF9\u7684\u65B9\u5F0F\u3002</p><p><strong>\u600E\u4E48\u7761\uFF1F</strong></p><p>\u6309\u7167\u5E38\u8BC6\uFF0C\u5B9D\u5B9D\u5410\u5976\u5982\u679C\u4EF0\u5367\u4F4D\u4E0D\u662F\u8981\u545B\u5230\uFF0C\u662F\u4E0D\u662F\u5F88\u5371\u9669\uFF1F\u5176\u5B9E\uFF0C\u5982\u679C\u6211\u4EEC\u4ECE\u8FDB\u4E00\u6B65\u7684\u89E3\u5256\u5B66\u65B9\u9762\u53BB\u7406\u89E3\uFF0C\u53EF\u80FD\u4F1A\u66F4\u597D\u7406\u89E3\u3002</p><p>\u5982\u4E0A\u56FE\uFF0C\u5B9D\u5B9D\u4EF0\u5367\u4F4D\u65F6\uFF0C\u98DF\u7BA1\u5728\u6C14\u7BA1\u7684\u4E0B\u65B9\uFF0C\u901A\u8FC7\u98DF\u7BA1\u8FD4\u6D41\u7684\u6DB2\u4F53\u53EF\u4EE5\u901A\u8FC7\u541E\u54BD\u6E05\u9664\uFF0C\u907F\u514D\u8FDB\u5165\u6C14\u7BA1\uFF1B\u76F8\u53CD\uFF0C\u5982\u679C\u662F\u4FEF\u5367\u4F4D\u6216\u8005\u4FA7\u5367\u4F4D\uFF0C\u98DF\u7BA1\u5728\u6C14\u7BA1\u4E0A\u65B9\u6216\u540C\u4E00\u4E2A\u6C34\u5E73\uFF0C\u90A3\u6DB2\u4F53\u8FDB\u5165\u6C14\u7BA1\u7684\u98CE\u9669\u66F4\u5927\u3002</p><p>\u6240\u4EE5\uFF0C\u4E0D\u8BBA\u5B69\u5B50\u662F\u5426\u6709\u8FD4\u6D41\u95EE\u9898\uFF0C\u4EF0\u5367\u90FD\u662F\u6700\u5B89\u5168\u7684\uFF0C\u5BF9\u4E8E\u8F7B\u5EA6\u5230\u4E2D\u5EA6\u7684\u80C3\u98DF\u7BA1\u8FD4\u6D41\u75C7\u60A3\u513F\uFF0C\u4EF0\u5367\u5176\u5B9E\u8FD8\u662F\u6CBB\u7597\u65B9\u6848\u7EC4\u6210\u90E8\u5206\u3002\u4E3B\u89C2\u60F3\u8C61\u7684\u4FA7\u5367\u4F4D\u6216\u4FEF\u5367\u4F4D\u7684\u597D\u5904\uFF0C\u90FD\u4E0D\u503C\u5F97\u8BA9\u5B69\u5B50\u4ED8\u51FA\u731D\u6B7B\u98CE\u9669\u589E\u52A0\u7684\u4EE3\u4EF7\u3002\u53EA\u6709\u5728\u7F55\u89C1\u6216\u8005\u6781\u7AEF\u60C5\u51B5\u4E0B\uFF0C\u6BD4\u5982\u8003\u8651\u5176\u4ED6\u7684\u75C5\u7406\u6027\u539F\u56E0\uFF0C\u5728\u533B\u751F\u7684\u6307\u5BFC\u4E0B\u624D\u53EF\u4EE5\u8BA9\u5E7C\u513F\u4F7F\u7528\u4FEF\u5367\u6216\u8005\u4FA7\u5367\u4F4D\u3002</p><p><strong>\u9700\u8981\u62AC\u9AD8\u5E8A\u5934\u5417\uFF1F</strong></p><p>\u4E0D\u63A8\u8350\u3002\u62AC\u9AD8\u5E8A\u5934\u4E0D\u4F46\u4E0D\u4F1A\u51CF\u5C11\u5410\u5976\u73B0\u8C61\uFF0C\u76F8\u53CD\u53EF\u80FD\u8BA9\u5B9D\u5B9D\u8EAB\u4F53\u4E0B\u6ED1\uFF0C\u4ECE\u800C\u5BFC\u81F4\u5934\u9762\u90E8\u88AB\u6BEF\u5B50\u7B49\u906E\u76D6\u7684\u98CE\u9669\u3002\u6B64\u5916\uFF0C\u4E0B\u6ED1\u81F3\u5E8A\u5E95\u90E8\u65F6\uFF0C\u8FD8\u53EF\u80FD\u8BA9\u5B69\u5B50\u51FA\u73B0\u5BFC\u81F4\u547C\u5438\u4E0D\u7545\u7684\u7761\u7720\u59FF\u52BF\uFF0C\u4ECE\u800C\u5F15\u8D77\u5371\u9669\u3002\u6240\u4EE5\u5BF9\u4E8E\u5410\u5976\u548C\u6709\u8FD4\u6D41\u73B0\u8C61\u7684\u5E7C\u513F\uFF0C\u4E0D\u63A8\u8350\u62AC\u9AD8\u5E8A\u5934\u3002\uFF08\u672C\u6587\u7531\u516C\u4F17\u53F7<strong>DrHAN_AU</strong>\u6388\u6743\u8F6C\u8F7D\uFF0C\u70B9\u51FB\u9605\u8BFB\u539F\u6587\u53EF\u67E5\u770B\u76F8\u5173\u53C2\u8003\u6587\u732E\uFF0C\u9898\u56FE\u6765\u81EAPixabay\uFF0C\u6388\u6743\u57FA\u4E8ECC0\u534F\u8BAE\uFF09</p><hr><p>\u76F8\u5173\u533B\u751F\u63A8\u8350\uFF1A\u8D75\u6D93\uFF0C\u5317\u4EAC\u548C\u7766\u5BB6\u533B\u9662\u513F\u79D1\u533B\u751F\uFF0C\u4E3B\u6CBB\u533B\u751F</p><p>\u957F\u6309\u4E0B\u9762\u4E8C\u7EF4\u7801\u52A0\u5173\u6CE8\uFF0C\u53EF\u5411\u8D75\u533B\u751F\u54A8\u8BE2<strong>\u5410\u5976\u3001\u5455\u5410\u3001\u8179\u6CFB\u3001\u53D1\u70ED\u3001\u54B3\u55FD</strong>\u7B49\u513F\u79D1\u95EE\u9898\u3002</p>',36),b={href:"http://mp.weixin.qq.com/s?__biz=MzA5NzE5ODIyOQ==&mid=2651626366&idx=1&sn=7063dcbc6b9e8c37ff8f842b42937b0b&chksm=8b5cb284bc2b3b92e6d3f8be0dbbf7f0f82598a9fad80d7355d1b599831a3935c9cb55918a15&scene=21#wechat_redirect",target:"_blank",rel:"noopener noreferrer"},f=c("\u67E5\u770B\u539F\u6587");function h(l,g){const e=r("ExternalLinkIcon");return t(),n(a,null,[i,p("p",null,[p("a",b,[f,o(e)])])],64)}var u=d(_,[["render",h]]);export{u as default};
